// End-to-End Tests for Crisis Flow
import { test, expect } from '@playwright/test';

// Crisis response must be under 200ms
const CRISIS_RESPONSE_THRESHOLD = 200;

test.describe('Crisis Intervention Flow', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
    // Wait for app to be fully loaded
    await page.waitForSelector('[data-testid="app-ready"]', { timeout: 5000 });
  });

  test('should display crisis button prominently on all pages', async ({ page }) => {
    // Check crisis button on home page
    const crisisButton = page.locator('[data-testid="crisis-button"]');
    await expect(crisisButton).toBeVisible();
    await expect(crisisButton).toHaveCSS('position', 'fixed');
    
    // Navigate to different pages and verify button persists
    const pages = ['/dashboard', '/wellness', '/community', '/professionals'];
    
    for (const route of pages) {
      await page.goto(route);
      await expect(crisisButton).toBeVisible();
      
      // Verify button is always accessible (z-index)
      const zIndex = await crisisButton.evaluate(el => 
        window.getComputedStyle(el).zIndex
      );
      expect(parseInt(zIndex)).toBeGreaterThan(1000);
    }
  });

  test('should respond to crisis trigger within 200ms', async ({ page }) => {
    const crisisButton = page.locator('[data-testid="crisis-button"]');
    
    // Measure response time
    const startTime = Date.now();
    await crisisButton.click();
    
    // Wait for crisis modal to appear
    await page.waitForSelector('[data-testid="crisis-modal"]');
    const responseTime = Date.now() - startTime;
    
    expect(responseTime).toBeLessThan(CRISIS_RESPONSE_THRESHOLD);
    
    // Verify crisis resources are immediately visible
    await expect(page.locator('text=988')).toBeVisible();
    await expect(page.locator('text=Crisis Text Line')).toBeVisible();
  });

  test('should provide multiple crisis contact methods', async ({ page }) => {
    await page.click('[data-testid="crisis-button"]');
    
    // Phone hotline
    const hotlineLink = page.locator('a[href="tel:988"]');
    await expect(hotlineLink).toBeVisible();
    await expect(hotlineLink).toHaveText(/988/);
    
    // Text support
    const textSupport = page.locator('text=Text HOME to 741741');
    await expect(textSupport).toBeVisible();
    
    // Online chat option
    const chatOption = page.locator('[data-testid="crisis-chat"]');
    await expect(chatOption).toBeVisible();
    
    // Local emergency resources
    const localResources = page.locator('[data-testid="local-resources"]');
    await expect(localResources).toBeVisible();
  });

  test('should work offline with cached crisis resources', async ({ page, context }) => {
    // Load page first to cache resources
    await page.goto('/');
    await page.click('[data-testid="crisis-button"]');
    await page.click('[data-testid="crisis-close"]');
    
    // Go offline
    await context.setOffline(true);
    
    // Crisis button should still work
    await page.click('[data-testid="crisis-button"]');
    
    // Offline crisis resources should be available
    await expect(page.locator('text=988')).toBeVisible();
    await expect(page.locator('text=Offline Resources Available')).toBeVisible();
    
    // Verify cached resources
    const resources = await page.locator('[data-testid="offline-resources"]').textContent();
    expect(resources).toContain('988');
    expect(resources).toContain('741741');
  });

  test('should alert professionals when crisis is triggered', async ({ page }) => {
    // Login as a user with professional support enabled
    await page.goto('/login');
    await page.fill('[name="email"]', 'patient@example.com');
    await page.fill('[name="password"]', 'Test123!');
    await page.click('[type="submit"]');
    
    await page.waitForURL('/dashboard');
    
    // Trigger crisis
    await page.click('[data-testid="crisis-button"]');
    
    // Verify professional alert notification
    await expect(page.locator('text=Professional support has been notified')).toBeVisible();
    
    // Check for professional response indicator
    await expect(page.locator('[data-testid="professional-responding"]')).toBeVisible();
  });

  test('should track crisis events for safety monitoring', async ({ page }) => {
    // Enable analytics tracking
    await page.evaluate(() => {
      window.localStorage.setItem('analytics_consent', 'true');
    });
    
    // Intercept analytics calls
    const analyticsRequests: any[] = [];
    await page.route('**/api/analytics/event', async route => {
      const request = route.request();
      analyticsRequests.push(await request.postDataJSON());
      await route.fulfill({ status: 200, body: '{"tracked": true}' });
    });
    
    // Trigger crisis
    await page.click('[data-testid="crisis-button"]');
    
    // Verify analytics event was sent
    await page.waitForTimeout(100);
    const crisisEvent = analyticsRequests.find(r => r.category === 'crisis');
    expect(crisisEvent).toBeDefined();
    expect(crisisEvent.action).toBe('button_clicked');
    expect(crisisEvent.timestamp).toBeDefined();
  });

  test('should provide crisis text alternatives for accessibility', async ({ page }) => {
    await page.click('[data-testid="crisis-button"]');
    
    // Check for screen reader announcements
    const ariaLive = page.locator('[aria-live="assertive"]');
    await expect(ariaLive).toContainText(/crisis support/i);
    
    // Verify all crisis options have text alternatives
    const images = page.locator('[data-testid="crisis-modal"] img');
    const imageCount = await images.count();
    
    for (let i = 0; i < imageCount; i++) {
      const img = images.nth(i);
      const altText = await img.getAttribute('alt');
      expect(altText).toBeTruthy();
    }
    
    // Check keyboard navigation
    await page.keyboard.press('Tab');
    const focusedElement = await page.evaluate(() => document.activeElement?.getAttribute('data-testid'));
    expect(focusedElement).toBeTruthy();
  });

  test('should handle crisis escalation workflow', async ({ page }) => {
    await page.click('[data-testid="crisis-button"]');
    
    // Initial self-help resources
    await expect(page.locator('[data-testid="self-help-resources"]')).toBeVisible();
    
    // Escalate to professional support
    await page.click('[data-testid="need-more-help"]');
    await expect(page.locator('[data-testid="professional-options"]')).toBeVisible();
    
    // Further escalate to emergency
    await page.click('[data-testid="emergency-help"]');
    await expect(page.locator('[data-testid="emergency-resources"]')).toBeVisible();
    await expect(page.locator('text=Call 911')).toBeVisible();
  });

  test('should maintain crisis state across page navigation', async ({ page }) => {
    // Trigger crisis mode
    await page.click('[data-testid="crisis-button"]');
    await page.click('[data-testid="activate-crisis-mode"]');
    
    // Navigate to different page
    await page.goto('/wellness');
    
    // Crisis mode should persist
    await expect(page.locator('[data-testid="crisis-mode-active"]')).toBeVisible();
    
    // Crisis resources should remain easily accessible
    const crisisBar = page.locator('[data-testid="crisis-resource-bar"]');
    await expect(crisisBar).toBeVisible();
    await expect(crisisBar).toContainText('988');
  });

  test('should validate crisis response on mobile devices', async ({ _page, browser }) => {
    // Create mobile context
    const iPhone = {
      viewport: { width: 375, height: 667 },
      userAgent: 'Mozilla/5.0 (iPhone; CPU iPhone OS 14_0 like Mac OS X)',
      hasTouch: true,
      isMobile: true,
    };
    
    const context = await browser.newContext(iPhone);
    const mobilePage = await context.newPage();
    
    await mobilePage.goto('/');
    
    // Crisis button should be thumb-reachable
    const crisisButton = mobilePage.locator('[data-testid="crisis-button"]');
    const buttonBox = await crisisButton.boundingBox();
    
    // Button should be in lower portion of screen for thumb reach
    expect(buttonBox?.y).toBeGreaterThan(400);
    
    // Minimum touch target size (44x44 pixels)
    expect(buttonBox?.width).toBeGreaterThanOrEqual(44);
    expect(buttonBox?.height).toBeGreaterThanOrEqual(44);
    
    // Test touch interaction
    await crisisButton.tap();
    await expect(mobilePage.locator('[data-testid="crisis-modal"]')).toBeVisible();
    
    await context.close();
  });
});

test.describe('Crisis Recovery and Follow-up', () => {
  test('should provide post-crisis check-in', async ({ page }) => {
    // Simulate crisis event
    await page.goto('/');
    await page.click('[data-testid="crisis-button"]');
    await page.click('[data-testid="crisis-resolved"]');
    
    // Wait for follow-up prompt (simulated delay)
    await page.waitForTimeout(2000);
    
    // Check-in notification should appear
    await expect(page.locator('[data-testid="crisis-checkin"]')).toBeVisible();
    await expect(page.locator('text=How are you feeling now?')).toBeVisible();
    
    // Provide feedback
    await page.click('[data-testid="feeling-better"]');
    
    // Verify support resources are offered
    await expect(page.locator('[data-testid="follow-up-resources"]')).toBeVisible();
  });

  test('should save crisis intervention history for professionals', async ({ page }) => {
    // Login as professional
    await page.goto('/login');
    await page.fill('[name="email"]', 'professional@example.com');
    await page.fill('[name="password"]', 'Test123!');
    await page.click('[type="submit"]');
    
    await page.goto('/professional/dashboard');
    
    // View crisis interventions
    await page.click('[data-testid="crisis-interventions"]');
    
    // Verify intervention history is displayed
    const interventions = page.locator('[data-testid="intervention-item"]');
    await expect(interventions).toHaveCount(3); // Based on mock data
    
    // Check intervention details
    await interventions.first().click();
    await expect(page.locator('[data-testid="intervention-details"]')).toBeVisible();
    await expect(page.locator('text=Response Time')).toBeVisible();
    await expect(page.locator('text=Outcome')).toBeVisible();
  });
});

test.describe('Crisis System Performance', () => {
  test('should handle multiple simultaneous crisis triggers', async ({ browser }) => {
    const contexts = [];
    const pages = [];
    
    // Create 10 concurrent users
    for (let i = 0; i < 10; i++) {
      const context = await browser.newContext();
      const page = await context.newPage();
      contexts.push(context);
      pages.push(page);
      await page.goto('/');
    }
    
    // Trigger crisis simultaneously
    const promises = pages.map(async page => {
      const startTime = Date.now();
      await page.click('[data-testid="crisis-button"]');
      await page.waitForSelector('[data-testid="crisis-modal"]');
      return Date.now() - startTime;
    });
    
    const responseTimes = await Promise.all(promises);
    
    // All responses should be under threshold
    responseTimes.forEach(time => {
      expect(time).toBeLessThan(CRISIS_RESPONSE_THRESHOLD * 2); // Allow some overhead for concurrent requests
    });
    
    // Cleanup
    for (const context of contexts) {
      await context.close();
    }
  });

  test('should prioritize crisis requests over other operations', async ({ page }) => {
    // Start a heavy operation
    await page.goto('/analytics');
    await page.click('[data-testid="generate-report"]'); // Long-running operation
    
    // Immediately trigger crisis
    const startTime = Date.now();
    await page.click('[data-testid="crisis-button"]');
    await page.waitForSelector('[data-testid="crisis-modal"]');
    const responseTime = Date.now() - startTime;
    
    // Crisis should interrupt and respond quickly
    expect(responseTime).toBeLessThan(CRISIS_RESPONSE_THRESHOLD);
    
    // Verify heavy operation was paused
    await expect(page.locator('text=Report generation paused')).toBeVisible();
  });
});

import { test, expect, Page } from '@playwright/test';
import AxeBuilder from '@axe-core/playwright';

/**
 * E2E Tests for Crisis Intervention Flow
 * Critical path testing for mental health emergencies
 */

test.describe('Crisis Intervention E2E', () => {
  let page: Page;

  test.beforeEach(async ({ page: testPage }) => {
    page = testPage;
    await page.goto('/');
    
    // Set up performance monitoring
    await page.evaluateOnNewDocument(() => {
      window.performanceMarks = new Map();
      window.markPerformance = (name: string) => {
        window.performanceMarks.set(name, performance.now());
      };
    });
  });

  test('Crisis button responds within 200ms requirement', async () => {
    // Navigate to dashboard
    await page.goto('/dashboard');
    
    // Start performance measurement
    await page.evaluate(() => window.markPerformance('crisis-start'));
    
    // Click crisis button
    await page.click('button:has-text("Crisis Help")', { timeout: 1000 });
    
    // Wait for crisis modal
    await expect(page.locator('text=Crisis Resources')).toBeVisible({ timeout: 200 });
    
    // Measure response time
    const responseTime = await page.evaluate(() => {
      window.markPerformance('crisis-end');
      const start = window.performanceMarks.get('crisis-start');
      const end = window.performanceMarks.get('crisis-end');
      return end - start;
    });
    
    expect(responseTime).toBeLessThan(200);
  });

  test('Complete crisis intervention flow', async () => {
    await page.goto('/dashboard');
    
    // Step 1: User indicates crisis
    await page.click('button:has-text("Crisis Help")');
    
    // Step 2: Crisis resources should appear immediately
    await expect(page.locator('text=988')).toBeVisible();
    await expect(page.locator('text=Crisis Text Line')).toBeVisible();
    await expect(page.locator('text=Text HOME to 741741')).toBeVisible();
    
    // Step 3: Verify professional notification
    await expect(page.locator('text=Professional support notified')).toBeVisible({ timeout: 5000 });
    
    // Step 4: Test hotline link
    const [popup] = await Promise.all([
      page.waitForEvent('popup'),
      page.click('a[href^="tel:988"]')
    ]);
    
    // Verify tel: protocol handling
    expect(popup).toBeDefined();
  });

  test('Crisis resources remain available offline', async () => {
    await page.goto('/dashboard');
    
    // Go offline
    await page.context().setOffline(true);
    
    // Click crisis button
    await page.click('button:has-text("Crisis Help")');
    
    // Offline resources should still be available
    await expect(page.locator('text=Offline Crisis Resources')).toBeVisible();
    await expect(page.locator('text=988')).toBeVisible();
    await expect(page.locator('text=Text HOME to 741741')).toBeVisible();
    
    // Go back online
    await page.context().setOffline(false);
  });

  test('Low mood triggers crisis support automatically', async () => {
    await page.goto('/wellness/mood');
    
    // Set very low mood (crisis level)
    const slider = page.locator('input[type="range"]');
    await slider.fill('2');
    
    // Add concerning notes
    await page.fill('textarea[placeholder*="Add notes"]', 'Feeling hopeless');
    
    // Submit mood
    await page.click('button:has-text("Log Mood")');
    
    // Crisis support should be triggered
    await expect(page.locator('text=Crisis Support Available')).toBeVisible();
    await expect(page.locator('text=988')).toBeVisible();
  });

  test('Crisis intervention is keyboard accessible', async () => {
    await page.goto('/dashboard');
    
    // Navigate to crisis button using keyboard
    await page.keyboard.press('Tab');
    await page.keyboard.press('Tab');
    await page.keyboard.press('Tab');
    
    // Activate crisis button with Enter
    await page.keyboard.press('Enter');
    
    // Modal should be accessible
    await expect(page.locator('text=Crisis Resources')).toBeVisible();
    
    // Tab through resources
    await page.keyboard.press('Tab');
    const focusedElement = await page.evaluate(() => document.activeElement?.tagName);
    expect(focusedElement).toBeTruthy();
    
    // Close with Escape
    await page.keyboard.press('Escape');
    await expect(page.locator('text=Crisis Resources')).not.toBeVisible();
  });

  test('Crisis support works on mobile devices', async ({ browser }) => {
    // Create mobile context
    const context = await browser.newContext({
      ...devices['iPhone 12'],
      permissions: ['geolocation'],
      geolocation: { latitude: 37.7749, longitude: -122.4194 }
    });
    
    const mobilePage = await context.newPage();
    await mobilePage.goto('/dashboard');
    
    // Test touch interaction
    await mobilePage.tap('button:has-text("Crisis Help")');
    
    // Resources should be mobile-optimized
    await expect(mobilePage.locator('text=988')).toBeVisible();
    
    // Test tel: link on mobile
    const telLink = mobilePage.locator('a[href^="tel:988"]');
    await expect(telLink).toHaveAttribute('href', 'tel:988');
    
    await context.close();
  });

  test('Crisis data is encrypted and secure', async () => {
    await page.goto('/dashboard');
    
    // Monitor network requests
    const encryptedRequests: boolean[] = [];
    
    page.on('request', request => {
      if (request.url().includes('/api/crisis')) {
        const headers = request.headers();
        encryptedRequests.push(headers['content-type'] === 'application/json');
      }
    });
    
    // Trigger crisis
    await page.click('button:has-text("Crisis Help")');
    
    // Wait for API call
    await page.waitForTimeout(1000);
    
    // Check localStorage for encrypted data
    const storageData = await page.evaluate(() => {
      const data = localStorage.getItem('crisis_session');
      return data ? !data.startsWith('{') : false; // Should not be plain JSON
    });
    
    expect(storageData).toBeTruthy();
  });

  test('Professional connection during crisis', async () => {
    await page.goto('/dashboard');
    
    // Trigger crisis
    await page.click('button:has-text("Crisis Help")');
    
    // Wait for professional notification
    await expect(page.locator('text=Professional support notified')).toBeVisible();
    
    // Check for connection status
    const connectionStatus = await page.locator('[data-testid="professional-status"]');
    
    if (await connectionStatus.isVisible()) {
      const statusText = await connectionStatus.textContent();
      expect(['Available', 'Connecting', 'In Queue']).toContain(statusText);
    }
  });

  test('Crisis intervention meets WCAG 2.1 AA standards', async () => {
    await page.goto('/dashboard');
    
    // Open crisis modal
    await page.click('button:has-text("Crisis Help")');
    await page.waitForSelector('text=Crisis Resources');
    
    // Run accessibility scan
    const accessibilityScanResults = await new AxeBuilder({ page })
      .withTags(['wcag2a', 'wcag2aa', 'wcag21a', 'wcag21aa'])
      .analyze();
    
    expect(accessibilityScanResults.violations).toEqual([]);
  });

  test('Crisis follow-up is scheduled after intervention', async () => {
    await page.goto('/dashboard');
    
    // Trigger and complete crisis intervention
    await page.click('button:has-text("Crisis Help")');
    await expect(page.locator('text=Crisis Resources')).toBeVisible();
    
    // Close modal
    await page.click('button:has-text("Close")');
    
    // Check for follow-up scheduling
    const followUpScheduled = await page.evaluate(() => {
      return sessionStorage.getItem('crisis_follow_up') !== null;
    });
    
    expect(followUpScheduled).toBeTruthy();
    
    // Verify follow-up reminder appears
    await page.reload();
    await expect(page.locator('text=Check-in scheduled')).toBeVisible({ timeout: 5000 });
  });

  test('Crisis history is tracked for pattern detection', async () => {
    await page.goto('/wellness/mood');
    
    // Log multiple low moods
    for (let i = 0; i < 3; i++) {
      const slider = page.locator('input[type="range"]');
      await slider.fill('3');
      await page.click('button:has-text("Log Mood")');
      await page.waitForTimeout(500);
    }
    
    // Pattern warning should appear
    await expect(page.locator('text=mood has been declining')).toBeVisible();
    await expect(page.locator('text=Consider reaching out')).toBeVisible();
  });

  test('Multi-language crisis support', async () => {
    // Set Spanish language preference
    await page.goto('/settings');
    await page.selectOption('select[name="language"]', 'es');
    await page.click('button:has-text("Save")');
    
    // Navigate to dashboard
    await page.goto('/dashboard');
    
    // Crisis button should be in Spanish
    const crisisButton = await page.locator('button:has-text("Ayuda de Crisis")');
    
    if (await crisisButton.isVisible()) {
      await crisisButton.click();
      
      // Resources should include Spanish options
      await expect(page.locator('text=988')).toBeVisible();
      await expect(page.locator('text=LÃ­nea de Crisis')).toBeVisible();
    }
  });

  test('Performance monitoring during crisis', async () => {
    await page.goto('/dashboard');
    
    // Collect performance metrics
    const metrics = await page.evaluate(() => {
      const navigation = performance.getEntriesByType('navigation')[0] as any;
      return {
        domContentLoaded: navigation.domContentLoadedEventEnd - navigation.domContentLoadedEventStart,
        loadComplete: navigation.loadEventEnd - navigation.loadEventStart,
      };
    });
    
    // Page should load quickly
    expect(metrics.domContentLoaded).toBeLessThan(1000);
    expect(metrics.loadComplete).toBeLessThan(2000);
    
    // Measure crisis button interaction
    const interactionMetrics = await page.evaluate(async () => {
      const start = performance.now();
      (document.querySelector('button:has-text("Crisis Help")') as any)?.click();
      await new Promise(resolve => setTimeout(resolve, 100));
      const end = performance.now();
      return end - start;
    });
    
    expect(interactionMetrics).toBeLessThan(200);
  });
});

// Declare global window extensions for TypeScript
declare global {
  interface Window {
    performanceMarks: Map<string, number>;
    markPerformance: (name: string) => void;
  }
}

const devices = {
  'iPhone 12': {
    viewport: { width: 390, height: 844 },
    userAgent: 'Mozilla/5.0 (iPhone; CPU iPhone OS 14_4 like Mac OS X) AppleWebKit/605.1.15',
    deviceScaleFactor: 3,
    isMobile: true,
    hasTouch: true,
  }
};

/**
 * Unified Error Boundary Component
 * Combines crisis-aware features, multiple fallback options, and comprehensive error handling
 * Ensures users never lose access to critical crisis intervention resources during errors
 */

import React, { Component, ErrorInfo, ReactNode } from 'react';
import { AlertTriangle, Phone, MessageCircle, Home, RefreshCw } from 'lucide-react';
import { logError } from '../utils/logger';
import { logger } from '../services/logging/logger';

interface ErrorBoundaryState {
  hasError: boolean;
  error: Error | null;
  errorInfo: ErrorInfo | null;
  errorId: string;
}

interface ErrorBoundaryProps {
  children: ReactNode;
  fallback?: ReactNode;
  FallbackComponent?: React.ComponentType<unknown>;
  fallbackRender?: (props: { error: Error; resetErrorBoundary: () => void }) => ReactNode;
  onError?: (error: Error, errorInfo: ErrorInfo) => void;
  onReset?: () => void;
  showCrisisResources?: boolean;
}

export class ErrorBoundary extends Component<ErrorBoundaryProps, ErrorBoundaryState> {
  private retryCount = 0;
  private maxRetries = 3;

  constructor(props: ErrorBoundaryProps) {
    super(props);

    this.state = {
      hasError: false,
      error: null,
      errorInfo: null,
      errorId: this.generateErrorId()
    };
  }

  private generateErrorId(): string {
    return `err_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  static getDerivedStateFromError(error: Error): Partial<ErrorBoundaryState> {
    return {
      hasError: true,
      error,
      errorId: `err_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
    };
  }

  override componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    // Update state with error info
    this.setState({ error, errorInfo });

    // Log error with multiple logging systems
    logError('Component error caught by ErrorBoundary', 'ErrorBoundary', { error, errorInfo });

    // Log to crisis-aware logging system if available
    if (logger?.logCrisisIntervention) {
      logger.logCrisisIntervention('component_error', undefined, {
        error: error.message,
        component: errorInfo.componentStack,
        severity: 'high',
        emergency_resources_available: true,
        errorId: this.state.errorId
      });
    }

    // Log error details for debugging
    logger.error('ðŸš¨ ERROR BOUNDARY CAUGHT:', {
      error: error.message,
      stack: error.stack,
      componentStack: errorInfo.componentStack,
      errorId: this.state.errorId,
      timestamp: new Date().toISOString(),
      userAgent: navigator.userAgent,
      url: window.location.href
    });

    // Send error to monitoring service
    this.reportError(error, errorInfo);

    // Call custom error handler
    if (this.props.onError) {
      this.props.onError(error, errorInfo);
    }
  }

  private reportError = async (error: Error, errorInfo: ErrorInfo) => {
    try {
      // Store error in localStorage for analysis
      const _errorReport = {
        errorId: this.state.errorId,
        message: error.message,
        stack: error.stack,
        componentStack: errorInfo.componentStack,
        timestamp: new Date().toISOString(),
        url: window.location.href,
        userAgent: navigator.userAgent
      };

      localStorage.setItem(`error_${this.state.errorId}`, JSON.stringify(_errorReport));

      // Send to monitoring service in production
      if (import.meta.env.PROD) {
        try {
          fetch('/api/monitoring/crisis-error', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(_errorReport),
          }).catch(() => {
            // Fail silently - don't let monitoring failures affect crisis support
          });
        } catch {
          // Fail silently
        }
      }

      // Send to Sentry if available
      if ((window as unknown).Sentry) {
        (window as unknown).Sentry.captureException(error, {
          tags: {
            component: 'ErrorBoundary',
            errorId: this.state.errorId
          },
          extra: {
            componentStack: errorInfo.componentStack
          }
        });
      }
    } catch (_error) {
      logger.error('Failed to report error');
    }
  };

  private handleReset = () => {
    if (this.retryCount < this.maxRetries) {
      this.retryCount++;
      logger.info(`Retrying... (${this.retryCount}/${this.maxRetries})`, 'ErrorBoundary');

      this.setState({
        hasError: false,
        error: null,
        errorInfo: null,
        errorId: this.generateErrorId()
      });

      // Call onReset callback if provided
      if (this.props.onReset) {
        this.props.onReset();
      }
    } else {
      logger.error('âŒ Maximum retry attempts reached');
    }
  };

  private handleReload = () => {
    window.location.reload();
  };

  private handleEmergencyCall = (number: string) => {
    if (logger?.logCrisisIntervention) {
      logger.logCrisisIntervention('emergency_call_from_error_boundary', undefined, {
        number,
