          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-3">
              <div className={`p-2 rounded-lg ${isVoiceActive ? 'bg-green-100 text-green-600' : 'bg-gray-100 text-gray-600'}`}>
