export function SupportGroups() {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState('');

  // Fetch groups
  const { data, _isLoading, _error } = useQuery({
    queryKey: ['groups', selectedCategory, searchQuery],
    queryFn: () => communityService.getGroups({
      category: selectedCategory === 'all' ? undefined : selectedCategory,
      search: searchQuery || undefined,
      limit: 12,
    }),
  });

  // Join group mutation
  const joinMutation = useMutation({
    mutationFn: (_groupId: string) => communityService.joinGroup(_groupId),
    onSuccess: () => {
      toast.success('Successfully joined the group!');
      queryClient.invalidateQueries({ queryKey: ['groups'] });
    },
    onError: () => {
      toast._error('Failed to join group');
    },
  });

  // Leave group mutation
  const leaveMutation = useMutation({
    mutationFn: (_groupId: string) => communityService.leaveGroup(_groupId),
    onSuccess: () => {
      toast.success('You have left the group');
      queryClient.invalidateQueries({ queryKey: ['groups'] });
    },
    onError: () => {
      toast._error('Failed to leave group');
    },
  });

  const handleManageGroup = (group: SupportGroup) => {
    // Navigate to group management page
    window.location.href = `/community/groups/${group.id}/manage`;
  };

  const categories = [
    { value: 'all', label: 'All Groups', icon: Globe },
    { value: 'anxiety', label: 'Anxiety', icon: Heart },
    { value: 'depression', label: 'Depression', icon: Heart },
    { value: 'trauma', label: 'Trauma', icon: Shield },
    { value: 'addiction', label: 'Addiction', icon: Heart },
    { value: 'grief', label: 'Grief', icon: Heart },
    { value: 'relationships', label: 'Relationships', icon: Users },
    { value: 'self-esteem', label: 'Self-Esteem', icon: TrendingUp },
  ];

  if (_isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (_error) {
    return (
      <div className="text-center py-12">
        <p className="text-red-600">Failed to load support groups. Please try again later.</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold text-gray-900">Support Groups</h2>
        {user && (
          <button
            onClick={() => setShowCreateModal(true)}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors flex items-center space-x-2"
          >
            <UserPlus className="h-4 w-4" />
            <span>Create Group</span>
          </button>
        )}
      </div>

      {/* Search and Filters */}
      <div className="space-y-4">
        <div className="flex items-center space-x-4">
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search groups..."
            className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
        </div>

        <div className="flex items-center space-x-2 overflow-x-auto pb-2">
          {categories.map((category) => {
            const Icon = category.icon;
            return (
              <button
                key={category.value}
                onClick={() => setSelectedCategory(category.value)}
                className={`flex items-center space-x-2 px-4 py-2 rounded-lg text-sm font-medium whitespace-nowrap transition-colors ${
                  selectedCategory === category.value
                    ? 'bg-blue-100 text-blue-700'
                    : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
              >
                <Icon className="h-4 w-4" />
                <span>{category.label}</span>
              </button>
            );
          })}
        </div>
      </div>

      {/* Groups Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {data?.groups && data.groups.length > 0 ? (
          data.groups.map((group) => (
            <GroupCard
              key={group.id}
              group={group}
              onJoin={joinMutation.mutate}
              onLeave={leaveMutation.mutate}
              onManage={handleManageGroup}
            />
          ))
        ) : (
          <div className="col-span-full text-center py-12 bg-gray-50 rounded-lg">
            <Users className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-600">No groups found. Create one to get started!</p>
          </div>
        )}
      </div>

      {/* Create Group Modal */}
      <CreateGroupModal
        isOpen={showCreateModal}
        onClose={() => setShowCreateModal(false)}
      />
    </div>
  );
}

import React, { useState, useEffect, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Sparkles, Heart, Shield, Zap } from 'lucide-react';
import { useConsoleSound } from '../../services/console/ConsoleSoundSystem';

interface ConsoleBootSequenceProps {
  onBootComplete: () => void;
  skipBoot?: boolean;
}

export function ConsoleBootSequence({ onBootComplete, skipBoot = false }: ConsoleBootSequenceProps) {
  const [bootStage, setBootStage] = useState(0);
  const [showSkip, setShowSkip] = useState(false);
  const { playSound } = useConsoleSound();

  // Boot sequence stages - wrapped in useMemo to prevent recreating on every render
  const bootStages = useMemo(() => [
    { id: 0, duration: 1500, name: 'initializing' },
    { id: 1, duration: 2000, name: 'logo' },
    { id: 2, duration: 1500, name: 'loading' },
    { id: 3, duration: 1000, name: 'ready' },
  ], []);

  useEffect(() => {
    if (skipBoot) {
      onBootComplete();
      return;
    }

    // Show skip button after 2 seconds
    const _skipTimer = setTimeout(() => {
      setShowSkip(true);
    }, 2000);

    // Play startup sound
    playSound('startup');

    // Progress through boot stages
    const _timer = setTimeout(() => {
      if (bootStage < bootStages.length - 1) {
        setBootStage(bootStage + 1);
      } else {
        // Boot complete
        setTimeout(onBootComplete, 500);
      }
    }, bootStages[bootStage]?.duration || 1000);

    return () => {
      clearTimeout(_timer);
      clearTimeout(_skipTimer);
    };
  }, [bootStage, onBootComplete, skipBoot, playSound, bootStages]);

  const handleSkip = () => {
    onBootComplete();
  };

  if (skipBoot) return null;

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        className="fixed inset-0 z-50 bg-black flex items-center justify-center overflow-hidden"
      >
        {/* Dynamic background */}
        <div className="absolute inset-0">
          <motion.div
            animate={{
              background: [
                'radial-gradient(circle at 30% 40%, #1a1a1a 0%, #000000 70%)',
                'radial-gradient(circle at 70% 60%, #1e3a8a 0%, #000000 70%)',
                'radial-gradient(circle at 50% 50%, #7c3aed 0%, #000000 70%)',
                'radial-gradient(circle at 50% 50%, #059669 0%, #000000 70%)',
              ]
            }}
            transition={{
              duration: 6,
              ease: 'easeInOut',
            }}
            className="w-full h-full"
          />
          
          {/* Animated particles */}
          {Array.from({ length: 20 }).map((_, i) => (
            <motion.div
              key={i}
              initial={{
                x: Math.random() * window.innerWidth,
                y: Math.random() * window.innerHeight,
                opacity: 0,
              }}
              animate={{
                x: Math.random() * window.innerWidth,
                y: Math.random() * window.innerHeight,
                opacity: [0, 1, 0],
              }}
              transition={{
                duration: 4 + Math.random() * 4,
                repeat: Infinity,
                delay: Math.random() * 2,
              }}
              className="absolute w-1 h-1 bg-blue-400 rounded-full blur-sm"
            />
          ))}
        </div>

        {/* Boot sequence content */}
        <div className="relative z-10 text-center">
          {bootStage === 0 && (
            <motion.div
              initial={{ opacity: 0, scale: 0.5 }}
              animate={{ opacity: 1, scale: 1 }}
