import { logger } from '@/utils/logger';
/**
 * Unified Crisis Button Component
 * Consolidates all crisis button implementations with theming, haptic feedback, and accessibility
 */

import React, { useState, useRef, useCallback, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Phone, MessageSquare, Shield, AlertTriangle, Heart, MapPin } from 'lucide-react';
import { useVibration } from '../../hooks/useVibration';
import { useNetworkStatus } from '../../hooks/useNetworkStatus';
import { 
  handleEmergencyCall, 
  logCrisisInteraction, 
  getEmergencyLocation,
  getRecommendedContacts
} from '../../utils/crisis';
import { 
  CRISIS_ACTIONS, 
  _EMERGENCY_CONTACTS, 
  HAPTIC_PATTERNS, 
  CRISIS_THEMES,
  CrisisLevel,
  CrisisTheme
} from '../../constants/crisis';

interface UnifiedCrisisButtonProps {
  variant?: 'primary' | 'floating' | 'quick' | 'emergency';
  theme?: CrisisTheme;
  crisisLevel?: CrisisLevel;
  size?: 'sm' | 'md' | 'lg' | 'xl';
  showLabel?: boolean;
  showSubtext?: boolean;
  disabled?: boolean;
  className?: string;
  onActionTaken?: (action: string) => void;
}

const iconMap = {
  Phone,
  MessageSquare,
  Shield,
  AlertTriangle,
  Heart,
  MapPin
};

export function UnifiedCrisisButton({
  variant = 'primary',
  theme = 'therapeutic',
  crisisLevel = 'moderate',
  size = 'lg',
  showLabel = true,
  showSubtext = true,
  disabled = false,
  className = '',
  onActionTaken
}: UnifiedCrisisButtonProps) {
  const [isExpanded, setIsExpanded] = useState(false);
  const [location, _setLocation] = useState<GeolocationCoordinates | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const longPressTimer = useRef<NodeJS.Timeout | null>(null);
  const { consoleVibrate } = useVibration();
  const { isOnline } = useNetworkStatus();

  // Get location on mount for emergency services
  useEffect(() => {
    if (variant === 'emergency' || crisisLevel === 'critical') {
      getEmergencyLocation()
        .then(_setLocation)
        .catch(error => logger.warn('Could not get location:', error));
    }
  }, [variant, crisisLevel]);

  const handleAction = useCallback(async (actionId: string) => {
    setIsLoading(true);
    consoleVibrate('activation', HAPTIC_PATTERNS.urgent);

    try {
      const action = CRISIS_ACTIONS.find(a => a.id === actionId);
      if (!action) return;

      // Log the action
      await logCrisisInteraction(actionId, crisisLevel, {
        variant,
        theme,
        location: location ? {
          lat: location.latitude,
          lng: location.longitude
        } : null
      });

      // Handle different action types
      if (typeof action.action === 'string') {
        if (action.action.startsWith('tel:') || action.action.startsWith('sms:')) {
          // Emergency contact
          const contactId = actionId.includes('988') ? '988' : 
                           actionId.includes('911') ? '911' : 
                           actionId.includes('text') ? 'crisis-text' : 'samhsa';
          
          await handleEmergencyCall(contactId, location || undefined);
        } else {
          // Navigate to internal route
          window.location.href = action.action;
        }
      }

      onActionTaken?.(_actionId);
    } catch (_error) {
      logger.error('Crisis action failed:');
      consoleVibrate('undefined', HAPTIC_PATTERNS.undefined);
    } finally {
      setIsLoading(false);
      setIsExpanded(false);
    }
  }, [crisisLevel, location, variant, theme, consoleVibrate, onActionTaken]);

  const handleLongPressStart = useCallback(() => {
    longPressTimer.current = setTimeout(() => {
      handleAction('call-988'); // Default long press action
    }, 800);
  }, [handleAction]);

  const handleLongPressEnd = useCallback(() => {
    if (longPressTimer.current) {
      clearTimeout(longPressTimer.current);
      longPressTimer.current = null;
    }
  }, []);

  const getThemeClasses = () => {
    const themes = CRISIS_THEMES[theme];
    return {
      primary: themes.crisis,
      emergency: themes.emergency,
      safe: themes.safe,
      accent: themes.accent
    };
  };

  const getSizeClasses = () => {
    const sizes = {
      sm: 'w-12 h-12 text-sm',
      md: 'w-16 h-16 text-base',
      lg: 'w-20 h-20 text-lg',
      xl: 'w-24 h-24 text-xl'
    };
    return sizes[size];
  };

  if (variant === 'primary') {
    return (
      <motion.button
        whileTap={{ scale: 0.95 }}
        onClick={() => handleAction('call-988')}
        onMouseDown={handleLongPressStart}
        onMouseUp={handleLongPressEnd}
        onMouseLeave={handleLongPressEnd}
        onTouchStart={handleLongPressStart}
        onTouchEnd={handleLongPressEnd}
        disabled={disabled || isLoading}
        className={`
          relative w-full p-6 rounded-2xl shadow-lg transition-all duration-300
          ${getThemeClasses().primary} text-white
          ${disabled ? 'opacity-50 cursor-not-allowed' : 'active:shadow-inner hover:shadow-xl'}
          ${crisisLevel === 'critical' ? 'animate-pulse' : ''}
          ${className}
        `}
        aria-label="Call 988 Suicide & Crisis Lifeline for immediate help"
      >
        <div className="flex flex-col items-center space-y-3">
          <div className="relative">
            <Phone className="h-10 w-10" />
            {isLoading && (
              <motion.div
                animate={{ rotate: 360 }}
                transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                className="absolute inset-0 border-2 border-white/30 border-t-white rounded-full"
              />
            )}
          </div>
          
          {showLabel && (
            <div className="text-center">
              <span className="text-xl font-bold block">Call 988</span>
              {showSubtext && (
                <span className="text-white/80 text-sm block mt-1">
                  Suicide & Crisis Lifeline
                </span>
              )}
            </div>
          )}
        </div>

        {/* Urgent indicator */}
        {(crisisLevel === 'critical' || crisisLevel === 'high') && (
          <div className="absolute top-3 right-3">
            <motion.span 
              className="flex h-4 w-4"
              animate={{ scale: [1, 1.2, 1] }}
              transition={{ duration: 1, repeat: Infinity }}
            >
              <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-white opacity-75"></span>
              <span className="relative inline-flex rounded-full h-4 w-4 bg-white"></span>
            </motion.span>
          </div>
        )}
      </motion.button>
    );
  }

  if (variant === 'floating') {
    return (
      <div className="fixed bottom-20 right-4 z-50">
        <AnimatePresence>
          {isExpanded && (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: 20 }}
              className="absolute bottom-16 right-0 space-y-2 mb-2"
            >
              {getRecommendedContacts(_crisisLevel).slice(0, 3).map((contact) => {
                const IconComponent = iconMap[contact.type === 'phone' ? 'Phone' : 'MessageSquare'];
                return (
                  <motion.button
                    key={contact.id}
                    whileTap={{ scale: 0.95 }}
                    onClick={() => handleAction(
                      contact.id === '988' ? 'call-988' : 
                      contact.id === 'crisis-text' ? 'text-home' : 'call-911'
                    )}
                    className={`
                      w-14 h-14 rounded-full shadow-lg flex items-center justify-center text-white
                      ${contact.priority === 1 ? getThemeClasses().emergency : getThemeClasses().primary}
                    `}
                    aria-label={`${contact.name}: ${contact.description}`}
                  >
                    <IconComponent className="h-6 w-6" />
                  </motion.button>
                );
              })}
            </motion.div>
          )}
        </AnimatePresence>
        
        <motion.button
          whileTap={{ scale: 0.9 }}
          onClick={() => setIsExpanded(!isExpanded)}
          onMouseDown={handleLongPressStart}
          onMouseUp={handleLongPressEnd}
          onMouseLeave={handleLongPressEnd}
          onTouchStart={handleLongPressStart}
          onTouchEnd={handleLongPressEnd}
          className={`
            ${getSizeClasses()} ${getThemeClasses().emergency} 
            rounded-full shadow-2xl flex items-center justify-center text-white relative
            transition-all duration-300 hover:scale-105
          `}
          aria-label="Emergency crisis support options"
        >
          <AlertTriangle className="h-8 w-8" />
          <motion.span 
            className="absolute -top-1 -right-1 flex h-4 w-4"
            animate={{ scale: [1, 1.3, 1] }}
            transition={{ duration: 1, repeat: Infinity }}
          >
            <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-red-400 opacity-75"></span>
            <span className="relative inline-flex rounded-full h-4 w-4 bg-red-400"></span>
          </motion.span>
        </motion.button>
      </div>
    );
  }

  if (variant === 'quick') {
    return (
      <motion.button
        whileTap={{ scale: 0.95 }}
        onClick={() => handleAction('call-988')}
        disabled={disabled || isLoading}
        className={`
          relative ${getSizeClasses()} ${getThemeClasses().primary}
          rounded-xl shadow-lg flex items-center justify-center text-white
          transition-all duration-300 
          ${disabled ? 'opacity-50 cursor-not-allowed' : 'hover:scale-105 active:scale-95'}
          ${className}
        `}
        aria-label="Quick crisis support"
      >
        <AlertTriangle className={`${size === 'sm' ? 'h-5 w-5' : 'h-6 w-6'}`} />
        
        {!isOnline && (
          <span className="absolute -top-1 -right-1 w-3 h-3 bg-yellow-500 rounded-full border-2 border-white" />
        )}
      </motion.button>
    );
  }

  if (variant === 'emergency') {
    return (
      <div className="space-y-4">
        {/* Primary Emergency Actions */}
        <motion.button
          whileTap={{ scale: 0.95 }}
          onClick={() => handleAction('call-988')}
          disabled={disabled}
          className={`
            w-full p-6 rounded-2xl shadow-lg transition-all
            ${getThemeClasses().emergency} text-white
            ${crisisLevel === 'critical' ? 'animate-pulse' : ''}
            ${disabled ? 'opacity-50 cursor-not-allowed' : 'active:shadow-inner'}
          `}
          aria-label="Call 988 Suicide & Crisis Lifeline - Primary emergency contact"
        >
          <div className="flex items-center space-x-4">
            <div className="flex-shrink-0">
              <Phone className="h-10 w-10" />
            </div>
            <div className="text-left">
              <div className="text-xl font-bold">Call 988</div>
              <div className="text-white/80 text-sm">Suicide & Crisis Lifeline â€¢ Available 24/7</div>
            </div>
          </div>
        </motion.button>

        {/* Secondary Actions Grid */}
        <div className="grid grid-cols-2 gap-4">
          <motion.button
            whileTap={{ scale: 0.95 }}
            onClick={() => handleAction('text-home')}
            disabled={disabled}
            className={`
              p-4 rounded-xl shadow transition-all
              ${getThemeClasses().primary} text-white
              ${disabled ? 'opacity-50 cursor-not-allowed' : 'hover:shadow-lg'}
            `}
            aria-label="Text HOME to 741741 for crisis support"
          >
            <MessageSquare className="h-8 w-8 mx-auto mb-2" />
            <div className="text-sm font-semibold">Text Help</div>
            <div className="text-xs text-white/80">741741</div>
          </motion.button>

          <motion.button
            whileTap={{ scale: 0.95 }}
            onClick={() => handleAction('call-911')}
            disabled={disabled}
            className={`
              p-4 rounded-xl shadow transition-all
              ${getThemeClasses().emergency} text-white
              ${disabled ? 'opacity-50 cursor-not-allowed' : 'hover:shadow-lg'}
            `}
            aria-label="Call 911 for emergency services"
          >
            <Shield className="h-8 w-8 mx-auto mb-2" />
            <div className="text-sm font-semibold">Call 911</div>
            <div className="text-xs text-white/80">Emergency</div>
          </motion.button>
        </div>

        {/* Location Status */}
        {location && (
          <div className="text-xs text-gray-600 text-center">
            ðŸ“ Location available for emergency services
          </div>
        )}
      </div>
    );
  }

  return null;
}

import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  X, ChevronRight, ChevronLeft, Check, 
  Shield, Brain, _Heart, _Users, _Target, 
  Sparkles, _Bell, _MapPin, Phone, Settings,
  _HelpCircle, Award, TrendingUp
} from 'lucide-react';
import { useAnalytics } from '../../hooks/useAnalytics';
import { useAuth } from '../../hooks/useAuth';

interface OnboardingStep {
  id: string;
  title: string;
  description: string;
  icon: React.ComponentType<unknown>;
  content: React.ReactNode;
  action?: () => void;
  actionLabel?: string;
}

interface DashboardOnboardingProps {
  onComplete: () => void;
  onSkip?: () => void;
}

export function DashboardOnboarding({ onComplete, onSkip }: DashboardOnboardingProps) {
  const { user } = useAuth();
  const { trackEvent } = useAnalytics();
  const [currentStep, setCurrentStep] = useState(0);
  const [completedSteps, setCompletedSteps] = useState<Set<string>>(new Set());
  const [showWelcome, setShowWelcome] = useState(true);

  const steps: OnboardingStep[] = [
    {
      id: 'welcome',
      title: 'Welcome to Your Personal Dashboard',
      description: 'Your mental health journey starts here',
      icon: Sparkles,
      content: (
        <div className="space-y-4">
          <p className="text-gray-600">
            Hi {user?.name?.split(' ')[0] || 'there'}! Your new dashboard is designed to support your mental wellness journey with powerful tools and insights.
          </p>
          <div className="grid grid-cols-2 gap-3">
            <div className="flex items-center space-x-2 p-3 bg-green-50 rounded-lg">
              <Check className="h-5 w-5 text-green-600" />
              <span className="text-sm">24/7 Crisis Support</span>
            </div>
            <div className="flex items-center space-x-2 p-3 bg-blue-50 rounded-lg">
              <Check className="h-5 w-5 text-blue-600" />
              <span className="text-sm">AI-Powered Insights</span>
            </div>
            <div className="flex items-center space-x-2 p-3 bg-purple-50 rounded-lg">
              <Check className="h-5 w-5 text-purple-600" />
              <span className="text-sm">Activity Tracking</span>
            </div>
            <div className="flex items-center space-x-2 p-3 bg-orange-50 rounded-lg">
              <Check className="h-5 w-5 text-orange-600" />
              <span className="text-sm">Professional Care</span>
            </div>
          </div>
        </div>
      )
    },
    {
      id: 'crisis',
      title: 'Crisis Support Always Available',
      description: 'Get help when you need it most',
      icon: Shield,
      content: (
        <div className="space-y-4">
          <div className="bg-red-50 border border-red-200 rounded-lg p-4">
            <div className="flex items-start space-x-3">
              <Phone className="h-6 w-6 text-red-600 mt-1" />
              <div>
                <h4 className="font-semibold text-red-900">Emergency Hotline: 988</h4>
                <p className="text-sm text-red-700 mt-1">
                  Available 24/7 for immediate crisis support. This number is always accessible, even offline.
                </p>
              </div>
            </div>
          </div>
          <div className="space-y-2">
            <p className="text-sm text-gray-600">Your crisis panel includes:</p>
            <ul className="space-y-1">
              <li className="flex items-center space-x-2">
                <Check className="h-4 w-4 text-green-600" />
                <span className="text-sm">Real-time risk assessment</span>
              </li>
              <li className="flex items-center space-x-2">
                <Check className="h-4 w-4 text-green-600" />
                <span className="text-sm">One-tap emergency contacts</span>
              </li>
              <li className="flex items-center space-x-2">
                <Check className="h-4 w-4 text-green-600" />
                <span className="text-sm">Location sharing for emergencies</span>
              </li>
              <li className="flex items-center space-x-2">
                <Check className="h-4 w-4 text-green-600" />
                <span className="text-sm">Personalized safety plan</span>
              </li>
            </ul>
          </div>
        </div>
      ),
      actionLabel: 'Set Up Safety Plan',
      action: () => {
        window.location.href = '/crisis/safety-plan';
      }
    },
    {
      id: 'tracking',
      title: 'Track Your Progress',
      description: 'Monitor your wellness journey',
      icon: TrendingUp,
      content: (
        <div className="space-y-4">
          <p className="text-gray-600">
            Track your mood, activities, and wellness goals all in one place.
          </p>
          <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-lg p-4">
            <div className="grid grid-cols-3 gap-4 text-center">
              <div>
                <div className="text-2xl font-bold text-purple-600">7</div>
                <div className="text-xs text-gray-600">Day Streak</div>
              </div>
              <div>
                <div className="text-2xl font-bold text-blue-600">85%</div>
                <div className="text-xs text-gray-600">Goal Progress</div>
              </div>
              <div>
                <div className="text-2xl font-bold text-green-600">+12</div>
                <div className="text-xs text-gray-600">Mood Trend</div>
              </div>
            </div>
          </div>
          <div className="flex items-center space-x-2 p-3 bg-yellow-50 rounded-lg">
            <Award className="h-5 w-5 text-yellow-600" />
            <span className="text-sm">Earn achievements as you progress!</span>
          </div>
        </div>
      ),
      actionLabel: 'Log First Mood',
      action: () => {
        window.location.href = '/wellness/mood';
      }
    },
    {
      id: 'ai-insights',
      title: 'AI-Powered Insights',
      description: 'Get personalized recommendations',
      icon: Brain,
      content: (
        <div className="space-y-4">
          <p className="text-gray-600">
            Our AI analyzes your patterns to provide personalized insights and recommendations.
          </p>
          <div className="space-y-3">
            <div className="flex items-start space-x-3 p-3 bg-purple-50 rounded-lg">
              <Sparkles className="h-5 w-5 text-purple-600 mt-0.5" />
              <div>
                <h4 className="text-sm font-semibold">Pattern Recognition</h4>
                <p className="text-xs text-gray-600 mt-1">
                  Identifies triggers and positive influences in your daily life
                </p>
              </div>
            </div>
            <div className="flex items-start space-x-3 p-3 bg-blue-50 rounded-lg">
              <Brain className="h-5 w-5 text-blue-600 mt-0.5" />
              <div>
                <h4 className="text-sm font-semibold">Smart Recommendations</h4>
                <p className="text-xs text-gray-600 mt-1">
                  Suggests activities and coping strategies based on your needs
                </p>
              </div>
            </div>
            <div className="flex items-start space-x-3 p-3 bg-green-50 rounded-lg">
              <TrendingUp className="h-5 w-5 text-green-600 mt-0.5" />
              <div>
                <h4 className="text-sm font-semibold">Progress Predictions</h4>
                <p className="text-xs text-gray-600 mt-1">
                  Forecasts your wellness trajectory and celebrates achievements
                </p>
              </div>
            </div>
          </div>
        </div>
      )
    },
    {
      id: 'customize',
      title: 'Make It Yours',
      description: 'Customize your dashboard',
      icon: Settings,
      content: (
        <div className="space-y-4">
          <p className="text-gray-600">
            Personalize your dashboard to match your needs and preferences.
          </p>
          <div className="space-y-3">
            <div className="p-3 border border-gray-200 rounded-lg">
              <h4 className="text-sm font-semibold mb-2">Widget Layout</h4>
              <p className="text-xs text-gray-600">
                Drag and drop widgets to arrange your perfect dashboard
              </p>
            </div>
            <div className="p-3 border border-gray-200 rounded-lg">
              <h4 className="text-sm font-semibold mb-2">Notification Preferences</h4>
              <p className="text-xs text-gray-600">
                Choose when and how you want to receive reminders
              </p>
            </div>
            <div className="p-3 border border-gray-200 rounded-lg">
              <h4 className="text-sm font-semibold mb-2">Privacy Settings</h4>
              <p className="text-xs text-gray-600">
                Control your data sharing and visibility preferences
              </p>
            </div>
          </div>
        </div>
      ),
      actionLabel: 'Open Settings',
      action: () => {
        window.location.href = '/settings';
      }
    }
  ];

  useEffect(() => {
    trackEvent('onboarding_started', {
      userId: user?.id,
      totalSteps: steps.length
    });
  }, [trackEvent, user?.id, steps.length]);

  const handleNext = () => {
    const step = steps[currentStep];
    if (!step) return;

    setCompletedSteps(_prev => new Set(_prev).add(step.id));

    trackEvent('onboarding_step_completed', {
      stepId: step.id,
      stepIndex: currentStep
    });

    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    } else {
      handleComplete();
    }
  };

  const handlePrevious = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleComplete = () => {
    localStorage.setItem('dashboard_onboarding_completed', 'true');
    trackEvent('onboarding_completed', {
      userId: user?.id,
      _completedSteps: Array.from(_completedSteps)
    });
    onComplete();
  };

  const handleSkip = () => {
    trackEvent('onboarding_skipped', {
      userId: user?.id,
      skippedAtStep: currentStep
    });
    if (_onSkip) {
      onSkip();
    } else {
      handleComplete();
    }
  };

  const currentStepData = steps[currentStep];
  const progress = ((currentStep + 1) / steps.length) * 100;

  // Early return if no current step data
  if (!currentStepData) {
    return null;
  }

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50"
      >
        <motion.div
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          exit={{ scale: 0.9, opacity: 0 }}
          className="bg-white rounded-xl shadow-2xl max-w-lg w-full max-h-[90vh] overflow-hidden"
        >
          {/* Header */}
          <div className="relative bg-gradient-to-r from-primary-500 to-purple-600 p-6 text-white">
            <button
              onClick={handleSkip}
              className="absolute top-4 right-4 p-1 hover:bg-white/20 rounded-lg transition-colors"
              aria-label="Skip onboarding"
            >
              <X className="h-5 w-5" />
            </button>

            <div className="flex items-center space-x-3">
              <div className="p-3 bg-white/20 rounded-lg">
                <currentStepData.icon className="h-6 w-6" />
              </div>
              <div>
                <h2 className="text-xl font-bold">{currentStepData.title}</h2>
                <p className="text-sm text-white/80">{currentStepData.description}</p>
              </div>
            </div>

            {/* Progress bar */}
            <div className="mt-4">
              <div className="flex justify-between text-xs mb-1">
                <span>Step {currentStep + 1} of {steps.length}</span>
                <span>{Math.round(progress)}%</span>
              </div>
              <div className="w-full bg-white/20 rounded-full h-2">
                <motion.div
                  className="bg-white rounded-full h-2"
                  initial={{ width: 0 }}
                  animate={{ width: `${progress}%` }}
                  transition={{ duration: 0.3 }}
                />
              </div>
            </div>
          </div>

          {/* Content */}
          <div className="p-6 max-h-[400px] overflow-y-auto">
            <AnimatePresence mode="wait">
              <motion.div
                key={currentStep}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
              >
                {currentStepData.content}
              </motion.div>
            </AnimatePresence>
          </div>

          {/* Footer */}
          <div className="border-t border-gray-200 p-6 bg-gray-50">
            <div className="flex justify-between items-center">
              <button
                onClick={currentStep === 0 ? handleSkip : handlePrevious}
                className="px-4 py-2 text-gray-600 hover:text-gray-900 transition-colors flex items-center space-x-1"
              >
                <ChevronLeft className="h-4 w-4" />
                <span>{currentStep === 0 ? 'Skip' : 'Back'}</span>
              </button>

              <div className="flex space-x-3">
                {currentStepData.action && (
                  <button
                    onClick={currentStepData.action}
                    className="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition-colors"
                  >
                    {currentStepData.actionLabel}
                  </button>
                )}

                <button
                  onClick={handleNext}
                  className="px-6 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 transition-colors flex items-center space-x-1"
                >
                  <span>{currentStep === steps.length - 1 ? 'Get Started' : 'Next'}</span>
                  <ChevronRight className="h-4 w-4" />
                </button>
              </div>
            </div>
          </div>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
}

