import { useState, useMemo } from 'react';
import { motion } from 'framer-motion';
import {
  TrendingUp,
  TrendingDown,
  Activity,
  _Calendar,
  BarChart3,
  _PieChart,
  Clock,
  Zap,
  Heart,
  Brain,
  Users,
  X,
  Sun,
  Moon,
  Cloud,
  Award,
  AlertCircle,
  _Filter,
  Download,
  ChevronLeft,
  ChevronRight,
  _Info
} from 'lucide-react';
import { useActivityStore } from '../../../stores/activityStore';
import { format, startOfWeek, _endOfWeek, eachDayOfInterval, subDays, addDays, isToday } from 'date-fns';

interface ActivityAnalyticsProps {
  _onExportData?: () => void;
  _onViewDetails?: (activityId: string) => void;
}

export function ActivityAnalytics({
  _onExportData,
  _onViewDetails
}: ActivityAnalyticsProps) {
  const {
    activityHistory,
    _activities,
    _goals,
    _habits,
    correlateActivitiesWithMood,
    _analyzeActivityEffectiveness,
    exportProgressReport
  } = useActivityStore();

  const [selectedPeriod, setSelectedPeriod] = useState<'week' | 'month' | 'year'>('week');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [currentDate, setCurrentDate] = useState(new Date());
  const [showInsights, setShowInsights] = useState(true);

  // Get date range based on selected period
  const getDateRange = () => {
    const end = currentDate;
    let start;

    switch (_selectedPeriod) {
      case 'week':
        start = startOfWeek(currentDate);
        break;
      case 'month':
        start = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
        break;
      case 'year':
        start = new Date(currentDate.getFullYear(), 0, 1);
        break;
      default:
        start = subDays(currentDate, 7);
    }

    return { start, end };
  };

  // Filter activities by date range and category
  const filteredActivities = useMemo(() => {
    const { start, end } = getDateRange();

    return activityHistory.filter(activity => {
      const activityDate = activity.completedAt ? new Date(activity.completedAt) : null;
      if (!activityDate) return false;

      const inDateRange = activityDate >= start && activityDate <= end;
      const inCategory = selectedCategory === 'all' || activity.category === selectedCategory;

      return inDateRange && inCategory;
    });
  }, [activityHistory, selectedPeriod, selectedCategory, currentDate]);

  // Calculate statistics
  const stats = useMemo(() => {
    const completed = filteredActivities.filter(a => a.completed).length;
    const total = filteredActivities.length;
    const completionRate = total > 0 ? (completed / total) * 100 : 0;

    const moodImpacts = filteredActivities
      .filter(a => a.actualMoodImpact !== undefined)
      .map(a => a.actualMoodImpact!);

    const avgMoodImpact = moodImpacts.length > 0
      ? moodImpacts.reduce((sum, impact) => sum + impact, 0) / moodImpacts.length
      : 0;

    const totalTime = filteredActivities
      .filter(a => a.duration)
      .reduce((sum, a) => sum + (a.duration || 0), 0);

    // Category breakdown
    const categoryBreakdown = new Map<string, number>();
    filteredActivities.forEach(activity => {
      const count = categoryBreakdown.get(activity.category) || 0;
      categoryBreakdown.set(activity.category, count + 1);
    });

    // Time of day analysis
    const timeOfDayBreakdown = {
      morning: 0, // 6-12
      afternoon: 0, // 12-18
      evening: 0, // 18-24
      night: 0 // 0-6
    };

    filteredActivities.forEach(activity => {
      if (activity.scheduledTime) {
        const hour = new Date(activity.scheduledTime).getHours();
        if (hour >= 6 && hour < 12) timeOfDayBreakdown.morning++;
        else if (hour >= 12 && hour < 18) timeOfDayBreakdown.afternoon++;
        else if (hour >= 18 && hour < 24) timeOfDayBreakdown.evening++;
        else timeOfDayBreakdown.night++;
      }
    });

    // Energy level analysis
    const energyBreakdown = {
      low: filteredActivities.filter(a => a.energyLevel === 'low').length,
      medium: filteredActivities.filter(a => a.energyLevel === 'medium').length,
      high: filteredActivities.filter(a => a.energyLevel === 'high').length,
    };

    return {
      completed,
      total,
      completionRate,
      avgMoodImpact,
      totalTime,
      categoryBreakdown,
      timeOfDayBreakdown,
      energyBreakdown
    };
  }, [filteredActivities]);

  // Get mood correlations
  const moodCorrelations = useMemo(() => {
    return correlateActivitiesWithMood().slice(0, 5);
  }, [correlateActivitiesWithMood]);

  // Get daily activity data for chart
  const dailyData = useMemo(() => {
    const { start, end } = getDateRange();
    const days = eachDayOfInterval({ start, end });

    return days.map(day => {
      const dayActivities = filteredActivities.filter(activity => {
        if (!activity.completedAt) return false;
        const activityDate = new Date(activity.completedAt);
        return activityDate.toDateString() === day.toDateString();
      });

      const completed = dayActivities.filter(a => a.completed).length;
      const moodImpacts = dayActivities
        .filter(a => a.actualMoodImpact !== undefined)
        .map(a => a.actualMoodImpact!);

      const avgMood = moodImpacts.length > 0
        ? moodImpacts.reduce((sum, impact) => sum + impact, 0) / moodImpacts.length
        : 0;

      return {
        date: day,
        completed,
        avgMood,
        isToday: isToday(_day)
      };
    });
  }, [filteredActivities, selectedPeriod, currentDate]);

  // Get insights
  const insights = useMemo(() => {
    const insights = [];

    // Completion rate insight
    if (stats.completionRate >= 80) {
      insights.push({
        type: 'success',
        title: 'Excellent Completion Rate',
        message: `You&apos;re completing ${Math.round(stats.completionRate)}% of your activities!`,
        icon: Award
      });
    } else if (stats.completionRate < 50) {
      insights.push({
        type: 'warning',
        title: 'Low Completion Rate',
        message: 'Consider reducing _activities or adjusting your schedule',
        icon: AlertCircle
      });
    }

    // Mood impact insight
    if (stats.avgMoodImpact > 2) {
      insights.push({
        type: 'success',
        title: 'Positive Mood Impact',
        message: 'Your _activities are significantly improving your mood',
        icon: Heart
      });
    } else if (stats.avgMoodImpact < -1) {
      insights.push({
        type: 'warning',
        title: 'Negative Mood Trend',
        message: 'Some _activities may be affecting your mood negatively',
        icon: TrendingDown
      });
    }

    // Time of day insight
    const maxTimeOfDay = Object.entries(stats.timeOfDayBreakdown)
      .reduce((max, [time, count]) => count > max.count ? { time, count } : max, { time: '', count: 0 });

    if (maxTimeOfDay.count > 0) {
      insights.push({
        type: 'info',
        title: 'Peak Activity Time',
        message: `You&apos;re most active in the ${maxTimeOfDay.time}`,
        icon: Clock
      });
    }

    // Energy distribution insight
    if (stats.energyBreakdown.low > stats.energyBreakdown.high * 2) {
      insights.push({
        type: 'info',
        title: 'Low Energy Pattern',
        message: 'Most _activities are low energy - consider adding energizing _activities',
        icon: Zap
      });
    }

    return insights;
  }, [stats]);

  // Navigate dates
  const navigateDate = (direction: 'prev' | 'next') => {
    const days = selectedPeriod === 'week' ? 7 : selectedPeriod === 'month' ? 30 : 365;
    setCurrentDate(prev => direction === 'prev' ? subDays(prev, days) : addDays(prev, days));
  };

  // Export data
  const handleExport = () => {
    const report = exportProgressReport();
    const _blob = new Blob([report], { type: 'application/json' });
    const url = URL.createObjectURL(_blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `activity-report-${format(new Date(), 'yyyy-MM-dd')}.json`;
    a.click();
    URL.revokeObjectURL(_url);
  };

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="mb-4">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-primary-100 rounded-lg">
              <BarChart3 className="h-5 w-5 text-primary-600" />
            </div>
            <div>
              <h3 className="font-semibold text-gray-900">Activity Analytics</h3>
              <p className="text-sm text-gray-600">
                {format(getDateRange().start, 'MMM d')} - {format(getDateRange().end, 'MMM d, yyyy')}
              </p>
            </div>
          </div>

          <button
            onClick={handleExport}
            className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
            aria-label="Export data"
          >
            <Download className="h-5 w-5 text-gray-600" />
          </button>
        </div>

        {/* Period Selection & Navigation */}
        <div className="flex items-center justify-between mb-3">
          <div className="flex space-x-2">
            {(['week', 'month', 'year'] as const).map((period) => (
              <button
                key={period}
                onClick={() => setSelectedPeriod(_period)}
                className={`px-3 py-1 rounded-lg text-sm font-medium transition-colors ${
                  selectedPeriod === period
                    ? 'bg-primary-100 text-primary-700'
                    : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
              >
                {period.charAt(0).toUpperCase() + period.slice(1)}
              </button>
            ))}
          </div>

          <div className="flex items-center space-x-2">
            <button
              onClick={() => navigateDate('prev')}
              className="p-1 hover:bg-gray-100 rounded transition-colors"
            >
              <ChevronLeft className="h-4 w-4 text-gray-600" />
            </button>
            <button
              onClick={() => setCurrentDate(new Date())}
              className="px-2 py-1 text-xs bg-gray-100 hover:bg-gray-200 rounded transition-colors"
            >
              Today
            </button>
            <button
              onClick={() => navigateDate('next')}
              className="p-1 hover:bg-gray-100 rounded transition-colors"
            >
              <ChevronRight className="h-4 w-4 text-gray-600" />
            </button>
          </div>
        </div>

        {/* Key Metrics */}
        <div className="grid grid-cols-4 gap-2 mb-3">
          <div className="p-2 bg-blue-50 rounded-lg text-center">
            <div className="text-lg font-bold text-blue-600">{stats.completed}</div>
            <div className="text-xs text-blue-700">Completed</div>
          </div>
          <div className="p-2 bg-green-50 rounded-lg text-center">
            <div className="text-lg font-bold text-green-600">
              {Math.round(stats.completionRate)}%
            </div>
            <div className="text-xs text-green-700">Success Rate</div>
          </div>
          <div className="p-2 bg-purple-50 rounded-lg text-center">
            <div className="text-lg font-bold text-purple-600">
              {stats.avgMoodImpact > 0 ? '+' : ''}{stats.avgMoodImpact.toFixed(1)}
            </div>
            <div className="text-xs text-purple-700">Avg Mood</div>
          </div>
          <div className="p-2 bg-orange-50 rounded-lg text-center">
            <div className="text-lg font-bold text-orange-600">
              {Math.round(stats.totalTime / 60)}h
            </div>
            <div className="text-xs text-orange-700">Total Time</div>
          </div>
        </div>

        {/* Category _Filter */}
        <div className="flex space-x-2 overflow-x-auto pb-2">
          <button
            onClick={() => setSelectedCategory('all')}
            className={`px-3 py-1 rounded-full text-xs font-medium whitespace-nowrap transition-colors ${
              selectedCategory === 'all'
                ? 'bg-primary-100 text-primary-700'
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
            }`}
          >
            All ({stats.total})
          </button>
          {Array.from(stats.categoryBreakdown.entries()).map(([category, count]) => (
            <button
              key={category}
              onClick={() => setSelectedCategory(_category)}
              className={`px-3 py-1 rounded-full text-xs font-medium whitespace-nowrap transition-colors ${
                selectedCategory === category
                  ? 'bg-primary-100 text-primary-700'
                  : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
              }`}
            >
              {category.charAt(0).toUpperCase() + category.slice(1)} ({count})
            </button>
          ))}
        </div>
      </div>

      {/* Charts Section */}
      <div className="flex-1 overflow-y-auto space-y-4">
        {/* Daily Activity Chart */}
        <div className="bg-white p-4 rounded-lg border border-gray-200">
          <h4 className="font-medium text-gray-900 mb-3">Daily Activity Pattern</h4>
          <div className="space-y-2">
            {dailyData.map((day, index) => (
              <div key={index} className="flex items-center space-x-3">
                <div className="w-16 text-xs text-gray-600">
                  {format(day.date, 'EEE, MMM d')}
                </div>
                <div className="flex-1">
                  <div className="flex items-center space-x-2">
                    <div className="flex-1 bg-gray-100 rounded-full h-6 relative">
                      <motion.div
                        className="bg-gradient-to-r from-primary-500 to-primary-600 h-6 rounded-full flex items-center justify-end pr-2"
                        initial={{ width: 0 }}
                        animate={{ width: `${(day.completed / Math.max(...dailyData.map(d => d.completed), 1)) * 100}%` }}
                        transition={{ duration: 0.5, delay: index * 0.05 }}
                      >
                        <span className="text-xs text-white font-medium">{day.completed}</span>
                      </motion.div>
                    </div>
                    {day.avgMood !== 0 && (
                      <span className={`text-xs font-medium ${
                        day.avgMood > 0 ? 'text-green-600' : 'text-red-600'
                      }`}>
                        {day.avgMood > 0 ? '+' : ''}{day.avgMood.toFixed(1)}
                      </span>
                    )}
                    {day.isToday && (
                      <span className="px-2 py-0.5 bg-primary-100 text-primary-700 text-xs rounded-full">
                        Today
                      </span>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Category Distribution */}
        <div className="bg-white p-4 rounded-lg border border-gray-200">
          <h4 className="font-medium text-gray-900 mb-3">Category Distribution</h4>
          <div className="space-y-2">
            {Array.from(stats.categoryBreakdown.entries()).map(([category, count]) => {
              const _percentage = (count / stats.total) * 100;
              const getCategoryIcon = () => {
                switch (_category) {
                  case 'therapy': return Brain;
                  case 'wellness': return Heart;
                  case 'social': return Users;
                  case 'professional': return Activity;
                  default: return Zap;
                }
              };
              const Icon = getCategoryIcon();

              return (
                <div key={category} className="flex items-center space-x-3">
                  <Icon className="h-4 w-4 text-gray-500" />
                  <div className="w-20 text-sm text-gray-600">
                    {category.charAt(0).toUpperCase() + category.slice(1)}
                  </div>
                  <div className="flex-1">
                    <div className="bg-gray-100 rounded-full h-4 relative">
                      <motion.div
                        className={`h-4 rounded-full ${
                          category === 'therapy' ? 'bg-purple-500' :
                          category === 'wellness' ? 'bg-green-500' :
                          category === 'social' ? 'bg-blue-500' :
                          category === 'professional' ? 'bg-orange-500' :
                          'bg-gray-500'
                        }`}
                        initial={{ width: 0 }}
                        animate={{ width: `${_percentage}%` }}
                        transition={{ duration: 0.5 }}
                      />
                    </div>
                  </div>
                  <span className="text-sm text-gray-700 font-medium">
                    {count} ({Math.round(_percentage)}%)
                  </span>
                </div>
              );
            })}
          </div>
        </div>

        {/* Time of Day Analysis */}
        <div className="bg-white p-4 rounded-lg border border-gray-200">
          <h4 className="font-medium text-gray-900 mb-3">Activity by Time of Day</h4>
          <div className="grid grid-cols-4 gap-2">
            {Object.entries(stats.timeOfDayBreakdown).map(([time, count]) => {
              const getTimeIcon = () => {
                switch (_time) {
                  case 'morning': return Sun;
                  case 'afternoon': return Sun;
                  case 'evening': return Cloud;
                  case 'night': return Moon;
                  default: return Clock;
                }
              };
              const Icon = getTimeIcon();
              const _percentage = stats.total > 0 ? (count / stats.total) * 100 : 0;

              return (
                <div key={time} className="text-center p-2 bg-gray-50 rounded-lg">
                  <Icon className="h-6 w-6 text-gray-500 mx-auto mb-1" />
                  <div className="text-xs text-gray-600 mb-1">
                    {time.charAt(0).toUpperCase() + time.slice(1)}
                  </div>
                  <div className="text-lg font-bold text-gray-900">{count}</div>
                  <div className="text-xs text-gray-500">{Math.round(_percentage)}%</div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Energy Level Distribution */}
        <div className="bg-white p-4 rounded-lg border border-gray-200">
          <h4 className="font-medium text-gray-900 mb-3">Energy Level Distribution</h4>
          <div className="space-y-2">
            {Object.entries(stats.energyBreakdown).map(([level, count]) => {
              const _percentage = stats.total > 0 ? (count / stats.total) * 100 : 0;
              const getColor = () => {
                switch (_level) {
                  case 'low': return 'bg-blue-500';
                  case 'medium': return 'bg-yellow-500';
                  case 'high': return 'bg-red-500';
                  default: return 'bg-gray-500';
                }
              };

              return (
                <div key={level} className="flex items-center space-x-3">
                  <Zap className={`h-4 w-4 ${
                    level === 'low' ? 'text-blue-500' :
                    level === 'medium' ? 'text-yellow-500' :
                    'text-red-500'
                  }`} />
                  <div className="w-16 text-sm text-gray-600">
                    {level.charAt(0).toUpperCase() + level.slice(1)}
                  </div>
                  <div className="flex-1">
                    <div className="bg-gray-100 rounded-full h-4 relative">
                      <motion.div
                        className={`h-4 rounded-full ${getColor()}`}
                        initial={{ width: 0 }}
                        animate={{ width: `${_percentage}%` }}
                        transition={{ duration: 0.5 }}
                      />
                    </div>
                  </div>
                  <span className="text-sm text-gray-700 font-medium">
                    {count} ({Math.round(_percentage)}%)
                  </span>
                </div>
              );
            })}
          </div>
        </div>

        {/* Mood Correlations */}
        {moodCorrelations.length > 0 && (
          <div className="bg-white p-4 rounded-lg border border-gray-200">
            <h4 className="font-medium text-gray-900 mb-3">Activities with Strongest Mood Impact</h4>
            <div className="space-y-2">
              {moodCorrelations.map((correlation, index) => (
                <div key={index} className="flex items-center justify-between p-2 bg-gray-50 rounded-lg">
                  <span className="text-sm text-gray-700">{correlation.activity}</span>
                  <div className="flex items-center space-x-2">
                    {correlation.correlation > 0 ? (
                      <TrendingUp className="h-4 w-4 text-green-500" />
                    ) : (
                      <TrendingDown className="h-4 w-4 text-red-500" />
                    )}
                    <span className={`text-sm font-medium ${
                      correlation.correlation > 0 ? 'text-green-600' : 'text-red-600'
                    }`}>
                      {correlation.correlation > 0 ? '+' : ''}{correlation.correlation.toFixed(1)}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Insights */}
        {showInsights && insights.length > 0 && (
          <div className="bg-white p-4 rounded-lg border border-gray-200">
            <div className="flex items-center justify-between mb-3">
              <h4 className="font-medium text-gray-900">Insights & Recommendations</h4>
              <button
                onClick={() => setShowInsights(false)}
                className="text-gray-400 hover:text-gray-600"
              >
                <X className="h-4 w-4" />
              </button>
            </div>
            <div className="space-y-2">
              {insights.map((insight, index) => {
                const Icon = insight.icon;
                return (
                  <div
                    key={index}
                    className={`p-3 rounded-lg flex items-start space-x-3 ${
                      insight.type === 'success' ? 'bg-green-50' :
                      insight.type === 'warning' ? 'bg-yellow-50' :
                      'bg-blue-50'
                    }`}
                  >
                    <Icon className={`h-5 w-5 mt-0.5 ${
                      insight.type === 'success' ? 'text-green-600' :
                      insight.type === 'warning' ? 'text-yellow-600' :
                      'text-blue-600'
                    }`} />
                    <div>
                      <h5 className={`font-medium text-sm ${
                        insight.type === 'success' ? 'text-green-800' :
                        insight.type === 'warning' ? 'text-yellow-800' :
                        'text-blue-800'
                      }`}>
                        {insight.title}
                      </h5>
                      <p className={`text-xs mt-0.5 ${
                        insight.type === 'success' ? 'text-green-700' :
                        insight.type === 'warning' ? 'text-yellow-700' :
                        'text-blue-700'
                      }`}>
                        {insight.message}
                      </p>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Calendar,
  CheckCircle2,
  Circle,
  Clock,
  Activity as ActivityIcon,
  Zap,
  Heart,
  Brain,
  Users,
  Target,
  TrendingUp,
  AlertCircle,
  ChevronRight,
  Plus,
  _Edit2,
  _X,
  Star,
  Award
} from 'lucide-react';
import { useActivityStore } from '../../../stores/activityStore';
import { format, _isToday, isPast, _isFuture, _differenceInMinutes, addDays } from 'date-fns';

interface ActivityTrackerProps {
  onActivityClick?: (activity: unknown) => void;
  onAddActivity?: () => void;
  energyLevel?: 'low' | 'medium' | 'high';
  currentMood?: number;
}

export function ActivityTracker({ 
  onActivityClick, 
  onAddActivity,
  energyLevel = 'medium',
  currentMood = 5 
}: ActivityTrackerProps) {
  const {
    activities,
    _dailySchedule,
    completeActivity,
    rescheduleActivity,
    getActivityRecommendations,
    suggestReschedule,
    adaptScheduleForBadDay,
    generateDailySchedule
  } = useActivityStore();

  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showRecommendations, setShowRecommendations] = useState(false);
  const [editingActivity, setEditingActivity] = useState<string | null>(null);
  const [completionNote, setCompletionNote] = useState('');
  const [moodImpact, setMoodImpact] = useState<number>(0);
  const [adaptedForLowEnergy, setAdaptedForLowEnergy] = useState(false);

  // Generate daily schedule on mount and date change
  useEffect(() => {
    generateDailySchedule(_selectedDate);
  }, [selectedDate, generateDailySchedule]);

  // Get today's activities
  const todayActivities = activities.filter(activity => {
    if (!activity.scheduledTime) return false;
    const activityDate = new Date(activity.scheduledTime);
    return activityDate.toDateString() === selectedDate.toDateString();
  }).sort((a, b) => {
    if (!a.scheduledTime || !b.scheduledTime) return 0;
    return new Date(a.scheduledTime).getTime() - new Date(b.scheduledTime).getTime();
  });

  // Get recommended activities based on energy and mood
  const recommendations = getActivityRecommendations(energyLevel, currentMood);
  const rescheduleSuggestions = suggestReschedule(energyLevel);

  // Calculate completion stats
  const completedCount = todayActivities.filter(a => a.completed).length;
  const totalCount = todayActivities.length;
  const completionPercentage = totalCount > 0 ? (completedCount / totalCount) * 100 : 0;

  // Get activity icon based on category
  const getActivityIcon = (_category: string) => {
    switch (_category) {
      case 'therapy': return Brain;
      case 'wellness': return Heart;
      case 'social': return Users;
      case 'professional': return Target;
      case 'self-care': return ActivityIcon;
      default: return Circle;
    }
  };

  // Get energy indicator color
  const getEnergyColor = (_level: string) => {
    switch (_level) {
      case 'low': return 'text-blue-500 bg-blue-50';
      case 'medium': return 'text-yellow-500 bg-yellow-50';
      case 'high': return 'text-red-500 bg-red-50';
      default: return 'text-gray-500 bg-gray-50';
    }
  };

  // Get mood impact color
  const getMoodImpactColor = (impact: number) => {
    if (impact > 2) return 'text-green-600';
    if (impact > 0) return 'text-green-500';
    if (impact < -2) return 'text-red-600';
    if (impact < 0) return 'text-red-500';
    return 'text-gray-500';
  };

  // Handle activity completion
  const handleCompleteActivity = (activityId: string) => {
    completeActivity(activityId, moodImpact || undefined, completionNote || undefined);
    setEditingActivity(null);
    setCompletionNote('');
    setMoodImpact(0);
  };

  // Handle reschedule
  const handleReschedule = (activityId: string, newDate: Date) => {
    rescheduleActivity(activityId, newDate);
  };

  // Handle adapt for low energy
  const handleAdaptForLowEnergy = () => {
    adaptScheduleForBadDay();
    setAdaptedForLowEnergy(true);
    setTimeout(() => setAdaptedForLowEnergy(false), 3000);
  };

  return (
    <div className="h-full flex flex-col">
      {/* Header with Stats */}
      <div className="mb-4">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-primary-100 rounded-lg">
              <ActivityIcon className="h-5 w-5 text-primary-600" />
            </div>
            <div>
              <h3 className="font-semibold text-gray-900">Today's Activities</h3>
              <p className="text-sm text-gray-600">
                {completedCount} of {totalCount} completed
              </p>
            </div>
          </div>

          <button
            onClick={onAddActivity}
            className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
            aria-label="Add activity"
          >
            <Plus className="h-5 w-5 text-gray-600" />
          </button>
        </div>

        {/* Progress Bar */}
        <div className="w-full bg-gray-200 rounded-full h-2 mb-3">
          <motion.div
            className="bg-gradient-to-r from-primary-500 to-primary-600 h-2 rounded-full"
            initial={{ width: 0 }}
            animate={{ width: `${completionPercentage}%` }}
            transition={{ duration: 0.5, ease: "easeOut" }}
          />
        </div>

        {/* Energy Level Indicator */}
        <div className="flex items-center justify-between text-sm">
          <div className="flex items-center space-x-2">
            <Zap className="h-4 w-4 text-gray-500" />
            <span className="text-gray-600">Energy Level:</span>
            <span className={`px-2 py-1 rounded-full text-xs font-medium ${getEnergyColor(energyLevel)}`}>
              {energyLevel.charAt(0).toUpperCase() + energyLevel.slice(1)}
            </span>
          </div>

          {energyLevel === 'low' && !adaptedForLowEnergy && (
            <button
              onClick={handleAdaptForLowEnergy}
              className="text-xs text-primary-600 hover:text-primary-700 font-medium"
            >
              Adapt for low energy
            </button>
          )}

          {adaptedForLowEnergy && (
            <span className="text-xs text-green-600 font-medium">
              Schedule adapted âœ“
            </span>
          )}
        </div>
      </div>

      {/* Activity List */}
      <div className="flex-1 overflow-y-auto space-y-2 mb-3">
        <AnimatePresence mode="popLayout">
          {todayActivities.length === 0 ? (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="text-center py-8"
            >
              <Calendar className="h-12 w-12 text-gray-300 mx-auto mb-3" />
              <p className="text-gray-500 mb-3">No activities scheduled for today</p>
              <button
                onClick={onAddActivity}
                className="text-primary-600 hover:text-primary-700 font-medium text-sm"
              >
                Add your first activity
              </button>
            </motion.div>
          ) : (
            todayActivities.map((activity) => {
              const Icon = getActivityIcon(activity._category);
              const isPastDue = activity.scheduledTime && isPast(new Date(activity.scheduledTime)) && !activity.completed;
              const isEditing = editingActivity === activity.id;

              return (
                <motion.div
                  key={activity.id}
                  layout
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                  className={`
                    p-3 rounded-lg border transition-all cursor-pointer
                    ${activity.completed 
                      ? 'bg-green-50 border-green-200' 
                      : isPastDue
                      ? 'bg-red-50 border-red-200'
                      : 'bg-white border-gray-200 hover:border-primary-300'
                    }
                    ${isEditing ? 'ring-2 ring-primary-500' : ''}
                  `}
                  onClick={() => !isEditing && onActivityClick?.(activity)}
                >
                  <div className="flex items-start space-x-3">
                    {/* Completion Checkbox */}
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        if (!activity.completed) {
                          setEditingActivity(activity.id);
                        }
                      }}
                      className="mt-0.5"
                    >
                      {activity.completed ? (
                        <CheckCircle2 className="h-5 w-5 text-green-600" />
                      ) : (
                        <Circle className="h-5 w-5 text-gray-400 hover:text-primary-600" />
                      )}
                    </button>

                    {/* Activity Details */}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-start justify-between">
                        <div className="flex-1">
                          <div className="flex items-center space-x-2">
                            <Icon className="h-4 w-4 text-gray-500" />
                            <h4 className={`font-medium ${activity.completed ? 'line-through text-gray-500' : 'text-gray-900'}`}>
                              {activity.title}
                            </h4>
                            {activity.therapyHomework && (
                              <span className="px-2 py-0.5 bg-purple-100 text-purple-700 text-xs rounded-full">
                                Therapy
                              </span>
                            )}
                          </div>

                          {activity.description && (
                            <p className="text-sm text-gray-600 mt-1">{activity.description}</p>
                          )}

                          <div className="flex items-center space-x-3 mt-2 text-xs">
                            {activity.scheduledTime && (
                              <span className="flex items-center text-gray-500">
                                <Clock className="h-3 w-3 mr-1" />
                                {format(new Date(activity.scheduledTime), 'h:mm a')}
                              </span>
                            )}

                            {activity.duration && (
                              <span className="text-gray-500">
                                {activity.duration} min
                              </span>
                            )}

                            {activity.energyLevel && (
                              <span className={`px-2 py-0.5 rounded-full text-xs ${getEnergyColor(activity.energyLevel)}`}>
                                {activity.energyLevel} energy
                              </span>
                            )}

                            {activity.moodImpact && (
                              <span className={`flex items-center ${getMoodImpactColor(activity.moodImpact)}`}>
                                <TrendingUp className="h-3 w-3 mr-1" />
                                {activity.moodImpact > 0 ? '+' : ''}{activity.moodImpact}
                              </span>
                            )}
                          </div>

                          {/* Actual mood impact if completed */}
                          {activity.completed && activity.actualMoodImpact !== undefined && (
                            <div className="mt-2 flex items-center space-x-1 text-xs">
                              <span className="text-gray-500">Mood impact:</span>
                              <span className={`font-medium ${getMoodImpactColor(activity.actualMoodImpact)}`}>
                                {activity.actualMoodImpact > 0 ? '+' : ''}{activity.actualMoodImpact}
                              </span>
                              {activity.actualMoodImpact > activity.moodImpact! && (
                                <Star className="h-3 w-3 text-yellow-500" />
                              )}
                            </div>
                          )}
                        </div>

                        {/* Actions */}
                        {!activity.completed && isPastDue && (
                          <div className="flex items-center space-x-1">
                            <AlertCircle className="h-4 w-4 text-red-500" />
                            <button
                              onClick={(e) => {
                                e.stopPropagation();
                                handleReschedule(activity.id, addDays(new Date(), 1));
                              }}
                              className="text-xs text-red-600 hover:text-red-700 font-medium"
                            >
                              Reschedule
                            </button>
                          </div>
                        )}
                      </div>

                      {/* Completion Form */}
                      {isEditing && (
                        <motion.div
                          initial={{ opacity: 0, height: 0 }}
                          animate={{ opacity: 1, height: 'auto' }}
                          exit={{ opacity: 0, height: 0 }}
                          className="mt-3 p-3 bg-gray-50 rounded-lg"
                          onClick={(e) => e.stopPropagation()}
                        >
                          <div className="space-y-3">
                            <div>
                              <label className="text-sm font-medium text-gray-700">
                                How did this activity affect your mood?
                              </label>
                              <div className="flex items-center space-x-2 mt-1">
                                {[-3, -2, -1, 0, 1, 2, 3].map((impact) => (
                                  <button
                                    key={impact}
                                    onClick={() => setMoodImpact(_impact)}
                                    className={`
                                      px-3 py-1 rounded-lg text-sm font-medium transition-colors
                                      ${moodImpact === impact 
                                        ? impact > 0 
                                          ? 'bg-green-500 text-white' 
                                          : impact < 0
                                          ? 'bg-red-500 text-white'
                                          : 'bg-gray-500 text-white'
                                        : 'bg-white border border-gray-300 hover:border-gray-400'
                                      }
                                    `}
                                  >
                                    {impact > 0 ? '+' : ''}{impact}
                                  </button>
                                ))}
                              </div>
                            </div>

                            <div>
                              <label className="text-sm font-medium text-gray-700">
                                Notes (_optional)
                              </label>
                              <textarea
                                value={completionNote}
                                onChange={(e) => setCompletionNote(e.target.value)}
                                className="w-full mt-1 px-3 py-2 border border-gray-300 rounded-lg text-sm resize-none"
                                rows={2}
                                placeholder="How did it go? Any observations?"
                              />
                            </div>

                            <div className="flex justify-end space-x-2">
                              <button
                                onClick={() => {
                                  setEditingActivity(null);
                                  setCompletionNote('');
                                  setMoodImpact(0);
                                }}
                                className="px-3 py-1 text-sm text-gray-600 hover:text-gray-700"
                              >
                                Cancel
                              </button>
                              <button
                                onClick={() => handleCompleteActivity(activity.id)}
                                className="px-3 py-1 bg-primary-500 text-white text-sm rounded-lg hover:bg-primary-600"
                              >
                                Complete
                              </button>
                            </div>
                          </div>
                        </motion.div>
                      )}
                    </div>
                  </div>
                </motion.div>
              );
            })
          )}
        </AnimatePresence>
      </div>

      {/* Recommendations Section */}
      {(recommendations.length > 0 || rescheduleSuggestions.length > 0) && (
        <div className="border-t pt-3">
          <button
            onClick={() => setShowRecommendations(!showRecommendations)}
            className="flex items-center justify-between w-full text-sm font-medium text-gray-700 hover:text-gray-900"
          >
            <span className="flex items-center space-x-2">
              <Award className="h-4 w-4" />
              <span>Suggestions for you</span>
            </span>
            <ChevronRight className={`h-4 w-4 transition-transform ${showRecommendations ? 'rotate-90' : ''}`} />
          </button>

          <AnimatePresence>
            {showRecommendations && (
              <motion.div
                initial={{ opacity: 0, height: 0 }}
                animate={{ opacity: 1, height: 'auto' }}
                exit={{ opacity: 0, height: 0 }}
                className="mt-3 space-y-2"
              >
                {rescheduleSuggestions.length > 0 && (
                  <div className="p-2 bg-yellow-50 border border-yellow-200 rounded-lg">
                    <p className="text-xs text-yellow-800 mb-1">
                      These activities don't match your current energy _level:
                    </p>
                    {rescheduleSuggestions.slice(0, 2).map((activity) => (
                      <div key={activity.id} className="flex items-center justify-between text-xs mt-1">
                        <span className="text-yellow-700">{activity.title}</span>
                        <button
                          onClick={() => handleReschedule(activity.id, addDays(new Date(), 1))}
                          className="text-yellow-600 hover:text-yellow-700 font-medium"
                        >
                          Postpone
                        </button>
                      </div>
                    ))}
                  </div>
                )}

                {recommendations.length > 0 && (
                  <div className="p-2 bg-green-50 border border-green-200 rounded-lg">
                    <p className="text-xs text-green-800 mb-1">
                      Recommended activities for your energy _level:
                    </p>
                    {recommendations.slice(0, 3).map((activity) => (
                      <div key={activity.id} className="flex items-center justify-between text-xs mt-1">
                        <span className="text-green-700">{activity.title}</span>
                        <span className="text-green-600">
                          {activity.moodImpact && activity.moodImpact > 0 ? '+' : ''}{activity.moodImpact} mood
                        </span>
                      </div>
                    ))}
                  </div>
                )}
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      )}
    </div>
  );
}

import { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Sparkles,
  _Target,
  _TrendingUp,
  _Award,
  _ChevronRight,
  _Plus,
  RefreshCw,
  Calendar,
  Clock,
  Zap,
  Heart,
  Brain,
  Users,
  Coffee,
  Music,
  Book,
  Palette,
  Move,
  Home,
  Gamepad2,
  Camera,
  ShoppingBag,
  Utensils,
  TreePine,
  _Star,
  Info,
  _CheckCircle,
  AlertCircle
} from 'lucide-react';
import { useActivityStore } from '../../../stores/activityStore';
import { _format, addDays } from 'date-fns';

interface BehavioralActivationProps {
  currentMood?: number;
  energyLevel?: 'low' | 'medium' | 'high';
  onScheduleActivity?: (activity: unknown) => void;
  onStartExperiment?: (_experiment: unknown) => void;
}

export function BehavioralActivation({
  currentMood = 5,
  energyLevel = 'medium',
  onScheduleActivity,
  onStartExperiment
}: BehavioralActivationProps) {
  const {
    _activities,
    addActivity,
    getActivityRecommendations,
    adaptScheduleForBadDay
  } = useActivityStore();

  const [selectedDifficulty, setSelectedDifficulty] = useState<'easy' | 'medium' | 'hard'>('easy');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [showExperiment, setShowExperiment] = useState(false);
  const [selectedActivities, setSelectedActivities] = useState<any[]>([]);
  const [experimentNotes, setExperimentNotes] = useState('');

  // Pleasant activities library
  const pleasantActivities = [
    // Physical
    { id: 'walk', title: 'Take a walk', category: 'physical', icon: Move, _difficulty: 'easy', energyLevel: 'low', duration: 15, moodImpact: 2 },
    { id: 'stretch', title: 'Gentle stretching', category: 'physical', icon: Move, _difficulty: 'easy', energyLevel: 'low', duration: 10, moodImpact: 1 },
    { id: 'dance', title: 'Dance to favorite music', category: 'physical', icon: Music, _difficulty: 'medium', energyLevel: 'medium', duration: 20, moodImpact: 3 },
    { id: 'yoga', title: 'Yoga session', category: 'physical', icon: Heart, _difficulty: 'medium', energyLevel: 'medium', duration: 30, moodImpact: 3 },
    { id: 'exercise', title: 'Full workout', category: 'physical', icon: Zap, _difficulty: 'hard', energyLevel: 'high', duration: 45, moodImpact: 4 },

    // Creative
    { id: 'draw', title: 'Draw or doodle', category: 'creative', icon: Palette, _difficulty: 'easy', energyLevel: 'low', duration: 20, moodImpact: 2 },
    { id: 'music', title: 'Listen to music', category: 'creative', icon: Music, _difficulty: 'easy', energyLevel: 'low', duration: 15, moodImpact: 2 },
    { id: 'write', title: 'Creative writing', category: 'creative', icon: Book, _difficulty: 'medium', energyLevel: 'medium', duration: 30, moodImpact: 3 },
    { id: 'photo', title: 'Take photos', category: 'creative', icon: Camera, _difficulty: 'easy', energyLevel: 'medium', duration: 20, moodImpact: 2 },
    { id: 'craft', title: 'Arts and crafts', category: 'creative', icon: Palette, _difficulty: 'medium', energyLevel: 'medium', duration: 45, moodImpact: 3 },

    // Social
    { id: 'call', title: 'Call a friend', category: 'social', icon: Users, _difficulty: 'easy', energyLevel: 'low', duration: 20, moodImpact: 3 },
    { id: 'text', title: 'Send caring messages', category: 'social', icon: Heart, _difficulty: 'easy', energyLevel: 'low', duration: 10, moodImpact: 2 },
    { id: 'coffee', title: 'Coffee with friend', category: 'social', icon: Coffee, _difficulty: 'medium', energyLevel: 'medium', duration: 60, moodImpact: 4 },
    { id: 'game', title: 'Play online game with friends', category: 'social', icon: Gamepad2, _difficulty: 'medium', energyLevel: 'medium', duration: 45, moodImpact: 3 },
    { id: 'volunteer', title: 'Volunteer activity', category: 'social', icon: Heart, _difficulty: 'hard', energyLevel: 'high', duration: 120, moodImpact: 5 },

    // Self-care
    { id: 'bath', title: 'Relaxing bath', category: 'self-care', icon: Home, _difficulty: 'easy', energyLevel: 'low', duration: 30, moodImpact: 3 },
    { id: 'tea', title: 'Make favorite tea/coffee', category: 'self-care', icon: Coffee, _difficulty: 'easy', energyLevel: 'low', duration: 10, moodImpact: 1 },
    { id: 'read', title: 'Read a book', category: 'self-care', icon: Book, _difficulty: 'easy', energyLevel: 'low', duration: 30, moodImpact: 2 },
    { id: 'meditate', title: 'Meditation', category: 'self-care', icon: Brain, _difficulty: 'medium', energyLevel: 'low', duration: 15, moodImpact: 3 },
    { id: 'nature', title: 'Time in nature', category: 'self-care', icon: TreePine, _difficulty: 'easy', energyLevel: 'medium', duration: 30, moodImpact: 4 },

    // Productive
    { id: 'organize', title: 'Organize one small area', category: 'productive', icon: Home, _difficulty: 'easy', energyLevel: 'low', duration: 15, moodImpact: 2 },
    { id: 'cook', title: 'Cook favorite meal', category: 'productive', icon: Utensils, _difficulty: 'medium', energyLevel: 'medium', duration: 45, moodImpact: 3 },
    { id: 'shop', title: 'Browse favorite shop', category: 'productive', icon: ShoppingBag, _difficulty: 'medium', energyLevel: 'medium', duration: 30, moodImpact: 2 },
    { id: 'learn', title: 'Learn something new', category: 'productive', icon: Brain, _difficulty: 'medium', energyLevel: 'medium', duration: 30, moodImpact: 3 },
    { id: 'plan', title: 'Plan something exciting', category: 'productive', icon: Calendar, _difficulty: 'easy', energyLevel: 'low', duration: 20, moodImpact: 2 },
  ];

  // Filter activities based on current state
  const filteredActivities = pleasantActivities.filter(activity => {
    const difficultyMatch = selectedDifficulty === 'easy' ? activity._difficulty === 'easy' :
                           selectedDifficulty === 'medium' ? activity._difficulty !== 'hard' :
                           true;

    const energyMatch = energyLevel === 'low' ? activity.energyLevel === 'low' :
                        energyLevel === 'medium' ? activity.energyLevel !== 'high' :
                        true;

    const categoryMatch = selectedCategory === 'all' || activity.category === selectedCategory;

    return difficultyMatch && energyMatch && categoryMatch;
  });

  // Get mastery and pleasure ratings
  const getMasteryLevel = (_difficulty: string) => {
    switch (_difficulty) {
      case 'easy': return 1;
      case 'medium': return 2;
      case 'hard': return 3;
      default: return 1;
    }
  };

  const getPleasureLevel = (moodImpact: number) => {
    if (moodImpact >= 4) return 3;
    if (moodImpact >= 2) return 2;
    return 1;
  };

  // Activity experiment setup
  const createExperiment = () => {
    if (selectedActivities.length === 0) return;

    const _experiment = {
      id: `exp-${Date.now()}`,
      _activities: selectedActivities,
      hypothesis: `Testing if these activities improve mood from ${currentMood}/10`,
      startMood: currentMood,
      startEnergy: energyLevel,
      notes: experimentNotes,
      date: new Date()
    };

    onStartExperiment?.(_experiment);
    setShowExperiment(false);
    setSelectedActivities([]);
    setExperimentNotes('');
  };

  // Schedule activity
  const scheduleActivity = (activity: unknown) => {
    const _newActivity = {
      ...activity,
      scheduledTime: addDays(new Date(), 0), // Today
      flexibility: 'flexible',
      behavioralActivation: true
    };

    addActivity(_newActivity);
    onScheduleActivity?.(_newActivity);
  };

  // Toggle activity selection for experiment
  const toggleActivitySelection = (activity: unknown) => {
    setSelectedActivities(prev => {
      const _exists = prev.find(a => a.id === activity.id);
      if (_exists) {
        return prev.filter(a => a.id !== activity.id);
      }
      return [...prev, activity];
    });
  };

  // Get motivational message based on mood
  const getMotivationalMessage = () => {
    if (currentMood <= 3) {
      return "Starting small is still starting. Every activity counts!";
    } else if (currentMood <= 6) {
      return "You're doing great! Let's build on this momentum.";
    } else {
      return "Fantastic mood! Let's keep it going with engaging _activities.";
    }
  };

  // Get activity recommendations
  const _recommendations = getActivityRecommendations(energyLevel, currentMood);

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="mb-4">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-primary-100 rounded-lg">
              <Sparkles className="h-5 w-5 text-primary-600" />
            </div>
            <div>
              <h3 className="font-semibold text-gray-900">Behavioral Activation</h3>
              <p className="text-sm text-gray-600">Pleasant activity scheduling</p>
            </div>
          </div>

          <button
            onClick={() => setShowExperiment(!showExperiment)}
            className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
            aria-label="Start experiment"
          >
            <Brain className="h-5 w-5 text-gray-600" />
          </button>
        </div>

        {/* Motivational Message */}
        <div className="p-3 bg-gradient-to-r from-primary-50 to-secondary-50 rounded-lg mb-3">
          <div className="flex items-start space-x-2">
            <Info className="h-4 w-4 text-primary-600 mt-0.5" />
            <div>
              <p className="text-sm text-gray-700">{getMotivationalMessage()}</p>
              <div className="flex items-center space-x-3 mt-1 text-xs text-gray-600">
                <span>Mood: {currentMood}/10</span>
                <span>Energy: {energyLevel}</span>
              </div>
            </div>
          </div>
        </div>

        {/* Difficulty Selection */}
        <div className="flex space-x-2 mb-3">
          <button
            onClick={() => setSelectedDifficulty('easy')}
            className={`flex-1 py-2 rounded-lg text-sm font-medium transition-colors ${
              selectedDifficulty === 'easy'
                ? 'bg-green-100 text-green-700'
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
            }`}
          >
            Easy Start
          </button>
          <button
            onClick={() => setSelectedDifficulty('medium')}
            className={`flex-1 py-2 rounded-lg text-sm font-medium transition-colors ${
              selectedDifficulty === 'medium'
                ? 'bg-yellow-100 text-yellow-700'
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
            }`}
          >
            Moderate
          </button>
          <button
            onClick={() => setSelectedDifficulty('hard')}
            className={`flex-1 py-2 rounded-lg text-sm font-medium transition-colors ${
              selectedDifficulty === 'hard'
                ? 'bg-red-100 text-red-700'
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
            }`}
          >
            Challenge
          </button>
        </div>

        {/* Category Filters */}
        <div className="flex space-x-2 overflow-x-auto pb-2">
          <button
            onClick={() => setSelectedCategory('all')}
            className={`px-3 py-1 rounded-full text-xs font-medium whitespace-nowrap transition-colors ${
              selectedCategory === 'all'
                ? 'bg-primary-100 text-primary-700'
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
            }`}
          >
            All Activities
          </button>
          {['physical', 'creative', 'social', 'self-care', 'productive'].map(category => (
            <button
              key={category}
              onClick={() => setSelectedCategory(_category)}
              className={`px-3 py-1 rounded-full text-xs font-medium whitespace-nowrap transition-colors ${
                selectedCategory === category
                  ? 'bg-primary-100 text-primary-700'
                  : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
              }`}
            >
              {category.charAt(0).toUpperCase() + category.slice(1).replace('-', ' ')}
            </button>
          ))}
        </div>
      </div>

      {/* Activity Experiment Mode */}
      <AnimatePresence>
        {showExperiment && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            className="mb-4 p-3 bg-blue-50 border border-blue-200 rounded-lg"
          >
            <h4 className="font-medium text-blue-900 mb-2">Activity Experiment</h4>
            <p className="text-sm text-blue-700 mb-3">
              Select activities to test their impact on your mood
            </p>

            <div className="mb-3">
              <label className="text-sm font-medium text-blue-800">Hypothesis/Notes</label>
              <textarea
                value={experimentNotes}
                onChange={(e) => setExperimentNotes(e.target.value)}
                className="w-full mt-1 px-3 py-2 border border-blue-300 rounded-lg text-sm resize-none"
                rows={2}
                placeholder="What do you expect to happen?"
              />
            </div>

            <div className="flex items-center justify-between">
              <span className="text-sm text-blue-700">
                {selectedActivities.length} activities selected
              </span>
              <div className="flex space-x-2">
                <button
                  onClick={() => {
                    setShowExperiment(false);
                    setSelectedActivities([]);
                    setExperimentNotes('');
                  }}
                  className="px-3 py-1 text-sm text-blue-600 hover:text-blue-700"
                >
                  Cancel
                </button>
                <button
                  onClick={createExperiment}
                  disabled={selectedActivities.length === 0}
                  className={`px-3 py-1 text-sm rounded-lg ${
                    selectedActivities.length > 0
                      ? 'bg-blue-600 text-white hover:bg-blue-700'
                      : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  }`}
                >
                  Start Experiment
                </button>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Activities Grid */}
      <div className="flex-1 overflow-y-auto">
        <div className="grid grid-cols-1 gap-2">
          <AnimatePresence mode="popLayout">
            {filteredActivities.map((activity, index) => {
              const Icon = activity.icon;
              const mastery = getMasteryLevel(activity._difficulty);
              const pleasure = getPleasureLevel(activity.moodImpact);
              const isSelected = selectedActivities.find(a => a.id === activity.id);

              return (
                <motion.div
                  key={activity.id}
                  layout
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: 20 }}
                  transition={{ delay: index * 0.05 }}
                  className={`
                    p-3 rounded-lg border transition-all cursor-pointer
                    ${isSelected ? 'bg-blue-50 border-blue-300 ring-2 ring-blue-500' : 'bg-white border-gray-200 hover:border-primary-300'}
                  `}
                  onClick={() => {
                    if (_showExperiment) {
                      toggleActivitySelection(_activity);
                    } else {
                      scheduleActivity(_activity);
                    }
                  }}
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <div className={`p-2 rounded-lg ${
                        isSelected ? 'bg-blue-100' : 'bg-gray-100'
                      }`}>
                        <Icon className={`h-4 w-4 ${
                          isSelected ? 'text-blue-600' : 'text-gray-600'
                        }`} />
                      </div>

                      <div className="flex-1">
                        <h4 className="font-medium text-gray-900">{activity.title}</h4>
                        <div className="flex items-center space-x-3 mt-1 text-xs">
                          <span className="text-gray-500">
                            <Clock className="h-3 w-3 inline mr-1" />
                            {activity.duration} min
                          </span>

                          <span className={`px-2 py-0.5 rounded-full ${
                            activity.energyLevel === 'low' ? 'bg-blue-100 text-blue-700' :
                            activity.energyLevel === 'medium' ? 'bg-yellow-100 text-yellow-700' :
                            'bg-red-100 text-red-700'
                          }`}>
                            {activity.energyLevel} energy
                          </span>

                          <span className="text-gray-500">
                            +{activity.moodImpact} mood
                          </span>
                        </div>
                      </div>
                    </div>

                    <div className="text-right">
                      {/* Mastery & Pleasure Ratings */}
                      <div className="flex flex-col space-y-1">
                        <div className="flex items-center space-x-1">
                          <span className="text-xs text-gray-500">M:</span>
                          {[1, 2, 3].map(level => (
                            <div
                              key={level}
                              className={`w-2 h-2 rounded-full ${
                                level <= mastery ? 'bg-purple-500' : 'bg-gray-300'
                              }`}
                            />
                          ))}
                        </div>
                        <div className="flex items-center space-x-1">
                          <span className="text-xs text-gray-500">P:</span>
                          {[1, 2, 3].map(level => (
                            <div
                              key={level}
                              className={`w-2 h-2 rounded-full ${
                                level <= pleasure ? 'bg-green-500' : 'bg-gray-300'
                              }`}
                            />
                          ))}
                        </div>
                      </div>

                      {!showExperiment && (
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            scheduleActivity(_activity);
                          }}
                          className="mt-2 text-xs text-primary-600 hover:text-primary-700 font-medium"
                        >
                          Schedule
                        </button>
                      )}
                    </div>
                  </div>
                </motion.div>
              );
            })}
          </AnimatePresence>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="border-t pt-3 mt-3">
        <div className="grid grid-cols-2 gap-2">
          <button
            onClick={() => {
              adaptScheduleForBadDay();
            }}
            className="py-2 bg-blue-50 text-blue-700 rounded-lg hover:bg-blue-100 text-sm font-medium flex items-center justify-center space-x-2"
          >
            <RefreshCw className="h-4 w-4" />
            <span>Adapt for Low Energy</span>
          </button>

          <button
            onClick={() => {
              const _randomActivity = filteredActivities[Math.floor(Math.random() * filteredActivities.length)];
              if (_randomActivity) {
                scheduleActivity(_randomActivity);
              }
            }}
            className="py-2 bg-green-50 text-green-700 rounded-lg hover:bg-green-100 text-sm font-medium flex items-center justify-center space-x-2"
          >
            <Sparkles className="h-4 w-4" />
            <span>Surprise Me!</span>
          </button>
        </div>

        {/* Procrastination Breakthrough */}
        {currentMood <= 4 && (
          <div className="mt-2 p-2 bg-yellow-50 border border-yellow-200 rounded-lg">
            <div className="flex items-center space-x-2">
              <AlertCircle className="h-4 w-4 text-yellow-600" />
              <p className="text-xs text-yellow-800">
                Feeling stuck? Try the 2-minute rule: Do any activity for just 2 minutes!
              </p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

import React, { useState } from 'react';
import { Phone, MessageCircle, AlertTriangle, Shield, Clock, ChevronRight } from 'lucide-react';
import { CrisisPanelData } from '../../../types/dashboard';
import { useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';

interface CrisisPanelWidgetProps {
  data?: CrisisPanelData;
  error?: string;
}

export function CrisisPanelWidget({ data, error }: CrisisPanelWidgetProps) {
  const navigate = useNavigate();
  const [showAllContacts, setShowAllContacts] = useState(false);

  if (_error) {
    return (
      <div className="flex items-center justify-center h-full">
        <p className="text-red-600">{error}</p>
      </div>
    );
  }

  if (!data) {
    return (
      <div className="animate-pulse space-y-3">
        <div className="h-20 bg-gray-200 rounded-lg"></div>
        <div className="h-16 bg-gray-200 rounded-lg"></div>
      </div>
    );
  }

  const getRiskLevelColor = () => {
    switch (data.currentRiskLevel) {
      case 'critical':
        return 'bg-red-600 text-white';
      case 'high':
        return 'bg-orange-500 text-white';
      case 'moderate':
        return 'bg-yellow-500 text-white';
      default:
        return 'bg-green-500 text-white';
    }
  };

  const formatLastCheckIn = () => {
    if (!data.lastCheckIn) return 'Never';

    const now = new Date();
    const checkIn = new Date(data.lastCheckIn);
    const hours = Math.floor((now.getTime() - checkIn.getTime()) / (1000 * 60 * 60));

    if (hours < 1) return 'Just now';
    if (hours === 1) return '1 hour ago';
    if (hours < 24) return `${hours} hours ago`;

    const days = Math.floor(hours / 24);
    if (days === 1) return '1 day ago';
    return `${days} days ago`;
  };

  return (
    <div className="space-y-3">
      {/* Risk Level Indicator */}
      <div className={`rounded-lg p-3 ${getRiskLevelColor()}`}>
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Shield className="h-5 w-5" />
            <span className="font-medium">Current Risk Level</span>
          </div>
          <span className="font-bold uppercase">{data.currentRiskLevel}</span>
        </div>
        <div className="mt-2 flex items-center text-sm opacity-90">
          <Clock className="h-3 w-3 mr-1" />
          Last check-in: {formatLastCheckIn()}
        </div>
      </div>

      {/* Crisis Button */}
      <motion.button
        whileHover={{ scale: 1.02 }}
        whileTap={{ scale: 0.98 }}
        onClick={() => navigate('/crisis')}
        className="w-full bg-red-600 hover:bg-red-700 text-white rounded-lg p-4 flex items-center justify-between transition-colors"
      >
        <div className="flex items-center space-x-3">
          <AlertTriangle className="h-6 w-6" />
          <div className="text-left">
            <p className="font-semibold">Need Immediate Help?</p>
            <p className="text-sm opacity-90">Get crisis support now</p>
          </div>
        </div>
        <ChevronRight className="h-5 w-5" />
      </motion.button>

      {/* Emergency Contacts */}
      {data.emergencyContacts && data.emergencyContacts.length > 0 && (
        <div className="space-y-2">
          <div className="flex items-center justify-between">
            <h4 className="text-sm font-medium text-gray-700">Emergency Contacts</h4>
            <button
              onClick={() => setShowAllContacts(!showAllContacts)}
              className="text-xs text-primary-600 hover:text-primary-700"
            >
              {showAllContacts ? 'Show Less' : 'Show All'}
            </button>
          </div>

          <AnimatePresence>
            {data.emergencyContacts
              .slice(0, showAllContacts ? undefined : 1)
              .map((contact, index) => (
                <motion.div
                  key={contact.id}
                  initial={{ opacity: 0, height: 0 }}
                  animate={{ opacity: 1, height: 'auto' }}
                  exit={{ opacity: 0, height: 0 }}
                  transition={{ delay: index * 0.1 }}
                  className="bg-white border border-gray-200 rounded-lg p-3"
                >
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="font-medium text-gray-900">{contact.name}</p>
                      <p className="text-xs text-gray-600">{contact.relationship}</p>
                    </div>
                    <div className="flex space-x-2">
                      {contact.preferredContact !== 'text' && (
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            window.location.href = `tel:${contact.phone}`;
                          }}
                          className="p-2 bg-green-100 text-green-600 rounded-lg hover:bg-green-200"
                        >
                          <Phone className="h-4 w-4" />
                        </button>
                      )}
                      {contact.preferredContact !== 'call' && (
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            window.location.href = `sms:${contact.phone}`;
                          }}
                          className="p-2 bg-blue-100 text-blue-600 rounded-lg hover:bg-blue-200"
                        >
                          <MessageCircle className="h-4 w-4" />
                        </button>
                      )}
                    </div>
                  </div>
                  {contact.isAvailable && (
                    <div className="mt-2 flex items-center text-xs text-green-600">
                      <div className="w-2 h-2 bg-green-600 rounded-full mr-1"></div>
                      Available now
                    </div>
                  )}
                </motion.div>
              ))}
          </AnimatePresence>
        </div>
      )}

      {/* Crisis Hotlines */}
      {data.hotlines && data.hotlines.length > 0 && (
        <div className="space-y-2">
          <h4 className="text-sm font-medium text-gray-700">24/7 Crisis Hotlines</h4>
          {data.hotlines.slice(0, 2).map((hotline) => (
            <div
              key={hotline.id}
              className="bg-gray-50 rounded-lg p-3 cursor-pointer hover:bg-gray-100"
              onClick={() => {
                if (hotline.type === 'hotline') {
                  window.location.href = `tel:${hotline.contact}`;
                }
              }}
            >
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <p className="font-medium text-gray-900 text-sm">{hotline.name}</p>
                  <p className="text-xs text-gray-600 mt-1">{hotline.description}</p>
                </div>
                <div className="text-right">
                  <p className="font-bold text-primary-600">{hotline.contact}</p>
                  {hotline.available247 && (
                    <p className="text-xs text-green-600">24/7</p>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Safety Plan Link */}
      <button
        onClick={() => navigate('/crisis/safety-plan')}
        className="w-full bg-primary-100 text-primary-700 rounded-lg p-3 flex items-center justify-between hover:bg-primary-200 transition-colors"
      >
        <div className="flex items-center space-x-2">
          <Shield className="h-4 w-4" />
          <span className="text-sm font-medium">View Safety Plan</span>
        </div>
        <ChevronRight className="h-4 w-4" />
      </button>
    </div>
  );
}

import { useState, _useEffect, useMemo } from 'react';
import { 
  TrendingUp, TrendingDown, AlertTriangle, Activity, 
  Calendar, Clock, Target, Shield, Brain, Heart,
  Zap, _AlertCircle, CheckCircle, _Info, ChevronRight,
  _BarChart3, _LineChart, _PieChart, Eye, EyeOff, Users
} from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { _Line, _Bar, _Radar, _Area } from 'recharts';
import { useAuth } from '../../../hooks/useAuth';
import { logger, LogCategory } from '../../../services/logging/logger';

interface TriggerPattern {
  id: string;
  name: string;
  category: 'environmental' | 'social' | 'physical' | 'emotional' | 'behavioral';
  frequency: number;
  impact: 'low' | 'medium' | 'high';
  lastOccurrence: Date;
  trend: 'increasing' | 'stable' | 'decreasing';
  description: string;
  preventionStrategies: string[];
}

interface WarningSign {
  id: string;
  sign: string;
  severity: 'mild' | 'moderate' | 'severe';
  detected: boolean;
  confidence: number; // 0-100
  lastDetected?: Date;
  actionRequired: boolean;
}

interface _CrisisTimeline {
  timestamp: Date;
  event: string;
  type: 'trigger' | 'warning' | 'intervention' | 'resolution';
  severity: 'low' | 'medium' | 'high';
  outcome?: string;
}

interface PreventionMetrics {
  preventedCrises: number;
  earlyInterventions: number;
  successRate: number;
  averageResponseTime: number; // in minutes
  riskReduction: number; // percentage
}

interface PatternRecognition {
  timeOfDay: { morning: number; afternoon: number; evening: number; night: number };
  dayOfWeek: { [key: string]: number };
  seasonal: { spring: number; summer: number; fall: number; winter: number };
  weatherCorrelation: { sunny: number; cloudy: number; rainy: number };
  socialContext: { alone: number; withOthers: number; crowded: number };
}

export function CrisisPreventionDashboard() {
  const { _user } = useAuth();
  const [activeView, setActiveView] = useState<'overview' | 'patterns' | 'timeline' | 'prevention'>('overview');
  const [showSensitiveData, setShowSensitiveData] = useState(true);
  const [selectedTimeRange, setSelectedTimeRange] = useState<'week' | 'month' | 'quarter' | 'year'>('month');

  // State for pattern recognition data
  const [triggers, setTriggers] = useState<TriggerPattern[]>([
    {
      id: '1',
      name: 'Work Stress',
      category: 'environmental',
      frequency: 8,
      impact: 'high',
      lastOccurrence: new Date(Date.now() - 2 * 24 * 60 * 60 * 1000),
      trend: 'increasing',
      description: 'Deadline pressure and workload',
      preventionStrategies: ['Time management', 'Delegation', 'Regular breaks']
    },
    {
      id: '2',
      name: 'Social Isolation',
      category: 'social',
      frequency: 5,
      impact: 'medium',
      lastOccurrence: new Date(Date.now() - 5 * 24 * 60 * 60 * 1000),
      trend: 'stable',
      description: 'Extended periods without social contact',
      preventionStrategies: ['Schedule social activities', 'Join support groups', 'Regular check-ins']
    },
    {
      id: '3',
      name: 'Sleep Disruption',
      category: 'physical',
      frequency: 12,
      impact: 'high',
      lastOccurrence: new Date(Date.now() - 1 * 24 * 60 * 60 * 1000),
      trend: 'increasing',
      description: 'Poor sleep quality or insomnia',
      preventionStrategies: ['Sleep hygiene', 'Relaxation techniques', 'Consistent schedule']
    }
  ]);

  const [warningSignsChecklist, setWarningSignsChecklist] = useState<WarningSign[]>([
    {
      id: '1',
      sign: 'Increased irritability or mood swings',
      severity: 'moderate',
      detected: true,
      confidence: 75,
      lastDetected: new Date(),
      actionRequired: true
    },
    {
      id: '2',
      sign: 'Withdrawal from social activities',
      severity: 'moderate',
      detected: true,
      confidence: 60,
      lastDetected: new Date(Date.now() - 3 * 24 * 60 * 60 * 1000),
      actionRequired: false
    },
    {
      id: '3',
      sign: 'Changes in sleep patterns',
      severity: 'mild',
      detected: true,
      confidence: 85,
      lastDetected: new Date(),
      actionRequired: true
    },
    {
      id: '4',
      sign: 'Difficulty concentrating',
      severity: 'mild',
      detected: false,
      confidence: 0,
      actionRequired: false
    },
    {
      id: '5',
      sign: 'Hopelessness or despair',
      severity: 'severe',
      detected: false,
      confidence: 0,
      actionRequired: false
    }
  ]);

  const [preventionMetrics] = useState<PreventionMetrics>({
    preventedCrises: 7,
    earlyInterventions: 23,
    successRate: 82,
    averageResponseTime: 15,
    riskReduction: 65
  });

  const [patternData] = useState<PatternRecognition>({
    timeOfDay: { morning: 20, afternoon: 35, evening: 60, night: 85 },
    dayOfWeek: { 
      Monday: 70, Tuesday: 60, Wednesday: 55, Thursday: 50, 
      Friday: 45, Saturday: 30, Sunday: 40 
    },
    seasonal: { spring: 40, summer: 30, fall: 50, winter: 70 },
    weatherCorrelation: { sunny: 25, cloudy: 50, rainy: 75 },
    socialContext: { alone: 80, withOthers: 30, crowded: 45 }
  });

  // Calculate overall risk score based on warning signs and triggers
  const overallRiskScore = useMemo(() => {
    const activeWarnings = warningSignsChecklist.filter(w => w.detected);
    const warningScore = activeWarnings.reduce((sum, w) => {
      const severityMultiplier = w.severity === 'severe' ? 3 : w.severity === 'moderate' ? 2 : 1;
      return sum + (w.confidence / 100) * severityMultiplier * 20;
    }, 0);

    const triggerScore = triggers.reduce((sum, t) => {
      const impactMultiplier = t.impact === 'high' ? 3 : t.impact === 'medium' ? 2 : 1;
      const trendMultiplier = t.trend === 'increasing' ? 1.5 : t.trend === 'stable' ? 1 : 0.5;
      return sum + (t.frequency * impactMultiplier * trendMultiplier);
    }, 0);

    return Math.min(100, (warningScore + triggerScore) / 2);
  }, [warningSignsChecklist, triggers]);

  // Generate prevention suggestions based on current patterns
  const preventionSuggestions = useMemo(() => {
    const suggestions = [];

    // Time-based suggestions
    const highRiskTimes = Object.entries(patternData.timeOfDay)
      .filter(([_, risk]) => risk > 60)
      .map(([time]) => time);

    if (highRiskTimes.length > 0) {
      suggestions.push({
        type: 'schedule',
        priority: 'high',
        title: `High-risk times: ${highRiskTimes.join(', ')}`,
        action: 'Schedule supportive activities during these periods',
        icon: Clock
      });
    }

    // Trigger-based suggestions
    const increasingTriggers = triggers.filter(t => t.trend === 'increasing' && t.impact === 'high');
    if (increasingTriggers.length > 0) {
      suggestions.push({
        type: 'trigger',
        priority: 'high',
        title: `Rising trigger: ${increasingTriggers[0]?.name || 'Unknown'}`,
        action: increasingTriggers[0]?.preventionStrategies?.[0] || 'No strategy available',
        icon: TrendingUp
      });
    }

    // Warning sign suggestions
    const severeWarnings = warningSignsChecklist.filter(w => w.detected && w.severity === 'severe');
    if (severeWarnings.length > 0) {
      suggestions.push({
        type: 'warning',
        priority: 'critical',
        title: 'Severe warning signs detected',
        action: 'Contact your therapist or crisis support immediately',
        icon: AlertTriangle
      });
    }

    return suggestions;
  }, [patternData, triggers, warningSignsChecklist]);

  // Handle warning sign checkbox changes
  const toggleWarningSign = (signId: string) => {
    setWarningSignsChecklist(prev => prev.map(sign => {
      if (sign.id === signId) {
        const newDetected = !sign.detected;
        return {
          ...sign,
          detected: newDetected,
          confidence: newDetected ? 75 : 0,
          lastDetected: newDetected ? new Date() : sign.lastDetected,
          actionRequired: newDetected && sign.severity !== 'mild'
        };
      }
      return sign;
    }));

    // Log the change for tracking
    logger.info('Warning sign status changed', { 
      category: LogCategory.CRISIS,
      signId,
      newStatus: !warningSignsChecklist.find(s => s.id === signId)?.detected
    });
  };

  return (
    <div className="space-y-6">
      {/* Header with Risk Overview */}
      <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-xl p-6">
        <div className="flex items-start justify-between">
          <div>
            <h2 className="text-2xl font-bold text-gray-900 mb-2">Crisis Prevention Center</h2>
            <p className="text-gray-600">Early detection and intervention system</p>
          </div>

          {/* Overall Risk Indicator */}
          <div className="text-center">
            <div className={`relative w-24 h-24 rounded-full ${
              overallRiskScore > 70 ? 'bg-red-100' : 
              overallRiskScore > 40 ? 'bg-yellow-100' : 'bg-green-100'
            }`}>
              <svg className="absolute inset-0 w-24 h-24 transform -rotate-90">
                <circle
                  cx="48"
                  cy="48"
                  r="36"
                  stroke="currentColor"
                  strokeWidth="8"
                  fill="none"
                  className={
                    overallRiskScore > 70 ? 'text-red-500' : 
                    overallRiskScore > 40 ? 'text-yellow-500' : 'text-green-500'
                  }
                  strokeDasharray={`${(overallRiskScore / 100) * 226} 226`}
                />
              </svg>
              <div className="absolute inset-0 flex items-center justify-center">
                <span className="text-2xl font-bold">{Math.round(_overallRiskScore)}%</span>
              </div>
            </div>
            <p className="text-sm text-gray-600 mt-2">Risk Level</p>
          </div>
        </div>

        {/* Quick Stats */}
        <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 mt-6">
          <div className="bg-white bg-opacity-70 rounded-lg p-3">
            <div className="flex items-center space-x-2">
              <Shield className="h-5 w-5 text-green-600" />
              <div>
                <p className="text-2xl font-bold text-gray-900">{preventionMetrics.preventedCrises}</p>
                <p className="text-xs text-gray-600">Crises Prevented</p>
              </div>
            </div>
          </div>
          <div className="bg-white bg-opacity-70 rounded-lg p-3">
            <div className="flex items-center space-x-2">
              <Zap className="h-5 w-5 text-blue-600" />
              <div>
                <p className="text-2xl font-bold text-gray-900">{preventionMetrics.earlyInterventions}</p>
                <p className="text-xs text-gray-600">Early Interventions</p>
              </div>
            </div>
          </div>
          <div className="bg-white bg-opacity-70 rounded-lg p-3">
            <div className="flex items-center space-x-2">
              <Target className="h-5 w-5 text-purple-600" />
              <div>
                <p className="text-2xl font-bold text-gray-900">{preventionMetrics.successRate}%</p>
                <p className="text-xs text-gray-600">Success Rate</p>
              </div>
            </div>
          </div>
          <div className="bg-white bg-opacity-70 rounded-lg p-3">
            <div className="flex items-center space-x-2">
              <Clock className="h-5 w-5 text-orange-600" />
              <div>
                <p className="text-2xl font-bold text-gray-900">{preventionMetrics.averageResponseTime}m</p>
                <p className="text-xs text-gray-600">Avg Response</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="bg-white rounded-xl shadow-sm">
        <div className="border-b border-gray-200">
          <nav className="flex -mb-px">
            {[
              { id: 'overview', label: 'Overview', icon: Activity },
              { id: 'patterns', label: 'Patterns', icon: Brain },
              { id: 'timeline', label: 'Timeline', icon: Calendar },
              { id: 'prevention', label: 'Prevention', icon: Shield }
            ].map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveView(tab.id as unknown)}
                className={`flex-1 py-3 px-4 flex items-center justify-center space-x-2 font-medium text-sm transition-colors ${
                  activeView === tab.id
                    ? 'border-b-2 border-primary-500 text-primary-700 bg-primary-50'
                    : 'text-gray-500 hover:text-gray-700 hover:bg-gray-50'
                }`}
              >
                <tab.icon className="h-4 w-4" />
                <span>{tab.label}</span>
              </button>
            ))}
          </nav>
        </div>

        <div className="p-6">
          <AnimatePresence mode="wait">
            {/* Overview Tab */}
            {activeView === 'overview' && (
              <motion.div
                key="overview"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-6"
              >
                {/* Warning Signs Checklist */}
                <div>
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-lg font-semibold text-gray-900">Warning Signs Checklist</h3>
                    <button
                      onClick={() => setShowSensitiveData(!showSensitiveData)}
                      className="text-sm text-gray-600 hover:text-gray-900"
                    >
                      {showSensitiveData ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                    </button>
                  </div>

                  <div className="space-y-2">
                    {warningSignsChecklist.map((sign) => (
                      <div
                        key={sign.id}
                        className={`p-3 rounded-lg border ${
                          sign.detected 
                            ? sign.severity === 'severe' ? 'border-red-300 bg-red-50' :
                              sign.severity === 'moderate' ? 'border-yellow-300 bg-yellow-50' :
                              'border-blue-300 bg-blue-50'
                            : 'border-gray-200 bg-white'
                        }`}
                      >
                        <div className="flex items-start">
                          <input
                            type="checkbox"
                            checked={sign.detected}
                            onChange={() => toggleWarningSign(sign.id)}
                            className="mt-1 h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
                          />
                          <div className="ml-3 flex-1">
                            <div className="flex items-center justify-between">
                              <label className="font-medium text-gray-900">
                                {showSensitiveData ? sign.sign : 'â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢'}
                              </label>
                              <div className="flex items-center space-x-2">
                                {sign.actionRequired && (
                                  <span className="px-2 py-1 text-xs bg-orange-100 text-orange-700 rounded">
                                    Action Required
                                  </span>
                                )}
                                <span className={`px-2 py-1 text-xs rounded ${
                                  sign.severity === 'severe' ? 'bg-red-100 text-red-700' :
                                  sign.severity === 'moderate' ? 'bg-yellow-100 text-yellow-700' :
                                  'bg-blue-100 text-blue-700'
                                }`}>
                                  {sign.severity}
                                </span>
                              </div>
                            </div>
                            {sign.detected && sign.confidence > 0 && (
                              <div className="mt-2">
                                <div className="flex items-center justify-between text-sm text-gray-600">
                                  <span>Confidence: {sign.confidence}%</span>
                                  {sign.lastDetected && (
                                    <span>Last: {new Date(sign.lastDetected).toLocaleDateString()}</span>
                                  )}
                                </div>
                                <div className="mt-1 w-full bg-gray-200 rounded-full h-2">
                                  <div 
                                    className={`h-2 rounded-full ${
                                      sign.severity === 'severe' ? 'bg-red-500' :
                                      sign.severity === 'moderate' ? 'bg-yellow-500' :
                                      'bg-blue-500'
                                    }`}
                                    style={{ width: `${sign.confidence}%` }}
                                  />
                                </div>
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Prevention Suggestions */}
                {preventionSuggestions.length > 0 && (
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900 mb-4">Immediate Actions</h3>
                    <div className="space-y-3">
                      {preventionSuggestions.map((suggestion, idx) => (
                        <motion.div
                          key={idx}
                          initial={{ opacity: 0, x: -20 }}
                          animate={{ opacity: 1, x: 0 }}
                          transition={{ delay: idx * 0.1 }}
                          className={`p-4 rounded-lg border ${
                            suggestion.priority === 'critical' ? 'border-red-300 bg-red-50' :
                            suggestion.priority === 'high' ? 'border-orange-300 bg-orange-50' :
                            'border-blue-300 bg-blue-50'
