interface SupportContact {
  id: string;
  name: string;
  relationship: 'family' | 'friend' | 'therapist' | 'counselor' | 'doctor' | 'peer' | 'sponsor';
  phone: string;
  email?: string;
  preferredContact: 'call' | 'text' | 'video' | 'any';
  availability: {
    status: 'available' | 'busy' | 'offline' | 'emergency-only';
    lastSeen?: Date;
    timezone?: string;
    schedule?: {
      day: string;
      start: string;
      end: string;
    }[];
  };
  trustLevel: 1 | 2 | 3 | 4 | 5;
  specializations?: string[];
  notes?: string;
  lastContact?: Date;
  contactFrequency?: 'daily' | 'weekly' | 'monthly' | 'as-needed';
  isProfessional: boolean;
  isEmergencyContact: boolean;
}

interface SupportGroup {
  id: string;
  name: string;
  type: 'peer' | 'therapy' | 'recovery' | 'crisis' | 'community';
  memberCount: number;
  isActive: boolean;
  nextMeeting?: Date;
  meetingFrequency: string;
  isOnline: boolean;
  location?: string;
  moderators: string[];
  description: string;
  joinUrl?: string;
}

interface CrisisBuddy {
  id: string;
  buddyId: string;
  name: string;
  status: 'paired' | 'available' | 'busy';
  lastCheckIn: Date;
  sharedSafetyPlan: boolean;
  mutualSupport: boolean;
  timezone: string;
}

