          >
            {setting.options.map((option: string) => (
              <option key={option} value={option}>
                {option.charAt(0).toUpperCase() + option.slice(1)}
              </option>
            ))}
          </select>
        );

      case 'slider':
        return (
          <div className="flex items-center space-x-2">
            <input
              type="range"
              min={setting.min}
              max={setting.max}
              step={setting.step}
              value={setting.value}
              onChange={(e) => handleSettingChange(setting.id, parseFloat(e.target.value))}
              className="flex-1"
              aria-label={setting.label}
              aria-valuenow={setting.value}
              aria-valuemin={setting.min}
              aria-valuemax={setting.max}
            />
            <span className="text-sm font-medium w-12 text-right">
              {setting.value}{setting.unit || ''}
            </span>
          </div>
        );

      default:
        return null;
    }
  };

  // Accessibility presets
  const accessibilityPresets = [
    {
      id: 'low-vision',
      name: 'Low Vision',
      description: 'Optimized for users with low vision',
      settings: {
        highContrast: true,
        fontSize: 150,
        lineSpacing: 2,
        focusIndicator: 'bold'
      }
    },
    {
      id: 'dyslexia',
      name: 'Dyslexia Friendly',
      description: 'Easier reading for dyslexia',
      settings: {
        fontSize: 120,
        lineSpacing: 2,
        fontFamily: 'OpenDyslexic',
        backgroundColor: '#FFFBF0'
      }
    },
    {
      id: 'motor',
      name: 'Motor Impairment',
      description: 'Easier interaction for motor difficulties',
      settings: {
        touchTargetSize: 'extra-large',
        stickyKeys: true,
        dwellClicking: true,
        extendedTimeouts: true
      }
    },
    {
      id: 'cognitive',
      name: 'Cognitive Support',
      description: 'Simplified and focused interface',
      settings: {
        simplifiedUI: true,
        reducedMotion: true,
        errorPrevention: true,
        autoComplete: true
      }
    }
  ];

  if (_showCompactView) {
    // Compact view for quick access
    return (
      <div className="p-4 bg-white rounded-lg shadow-sm border border-gray-200">
        <div className="flex items-center justify-between mb-3">
          <h3 className="text-sm font-semibold text-gray-900">Quick Accessibility</h3>
          <button
            onClick={() => setShowHelp(!showHelp)}
            className="p-1 hover:bg-gray-100 rounded"
            aria-label="Accessibility help"
          >
            <HelpCircle className="h-4 w-4 text-gray-500" />
          </button>
        </div>

        <div className="grid grid-cols-2 gap-2">
          <button
            onClick={() => handleSettingChange('highContrast', !settings.highContrast)}
            className={`
              p-2 rounded-lg border text-sm font-medium transition-all
              ${settings.highContrast 
                ? 'bg-black text-white border-black' 
                : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
              }
            `}
          >
            High Contrast
          </button>

          <button
            onClick={() => handleSettingChange('screenReader', !settings.screenReader)}
            className={`
              p-2 rounded-lg border text-sm font-medium transition-all
              ${settings.screenReader 
                ? 'bg-blue-500 text-white border-blue-500' 
                : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
              }
            `}
          >
            Screen Reader
          </button>

          <button
            onClick={() => handleSettingChange('fontSize', settings.fontSize === 100 ? 150 : 100)}
            className="p-2 rounded-lg border border-gray-300 text-sm font-medium hover:bg-gray-50"
          >
            Text: {settings.fontSize}%
          </button>

          <button
            onClick={() => handleSettingChange('reducedMotion', !settings.reducedMotion)}
            className={`
              p-2 rounded-lg border text-sm font-medium transition-all
              ${settings.reducedMotion 
                ? 'bg-purple-500 text-white border-purple-500' 
                : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
              }
            `}
          >
            Reduced Motion
          </button>
        </div>
      </div>
    );
  }

  // Full command center view
  return (
    <div className="bg-white rounded-xl shadow-lg overflow-hidden">
      {/* Header */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 p-6 text-white">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-bold mb-2">Accessibility Command Center</h2>
            <p className="text-blue-100">Customize your experience for better accessibility</p>
          </div>

          <div className="flex items-center space-x-2">
            <button
              onClick={() => setShowPresetMenu(!showPresetMenu)}
              className="px-4 py-2 bg-white bg-opacity-20 rounded-lg hover:bg-opacity-30 transition-colors"
            >
              Presets
            </button>

            <button
              onClick={resetToDefaults}
              className="p-2 bg-white bg-opacity-20 rounded-lg hover:bg-opacity-30 transition-colors"
              aria-label="Reset to defaults"
            >
              <RefreshCw className="h-5 w-5" />
            </button>
          </div>
        </div>
      </div>

      {/* Preset menu */}
      {showPresetMenu && (
        <div className="p-4 bg-blue-50 border-b border-blue-200">
          <h3 className="text-sm font-semibold text-gray-900 mb-3">Quick Presets</h3>
          <div className="grid grid-cols-2 gap-2">
            {accessibilityPresets.map(preset => (
              <button
                key={preset.id}
                onClick={() => {
                  applyPreset(preset.settings as Partial<AccessibilitySettings>);
                  setShowPresetMenu(false);
                }}
                className="p-3 bg-white rounded-lg hover:shadow-md transition-all text-left"
              >
                <div className="font-medium text-sm">{preset.name}</div>
                <div className="text-xs text-gray-500 mt-1">{preset.description}</div>
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Section tabs */}
      <div className="flex border-b border-gray-200 overflow-x-auto">
        {sections.map(section => {
          const Icon = section.icon;
          return (
            <button
              key={section.id}
              onClick={() => setActiveSection(section.id)}
              className={`
                flex items-center space-x-2 px-4 py-3 border-b-2 transition-colors whitespace-nowrap
                ${activeSection === section.id 
                  ? 'border-blue-500 text-blue-600 bg-blue-50' 
                  : 'border-transparent text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                }
              `}
              aria-selected={activeSection === section.id}
              role="tab"
            >
              <Icon className="h-4 w-4" />
              <span className="font-medium">{section.label}</span>
            </button>
          );
        })}
      </div>

      {/* _Settings content */}
      <div className="p-6">
        <AnimatePresence mode="wait">
          <motion.div
            key={activeSection}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            className="space-y-4"
          >
            {getActiveSettings().map(setting => {
              const Icon = setting.icon;
              return (
                <div
                  key={setting.id}
                  className="flex items-start space-x-4 p-4 rounded-lg hover:bg-gray-50 transition-colors"
                >
                  <div className="p-2 bg-gray-100 rounded-lg">
                    <Icon className="h-5 w-5 text-gray-600" />
                  </div>

                  <div className="flex-1">
                    <div className="flex items-center justify-between mb-1">
                      <label className="font-medium text-gray-900">
                        {setting.label}
                      </label>
                      {renderSettingControl(setting)}
                    </div>
                    <p className="text-sm text-gray-500">{setting.description}</p>
                  </div>
                </div>
              );
            })}
          </motion.div>
        </AnimatePresence>
      </div>

      {/* Test mode */}
      <div className="px-6 pb-6">
        <button
          onClick={() => setTestMode(!testMode)}
          className="w-full px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 transition-colors"
        >
          {testMode ? 'Exit Test Mode' : 'Test Accessibility _Settings'}
        </button>

        {testMode && (
          <div className="mt-4 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
            <p className="text-sm text-yellow-800">
              Test mode active. Navigate the interface to experience your accessibility settings.
              All changes are applied in real-time.
            </p>
          </div>
        )}
      </div>

      {/* Help overlay */}
      {showHelp && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-xl p-6 max-w-md">
            <h3 className="text-lg font-semibold mb-3">Accessibility Help</h3>
            <div className="space-y-3 text-sm text-gray-600">
              <p>â€¢ Use Tab to navigate between controls</p>
              <p>â€¢ Press Space or Enter to activate buttons</p>
              <p>â€¢ Use arrow keys to adjust sliders</p>
              <p>â€¢ Screen reader users: Enable screen reader mode for audio feedback</p>
              <p>â€¢ Keyboard shortcuts are available for most actions</p>
            </div>
            <button
              onClick={() => setShowHelp(false)}
              className="mt-4 w-full px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
            >
              Close Help
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

import React, { useState, useEffect, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Phone, MessageSquare, MapPin, Shield, Heart, Users,
  AlertCircle, Activity, Brain, _Headphones, _Navigation,
  _Clock, CheckCircle, _XCircle, ChevronRight, _Volume2,
  _Zap, _Send, _Copy, Share2, ExternalLink, _Wifi, WifiOff, X
} from 'lucide-react';
import { useCrisisAssessment } from '../../../../hooks/useCrisisAssessment';
import { useGeolocation } from '../../../../hooks/useGeolocation';

interface CrisisQuickActionsHubProps {
  _userId: string;
  onActionTaken?: (action: string, details?: unknown) => void;
  emergencyContacts?: EmergencyContact[];
  safetyPlan?: SafetyPlan;
}

interface EmergencyContact {
  id: string;
  name: string;
  phone: string;
  relationship: string;
  isAvailable?: boolean;
  isPrimary?: boolean;
}

interface SafetyPlan {
  warningSignals: string[];
  copingStrategies: string[];
  safetyContacts: EmergencyContact[];
  professionalContacts: ProfessionalContact[];
  safeLocations: string[];
  reasonsToLive: string[];
}

interface ProfessionalContact {
  id: string;
  name: string;
  role: string;
  phone: string;
  available247?: boolean;
}

export function CrisisQuickActionsHub({
  _userId,
  onActionTaken,
  emergencyContacts = [],
  safetyPlan
}: CrisisQuickActionsHubProps) {
  const { assessmentData, _isAssessing, _updateAssessment } = useCrisisAssessment();
  const { location, error: _locationError, loading } = useGeolocation();

  const [activeAction, setActiveAction] = useState<string | null>(null);
  const [showSafetyPlan, setShowSafetyPlan] = useState(false);
  const [showGroundingExercise, setShowGroundingExercise] = useState(false);
  const [breathingActive, setBreathingActive] = useState(false);
  const [emergencyCallInProgress, setEmergencyCallInProgress] = useState(false);
  const [copiedToClipboard, setCopiedToClipboard] = useState<string | null>(null);
  const [offlineMode, setOfflineMode] = useState(!navigator.onLine);

  // Emergency hotlines
  const emergencyHotlines = [
    {
      id: '988',
      name: '988 Suicide & Crisis Lifeline',
      number: '988',
      description: '24/7 crisis support',
      type: 'crisis',
      available247: true
    },
    {
      id: 'crisis-text',
      name: 'Crisis Text Line',
      number: '741741',
      description: 'Text HOME to 741741',
      type: 'text',
      available247: true
    },
    {
      id: '911',
      name: 'Emergency Services',
      number: '911',
      description: 'Immediate emergency help',
      type: 'emergency',
      available247: true
    }
  ];

  // Monitor online/offline status
  useEffect(() => {
    const handleOnline = () => setOfflineMode(false);
    const handleOffline = () => setOfflineMode(true);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  // Handle emergency call
  const handleEmergencyCall = useCallback((number: string, name: string) => {
    setEmergencyCallInProgress(true);
    setActiveAction('calling');

    // Attempt to make call
    window.location.href = `tel:${number}`;

    // Log action
    onActionTaken?.('emergency_call', {
      number,
      name,
      timestamp: new Date().toISOString(),
      location
    });

    // Reset after delay
    setTimeout(() => {
      setEmergencyCallInProgress(false);
      setActiveAction(null);
    }, 3000);
  }, [location, onActionTaken]);

  // Handle text crisis line
  const handleCrisisText = useCallback((number: string) => {
    setActiveAction('texting');

    // Attempt to open SMS
    window.location.href = `sms:${number}?body=HOME`;

    onActionTaken?.('crisis_text', {
      number,
      timestamp: new Date().toISOString()
    });

    setTimeout(() => setActiveAction(null), 3000);
  }, [onActionTaken]);

  // Share location for emergency
  const shareEmergencyLocation = useCallback(async () => {
    if (location) {
      const googleMapsUrl = `https://www.google.com/maps?q=${location.coords.latitude},${location.coords.longitude}`;
      const _message = `Emergency: I need help. My location: ${googleMapsUrl}`;

      // Copy to clipboard
      navigator.clipboard.writeText(_message);
      setCopiedToClipboard('location');

      onActionTaken?.('location_shared', {
        location,
        timestamp: new Date().toISOString()
      });

      setTimeout(() => setCopiedToClipboard(null), 3000);
    }
  }, [location, onActionTaken]);

  // Start grounding exercise
  const startGroundingExercise = useCallback(() => {
    setShowGroundingExercise(true);
    setActiveAction('grounding');

    onActionTaken?.('grounding_started', {
      timestamp: new Date().toISOString()
    });
  }, [onActionTaken]);

  // Start breathing exercise
  const startBreathingExercise = useCallback(() => {
    setBreathingActive(true);
    setActiveAction('breathing');

    onActionTaken?.('breathing_started', {
      timestamp: new Date().toISOString()
    });
  }, [onActionTaken]);

  // Get crisis level color based on assessment data
  const getCrisisLevelColor = () => {
    if (!assessmentData?.overallRisk) return 'blue';

    if (assessmentData.overallRisk >= 8) return 'red';      // Critical
    if (assessmentData.overallRisk >= 6) return 'orange';   // High
    if (assessmentData.overallRisk >= 4) return 'yellow';   // Moderate
    return 'green';                                         // Low
  };

  return (
    <div className="bg-white rounded-xl shadow-xl overflow-hidden">
      {/* Crisis Level Header */}
      <div className={`bg-gradient-to-r from-${getCrisisLevelColor()}-500 to-${getCrisisLevelColor()}-600 p-4 text-white`}>
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <AlertCircle className="h-6 w-6 animate-pulse" />
            <div>
              <h2 className="text-lg font-bold">Crisis Support Hub</h2>
              <p className="text-sm opacity-90">Immediate help available 24/7</p>
            </div>
          </div>

          {offlineMode && (
            <div className="flex items-center space-x-1 px-2 py-1 bg-white bg-opacity-20 rounded-lg">
              <WifiOff className="h-4 w-4" />
              <span className="text-xs">Offline Mode</span>
            </div>
          )}
        </div>
      </div>

      {/* Emergency Hotlines - Always Visible */}
      <div className="p-4 bg-red-50 border-b border-red-200">
        <h3 className="text-sm font-semibold text-red-900 mb-3">Emergency Hotlines</h3>
        <div className="space-y-2">
          {emergencyHotlines.map(hotline => (
            <motion.button
              key={hotline.id}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={() => {
                if (hotline.type === 'text') {
                  handleCrisisText(hotline.number);
                } else {
                  handleEmergencyCall(hotline.number, hotline.name);
                }
              }}
              className={`
                w-full p-3 rounded-lg flex items-center justify-between
                ${hotline.type === 'emergency' 
                  ? 'bg-red-600 text-white hover:bg-red-700' 
                  : 'bg-white border border-red-300 hover:border-red-400 text-gray-900'
                }
                transition-all shadow-sm hover:shadow-md
              `}
              disabled={emergencyCallInProgress}
            >
              <div className="flex items-center space-x-3">
                {hotline.type === 'text' ? (
                  <MessageSquare className="h-5 w-5" />
                ) : (
                  <Phone className="h-5 w-5" />
                )}
                <div className="text-left">
                  <div className="font-semibold">{hotline.name}</div>
                  <div className={`text-xs ${hotline.type === 'emergency' ? 'text-red-100' : 'text-gray-500'}`}>
                    {hotline.description}
                  </div>
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <span className="font-bold text-lg">{hotline.number}</span>
                {hotline.available247 && (
                  <span className={`text-xs px-1.5 py-0.5 rounded-full ${
                    hotline.type === 'emergency' 
                      ? 'bg-red-700 text-red-100' 
                      : 'bg-green-100 text-green-700'
                  }`}>
                    24/7
                  </span>
                )}
              </div>
            </motion.button>
          ))}
        </div>
      </div>

      {/* Quick Crisis Actions */}
      <div className="p-4 space-y-3">
        {/* GPS Location Sharing */}
        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={shareEmergencyLocation}
          disabled={loading}
          className="w-full p-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-all flex items-center justify-between"
        >
          <div className="flex items-center space-x-3">
            <MapPin className="h-5 w-5" />
            <div className="text-left">
              <div className="font-semibold">Share Emergency Location</div>
              <div className="text-xs text-blue-100">Send GPS coordinates to contacts</div>
            </div>
          </div>

          {copiedToClipboard === 'location' ? (
            <CheckCircle className="h-5 w-5" />
          ) : loading ? (
            <div className="animate-spin rounded-full h-5 w-5 border-2 border-white border-t-transparent" />
          ) : (
            <Share2 className="h-5 w-5" />
          )}
        </motion.button>

        {/* Crisis Buddy Contact */}
        {emergencyContacts.length > 0 && (
          <div className="space-y-2">
            <h4 className="text-sm font-semibold text-gray-700">Crisis Buddies</h4>
            {emergencyContacts.slice(0, 2).map(contact => (
              <motion.button
                key={contact.id}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={() => handleEmergencyCall(contact.phone, contact.name)}
                className="w-full p-3 bg-white border border-gray-300 rounded-lg hover:border-blue-400 transition-all"
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <Users className="h-5 w-5 text-blue-500" />
                    <div className="text-left">
                      <div className="font-medium">{contact.name}</div>
                      <div className="text-xs text-gray-500">{contact.relationship}</div>
                    </div>
                  </div>

                  <div className="flex items-center space-x-2">
                    <Phone className="h-4 w-4 text-gray-400" />
                    {contact.isAvailable && (
                      <span className="w-2 h-2 bg-green-500 rounded-full" />
                    )}
                  </div>
                </div>
              </motion.button>
            ))}
          </div>
        )}

        {/* Safety Plan Access */}
        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={() => setShowSafetyPlan(!showSafetyPlan)}
          className="w-full p-3 bg-purple-500 text-white rounded-lg hover:bg-purple-600 transition-all flex items-center justify-between"
        >
          <div className="flex items-center space-x-3">
            <Shield className="h-5 w-5" />
            <div className="text-left">
              <div className="font-semibold">Safety Plan</div>
              <div className="text-xs text-purple-100">Access your personalized safety plan</div>
            </div>
          </div>
          <ChevronRight className={`h-5 w-5 transform transition-transform ${showSafetyPlan ? 'rotate-90' : ''}`} />
        </motion.button>

        {/* Grounding Techniques */}
        <div className="grid grid-cols-2 gap-2">
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={startGroundingExercise}
            className="p-3 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-all"
          >
            <Brain className="h-5 w-5 mx-auto mb-1" />
            <div className="text-xs font-semibold">Grounding</div>
            <div className="text-xs opacity-80">5-4-3-2-1</div>
          </motion.button>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={startBreathingExercise}
            className="p-3 bg-cyan-500 text-white rounded-lg hover:bg-cyan-600 transition-all"
          >
            <Activity className="h-5 w-5 mx-auto mb-1" />
            <div className="text-xs font-semibold">Breathe</div>
            <div className="text-xs opacity-80">4-7-8 Pattern</div>
          </motion.button>
        </div>

        {/* Crisis Chat */}
        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={() => {
            onActionTaken?.('crisis_chat_opened', { timestamp: new Date().toISOString() });
          }}
          className="w-full p-3 bg-indigo-500 text-white rounded-lg hover:bg-indigo-600 transition-all flex items-center justify-between"
        >
          <div className="flex items-center space-x-3">
            <MessageSquare className="h-5 w-5" />
            <div className="text-left">
              <div className="font-semibold">Crisis Chat</div>
              <div className="text-xs text-indigo-100">Connect with a counselor online</div>
            </div>
          </div>
          <ExternalLink className="h-4 w-4" />
        </motion.button>
      </div>

      {/* Safety Plan Expansion */}
      <AnimatePresence>
        {showSafetyPlan && safetyPlan && (
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: 'auto', opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            className="px-4 pb-4 space-y-3"
          >
            <div className="p-3 bg-yellow-50 rounded-lg">
              <h4 className="text-sm font-semibold text-yellow-900 mb-2">Warning Signals</h4>
              <ul className="text-xs text-yellow-700 space-y-1">
                {safetyPlan.warningSignals.slice(0, 3).map((signal, idx) => (
                  <li key={idx} className="flex items-start">
                    <span className="mr-2">â€¢</span>
                    <span>{signal}</span>
                  </li>
                ))}
              </ul>
            </div>

            <div className="p-3 bg-green-50 rounded-lg">
              <h4 className="text-sm font-semibold text-green-900 mb-2">Coping Strategies</h4>
              <ul className="text-xs text-green-700 space-y-1">
                {safetyPlan.copingStrategies.slice(0, 3).map((strategy, idx) => (
                  <li key={idx} className="flex items-start">
                    <CheckCircle className="h-3 w-3 mr-2 mt-0.5 flex-shrink-0" />
                    <span>{strategy}</span>
                  </li>
                ))}
              </ul>
            </div>

            <div className="p-3 bg-blue-50 rounded-lg">
              <h4 className="text-sm font-semibold text-blue-900 mb-2">Reasons to Live</h4>
              <ul className="text-xs text-blue-700 space-y-1">
                {safetyPlan.reasonsToLive.slice(0, 3).map((reason, idx) => (
                  <li key={idx} className="flex items-start">
                    <Heart className="h-3 w-3 mr-2 mt-0.5 flex-shrink-0 text-red-500" />
                    <span>{reason}</span>
                  </li>
                ))}
              </ul>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Grounding Exercise Modal */}
      {showGroundingExercise && (
        <GroundingExerciseModal
          onClose={() => {
            setShowGroundingExercise(false);
            setActiveAction(null);
          }}
          onComplete={() => {
            onActionTaken?.('grounding_completed', { timestamp: new Date().toISOString() });
          }}
        />
      )}

      {/* Breathing Exercise Overlay */}
      {breathingActive && (
        <BreathingExerciseOverlay
          onClose={() => {
            setBreathingActive(false);
            setActiveAction(null);
          }}
          onComplete={() => {
            onActionTaken?.('breathing_completed', { timestamp: new Date().toISOString() });
          }}
        />
      )}
    </div>
  );
}

// Grounding Exercise Modal Component
function GroundingExerciseModal({ onClose, onComplete }: { onClose: () => void; onComplete: () => void }) {
  const [step, setStep] = useState(0);
  const steps = [
    { sense: 'See', count: 5, instruction: 'Name 5 things you can see around you' },
    { sense: 'Touch', count: 4, instruction: 'Name 4 things you can touch' },
    { sense: 'Hear', count: 3, instruction: 'Name 3 things you can hear' },
    { sense: 'Smell', count: 2, instruction: 'Name 2 things you can smell' },
    { sense: 'Taste', count: 1, instruction: 'Name 1 thing you can taste' }
  ];

  const nextStep = () => {
    if (step < steps.length - 1) {
      setStep(step + 1);
    } else {
      onComplete();
      onClose();
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <motion.div
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        className="bg-white rounded-xl p-6 max-w-md w-full"
      >
        <h3 className="text-xl font-bold mb-4">5-4-3-2-1 Grounding Exercise</h3>

        <div className="mb-6">
          <div className="flex justify-between mb-2">
            {steps.map((_, idx) => (
              <div
                key={idx}
                className={`h-2 flex-1 mx-0.5 rounded-full ${
                  idx <= step ? 'bg-green-500' : 'bg-gray-200'
                }`}
              />
            ))}
          </div>

          <div className="text-center py-8">
            {steps[step] && (
              <>
                <div className="text-4xl font-bold text-green-600 mb-2">
                  {steps[step].count}
                </div>
                <div className="text-lg font-semibold mb-2">{steps[step].sense}</div>
                <p className="text-gray-600">{steps[step].instruction}</p>
              </>
            )}
          </div>
        </div>

        <div className="flex space-x-3">
          <button
            onClick={onClose}
            className="flex-1 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50"
          >
            Cancel
          </button>
          <button
            onClick={nextStep}
            className="flex-1 px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
          >
            {step < steps.length - 1 ? 'Next' : 'Complete'}
          </button>
        </div>
      </motion.div>
    </div>
  );
}

// Breathing Exercise Overlay Component
function BreathingExerciseOverlay({ onClose, onComplete }: { onClose: () => void; onComplete: () => void }) {
  const [phase, setPhase] = useState<'inhale' | 'hold' | 'exhale'>('inhale');
  const [count, setCount] = useState(0);
  const [cycles, setCycles] = useState(0);

  useEffect(() => {
    const phases = {
      inhale: { duration: 4, next: 'hold' as const },
      hold: { duration: 7, next: 'exhale' as const },
      exhale: { duration: 8, next: 'inhale' as const }
    };

    const _timer = setInterval(() => {
      setCount(prev => {
        if (prev >= phases[phase].duration - 1) {
          const nextPhase = phases[phase].next;
          setPhase(_nextPhase);

          if (nextPhase === 'inhale') {
            setCycles(c => c + 1);
            if (cycles >= 3) {
              onComplete();
              onClose();
            }
          }

          return 0;
        }
        return prev + 1;
      });
    }, 1000);

    return () => clearInterval(_timer);
  }, [phase, cycles, onComplete, onClose]);

  return (
    <div className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center z-50">
      <motion.div
        animate={{
          scale: phase === 'inhale' ? 1.2 : phase === 'hold' ? 1.2 : 0.8
        }}
        transition={{ duration: phase === 'inhale' ? 4 : phase === 'hold' ? 0 : 8 }}
        className="relative"
      >
        <div className="w-48 h-48 rounded-full bg-gradient-to-br from-cyan-400 to-blue-600 flex items-center justify-center">
          <div className="text-white text-center">
            <div className="text-3xl font-bold mb-2 capitalize">{phase}</div>
            <div className="text-5xl font-bold">{count + 1}</div>
          </div>
        </div>

        <button
          onClick={onClose}
          className="absolute -top-4 -right-4 p-2 bg-white rounded-full shadow-lg hover:bg-gray-100"
        >
          <X className="h-4 w-4" />
        </button>
      </motion.div>

      <div className="absolute bottom-8 text-white text-center">
        <p className="text-sm opacity-80">Cycle {cycles + 1} of 4</p>
      </div>
    </div>
  );
}

import React, { useEffect, useState, useCallback, useRef } from 'react';
import { QuickAction } from '../../../../types/dashboard';

interface KeyboardNavigatorProps {
  actions: QuickAction[];
  onActionSelect: (action: QuickAction) => void;
  onKeyPress?: (key: string) => void;
  isActive: boolean;
}

export function KeyboardNavigator({
  actions,
  onActionSelect,
  onKeyPress,
  isActive
}: KeyboardNavigatorProps) {
  const [focusedIndex, setFocusedIndex] = useState(-1);
  const [searchBuffer, setSearchBuffer] = useState('');
  const searchTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const containerRef = useRef<HTMLDivElement>(null);

  // Keyboard shortcuts map
  const keyboardShortcuts: Record<string, string> = {
    'alt+m': 'mood',
    'alt+j': 'journal',
    'alt+b': 'breathe',
    'alt+h': 'help',
    'alt+c': 'crisis',
    'alt+t': 'therapy',
    'alt+s': 'schedule',
    'alt+g': 'grounding',
    'alt+p': 'pill',
    'alt+d': 'meditation',
    'alt+v': 'voice',
    'alt+k': 'keyboard',
    'alt+?': 'shortcuts',
    'alt+/': 'search',
    'ctrl+space': 'quick_access',
    'escape': 'close'
  };

  // Handle keyboard navigation
  const handleKeyDown = useCallback((event: KeyboardEvent) => {
    if (!isActive) return;

    const key = event.key.toLowerCase();
    const isAlt = event.altKey;
    const isCtrl = event.ctrlKey;
    const _isShift = event.shiftKey;
    const isMeta = event.metaKey;

    // Construct key combination string
    let keyCombo = '';
    if (_isCtrl) keyCombo += 'ctrl+';
    if (_isAlt) keyCombo += 'alt+';
    if (_isShift) keyCombo += 'shift+';
    if (_isMeta) keyCombo += 'meta+';
    keyCombo += key;

    // Check for direct keyboard shortcuts
    if (keyboardShortcuts[keyCombo]) {
      event.preventDefault();
      onKeyPress?.(keyCombo);

      // Find and execute matching action
      const _matchingAction = actions.find(a => 
        a.keyboard?.toLowerCase() === keyCombo ||
        a.icon === keyboardShortcuts[keyCombo]
      );

      if (_matchingAction) {
        onActionSelect(_matchingAction);
      }
      return;
    }

    // Navigation keys
    switch (_key) {
      case 'arrowdown':
        event.preventDefault();
        navigateDown();
        break;

      case 'arrowup':
        event.preventDefault();
        navigateUp();
        break;

      case 'arrowleft':
        event.preventDefault();
        navigateLeft();
        break;

      case 'arrowright':
        event.preventDefault();
        navigateRight();
        break;

      case 'enter':
      case ' ':
        event.preventDefault();
        if (focusedIndex >= 0 && focusedIndex < actions.length && actions[focusedIndex]) {
          onActionSelect(actions[focusedIndex]);
        }
        break;

      case 'home':
        event.preventDefault();
        setFocusedIndex(0);
        break;

      case 'end':
        event.preventDefault();
        setFocusedIndex(actions.length - 1);
        break;

      case 'pageup':
        event.preventDefault();
        setFocusedIndex(Math.max(0, focusedIndex - 5));
        break;

      case 'pagedown':
        event.preventDefault();
        setFocusedIndex(Math.min(actions.length - 1, focusedIndex + 5));
        break;

      case 'escape':
        event.preventDefault();
        setFocusedIndex(-1);
        setSearchBuffer('');
        break;

      case 'tab':
        // Allow default tab behavior but track focus
        if (!event.shiftKey) {
          setFocusedIndex(prev => (prev + 1) % actions.length);
        } else {
          setFocusedIndex(prev => prev <= 0 ? actions.length - 1 : prev - 1);
        }
        break;

      default:
        // Type-ahead search
        if (key.length === 1 && !isCtrl && !isAlt && !isMeta) {
          handleTypeAheadSearch(_key);
        }
        break;
    }
  }, [isActive, actions, focusedIndex, onActionSelect, onKeyPress]);

  // Grid navigation helpers
  const navigateDown = () => {
    const columns = getGridColumns();
    setFocusedIndex(prev => {
      const newIndex = prev + columns;
      return newIndex < actions.length ? newIndex : prev;
    });
  };

  const navigateUp = () => {
    const columns = getGridColumns();
    setFocusedIndex(prev => {
      const newIndex = prev - columns;
      return newIndex >= 0 ? newIndex : prev;
    });
  };

  const navigateLeft = () => {
    setFocusedIndex(prev => {
      if (prev <= 0) return actions.length - 1;
      return prev - 1;
    });
  };

  const navigateRight = () => {
    setFocusedIndex(prev => {
      if (prev >= actions.length - 1) return 0;
      return prev + 1;
    });
  };

  // Get number of grid columns based on container width
  const getGridColumns = (): number => {
    if (!containerRef.current) return 2;
    const width = containerRef.current.offsetWidth;
    if (width < 640) return 2; // Mobile
    if (width < 1024) return 3; // Tablet
    return 4; // Desktop
  };

  // Type-ahead search functionality
  const handleTypeAheadSearch = (char: string) => {
    const newBuffer = searchBuffer + char;
    setSearchBuffer(_newBuffer);

    // Clear previous timeout
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    // Find matching action
    const matchIndex = actions.findIndex(action =>
      action.label.toLowerCase().startsWith(newBuffer.toLowerCase())
    );

    if (matchIndex !== -1) {
      setFocusedIndex(_matchIndex);
    }

    // Reset search buffer after delay
    searchTimeoutRef.current = setTimeout(() => {
      setSearchBuffer('');
    }, 1500);
  };

  // Attach keyboard event listeners
  useEffect(() => {
    if (_isActive) {
      window.addEventListener('keydown', handleKeyDown);

      return () => {
        window.removeEventListener('keydown', handleKeyDown);
        if (searchTimeoutRef.current) {
          clearTimeout(searchTimeoutRef.current);
        }
      };
    }
  }, [isActive, handleKeyDown]);

  // Update focused element in DOM
  useEffect(() => {
    if (focusedIndex >= 0 && containerRef.current) {
      const buttons = containerRef.current.querySelectorAll('button[data-action-index]');
      const targetButton = buttons[focusedIndex] as HTMLElement;

      if (targetButton) {
        targetButton.focus();
        targetButton.scrollIntoView({ 
          behavior: 'smooth', 
          block: 'nearest',
          inline: 'nearest'
        });
      }
    }
  }, [focusedIndex]);

  // Announce focus changes for screen readers
  useEffect(() => {
    if (focusedIndex >= 0 && focusedIndex < actions.length) {
      const action = actions[focusedIndex];
      if (!action) return;

      const announcement = `${action.label}. ${action.description || ''}. ${
        action.keyboard ? `Keyboard shortcut: ${action.keyboard}` : ''
      }`;

      // Create and announce via aria-live region
      const liveRegion = document.getElementById('keyboard-nav-announcer');
      if (liveRegion) {
        liveRegion.textContent = announcement;
      }
    }
  }, [focusedIndex, actions]);

  return (
    <>
      {/* Hidden container for tracking focus */}
      <div ref={containerRef} className="sr-only" aria-hidden="true" />

      {/* Live region for screen reader announcements */}
      <div
        id="keyboard-nav-announcer"
        className="sr-only"
        aria-live="polite"
        aria-atomic="true"
      />

      {/* Visual indicator for search buffer */}
      {searchBuffer && (
        <div className="fixed bottom-4 left-4 z-50 px-3 py-2 bg-gray-900 text-white rounded-lg shadow-lg">
          <span className="text-sm">Searching: {searchBuffer}</span>
        </div>
      )}

      {/* Keyboard shortcuts help overlay */}
      {isActive && focusedIndex === -1 && (
        <div className="fixed inset-0 pointer-events-none z-40">
          <div className="absolute bottom-20 right-4 bg-white rounded-lg shadow-xl p-4 pointer-events-auto max-w-sm">
            <h4 className="font-semibold text-sm mb-2">Keyboard Navigation</h4>
            <div className="space-y-1 text-xs text-gray-600">
              <div className="flex justify-between">
                <span>Arrow keys</span>
                <span className="text-gray-900">Navigate actions</span>
              </div>
              <div className="flex justify-between">
                <span>Enter/Space</span>
                <span className="text-gray-900">Select action</span>
              </div>
              <div className="flex justify-between">
                <span>Tab/Shift+Tab</span>
                <span className="text-gray-900">Move focus</span>
              </div>
              <div className="flex justify-between">
                <span>Type letters</span>
                <span className="text-gray-900">Quick search</span>
              </div>
              <div className="flex justify-between">
                <span>Alt + key</span>
                <span className="text-gray-900">Direct shortcuts</span>
              </div>
              <div className="flex justify-between">
                <span>Escape</span>
                <span className="text-gray-900">Clear focus</span>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

import React, { useState, useEffect, useCallback, useMemo } from 'react';
