      />
    </div>
  );
}

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Mic, MicOff, Volume2, X, AlertCircle, CheckCircle } from 'lucide-react';
import { logger } from '@/utils/logger';

interface VoiceCommandInterfaceProps {
  isActive: boolean;
  onCommand: (command: string) => void;
  onClose: () => void;
  supportedCommands?: string[];
}

export function VoiceCommandInterface({
  isActive,
  onCommand,
  onClose,
  supportedCommands = []
}: VoiceCommandInterfaceProps) {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [interimTranscript, setInterimTranscript] = useState('');
  const [status, setStatus] = useState<'idle' | 'listening' | 'processing' | 'success' | 'error'>('idle');
  const [errorMessage, setErrorMessage] = useState('');
  const [volume, setVolume] = useState(0);

  const recognitionRef = useRef<unknown>(null);
  const audioContextRef = useRef<AudioContext | null>(null);
  const analyserRef = useRef<AnalyserNode | null>(null);
  const microphoneRef = useRef<MediaStreamAudioSourceNode | null>(null);
  const animationFrameRef = useRef<number | null>(null);

  // Default voice commands
  const defaultCommands = [
    'log mood', 'start meditation', 'open journal', 'call crisis line',
    'breathing exercise', 'check in', 'view schedule', 'track medication',
    'safety plan', 'grounding exercise', 'contact therapist', 'emergency help'
  ];

  const commands = supportedCommands.length > 0 ? supportedCommands : defaultCommands;

  // Initialize speech recognition
  useEffect(() => {
    if (!isActive) return;

    const SpeechRecognition = (window as unknown).SpeechRecognition || (window as unknown).webkitSpeechRecognition;

    if (!SpeechRecognition) {
      setErrorMessage('Voice commands not supported in this browser');
      setStatus('error');
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-US';

    recognition.onstart = () => {
      setStatus('listening');
      setIsListening(true);
      setErrorMessage('');
      initializeAudioAnalyzer();
    };

    recognition.onresult = (event: unknown) => {
      let _interim = '';
      let final = '';

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          final += `${transcript  } `;
        } else {
          _interim += transcript;
        }
      }

      if (final) {
        setTranscript(prev => prev + final);
        processCommand(final.trim());
      }

      setInterimTranscript(_interim);
    };

    recognition.onerror = (event: unknown) => {
      logger.error('Speech recognition error:', event.error);
      setStatus('error');
      setIsListening(false);

      switch (event.error) {
        case 'no-speech':
          setErrorMessage('No speech detected. Please try again.');
          break;
        case 'audio-capture':
          setErrorMessage('Microphone not found. Please check your settings.');
          break;
        case 'not-allowed':
          setErrorMessage('Microphone access denied. Please enable permissions.');
          break;
        default:
          setErrorMessage('An error occurred. Please try again.');
      }

      stopAudioAnalyzer();
    };

    recognition.onend = () => {
      setIsListening(false);
      stopAudioAnalyzer();
    };

    recognitionRef.current = recognition;

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      stopAudioAnalyzer();
    };
  }, [isActive]);

  // Initialize audio analyzer for volume visualization
  const initializeAudioAnalyzer = async () => {
    try {
      const _stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      audioContextRef.current = new AudioContext();
      analyserRef.current = audioContextRef.current.createAnalyser();
      microphoneRef.current = audioContextRef.current.createMediaStreamSource(_stream);

      analyserRef.current.fftSize = 256;
      microphoneRef.current.connect(analyserRef.current);

      updateVolume();
    } catch (_error) {
      logger.error('Error initializing audio analyzer:');
    }
  };

  // Update volume meter
  const updateVolume = () => {
    if (!analyserRef.current) return;

    const dataArray = new Uint8Array(analyserRef.current.frequencyBinCount);
    analyserRef.current.getByteFrequencyData(dataArray);

    const average = dataArray.reduce((a, b) => a + b) / dataArray.length;
    setVolume(average / 255);

    animationFrameRef.current = requestAnimationFrame(_updateVolume);
  };

  // Stop audio analyzer
  const stopAudioAnalyzer = () => {
    if (animationFrameRef.current) {
      cancelAnimationFrame(animationFrameRef.current);
    }

    if (microphoneRef.current) {
      microphoneRef.current.disconnect();
    }

    if (audioContextRef.current && audioContextRef.current.state !== 'closed') {
      audioContextRef.current.close();
    }

    setVolume(0);
  };

  // Process recognized command
  const processCommand = useCallback((command: string) => {
    setStatus('processing');
    const lowerCommand = command.toLowerCase();

    // Find matching command
    const matchedCommand = commands.find(cmd => 
      lowerCommand.includes(cmd.toLowerCase())
    );

    if (_matchedCommand) {
      setStatus('success');
      onCommand(_lowerCommand);

      // Provide audio feedback
      const utterance = new SpeechSynthesisUtterance(`Executing ${matchedCommand}`);
      utterance.rate = 1.2;
      window.speechSynthesis.speak(_utterance);

      setTimeout(() => {
        setStatus('idle');
        setTranscript('');
      }, 2000);
    } else {
      setStatus('error');
      setErrorMessage('Command not recognized. Please try again.');

      setTimeout(() => {
        setStatus('listening');
        setErrorMessage('');
      }, 2000);
    }
  }, [commands, onCommand]);

  // Toggle listening
  const toggleListening = () => {
    if (!recognitionRef.current) return;

    if (_isListening) {
      recognitionRef.current.stop();
      setIsListening(false);
      setStatus('idle');
    } else {
      recognitionRef.current.start();
      setIsListening(true);
    }
  };

  // Speak command list
  const speakCommands = () => {
    const commandList = commands.join(', ');
    const utterance = new SpeechSynthesisUtterance(`Available commands are: ${commandList}`);
    utterance.rate = 0.9;
    window.speechSynthesis.speak(_utterance);
  };

  if (!isActive) return null;

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: 20 }}
        className="fixed bottom-4 right-4 z-50 w-96 max-w-[calc(100vw-2rem)]"
      >
        <div className="bg-white rounded-xl shadow-2xl border border-gray-200 overflow-hidden">
          {/* Header */}
          <div className="bg-gradient-to-r from-blue-500 to-purple-600 p-4 text-white">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <div className="relative">
                  {isListening ? (
                    <Mic className="h-5 w-5 animate-pulse" />
                  ) : (
                    <MicOff className="h-5 w-5" />
                  )}
                  {/* Volume indicator */}
                  {isListening && (
                    <motion.div
                      className="absolute -inset-2 rounded-full border-2 border-white"
                      animate={{ scale: 1 + volume * 0.5 }}
                      transition={{ duration: 0.1 }}
                    />
                  )}
                </div>
                <span className="font-semibold">Voice Commands</span>
              </div>

              <button
                onClick={onClose}
                className="p-1 hover:bg-white hover:bg-opacity-20 rounded-lg transition-colors"
                aria-label="Close voice commands"
              >
                <X className="h-4 w-4" />
              </button>
            </div>

            {/* Status indicator */}
            <div className="mt-2 flex items-center space-x-2">
              {status === 'listening' && (
                <>
                  <div className="flex space-x-1">
                    <motion.div
                      className="w-1 h-4 bg-white rounded-full"
                      animate={{ scaleY: [1, 1.5, 1] }}
                      transition={{ duration: 0.5, repeat: Infinity }}
                    />
                    <motion.div
                      className="w-1 h-4 bg-white rounded-full"
                      animate={{ scaleY: [1, 1.5, 1] }}
                      transition={{ duration: 0.5, repeat: Infinity, delay: 0.1 }}
                    />
                    <motion.div
                      className="w-1 h-4 bg-white rounded-full"
                      animate={{ scaleY: [1, 1.5, 1] }}
                      transition={{ duration: 0.5, repeat: Infinity, delay: 0.2 }}
                    />
                  </div>
                  <span className="text-sm">Listening...</span>
                </>
              )}

              {status === 'processing' && (
                <>
                  <motion.div
                    className="w-4 h-4 border-2 border-white border-t-transparent rounded-full"
                    animate={{ rotate: 360 }}
                    transition={{ duration: 1, repeat: Infinity, ease: 'linear' }}
                  />
                  <span className="text-sm">Processing...</span>
                </>
              )}

              {status === 'success' && (
                <>
                  <CheckCircle className="h-4 w-4" />
                  <span className="text-sm">Command executed!</span>
                </>
              )}

              {status === 'error' && (
                <>
                  <AlertCircle className="h-4 w-4" />
                  <span className="text-sm">Error occurred</span>
                </>
              )}

              {status === 'idle' && (
                <span className="text-sm text-white text-opacity-80">
                  Click the microphone to start
                </span>
              )}
            </div>
          </div>

          {/* Transcript display */}
          <div className="p-4 min-h-[100px] max-h-[200px] overflow-y-auto bg-gray-50">
            {(transcript || interimTranscript) ? (
              <div>
                {transcript && (
                  <p className="text-gray-900 mb-1">{transcript}</p>
                )}
                {interimTranscript && (
                  <p className="text-gray-500 italic">{interimTranscript}</p>
                )}
              </div>
            ) : (
              <p className="text-gray-400 text-center">
                Your voice commands will appear here
              </p>
            )}

            {errorMessage && (
              <div className="mt-2 p-2 bg-red-100 text-red-700 rounded-lg text-sm">
                {errorMessage}
              </div>
            )}
          </div>

          {/* Controls */}
          <div className="p-4 border-t border-gray-200 flex items-center justify-between">
            <button
              onClick={speakCommands}
              className="flex items-center space-x-1 text-sm text-gray-600 hover:text-gray-900"
              aria-label="Hear available commands"
            >
              <Volume2 className="h-4 w-4" />
              <span>Hear commands</span>
            </button>

            <button
              onClick={toggleListening}
              className={`
                px-4 py-2 rounded-lg font-medium transition-all
                ${isListening
                  ? 'bg-red-500 hover:bg-red-600 text-white'
                  : 'bg-blue-500 hover:bg-blue-600 text-white'
                }
              `}
              aria-label={isListening ? 'Stop listening' : 'Start listening'}
            >
              {isListening ? 'Stop' : 'Start'} Listening
            </button>
          </div>

          {/* Command suggestions */}
          <div className="px-4 pb-4">
            <p className="text-xs text-gray-500 mb-2">Try saying:</p>
            <div className="flex flex-wrap gap-1">
              {commands.slice(0, 6).map((cmd, index) => (
                <span
                  key={index}
                  className="text-xs px-2 py-1 bg-gray-100 text-gray-600 rounded-full"
                >
                  "{cmd}"
                </span>
              ))}
            </div>
          </div>
        </div>
      </motion.div>
    </AnimatePresence>
  );
}

import { useState, _useEffect, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  BookOpen,
  PenTool,
  Search,
  Filter,
  Tag,
  _Calendar,
  Heart,
  Smile,
  Frown,
  Meh,
  TrendingUp,
  Download,
  ChevronRight,
  Sparkles,
  Brain,
  Award,
  Clock,
  FileText,
  MessageSquare,
  CheckCircle,
  AlertCircle
} from 'lucide-react';
import { format, isToday, isYesterday, differenceInDays } from 'date-fns';

interface JournalEntry {
  id: string;
  timestamp: Date;
  content: string;
  mood?: number;
  emotions?: string[];
  tags?: string[];
  sentiment?: {
    score: number; // -1 to 1
    magnitude: number; // 0 to infinity
    emotions: {
      joy: number;
      sadness: number;
      anger: number;
      fear: number;
      surprise: number;
      love: number;
    };
  };
  gratitude?: string[];
  achievements?: string[];
  prompts?: string[];
  wordCount: number;
  editedAt?: Date;
  isPrivate?: boolean;
}

interface AdvancedJournalProps {
  entries?: JournalEntry[];
  onNewEntry?: () => void;
  onEditEntry?: (id: string) => void;
  onExport?: (entries: JournalEntry[]) => void;
  _onSearch?: (query: string) => void;
}

export function AdvancedJournal({ 
  entries = [], 
  onNewEntry, 
  onEditEntry,
  onExport,
  _onSearch 
}: AdvancedJournalProps) {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedTags, setSelectedTags] = useState<string[]>([]);
  const [selectedEmotions, setSelectedEmotions] = useState<string[]>([]);
  const [dateFilter, setDateFilter] = useState<'all' | 'today' | 'week' | 'month'>('all');
  const [showFilters, setShowFilters] = useState(false);
  const [selectedEntry, setSelectedEntry] = useState<string | null>(null);

  // Mock journal entries if none provided
  const mockEntries: JournalEntry[] = [
    {
      id: '1',
      timestamp: new Date(),
      content: "Today was a breakthrough day. I finally understood that my anxiety doesn't define me. The meditation session this morning really helped me see things from a different perspective.",
      mood: 7,
      emotions: ['hopeful', 'calm', 'grateful'],
      tags: ['breakthrough', 'meditation', 'anxiety'],
      sentiment: {
        score: 0.8,
        magnitude: 2.1,
        emotions: {
          joy: 0.7,
          sadness: 0.1,
          anger: 0,
          fear: 0.1,
          surprise: 0.3,
          love: 0.6
        }
      },
      gratitude: ['My support system', 'Morning sunlight', 'Progress in therapy'],
      achievements: ['Completed 30-minute meditation', 'Reached out to a friend'],
      wordCount: 42,
      prompts: ['What made today meaningful?']
    },
    {
      id: '2',
      timestamp: new Date(Date.now() - 86400000),
      content: "Struggled with motivation today, but I'm proud that I still managed to journal. Small wins count too.",
      mood: 5,
      emotions: ['tired', 'proud', 'determined'],
      tags: ['motivation', 'small-wins'],
      sentiment: {
        score: 0.3,
        magnitude: 1.5,
        emotions: {
          joy: 0.3,
          sadness: 0.4,
          anger: 0,
          fear: 0.2,
          surprise: 0,
          love: 0.2
        }
      },
      wordCount: 18,
      prompts: ['What am I proud of today?']
    }
  ];

  const journalEntries = entries.length > 0 ? entries : mockEntries;

  // Filter entries based on search and filters
  const filteredEntries = useMemo(() => {
    let filtered = [...journalEntries];

    // Search filter
    if (_searchQuery) {
      filtered = filtered.filter(entry =>
        entry.content.toLowerCase().includes(searchQuery.toLowerCase()) ||
        entry.tags?.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase())) ||
        entry.emotions?.some(emotion => emotion.toLowerCase().includes(searchQuery.toLowerCase()))
      );
    }

    // Tag filter
    if (selectedTags.length > 0) {
      filtered = filtered.filter(entry =>
        selectedTags.every(tag => entry.tags?.includes(tag))
      );
    }

    // Emotion filter
    if (selectedEmotions.length > 0) {
      filtered = filtered.filter(entry =>
        selectedEmotions.every(emotion => entry.emotions?.includes(emotion))
      );
    }

    // Date filter
    const now = new Date();
    switch (_dateFilter) {
      case 'today':
        filtered = filtered.filter(entry => isToday(new Date(entry.timestamp)));
        break;
      case 'week':
        filtered = filtered.filter(entry => 
          differenceInDays(now, new Date(entry.timestamp)) <= 7
        );
        break;
      case 'month':
        filtered = filtered.filter(entry => 
          differenceInDays(now, new Date(entry.timestamp)) <= 30
        );
        break;
    }

    return filtered.sort((a, b) => 
      new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime()
    );
  }, [journalEntries, searchQuery, selectedTags, selectedEmotions, dateFilter]);

  // Extract all unique tags and emotions
  const allTags = useMemo(() => {
    const tags = new Set<string>();
    journalEntries.forEach(entry => {
      entry.tags?.forEach(tag => tags.add(tag));
    });
    return Array.from(_tags);
  }, [journalEntries]);

  const allEmotions = useMemo(() => {
    const emotions = new Set<string>();
    journalEntries.forEach(entry => {
      entry.emotions?.forEach(emotion => emotions.add(emotion));
    });
    return Array.from(_emotions);
  }, [journalEntries]);

  // Calculate sentiment statistics
  const sentimentStats = useMemo(() => {
    const validEntries = filteredEntries.filter(e => e.sentiment);
    if (validEntries.length === 0) return null;

    const avgSentiment = validEntries.reduce((sum, e) => sum + (e.sentiment?.score || 0), 0) / validEntries.length;
    const _dominantEmotion = validEntries.reduce((emotions, entry) => {
      if (!entry.sentiment?.emotions) return emotions;
      Object.entries(entry.sentiment.emotions).forEach(([emotion, value]) => {
        emotions[emotion] = (emotions[emotion] || 0) + value;
      });
      return emotions;
    }, {} as Record<string, number>);

    const topEmotion = Object.entries(_dominantEmotion)
      .sort((a, b) => b[1] - a[1])[0];

    return {
      avgSentiment,
      topEmotion: topEmotion ? topEmotion[0] : null,
      totalWords: filteredEntries.reduce((sum, e) => sum + e.wordCount, 0),
      avgWords: Math.round(filteredEntries.reduce((sum, e) => sum + e.wordCount, 0) / filteredEntries.length)
    };
  }, [filteredEntries]);

  // Get writing prompts based on current mood and patterns
  const writingPrompts = useMemo(() => {
    const prompts = [
      {
        category: 'reflection',
        prompt: 'What moment from today would you like to remember?',
        difficulty: 'easy'
      },
      {
        category: 'gratitude',
        prompt: 'List three things that brought you joy today, no matter how small.',
        difficulty: 'easy'
      },
      {
        category: 'growth',
        prompt: 'What challenge did you face today and what did it teach you?',
        difficulty: 'medium'
      },
      {
        category: 'emotions',
        prompt: 'Describe your emotions today as if they were weather patterns.',
        difficulty: 'medium'
      },
      {
        category: 'future',
        prompt: 'Write a letter to yourself one year from now.',
        difficulty: 'deep'
      },
      {
        category: 'relationships',
        prompt: 'How did your interactions with others affect your mood today?',
        difficulty: 'deep'
      }
    ];

    // Customize prompts based on recent sentiment
    if (sentimentStats?.avgSentiment && sentimentStats.avgSentiment < 0) {
      prompts.unshift({
        category: 'support',
        prompt: 'What would you tell a friend who was feeling the way you are now?',
        difficulty: 'medium'
      });
    }

    return prompts.slice(0, 3);
  }, [sentimentStats]);

  // Format date for display
  const formatEntryDate = (_date: Date) => {
    const entryDate = new Date(_date);
    if (isToday(_entryDate)) return 'Today';
    if (isYesterday(_entryDate)) return 'Yesterday';
    return format(entryDate, 'MMM d, yyyy');
  };

  // Get mood emoji
  const getMoodEmoji = (mood?: number) => {
    if (!mood) return <Meh className="h-5 w-5 text-gray-400" />;
    if (mood >= 7) return <Smile className="h-5 w-5 text-green-500" />;
    if (mood >= 4) return <Meh className="h-5 w-5 text-yellow-500" />;
    return <Frown className="h-5 w-5 text-red-500" />;
  };

  // Get sentiment color
  const getSentimentColor = (score?: number) => {
    if (!score) return 'text-gray-500';
    if (score > 0.5) return 'text-green-600';
    if (score > 0) return 'text-blue-600';
    if (score > -0.5) return 'text-yellow-600';
    return 'text-red-600';
  };

  // Handle export
  const handleExport = () => {
    if (_onExport) {
      onExport(filteredEntries);
    } else {
      // Create markdown export
      const markdown = filteredEntries.map(entry => `
## ${format(new Date(entry.timestamp), 'MMMM d, yyyy - h:mm a')}
**Mood:** ${entry.mood || 'Not recorded'}
**Emotions:** ${entry.emotions?.join(', ') || 'None'}
**Tags:** ${entry.tags?.join(', ') || 'None'}

${entry.content}

${entry.gratitude ? `**Gratitude:** ${entry.gratitude.join(', ')}` : ''}
${entry.achievements ? `**Achievements:** ${entry.achievements.join(', ')}` : ''}
      `).join('\n---\n');

      const _blob = new Blob([markdown], { type: 'text/markdown' });
      const url = window.URL.createObjectURL(_blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `journal-export-${format(new Date(), 'yyyy-MM-dd')}.md`;
      a.click();
      window.URL.revokeObjectURL(_url);
    }
  };

  return (
    <div className="space-y-4">
      {/* Header with stats */}
      <div className="bg-gradient-to-r from-indigo-500 to-purple-600 rounded-xl p-4 text-white">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center space-x-3">
            <BookOpen className="h-6 w-6" />
            <div>
              <h3 className="text-lg font-semibold">Your Journal</h3>
              <p className="text-xs text-indigo-100">
                {filteredEntries.length} entries â€¢ {sentimentStats?.totalWords || 0} words
              </p>
            </div>
          </div>
          <button
            onClick={onNewEntry}
            className="px-4 py-2 bg-white text-indigo-600 rounded-lg font-medium hover:bg-indigo-50 transition-colors flex items-center"
          >
            <PenTool className="h-4 w-4 mr-2" />
            New Entry
          </button>
        </div>

        {/* Sentiment overview */}
        {sentimentStats && (
          <div className="grid grid-cols-3 gap-3">
            <div className="bg-white/20 rounded-lg p-2 text-center">
              <p className="text-xs text-indigo-100">Avg Sentiment</p>
              <p className={`text-lg font-bold ${sentimentStats.avgSentiment > 0 ? 'text-green-300' : 'text-red-300'}`}>
                {sentimentStats.avgSentiment > 0 ? '+' : ''}{(sentimentStats.avgSentiment * 100).toFixed(0)}%
              </p>
            </div>
            <div className="bg-white/20 rounded-lg p-2 text-center">
              <p className="text-xs text-indigo-100">Top Emotion</p>
              <p className="text-lg font-bold capitalize">
                {sentimentStats.topEmotion || 'Mixed'}
              </p>
            </div>
            <div className="bg-white/20 rounded-lg p-2 text-center">
              <p className="text-xs text-indigo-100">Avg Words</p>
              <p className="text-lg font-bold">
                {sentimentStats.avgWords}
              </p>
            </div>
          </div>
        )}
      </div>

      {/* Search and filters */}
      <div className="space-y-3">
        <div className="flex items-center space-x-2">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
