  enableKeyboardShortcuts: boolean;
  enableVoiceNavigation: boolean;
  reducedMotion: boolean;
  highContrast: boolean;
  fontSize: 'small' | 'medium' | 'large';
  favoriteRoutes: string[];
  recentRoutes: string[];
  customShortcuts: Record<string, string>;
}

// Navigation context state
interface NavigationContextState {
  mode: NavigationMode;
  preferences: NavigationPreferences;
  isSearchOpen: boolean;
  isMobileMenuOpen: boolean;
  breadcrumbs: Array<{ label: string; path: string }>;
  quickActions: Array<{ label: string; action: () => void; icon?: string }>;
  crisisDetected: boolean;
  userRole: 'patient' | 'caregiver' | 'professional' | 'guest';
  favoriteRoutes: string[]; // Access via preferences.favoriteRoutes for compatibility
  recentRoutes: string[]; // Access via preferences.recentRoutes for compatibility
  setMode: (mode: NavigationMode) => void;
  updatePreferences: (prefs: Partial<NavigationPreferences>) => void;
  setSearchOpen: (open: boolean) => void;
  setMobileMenuOpen: (open: boolean) => void;
  addToFavorites: (route: string) => void;
  removeFromFavorites: (route: string) => void;
  addToRecent: (route: string) => void;
  setCrisisDetected: (detected: boolean) => void;
  setUserRole: (role: 'patient' | 'caregiver' | 'professional' | 'guest') => void;
}

// Default preferences
const defaultPreferences: NavigationPreferences = {
  showQuickAccess: true,
  enableKeyboardShortcuts: true,
  enableVoiceNavigation: false,
  reducedMotion: false,
  highContrast: false,
  fontSize: 'medium',
  favoriteRoutes: [],
  recentRoutes: [],
  customShortcuts: {},
};

// Create context
const NavigationContext = createContext<NavigationContextState | undefined>(_undefined);

// Provider component
export function NavigationProvider({ children }: { children: ReactNode }) {
  const location = useLocation();
  const [mode, setMode] = useState<NavigationMode>('normal');
  const [preferences, setPreferences] = useState<NavigationPreferences>(() => {
    // Load preferences from localStorage if available
    const saved = localStorage.getItem('navigationPreferences');
    return saved ? { ...defaultPreferences, ...JSON.parse(_saved) } : defaultPreferences;
  });
  const [isSearchOpen, setSearchOpen] = useState(false);
  const [isMobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [crisisDetected, setCrisisDetected] = useState(false);
  const [userRole, setUserRole] = useState<'patient' | 'caregiver' | 'professional' | 'guest'>('guest');
  const [breadcrumbs, setBreadcrumbs] = useState<Array<{ label: string; path: string }>>([]);

  // Update breadcrumbs based on current path
  useEffect(() => {
    const pathSegments = location.pathname.split('/').filter(_Boolean);
    const newBreadcrumbs: Array<{ label: string; path: string }> = [
      { label: 'Home', path: '/' }
    ];

    let currentPath = '';
    pathSegments.forEach(segment => {
      currentPath += `/${segment}`;
      const label = segment.charAt(0).toUpperCase() + segment.slice(1).replace(/-/g, ' ');
      newBreadcrumbs.push({ label, path: currentPath });
    });

    setBreadcrumbs(_newBreadcrumbs);
  }, [location]);

  // Save preferences to localStorage when they change
  useEffect(() => {
    localStorage.setItem('navigationPreferences', JSON.stringify(preferences));
  }, [preferences]);

  // Check for reduced motion preference
  useEffect(() => {
    const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
    setPreferences(prev => ({ ...prev, reducedMotion: mediaQuery.matches }));

    const handleChange = (e: MediaQueryListEvent) => {
      setPreferences(prev => ({ ...prev, reducedMotion: e.matches }));
    };

    mediaQuery.addEventListener('change', handleChange);
    return () => mediaQuery.removeEventListener('change', handleChange);
  }, []);

  // Check for high contrast preference
  useEffect(() => {
    const mediaQuery = window.matchMedia('(prefers-contrast: high)');
    setPreferences(prev => ({ ...prev, highContrast: mediaQuery.matches }));

    const handleChange = (e: MediaQueryListEvent) => {
      setPreferences(prev => ({ ...prev, highContrast: e.matches }));
    };

    mediaQuery.addEventListener('change', handleChange);
    return () => mediaQuery.removeEventListener('change', handleChange);
  }, []);

  // Auto-detect crisis mode based on route
  useEffect(() => {
    if (location.pathname.includes('/crisis')) {
      setCrisisDetected(true);
      setMode('crisis');
    } else if (crisisDetected && !location.pathname.includes('/crisis')) {
      // Keep crisis mode active for a period after leaving crisis page
      const _timeout = setTimeout(() => {
        setCrisisDetected(false);
        setMode('normal');
      }, 5 * 60 * 1000); // 5 minutes
      return () => clearTimeout(_timeout);
    }
  }, [location, crisisDetected]);

  // Update recent routes
  useEffect(() => {
    addToRecent(location.pathname);
  }, [location]);

  const updatePreferences = (prefs: Partial<NavigationPreferences>) => {
    setPreferences(prev => ({ ...prev, ...prefs }));
  };

  const addToFavorites = (route: string) => {
    setPreferences(prev => ({
      ...prev,
      favoriteRoutes: [...new Set([...prev.favoriteRoutes, route])],
    }));
  };

  const removeFromFavorites = (route: string) => {
    setPreferences(prev => ({
      ...prev,
      favoriteRoutes: prev.favoriteRoutes.filter(r => r !== route),
    }));
  };

  const addToRecent = (route: string) => {
    setPreferences(prev => {
      const recent = [route, ...prev.recentRoutes.filter(r => r !== route)].slice(0, 10);
      return { ...prev, recentRoutes: recent };
    });
  };

  // Quick actions based on current context
  const quickActions = React.useMemo(() => {
    const actions = [];

    if (_crisisDetected) {
      actions.push(
        { label: 'Call Hotline', action: () => window.location.href = 'tel:988', icon: 'ðŸ“ž' },
        { label: 'Text Support', action: () => window.location.href = 'sms:741741', icon: 'ðŸ’¬' },
        { label: 'Breathing Exercise', action: () => window.location.href = '/wellness/breathing', icon: 'ðŸ«' }
      );
    } else {
      actions.push(
        { label: 'Log Mood', action: () => window.location.href = '/wellness/mood', icon: 'ðŸ˜Š' },
        { label: 'Journal', action: () => window.location.href = '/wellness/journal', icon: 'ðŸ“' },
        { label: 'Meditate', action: () => window.location.href = '/wellness/meditation', icon: 'ðŸ§˜' }
      );
    }

    return actions;
  }, [crisisDetected]);

  const value: NavigationContextState = {
    mode,
    preferences,
    isSearchOpen,
    isMobileMenuOpen,
    breadcrumbs,
    quickActions,
    crisisDetected,
    userRole,
    favoriteRoutes: preferences.favoriteRoutes, // Expose as direct property for easier access
    recentRoutes: preferences.recentRoutes, // Expose as direct property for easier access
    setMode,
    updatePreferences,
    setSearchOpen,
    setMobileMenuOpen,
    addToFavorites,
    removeFromFavorites,
    addToRecent,
    setCrisisDetected,
    setUserRole,
  };

  return (
    <NavigationContext.Provider value={value}>
      {children}
    </NavigationContext.Provider>
  );
}

// Hook to use navigation context
export function useNavigation() {
  const context = useContext(_NavigationContext);
  if (!context) {
    throw new Error('useNavigation must be used within NavigationProvider');
  }
  return context;
}

import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Bell,
  BellOff,
  Settings,
  Clock,
  CheckCircle,
  AlertCircle,
  Info,
  X,
  Calendar,
  Zap,
  Heart,
  MessageCircle,
  Phone,
  Plus,
  Filter,
  Search,
  MoreHorizontal
} from 'lucide-react';
import {
  comprehensiveNotificationService,
  NotificationPreferences,
  NotificationRule,
  SmartNotification
} from '../../services/notifications/ComprehensiveNotificationService';
import { logger } from '../../utils/logger';

interface NotificationCenterProps {
  className?: string;
}

export const NotificationCenter: React.FC<NotificationCenterProps> = ({
  className = ''
}) => {
  const [preferences, setPreferences] = useState<NotificationPreferences | null>(null);
  const [rules, setRules] = useState<NotificationRule[]>([]);
  const [notifications, setNotifications] = useState<SmartNotification[]>([]);
  const [_isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState<'notifications' | 'rules' | 'settings'>('notifications');
  const [filterPriority, setFilterPriority] = useState<'all' | 'low' | 'medium' | 'high' | 'critical'>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [inAppNotifications, setInAppNotifications] = useState<any[]>([]);

  useEffect(() => {
    initializeNotificationCenter();
    setupInAppNotificationListener();
  }, []);

  const initializeNotificationCenter = async () => {
    try {
      setIsLoading(true);
      
      // Load preferences, rules, and notification history
      const _userPreferences = comprehensiveNotificationService.getPreferences();
      const _allRules = comprehensiveNotificationService.getAllRules();
      const _notificationHistory = comprehensiveNotificationService.getNotificationHistory();
      
      setPreferences(_userPreferences);
      setRules(_allRules);
      setNotifications(_notificationHistory);
      
    } catch (_error) {
      logger.error('Failed to initialize notification center:');
    } finally {
      setIsLoading(false);
    }
  };

  const setupInAppNotificationListener = () => {
    const handleInAppNotification = (event: CustomEvent) => {
      const notification = event.detail;
      setInAppNotifications(prev => [...prev, {
        ...notification,
        timestamp: Date.now(),
        show: true
      }]);
      
      // Auto-hide after 8 seconds for non-critical notifications
      if (notification.priority !== 'critical') {
        setTimeout(() => {
          setInAppNotifications(prev => 
            prev.map(n => n.id === notification.id ? { ...n, show: false } : n)
          );
        }, 8000);
      }
    };

    window.addEventListener('inAppNotification', handleInAppNotification as EventListener);
    
    return () => {
      window.removeEventListener('inAppNotification', handleInAppNotification as EventListener);
    };
  };

  const updatePreferences = async (_updates: Partial<NotificationPreferences>) => {
    try {
      await comprehensiveNotificationService.updatePreferences(_updates);
      const _updatedPreferences = comprehensiveNotificationService.getPreferences();
      setPreferences(_updatedPreferences);
    } catch (_error) {
      logger.error('Failed to update preferences:');
    }
  };

  const toggleRule = async (ruleId: string) => {
    try {
      const rule = rules.find(r => r.id === ruleId);
      if (rule) {
        rule.isActive = !rule.isActive;
        setRules(prev => prev.map(r => r.id === ruleId ? rule : r));
        
        // In a real implementation, this would update the rule in the service
        // For now, we'll just update the local state
      }
    } catch (_error) {
      logger.error('Failed to toggle rule:');
    }
  };

  const dismissInAppNotification = (notificationId: string) => {
    setInAppNotifications(prev => 
      prev.map(n => n.id === notificationId ? { ...n, show: false } : n)
    );
  };

  const handleNotificationAction = async (notificationId: string, actionId: string) => {
    try {
      await comprehensiveNotificationService.handleNotificationClick(notificationId, actionId);
      dismissInAppNotification(_notificationId);
    } catch (_error) {
      logger.error('Failed to handle notification action:');
    }
  };

  const getNotificationIcon = (_type: NotificationRule['type']) => {
    switch (_type) {
      case 'wellness_reminder': return Heart;
      case 'medication_reminder': return Clock;
      case 'mood_check': return MessageCircle;
      case 'crisis_followup': return AlertCircle;
      case 'appointment_reminder': return Calendar;
      case 'self_care_prompt': return Zap;
      default: return Info;
    }
  };

  const getPriorityColor = (_priority: string) => {
    switch (_priority) {
      case 'critical': return 'text-red-600 bg-red-50 border-red-200';
      case 'high': return 'text-orange-600 bg-orange-50 border-orange-200';
      case 'medium': return 'text-blue-600 bg-blue-50 border-blue-200';
      case 'low': return 'text-gray-600 bg-gray-50 border-gray-200';
      default: return 'text-gray-600 bg-gray-50 border-gray-200';
    }
  };

  const filteredNotifications = notifications.filter(notification => {
    const matchesPriority = filterPriority === 'all' || notification._priority === filterPriority;
    const matchesSearch = searchQuery === '' || 
      notification.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      notification.body.toLowerCase().includes(searchQuery.toLowerCase());
    
    return matchesPriority && matchesSearch;
  });

  if (_isLoading) {
    return (
