    data-priority="critical"
  >
    <AlertTriangle className="h-6 w-6" />
  </button>
));

CrisisButton.displayName = 'CrisisButton';

interface OptimizedCrisisInterventionProps {
  userId?: string;
  onActivate?: () => void;
  preloadResources?: boolean;
}

export function OptimizedCrisisIntervention({
  userId,
  onActivate,
  preloadResources = true,
}: OptimizedCrisisInterventionProps) {
  const [isActive, setIsActive] = useState(false);
  const [responseTime, setResponseTime] = useState<number | null>(null);
  const activationTime = useRef<number>(0);
  const modalRef = useRef<HTMLDivElement>(null);

  // Pre-cache DOM references
  const _buttonRef = useRef<HTMLButtonElement>(null);
  const _resourcesRef = useRef<HTMLDivElement>(null);

  // Optimized activation handler
  const handleActivation = useCallback(() => {
    activationTime.current = performance.now();
    performanceMonitor.measureStart('crisis-intervention-activate');

    // Immediate state update - no transitions
    setIsActive(true);

    // Force immediate render
    if (modalRef.current) {
      modalRef.current.style.display = 'block';
      modalRef.current.classList.add('active');
    }

    // Measure response time
    requestAnimationFrame(() => {
      const time = performance.now() - activationTime.current;
      setResponseTime(time);
      performanceMonitor.measureEnd('crisis-intervention-activate');

      if (time > 200) {
        logger.warn(`Crisis intervention response time exceeded target: ${time.toFixed(2)}ms`);
      }
    });

    // Notify parent
    if (_onActivate) {
      onActivate();
    }

    // Log to analytics (non-blocking)
// @ts-expect-error - requestIdleCallback is a global API
    requestIdleCallback(() => {
      logCrisisActivation(_userId);
    });
  }, [userId, onActivate]);

  // Set up global listener for crisis activation
  useEffect(() => {
    const listener = () => handleActivation();
    window.addEventListener('crisis-activate', listener);

    // Preload resources if enabled
    if (_preloadResources) {
      preloadCrisisResources();
    }

    return () => {
      window.removeEventListener('crisis-activate', listener);
    };
  }, [handleActivation, preloadResources]);

  // Keyboard shortcut for crisis activation (Ctrl+Shift+H)
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.ctrlKey && e.shiftKey && e.key === 'H') {
        e.preventDefault();
        handleActivation();
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [handleActivation]);

  // Close handler
  const handleClose = useCallback(() => {
    setIsActive(false);
    if (modalRef.current) {
      modalRef.current.style.display = 'none';
      modalRef.current.classList.remove('active');
    }
  }, []);

  // Call hotline (immediate action)
  const callHotline = useCallback((number: string) => {
    performanceMonitor.measureStart('crisis-call-initiate');

    if (number === '988' || number.includes('800')) {
      window.location.href = `tel:${number}`;
    } else {
      navigator.clipboard.writeText(number);
      alert(`Number copied: ${number}`);
    }

    performanceMonitor.measureEnd('crisis-call-initiate');
  }, []);

  return (
    <>
      {/* Always-visible crisis button */}
      <CrisisButton />

      {/* Pre-rendered crisis modal (hidden by default) */}
      <div
        ref={modalRef}
        className="crisis-modal-optimized fixed inset-0 z-[10000] bg-black bg-opacity-90 hidden"
        style={{ display: isActive ? 'block' : 'none' }}
        data-priority="critical"
      >
        <div className="min-h-screen flex items-center justify-center p-4">
          <div className="bg-white rounded-xl max-w-2xl w-full p-6 shadow-2xl">
            {/* Header with response time indicator */}
            <div className="flex items-center justify-between mb-6">
              <div className="flex items-center space-x-3">
                <div className="p-3 bg-red-100 rounded-full">
                  <Shield className="h-8 w-8 text-red-600" />
                </div>
                <div>
                  <h1 className="text-2xl font-bold text-gray-900">Crisis Support</h1>
                  <p className="text-sm text-gray-600">Immediate help is available</p>
                </div>
              </div>
              {responseTime && (
                <div className="flex items-center space-x-2 text-xs text-gray-500">
                  <Activity className="h-3 w-3" />
                  <span>{responseTime.toFixed(0)}ms</span>
                </div>
              )}
            </div>

            {/* Emergency hotlines - highest priority */}
            <div className="mb-6 p-4 bg-red-50 rounded-lg border-2 border-red-200">
              <h2 className="text-lg font-semibold text-red-900 mb-3 flex items-center">
                <Phone className="h-5 w-5 mr-2" />
                Emergency Hotlines
              </h2>
              <div className="space-y-2">
                {CRISIS_RESOURCES.hotlines.map((hotline) => (
                  <button
                    key={hotline.number}
                    onClick={() => callHotline(hotline.number)}
                    className="w-full text-left p-3 bg-white rounded-lg hover:bg-red-100 transition-colors duration-100 flex items-center justify-between group"
                  >
                    <div>
                      <p className="font-medium text-gray-900">{hotline.name}</p>
                      <p className="text-sm text-gray-600">{hotline.number}</p>
                    </div>
                    <Phone className="h-5 w-5 text-red-600 group-hover:animate-pulse" />
                  </button>
                ))}
              </div>
            </div>

            {/* Immediate actions */}
            <div className="mb-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-3 flex items-center">
                <Heart className="h-5 w-5 mr-2 text-red-500" />
                Immediate Actions
              </h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                {CRISIS_RESOURCES.immediate.map((action, index) => (
                  <div
                    key={index}
                    className="p-3 bg-blue-50 rounded-lg border border-blue-200"
                  >
                    <p className="text-sm text-blue-900">{action}</p>
                  </div>
                ))}
              </div>
            </div>

            {/* Coping techniques */}
            <div className="mb-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-3">
                Quick Coping Techniques
              </h3>
              <div className="space-y-2">
                {CRISIS_RESOURCES.techniques.map((technique) => (
                  <div
                    key={technique.name}
                    className="flex items-center justify-between p-3 bg-gray-50 rounded-lg"
                  >
                    <div>
                      <p className="font-medium text-gray-900">{technique.name}</p>
                      <p className="text-xs text-gray-500">{technique.duration}</p>
                    </div>
                    <div className="text-sm text-green-600 font-medium">
                      {technique.effectiveness}% effective
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Action buttons */}
            <div className="flex items-center justify-between">
              <button
                onClick={() => window.location.href = '/crisis/chat'}
                className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors flex items-center space-x-2"
              >
                <MessageCircle className="h-5 w-5" />
                <span>Start Crisis Chat</span>
              </button>

              <button
                onClick={handleClose}
                className="px-6 py-3 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Inline critical styles for fastest rendering */}
      <style>{`
        .crisis-button-optimized {
          will-change: transform;
          contain: layout style paint;
        }

        .crisis-modal-optimized {
          will-change: opacity;
          contain: layout style paint;
        }

        .crisis-modal-optimized.active {
          animation: crisis-fade-in 100ms ease-out;
        }

        @keyframes crisis-fade-in {
          from { opacity: 0; }
          to { opacity: 1; }
        }

        /* Force GPU acceleration */
        .crisis-button-optimized,
        .crisis-modal-optimized {
          transform: translateZ(0);
          backface-visibility: hidden;
        }
      `}</style>
    </>
  );
}

/**
 * Preload crisis resources for instant access
 */
function preloadCrisisResources() {
  // Preload images
  const images = ['/crisis-support.svg', '/emergency.svg'];
  images.forEach(src => {
    const img = new Image();
    img.src = src;
  });

  // Preconnect to emergency services APIs
  const link = document.createElement('link');
  link.rel = 'preconnect';
  link.href = 'https://988lifeline.org';
  document.head.appendChild(_link);

  // Cache crisis data in IndexedDB for offline access
  if ('indexedDB' in window) {
    cacheOfflineCrisisData();
  }
}

/**
 * Cache crisis data for offline support
 */
async function cacheOfflineCrisisData() {
  try {
    const db = await openCrisisDatabase();
    const tx = db.transaction(['resources'], 'readwrite');
    const store = tx.objectStore('resources');

    await store.put({
      id: 'crisis-resources',
      data: CRISIS_RESOURCES,
      timestamp: Date.now(),
    });
  } catch (_error) {
    logger.error('Failed to cache crisis data:');
  }
}

/**
 * Open IndexedDB for crisis data
 */
function openCrisisDatabase(): Promise<IDBDatabase> {
  return new Promise((resolve, reject) => {
    const request = indexedDB.open('CrisisSupport', 1);

    request.onerror = () => reject(request.error);
    request.onsuccess = () => resolve(request.result);

    request.onupgradeneeded = (event) => {
      const db = (event.target as unknown).result;
      if (!db.objectStoreNames.contains('resources')) {
        db.createObjectStore('resources', { keyPath: 'id' });
      }
    };
  });
}

/**
 * Log crisis activation for analytics
 */
function logCrisisActivation(userId?: string) {
  // Send to analytics service
  if (window.gtag) {
    window.gtag('event', 'crisis_activation', {
      event_category: 'Crisis Support',
      event_label: userId || 'anonymous',
      value: 1,
    });
  }

  // Store locally for follow-up
  localStorage.setItem('last_crisis_activation', Date.now().toString());
}

import { logger } from '@/utils/logger';
/**
 * Performance Monitoring Dashboard
 * Displays real-time performance metrics for crisis response and general app performance
 */

import { useState, useEffect, useMemo, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Activity,
  AlertTriangle,
  Heart,
  MemoryStick,
  TrendingUp,
  TrendingDown,
  Wifi,
  WifiOff,
  Zap,
  CheckCircle,
  XCircle,
  AlertCircle,
  Smartphone
} from 'lucide-react';
import { performanceMonitor, PERFORMANCE_THRESHOLDS } from '../../utils/performance/performanceMonitor';
import { useMobileFeatures } from '../../hooks/useMobileFeatures';

interface MetricSummary {
  name: string;
  value: number;
  threshold: number;
  _status: 'good' | 'warning' | 'critical';
  trend: 'up' | 'down' | 'stable';
}

interface PerformanceStats {
  crisisResponseTime: number;
  pageLoadTime: number;
  memoryUsage: number;
  coreWebVitals: {
    LCP: number;
    FID: number;
    CLS: number;
    FCP: number;
    TTFB: number;
  };
  networkSpeed: string;
  deviceInfo: unknown;
}

export function PerformanceDashboard() {
  const { deviceInfo } = useMobileFeatures();
  const [stats, setStats] = useState<PerformanceStats | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [refreshInterval, setRefreshInterval] = useState(5000); // 5 seconds
  const [showDetails, setShowDetails] = useState(false);
  const [selectedMetric, setSelectedMetric] = useState<string | null>(null);

  const refreshStats = useCallback(async () => {
    try {
      const summary = performanceMonitor.getPerformanceSummary();
      const _metrics = performanceMonitor.getMetrics();
      
      // Get memory info if available
      const memoryInfo = (performance as unknown).memory || {};
      
      // Get connection info if available
      const connection = (navigator as unknown).connection || {};
      
      const _newStats: PerformanceStats = {
        crisisResponseTime: summary.crisis_response_time?.avg || 0,
        pageLoadTime: summary.navigation_timing?.avg || 0,
        memoryUsage: memoryInfo.usedJSHeapSize ? 
          memoryInfo.usedJSHeapSize / 1048576 : 0, // Convert to MB
        coreWebVitals: {
          LCP: summary.LCP?.latest || 0,
          FID: summary.FID?.latest || 0,
          CLS: summary.CLS?.latest || 0,
          FCP: summary.FCP?.latest || 0,
          TTFB: summary.TTFB?.latest || 0
        },
        networkSpeed: connection.effectiveType || 'unknown',
        deviceInfo
      };
      
      setStats(_newStats);
    } catch (_error) {
      logger.error('Failed to refresh performance stats:');
    }
  }, [deviceInfo]);

  // Initialize performance monitoring
  useEffect(() => {
    const initializeMonitoring = async () => {
      try {
        // Start monitoring
        performanceMonitor.recordMetric('dashboard_load_start', Date.now());
        
        // Get initial stats
        await refreshStats();
        
        performanceMonitor.recordMetric('dashboard_load_complete', Date.now());
        setIsLoading(false);
      } catch (_error) {
        logger.error('Failed to initialize performance monitoring:');
        setIsLoading(false);
      }
    };

    initializeMonitoring();
    refreshStats();
  }, [refreshStats]);

  // Auto-refresh performance data
  useEffect(() => {
    const _interval = setInterval(refreshStats, refreshInterval);
    return () => clearInterval(_interval);
  }, [refreshInterval, refreshStats]);

  // Generate metric summaries with status and trends
  const metricSummaries: MetricSummary[] = useMemo(() => {
    if (!stats) return [];

    return [
      {
        name: 'Crisis Response',
        value: stats.crisisResponseTime,
        threshold: PERFORMANCE_THRESHOLDS.CRISIS_PAGE_LOAD,
        _status: stats.crisisResponseTime <= PERFORMANCE_THRESHOLDS.CRISIS_PAGE_LOAD ? 'good' : 
                stats.crisisResponseTime <= PERFORMANCE_THRESHOLDS.CRISIS_PAGE_LOAD * 1.5 ? 'warning' : 'critical',
        trend: 'stable'
      },
      {
        name: 'Page Load Time',
        value: stats.pageLoadTime,
        threshold: 3000,
        _status: stats.pageLoadTime <= 3000 ? 'good' : 
                stats.pageLoadTime <= 5000 ? 'warning' : 'critical',
        trend: 'stable'
      },
      {
        name: 'Memory Usage',
        value: stats.memoryUsage,
        threshold: PERFORMANCE_THRESHOLDS.MAX_MEMORY_MB,
        _status: stats.memoryUsage <= PERFORMANCE_THRESHOLDS.MAX_MEMORY_MB * 0.7 ? 'good' : 
                stats.memoryUsage <= PERFORMANCE_THRESHOLDS.MAX_MEMORY_MB ? 'warning' : 'critical',
        trend: 'up'
      },
      {
        name: 'Largest Contentful Paint',
        value: stats.coreWebVitals.LCP,
        threshold: PERFORMANCE_THRESHOLDS.LCP,
        _status: stats.coreWebVitals.LCP <= PERFORMANCE_THRESHOLDS.LCP ? 'good' : 
                stats.coreWebVitals.LCP <= PERFORMANCE_THRESHOLDS.LCP * 1.5 ? 'warning' : 'critical',
        trend: 'stable'
      },
      {
        name: 'First Input Delay',
        value: stats.coreWebVitals.FID,
        threshold: PERFORMANCE_THRESHOLDS.FID,
        _status: stats.coreWebVitals.FID <= PERFORMANCE_THRESHOLDS.FID ? 'good' : 
                stats.coreWebVitals.FID <= PERFORMANCE_THRESHOLDS.FID * 1.5 ? 'warning' : 'critical',
        trend: 'stable'
      },
      {
        name: 'Cumulative Layout Shift',
        value: stats.coreWebVitals.CLS,
        threshold: PERFORMANCE_THRESHOLDS.CLS,
        _status: stats.coreWebVitals.CLS <= PERFORMANCE_THRESHOLDS.CLS ? 'good' : 
                stats.coreWebVitals.CLS <= PERFORMANCE_THRESHOLDS.CLS * 2 ? 'warning' : 'critical',
        trend: 'stable'
      }
    ];
  }, [stats]);

  const getStatusIcon = (_status: string) => {
    switch (_status) {
      case 'good': return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'warning': return <AlertCircle className="h-5 w-5 text-yellow-500" />;
      case 'critical': return <XCircle className="h-5 w-5 text-red-500" />;
      default: return <Activity className="h-5 w-5 text-gray-500" />;
    }
  };

  const getStatusColor = (_status: string) => {
    switch (_status) {
      case 'good': return 'text-green-600 bg-green-50';
      case 'warning': return 'text-yellow-600 bg-yellow-50';
      case 'critical': return 'text-red-600 bg-red-50';
      default: return 'text-gray-600 bg-gray-50';
    }
  };

  const formatValue = (name: string, value: number) => {
    if (name === 'Memory Usage') {
      return `${value.toFixed(1)} MB`;
    } else if (name === 'Cumulative Layout Shift') {
      return value.toFixed(3);
    } else {
      return `${value.toFixed(0)} ms`;
    }
  };

  if (_isLoading) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
        <span className="ml-4 text-gray-600">Loading performance data...</span>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Performance Dashboard</h2>
          <p className="text-sm text-gray-600 mt-1">
            Real-time monitoring for optimal mental health support
          </p>
        </div>
        <div className="flex items-center space-x-3">
          <button
            onClick={() => setShowDetails(!showDetails)}
            className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50"
          >
            {showDetails ? 'Hide Details' : 'Show Details'}
          </button>
          <button
            onClick={refreshStats}
            className="px-4 py-2 text-sm font-medium text-white bg-primary-600 rounded-lg hover:bg-primary-700 flex items-center"
          >
            <Activity className="h-4 w-4 mr-2" />
            Refresh
          </button>
        </div>
      </div>

      {/* Critical Metrics Alert */}
      {metricSummaries.some(m => m._status === 'critical') && (
        <motion.div
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-red-50 border border-red-200 rounded-lg p-4"
        >
          <div className="flex items-center">
            <AlertTriangle className="h-5 w-5 text-red-500 mr-3" />
            <div>
              <h3 className="font-medium text-red-800">Performance Issues Detected</h3>
              <p className="text-sm text-red-600 mt-1">
                Critical performance metrics are exceeding safe thresholds. Crisis features may be impacted.
              </p>
            </div>
          </div>
        </motion.div>
      )}

      {/* Device & Network Info */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="bg-white rounded-lg border border-gray-200 p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <Smartphone className="h-5 w-5 text-blue-500 mr-3" />
              <span className="text-sm font-medium text-gray-700">Device</span>
            </div>
          </div>
          <div className="mt-3 space-y-1 text-sm text-gray-600">
            <div>Type: {deviceInfo.isMobile ? 'Mobile' : deviceInfo.isTablet ? 'Tablet' : 'Desktop'}</div>
            <div>Screen: {deviceInfo.screenSize.toUpperCase()}</div>
            <div>PWA: {deviceInfo.isPWA ? 'Yes' : 'No'}</div>
          </div>
        </div>

        <div className="bg-white rounded-lg border border-gray-200 p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              {stats?.networkSpeed === 'offline' ? 
                <WifiOff className="h-5 w-5 text-red-500 mr-3" /> :
                <Wifi className="h-5 w-5 text-green-500 mr-3" />
              }
              <span className="text-sm font-medium text-gray-700">Network</span>
            </div>
          </div>
          <div className="mt-3 text-sm text-gray-600">
            <div>Speed: {stats?.networkSpeed || 'Unknown'}</div>
            <div>Status: {navigator.onLine ? 'Online' : 'Offline'}</div>
          </div>
        </div>

        <div className="bg-white rounded-lg border border-gray-200 p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <MemoryStick className="h-5 w-5 text-purple-500 mr-3" />
              <span className="text-sm font-medium text-gray-700">Memory</span>
            </div>
          </div>
          <div className="mt-3 text-sm text-gray-600">
            <div>Used: {formatValue('Memory Usage', stats?.memoryUsage || 0)}</div>
            <div className={`mt-1 px-2 py-1 rounded text-xs ${
              (stats?.memoryUsage || 0) <= PERFORMANCE_THRESHOLDS.MAX_MEMORY_MB * 0.7 ? 'bg-green-100 text-green-700' :
              (stats?.memoryUsage || 0) <= PERFORMANCE_THRESHOLDS.MAX_MEMORY_MB ? 'bg-yellow-100 text-yellow-700' :
              'bg-red-100 text-red-700'
            }`}>
              {(stats?.memoryUsage || 0) <= PERFORMANCE_THRESHOLDS.MAX_MEMORY_MB * 0.7 ? 'Optimal' :
               (stats?.memoryUsage || 0) <= PERFORMANCE_THRESHOLDS.MAX_MEMORY_MB ? 'High' : 'Critical'}
            </div>
          </div>
        </div>
      </div>

      {/* Performance Metrics Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {metricSummaries.map((metric) => (
          <motion.div
            key={metric.name}
            whileHover={{ scale: 1.02 }}
            className={`bg-white rounded-lg border-2 transition-colors cursor-pointer ${
              selectedMetric === metric.name ? 'border-primary-300' : 'border-gray-200'
            }`}
            onClick={() => setSelectedMetric(selectedMetric === metric.name ? null : metric.name)}
          >
            <div className="p-4">
              <div className="flex items-center justify-between mb-3">
                <div className="flex items-center">
                  {getStatusIcon(metric._status)}
                  <span className="ml-2 text-sm font-medium text-gray-700">
                    {metric.name}
                  </span>
                </div>
                {metric.trend === 'up' ? (
                  <TrendingUp className="h-4 w-4 text-red-500" />
                ) : metric.trend === 'down' ? (
                  <TrendingDown className="h-4 w-4 text-green-500" />
                ) : null}
              </div>

              <div className="space-y-2">
                <div className="flex items-baseline justify-between">
                  <span className="text-2xl font-bold text-gray-900">
                    {formatValue(metric.name, metric.value)}
                  </span>
                  <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(metric._status)}`}>
                    {metric._status.toUpperCase()}
                  </span>
                </div>

                {/* Progress Bar */}
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div
                    className={`h-2 rounded-full transition-all duration-300 ${
                      metric._status === 'good' ? 'bg-green-500' :
                      metric._status === 'warning' ? 'bg-yellow-500' : 'bg-red-500'
                    }`}
                    style={{
                      width: `${Math.min(100, (metric.value / metric.threshold) * 100)}%`
                    }}
                  />
                </div>

                <div className="text-xs text-gray-500">
                  Threshold: {formatValue(metric.name, metric.threshold)}
                </div>
              </div>
            </div>

            {/* Expanded Details */}
            <AnimatePresence>
              {selectedMetric === metric.name && showDetails && (
                <motion.div
                  initial={{ height: 0, opacity: 0 }}
                  animate={{ height: 'auto', opacity: 1 }}
                  exit={{ height: 0, opacity: 0 }}
                  className="border-t border-gray-200 p-4 bg-gray-50"
                >
                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <span className="text-gray-600">Current:</span>
                      <span className="font-medium">{formatValue(metric.name, metric.value)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Target:</span>
                      <span className="font-medium">{formatValue(metric.name, metric.threshold)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Performance:</span>
                      <span className={`font-medium ${
                        metric.value <= metric.threshold ? 'text-green-600' : 'text-red-600'
                      }`}>
                        {metric.value <= metric.threshold ? 'Within Target' : 'Exceeds Target'}
                      </span>
                    </div>
                  </div>
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>
        ))}
      </div>

      {/* Crisis-Specific Metrics */}
      <div className="bg-red-50 rounded-lg border border-red-200 p-6">
        <div className="flex items-center mb-4">
          <Heart className="h-6 w-6 text-red-500 mr-3" />
          <h3 className="text-lg font-semibold text-red-800">Crisis Response Performance</h3>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="text-center">
            <div className="text-2xl font-bold text-red-700">
              {formatValue('Crisis Response', stats?.crisisResponseTime || 0)}
            </div>
            <div className="text-sm text-red-600">Response Time</div>
            <div className="text-xs text-red-500 mt-1">
              Target: &lt;200ms
            </div>
          </div>
          
          <div className="text-center">
            <div className="text-2xl font-bold text-red-700">
              &lt;50ms
            </div>
            <div className="text-sm text-red-600">988 Access</div>
            <div className="text-xs text-red-500 mt-1">
              Critical Priority
            </div>
          </div>
          
          <div className="text-center">
            <div className="text-2xl font-bold text-red-700">
              &lt;100ms
            </div>
            <div className="text-sm text-red-600">Safety Plan</div>
            <div className="text-xs text-red-500 mt-1">
              High Priority
            </div>
          </div>
          
          <div className="text-center">
            <div className="text-2xl font-bold text-red-700">
              &lt;50ms
            </div>
            <div className="text-sm text-red-600">Emergency Contacts</div>
            <div className="text-xs text-red-500 mt-1">
              Critical Priority
            </div>
          </div>
        </div>
      </div>

      {/* Performance Recommendations */}
      <div className="bg-blue-50 rounded-lg border border-blue-200 p-6">
        <div className="flex items-center mb-4">
          <Zap className="h-6 w-6 text-blue-500 mr-3" />
          <h3 className="text-lg font-semibold text-blue-800">Optimization Recommendations</h3>
        </div>
        
        <div className="space-y-3">
          {metricSummaries
            .filter(m => m._status !== 'good')
            .map(metric => (
              <div key={metric.name} className="flex items-start space-x-3">
                <div className={`w-2 h-2 rounded-full mt-2 ${
                  metric._status === 'warning' ? 'bg-yellow-400' : 'bg-red-400'
                }`} />
                <div className="flex-1">
                  <div className="text-sm font-medium text-blue-800">
                    {metric.name} Optimization
                  </div>
                  <div className="text-sm text-blue-600 mt-1">
                    {metric.name === 'Crisis Response' && 'Consider preloading crisis resources and optimizing critical path rendering.'}
                    {metric.name === 'Memory Usage' && 'Review memory leaks and implement aggressive cleanup for unused resources.'}
                    {metric.name === 'Page Load Time' && 'Implement code splitting and lazy loading for non-critical components.'}
                    {metric.name === 'Largest Contentful Paint' && 'Optimize images and implement resource prioritization.'}
                    {metric.name === 'First Input Delay' && 'Reduce JavaScript execution time and implement input response optimization.'}
                    {metric.name === 'Cumulative Layout Shift' && 'Reserve space for dynamic content and optimize font loading.'}
                  </div>
                </div>
              </div>
            ))}
          
          {metricSummaries.every(m => m._status === 'good') && (
            <div className="flex items-center space-x-3">
              <CheckCircle className="w-5 h-5 text-green-500" />
              <span className="text-sm text-green-700">
                All performance _metrics are within optimal ranges!
              </span>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

/**
 * High-Performance Virtualized List Component
 * Optimized for mental health community posts and large datasets
 */

import React, { useCallback, useRef, useMemo, memo, CSSProperties } from 'react';
import { List as List, VList as VList } from 'react-window';
import type { ListChildComponentProps } from 'react-window';
import { useInView } from 'react-intersection-observer';
import { performanceMonitor } from '../../utils/performance/performanceMonitor';
import { LoadingFallbacks } from '../../utils/performance/concurrentFeatures';

interface VirtualizedListProps<T> {
  items: T[];
  renderItem: (item: T, index: number, style: CSSProperties) => React.ReactNode;
  itemHeight?: number | ((index: number) => number);
  height?: number;
  width?: string | number;
  overscan?: number;
  onEndReached?: () => void;
  endReachedThreshold?: number;
  loading?: boolean;
  emptyComponent?: React.ReactNode;
  headerComponent?: React.ReactNode;
  footerComponent?: React.ReactNode;
  className?: string;
  estimatedItemSize?: number;
  getItemKey?: (index: number, data: T[]) => string;
}

/**
 * Optimized virtualized list with mental health app specific features
 */
export function VirtualizedList<T>({
  items,
  renderItem,
  itemHeight = 150,
  height = 600,
  width = '100%',
  overscan = 3,
  onEndReached,
  endReachedThreshold = 0.8,
  loading = false,
  emptyComponent,
  headerComponent,
  footerComponent,
  className = '',
  estimatedItemSize = 150,
  getItemKey,
}: VirtualizedListProps<T>) {
  const listRef = useRef<unknown>(null);
  const [isNearEnd, setIsNearEnd] = React.useState(false);
  const lastScrollTop = useRef(0);
  const scrollFrameId = useRef<number>();

  // Performance monitoring
  React.useEffect(() => {
    performanceMonitor.measureStart('virtualized-list-mount');
    return () => {
      performanceMonitor.measureEnd('virtualized-list-mount');
    };
  }, []);

  // Determine if we should use fixed or variable size list
  const isVariableHeight = typeof itemHeight === 'function';

  // Memoized item renderer
  const ItemRenderer = memo(({ index, style, data }: ListChildComponentProps<T[]>) => {
    const item = data[index];

    // Add intersection observer for lazy loading images
    const { ref, inView } = useInView({
      threshold: 0,
      triggerOnce: true,
      rootMargin: '50px',
    });

    return (
      <div ref={ref} style={style} className="px-4">
        {inView && item ? renderItem(item, index, style) : <LoadingFallbacks.Skeleton lines={3} />}
      </div>
    );
  });

  // Handle scroll with throttling for performance
  const handleScroll = useCallback(({ scrollOffset, _scrollDirection }: unknown) => {
    // Cancel previous frame
    if (scrollFrameId.current) {
      cancelAnimationFrame(scrollFrameId.current);
    }

    scrollFrameId.current = requestAnimationFrame(() => {
      const scrollTop = scrollOffset;
      const scrollHeight = listRef.current?.props?.height || height;
      const totalHeight = isVariableHeight
        ? items.length * estimatedItemSize
        : items.length * (itemHeight as number);

      const scrollPercentage = (scrollTop + scrollHeight) / totalHeight;

      // Check if near end
      if (scrollPercentage > endReachedThreshold && !isNearEnd && onEndReached) {
        setIsNearEnd(true);
        performanceMonitor.measureStart('infinite-scroll-load');
        onEndReached();

        // Reset after delay to allow new loads
        setTimeout(() => {
          setIsNearEnd(false);
          performanceMonitor.measureEnd('infinite-scroll-load');
        }, 1000);
      }

      lastScrollTop.current = scrollTop;
    });
  }, [items.length, isNearEnd, onEndReached, endReachedThreshold, height, itemHeight, isVariableHeight, estimatedItemSize]);

  // Get item size for variable height list
  const getItemSize = useMemo(() => {
    if (_isVariableHeight) {
      return itemHeight as (index: number) => number;
    }
    return (_index: number) => itemHeight as number;
  }, [itemHeight, isVariableHeight]);

  // Custom item key for better performance
  const itemKey = useCallback((index: number, data: T[]) => {
    if (_getItemKey) {
      return getItemKey(index, data);
    }
    // Use a stable key based on item properties if possible
    const item = data[index] as unknown;
    return item?.id || item?.key || `item-${index}`;
  }, [getItemKey]);

  // Empty state
  if (!loading && items.length === 0 && emptyComponent) {
    return <>{emptyComponent}</>;
  }

  const ListComponent = isVariableHeight ? VList : List;

  return (
    <div className={`virtualized-list-container ${className}`}>
      {headerComponent}

      {items.length > 0 && (
        <ListComponent
          ref={listRef}
          height={height}
          width={width}
          itemCount={items.length}
          itemSize={isVariableHeight ? getItemSize : (typeof itemHeight === 'number' ? () => itemHeight : itemHeight)}
          overscanCount={overscan}
          onScroll={handleScroll}
          itemData={items}
          itemKey={itemKey}
          estimatedItemSize={estimatedItemSize}
          className="scrollbar-thin scrollbar-thumb-gray-400 scrollbar-track-gray-100"
        >
          {ItemRenderer}
        </ListComponent>
      )}

      {loading && (
        <div className="py-4">
          <LoadingFallbacks.List items={3} />
        </div>
      )}

      {footerComponent}
    </div>
  );
}

/**
 * Optimized post list item component
 */
export const VirtualizedPostItem = memo(({ 
  post, 
  style,
  onLike,
  onComment,
  onShare,
  onEdit,
  onDelete,
  onReport,
  isOwner,
}: unknown) => {
  // Defer non-critical updates
  const [showActions, setShowActions] = React.useState(false);

  React.useEffect(() => {
    // Lazy load action buttons
    const _timer = setTimeout(() => setShowActions(true), 100);
    return () => clearTimeout(_timer);
  }, []);

  return (
    <div style={style} className="virtualized-post-item">
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4 hover:shadow-md transition-shadow">
        {/* Post header */}
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center space-x-2">
