                    <label htmlFor="has-insurance" className="font-medium">I have insurance</label>
                  </div>

                  {formData.hasInsurance && (
                    <div className="ml-7 space-y-4 p-4 bg-gray-50 rounded-lg">
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2" htmlFor="has-insurance">Insurance Provider *</label>
                        <select
                          value={formData.insuranceProvider}
                          onChange={(e) => updateFormData('insuranceProvider', e.target.value)}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                          required
                        >
                          <option value="">Select provider...</option>
                          {therapist?.insuranceAccepted?.map((provider: string) => (
                            <option key={provider} value={provider}>{provider}</option>
                          ))}
                        </select>
                      </div>

                      <div className="grid grid-cols-2 gap-4">
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-2" htmlFor="has-insurance">Member ID *</label>
                          <input
                            type="text"
                            value={formData.memberId}
                            onChange={(e) => updateFormData('memberId', e.target.value)}
                            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                            required
                          />
                        </div>
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-2" htmlFor="has-insurance">Group Number</label>
                          <input
                            type="text"
                            value={formData.groupNumber}
                            onChange={(e) => updateFormData('groupNumber', e.target.value)}
                            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                          />
                        </div>
                      </div>

                      <div className="bg-green-50 p-3 rounded-md">
                        <p className="text-sm text-green-700">
                          <CheckCircle className="w-4 h-4 inline mr-1" />
                          Your insurance is accepted. Typical copay: $25-50
                        </p>
                      </div>
                    </div>
                  )}

                  <div className="flex items-center gap-3">
                    <input
                      type="radio"
                      id="self-pay"
                      name="insurance-option"
                      checked={!formData.hasInsurance}
                      onChange={(e) => updateFormData('hasInsurance', !e.target.checked)}
                      className="w-4 h-4 text-blue-600 focus:ring-blue-500"
                    />
                    <label htmlFor="self-pay" className="font-medium">I'll pay out-of-pocket</label>
                  </div>

                  {!formData.hasInsurance && (
                    <div className="ml-7 space-y-4 p-4 bg-gray-50 rounded-lg">
                      <div className="space-y-2">
                        {[
                          { value: 'full-rate', label: `Full Rate - $${therapist?.sessionRate || 180}` },
                          { value: 'sliding-scale', label: 'Sliding Scale - Based on income' }
                        ].map(option => (
                          <label key={option.value} className="flex items-center gap-3" htmlFor="has-insurance">
                            <input
                              type="radio"
                              name="payment-method"
                              value={option.value}
                              checked={formData.paymentMethod === option.value}
                              onChange={(e) => updateFormData('paymentMethod', e.target.value)}
                              className="w-4 h-4 text-blue-600 focus:ring-blue-500"
                            />
                            <span>{option.label}</span>
                          </label>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </motion.div>
            )}

            {/* Step 5: Confirmation */}
            {currentStep === 5 && (
              <motion.div
                key="step5"
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                className="space-y-6"
              >
                <h3 className="text-xl font-semibold">Confirm Your Appointment</h3>
                
                <div className="bg-gradient-to-r from-blue-50 to-green-50 p-6 rounded-lg">
                  <div className="space-y-4">
                    <div className="flex items-center gap-3">
                      <Calendar className="w-5 h-5 text-blue-600" />
                      <span className="font-medium">
                        {selectedDate?.toLocaleDateString('en-US', { 
                          weekday: 'long', 
                          year: 'numeric', 
                          month: 'long', 
                          day: 'numeric' 
                        })} at {selectedTime}
                      </span>
                    </div>
                    <div className="flex items-center gap-3">
                      <User className="w-5 h-5 text-blue-600" />
                      <span>{therapist?.name}</span>
                    </div>
                    <div className="flex items-center gap-3">
                      {formData.format === 'video' ? (
                        <Video className="w-5 h-5 text-blue-600" />
                      ) : (
                        <MapPin className="w-5 h-5 text-blue-600" />
                      )}
                      <span>{formData.format === 'video' ? 'Video Session' : 'In-Person Session'}</span>
                    </div>
                    <div className="flex items-center gap-3">
                      <CreditCard className="w-5 h-5 text-blue-600" />
                      <span>
                        {formData.hasInsurance 
                          ? `Insurance: ${formData.insuranceProvider}` 
                          : `Payment: ${formData.paymentMethod === 'sliding-scale' ? 'Sliding Scale' : `$${therapist?.sessionRate}`}`
                        }
                      </span>
                    </div>
                  </div>
                </div>

                <div className="bg-yellow-50 border border-yellow-200 p-4 rounded-lg">
                  <div className="flex items-start gap-3">
                    <AlertTriangle className="w-5 h-5 text-yellow-600 flex-shrink-0 mt-0.5" />
                    <div className="space-y-2">
                      <p className="font-medium text-yellow-800">Please Note:</p>
                      <ul className="text-sm text-yellow-700 space-y-1 ml-4 list-disc">
                        <li>You'll receive a confirmation email within 5 minutes</li>
                        <li>Please arrive 10 minutes early (or join video call early)</li>
                        <li>Cancellations must be made 24 hours in advance</li>
                        <li>A secure video link will be sent before your session</li>
                      </ul>
                    </div>
                  </div>
                </div>

                <div className="flex items-center gap-2 p-4 bg-green-50 rounded-lg">
                  <Shield className="w-5 h-5 text-green-600" />
                  <span className="text-sm text-green-700">
                    Your information is encrypted and HIPAA-compliant
                  </span>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* Footer */}
        <div className="border-t bg-gray-50 px-6 py-4 flex items-center justify-between">
          <button
            onClick={prevStep}
            disabled={currentStep === 1}
            className="flex items-center gap-2 px-4 py-2 text-gray-600 hover:text-gray-800 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <ArrowLeft className="w-4 h-4" />
            Previous
          </button>

          <div className="flex items-center gap-3">
            {currentStep < BOOKING_STEPS.length ? (
              <button
                onClick={nextStep}
                disabled={!canProceedToNext()}
                className="flex items-center gap-2 bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                Next
                <ArrowRight className="w-4 h-4" />
              </button>
            ) : (
              <button
                onClick={handleSubmit}
                disabled={loading || !canProceedToNext()}
                className="flex items-center gap-2 bg-green-600 text-white px-6 py-2 rounded-lg hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                {loading ? (
                  <>
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
                    Booking...
                  </>
                ) : (
                  <>
                    <CheckCircle className="w-4 h-4" />
                    Confirm Booking
                  </>
                )}
              </button>
            )}
          </div>
        </div>
      </motion.div>
    </div>
  );
}

import { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MessageSquare, Send, Users, _Clock, Phone, Video,
  _FileText, Paperclip, Image, _AlertTriangle, _CheckCircle,
  _Plus, Search, _Filter, Bell, _BellOff, Settings,
  UserPlus, _UserMinus, MoreHorizontal, _Star, _Flag,
  _Calendar, _MapPin, _Stethoscope, _Briefcase, Heart,
  _Shield, _Zap, _Upload, _Download, _Eye, _EyeOff,
  _Edit2, _Trash2, _Copy, _Share, _Archive, _RefreshCw
} from 'lucide-react';

interface CareTeamMember {
  id: string;
  name: string;
  _role: 'primary_therapist' | 'psychiatrist' | 'case_manager' | 'nurse' | 'social_worker' | 'peer_support' | 'family_member' | 'medical_provider' | 'other';
  credentials?: string;
  specialty?: string;
  organization?: string;
  email: string;
  phone?: string;
  avatar?: string;
  status: 'active' | 'inactive' | 'on_leave';
  permissions: Permission[];
  lastActive?: Date;
  timezone?: string;
  preferredContact: 'email' | 'phone' | 'secure_message';
}

interface Permission {
  area: 'treatment_plan' | 'session_notes' | 'medications' | 'crisis_plan' | 'assessments' | 'scheduling' | 'billing';
  level: 'view' | 'edit' | 'full';
}

interface Message {
  id: string;
  threadId: string;
  senderId: string;
  recipientIds: string[];
  content: string;
  timestamp: Date;
  type: 'text' | 'file' | 'image' | 'voice' | 'video_call' | 'phone_call' | 'system';
  attachments?: Attachment[];
  _priority: 'low' | 'normal' | 'high' | 'urgent';
  status: 'sent' | 'delivered' | 'read';
  readBy: { userId: string; timestamp: Date }[];
  tags?: string[];
  relatedTo?: {
    type: 'patient' | 'session' | 'treatment_plan' | 'assessment';
    id: string;
    name: string;
  };
  isEncrypted: boolean;
  editedAt?: Date;
  deletedAt?: Date;
}

interface Attachment {
  id: string;
  name: string;
  type: 'document' | 'image' | 'audio' | 'video';
  size: number;
  url: string;
  uploadedBy: string;
  uploadedAt: Date;
  isSecure: boolean;
}

interface Thread {
  id: string;
  name: string;
  type: 'direct' | 'group' | 'team' | 'emergency';
  participantIds: string[];
  createdBy: string;
  createdAt: Date;
  lastActivity: Date;
  lastMessage?: Message;
  isArchived: boolean;
  isMuted: boolean;
  tags?: string[];
  relatedPatientId?: string;
  _priority: 'low' | 'normal' | 'high' | 'urgent';
  unreadCount: number;
}

interface Notification {
  id: string;
  type: 'message' | 'mention' | 'file_shared' | 'emergency' | 'system';
  title: string;
  content: string;
  timestamp: Date;
  isRead: boolean;
  actionUrl?: string;
  senderId?: string;
  _priority: 'low' | 'normal' | 'high' | 'urgent';
}

interface CareTeamCommunicationProps {
  currentUserId: string;
  patientId?: string;
  careTeam: CareTeamMember[];
  threads?: Thread[];
  onSendMessage?: (threadId: string, message: Partial<Message>) => void;
  _onCreateThread?: (thread: Partial<Thread>) => void;
  _onAddMember?: (member: CareTeamMember) => void;
  _onRemoveMember?: (memberId: string) => void;
  _onUpdatePermissions?: (memberId: string, permissions: Permission[]) => void;
  isProvider?: boolean;
}

export function CareTeamCommunication({
  currentUserId,
  patientId,
  careTeam,
  threads = [],
  onSendMessage,
  _onCreateThread,
  _onAddMember,
  _onRemoveMember,
  _onUpdatePermissions,
  isProvider = false
}: CareTeamCommunicationProps) {
  const [activeTab, setActiveTab] = useState<'messages' | 'team' | 'notifications'>('messages');
  const [selectedThread, setSelectedThread] = useState<Thread | null>(null);
  const [newMessage, setNewMessage] = useState('');
  const [showNewThread, setShowNewThread] = useState(false);
  const [showMemberForm, setShowMemberForm] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterPriority, setFilterPriority] = useState<'all' | 'low' | 'normal' | 'high' | 'urgent'>('all');
  const [notifications, _setNotifications] = useState<Notification[]>([]);
  const [_isTyping, _setIsTyping] = useState<Record<string, boolean>>({});
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Auto-scroll to bottom of messages
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [selectedThread?.id]);

  // Get current user
  const currentUser = careTeam.find(member => member.id === currentUserId);

  // Get role color
  const getRoleColor = (_role: string) => {
    switch (_role) {
      case 'primary_therapist': return 'text-blue-700 bg-blue-100';
      case 'psychiatrist': return 'text-purple-700 bg-purple-100';
      case 'case_manager': return 'text-green-700 bg-green-100';
      case 'nurse': return 'text-pink-700 bg-pink-100';
      case 'social_worker': return 'text-indigo-700 bg-indigo-100';
      case 'peer_support': return 'text-yellow-700 bg-yellow-100';
      case 'family_member': return 'text-orange-700 bg-orange-100';
      case 'medical_provider': return 'text-red-700 bg-red-100';
      default: return 'text-gray-700 bg-gray-100';
    }
  };

  // Get priority color
  const getPriorityColor = (_priority: string) => {
    switch (_priority) {
      case 'urgent': return 'text-red-600 bg-red-100 border-red-200';
      case 'high': return 'text-orange-600 bg-orange-100 border-orange-200';
      case 'normal': return 'text-blue-600 bg-blue-100 border-blue-200';
      case 'low': return 'text-gray-600 bg-gray-100 border-gray-200';
      default: return 'text-gray-600 bg-gray-100 border-gray-200';
    }
  };

  // Handle send message
  const handleSendMessage = () => {
    if (!newMessage.trim() || !selectedThread) return;

    const message: Partial<Message> = {
      content: newMessage.trim(),
      type: 'text',
      _priority: 'normal',
      recipientIds: selectedThread.participantIds.filter(id => id !== currentUserId),
      relatedTo: patientId ? {
        type: 'patient',
        id: patientId,
        name: 'Current Patient'
      } : undefined
    };

    onSendMessage?.(selectedThread.id, message);
    setNewMessage('');
  };

  // Filter threads
  const filteredThreads = threads.filter(thread => {
    const matchesSearch = thread.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      thread.lastMessage?.content.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesPriority = filterPriority === 'all' || thread._priority === filterPriority;
    return matchesSearch && matchesPriority;
  });

  // Get thread participants
  const getThreadParticipants = (thread: Thread) => {
    return careTeam.filter(member => thread.participantIds.includes(member.id));
  };

  // Format timestamp
  const formatTimestamp = (timestamp: Date) => {
    const now = new Date();
    const diff = now.getTime() - timestamp.getTime();
    const minutes = Math.floor(diff / 60000);
    const hours = Math.floor(diff / 3600000);
    const days = Math.floor(diff / 86400000);

    if (minutes < 1) return 'Just now';
    if (minutes < 60) return `${minutes}m ago`;
    if (hours < 24) return `${hours}h ago`;
    if (days < 7) return `${days}d ago`;
    return timestamp.toLocaleDateString();
  };

  return (
    <div className="h-full flex bg-white rounded-xl shadow-lg overflow-hidden">
      {/* Sidebar */}
      <div className="w-80 bg-gray-50 border-r border-gray-200 flex flex-col">
        {/* Header */}
        <div className="p-4 border-b border-gray-200">
          <div className="flex items-center justify-between mb-3">
            <h2 className="text-lg font-semibold text-gray-900">Care Team</h2>
            <div className="flex space-x-1">
              <button
                onClick={() => setShowNewThread(true)}
                className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
                title="New message"
              >
                <MessageSquare className="h-4 w-4" />
              </button>
              <button
                onClick={() => setShowMemberForm(true)}
                className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
                title="Add team member"
              >
                <UserPlus className="h-4 w-4" />
              </button>
            </div>
          </div>

          {/* Tabs */}
          <div className="flex space-x-1">
            {[
              { id: 'messages', label: 'Messages', icon: MessageSquare },
              { id: 'team', label: 'Team', icon: Users },
              { id: 'notifications', label: 'Alerts', icon: Bell }
            ].map(({ id, label, icon: Icon }) => (
              <button
                key={id}
                onClick={() => setActiveTab(id as unknown)}
                className={`flex-1 px-3 py-2 text-sm font-medium rounded-lg transition-all flex items-center justify-center space-x-1 ${
                  activeTab === id
                    ? 'bg-primary-100 text-primary-700'
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
                }`}
              >
                <Icon className="h-4 w-4" />
                <span>{label}</span>
                {id === 'notifications' && notifications.filter(n => !n.isRead).length > 0 && (
                  <span className="ml-1 px-1.5 py-0.5 text-xs bg-red-500 text-white rounded-full">
                    {notifications.filter(n => !n.isRead).length}
                  </span>
                )}
              </button>
            ))}
          </div>
        </div>

        {/* Content */}
        <div className="flex-1 overflow-y-auto">
          {activeTab === 'messages' && (
            <div className="p-4 space-y-2">
              {/* Search and _Filter */}
              <div className="space-y-2 mb-4">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                  <input
                    type="text"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    placeholder="Search messages..."
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                  />
                </div>
                <select
                  value={filterPriority}
                  onChange={(e) => setFilterPriority(e.target.value as unknown)}
                  className="w-full px-3 py-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500"
                >
                  <option value="all">All Priorities</option>
                  <option value="urgent">Urgent</option>
                  <option value="high">High</option>
                  <option value="normal">Normal</option>
                  <option value="low">Low</option>
                </select>
              </div>

              {/* Threads List */}
              <div className="space-y-1">
                {filteredThreads.map((thread) => {
                  const participants = getThreadParticipants(thread);
                  return (
                    <motion.div
                      key={thread.id}
                      initial={{ opacity: 0, x: -10 }}
                      animate={{ opacity: 1, x: 0 }}
                      className={`p-3 rounded-lg cursor-pointer transition-all hover:bg-gray-100 ${
                        selectedThread?.id === thread.id ? 'bg-primary-100 border border-primary-200' : ''
                      }`}
                      onClick={() => setSelectedThread(thread)}
                    >
                      <div className="flex items-start justify-between mb-1">
                        <div className="flex items-center space-x-2">
                          <h4 className="font-medium text-gray-900 text-sm">{thread.name}</h4>
                          {thread._priority !== 'normal' && (
                            <span className={`px-1.5 py-0.5 text-xs rounded-full ${getPriorityColor(thread._priority)}`}>
                              {thread._priority}
                            </span>
                          )}
                        </div>
                        <span className="text-xs text-gray-500">
                          {formatTimestamp(thread.lastActivity)}
                        </span>
                      </div>
                      
                      {thread.lastMessage && (
                        <p className="text-sm text-gray-600 truncate">
                          {thread.lastMessage.content}
                        </p>
                      )}
                      
                      <div className="flex items-center justify-between mt-2">
                        <div className="flex -space-x-2">
                          {participants.slice(0, 3).map((participant) => (
                            <div
                              key={participant.id}
                              className="h-6 w-6 bg-primary-200 rounded-full flex items-center justify-center border-2 border-white"
                              title={participant.name}
                            >
                              {participant.avatar ? (
                                <img 
                                  src={participant.avatar} 
                                  alt={participant.name}
                                  className="h-6 w-6 rounded-full object-cover"
                                />
                              ) : (
                                <span className="text-xs text-primary-700 font-medium">
                                  {participant.name.split(' ').map(n => n[0]).join('')}
                                </span>
                              )}
                            </div>
                          ))}
                          {participants.length > 3 && (
                            <div className="h-6 w-6 bg-gray-200 rounded-full flex items-center justify-center border-2 border-white">
                              <span className="text-xs text-gray-600 font-medium">
                                +{participants.length - 3}
                              </span>
                            </div>
                          )}
                        </div>
                        
                        {thread.unreadCount > 0 && (
                          <span className="px-2 py-1 bg-primary-500 text-white text-xs rounded-full">
                            {thread.unreadCount}
                          </span>
                        )}
                      </div>
                    </motion.div>
                  );
                })}
              </div>
            </div>
          )}

          {activeTab === 'team' && (
            <div className="p-4 space-y-3">
              {careTeam
                .filter(member => 
                  member.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                  member._role.toLowerCase().includes(searchTerm.toLowerCase())
                )
                .map((member) => (
                  <motion.div
                    key={member.id}
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="p-3 bg-white border border-gray-200 rounded-lg"
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex items-center space-x-3">
                        <div className="h-10 w-10 bg-primary-200 rounded-full flex items-center justify-center">
                          {member.avatar ? (
                            <img 
                              src={member.avatar} 
                              alt={member.name}
                              className="h-10 w-10 rounded-full object-cover"
                            />
                          ) : (
                            <span className="text-primary-700 font-semibold">
                              {member.name.split(' ').map(n => n[0]).join('')}
                            </span>
                          )}
                        </div>
                        <div>
                          <h4 className="font-medium text-gray-900">{member.name}</h4>
                          <div className="flex items-center space-x-2">
                            <span className={`px-2 py-1 text-xs rounded-full ${getRoleColor(member._role)}`}>
                              {member._role.replace('_', ' ')}
                            </span>
                            {member.credentials && (
                              <span className="text-xs text-gray-600">{member.credentials}</span>
                            )}
                          </div>
                          {member.organization && (
                            <p className="text-xs text-gray-500 mt-1">{member.organization}</p>
                          )}
                        </div>
                      </div>
                      
                      <div className="flex items-center space-x-1">
                        <div className={`h-2 w-2 rounded-full ${
                          member.status === 'active' ? 'bg-green-400' :
                          member.status === 'on_leave' ? 'bg-yellow-400' :
                          'bg-gray-400'
                        }`} />
                        {isProvider && (
                          <button
                            className="p-1 text-gray-400 hover:text-gray-600"
                            title="Settings"
                          >
                            <Settings className="h-4 w-4" />
                          </button>
                        )}
                      </div>
                    </div>
                    
                    <div className="flex items-center space-x-3 mt-2 text-sm">
                      <div className="flex items-center text-gray-600">
                        <Phone className="h-3 w-3 mr-1" />
                        <span>{member.phone || 'N/A'}</span>
                      </div>
                      {member.lastActive && (
                        <span className="text-gray-500">
                          Active {formatTimestamp(member.lastActive)}
                        </span>
                      )}
                    </div>
                  </motion.div>
                ))}
            </div>
          )}

          {activeTab === 'notifications' && (
            <div className="p-4 space-y-2">
              {notifications.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <Bell className="h-12 w-12 mx-auto mb-3 text-gray-300" />
                  <p>No notifications</p>
                </div>
              ) : (
                notifications.map((notification) => (
                  <motion.div
                    key={notification.id}
                    initial={{ opacity: 0, x: -10 }}
                    animate={{ opacity: 1, x: 0 }}
                    className={`p-3 rounded-lg border ${
                      notification.isRead ? 'bg-gray-50 border-gray-200' : 'bg-primary-50 border-primary-200'
                    }`}
                  >
                    <div className="flex items-start justify-between mb-1">
                      <h4 className="font-medium text-gray-900 text-sm">{notification.title}</h4>
                      <div className="flex items-center space-x-2">
                        <span className={`px-2 py-1 text-xs rounded-full ${getPriorityColor(notification._priority)}`}>
                          {notification._priority}
                        </span>
                        <span className="text-xs text-gray-500">
                          {formatTimestamp(notification.timestamp)}
                        </span>
                      </div>
                    </div>
                    <p className="text-sm text-gray-600">{notification.content}</p>
                    {!notification.isRead && (
                      <div className="h-2 w-2 bg-primary-500 rounded-full absolute top-3 right-3" />
                    )}
                  </motion.div>
                ))
              )}
            </div>
          )}
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {selectedThread ? (
          <>
            {/* Thread Header */}
            <div className="p-4 border-b border-gray-200 bg-white">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className="flex -space-x-2">
                    {getThreadParticipants(selectedThread).slice(0, 3).map((participant) => (
                      <div
                        key={participant.id}
                        className="h-8 w-8 bg-primary-200 rounded-full flex items-center justify-center border-2 border-white"
                      >
                        {participant.avatar ? (
                          <img 
                            src={participant.avatar} 
                            alt={participant.name}
                            className="h-8 w-8 rounded-full object-cover"
                          />
                        ) : (
                          <span className="text-xs text-primary-700 font-medium">
                            {participant.name.split(' ').map(n => n[0]).join('')}
                          </span>
                        )}
                      </div>
                    ))}
                  </div>
                  <div>
                    <h3 className="font-medium text-gray-900">{selectedThread.name}</h3>
                    <p className="text-sm text-gray-600">
                      {getThreadParticipants(selectedThread).map(p => p.name).join(', ')}
                    </p>
                  </div>
                </div>
                
                <div className="flex items-center space-x-2">
                  <button className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors">
                    <Phone className="h-4 w-4" />
                  </button>
                  <button className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors">
                    <Video className="h-4 w-4" />
                  </button>
                  <button className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors">
                    <MoreHorizontal className="h-4 w-4" />
                  </button>
                </div>
              </div>
              
              {selectedThread.relatedPatientId && (
                <div className="mt-2 flex items-center space-x-2 text-sm text-gray-600">
                  <Heart className="h-4 w-4" />
                  <span>Related to patient care</span>
                </div>
              )}
            </div>

            {/* Messages */}
            <div className="flex-1 overflow-y-auto p-4 space-y-4">
              {/* Mock messages for demonstration */}
              <div className="space-y-4">
                <div className="flex items-start space-x-3">
                  <div className="h-8 w-8 bg-blue-200 rounded-full flex items-center justify-center">
                    <span className="text-xs text-blue-700 font-medium">DR</span>
                  </div>
                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-1">
                      <span className="font-medium text-gray-900 text-sm">Dr. Robinson</span>
                      <span className="text-xs text-gray-500">2:30 PM</span>
                    </div>
                    <div className="bg-gray-100 rounded-lg p-3 text-sm text-gray-800">
                      Patient showed significant improvement in anxiety levels during yesterday's session. 
                      Considering adjusting medication dosage. Thoughts?
                    </div>
                  </div>
                </div>

                <div className="flex items-start space-x-3 justify-end">
                  <div className="flex-1 text-right">
                    <div className="flex items-center justify-end space-x-2 mb-1">
                      <span className="text-xs text-gray-500">2:35 PM</span>
                      <span className="font-medium text-gray-900 text-sm">You</span>
                    </div>
                    <div className="bg-primary-500 text-white rounded-lg p-3 text-sm inline-block">
                      Agreed. The CBT techniques seem to be working well. Let&apos;s schedule a review 
                      meeting to discuss medication adjustments.
                    </div>
                  </div>
                  <div className="h-8 w-8 bg-primary-200 rounded-full flex items-center justify-center">
                    <span className="text-xs text-primary-700 font-medium">
                      {currentUser?.name.split(' ').map(n => n[0]).join('') || 'ME'}
                    </span>
                  </div>
                </div>
              </div>
              
              <div ref={messagesEndRef} />
            </div>

            {/* Message Input */}
            <div className="p-4 border-t border-gray-200 bg-white">
              <div className="flex items-end space-x-3">
                <div className="flex-1">
                  <div className="border border-gray-300 rounded-lg">
                    <textarea
                      value={newMessage}
                      onChange={(e) => setNewMessage(e.target.value)}
                      placeholder="Type your message..."
                      className="w-full p-3 border-none rounded-lg resize-none focus:ring-0"
                      rows={3}
                      onKeyDown={(e) => {
                        if (e.key === 'Enter' && !e.shiftKey) {
                          e.preventDefault();
                          handleSendMessage();
                        }
                      }}
                    />
                    <div className="flex items-center justify-between p-2 border-t border-gray-200">
                      <div className="flex items-center space-x-2">
                        <button className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors">
                          <Paperclip className="h-4 w-4" />
                        </button>
                        <button className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors">
                          <Image className="h-4 w-4" />
                        </button>
                        <select className="text-sm border-none text-gray-600 focus:ring-0">
                          <option value="normal">Normal</option>
                          <option value="high">High Priority</option>
                          <option value="urgent">Urgent</option>
                        </select>
                      </div>
                      <button
                        onClick={handleSendMessage}
                        disabled={!newMessage.trim()}
                        className="px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center space-x-1"
                      >
                        <Send className="h-4 w-4" />
                        <span>Send</span>
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center">
            <div className="text-center">
              <MessageSquare className="h-12 w-12 mx-auto text-gray-300 mb-3" />
              <h3 className="text-lg font-medium text-gray-900 mb-1">No conversation selected</h3>
              <p className="text-gray-500">Choose a thread from the sidebar to start messaging</p>
            </div>
          </div>
        )}
      </div>

      {/* New Thread Modal */}
      <AnimatePresence>
        {showNewThread && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
            onClick={() => setShowNewThread(false)}
          >
            <motion.div
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.9, opacity: 0 }}
              className="bg-white rounded-xl p-6 max-w-lg w-full"
              onClick={(e) => e.stopPropagation()}
            >
              <h3 className="text-xl font-semibold mb-4">New Conversation</h3>
              
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Thread Name
                  </label>
                  <input
                    type="text"
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500"
                    placeholder="Enter thread name..."
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Select Participants
                  </label>
                  <div className="space-y-2 max-h-48 overflow-y-auto">
                    {careTeam.filter(m => m.id !== currentUserId).map((member) => (
                      <label key={member.id} className="flex items-center p-2 hover:bg-gray-50 rounded">
                        <input
                          type="checkbox"
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <div className="ml-3 flex items-center space-x-2">
                          <span className="font-medium text-gray-900">{member.name}</span>
                          <span className={`px-2 py-1 text-xs rounded-full ${getRoleColor(member._role)}`}>
                            {member._role.replace('_', ' ')}
                          </span>
                        </div>
                      </label>
                    ))}
                  </div>
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Priority
                  </label>
                  <select className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500">
                    <option value="normal">Normal</option>
                    <option value="high">High</option>
                    <option value="urgent">Urgent</option>
                  </select>
                </div>
              </div>
              
              <div className="flex space-x-2 mt-6">
                <button
                  onClick={() => setShowNewThread(false)}
                  className="flex-1 px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
                >
                  Cancel
                </button>
                <button
                  onClick={() => setShowNewThread(false)}
                  className="flex-1 px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 transition-colors"
                >
                  Create Thread
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Add Member Modal */}
      <AnimatePresence>
        {showMemberForm && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
            onClick={() => setShowMemberForm(false)}
          >
            <motion.div
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.9, opacity: 0 }}
              className="bg-white rounded-xl p-6 max-w-lg w-full max-h-[80vh] overflow-y-auto"
              onClick={(e) => e.stopPropagation()}
            >
              <h3 className="text-xl font-semibold mb-4">Add Team Member</h3>
              
              <div className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      First Name
                    </label>
                    <input
                      type="text"
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Last Name
                    </label>
                    <input
                      type="text"
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500"
                    />
                  </div>
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Role
                  </label>
                  <select className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500">
                    <option value="primary_therapist">Primary Therapist</option>
                    <option value="psychiatrist">Psychiatrist</option>
                    <option value="case_manager">Case Manager</option>
                    <option value="nurse">Nurse</option>
                    <option value="social_worker">Social Worker</option>
                    <option value="peer_support">Peer Support</option>
                    <option value="family_member">Family Member</option>
                    <option value="medical_provider">Medical Provider</option>
                    <option value="other">Other</option>
                  </select>
                </div>
                
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Email
                    </label>
                    <input
                      type="email"
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Phone
                    </label>
                    <input
                      type="tel"
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500"
                    />
                  </div>
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Organization
                  </label>
                  <input
                    type="text"
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500"
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Permissions
                  </label>
                  <div className="space-y-2">
                    {[
                      'Treatment Plan',
                      'Session Notes', 
                      'Medications',
                      'Crisis Plan',
                      'Assessments',
                      'Scheduling'
                    ].map((permission) => (
                      <div key={permission} className="flex items-center justify-between p-2 border border-gray-200 rounded">
                        <span className="text-sm">{permission}</span>
                        <select className="text-sm border-none focus:ring-0">
                          <option value="none">No Access</option>
                          <option value="view">View Only</option>
                          <option value="edit">Edit</option>
                          <option value="full">Full Access</option>
                        </select>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
              
              <div className="flex space-x-2 mt-6">
                <button
                  onClick={() => setShowMemberForm(false)}
                  className="flex-1 px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
                >
                  Cancel
                </button>
                <button
                  onClick={() => setShowMemberForm(false)}
                  className="flex-1 px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 transition-colors"
                >
                  Add Member
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Bell,
  Clock,
  _Calendar,
  Smartphone,
  Watch,
  Volume2,
  _VolumeX,
  Settings,
  BrainCircuit,
  TrendingUp,
  AlertTriangle,
  CheckCircle,
  XCircle,
  Pill,
  _Coffee,
  _Sun,
  _Moon,
  Activity,
  Target,
  Zap,
  ChevronRight,
  Play,
  _Pause,
  _RotateCcw,
  MapPin,
  User,
  _HeartPulse,
  FileText,
  Lightbulb
} from 'lucide-react';

interface SmartReminderProps {
  _userId: string;
  medications: unknown[];
  _onUpdateReminders: (reminders: SmartReminder[]) => void;
}

interface SmartReminder {
  id: string;
  medicationId: string;
  _type: 'time' | 'location' | 'context' | 'adaptive' | 'emergency';
  enabled: boolean;
  settings: ReminderSettings;
  intelligence: ReminderIntelligence;
  effectiveness: number; // 0-1
  lastTriggered?: Date;
  successRate: number; // 0-1
  userFeedback: 'helpful' | 'annoying' | 'neutral';
}

interface ReminderSettings {
  // Time-based
  times?: string[];
  advanceNotice?: number; // _minutes
  snoozeOptions?: number[]; // _minutes
  
  // Location-based
  locations?: LocationTrigger[];
  geofenceRadius?: number; // meters
  
  // Context-aware
  contextTriggers?: ContextTrigger[];
  
  // Adaptive
  adaptToRoutine?: boolean;
  learnFromBehavior?: boolean;
  personalizeContent?: boolean;
  
  // Delivery methods
  pushNotification?: boolean;
  sms?: boolean;
  email?: boolean;
  smartWatch?: boolean;
  
  // Sound and vibration
  soundEnabled?: boolean;
  customSound?: string;
  vibrationPattern?: 'gentle' | 'strong' | 'custom';
  
  // Escalation
  escalationEnabled?: boolean;
  escalationDelay?: number; // _minutes
  escalationContacts?: string[];
}

interface LocationTrigger {
  id: string;
  name: string;
  latitude: number;
  longitude: number;
  trigger: 'enter' | 'exit';
  description: string;
}

interface ContextTrigger {
  id: string;
  _type: 'meal' | 'exercise' | 'bedtime' | 'work' | 'stress' | 'mood';
  condition: string;
  description: string;
}

interface ReminderIntelligence {
  // Pattern recognition
  optimalTimes?: string[];
  adherencePatterns?: AdherencePattern[];
  
  // Personalization
  preferredChannels?: string[];
  responseLatency?: number; // average time to respond
  
  // Smart features
  moodCorrelation?: number; // -1 to 1
  sideEffectPrediction?: number; // 0-1
  dosageOptimization?: DosageRecommendation;
  
  // Learning
  adaptationHistory?: AdaptationEvent[];
  confidenceScore?: number; // 0-1
}

interface AdherencePattern {
  pattern: string;
  confidence: number;
  impact: number; // effect on adherence
  recommendation: string;
}

interface DosageRecommendation {
  currentEffectiveness: number;
  suggestedChanges: string[];
  reasoning: string;
  confidence: number;
}

interface AdaptationEvent {
  date: Date;
  trigger: string;
  action: string;
  result: string;
  effectiveness: number;
}

interface AIInsight {
  _type: 'pattern' | 'optimization' | 'warning' | 'success';
  title: string;
  description: string;
  confidence: number;
  actionable: boolean;
  actions?: string[];
}

export function MedicationSmartReminders({ 
  _userId, 
  medications, 
  _onUpdateReminders 
}: SmartReminderProps) {
  const [reminders, setReminders] = useState<SmartReminder[]>([]);
  const [activeTab, setActiveTab] = useState<'overview' | 'configure' | '_insights' | 'history'>('overview');
  const [_selectedMedication, setSelectedMedication] = useState<string | null>(null);
  const [isLearningMode, setIsLearningMode] = useState(true);
  const [aiInsights, setAiInsights] = useState<AIInsight[]>([]);
  const [reminderTest, setReminderTest] = useState<{ active: boolean; _type: string } | null>(null);

  useEffect(() => {
    generateInitialReminders();
    generateAIInsights();
  }, [medications]);

  const generateInitialReminders = () => {
    const _initialReminders: SmartReminder[] = medications.map((med, index) => ({
      id: `reminder-${med.id}`,
      medicationId: med.id,
      _type: 'adaptive',
      enabled: true,
      settings: {
        times: med.schedule?.map((s: unknown) => s.time) || ['08:00'],
        advanceNotice: 15,
        snoozeOptions: [5, 10, 30],
        adaptToRoutine: true,
        learnFromBehavior: true,
        personalizeContent: true,
        pushNotification: true,
        soundEnabled: true,
        vibrationPattern: 'gentle',
        escalationEnabled: true,
        escalationDelay: 60
      },
      intelligence: {
        optimalTimes: med.schedule?.map((s: unknown) => s.time) || ['08:00'],
        adherencePatterns: [
          {
            pattern: `Better adherence when taken with ${index % 2 === 0 ? 'breakfast' : 'dinner'}`,
            confidence: 0.75 + Math.random() * 0.2,
            impact: 0.3 + Math.random() * 0.4,
            recommendation: `Consider scheduling with ${index % 2 === 0 ? 'morning' : 'evening'} meal`
          }
        ],
        preferredChannels: ['push', 'vibration'],
        responseLatency: 5 + Math.random() * 15,
        moodCorrelation: (Math.random() - 0.5) * 0.8,
        confidenceScore: 0.6 + Math.random() * 0.3
      },
      effectiveness: 0.7 + Math.random() * 0.25,
      successRate: 0.65 + Math.random() * 0.3,
      userFeedback: Math.random() > 0.7 ? 'helpful' : Math.random() > 0.5 ? 'neutral' : 'annoying'
    }));

    setReminders(_initialReminders);
  };

  const generateAIInsights = () => {
    const _insights: AIInsight[] = [
      {
        _type: 'pattern',
        title: 'Optimal Timing Discovered',
        description: 'Your medication adherence is 23% higher when taken within 30 _minutes of breakfast.',
        confidence: 0.87,
        actionable: true,
        actions: ['Adjust reminder to 8:15 AM', 'Link reminder to meal tracking']
      },
      {
        _type: 'optimization',
        title: 'Smart Scheduling Opportunity',
        description: 'Consolidating your 8 AM and 8:30 AM medications into a single reminder could improve adherence.',
        confidence: 0.74,
        actionable: true,
        actions: ['Create combined reminder', 'Test for 2 weeks']
      },
      {
        _type: 'warning',
        title: 'Missed Dose Pattern',
        description: 'You\'ve missed your evening medication 4 times this week, typically on busy weekdays.',
        confidence: 0.92,
        actionable: true,
        actions: ['Enable location-based reminders', 'Add backup reminder 2 _hours later']
      },
      {
        _type: 'success',
        title: 'Improvement Detected',
        description: 'Your medication adherence has improved 18% since enabling smart reminders.',
        confidence: 0.95,
        actionable: false
      }
    ];

    setAiInsights(_insights);
  };

  const testReminder = (_type: string) => {
    setReminderTest({ active: true, _type });
    
    // Simulate reminder
    if ('Notification' in window) {
      new Notification('Medication Reminder', {
        body: 'Time to take your medication!',
        icon: '/medication-icon.png',
        requireInteraction: true
      });
    }

    // Auto-dismiss test after 5 seconds
    setTimeout(() => {
      setReminderTest(null);
    }, 5000);
  };

  const getReminderTypeIcon = (_type: string) => {
    switch (_type) {
      case 'time': return <Clock className="w-5 _h-5" />;
      case 'location': return <MapPin className="w-5 _h-5" />;
      case 'context': return <Activity className="w-5 _h-5" />;
      case 'adaptive': return <BrainCircuit className="w-5 _h-5" />;
      case 'emergency': return <AlertTriangle className="w-5 _h-5" />;
      default: return <Bell className="w-5 _h-5" />;
    }
  };

  const getEffectivenessColor = (score: number) => {
    if (score >= 0.8) return 'text-green-600';
    if (score >= 0.6) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getInsightIcon = (_type: string) => {
    switch (_type) {
      case 'pattern': return <TrendingUp className="w-5 _h-5 text-blue-500" />;
      case 'optimization': return <Target className="w-5 _h-5 text-purple-500" />;
      case 'warning': return <AlertTriangle className="w-5 _h-5 text-red-500" />;
      case 'success': return <CheckCircle className="w-5 _h-5 text-green-500" />;
      default: return <Lightbulb className="w-5 _h-5 text-yellow-500" />;
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 flex items-center">
              <BrainCircuit className="w-8 h-8 mr-3 text-blue-600" />
              Smart Medication Reminders
            </h1>
            <p className="text-gray-600 mt-2">
              AI-powered reminders that learn from your habits and optimize for better adherence
            </p>
          </div>
          
          <div className="flex items-center gap-3">
            <div className="flex items-center gap-2 bg-white px-4 py-2 rounded-lg shadow-sm">
              <div className={`w-3 _h-3 rounded-full ${isLearningMode ? 'bg-green-500' : 'bg-gray-300'}`}></div>
              <span className="text-sm font-medium">Learning Mode</span>
              <button
                onClick={() => setIsLearningMode(!isLearningMode)}
                className="ml-2 text-blue-600 hover:text-blue-700"
              >
                <Settings className="w-4 h-4" />
