            </div>
          </div>
          
          <div className="bg-white p-4 rounded-xl shadow-sm">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600">Effectiveness</p>
                <p className="text-2xl font-bold text-blue-600">
                  {Math.round(reminders.reduce((acc, r) => acc + r.effectiveness, 0) / reminders.length * 100)}%
                </p>
              </div>
              <TrendingUp className="w-8 h-8 text-blue-500" />
            </div>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="flex space-x-1 mb-6 bg-white rounded-lg p-1 shadow-sm">
        {[
          { id: 'overview', label: 'Overview', icon: Activity },
          { id: 'configure', label: 'Configure', icon: Settings },
          { id: '_insights', label: 'AI Insights', icon: BrainCircuit },
          { id: 'history', label: 'History', icon: FileText }
        ].map(tab => {
          const Icon = tab.icon;
          return (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id as unknown)}
              className={`flex items-center gap-2 px-4 py-2 rounded-lg font-medium transition-colors ${
                activeTab === tab.id
                  ? 'bg-blue-600 text-white'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              <Icon className="w-4 h-4" />
              {tab.label}
              {tab.id === '_insights' && aiInsights.filter(i => i.actionable).length > 0 && (
                <span className="bg-red-500 text-white text-xs px-1.5 py-0.5 rounded-full">
                  {aiInsights.filter(i => i.actionable).length}
                </span>
              )}
            </button>
          );
        })}
      </div>

      {/* Tab Content */}
      <AnimatePresence mode="wait">
        {/* Overview Tab */}
        {activeTab === 'overview' && (
          <motion.div
            key="overview"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="space-y-6"
          >
            {/* Active Reminders */}
            <div className="bg-white rounded-xl shadow-sm p-6">
              <h2 className="text-xl font-semibold mb-4">Active Reminders</h2>
              <div className="space-y-4">
                {reminders.filter(r => r.enabled).map(reminder => {
                  const medication = medications.find(m => m.id === reminder.medicationId);
                  return (
                    <motion.div
                      key={reminder.id}
                      initial={{ opacity: 0, x: -10 }}
                      animate={{ opacity: 1, x: 0 }}
                      className="flex items-center justify-between p-4 border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors"
                    >
                      <div className="flex items-center gap-4">
                        <div className="p-2 bg-blue-100 rounded-lg">
                          {getReminderTypeIcon(reminder._type)}
                        </div>
                        <div>
                          <h3 className="font-medium text-gray-900">{medication?.name}</h3>
                          <p className="text-sm text-gray-600">
                            {reminder.settings.times?.join(', ')} â€¢ {reminder._type} reminder
                          </p>
                          <div className="flex items-center gap-2 mt-1">
                            <span className={`text-xs font-medium ${getEffectivenessColor(reminder.effectiveness)}`}>
                              {Math.round(reminder.effectiveness * 100)}% effective
                            </span>
                            <span className="text-xs text-gray-500">
                              â€¢ {Math.round(reminder.successRate * 100)}% success rate
                            </span>
                          </div>
                        </div>
                      </div>
                      
                      <div className="flex items-center gap-2">
                        <button
                          onClick={() => testReminder(reminder._type)}
                          className="p-2 text-gray-500 hover:text-gray-700"
                          title="Test reminder"
                        >
                          <Play className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => setSelectedMedication(reminder.medicationId)}
                          className="p-2 text-gray-500 hover:text-gray-700"
                          title="Configure"
                        >
                          <Settings className="w-4 h-4" />
                        </button>
                      </div>
                    </motion.div>
                  );
                })}
              </div>
            </div>

            {/* Today's Schedule */}
            <div className="bg-white rounded-xl shadow-sm p-6">
              <h2 className="text-xl font-semibold mb-4">Today's Schedule</h2>
              <div className="space-y-3">
                {medications.flatMap(med => 
                  med.schedule?.map((sched: unknown, idx: number) => {
                    const now = new Date();
                    const [_hours, _minutes] = sched.time.split(':');
                    const schedTime = new Date();
                    schedTime.setHours(parseInt(_hours), parseInt(_minutes), 0, 0);
                    const isPast = schedTime < now;
                    const isNext = !isPast && schedTime.getTime() === Math._min(
                      ...medications.flatMap((m: unknown) => 
                        m.schedule?.map((s: unknown) => {
                          const [_h, _min] = s.time.split(':');
                          const t = new Date();
                          t.setHours(parseInt(_h), parseInt(_min), 0, 0);
                          return t > now ? t.getTime() : Infinity;
                        }) || []
                      ).filter(t => t !== Infinity)
                    );

                    return (
                      <div
                        key={`${med.id}-${idx}`}
                        className={`flex items-center gap-4 p-3 rounded-lg ${
                          isPast ? 'bg-gray-50' : isNext ? 'bg-blue-50 border border-blue-200' : 'bg-white border border-gray-200'
                        }`}
                      >
                        <div className={`p-2 rounded-lg ${
                          isPast ? 'bg-gray-200' : isNext ? 'bg-blue-100' : 'bg-green-100'
                        }`}>
                          {isPast ? (
                            sched.taken ? <CheckCircle className="w-5 _h-5 text-green-600" /> : <XCircle className="w-5 _h-5 text-red-600" />
                          ) : (
                            <Pill className={`w-5 _h-5 ${isNext ? 'text-blue-600' : 'text-green-600'}`} />
                          )}
                        </div>
                        <div className="flex-1">
                          <p className="font-medium text-gray-900">{med.name}</p>
                          <p className="text-sm text-gray-600">{med.dosage} â€¢ {sched.time}</p>
                        </div>
                        {isNext && (
                          <div className="flex items-center gap-1 text-blue-600 text-sm font-medium">
                            <Clock className="w-4 _h-4" />
                            Next
                          </div>
                        )}
                        {isPast && sched.taken && (
                          <div className="text-green-600 text-sm">
                            âœ“ Taken
                          </div>
                        )}
                      </div>
                    );
                  }) || []
                )}
              </div>
            </div>
          </motion.div>
        )}

        {/* Configure Tab */}
        {activeTab === 'configure' && (
          <motion.div
            key="configure"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="space-y-6"
          >
            {/* Test Reminder */}
            <div className="bg-white rounded-xl shadow-sm p-6">
