};

// Sleep quality levels
const SLEEP_QUALITY = {
  poor: { name: 'Poor', color: 'text-red-500', value: 1 },
  fair: { name: 'Fair', color: 'text-orange-500', value: 2 },
  good: { name: 'Good', color: 'text-yellow-500', value: 3 },
