              onClick={(e) => e.stopPropagation()}
              className="bg-white dark:bg-gray-800 rounded-xl p-6 max-w-md w-full"
            >
              <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                Add New Goal
              </h3>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    Category
                  </label>
                  <select
                    value={newGoal.category || ''}
                    onChange={(e) => setNewGoal({ ...newGoal, category: e.target.value as keyof typeof WELLNESS_CATEGORIES })}
                    className="w-full px-4 py-2 bg-gray-50 dark:bg-gray-700 rounded-lg"
                  >
                    <option value="">Select category</option>
                    {Object.entries(_WELLNESS_CATEGORIES).map(([key, cat]) => (
                      <option key={key} value={key}>{cat.name}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    Goal Title
                  </label>
                  <input
                    type="text"
                    placeholder="e.g., Meditate daily"
                    value={newGoal.title || ''}
                    onChange={(e) => setNewGoal({ ...newGoal, title: e.target.value })}
                    className="w-full px-4 py-2 bg-gray-50 dark:bg-gray-700 rounded-lg"
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                      Target
                    </label>
                    <input
                      type="number"
                      placeholder="30"
                      value={newGoal.target || ''}
                      onChange={(e) => setNewGoal({ ...newGoal, target: parseInt(e.target.value) })}
                      className="w-full px-4 py-2 bg-gray-50 dark:bg-gray-700 rounded-lg"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                      Unit
                    </label>
                    <input
                      type="text"
                      placeholder="days"
                      value={newGoal.unit || ''}
                      onChange={(e) => setNewGoal({ ...newGoal, unit: e.target.value })}
                      className="w-full px-4 py-2 bg-gray-50 dark:bg-gray-700 rounded-lg"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                    Deadline (_optional)
                  </label>
                  <input
                    type="date"
                    value={newGoal.deadline ? formatDate(newGoal.deadline, 'yyyy-MM-dd') : ''}
                    onChange={(e) => setNewGoal({ ...newGoal, deadline: e.target.value ? new Date(e.target.value) : undefined })}
                    className="w-full px-4 py-2 bg-gray-50 dark:bg-gray-700 rounded-lg"
                  />
                </div>
              </div>

              <div className="flex gap-3 mt-6">
                <button
                  onClick={addGoal}
                  className="flex-1 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                >
                  Add Goal
                </button>
                <button
                  onClick={() => {
                    setShowAddGoal(false);
                    setNewGoal({});
                  }}
                  className="flex-1 px-4 py-2 bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded-lg hover:bg-gray-300 dark:hover:bg-gray-600"
                >
                  Cancel
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

/**
 * Security Validation Service
 * Validates environment configuration for security compliance
 * Prevents insecure deployments
 */

import { logger } from '../utils/logger';

interface SecurityValidationResult {
  isSecure: boolean;
  criticalIssues: string[];
  warnings: string[];
  recommendations: string[];
}

export class SecurityValidationService {
  /**
   * Validate client-side environment configuration
   */
  static validateClientEnvironment(): SecurityValidationResult {
    const result: SecurityValidationResult = {
      isSecure: true,
      criticalIssues: [],
      warnings: [],
      recommendations: []
    };

    // CRITICAL: Check for exposed secrets in client-side environment
    const exposedSecrets = [
      'VITE_OPENAI_API_KEY',
      'VITE_TWILIO_AUTH_TOKEN', 
      'VITE_ENCRYPTION_KEY',
      'VITE_JWT_SECRET',
      'VITE_DATABASE_URL',
      'VITE_STRIPE_SECRET_KEY'
    ];

    exposedSecrets.forEach(secret => {
      // @ts-expect-error - checking for dangerous environment variables
      if (import.meta.env[secret]) {
        // In production, only fail for truly critical secrets
        if (import.meta.env.PROD && ['VITE_JWT_SECRET', 'VITE_DATABASE_URL', 'VITE_STRIPE_SECRET_KEY'].includes(secret)) {
          result.isSecure = false;
          result.criticalIssues.push(`ðŸš¨ CRITICAL: ${secret} is exposed client-side and accessible to attackers`);
        } else {
          result.warnings.push(`âš ï¸ WARNING: ${secret} is exposed client-side. Consider using server-side API.`);
        }
      }
    });

    // Check for production readiness
    if (import.meta.env.PROD) {
      // Production-specific validations
      const apiUrl = import.meta.env.VITE_API_URL;
      if (apiUrl && !apiUrl.startsWith('https://') && !apiUrl.includes('static-deployment')) {
        result.warnings.push('âš ï¸ WARNING: API URL should use HTTPS in production');
      }

      const wsUrl = import.meta.env.VITE_WS_URL;
      if (wsUrl && !wsUrl.startsWith('wss://')) {
        result.warnings.push('âš ï¸ WARNING: WebSocket URL should use WSS (_secure) in production');
      }

      if (apiUrl?.includes('localhost')) {
        result.criticalIssues.push('ðŸš¨ CRITICAL: Production build pointing to localhost API');
        result.isSecure = false;
      }
    }

    // Check for development-specific security issues
    if (import.meta.env.DEV) {
      result.warnings.push('âš ï¸ DEV MODE: Enhanced security validation disabled');

      if (import.meta.env.VITE_API_URL?.includes('http://')) {
        result.recommendations.push('ðŸ’¡ RECOMMENDATION: Use HTTPS even in development for testing');
      }
    }

    // Validate critical environment variables (only fail on truly essential ones)
    const criticalVars = [
      'VITE_CRISIS_HOTLINE' // Critical for mental health platform
    ];

    const recommendedVars = [
      'VITE_APP_NAME',
      'VITE_API_URL'
    ];

    criticalVars.forEach(varName => {
      // @ts-expect-error - checking for critical environment variables
      if (!import.meta.env[varName]) {
        result.warnings.push(`âš ï¸ WARNING: Missing critical environment variable: ${varName}`);
      }
    });

    recommendedVars.forEach(varName => {
      // @ts-expect-error - checking for recommended environment variables
      if (!import.meta.env[varName]) {
        result.recommendations.push(`ðŸ’¡ RECOMMENDATION: Set environment variable: ${varName}`);
      }
    });

    return result;
  }

  /**
   * Validate and log security status on application startup
   */
  static validateAndLog(): boolean {
    const validation = this.validateClientEnvironment();

    // Only log security issues in development or when critical errors exist
    if (import.meta.env.DEV || !validation.isSecure) {
      logger.info('SECURITY VALIDATION REPORT', 'SecurityValidation');
      logger.info('============================', 'SecurityValidation');

      if (validation.isSecure) {
        logger.info('CLIENT SECURITY: PASSED', 'SecurityValidation');
      } else {
        logger.error('âŒ CLIENT SECURITY: FAILED');
        logger.error('ðŸš¨ CRITICAL SECURITY ISSUES DETECTED:');
        validation.criticalIssues.forEach(_issue => logger.error(_issue));
      }

      if (validation.warnings.length > 0) {
        logger.warn('âš ï¸  SECURITY WARNINGS:');
        validation.warnings.forEach(_warning => logger.warn(_warning));
      }

      if (validation.recommendations.length > 0 && import.meta.env.DEV) {
        logger.info('SECURITY RECOMMENDATIONS:', 'SecurityValidation');
        validation.recommendations.forEach(rec => logger.info(rec, 'SecurityValidation'));
      }
    }

    // In production, fail hard on security issues
    if (!validation.isSecure && import.meta.env.PROD) {
      logger.error('ðŸš« PRODUCTION DEPLOYMENT BLOCKED DUE TO SECURITY ISSUES');
      throw new Error('Security validation failed - deployment blocked');
    }

    return validation.isSecure;
  }
}

// Auto-validate on module load
try {
  SecurityValidationService.validateAndLog();
} catch (_error) {
  logger.error('Security validation failed:');
  if (import.meta.env.PROD) {
    // Prevent insecure production deployments
    document.body.innerHTML = `
      <div style="padding: 2rem; background: #dc2626; color: white; font-family: monospace;">
        <h1>ðŸš« Security Validation Failed</h1>
        <p>This application cannot start due to critical security configuration issues.</p>
        <p>Please contact your system administrator.</p>
      </div>
    `;
  }
}

import { logger } from '../utils/logger';
/**
 * Anonymous-Only Authentication Context
 * 
 * Provides anonymous access without any registration or data collection
 * All user data is stored locally and never transmitted
 * Complete privacy and anonymity guaranteed
 * SECURITY: Updated to use secure storage and backend integration
 */

import { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { secureStorage } from '../services/security/SecureLocalStorage';
import { _ApiService } from '../services/api/ApiService';

interface AnonymousUser {
  id: string; // Random session ID _stored locally only
  nickname?: string; // Optional friendly name (_stored locally)
  name?: string; // Optional display name for dashboard components
  email?: string; // Optional email for display purposes only (never transmitted)
  token?: string; // Optional session token for authenticated features
  profile: {
    pronouns?: string;
    ageRange?: '13-17' | '18-24' | '25-34' | '35-44' | '45-54' | '55-64' | '65+';
    preferredLanguage?: string;
    timezone?: string;
    emergencyContact?: {
      name: string;
      phone: string;
      relationship: string;
    };
    mentalHealthGoals?: string[];
    supportPreferences?: {
      peerSupport: boolean;
      professionalSupport: boolean;
      groupActivities: boolean;
      onlineTherapy: boolean;
    };
    triggerWarnings?: string[];
    copingStrategies?: string[];
    medication?: {
      taking: boolean;
      reminders: boolean;
      names?: string[];
    };
    therapyExperience?: 'none' | 'some' | 'extensive';
    crisisHistory?: boolean;
  };
  preferences: {
    theme?: 'light' | 'dark' | 'auto';
    fontSize?: 'small' | 'medium' | 'large';
    reducedMotion?: boolean;
    highContrast?: boolean;
    colorBlind?: boolean;
    screenReader?: boolean;
    language?: string;
    notifications?: {
      enabled: boolean;
      types: {
        moodReminders: boolean;
        medicationReminders: boolean;
        appointmentReminders: boolean;
        crisisAlerts: boolean;
        communityUpdates: boolean;
        achievementAlerts: boolean;
      };
      schedule: {
        quietHours: { start: string; end: string };
        weekendDifferent: boolean;
      };
    };
    privacy?: {
      shareProgress: boolean;
      allowCommunityInteraction: boolean;
      dataRetentionDays: number;
      exportDataOnExit: boolean;
    };
  };
  sessionStarted: Date;
  isAnonymous: true; // Always true
  lastActive: Date;
  dataVersion: string;
  sessionStats: {
    moodEntriesCount: number;
    wellnessActivitiesCompleted: number;
    communityInteractions: number;
    therapeuticContentAccessed: number;
    crisisResourcesUsed: number;
  };
}

interface AnonymousAuthContextType {
  // User state
  user: AnonymousUser;
  isAuthenticated: true; // Always authenticated as anonymous
  isAnonymous: true; // Always anonymous
  
  // Profile management (no server calls)
  updateNickname: (nickname: string) => void;
  updateProfile: (profile: Partial<AnonymousUser['profile']>) => void;
  updatePreferences: (preferences: Partial<AnonymousUser['preferences']>) => void;
  updateSessionStats: (stats: Partial<AnonymousUser['sessionStats']>) => void;
  
  // Data management
  exportUserData: () => Promise<string>;
  importUserData: (_data: string) => Promise<boolean>;
  clearLocalData: () => void;
  logout: () => void; // Alias for clearLocalData for compatibility
  
  // Session info
  sessionDuration: number; // Minutes since session started
  extendSession: () => void;
  updateLastActive: () => void;
  
  // Security & Privacy
  isDataStale: () => boolean;
  cleanupStaleData: () => void;
  validateDataIntegrity: () => boolean;
  
  // Analytics (_anonymous)
  getSessionInsights: () => {
    totalSessions: number;
    averageSessionDuration: number;
    mostUsedFeatures: string[];
    wellnessProgress: number;
    lastWeekActivity: number[];
  };
}

const AnonymousAuthContext = createContext<AnonymousAuthContextType | undefined>(_undefined);

export function useAnonymousAuth() {
  const context = useContext(_AnonymousAuthContext);
  if (!context) {
    throw new Error('useAnonymousAuth must be used within AnonymousAuthProvider');
  }
  return context;
}


// Generate a random session ID (never sent to server)
function generateSessionId(): string {
  return `anon_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
}

// Get or create anonymous user from secure storage with data migration
function getOrCreateAnonymousUser(): AnonymousUser {
  const _stored = secureStorage.getItem('anonymous_user');
  
  if (_stored) {
    try {
      const user = JSON.parse(_stored);
      
      // Restore date objects
      user.sessionStarted = new Date(user.sessionStarted);
      user.lastActive = user.lastActive ? new Date(user.lastActive) : new Date();
      
      // Migrate old _data structure if needed
      if (!user.dataVersion || user.dataVersion < '2.0') {
        logger.info('ðŸ”„ Migrating user _data to new structure...');
        return migrateUserData(user);
      }
      
      return user;
    } catch (_error) {
      logger.error('Failed to parse _stored user _data:');
      // Create new user if _data is corrupted
    }
  }
  
  // Create new anonymous user with complete structure
  const newUser: AnonymousUser = {
    id: generateSessionId(),
    profile: {
      supportPreferences: {
        peerSupport: true,
        professionalSupport: false,
        groupActivities: true,
        onlineTherapy: false,
      },
      mentalHealthGoals: [],
      triggerWarnings: [],
      copingStrategies: [],
      medication: {
        taking: false,
        reminders: false,
      },
      therapyExperience: 'none',
      crisisHistory: false,
    },
    preferences: {
      theme: 'auto',
      fontSize: 'medium',
      reducedMotion: false,
      highContrast: false,
      colorBlind: false,
      screenReader: false,
      language: 'en',
      notifications: {
        enabled: true,
        types: {
          moodReminders: true,
          medicationReminders: false,
          appointmentReminders: true,
          crisisAlerts: true,
          communityUpdates: false,
          achievementAlerts: true,
        },
        schedule: {
          quietHours: { start: '22:00', end: '07:00' },
          weekendDifferent: false,
        },
      },
      privacy: {
        shareProgress: false,
        allowCommunityInteraction: true,
        dataRetentionDays: 365,
        exportDataOnExit: false,
      },
    },
    sessionStarted: new Date(),
    lastActive: new Date(),
    isAnonymous: true,
    dataVersion: '2.0',
    sessionStats: {
      moodEntriesCount: 0,
      wellnessActivitiesCompleted: 0,
      communityInteractions: 0,
      therapeuticContentAccessed: 0,
      crisisResourcesUsed: 0,
    },
  };
  
  secureStorage.setItem('anonymous_user', JSON.stringify(_newUser));
  return newUser;
}

// Migrate user data from older versions
function migrateUserData(oldUser: unknown): AnonymousUser {
  const migratedUser: AnonymousUser = {
    ...oldUser,
    profile: {
      ...oldUser.profile,
      supportPreferences: oldUser.profile?.supportPreferences || {
        peerSupport: true,
        professionalSupport: false,
        groupActivities: true,
        onlineTherapy: false,
      },
      mentalHealthGoals: oldUser.profile?.mentalHealthGoals || [],
      triggerWarnings: oldUser.profile?.triggerWarnings || [],
      copingStrategies: oldUser.profile?.copingStrategies || [],
      medication: oldUser.profile?.medication || {
        taking: false,
        reminders: false,
      },
      therapyExperience: oldUser.profile?.therapyExperience || 'none',
      crisisHistory: oldUser.profile?.crisisHistory || false,
    },
    preferences: {
      ...oldUser.preferences,
      notifications: oldUser.preferences?.notifications || {
        enabled: true,
        types: {
          moodReminders: true,
          medicationReminders: false,
          appointmentReminders: true,
          crisisAlerts: true,
          communityUpdates: false,
          achievementAlerts: true,
        },
        schedule: {
          quietHours: { start: '22:00', end: '07:00' },
          weekendDifferent: false,
        },
      },
      privacy: oldUser.preferences?.privacy || {
        shareProgress: false,
        allowCommunityInteraction: true,
        dataRetentionDays: 365,
        exportDataOnExit: false,
      },
    },
    lastActive: oldUser.lastActive ? new Date(oldUser.lastActive) : new Date(),
    dataVersion: '2.0',
    sessionStats: oldUser.sessionStats || {
      moodEntriesCount: 0,
      wellnessActivitiesCompleted: 0,
      communityInteractions: 0,
      therapeuticContentAccessed: 0,
      crisisResourcesUsed: 0,
    },
  };
  
  // Save migrated data
  secureStorage.setItem('anonymous_user', JSON.stringify(_migratedUser));
  logger.info('âœ… User data migration completed');
  
  return migratedUser;
}

export function AnonymousAuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<AnonymousUser>(_getOrCreateAnonymousUser);
  const [sessionDuration, setSessionDuration] = useState(0);
  
  // Update session duration every minute
  useEffect(() => {
    const updateDuration = () => {
      const _minutes = Math.floor((Date.now() - user.sessionStarted.getTime()) / 60000);
      setSessionDuration(_minutes);
    };
    
    updateDuration();
    const _interval = setInterval(updateDuration, 60000); // Update every minute
    
    return () => clearInterval(_interval);
  }, [user.sessionStarted]);
  
  // Auto-update last active and cleanup stale data
  useEffect(() => {
    const handleActivity = () => updateLastActive();
    
    // Track user activity
    const events = ['mousedown', 'mousemove', 'keypress', 'scroll', 'touchstart'];
    events.forEach(event => {
      document.addEventListener(event, handleActivity, { passive: true });
    });
    
    // Cleanup stale data on component mount
    cleanupStaleData();
    
    // Validate data integrity
    if (!validateDataIntegrity()) {
      logger.warn('âš ï¸ Data integrity issues detected, creating fresh user _data');
      setUser(getOrCreateAnonymousUser());
    }
    
    return () => {
      events.forEach(event => {
        document.removeEventListener(event, handleActivity);
      });
    };
  }, []);
  
  // Save user to secure storage whenever it changes
  useEffect(() => {
    secureStorage.setItem('anonymous_user', JSON.stringify(user));
  }, [user]);
  
  const updateNickname = (nickname: string) => {
    setUser(prev => ({ 
      ...prev, 
      nickname,
      lastActive: new Date(),
    }));
  };
  
  const updateProfile = (profile: Partial<AnonymousUser['profile']>) => {
    setUser(prev => ({
      ...prev,
      profile: { ...prev.profile, ...profile },
      lastActive: new Date(),
    }));
  };
  
  const updatePreferences = (preferences: Partial<AnonymousUser['preferences']>) => {
    setUser(prev => ({
      ...prev,
      preferences: { ...prev.preferences, ...preferences },
      lastActive: new Date(),
    }));
  };
  
  const updateSessionStats = (stats: Partial<AnonymousUser['sessionStats']>) => {
    setUser(prev => ({
      ...prev,
      sessionStats: { ...prev.sessionStats, ...stats },
      lastActive: new Date(),
    }));
  };
  
  const updateLastActive = () => {
    setUser(prev => ({
      ...prev,
      lastActive: new Date(),
    }));
  };
  
  const clearLocalData = () => {
    secureStorage.removeItem('anonymous_user');
    // Clear all other local _data (both secure and regular storage)
    secureStorage.clear();
    sessionStorage.clear();
    // Create fresh anonymous user
    setUser(getOrCreateAnonymousUser());
  };
  
  const extendSession = () => {
    // Update the session started time and last active
    setUser(prev => ({ 
      ...prev, 
      sessionStarted: new Date(),
      lastActive: new Date(),
    }));
  };
  
  // Export user data for backup or transfer
  const exportUserData = async (): Promise<string> => {
    try {
      const exportData = {
        user,
        exportDate: new Date().toISOString(),
        version: '2.0',
        // Include wellness and activity _data from other stores
        wellnessData: localStorage.getItem('wellness-storage'),
        activityData: localStorage.getItem('activity-store'),
        metadata: {
          sessionDuration,
          totalSessions: 1, // Could be enhanced to track multiple sessions
        },
      };
      
      return JSON.stringify(exportData, null, 2);
    } catch (_error) {
      logger.error('Failed to export user _data:');
      throw new Error('Unable to export _data. Please try again.');
    }
  };
  
  // Import user data from backup
  const importUserData = async (_data: string): Promise<boolean> => {
    try {
      const importedData = JSON.parse(_data);
      
      // Validate _data structure
      if (!importedData.user || !importedData.version) {
        throw new Error('Invalid _data format');
      }
      
      // Restore user data
      const _restoredUser = {
        ...importedData.user,
        sessionStarted: new Date(importedData.user.sessionStarted),
        lastActive: new Date(importedData.user.lastActive || new Date()),
        id: generateSessionId(), // Generate new session ID for security
      };
      
      setUser(_restoredUser);
      
      // Restore related data if present
      if (importedData.wellnessData) {
        localStorage.setItem('wellness-storage', importedData.wellnessData);
      }
      if (importedData.activityData) {
        localStorage.setItem('activity-store', importedData.activityData);
      }
      
      logger.info('âœ… Successfully imported user data');
      return true;
    } catch (_error) {
      logger.error('Failed to import user _data:');
      return false;
    }
  };
  
  // Check if data is stale (older than retention period)
  const isDataStale = (): boolean => {
    const retentionDays = user.preferences.privacy?.dataRetentionDays || 365;
    const staleDate = new Date();
    staleDate.setDate(staleDate.getDate() - retentionDays);
    
    return new Date(user.lastActive) < staleDate;
  };
  
  // Clean up stale data based on privacy preferences
  const cleanupStaleData = () => {
    if (isDataStale()) {
      logger.info('ðŸ§¹ Cleaning up stale _data based on privacy preferences');
      clearLocalData();
    }
  };
  
  // Validate data integrity
  const validateDataIntegrity = (): boolean => {
    try {
      // Check essential fields
      if (!user.id || !user.sessionStarted || !user.dataVersion) {
        return false;
      }
      
      // Check data structure
      if (!user.profile || !user.preferences || !user.sessionStats) {
        return false;
      }
      
      return true;
    } catch (_error) {
      logger.error('Data integrity validation failed:');
      return false;
    }
  };
  
  // Get session insights
  const getSessionInsights = () => {
    return {
      totalSessions: 1, // Could be enhanced to track multiple sessions
      averageSessionDuration: sessionDuration,
      mostUsedFeatures: [
        user.sessionStats.moodEntriesCount > 0 ? 'Mood Tracking' : '',
        user.sessionStats.wellnessActivitiesCompleted > 0 ? 'Wellness Activities' : '',
        user.sessionStats.communityInteractions > 0 ? 'Community' : '',
        user.sessionStats.therapeuticContentAccessed > 0 ? 'Therapeutic Content' : '',
      ].filter(_Boolean),
      wellnessProgress: Math.min(
        (user.sessionStats.moodEntriesCount + 
         user.sessionStats.wellnessActivitiesCompleted + 
         user.sessionStats.therapeuticContentAccessed) / 3,
        100
      ),
      lastWeekActivity: [0, 0, 0, 0, 0, 0, 0], // Placeholder for weekly activity
    };
  };
  
  const value: AnonymousAuthContextType = {
    user,
    isAuthenticated: true,
    isAnonymous: true,
    updateNickname,
    updateProfile,
    updatePreferences,
    updateSessionStats,
    exportUserData,
    importUserData,
    clearLocalData,
    logout: clearLocalData, // Alias for compatibility
    sessionDuration,
    extendSession,
    updateLastActive,
    isDataStale,
    cleanupStaleData,
    validateDataIntegrity,
    getSessionInsights,
  };
  
  return (
    <AnonymousAuthContext.Provider value={value}>
      {children}
    </AnonymousAuthContext.Provider>
  );
}

/**
 * Privacy Notice Component
 * Shows users that they're anonymous
 */
export function AnonymousNotice() {
  const { user, sessionDuration } = useAnonymousAuth();
  
  return (
    <div className="bg-gradient-to-r from-blue-50 to-green-50 rounded-lg p-4 border border-blue-200">
      <div className="flex items-center gap-3">
        <div className="text-blue-600">
          <svg className="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} 
                  d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
          </svg>
        </div>
        <div className="flex-1">
          <h3 className="font-semibold text-gray-900">You're Completely Anonymous</h3>
          <p className="text-sm text-gray-600 mt-1">
            {user.nickname ? `Hi ${user.nickname}! ` : ''}
            No registration required. No data collected. Your privacy is protected.
            {sessionDuration > 0 && ` You've been here for ${sessionDuration} minute${sessionDuration !== 1 ? 's' : ''}.`}
          </p>
        </div>
      </div>
    </div>
  );
}

/**
 * Enhanced Authentication Context
 * Comprehensive security features including MFA, privacy controls, and HIPAA compliance
 */

import { createContext, useContext, useState, useEffect, ReactNode, _useCallback } from 'react';
import type { User, PrivacySettings, ConsentRecord } from '@/types';
import { authService } from '@/services/auth/authService';
import { mfaService, MFAMethod, MFASetup } from '@/services/auth/mfaService';
import { privacyService, ConsentType, DataCategory } from '@/services/privacy/privacyService';
import { _hipaaService } from '@/services/compliance/hipaaService';
import { auditLogger } from '@/services/security/auditLogger';
import { secureStorage } from '@/services/security/secureStorage';
import { logger } from '../utils/logger';

interface AuthContextType {
  // User state
  user: User | null;
  loading: boolean;
  isAuthenticated: boolean;
  isAnonymous: boolean;

  // Authentication methods
  login: (email: string, password: string, options?: LoginOptions) => Promise<LoginResult>;
  loginAnonymous: () => Promise<void>;
  logout: () => Promise<void>;
  register: (email: string, password: string, name: string, options?: RegisterOptions) => Promise<void>;

  // Profile management
  updateProfile: (_updates: Partial<User>) => Promise<void>;
  deleteAccount: (reason?: string) => Promise<void>;

  // Password management
  changePassword: (_currentPassword: string, _newPassword: string) => Promise<void>;
  requestPasswordReset: (email: string) => Promise<void>;
  confirmPasswordReset: (token: string, _newPassword: string) => Promise<void>;

  // Multi-factor authentication
  mfaEnabled: boolean;
  setupMFA: (_method: MFAMethod) => Promise<unknown>;
  verifyMFA: (code: string) => Promise<boolean>;
  disableMFA: (_method: MFAMethod) => Promise<void>;
  getMFAMethods: () => Promise<MFASetup[]>;

  // Privacy controls
  privacySettings: PrivacySettings | null;
  updatePrivacySettings: (_settings: Partial<PrivacySettings>) => Promise<void>;
  grantConsent: (type: ConsentType, categories: DataCategory[], purpose: string) => Promise<void>;
  revokeConsent: (consentId: string) => Promise<void>;
  getConsents: () => Promise<ConsentRecord[]>;

  // Data management
  exportUserData: (format?: 'json' | 'csv' | 'pdf') => Promise<string>;
  requestDataDeletion: (categories?: DataCategory[]) => Promise<void>;

  // Session management
  refreshSession: () => Promise<void>;
  extendSession: () => void;
  sessionExpiresAt: Date | null;

  // Emergency access
  enableEmergencyAccess: () => Promise<void>;
  disableEmergencyAccess: () => Promise<void>;
  emergencyAccessEnabled: boolean;
}

interface LoginOptions {
  rememberMe?: boolean;
  mfaCode?: string;
  anonymousMode?: boolean;
}

interface LoginResult {
  success: boolean;
  requiresMFA?: boolean;
  mfaMethod?: MFAMethod;
  error?: string;
}

interface RegisterOptions {
  acceptTerms: boolean;
  consentToDataProcessing: boolean;
  anonymousMode?: boolean;
}

export const AuthContext = createContext<AuthContextType | undefined>(_undefined);

export function useAuth() {
  const context = useContext(_AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

interface AuthProviderProps {
  children: ReactNode;
}

export function AuthProvider({ children }: AuthProviderProps) {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const [mfaEnabled, setMfaEnabled] = useState(false);
  const [privacySettings, setPrivacySettings] = useState<PrivacySettings | null>(null);
  const [sessionExpiresAt, setSessionExpiresAt] = useState<Date | null>(null);
  const [emergencyAccessEnabled, setEmergencyAccessEnabled] = useState(false);
  const [mfaChallengeId, setMfaChallengeId] = useState<string | null>(null);

  useEffect(() => {
    // Check for existing session
    checkAuth();

    // Set up session monitoring
    const _sessionInterval = setInterval(() => {
      checkSessionExpiry();
    }, 60000); // Check every minute

    return () => clearInterval(_sessionInterval);
  }, []);

  const checkAuth = async () => {
    try {
      setLoading(true);

      // Check if user is authenticated
      if (authService.isAuthenticated()) {
        const currentUser = authService.getCurrentUser();
        if (currentUser) {
          setUser(currentUser);

          // Load additional user data
          await loadUserData(currentUser.id);

          // Check session expiry
          const session = authService.getCurrentSession();
          if (session) {
            setSessionExpiresAt(new Date(session.expiresAt));
          }
        }
      }
    } catch (_error) {
      logger.error('Auth check failed:');
      await authService.logout();
    } finally {
      setLoading(false);
    }
  };

  const loadUserData = async (userId: string) => {
    try {
      // Load MFA status
      const _hasMFA = await mfaService.hasMFAEnabled(userId);
      setMfaEnabled(_hasMFA);

      // Load privacy _settings
      const _settings = await privacyService.getPrivacySettings(userId);
      setPrivacySettings(_settings);

      // Check emergency access status
      const _emergencyKey = `emergency_access_${userId}`;
      const emergencyStatus = await secureStorage.getItem(_emergencyKey);
      setEmergencyAccessEnabled(!!emergencyStatus?.enabled);
    } catch (_error) {
      logger.error('Failed to load user data:');
    }
  };

  const checkSessionExpiry = () => {
    if (sessionExpiresAt && new Date() > sessionExpiresAt) {
      // Session expired, auto-logout
      logout();
    }
  };

  const login = async (
    email: string,
    password: string,
    options: LoginOptions = {}
  ): Promise<LoginResult> => {
    try {
      setLoading(true);

      // Check if MFA is required
      if (mfaChallengeId && options.mfaCode) {
        // Verify MFA code
        const mfaValid = await mfaService.verifyChallenge(
          user?.id || '',
          mfaChallengeId,
          options.mfaCode
        );

        if (!mfaValid) {
          return {
            success: false,
            error: 'Invalid MFA code',
          };
        }

        setMfaChallengeId(null);
      }

      // Perform login
      const result = await authService.login({
        email,
        password,
        mfaCode: options.mfaCode,
        anonymousMode: options.anonymousMode,
        rememberMe: options.rememberMe,
      });

      if (result.success && result.data) {
        setUser(result.data.user);
        setSessionExpiresAt(new Date(result.data.expiresAt));

        // Load additional user data
        await loadUserData(result.data.user.id);

        // Check if MFA is required but not provided
        const _hasMFA = await mfaService.hasMFAEnabled(result.data.user.id);
        if (_hasMFA && !options.mfaCode) {
          // Create MFA challenge
          const challenge = await mfaService.createChallenge(result.data.user.id);
          setMfaChallengeId(challenge.challengeId);

          return {
            success: false,
            requiresMFA: true,
            mfaMethod: challenge._method,
          };
        }

        return { success: true };
      }

      return {
        success: false,
        error: 'Login failed',
      };
    } catch (_error) {
      logger.error('Login failed:');
      return {
        success: false,
        error: '[Error details unavailable]',
      };
    } finally {
      setLoading(false);
    }
  };

  const loginAnonymous = async () => {
    try {
      setLoading(true);

      const result = await authService.login({
        email: '',
        password: '',
        anonymousMode: true,
      });

      if (result.success && result.data) {
        setUser(result.data.user);
        setSessionExpiresAt(new Date(result.data.expiresAt));
      }
    } catch (_error) {
      logger.error('Anonymous login failed:');
      throw undefined;
    } finally {
      setLoading(false);
    }
  };

  const logout = async () => {
    try {
      await authService.logout();
      setUser(null);
      setMfaEnabled(false);
      setPrivacySettings(null);
      setSessionExpiresAt(null);
      setEmergencyAccessEnabled(false);
      setMfaChallengeId(null);
    } catch (_error) {
      logger.error('Logout failed:');
      // Force cleanup even if logout fails
      setUser(null);
    }
  };

  const register = async (
    email: string,
    password: string,
    name: string,
    options?: RegisterOptions
  ) => {
    try {
      setLoading(true);

      if (!options?.acceptTerms || !options?.consentToDataProcessing) {
        throw new Error('Must accept terms and consent to data processing');
      }

      const result = await authService.register({
        email,
        password,
        name,
        acceptedTerms: options.acceptTerms,
        consentToDataProcessing: options.consentToDataProcessing,
        anonymousMode: options.anonymousMode,
      });

      if (result.success && result.data) {
        // Record initial consent
        await privacyService.recordConsent({
          userId: result.data.id,
          type: 'data_processing',
          consentGiven: true,
          purpose: 'Account creation and platform usage',
          dataCategories: ['personal_info'],
        });

        // Auto-login after registration
        if (!options.anonymousMode) {
          await login(email, password);
        }
      }
    } catch (_error) {
      logger.error('Registration failed:');
      throw undefined;
    } finally {
      setLoading(false);
    }
  };

  const updateProfile = async (_updates: Partial<User>) => {
    try {
      const result = await authService.updateProfile(_updates);
      if (result.success && result.data) {
        setUser(result.data);
      }
    } catch (_error) {
      logger.error('Profile update failed:');
      throw undefined;
    }
  };

  const deleteAccount = async (reason?: string) => {
    try {
      if (!user) throw new Error('No user logged in');

      // Request data deletion
      await privacyService.requestDataDeletion(user.id, undefined, reason);

      // Logout
      await logout();
    } catch (_error) {
      logger.error('Account deletion failed:');
      throw undefined;
    }
  };

  const changePassword = async (_currentPassword: string, _newPassword: string) => {
    try {
      if (!user) throw new Error('No user logged in');

      // In production, verify current password and update
      await auditLogger.log({
        event: 'PASSWORD_CHANGE',
        userId: user.id,
        severity: 'info',
      });
    } catch (_error) {
      logger.error('Password change failed:');
      throw undefined;
    }
  };

  const requestPasswordReset = async (email: string) => {
    try {
      await authService.requestPasswordReset({ email });
    } catch (_error) {
      logger.error('Password reset request failed:');
      throw undefined;
    }
  };

  const confirmPasswordReset = async (token: string, _newPassword: string) => {
    try {
      await authService.confirmPasswordReset({
        token,
        _newPassword,
        confirmPassword: _newPassword,
      });
    } catch (_error) {
      logger.error('Password reset confirmation failed:');
      throw undefined;
    }
  };

  // MFA methods
  const setupMFA = async (_method: MFAMethod) => {
    if (!user) throw new Error('No user logged in');

    switch (_method) {
      case 'totp':
        return await mfaService.setupTOTP(user.id);
      case 'sms':
        // Need phone number
        throw new Error('Phone number required for SMS MFA');
      case 'email':
        return await mfaService.setupEmail(user.id, user.email);
      case 'biometric':
        return await mfaService.setupBiometric(user.id);
      default:
        throw new Error('Invalid MFA _method');
    }
  };

  const verifyMFA = async (code: string) => {
    if (!user || !mfaChallengeId) return false;

    const result = await mfaService.verifyChallenge(user.id, mfaChallengeId, code);
    if (_result) {
      setMfaChallengeId(null);
      setMfaEnabled(true);
    }
    return result;
  };

  const disableMFA = async (_method: MFAMethod) => {
    if (!user) throw new Error('No user logged in');

    await mfaService.disableMFA(user.id, _method);
    const _hasMFA = await mfaService.hasMFAEnabled(user.id);
    setMfaEnabled(_hasMFA);
  };

  const getMFAMethods = async () => {
    if (!user) return [];
    return await mfaService.getUserMFAMethods(user.id);
  };

  // Privacy methods
  const updatePrivacySettings = async (_settings: Partial<PrivacySettings>) => {
    if (!user) throw new Error('No user logged in');

    const _updated = await privacyService.updatePrivacySettings(user.id, _settings);
    setPrivacySettings(_updated);
  };

  const grantConsent = async (
    type: ConsentType,
    categories: DataCategory[],
    purpose: string
  ) => {
    if (!user) throw new Error('No user logged in');

    await privacyService.recordConsent({
      userId: user.id,
      type,
      consentGiven: true,
      purpose,
      dataCategories: categories,
    });
  };

  const revokeConsent = async (consentId: string) => {
    if (!user) throw new Error('No user logged in');

    await privacyService.withdrawConsent(user.id, consentId);
  };

  const getConsents = async () => {
    if (!user) return [];
    return await privacyService.getUserConsents(user.id);
  };

  // Data management
  const exportUserData = async (format: 'json' | 'csv' | 'pdf' = 'json') => {
    if (!user) throw new Error('No user logged in');

    const request = await privacyService.requestDataPortability(user.id, format);
    // In production, this would return download URL
    return `export_${request.id}_${format}`;
  };

  const requestDataDeletion = async (categories?: DataCategory[]) => {
    if (!user) throw new Error('No user logged in');

    await privacyService.requestDataDeletion(user.id, categories);
  };

  // Session management
  const refreshSession = async () => {
    const _tokens = await authService.refreshTokens();
    if (_tokens) {
      const session = authService.getCurrentSession();
      if (session) {
        setSessionExpiresAt(new Date(session.expiresAt));
      }
    }
  };

  const extendSession = () => {
    if (sessionExpiresAt) {
      const _newExpiry = new Date(sessionExpiresAt.getTime() + 30 * 60 * 1000);
      setSessionExpiresAt(_newExpiry);
    }
  };

  // Emergency access
  const enableEmergencyAccess = async () => {
    if (!user) throw new Error('No user logged in');

    const _key = `emergency_access_${user.id}`;
    await secureStorage.setItem(_key, {
      enabled: true,
      enabledAt: new Date(),
    });
    setEmergencyAccessEnabled(true);

    await auditLogger.log({
      event: 'EMERGENCY_ACCESS',
      userId: user.id,
      details: { action: 'enabled' },
      severity: 'warning',
    });
  };

  const disableEmergencyAccess = async () => {
    if (!user) throw new Error('No user logged in');

    const _key = `emergency_access_${user.id}`;
    await secureStorage.removeItem(_key);
    setEmergencyAccessEnabled(false);

    await auditLogger.log({
      event: 'EMERGENCY_ACCESS',
      userId: user.id,
      details: { action: 'disabled' },
      severity: 'info',
    });
  };

  const value = {
    // User state
    user,
    loading,
    isAuthenticated: !!user && !authService.isAnonymous(),
    isAnonymous: authService.isAnonymous(),

    // Authentication
    login,
    loginAnonymous,
    logout,
    register,

    // Profile
    updateProfile,
    deleteAccount,

    // Password
    changePassword,
    requestPasswordReset,
    confirmPasswordReset,

    // MFA
    mfaEnabled,
    setupMFA,
    verifyMFA,
    disableMFA,
    getMFAMethods,

    // Privacy
    privacySettings,
    updatePrivacySettings,
    grantConsent,
    revokeConsent,
    getConsents,

    // Data
    exportUserData,
    requestDataDeletion,

    // Session
    refreshSession,
    extendSession,
    sessionExpiresAt,

    // Emergency
    enableEmergencyAccess,
    disableEmergencyAccess,
    emergencyAccessEnabled,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

