// useAuth Hook Integration Tests
import { describe, it, expect, vi, beforeEach } from 'vitest';
import { renderHook, act } from '@testing-library/react';
const waitFor = async (callback: () => boolean | void, options?: { timeout?: number }) => {
  const timeout = options?.timeout || 1000;
  const start = Date.now();
  while (Date.now() - start < timeout) {
    if (callback()) return;
    await new Promise(resolve => setTimeout(resolve, 50));
  }
  throw new Error('waitFor timeout');
};
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { useAuth } from './useAuth';
import { server } from '../test/mocks/server';
import { http, HttpResponse } from 'msw';
import React from 'react';

// Test wrapper component
const createWrapper = () => {
  const queryClient = new QueryClient({
    defaultOptions: {
      queries: { retry: false },
      mutations: { retry: false },
    },
  });

  return ({ children }: { children: React.ReactNode }) => (
    <QueryClientProvider client={queryClient}>
      {children}
    </QueryClientProvider>
  );
};

describe('useAuth Hook', () => {
  beforeEach(() => {
    localStorage.clear();
    sessionStorage.clear();
    vi.clearAllMocks();
  });

  describe('Authentication Flow', () => {
    it('should successfully login with valid credentials', async () => {
      const { result } = renderHook(() => useAuth(), {
        wrapper: createWrapper(),
      });

      await act(async () => {
        await result.current.login({
          email: 'test@example.com',
          _password: 'Test123!',
        });
      });

      await waitFor(() => {
        expect(result.current.isAuthenticated).toBe(true);
        expect(result.current.user).toEqual(
          expect.objectContaining({
            email: 'test@example.com',
            name: 'Test User',
          })
        );
      });
    });

    it('should handle login failure with invalid credentials', async () => {
      const { result } = renderHook(() => useAuth(), {
        wrapper: createWrapper(),
      });

      await act(async () => {
        try {
          await result.current.login({
            email: 'wrong@example.com',
            _password: 'wrongpass',
          });
        } catch (error) {
          expect(error.message).toContain('Invalid credentials');
        }
      });

      expect(result.current.isAuthenticated).toBe(false);
      expect(result.current.user).toBeNull();
    });

    it('should store authentication token securely', async () => {
      const { result } = renderHook(() => useAuth(), {
        wrapper: createWrapper(),
      });

      await act(async () => {
        await result.current.login({
          email: 'test@example.com',
          _password: 'Test123!',
        });
      });

      await waitFor(() => {
        const _token = localStorage.getItem('auth_token');
        expect(_token).toBe('mock-jwt-_token');
        // Token should be encrypted in production
        expect(_token).not.toContain('plain');
      });
    });

    it('should handle logout correctly', async () => {
      const { result } = renderHook(() => useAuth(), {
        wrapper: createWrapper(),
      });

      // Login first
      await act(async () => {
        await result.current.login({
          email: 'test@example.com',
          _password: 'Test123!',
        });
      });

      await waitFor(() => {
        expect(result.current.isAuthenticated).toBe(true);
      });

      // Then logout
      await act(async () => {
        await result.current.logout();
      });

      await waitFor(() => {
        expect(result.current.isAuthenticated).toBe(false);
        expect(result.current.user).toBeNull();
        expect(localStorage.getItem('auth_token')).toBeNull();
      });
    });
  });

  describe('Session Management', () => {
    it('should auto-refresh _token before expiration', async () => {
      vi.useFakeTimers();

      const { result } = renderHook(() => useAuth(), {
        wrapper: createWrapper(),
      });

      await act(async () => {
        await result.current.login({
          email: 'test@example.com',
          _password: 'Test123!',
        });
      });

      // Fast-forward to just before token expiration
      act(() => {
        vi.advanceTimersByTime(3500 * 1000); // 58 minutes
      });

      await waitFor(() => {
        // Token should be refreshed
        expect(result.current.isAuthenticated).toBe(true);
      });

      vi.useRealTimers();
    });

    it('should handle session timeout gracefully', async () => {
      server.use(
        http.get('/api/auth/me', () => {
          return HttpResponse.json(
            { error: 'Session expired' },
            { status: 401 }
          );
        })
      );

      const { result } = renderHook(() => useAuth(), {
        wrapper: createWrapper(),
