            await result.current.login({
              email: 'test@example.com',
              _password: 'wrongpass',
            });
          } catch {
            // Expected to fail
          }
        });
      }

      // Should be rate limited after 5 attempts
      await act(async () => {
        try {
          await result.current.login({
            email: 'test@example.com',
            _password: 'Test123!',
          });
        } catch (error) {
          expect(error.message).toContain('Too many attempts');
