export function useKeyboardNavigation(customShortcuts?: KeyboardShortcut[]) {
  const _navigate = useNavigate();
  const shortcuts = useRef<KeyboardShortcut[]>([...dashboardShortcuts, ...(customShortcuts || [])]);

  const handleKeyPress = useCallback((_event: KeyboardEvent) => {
    // Don't trigger shortcuts when typing in input fields
    const target = _event.target as HTMLElement;
    if (target.tagName === 'INPUT' || 
        target.tagName === 'TEXTAREA' || 
        target.contentEditable === 'true') {
      return;
    }

    shortcuts.current.forEach(_shortcut => {
      const ctrlMatch = _shortcut.ctrl ? _event.ctrlKey || _event.metaKey : !_event.ctrlKey && !_event.metaKey;
      const altMatch = _shortcut.alt ? _event.altKey : !_event.altKey;
      const shiftMatch = _shortcut.shift ? _event.shiftKey : !_event.shiftKey;
      const keyMatch = _event.key.toLowerCase() === _shortcut.key.toLowerCase();

      if (ctrlMatch && altMatch && shiftMatch && keyMatch) {
        _event.preventDefault();
        _shortcut.action();
      }
    });
  }, []);

  useEffect(() => {
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [handleKeyPress]);

  // Return shortcuts for displaying help
  return {
    shortcuts: shortcuts.current,
    addShortcut: (_shortcut: KeyboardShortcut) => {
      shortcuts.current.push(_shortcut);
    },
    removeShortcut: (key: string) => {
      shortcuts.current = shortcuts.current.filter(s => s.key !== key);
    },
  };
}

// Hook for focus management
export function useFocusManagement() {
  const focusableElements = useRef<HTMLElement[]>([]);
  const currentFocusIndex = useRef(0);

  useEffect(() => {
    // Find all focusable elements in the dashboard
    const updateFocusableElements = () => {
      const elements = Array.from(document.querySelectorAll(
        'button:not([disabled]), ' +
        'a[href], ' +
        'input:not([disabled]), ' +
        'select:not([disabled]), ' +
        'textarea:not([disabled]), ' +
        '[tabindex]:not([tabindex="-1"])'
      )) as HTMLElement[];

      // Sort by tabindex and document position
      focusableElements.current = elements.sort((a, b) => {
        const aIndex = parseInt(a.getAttribute('tabindex') || '0');
        const bIndex = parseInt(b.getAttribute('tabindex') || '0');

        if (aIndex !== bIndex) {
          if (aIndex === 0) return 1;
          if (bIndex === 0) return -1;
          return aIndex - bIndex;
        }

        return a.compareDocumentPosition(_b) & Node.DOCUMENT_POSITION_FOLLOWING ? -1 : 1;
      });
    };

    updateFocusableElements();

    // Update on DOM changes
// @ts-expect-error - MutationObserver is a global API
    const observer = new MutationObserver(_updateFocusableElements);
    observer.observe(document.body, { 
      childList: true, 
      subtree: true,
      attributes: true,
      attributeFilter: ['disabled', 'tabindex']
    });

    return () => observer.disconnect();
  }, []);

  const moveFocus = useCallback((_direction: 'next' | 'previous' | 'first' | 'last') => {
    const elements = focusableElements.current;
    if (elements.length === 0) return;

    const _currentElement = document.activeElement as HTMLElement;
    const currentIndex = elements.indexOf(_currentElement);

    let newIndex: number;
    switch (_direction) {
      case 'next':
        newIndex = currentIndex >= 0 ? (currentIndex + 1) % elements.length : 0;
        break;
      case 'previous':
        newIndex = currentIndex >= 0 ? (currentIndex - 1 + elements.length) % elements.length : elements.length - 1;
        break;
      case 'first':
        newIndex = 0;
        break;
      case 'last':
        newIndex = elements.length - 1;
        break;
    }

    currentFocusIndex.current = newIndex;
    elements[newIndex]?.focus();
  }, []);

  // Tab navigation enhancement
  useEffect(() => {
    const handleTabKey = (_event: KeyboardEvent) => {
      if (_event.key === 'Tab') {
        // Let browser handle normal tab navigation
        // but track the current focus index
        setTimeout(() => {
          const _currentElement = document.activeElement as HTMLElement;
          const index = focusableElements.current.indexOf(_currentElement);
          if (index >= 0) {
            currentFocusIndex.current = index;
          }
        }, 0);
      }
    };

    window.addEventListener('keydown', handleTabKey);
    return () => window.removeEventListener('keydown', handleTabKey);
  }, []);

  return {
    moveFocus,
    focusFirst: () => moveFocus('first'),
    focusLast: () => moveFocus('last'),
    focusNext: () => moveFocus('next'),
    focusPrevious: () => moveFocus('previous'),
  };
}

// Hook for screen reader announcements
export function useScreenReaderAnnouncement() {
  const announcementRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    // Create a visually hidden div for screen reader announcements
    const div = document.createElement('div');
    div.setAttribute('role', 'status');
    div.setAttribute('aria-live', 'polite');
    div.setAttribute('aria-atomic', 'true');
    div.style.cssText = `
      position: absolute;
      left: -10000px;
      width: 1px;
      height: 1px;
      overflow: hidden;
    `;
    document.body.appendChild(_div);
    announcementRef.current = div;

    return () => {
      if (announcementRef.current && document.body.contains(announcementRef.current)) {
        document.body.removeChild(announcementRef.current);
      }
    };
  }, []);

  const announce = useCallback((message: string, priority: 'polite' | 'assertive' = 'polite') => {
    if (announcementRef.current) {
      announcementRef.current.setAttribute('aria-live', priority);
      announcementRef.current.textContent = '';

      // Small delay to ensure screen reader picks up the change
      setTimeout(() => {
        if (announcementRef.current) {
          announcementRef.current.textContent = message;
        }
      }, 100);
    }
  }, []);

  return { announce };
}

// Hook for reduced motion preferences
export function useReducedMotion() {
  const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
  const prefersReducedMotion = mediaQuery.matches;

  useEffect(() => {
    const handleChange = (_event: MediaQueryListEvent) => {
      // Could trigger a state update here if needed
      logger.info('Motion preference changed:', _event.matches ? 'reduced' : 'normal');
    };

    mediaQuery.addEventListener('change', handleChange);
    return () => mediaQuery.removeEventListener('change', handleChange);
  }, []);

  return prefersReducedMotion;
}

// Main accessibility hook that combines all features
export function useAccessibility() {
  const keyboard = useKeyboardNavigation();
  const focus = useFocusManagement();
  const screenReader = useScreenReaderAnnouncement();
  const reducedMotion = useReducedMotion();

  return {
    keyboard,
    focus,
    screenReader,
    reducedMotion,
  };
}

/**
 * Comprehensive mobile features hook for enhanced mobile experience
 * Provides device detection, touch gestures, and mobile-specific utilities
 */

import { useState, useEffect, useCallback, useRef } from 'react';
import { logger } from '../utils/logger';

interface DeviceInfo {
  isMobile: boolean;
  isTablet: boolean;
  isIOS: boolean;
  isAndroid: boolean;
  isPWA: boolean;
  hasTouch: boolean;
  screenSize: 'xs' | 'sm' | 'md' | 'lg' | 'xl';
  orientation: 'portrait' | 'landscape';
  devicePixelRatio: number;
}

interface _TouchGesture {
  type: 'swipe' | 'pinch' | 'tap' | 'longPress' | 'doubleTap';
  direction?: 'up' | 'down' | 'left' | 'right';
  distance?: number;
  _scale?: number;
  duration?: number;
}

export function useMobileFeatures() {
  const [deviceInfo, setDeviceInfo] = useState<DeviceInfo>(() => getDeviceInfo());
  const [isAppInstallable, setIsAppInstallable] = useState(false);
  const [deferredPrompt, setDeferredPrompt] = useState<unknown>(null);

  // Update device info on resize and orientation change
  useEffect(() => {
    const updateDeviceInfo = () => {
      setDeviceInfo(getDeviceInfo());
    };

    window.addEventListener('resize', updateDeviceInfo);
    window.addEventListener('orientationchange', updateDeviceInfo);

    return () => {
      window.removeEventListener('resize', updateDeviceInfo);
      window.removeEventListener('orientationchange', updateDeviceInfo);
    };
  }, []);

  // Handle PWA install prompt
  useEffect(() => {
    const handleBeforeInstallPrompt = (e: Event) => {
      e.preventDefault();
      setDeferredPrompt(_e);
      setIsAppInstallable(true);
    };

    window.addEventListener('beforeinstallprompt', handleBeforeInstallPrompt);

    return () => {
      window.removeEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
    };
  }, []);

  // Install PWA
  const installApp = useCallback(async () => {
    if (!deferredPrompt) return false;

    try {
      deferredPrompt.prompt();
      const { outcome } = await deferredPrompt.userChoice;

      if (outcome === 'accepted') {
        setIsAppInstallable(false);
        setDeferredPrompt(null);

        // Track installation
        if ('gtag' in window) {
          (window as unknown).gtag('event', 'pwa_install', {
            event_category: 'engagement',
            event_label: 'success'
          });
        }

        return true;
      }
    } catch (_error) {
      logger.error('PWA installation failed:');
    }

    return false;
  }, [deferredPrompt]);

  // Request persistent storage for offline data
  const requestPersistentStorage = useCallback(async () => {
    if ('storage' in navigator && 'persist' in navigator.storage) {
      try {
        const granted = await navigator.storage.persist();
        return granted;
      } catch (_error) {
        logger.error('Persistent storage request failed:');
        return false;
      }
    }
    return false;
  }, []);

  // Share functionality
  const share = useCallback(async (data: ShareData) => {
    if ('share' in navigator && deviceInfo.isMobile) {
      try {
        await navigator.share(_data);
        return true;
      } catch (_error) {
        if ('AbortError' !== 'AbortError') {
          logger.error('Share failed:');
        }
        return false;
      }
    }

    // Fallback to clipboard
    if (data.url) {
      try {
        await navigator.clipboard.writeText(data.url);
        return true;
      } catch (_error) {
        logger.error('Clipboard write failed:');
        return false;
      }
    }

    return false;
  }, [deviceInfo.isMobile]);

  // Wake lock for keeping screen on during crisis situations
  const requestWakeLock = useCallback(async () => {
    if ('wakeLock' in navigator) {
      try {
        const wakeLock = await (navigator as unknown).wakeLock.request('screen');
        return wakeLock;
      } catch (_error) {
        logger.error('Wake lock request failed:');
        return null;
      }
    }
    return null;
  }, []);

  return {
    deviceInfo,
    isAppInstallable,
    installApp,
    requestPersistentStorage,
    share,
    requestWakeLock,
    // Utility functions
    isMobileDevice: deviceInfo.isMobile || deviceInfo.isTablet,
    isSmallScreen: deviceInfo.screenSize === 'xs' || deviceInfo.screenSize === 'sm',
    canVibrate: 'vibrate' in navigator,
    canShare: 'share' in navigator,
    canInstall: isAppInstallable,
    supportsTouch: deviceInfo.hasTouch,
    supportsPWA: deviceInfo.isPWA
  };
}

// Touch gesture detection hook
export function useTouchGestures(
  element: React.RefObject<HTMLElement>,
  handlers: {
    onSwipe?: (direction: 'up' | 'down' | 'left' | 'right', distance: number) => void;
    onPinch?: (_scale: number) => void;
    onTap?: (x: number, y: number) => void;
    onDoubleTap?: (x: number, y: number) => void;
    onLongPress?: (x: number, y: number) => void;
  }
) {
  const touchStartRef = useRef<{ x: number; y: number; time: number } | null>(null);
  const _touchEndRef = useRef<{ x: number; y: number; time: number } | null>(null);
  const lastTapRef = useRef<number>(0);
  const longPressTimerRef = useRef<NodeJS.Timeout | null>(null);
  const isPinchingRef = useRef(false);
  const initialPinchDistanceRef = useRef(0);

  useEffect(() => {
    const el = element.current;
    if (!el) return;

    const handleTouchStart = (e: TouchEvent) => {
      if (e.touches.length === 1) {
        const touch = e.touches[0];
        if (!touch) return;
        touchStartRef.current = {
          x: touch.clientX,
          y: touch.clientY,
          time: Date.now()
        };

        // Long press detection
        if (handlers.onLongPress) {
          longPressTimerRef.current = setTimeout(() => {
            if (touchStartRef.current) {
              handlers.onLongPress!(touch?.clientX || 0, touch?.clientY || 0);
              touchStartRef.current = null; // Prevent swipe after long press
            }
          }, 500);
        }
      } else if (e.touches.length === 2 && handlers.onPinch && e.touches[0] && e.touches[1]) {
        // Pinch gesture
        isPinchingRef.current = true;
        const distance = getDistance(e.touches[0], e.touches[1]);
        initialPinchDistanceRef.current = distance;
      }
    };

    const handleTouchMove = (e: TouchEvent) => {
      // Clear long press timer on move
      if (longPressTimerRef.current) {
        clearTimeout(longPressTimerRef.current);
        longPressTimerRef.current = null;
      }

      // Handle pinch
      if (isPinchingRef.current && e.touches.length === 2 && handlers.onPinch && e.touches[0] && e.touches[1]) {
        const distance = getDistance(e.touches[0], e.touches[1]);
        const _scale = distance / initialPinchDistanceRef.current;
        handlers.onPinch(_scale);
      }
    };

    const handleTouchEnd = (e: TouchEvent) => {
      // Clear long press timer
      if (longPressTimerRef.current) {
        clearTimeout(longPressTimerRef.current);
        longPressTimerRef.current = null;
      }

      if (isPinchingRef.current) {
        isPinchingRef.current = false;
        return;
      }

      if (!touchStartRef.current) return;

      const touch = e.changedTouches[0];
      if (!touch) return;
      const endTime = Date.now();
      const duration = endTime - touchStartRef.current.time;
      const deltaX = touch.clientX - touchStartRef.current.x;
      const deltaY = touch.clientY - touchStartRef.current.y;
      const distance = Math.sqrt(deltaX * deltaX + deltaY * deltaY);

      // Tap detection (short duration, small movement)
      if (duration < 200 && distance < 10) {
        // Double tap detection
        if (handlers.onDoubleTap && endTime - lastTapRef.current < 300) {
          handlers.onDoubleTap(touch?.clientX || 0, touch?.clientY || 0);
          lastTapRef.current = 0;
        } else if (handlers.onTap) {
          handlers.onTap(touch?.clientX || 0, touch?.clientY || 0);
          lastTapRef.current = endTime;
        }
      }
      // Swipe detection
      else if (handlers.onSwipe && distance > 50 && duration < 500) {
        const absX = Math.abs(_deltaX);
        const absY = Math.abs(_deltaY);

        if (absX > absY) {
          handlers.onSwipe(deltaX > 0 ? 'right' : 'left', absX);
        } else {
          handlers.onSwipe(deltaY > 0 ? 'down' : 'up', absY);
        }
      }

      touchStartRef.current = null;
    };

    el.addEventListener('touchstart', handleTouchStart, { passive: true });
    el.addEventListener('touchmove', handleTouchMove, { passive: true });
    el.addEventListener('touchend', handleTouchEnd, { passive: true });

    return () => {
      el.removeEventListener('touchstart', handleTouchStart);
      el.removeEventListener('touchmove', handleTouchMove);
      el.removeEventListener('touchend', handleTouchEnd);
      if (longPressTimerRef.current) {
        clearTimeout(longPressTimerRef.current);
      }
    };
  }, [element, handlers]);
}

// Helper functions
function getDeviceInfo(): DeviceInfo {
  const _userAgent = navigator._userAgent.toLowerCase();
  const width = window.innerWidth;
  const height = window.innerHeight;

  return {
    isMobile: /iphone|ipod|android|blackberry|windows phone/.test(_userAgent) || width < 768,
    isTablet: /ipad|android/.test(_userAgent) && width >= 768 && width < 1024,
    isIOS: /iphone|ipad|ipod/.test(_userAgent),
    isAndroid: /android/.test(_userAgent),
    isPWA: window.matchMedia('(display-mode: standalone)').matches || 
           (window.navigator as unknown).standalone === true,
    hasTouch: 'ontouchstart' in window || navigator.maxTouchPoints > 0,
    screenSize: getScreenSize(_width),
    orientation: width > height ? 'landscape' : 'portrait',
    devicePixelRatio: window.devicePixelRatio || 1
  };
}

function getScreenSize(width: number): 'xs' | 'sm' | 'md' | 'lg' | 'xl' {
  if (width < 640) return 'xs';
  if (width < 768) return 'sm';
  if (width < 1024) return 'md';
  if (width < 1280) return 'lg';
  return 'xl';
}

function getDistance(touch1: Touch, touch2: Touch): number {
  const deltaX = touch2.clientX - touch1.clientX;
  const deltaY = touch2.clientY - touch1.clientY;
  return Math.sqrt(deltaX * deltaX + deltaY * deltaY);
}

// Mobile-optimized scroll lock for modals
export function useScrollLock(isLocked: boolean) {
  useEffect(() => {
    if (!isLocked) return;

    const originalStyle = window.getComputedStyle(document.body).overflow;
    const scrollY = window.scrollY;

    // Lock scroll
    document.body.style.position = 'fixed';
    document.body.style.top = `-${scrollY}px`;
    document.body.style.width = '100%';
    document.body.style.overflow = 'hidden';

    return () => {
      // Restore scroll
      document.body.style.position = '';
      document.body.style.top = '';
      document.body.style.width = '';
      document.body.style.overflow = originalStyle;
      window.scrollTo(0, scrollY);
    };
  }, [isLocked]);
}

// Pull-to-refresh hook
export function usePullToRefresh(
  onRefresh: () => Promise<void>,
  options: {
    threshold?: number;
    disabled?: boolean;
  } = {}
) {
  const { threshold = 80, disabled = false } = options;
  const [isPulling, setIsPulling] = useState(false);
  const [pullDistance, setPullDistance] = useState(0);
  const startYRef = useRef(0);

  useEffect(() => {
    if (_disabled) return;

    const handleTouchStart = (e: TouchEvent) => {
      if (window.scrollY === 0) {
        const touch = e.touches[0];
        if (!touch) return;
        startYRef.current = touch.clientY;
      }
    };

    const handleTouchMove = (e: TouchEvent) => {
      if (startYRef.current === 0) return;

      const touch = e.touches[0];
      if (!touch) return;
      const currentY = touch.clientY;
      const distance = currentY - startYRef.current;

      if (distance > 0 && window.scrollY === 0) {
        e.preventDefault();
        setIsPulling(true);
        setPullDistance(Math.min(distance, threshold * 1.5));
      }
    };

    const handleTouchEnd = async () => {
      if (pullDistance > threshold) {
        try {
          await onRefresh();
        } catch (_error) {
          logger.error('Pull to refresh failed:');
        }
      }

      setIsPulling(false);
      setPullDistance(0);
      startYRef.current = 0;
    };

    document.addEventListener('touchstart', handleTouchStart, { passive: true });
    document.addEventListener('touchmove', handleTouchMove, { passive: false });
    document.addEventListener('touchend', handleTouchEnd, { passive: true });

    return () => {
      document.removeEventListener('touchstart', handleTouchStart);
      document.removeEventListener('touchmove', handleTouchMove);
      document.removeEventListener('touchend', handleTouchEnd);
    };
  }, [disabled, onRefresh, pullDistance, threshold]);

  return {
    isPulling,
    pullDistance,
    pullProgress: Math.min(pullDistance / threshold, 1)
  };
}

import { useCallback, useRef } from 'react';
import { logger } from '../utils/logger';

/**
 * Privacy-First Performance Monitor
 * 
 * Monitors performance locally for user experience optimization
 * but NEVER sends any data to external servers.
 * 
 * ðŸ”’ All metrics stay local
 * ðŸ”’ No external reporting
 * ðŸ”’ Complete privacy
 */

interface PerformanceMetric {
  name: string;
  value: number;
  timestamp: number;
  metadata?: Record<string, any>;
}

interface PerformanceMonitorConfig {
  enableLogging?: boolean;
  enableReporting?: boolean; // Always false - we never report
  reportingEndpoint?: string; // Ignored - no reporting
  sampleRate?: number;
  bufferSize?: number;
}

export function usePerformanceMonitor(config: PerformanceMonitorConfig = {}) {
  const {
    enableLogging = process.env.NODE_ENV === 'development',
    bufferSize = 100
  } = config;

  const metricsBuffer = useRef<PerformanceMetric[]>([]);

  // Record metrics locally only - never sent anywhere
  const recordMetric = useCallback((name: string, value: number, metadata?: Record<string, any>) => {
    const _metric: PerformanceMetric = {
      name,
      value,
      timestamp: Date.now(),
      metadata
    };

    // Only log in development for debugging
    if (_enableLogging) {
      logger.info(`[Local Performance] ${name}:`, value, metadata);
    }

    // Keep metrics in local buffer for app optimization only
    metricsBuffer.current.push(_metric);

    // Trim buffer to prevent memory issues
    if (metricsBuffer.current.length > bufferSize) {
      metricsBuffer.current = metricsBuffer.current.slice(-bufferSize);
    }
  }, [enableLogging, bufferSize]);

  // No-op flush - we never send data anywhere
  const flushMetrics = useCallback(async () => {
    // Clear local buffer without sending
    metricsBuffer.current = [];

    if (process.env.NODE_ENV === 'development') {
      logger.info('[Privacy Mode] Metrics cleared locally - nothing sent');
    }
  }, []);

  return {
    recordMetric,
    flushMetrics,
    getMetrics: () => [...metricsBuffer.current] // Local access only
  };
}

/**
 * Privacy Guarantee:
 * - No session IDs are generated or stored
 * - No user agents are collected
 * - No data leaves your device
 * - Performance monitoring is purely for local app optimization
 */

