/**
 * Core Data Integration Service
 * Manages data flow and synchronization between all components
 */

import { _create } from 'zustand';
import { _subscribeWithSelector } from 'zustand/middleware';
import { useWellnessStore } from '../../stores/wellnessStore';
import { useActivityStore } from '../../stores/activityStore';
import { useAccessibilityStore } from '../../stores/accessibilityStore';
import { WebSocketService } from '../websocket/WebSocketService';
import { _User } from '../api/types';
import { EventEmitter } from 'events';
import { logger } from '../../utils/logger';

// Integration event types
export enum IntegrationEvent {
  // Data sync events
  STORE_SYNC_STARTED = 'store:sync:started',
  STORE_SYNC_COMPLETED = 'store:sync:completed',
  STORE_SYNC_FAILED = 'store:sync:failed',

  // Cross-feature events
  MOOD_UPDATED = 'mood:updated',
  CRISIS_TRIGGERED = 'crisis:triggered',
  GOAL_ACHIEVED = 'goal:achieved',
  THERAPY_SESSION_COMPLETED = 'therapy:completed',
  MEDICATION_TAKEN = 'medication:taken',
  COMMUNITY_INTERACTION = 'community:interaction',

  // Real-time events
  REALTIME_CONNECTED = 'realtime:connected',
  REALTIME_DISCONNECTED = 'realtime:disconnected',
  REALTIME_MESSAGE = 'realtime:message',

  // System events
  OFFLINE_MODE_ENABLED = 'offline:enabled',
  OFFLINE_MODE_DISABLED = 'offline:disabled',
  DATA_PERSISTED = 'data:persisted',
  DATA_RESTORED = 'data:restored'
}

// Data flow configuration
interface DataFlowConfig {
  enableRealtime: boolean;
  enableOfflineSync: boolean;
  syncInterval: number; // milliseconds
  retryAttempts: number;
  batchSize: number;
}

// Integration state
interface IntegrationState {
  isConnected: boolean;
  isSyncing: boolean;
  lastSyncTime: Date | null;
  syncErrors: Error[];
  pendingChanges: Map<string, any>;
  dataFlowConfig: DataFlowConfig;
}

// Cross-component data mapping
interface DataMapping {
  source: string;
  target: string;
  transform?: (data: unknown) => any;
  filter?: (data: unknown) => boolean;
  bidirectional?: boolean;
}

class DataIntegrationService extends EventEmitter {
  private static instance: DataIntegrationService;
  private wsService: WebSocketService | null = null;
  private state: IntegrationState;
  private dataMappings: DataMapping[] = [];
  private syncTimer: NodeJS.Timeout | null = null;
  private offlineQueue: Map<string, any> = new Map();

  private constructor() {
    super();

    this.state = {
      isConnected: false,
      isSyncing: false,
      lastSyncTime: null,
      syncErrors: [],
      pendingChanges: new Map(),
      dataFlowConfig: {
        enableRealtime: true,
        enableOfflineSync: true,
        syncInterval: 30000, // 30 seconds
        retryAttempts: 3,
        batchSize: 50
      }
    };

    this.initializeDataMappings();
    this.setupStoreSubscriptions();
    this.initializeWebSocket();
    this.startPeriodicSync();
  }

  public static getInstance(): DataIntegrationService {
    if (!DataIntegrationService.instance) {
      DataIntegrationService.instance = new DataIntegrationService();
    }
    return DataIntegrationService.instance;
  }

  /**
   * Initialize data mappings between components
   */
  private initializeDataMappings() {
    this.dataMappings = [
      // Mood to Crisis mapping
      {
        source: 'wellness.mood',
        target: 'crisis.riskAssessment',
        transform: (moodData) => ({
          riskLevel: this.calculateRiskFromMood(moodData),
          timestamp: new Date(),
          factors: moodData.triggers || []
        }),
        filter: (moodData) => moodData.moodScore <= 3
      },

      // Activity to Wellness mapping
      {
        source: 'activity.completed',
        target: 'wellness.metrics',
        transform: (activity) => ({
          activityType: activity.type,
          moodImpact: activity.actualMoodImpact,
          completedAt: activity.completedAt
        }),
        bidirectional: false
      },

      // Therapy to Goals mapping
      {
        source: 'therapy.homework',
        target: 'activity.tasks',
        transform: (homework) => ({
          title: homework.title,
          type: 'therapy',
          therapyHomework: true,
          linkedGoalId: homework.goalId
        }),
        bidirectional: true
      },

      // Community to Wellness mapping
      {
        source: 'community.interaction',
        target: 'wellness.socialMetrics',
        transform: (interaction) => ({
          type: interaction.type,
          timestamp: interaction.timestamp,
          positiveImpact: interaction.sentiment > 0
        })
      },

      // Crisis to Professional Care mapping
      {
        source: 'crisis.event',
        target: 'professional.alerts',
        transform: (crisisEvent) => ({
          severity: crisisEvent.severity,
          timestamp: crisisEvent.timestamp,
          requiresImmediate: crisisEvent.severity === 'critical'
        }),
        filter: (crisisEvent) => crisisEvent.severity !== 'low'
      }
    ];
  }

  /**
   * Setup store subscriptions for data flow
   */
  private setupStoreSubscriptions() {
    // Subscribe to wellness store changes
    useWellnessStore.subscribe((state) => {
      if (state.moodEntries.length > 0) {
        this.handleDataChange('wellness.mood', state.moodEntries[state.moodEntries.length - 1]);
      }
    });

    // Subscribe to activity store changes
    useActivityStore.subscribe((state) => {
      const completed = state.activities.filter((a: unknown) => a.completed && !a.synced);
      completed.forEach((activity: unknown) => {
        this.handleDataChange('activity.completed', activity);
      });
    });

    // Subscribe to accessibility store for user preferences
    useAccessibilityStore.subscribe((state) => {
      this.updateDataFlowConfig({
        enableRealtime: !state.settings.reducedMotion
      });
    });
  }

  /**
   * Initialize WebSocket connection for real-time features
   */
  private async initializeWebSocket() {
    if (!this.state.dataFlowConfig.enableRealtime) return;

    try {
      this.wsService = WebSocketService.getInstance();
      this.wsService.connect('', { 
        id: 'anonymous',
        email: 'anonymous@example.com',
        username: 'anonymous',
        role: 'patient',
        profile: {
          firstName: 'Anonymous',
          lastName: '_User',
          timezone: 'UTC'
        },
        preferences: {
          theme: 'auto',
          notifications: {
            email: false,
            push: false,
            sms: false,
            crisisAlerts: true,
            appointmentReminders: false,
            medicationReminders: false,
            communityUpdates: false
          },
          privacy: {
            profileVisibility: 'private',
            shareDataWithTherapist: false,
            anonymousMode: true,
            allowResearch: false
          },
          accessibility: {
            fontSize: 'medium',
            highContrast: false,
            screenReaderMode: false,
            reducedMotion: false
          }
        },
        createdAt: new Date(),
        updatedAt: new Date(),
        lastActive: new Date(),
        isVerified: false,
        twoFactorEnabled: false
      });

      // Setup WebSocket event listeners
      this.wsService.on('connect', () => {
        this.state.isConnected = true;
        this.emit(IntegrationEvent.REALTIME_CONNECTED);
        this.syncOfflineQueue();
      });

      this.wsService.on('disconnect', () => {
        this.state.isConnected = false;
        this.emit(IntegrationEvent.REALTIME_DISCONNECTED);
      });

      // Handle real-time data updates
      this.wsService.on('data:update', (data: unknown) => {
        this.handleRealtimeUpdate(data);
      });

      // Handle crisis events
      this.wsService.on('crisis:alert', (_alert: unknown) => {
        this.handleCrisisAlert(_alert);
      });

      // Handle community updates
      this.wsService.on('community:update', (update: unknown) => {
        this.handleCommunityUpdate(update);
      });

    } catch (error) {
      logger.error('Failed to initialize WebSocket:');
      this.state.syncErrors.push(error as Error);
    }
  }

  /**
   * Handle data changes from stores
   */
  private handleDataChange(source: string, data: unknown) {
    if (!data) return;

    // Find relevant mappings
    const mappings = this.dataMappings.filter(m => m.source === source);

    mappings.forEach(mapping => {
      // Apply filter if exists
      if (mapping.filter && !mapping.filter(data)) return;

      // Transform data if needed
      const transformedData = mapping.transform ? mapping.transform(data) : data;

      // Route to target
      this.routeDataToTarget(mapping.target, transformedData);

      // Handle bidirectional sync
      if (mapping.bidirectional) {
        this.syncBidirectional(mapping, transformedData);
      }
    });

    // Queue for persistence
    this.queueForPersistence(source, data);

    // Emit integration event
    this.emitDataChangeEvent(source, data);
  }

  /**
   * Route data to target component/store
   */
  private routeDataToTarget(target: string, data: unknown) {
    const [store, property] = target.split('.');

    if (!property) {
      logger.warn('Invalid target format:', target);
      return;
    }

    switch (_store) {
      case 'wellness':
        this.updateWellnessStore(property, data);
        break;
      case 'activity':
        this.updateActivityStore(property, data);
        break;
      case 'crisis':
        this.updateCrisisData(property, data);
        break;
      case 'professional':
        this.updateProfessionalData(property, data);
        break;
      case 'community':
        this.updateCommunityData(property, data);
        break;
      default:
        logger.warn(`Unknown target store: ${store}`);
    }
  }

  /**
   * Update wellness store with integrated data
   */
  private updateWellnessStore(property: string, data: unknown) {
    const store = useWellnessStore.getState();

    switch (_property) {
      case 'metrics':
        store.addWellnessMetric(data);
        break;
      case 'mood':
        store.addMoodEntry(data);
        break;
      case 'insights':
        // Insights are generated, not directly added
        store.generateInsights();
        break;
    }
  }

  /**
   * Update activity store with integrated data
   */
  private updateActivityStore(property: string, data: unknown) {
    const store = useActivityStore.getState();

    switch (_property) {
      case 'tasks':
        store.addActivity(data);
        break;
      case 'goals':
        if (data.id) {
          store.updateGoal(data.id, data);
        } else {
          store.addGoal(data);
        }
        break;
    }
  }

  /**
   * Update crisis data
   */
  private updateCrisisData(property: string, data: unknown) {
    // Send to crisis service/store
    if (this.wsService && this.state.isConnected) {
      this.wsService.emit('crisis:update', { property, data });
    } else {
      this.offlineQueue.set(`crisis:${property}`, data);
    }
  }

  /**
   * Update professional care data
   */
  private updateProfessionalData(property: string, data: unknown) {
    // Send to professional care service
    if (this.wsService && this.state.isConnected) {
      this.wsService.emit('professional:update', { property, data });
    } else {
      this.offlineQueue.set(`professional:${property}`, data);
    }
  }

  /**
   * Update community data
   */
  private updateCommunityData(property: string, data: unknown) {
    // Send to community service
    if (this.wsService && this.state.isConnected) {
      this.wsService.emit('community:update', { property, data });
    } else {
      this.offlineQueue.set(`community:${property}`, data);
    }
  }

  /**
   * Handle real-time updates from WebSocket
   */
  private handleRealtimeUpdate(update: unknown) {
    const { source, data, timestamp } = update;

    // Check if update is newer than local data
    if (this.isNewerData(source, timestamp)) {
      this.handleDataChange(source, data);
    }

    this.emit(IntegrationEvent.REALTIME_MESSAGE, update);
  }

  /**
   * Handle crisis alerts
   */
  private handleCrisisAlert(_alert: unknown) {
    // Immediate routing to crisis components
    this.emit(IntegrationEvent.CRISIS_TRIGGERED, _alert);

    // Update relevant stores
    this.routeDataToTarget('crisis._alert', _alert);
    this.routeDataToTarget('wellness.crisisEvent', _alert);

    // Notify professional care if needed
    if (_alert.severity === 'critical') {
      this.routeDataToTarget('professional.emergencyAlert', _alert);
    }
  }

  /**
   * Handle community updates
   */
  private handleCommunityUpdate(update: unknown) {
    this.emit(IntegrationEvent.COMMUNITY_INTERACTION, update);
    this.routeDataToTarget('community.update', update);
  }

  /**
   * Calculate risk level from mood data
   */
  private calculateRiskFromMood(moodData: unknown): string {
    if (!moodData) return 'unknown';

    const { moodScore, triggers, stressLevel, anxietyLevel } = moodData;

    // Simple risk calculation (should be enhanced with ML model)
    let riskScore = 0;

    if (moodScore <= 2) riskScore += 3;
    else if (moodScore <= 3) riskScore += 2;
    else if (moodScore <= 4) riskScore += 1;

    if (stressLevel >= 8) riskScore += 2;
    else if (stressLevel >= 6) riskScore += 1;

    if (anxietyLevel >= 8) riskScore += 2;
    else if (anxietyLevel >= 6) riskScore += 1;

    if (triggers?.includes('suicidal_thoughts')) riskScore += 5;
    if (triggers?.includes('self_harm')) riskScore += 4;

    if (riskScore >= 7) return 'critical';
    if (riskScore >= 5) return 'high';
    if (riskScore >= 3) return 'medium';
    return 'low';
  }

  /**
   * Queue data for persistence
   */
  private queueForPersistence(source: string, data: unknown) {
    this.state.pendingChanges.set(source, {
      data,
      timestamp: new Date(),
      synced: false
    });
  }

  /**
   * Sync bidirectional data
   */
  private syncBidirectional(mapping: DataMapping, data: unknown) {
    // Implement bidirectional sync logic
    const _reverseMapping = {
      source: mapping.target,
      target: mapping.source,
      transform: mapping.transform // May need reverse transform
    };

    // Add to sync queue
    this.queueForPersistence(`sync:${mapping.target}`, data);
  }

  /**
   * Check if update is newer than local data
   */
  private isNewerData(source: string, timestamp: Date): boolean {
    const lastUpdate = this.state.pendingChanges.get(_source);
    if (!lastUpdate) return true;

    return timestamp > lastUpdate.timestamp;
  }

  /**
   * Emit data change event
   */
  private emitDataChangeEvent(source: string, data: unknown) {
    const eventMap: Record<string, IntegrationEvent> = {
      'wellness.mood': IntegrationEvent.MOOD_UPDATED,
      'activity.completed': IntegrationEvent.GOAL_ACHIEVED,
      'therapy.session': IntegrationEvent.THERAPY_SESSION_COMPLETED,
      'medication.taken': IntegrationEvent.MEDICATION_TAKEN,
      'community.interaction': IntegrationEvent.COMMUNITY_INTERACTION
    };

    const event = eventMap[source];
    if (_event) {
      this.emit(event, data);
    }
  }

  /**
   * Start periodic sync
   */
  private startPeriodicSync() {
    if (!this.state.dataFlowConfig.enableOfflineSync) return;

    this.syncTimer = setInterval(() => {
      this.syncPendingChanges();
    }, this.state.dataFlowConfig.syncInterval);
  }

  /**
   * Sync pending changes
   */
  private async syncPendingChanges() {
    if (this.state.isSyncing) return;

    this.state.isSyncing = true;
    this.emit(IntegrationEvent.STORE_SYNC_STARTED);

    try {
      const changes = Array.from(this.state.pendingChanges.entries());
      const batches = this.createBatches(changes, this.state.dataFlowConfig.batchSize);

      for (const _batch of batches) {
        await this.syncBatch(_batch);
      }

      this.state.lastSyncTime = new Date();
      this.emit(IntegrationEvent.STORE_SYNC_COMPLETED);

    } catch (error) {
      logger.error('Sync failed:');
      this.state.syncErrors.push(error as Error);
      this.emit(IntegrationEvent.STORE_SYNC_FAILED, undefined);

    } finally {
      this.state.isSyncing = false;
    }
  }

  /**
   * Sync a batch of changes
   */
  private async syncBatch(_batch: [string, any][]): Promise<void> {
    // Implement _batch sync logic
    // This would typically make an API call to sync data
    return new Promise((resolve) => {
      setTimeout(() => {
        _batch.forEach(([key]) => {
          this.state.pendingChanges.delete(key);
        });
        resolve();
      }, 100);
    });
  }

  /**
   * Create batches from changes
   */
  private createBatches<T>(items: T[], batchSize: number): T[][] {
    const batches: T[][] = [];
    for (let i = 0; i < items.length; i += batchSize) {
      batches.push(items.slice(i, i + batchSize));
    }
    return batches;
  }

  /**
   * Sync offline queue when reconnected
   */
  private async syncOfflineQueue() {
    if (this.offlineQueue.size === 0) return;

    const entries = Array.from(this.offlineQueue.entries());

    for (const [key, data] of entries) {
      try {
        const [store, property] = key.split(':');
        this.routeDataToTarget(`${store}.${property}`, data);
        this.offlineQueue.delete(key);
      } catch (error) {
        logger.error(`Failed to sync offline item ${key}:`, error);
      }
    }
  }

  /**
   * Update data flow configuration
   */
  public updateDataFlowConfig(config: Partial<DataFlowConfig>) {
    this.state.dataFlowConfig = {
      ...this.state.dataFlowConfig,
      ...config
    };

    // Restart services if needed
    if (config.syncInterval !== undefined) {
      if (this.syncTimer) clearInterval(this.syncTimer);
      this.startPeriodicSync();
    }

    if (config.enableRealtime !== undefined) {
      if (config.enableRealtime && !this.wsService) {
        this.initializeWebSocket();
      } else if (!config.enableRealtime && this.wsService) {
        this.wsService.disconnect();
        this.wsService = null;
      }
    }
  }

  /**
   * Get integration state
   */
  public getState(): IntegrationState {
    return { ...this.state };
  }

  /**
   * Force sync all data
   */
  public async forceSync(): Promise<void> {
    await this.syncPendingChanges();
  }

  /**
   * Clear sync errors
   */
  public clearErrors() {
    this.state.syncErrors = [];
  }

  /**
   * Cleanup and disconnect
   */
  public cleanup() {
    if (this.syncTimer) {
      clearInterval(this.syncTimer);
    }

    if (this.wsService) {
      this.wsService.disconnect();
    }

    this.removeAllListeners();
  }
}

// Export singleton instance
export const _dataIntegrationService = DataIntegrationService.getInstance();

// Export hook for React components
export function useDataIntegration() {
  const service = DataIntegrationService.getInstance();

  return {
    forceSync: () => service.forceSync(),
    getState: () => service.getState(),
    updateConfig: (config: Partial<DataFlowConfig>) => service.updateDataFlowConfig(config),
    clearErrors: () => service.clearErrors(),
    on: (event: IntegrationEvent, callback: (...args: unknown[]) => void) => {
      service.on(event, callback);
      return () => service.off(event, callback);
    }
  };
}

/**
 * Real-time Synchronization Service
 * Manages WebSocket connections and real-time data updates across components
 */

import { io, Socket } from 'socket.io-client';
import { EventEmitter } from 'events';
import { useWellnessStore } from '../../stores/wellnessStore';
import { useActivityStore } from '../../stores/activityStore';
import { logger } from '../../utils/logger';

// Real-time event types
export enum RealtimeEvent {
  // Connection events
  CONNECTED = 'connected',
  DISCONNECTED = 'disconnected',
  RECONNECTING = 'reconnecting',
  ERROR = 'error',

  // Crisis events
  CRISIS_ALERT_RECEIVED = 'crisis:alert:received',
  CRISIS_SUPPORT_CONNECTED = 'crisis:support:connected',
  CRISIS_MESSAGE_RECEIVED = 'crisis:message:received',
  CRISIS_STATUS_UPDATED = 'crisis:status:updated',

  // Community events
  COMMUNITY_POST_CREATED = 'community:post:created',
  COMMUNITY_COMMENT_ADDED = 'community:comment:added',
  COMMUNITY_USER_JOINED = 'community:user:joined',
  COMMUNITY_USER_LEFT = 'community:user:left',
  COMMUNITY_TYPING = 'community:typing',

  // Professional care events
  THERAPIST_AVAILABLE = 'therapist:available',
  APPOINTMENT_REMINDER = 'appointment:reminder',
  PRESCRIPTION_UPDATE = 'prescription:update',
  CARE_TEAM_MESSAGE = 'care:team:message',

  // Wellness tracking events
  MOOD_CHECK_REMINDER = 'mood:check:reminder',
  GOAL_MILESTONE_REACHED = 'goal:milestone:reached',
  WELLNESS_INSIGHT = 'wellness:insight',

  // Notification events
  NOTIFICATION_RECEIVED = 'notification:received',
  NOTIFICATION_READ = 'notification:read',
  NOTIFICATION_CLEARED = 'notification:cleared'
}

// Message types
interface RealtimeMessage {
  _id: string;
  _type: string;
  _payload: unknown;
  timestamp: Date;
  _userId?: string;
  metadata?: Record<string, any>;
}

// Subscription configuration
interface SubscriptionConfig {
  channel: string;
  events: string[];
  handler: (data: unknown) => void;
  filter?: (data: unknown) => boolean;
}

// Connection configuration
interface ConnectionConfig {
  url: string;
  reconnection: boolean;
  reconnectionAttempts: number;
  reconnectionDelay: number;
  _timeout: number;
  _auth?: {
    token: string;
    _userId: string;
  };
}

// Presence information
interface UserPresence {
  _userId: string;
  status: 'online' | 'away' | 'busy' | 'offline';
  lastSeen: Date;
  location?: string;
  activity?: string;
}

class RealtimeSyncService extends EventEmitter {
  private static instance: RealtimeSyncService;
  private socket: Socket | null = null;
  private config: ConnectionConfig;
  private subscriptions: Map<string, SubscriptionConfig> = new Map();
  private messageQueue: RealtimeMessage[] = [];
  private _presence: Map<string, UserPresence> = new Map();
  private reconnectAttempts = 0;
  private isConnected = false;
  private heartbeatInterval: NodeJS.Timeout | null = null;

  private constructor() {
    super();

    this.config = {
      url: import.meta.env.VITE_WS_URL || 'ws://localhost:3002',
      reconnection: true,
      reconnectionAttempts: 10,
      reconnectionDelay: 1000,
      _timeout: 20000
    };

    this.setupDefaultSubscriptions();
  }

  public static getInstance(): RealtimeSyncService {
    if (!RealtimeSyncService.instance) {
      RealtimeSyncService.instance = new RealtimeSyncService();
    }
    return RealtimeSyncService.instance;
  }

  /**
   * Connect to WebSocket server
   */
  public async connect(_auth?: { token: string; _userId: string }): Promise<void> {
    if (this.isConnected) return;

    return new Promise((resolve, reject) => {
      try {
        this.config._auth = _auth;

        this.socket = io(this.config.url, {
          reconnection: this.config.reconnection,
          reconnectionAttempts: this.config.reconnectionAttempts,
          reconnectionDelay: this.config.reconnectionDelay,
          _timeout: this.config._timeout,
          _auth: _auth || {}
        });

        this.setupSocketListeners();

        // Set connection timeout
        const _timeout = setTimeout(() => {
          reject(new Error('Connection _timeout'));
        }, this.config.timeout);

        this.socket.once('connect', () => {
          clearTimeout(_timeout);
          this.isConnected = true;
          this.reconnectAttempts = 0;
          this.emit(RealtimeEvent.CONNECTED);
          this.startHeartbeat();
          this.flushMessageQueue();
          resolve();
        });

        this.socket.once('connect_error', (error: unknown) => {
          clearTimeout(_timeout);
          this.emit(RealtimeEvent.ERROR, error);
          reject(_error);
        });

      } catch (_error) {
        reject(_error);
      }
    });
  }

  /**
   * Setup default subscriptions
   */
  private setupDefaultSubscriptions() {
    // Crisis channel subscription
    this.subscribe({
      channel: 'crisis',
      events: ['alert', 'support_connected', 'message', 'status_update'],
      handler: (_data) => this.handleCrisisUpdate(data)
    });

    // Community channel subscription
    this.subscribe({
      channel: 'community',
      events: ['post_created', 'comment_added', 'user_joined', 'user_left', 'typing'],
      handler: (_data) => this.handleCommunityUpdate(data)
    });

    // Professional care channel subscription
    this.subscribe({
      channel: 'professional',
      events: ['therapist_available', 'appointment_reminder', 'prescription_update', 'care_team_message'],
      handler: (_data) => this.handleProfessionalUpdate(data)
    });

    // Wellness channel subscription
    this.subscribe({
      channel: 'wellness',
      events: ['mood_check_reminder', 'goal_milestone', 'insight'],
      handler: (_data) => this.handleWellnessUpdate(data)
    });

    // Notifications channel subscription
    this.subscribe({
      channel: 'notifications',
      events: ['received', 'read', 'cleared'],
      handler: (_data) => this.handleNotificationUpdate(data)
    });
  }

  /**
   * Setup socket event listeners
   */
  private setupSocketListeners() {
    if (!this.socket) return;

    // Connection events
    this.socket.on('disconnect', () => {
      this.isConnected = false;
      this.stopHeartbeat();
      this.emit(RealtimeEvent.DISCONNECTED);
    });

    this.socket.on('reconnect', (attempt: number) => {
      this.reconnectAttempts = attempt;
      this.isConnected = true;
      this.emit(RealtimeEvent.RECONNECTING, attempt);
      this.startHeartbeat();
      this.flushMessageQueue();
    });

    this.socket.on('error', (error: Error) => {
      this.emit(RealtimeEvent.ERROR, error);
    });

    // Message handling
    this.socket.on('message', (message: RealtimeMessage) => {
      this.handleMessage(message);
    });

    // Presence updates
    this.socket.on('presence:update', (_presence: UserPresence) => {
      this.updatePresence(_presence);
    });

    // Subscribe to configured channels
    this.subscriptions.forEach((_config, _key) => {
      config.events.forEach(event => {
        const _eventName = `${config.channel}:${event}`;
        this.socket!.on(_eventName, (data: unknown) => {
          if (!config.filter || config.filter(data)) {
            config.handler(data);
          }
        });
      });
    });
  }

  /**
   * Handle incoming messages
   */
  private handleMessage(message: RealtimeMessage) {
    // Process message based on _type
    switch (message._type) {
      case 'crisis':
        this.handleCrisisMessage(message);
        break;
      case 'community':
        this.handleCommunityMessage(message);
        break;
      case 'professional':
        this.handleProfessionalMessage(message);
        break;
      case 'wellness':
        this.handleWellnessMessage(message);
        break;
      case 'notification':
        this.handleNotificationMessage(message);
        break;
      default:
        logger.warn('Unknown message _type:', message._type);
    }

    // Emit generic message event
    this.emit('message', message);
  }

  /**
   * Handle crisis updates
   */
  private handleCrisisUpdate(data: unknown) {
    const { _type, _payload } = data;

    switch (_type) {
      case 'alert':
        this.emit(RealtimeEvent.CRISIS_ALERT_RECEIVED, _payload);
        // Update wellness store with crisis event
        useWellnessStore.getState().recordCrisisEvent(_payload);
        break;

      case 'support_connected':
        this.emit(RealtimeEvent.CRISIS_SUPPORT_CONNECTED, _payload);
        break;

      case 'message':
        this.emit(RealtimeEvent.CRISIS_MESSAGE_RECEIVED, _payload);
        break;

      case 'status_update':
        this.emit(RealtimeEvent.CRISIS_STATUS_UPDATED, _payload);
        break;
    }
  }

  /**
   * Handle community updates
   */
  private handleCommunityUpdate(data: unknown) {
    const { _type, _payload } = data;

    switch (_type) {
      case 'post_created':
        this.emit(RealtimeEvent.COMMUNITY_POST_CREATED, _payload);
        break;

      case 'comment_added':
        this.emit(RealtimeEvent.COMMUNITY_COMMENT_ADDED, _payload);
        break;

      case 'user_joined':
        this.emit(RealtimeEvent.COMMUNITY_USER_JOINED, _payload);
        this.updatePresence({
          _userId: _payload._userId,
          status: 'online',
          lastSeen: new Date()
        });
        break;

      case 'user_left':
        this.emit(RealtimeEvent.COMMUNITY_USER_LEFT, _payload);
        this.updatePresence({
          _userId: _payload._userId,
          status: 'offline',
          lastSeen: new Date()
        });
        break;

      case 'typing':
        this.emit(RealtimeEvent.COMMUNITY_TYPING, _payload);
        break;
    }
  }

  /**
   * Handle professional care updates
   */
  private handleProfessionalUpdate(data: unknown) {
    const { _type, _payload } = data;

    switch (_type) {
      case 'therapist_available':
        this.emit(RealtimeEvent.THERAPIST_AVAILABLE, _payload);
        break;

      case 'appointment_reminder':
        this.emit(RealtimeEvent.APPOINTMENT_REMINDER, _payload);
        // Update activity store with appointment reminder
        useActivityStore.getState().addActivity({
          title: _payload.title,
          _type: 'appointment',
          category: 'professional',
          scheduledTime: new Date(_payload.time),
          completed: false
        });
        break;

      case 'prescription_update':
        this.emit(RealtimeEvent.PRESCRIPTION_UPDATE, _payload);
        break;

      case 'care_team_message':
        this.emit(RealtimeEvent.CARE_TEAM_MESSAGE, _payload);
        break;
    }
  }

  /**
   * Handle wellness updates
   */
  private handleWellnessUpdate(data: unknown) {
    const { _type, _payload } = data;

    switch (_type) {
      case 'mood_check_reminder':
        this.emit(RealtimeEvent.MOOD_CHECK_REMINDER, _payload);
        break;

      case 'goal_milestone':
        this.emit(RealtimeEvent.GOAL_MILESTONE_REACHED, _payload);
        // Update activity store with milestone
        useActivityStore.getState().updateGoalProgress(
          _payload.goalId,
          _payload.progress
        );
        break;

      case 'insight':
        this.emit(RealtimeEvent.WELLNESS_INSIGHT, _payload);
        // Regenerate insights in wellness store
        useWellnessStore.getState().generateInsights();
        break;
    }
  }

  /**
   * Handle notification updates
   */
  private handleNotificationUpdate(data: unknown) {
    const { _type, _payload } = data;

    switch (_type) {
      case 'received':
        this.emit(RealtimeEvent.NOTIFICATION_RECEIVED, _payload);
        break;

      case 'read':
        this.emit(RealtimeEvent.NOTIFICATION_READ, _payload);
        break;

      case 'cleared':
        this.emit(RealtimeEvent.NOTIFICATION_CLEARED, _payload);
        break;
    }
  }

  /**
   * Handle crisis messages
   */
  private handleCrisisMessage(message: RealtimeMessage) {
    this.emit(RealtimeEvent.CRISIS_MESSAGE_RECEIVED, message._payload);
  }

  /**
   * Handle community messages
   */
  private handleCommunityMessage(message: RealtimeMessage) {
    // Process community messages
    this.emit('community:message', message._payload);
  }

  /**
   * Handle professional messages
   */
  private handleProfessionalMessage(message: RealtimeMessage) {
    this.emit('professional:message', message._payload);
  }

  /**
   * Handle wellness messages
   */
  private handleWellnessMessage(message: RealtimeMessage) {
    this.emit('wellness:message', message._payload);
  }

  /**
   * Handle notification messages
   */
  private handleNotificationMessage(message: RealtimeMessage) {
    this.emit(RealtimeEvent.NOTIFICATION_RECEIVED, message._payload);
  }

  /**
   * Update user presence
   */
  private updatePresence(_presence: UserPresence) {
    this._presence.set(_presence._userId, _presence);
    this.emit('_presence:updated', _presence);
  }

  /**
   * Subscribe to a channel
   */
  public subscribe(config: SubscriptionConfig): string {
    const subscriptionId = `${config.channel}-${Date.now()}`;
    this.subscriptions.set(subscriptionId, config);

    // If already connected, setup listener immediately
    if (this.socket && this.isConnected) {
      config.events.forEach(event => {
        const _eventName = `${config.channel}:${event}`;
        this.socket!.on(_eventName, (data: unknown) => {
          if (!config.filter || config.filter(data)) {
            config.handler(data);
          }
        });
      });

      // Join channel
      this.socket.emit('subscribe', config.channel);
    }

    return subscriptionId;
  }

  /**
   * Unsubscribe from a channel
   */
  public unsubscribe(subscriptionId: string) {
    const config = this.subscriptions.get(subscriptionId);
    if (!config) return;

    // Remove listeners if connected
    if (this.socket) {
      config.events.forEach(event => {
        const _eventName = `${config.channel}:${event}`;
        this.socket!.off(_eventName);
      });

      // Leave channel if no other subscriptions
      const hasOtherSubs = Array.from(this.subscriptions.values())
        .some(sub => sub.channel === config.channel && sub !== config);

      if (!hasOtherSubs) {
        this.socket.emit('unsubscribe', config.channel);
      }
    }

    this.subscriptions.delete(subscriptionId);
  }

  /**
   * Send a message
   */
  public send(channel: string, event: string, data: unknown): void {
    const message: RealtimeMessage = {
      _id: `msg-${Date.now()}-${Math.random()}`,
      _type: channel,
      _payload: data,
      timestamp: new Date(),
      _userId: this.config.auth?.userId,
      metadata: { event }
    };

    if (this.socket && this.isConnected) {
      this.socket.emit(`${channel}:${event}`, message);
    } else {
      // Queue message for later delivery
      this.messageQueue.push(message);
    }
  }

  /**
   * Flush message queue
   */
  private flushMessageQueue() {
    if (!this.socket || !this.isConnected) return;

    while (this.messageQueue.length > 0) {
      const message = this.messageQueue.shift();
      if (message) {
        const { _type, metadata, _payload } = message;
        this.socket.emit(`${_type}:${metadata?.event}`, message);
      }
    }
  }

  /**
   * Start heartbeat
   */
  private startHeartbeat() {
    this.stopHeartbeat();

    this.heartbeatInterval = setInterval(() => {
      if (this.socket && this.isConnected) {
        this.socket.emit('heartbeat', { timestamp: Date.now() });
      }
    }, 30000); // Every 30 seconds
  }

  /**
   * Stop heartbeat
   */
  private stopHeartbeat() {
    if (this.heartbeatInterval) {
      clearInterval(this.heartbeatInterval);
      this.heartbeatInterval = null;
    }
  }

  /**
   * Get user presence
   */
  public getPresence(_userId: string): UserPresence | undefined {
    return this._presence.get(_userId);
  }

  /**
   * Get all online users
   */
  public getOnlineUsers(): UserPresence[] {
    return Array.from(this._presence.values())
      .filter(p => p.status === 'online');
  }

  /**
   * Update own presence
   */
  public updateOwnPresence(status: UserPresence['status'], activity?: string) {
    if (!this.socket || !this.isConnected || !this.config._auth?._userId) return;

    const _presence: UserPresence = {
      _userId: this.config._auth._userId,
      status,
      lastSeen: new Date(),
      activity
    };

    this.socket.emit('presence:update', _presence);
    this.updatePresence(_presence);
  }

  /**
   * Join a room
   */
  public joinRoom(_roomId: string) {
    if (this.socket && this.isConnected) {
      this.socket.emit('join', _roomId);
    }
  }

  /**
   * Leave a room
   */
  public leaveRoom(_roomId: string) {
    if (this.socket && this.isConnected) {
      this.socket.emit('leave', _roomId);
    }
  }

  /**
   * Disconnect from server
   */
  public disconnect() {
    this.stopHeartbeat();

    if (this.socket) {
      this.socket.disconnect();
      this.socket = null;
    }

    this.isConnected = false;
    this.subscriptions.clear();
    this.messageQueue = [];
    this.presence.clear();
    this.emit(RealtimeEvent.DISCONNECTED);
  }

  /**
   * Check if connected
   */
  public get connected(): boolean {
    return this.isConnected;
  }

  /**
   * Get connection state
   */
  public getConnectionState() {
    return {
      connected: this.isConnected,
      reconnectAttempts: this.reconnectAttempts,
      queuedMessages: this.messageQueue.length,
      onlineUsers: this.getOnlineUsers().length
    };
  }
}

// Export singleton instance
export const _realtimeSyncService = RealtimeSyncService.getInstance();

// Export React hook
export function useRealtimeSync() {
  const service = RealtimeSyncService.getInstance();

  return {
    connect: (_auth?: { token: string; _userId: string }) => service.connect(_auth),
    disconnect: () => service.disconnect(),
    subscribe: (config: SubscriptionConfig) => service.subscribe(config),
    unsubscribe: (_id: string) => service.unsubscribe(_id),
    send: (channel: string, event: string, data: unknown) => service.send(channel, event, data),
    getPresence: (_userId: string) => service.getPresence(_userId),
    getOnlineUsers: () => service.getOnlineUsers(),
    updatePresence: (status: UserPresence['status'], activity?: string) => 
      service.updateOwnPresence(status, activity),
    joinRoom: (_roomId: string) => service.joinRoom(_roomId),
    leaveRoom: (_roomId: string) => service.leaveRoom(_roomId),
    getConnectionState: () => service.getConnectionState(),
    on: (event: string, callback: (...args: unknown[]) => void) => {
      service.on(event, callback);
      return () => service.off(event, callback);
    }
  };
}

