/**
 * Secure LocalStorage Wrapper
 * 
 * SECURITY FIX: Encrypts all sensitive data before storing in localStorage
 * Prevents XSS attacks from accessing plain text mental health data
 */

// @ts-expect-error - CryptoJS types compatibility
import CryptoJS from 'crypto-js';
import { logger } from '../../utils/logger';

// Sensitive data types that must be encrypted
const SENSITIVE_DATA_KEYS = [
  'mood_data',
  'wellnessData',
  'journalEntries',
  'journalDraft',
  'crisis_sessions',
  'safety_plan',
  'safetyPlan',
  'emergencyContacts',
  'crisis_assessment',
  'last_crisis_assessment',
  'patient_data',
  'user_data',
  'current_user',
  'access_token',
  'refresh_token',
  'auth_token',
  'sessionId',
  'userId',
  'anonymous_user',
  'critical_events',
  'corev4_critical_errors'
];

class SecureLocalStorage {
  private encryptionKey: string;
  private isInitialized: boolean = false;

  constructor() {
    try {
      // Get encryption key from environment (secure approach)
      this.encryptionKey = import.meta.env.VITE_ENCRYPTION_KEY || this.generateTempKey();
      this.isInitialized = true;
    } catch (error) {
      logger.error('ðŸ”’ SecureLocalStorage initialization failed:', error);
      // Fallback to a simple key to prevent app crashes
      this.encryptionKey = `fallback_emergency_key_${  Date.now()}`;
      this.isInitialized = false;
    }
  }

  /**
   * Generate temporary encryption key (development only)
   * WARNING: This key will not persist between sessions
   */
  private generateTempKey(): string {
    try {
      // Try CryptoJS first
      // @ts-expect-error - CryptoJS lib property access
      const tempKey = CryptoJS.lib.WordArray.random(256/8).toString();
      logger.warn('âš ï¸ Using temporary encryption key. Set VITE_ENCRYPTION_KEY for production.');
      return tempKey;
    } catch {
      // Fallback to browser crypto API or manual generation
      logger.warn('âš ï¸ CryptoJS random failed, using fallback method');

      if (typeof crypto !== 'undefined' && crypto.getRandomValues) {
        // Use browser's crypto API
        const array = new Uint8Array(32);
        crypto.getRandomValues(array);
        const tempKey = Array.from(array, byte => byte.toString(16).padStart(2, '0')).join('');
        logger.warn('âš ï¸ Using browser crypto for temporary key. Set VITE_ENCRYPTION_KEY for production.');
        return tempKey;
      } else {
        // Final fallback - deterministic but unique per session
        const timestamp = Date.now().toString(36);
        const random = Math.random().toString(36).substring(2);
        const tempKey = `fallback_${timestamp}_${random}`;
        logger.warn('âš ï¸ Using fallback key generation. Set VITE_ENCRYPTION_KEY for production.');
        return tempKey;
      }
    }
  }

  /**
   * Check if a key contains sensitive data that should be encrypted
   */
  private isSensitiveData(key: string): boolean {
    return SENSITIVE_DATA_KEYS.some(sensitiveKey => 
      key.includes(sensitiveKey) || key.toLowerCase().includes(sensitiveKey.toLowerCase())
    );
  }

  /**
   * Encrypt sensitive data
   */
  private encrypt(data: string): string {
    try {
      // Check if CryptoJS is properly initialized
      if (!CryptoJS || !CryptoJS.AES || !this.isInitialized) {
        logger.warn('ðŸ”’ CryptoJS not properly initialized, storing data unencrypted');
        return data; // Store unencrypted but warn about it
      }

      // @ts-expect-error - CryptoJS method access
      const encrypted = CryptoJS.AES.encrypt(data, this.encryptionKey, {
        // @ts-expect-error - CryptoJS mode property access
        mode: CryptoJS.mode.CBC,
        // @ts-expect-error - CryptoJS padding property access
        padding: CryptoJS.pad.Pkcs7
      });
      return encrypted.toString();
    } catch (error) {
      logger.error('ðŸ”’ Encryption failed:', error);
      logger.warn('ðŸ”’ Storing data unencrypted due to encryption failure');
      return data; // Fallback to unencrypted to prevent app crashes
    }
  }

  /**
   * Decrypt sensitive data
   */
  private decrypt(encryptedData: string): string {
    try {
      // Validate input data
      if (!encryptedData || typeof encryptedData !== 'string') {
        logger.warn('ðŸ”’ Invalid encrypted data format, clearing...');
        return '';
      }

      // Check if CryptoJS is properly initialized
      if (!CryptoJS || !CryptoJS.AES || !this.isInitialized) {
        logger.warn('ðŸ”’ CryptoJS not properly initialized, returning data as-is');
        return encryptedData; // Return unencrypted data
      }

      // @ts-expect-error - CryptoJS method access
      const decrypted = CryptoJS.AES.decrypt(encryptedData, this.encryptionKey, {
        // @ts-expect-error - CryptoJS mode property access
        mode: CryptoJS.mode.CBC,
        // @ts-expect-error - CryptoJS padding property access
        padding: CryptoJS.pad.Pkcs7
      });

      // @ts-expect-error - CryptoJS encoding access
      const result = decrypted.toString(CryptoJS.enc.Utf8);

      if (!result) {
        logger.warn('ðŸ”’ Corrupted data detected, returning original...');
        return encryptedData; // Return original data instead of empty string
      }

      return result;
    } catch (error) {
      logger.error('ðŸ”’ Decryption failed:', error);
      // Return original data instead of throwing to prevent app crashes
      logger.warn('ðŸ”’ Returning data unencrypted due to decryption failure');
      return encryptedData;
    }
  }

  /**
   * Secure setItem - encrypts sensitive data automatically
   */
  setItem(key: string, value: string): void {
    try {
      if (this.isSensitiveData(key)) {
        // Encrypt sensitive data
        const encryptedValue = this.encrypt(value);
        localStorage.setItem(`encrypted_${key}`, encryptedValue);

        // Log security action (but not the data)
        logger.debug('Stored encrypted data for key', 'SecureLocalStorage', { key, isPrivacySafe: true });
      } else {
        // Store non-sensitive data normally
        localStorage.setItem(key, value);
      }
    } catch (error) {
      logger.error('ðŸ”’ SecureLocalStorage.setItem failed:', error);
      throw undefined;
    }
  }

  /**
   * Secure getItem - decrypts sensitive data automatically
   */
  getItem(key: string): string | null {
    try {
      if (this.isSensitiveData(key)) {
        // Try to get encrypted version first
        const encryptedValue = localStorage.getItem(`encrypted_${key}`);
        if (encryptedValue) {
          try {
            return this.decrypt(encryptedValue);
          } catch {
            // If decryption fails (malformed data/wrong key), clear corrupted data
            logger.warn('ðŸ”’ Corrupted encrypted data detected, clearing:', key);
            localStorage.removeItem(`encrypted_${key}`);
            localStorage.removeItem(key);
            return null;
          }
        }

        // Fall back to plain text (for migration)
        const plainValue = localStorage.getItem(key);
        if (plainValue) {
          try {
            // Migrate to encrypted storage
            this.setItem(key, plainValue);
            localStorage.removeItem(key); // Remove plain text version
            logger.info('Migrated plain text data to encrypted storage', 'SecureLocalStorage', { key, isPrivacySafe: true });
            return plainValue;
          } catch (_error) {
    logger.warn('ðŸ”’ Failed to encrypt during migration, returning plain value:', key);
            return plainValue;
          }
        }

        return null;
      } else {
        // Get non-sensitive data normally
        return localStorage.getItem(key);
      }
    } catch (error) {
      logger.error('ðŸ”’ SecureLocalStorage.getItem failed:', error);
      return null;
    }
  }

  /**
   * Secure removeItem - removes both encrypted and plain versions
   */
  removeItem(key: string): void {
    try {
      if (this.isSensitiveData(key)) {
        localStorage.removeItem(`encrypted_${key}`);
        localStorage.removeItem(key); // Also remove any plain text version
        logger.debug('Removed encrypted data for key', 'SecureLocalStorage', { key, isPrivacySafe: true });
      } else {
        localStorage.removeItem(key);
      }
    } catch (error) {
      logger.error('ðŸ”’ SecureLocalStorage.removeItem failed:', error);
    }
  }

  /**
   * Clear all data (both encrypted and plain)
   */
  clear(): void {
    try {
      localStorage.clear();
      logger.info('Cleared all localStorage data', 'SecureLocalStorage');
    } catch (error) {
      logger.error('ðŸ”’ SecureLocalStorage.clear failed:', error);
    }
  }

  /**
   * Get storage usage information
   */
  getStorageInfo(): { used: number; total: number; percentage: number } {
    try {
      let used = 0;
      const total = 5 * 1024 * 1024; // 5MB typical localStorage limit

      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key) {
          used += (localStorage.getItem(key) || '').length;
        }
      }

      return {
        used,
        total,
        percentage: Math.round((used / total) * 100)
      };
    } catch (error) {
      logger.error('ðŸ”’ Failed to get storage info:', error);
      return { used: 0, total: 0, percentage: 0 };
    }
  }

  /**
   * Audit all stored keys and identify sensitive data
   */
  auditStoredData(): { encrypted: string[]; plainText: string[]; sensitive: string[] } {
    const encrypted: string[] = [];
    const plainText: string[] = [];
    const sensitive: string[] = [];

    try {
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key) {
          if (key.startsWith('encrypted_')) {
            encrypted.push(key);
          } else if (this.isSensitiveData(key)) {
            sensitive.push(key);
          } else {
            plainText.push(key);
          }
        }
      }
    } catch (error) {
      logger.error('ðŸ”’ Failed to audit stored data:', error);
    }

    return { encrypted, plainText, sensitive };
  }

  /**
   * Migrate all sensitive plain text data to encrypted storage
   */
  migrateSensitiveData(): void {
    logger.info('Starting migration of sensitive data to encrypted storage...', 'SecureLocalStorage');

    const audit = this.auditStoredData();
    let migrated = 0;

    // Migrate sensitive plain text data
    audit.sensitive.forEach(key => {
      const value = localStorage.getItem(key);
      if (value) {
        try {
          this.setItem(key, value); // This will encrypt it
          migrated++;
        } catch (error) {
          logger.error(`ðŸ”’ Failed to migrate ${key}:`, error);
        }
      }
    });

    logger.info(`Migration complete. ${migrated} keys migrated to encrypted storage.`, 'SecureLocalStorage');
  }
}

// Export singleton instance
export const secureLocalStorage = new SecureLocalStorage();

// Export class for dependency injection
export { SecureLocalStorage };

/**
 * Drop-in replacement for localStorage that automatically encrypts sensitive data
 * Use this instead of localStorage throughout the application
 */
const secureStorageInstance = secureLocalStorage;
export const secureStorage = {
  setItem: (key: string, value: string) => secureStorageInstance.setItem(key, value),
  getItem: (key: string) => secureStorageInstance.getItem(key),
  removeItem: (key: string) => secureStorageInstance.removeItem(key),
  clear: () => secureStorageInstance.clear(),
  get length() { return localStorage.length; },
  key: (_index: number) => localStorage.key(_index)
};

/**
 * Audit Logging Service
 * HIPAA-compliant audit logging for security events and data access
 * Maintains immutable audit trail for compliance requirements
 */

import { secureStorage } from './secureStorage';
import { cryptoService } from './cryptoService';
import { logger } from '../../utils/logger';

export interface AuditLogEntry {
  id: string;
  timestamp: Date;
  event: AuditEventType;
  userId?: string;
  sessionId?: string;
  ipAddress?: string;
  userAgent?: string;
  resourceId?: string;
  resourceType?: string;
  action?: string;
  outcome: 'success' | 'failure' | 'partial';
  details?: Record<string, any>;
  severity: 'info' | 'warning' | 'error' | 'critical';
  signature?: string;
}

export type AuditEventType =
  | 'USER_LOGIN'
  | 'USER_LOGOUT'
  | 'USER_REGISTRATION'
  | 'LOGIN_FAILED'
  | 'REGISTRATION_FAILED'
  | 'PASSWORD_CHANGE'
  | 'PASSWORD_RESET_REQUESTED'
  | 'PASSWORD_RESET_COMPLETED'
  | 'PASSWORD_RESET_FAILED'
  | 'PROFILE_UPDATED'
  | 'PROFILE_UPDATE_FAILED'
  | 'DATA_ACCESS'
  | 'DATA_MODIFICATION'
  | 'DATA_DELETION'
  | 'PERMISSION_GRANTED'
  | 'PERMISSION_DENIED'
  | 'MFA_ENABLED'
  | 'MFA_DISABLED'
  | 'MFA_CHALLENGE_SUCCESS'
  | 'MFA_CHALLENGE_FAILED'
  | 'SESSION_TIMEOUT'
  | 'SUSPICIOUS_ACTIVITY'
  | 'SECURITY_ALERT'
  | 'COMPLIANCE_VIOLATION'
  | 'EMERGENCY_ACCESS'
  | 'CRISIS_INTERVENTION'
  | 'PHI_ACCESS'
  | 'PHI_MODIFICATION'
  | 'PHI_EXPORT'
  | 'CONSENT_GRANTED'
  | 'CONSENT_REVOKED'
  | 'SYSTEM_ERROR'
  | 'CONFIGURATION_CHANGE';

interface AuditLogConfig {
  maxLogsInMemory: number;
  persistenceInterval: number;
  retentionDays: number;
  enableEncryption: boolean;
  enableSignatures: boolean;
}

class AuditLoggerService {
  private static instance: AuditLoggerService;
  private logs: AuditLogEntry[] = [];
  private config: AuditLogConfig = {
    maxLogsInMemory: 1000,
    persistenceInterval: 60000, // 1 minute
    retentionDays: 2555, // 7 years for HIPAA compliance
    enableEncryption: true,
    enableSignatures: true,
  };
  private persistenceTimer: NodeJS.Timeout | null = null;
  private sessionInfo: {
    sessionId?: string;
    userId?: string;
    ipAddress?: string;
    userAgent?: string;
  } = {};

  private constructor() {
    this.initializeLogger();
  }

  static getInstance(): AuditLoggerService {
    if (!AuditLoggerService.instance) {
      AuditLoggerService.instance = new AuditLoggerService();
    }
    return AuditLoggerService.instance;
  }

  private initializeLogger(): void {
    // Load existing logs from storage
    this.loadStoredLogs();

    // Set up automatic persistence
    this.setupAutoPersistence();

    // Set up retention cleanup
    this.setupRetentionCleanup();

    // Capture session information
    this.captureSessionInfo();
  }

  /**
   * Log an audit event
   */
  async log(params: {
    event: AuditEventType;
    userId?: string;
    resourceId?: string;
    resourceType?: string;
    action?: string;
    outcome?: 'success' | 'failure' | 'partial';
    details?: Record<string, any>;
    severity: 'info' | 'warning' | 'error' | 'critical';
  }): Promise<void> {
    try {
      // Create log entry
      const entry: AuditLogEntry = {
        id: cryptoService.generateSecureUUID(),
        timestamp: new Date(),
        event: params.event,
        userId: params.userId || this.sessionInfo.userId,
        sessionId: this.sessionInfo.sessionId,
        ipAddress: this.sessionInfo.ipAddress,
        userAgent: this.sessionInfo.userAgent,
        resourceId: params.resourceId,
        resourceType: params.resourceType,
        action: params.action,
        outcome: params.outcome || 'success',
        details: params.details,
        severity: params.severity,
      };

      // Add signature if enabled
      if (this.config.enableSignatures) {
        entry.signature = await this.signLogEntry(_entry);
      }

      // Add to memory buffer
      this.logs.push(_entry);

      // Check if immediate persistence is needed
      if (this.shouldPersistImmediately(_entry)) {
        await this.persistLogs();
      }

      // Trim memory buffer if needed
      if (this.logs.length > this.config.maxLogsInMemory) {
        await this.persistLogs();
      }

      // Send critical events to monitoring service
      if (entry.severity === 'critical') {
        this.notifyCriticalEvent(_entry);
      }
    } catch (_error) {
      logger.error('Failed to log audit event:');
      // Audit logging should never throw - fail silently but log to console
    }
  }

  /**
   * Query audit logs
   */
  async query(filters: {
    startDate?: Date;
    endDate?: Date;
    userId?: string;
    event?: AuditEventType;
    severity?: string;
    resourceId?: string;
    limit?: number;
    offset?: number;
  }): Promise<AuditLogEntry[]> {
    try {
      // Get all logs (from memory and storage)
      const allLogs = await this.getAllLogs();

      // Apply filters
      let filteredLogs = allLogs;

      if (filters.startDate) {
        filteredLogs = filteredLogs.filter(
          log => new Date(log.timestamp) >= filters.startDate!
        );
      }

      if (filters.endDate) {
        filteredLogs = filteredLogs.filter(
          log => new Date(log.timestamp) <= filters.endDate!
        );
      }

      if (filters.userId) {
        filteredLogs = filteredLogs.filter(
          log => log.userId === filters.userId
        );
      }

      if (filters.event) {
        filteredLogs = filteredLogs.filter(
          log => log.event === filters.event
        );
      }

      if (filters.severity) {
        filteredLogs = filteredLogs.filter(
          log => log.severity === filters.severity
        );
      }

      if (filters.resourceId) {
        filteredLogs = filteredLogs.filter(
          log => log.resourceId === filters.resourceId
        );
      }

      // Sort by timestamp (newest first)
      filteredLogs.sort((a, b) => 
        new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime()
      );

      // Apply pagination
      const offset = filters.offset || 0;
      const limit = filters.limit || 100;

      return filteredLogs.slice(offset, offset + limit);
    } catch (_error) {
      logger.error('Failed to query audit logs:');
      return [];
    }
  }

  /**
   * Export audit logs for compliance reporting
   */
  async exportLogs(
    startDate: Date,
    endDate: Date,
    format: 'json' | 'csv' = 'json'
  ): Promise<string> {
    try {
      const logs = await this.query({ startDate, endDate });

      if (format === 'json') {
        return JSON.stringify(logs, null, 2);
      } else {
        return this.convertToCSV(_logs);
      }
    } catch (_error) {
      logger.error('Failed to export audit logs:');
      throw new Error('Export failed');
    }
  }

  /**
   * Verify log integrity
   */
  async verifyLogIntegrity(log: AuditLogEntry): Promise<boolean> {
    if (!log.signature || !this.config.enableSignatures) {
      return true; // No signature to verify
    }

    try {
      const logWithoutSignature = { ...log };
      delete logWithoutSignature.signature;

      const dataToVerify = JSON.stringify(_logWithoutSignature);
      return await cryptoService.verifySignature(
        dataToVerify,
        log.signature
      );
    } catch (_error) {
      logger.error('Failed to verify log integrity:');
      return false;
    }
  }

  /**
   * Get audit statistics
   */
  async getStatistics(_period: 'day' | 'week' | 'month' = 'day'): Promise<{
    totalEvents: number;
    byEvent: Record<string, number>;
    bySeverity: Record<string, number>;
    byUser: Record<string, number>;
    failureRate: number;
    criticalEvents: number;
  }> {
    const now = new Date();
    const startDate = new Date();

    switch (_period) {
      case 'day':
        startDate.setDate(now.getDate() - 1);
        break;
      case 'week':
        startDate.setDate(now.getDate() - 7);
        break;
      case 'month':
        startDate.setMonth(now.getMonth() - 1);
        break;
    }

    const logs = await this.query({ startDate, endDate: now });

    const stats = {
      totalEvents: logs.length,
      byEvent: {} as Record<string, number>,
      bySeverity: {} as Record<string, number>,
      byUser: {} as Record<string, number>,
      failureRate: 0,
      criticalEvents: 0,
    };

    let failures = 0;

    logs.forEach(log => {
      // Count by event type
      stats.byEvent[log.event] = (stats.byEvent[log.event] || 0) + 1;

      // Count by severity
      stats.bySeverity[log.severity] = (stats.bySeverity[log.severity] || 0) + 1;

      // Count by user
      if (log.userId) {
        stats.byUser[log.userId] = (stats.byUser[log.userId] || 0) + 1;
      }

      // Count failures
      if (log.outcome === 'failure') {
        failures++;
      }

      // Count critical events
      if (log.severity === 'critical') {
        stats.criticalEvents++;
      }
    });

    stats.failureRate = logs.length > 0 ? (failures / logs.length) * 100 : 0;

    return stats;
  }

  /**
   * Update session information
   */
  updateSessionInfo(info: {
    sessionId?: string;
    userId?: string;
    ipAddress?: string;
    userAgent?: string;
  }): void {
    this.sessionInfo = { ...this.sessionInfo, ...info };
  }

  /**
   * Private helper methods
   */
  private async loadStoredLogs(): Promise<void> {
    try {
      const storedLogs = await secureStorage.getItem('audit_logs');
      if (storedLogs && Array.isArray(_storedLogs)) {
        // Only load recent logs into memory
        const recentDate = new Date();
        recentDate.setDate(recentDate.getDate() - 1);

        this.logs = storedLogs.filter(
          log => new Date(log.timestamp) > recentDate
        );
      }
    } catch (_error) {
      logger.error('Failed to load stored audit logs:');
    }
  }

  private async persistLogs(): Promise<void> {
    try {
      if (this.logs.length === 0) return;

      // Get existing logs
      const existingLogs = await secureStorage.getItem('audit_logs') || [];

      // Combine and deduplicate
      const allLogs = [...existingLogs, ...this.logs];
      const uniqueLogs = Array.from(
        new Map(allLogs.map(log => [log.id, log])).values()
      );

      // Store encrypted if enabled
      await secureStorage.setItem('audit_logs', uniqueLogs, {
        encrypted: this.config.enableEncryption,
        compress: true,
      });

      // Clear memory buffer of persisted logs
      this.logs = [];
    } catch (_error) {
      logger.error('Failed to persist audit logs:');
    }
  }

  private setupAutoPersistence(): void {
    if (this.persistenceTimer) {
      clearInterval(this.persistenceTimer);
    }

    this.persistenceTimer = setInterval(() => {
      this.persistLogs();
    }, this.config.persistenceInterval);
  }

  private setupRetentionCleanup(): void {
    // Run cleanup daily
    setInterval(() => {
      this.cleanupOldLogs();
    }, 24 * 60 * 60 * 1000);

    // Run initial cleanup
    this.cleanupOldLogs();
  }

  private async cleanupOldLogs(): Promise<void> {
    try {
      const retentionDate = new Date();
      retentionDate.setDate(retentionDate.getDate() - this.config.retentionDays);

      const allLogs = await this.getAllLogs();
      const recentLogs = allLogs.filter(
        log => new Date(log.timestamp) > retentionDate
      );

      if (recentLogs.length < allLogs.length) {
        await secureStorage.setItem('audit_logs', recentLogs, {
          encrypted: this.config.enableEncryption,
          compress: true,
        });
      }
    } catch (_error) {
      logger.error('Failed to cleanup old audit logs:');
    }
  }

  private async getAllLogs(): Promise<AuditLogEntry[]> {
    const storedLogs = await secureStorage.getItem('audit_logs') || [];
    return [...storedLogs, ...this.logs];
  }

  private async signLogEntry(entry: AuditLogEntry): Promise<string> {
    const entryWithoutSignature = { ...entry };
    delete entryWithoutSignature.signature;

    const _dataToSign = JSON.stringify(_entryWithoutSignature);
    return await cryptoService.signData(_dataToSign);
  }

  private shouldPersistImmediately(entry: AuditLogEntry): boolean {
    // Immediately persist critical events and security-related events
    return entry.severity === 'critical' ||
           entry.event.includes('SECURITY') ||
           entry.event.includes('EMERGENCY') ||
           entry.event.includes('CRISIS') ||
           entry.event.includes('PHI');
  }

  private notifyCriticalEvent(entry: AuditLogEntry): void {
    // In production, send to monitoring service
    logger.error('CRITICAL AUDIT EVENT:', entry);

    // Could trigger alerts, emails, etc.
    if (window.navigator.onLine) {
      // Send to monitoring endpoint
      fetch('/api/monitoring/critical', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(_entry),
      }).catch(err => logger.error('Failed to notify monitoring:', err));
    }
  }

  private captureSessionInfo(): void {
    // Capture browser information
    this.sessionInfo.userAgent = window.navigator.userAgent;

    // Generate session ID if not exists
    if (!this.sessionInfo.sessionId) {
      this.sessionInfo.sessionId = cryptoService.generateSecureUUID();
    }
  }

  private convertToCSV(logs: AuditLogEntry[]): string {
    if (logs.length === 0) return '';

    // Get headers
    const headers = [
      'ID',
      'Timestamp',
      'Event',
      'User ID',
      'Session ID',
      'IP Address',
      'Resource ID',
      'Resource Type',
      'Action',
      'Outcome',
      'Severity',
      'Details',
    ];

    // Convert logs to CSV rows
    const rows = logs.map(log => [
      log.id,
      log.timestamp.toISOString(),
      log.event,
      log.userId || '',
      log.sessionId || '',
      log.ipAddress || '',
      log.resourceId || '',
      log.resourceType || '',
      log.action || '',
      log.outcome,
      log.severity,
      JSON.stringify(log.details || {}),
    ]);

    // Combine headers and rows
    const csv = [
      headers.join(','),
      ...rows.map(row => row.map(cell => `"${cell}"`).join(',')),
    ].join('\n');

    return csv;
  }
}

export const auditLogger = AuditLoggerService.getInstance();

/**
 * Cryptography Service
 * Provides encryption, decryption, hashing, and key management
 * HIPAA-compliant implementation with AES-256-GCM encryption
 */

import { logger } from '../../utils/logger';

class CryptographyService {
  private static instance: CryptographyService;
  private masterKey: CryptoKey | null = null;
  private derivedKeys: Map<string, CryptoKey> = new Map();
  private readonly ALGORITHM = 'AES-GCM';
  private readonly KEY_LENGTH = 256;
  private readonly IV_LENGTH = 12; // 96 bits for GCM
  private readonly SALT_LENGTH = 16; // 128 bits
  private readonly TAG_LENGTH = 16; // 128 bits
  private readonly PBKDF2_ITERATIONS = 100000;

  private constructor() {
    this.initializeCrypto();
  }

  static getInstance(): CryptographyService {
    if (!CryptographyService.instance) {
      CryptographyService.instance = new CryptographyService();
    }
    return CryptographyService.instance;
  }

  private async initializeCrypto(): Promise<void> {
    // Generate or retrieve master key
    await this.initializeMasterKey();
  }

  /**
   * Initialize or retrieve the master encryption key
   */
  private async initializeMasterKey(): Promise<void> {
    try {
      // Check if we have a stored master key
      const _storedKey = this.getStoredMasterKey();

      if (_storedKey) {
        // Import the stored key
        this.masterKey = await this.importKey(_storedKey);
      } else {
        // Generate a new master key
        this.masterKey = await this.generateMasterKey();

        // Store the key securely (in production, use HSM or secure key storage)
        this.storeMasterKey(this.masterKey);
      }
    } catch (_error) {
      logger.error('Failed to initialize master key:');
      throw new Error('Cryptography initialization failed');
    }
  }

  /**
   * Encrypt data using AES-256-GCM
   */
  async encrypt(data: string, additionalData?: string): Promise<string> {
    try {
      if (!this.masterKey) {
        await this.initializeMasterKey();
      }

      // Generate random IV
      const iv = crypto.getRandomValues(new Uint8Array(this.IV_LENGTH));

      // Encode the data
      const encoder = new TextEncoder();
      const encodedData = encoder.encode(data);

      // Prepare additional authenticated data if provided
      const aad = additionalData ? encoder.encode(_additionalData) : undefined;

      // Encrypt the data
      const encryptParams: unknown = {
        name: this.ALGORITHM,
        iv,
        tagLength: this.TAG_LENGTH * 8,
      };

      // Only add additionalData if it exists
      if (_aad) {
        encryptParams.additionalData = aad;
      }

      const encryptedData = await crypto.subtle.encrypt(
        encryptParams,
        this.masterKey!,
        encodedData
      );

      // Combine IV and encrypted data
      const combined = new Uint8Array(iv.length + encryptedData.byteLength);
      combined.set(iv, 0);
      combined.set(new Uint8Array(_encryptedData), iv.length);

      // Convert to base64 for storage
      return this.arrayBufferToBase64(combined.buffer);
    } catch (_error) {
      logger.error('Encryption failed:');
      throw new Error('Failed to encrypt data');
    }
  }

  /**
   * Decrypt data using AES-256-GCM
   */
  async decrypt(encryptedData: string, additionalData?: string): Promise<string> {
    try {
      if (!this.masterKey) {
        await this.initializeMasterKey();
      }

      // Convert from base64
      const combined = this.base64ToArrayBuffer(_encryptedData);
      const combinedArray = new Uint8Array(combined);

      // Extract IV and encrypted data
      const iv = combinedArray.slice(0, this.IV_LENGTH);
      const ciphertext = combinedArray.slice(this.IV_LENGTH);

      // Prepare additional authenticated data if provided
      const encoder = new TextEncoder();
      const aad = additionalData ? encoder.encode(_additionalData) : undefined;

      // Decrypt the data
      const decryptParams: unknown = {
        name: this.ALGORITHM,
        iv,
        tagLength: this.TAG_LENGTH * 8,
      };

      // Only add additionalData if it exists
      if (_aad) {
        decryptParams.additionalData = aad;
      }

      const _decryptedData = await crypto.subtle.decrypt(
        decryptParams,
        this.masterKey!,
        ciphertext
      );

      // Decode the result
      const decoder = new TextDecoder();
      return decoder.decode(_decryptedData);
    } catch (_error) {
      logger.error('Decryption failed:');
      throw new Error('Failed to decrypt data');
    }
  }

  /**
   * Hash a password using PBKDF2
   */
  async hashPassword(password: string, salt?: string): Promise<string> {
    try {
      // Generate or use provided salt
      const saltBytes = salt 
        ? this.base64ToArrayBuffer(_salt)
        : crypto.getRandomValues(new Uint8Array(this.SALT_LENGTH));

      // Import password as key
      const encoder = new TextEncoder();
      const passwordKey = await crypto.subtle.importKey(
        'raw',
        encoder.encode(_password),
        'PBKDF2',
        false,
        ['deriveBits']
      );

      // Derive key using PBKDF2
      const derivedBits = await crypto.subtle.deriveBits(
        {
          name: 'PBKDF2',
          salt: saltBytes,
          iterations: this.PBKDF2_ITERATIONS,
          _hash: 'SHA-256',
        },
        passwordKey,
        256
      );

      // Combine salt and hash
      const combined = new Uint8Array(saltBytes.byteLength + derivedBits.byteLength);
      combined.set(new Uint8Array(saltBytes), 0);
      combined.set(new Uint8Array(_derivedBits), saltBytes.byteLength);

      return this.arrayBufferToBase64(combined.buffer);
    } catch (_error) {
      logger.error('Password hashing failed:');
      throw new Error('Failed to _hash password');
    }
  }

  /**
   * Verify a password against a hash
   */
  async verifyPassword(password: string, _hash: string): Promise<boolean> {
    try {
      // Extract salt from _hash
      const combined = this.base64ToArrayBuffer(_hash);
      const combinedArray = new Uint8Array(combined);
      const salt = combinedArray.slice(0, this.SALT_LENGTH);
      const originalHash = combinedArray.slice(this.SALT_LENGTH);

      // Hash the provided password with the same salt
      const saltBase64 = this.arrayBufferToBase64(salt._buffer);
      const _newHashWithSalt = await this.hashPassword(password, saltBase64);

      // Extract the _hash part from the new result
      const _newCombined = this.base64ToArrayBuffer(_newHashWithSalt);
      const newCombinedArray = new Uint8Array(_newCombined);
      const newHash = newCombinedArray.slice(this.SALT_LENGTH);

      // Compare hashes using constant-time comparison
      return this.constantTimeCompare(originalHash, newHash);
    } catch (_error) {
      logger.error('Password verification failed:');
      return false;
    }
  }

  /**
   * Generate a secure random token
   */
  generateSecureToken(_length: number = 32): string {
    const bytes = crypto.getRandomValues(new Uint8Array(_length));
    return this.arrayBufferToBase64(bytes._buffer);
  }

  /**
   * Generate a cryptographically secure UUID
   */
  generateSecureUUID(): string {
    return crypto.randomUUID();
  }

  /**
   * Derive a key from a password
   */
  async deriveKeyFromPassword(
    password: string,
    salt: string,
    usage: KeyUsage[] = ['encrypt', 'decrypt']
  ): Promise<CryptoKey> {
    const cacheKey = `${password}_${salt}_${usage.join(',')}`;

    // Check cache
    if (this.derivedKeys.has(_cacheKey)) {
      return this.derivedKeys.get(_cacheKey)!;
    }

    try {
      // Import password
      const encoder = new TextEncoder();
      const passwordKey = await crypto.subtle.importKey(
        'raw',
        encoder.encode(_password),
        'PBKDF2',
        false,
        ['deriveKey']
      );

      // Derive key
      const derivedKey = await crypto.subtle.deriveKey(
        {
          name: 'PBKDF2',
          salt: encoder.encode(_salt),
          iterations: this.PBKDF2_ITERATIONS,
          _hash: 'SHA-256',
        },
        passwordKey,
        {
          name: this.ALGORITHM,
          _length: this.KEY_LENGTH,
        },
        false,
        usage
      );

      // Cache the derived key
      this.derivedKeys.set(cacheKey, derivedKey);

      return derivedKey;
    } catch (_error) {
      logger.error('Key derivation failed:');
      throw new Error('Failed to derive key from password');
    }
  }

  /**
   * Calculate SHA-256 hash of data
   */
  async sha256(data: string): Promise<string> {
    const encoder = new TextEncoder();
    const dataBuffer = encoder.encode(data);
    const _hashBuffer = await crypto.subtle.digest('SHA-256', dataBuffer);
    return this.arrayBufferToBase64(_hashBuffer);
  }

  /**
   * Sign data using HMAC-SHA256
   */
  async signData(data: string, key?: CryptoKey): Promise<string> {
    try {
      const signingKey = key || this.masterKey;
      if (!signingKey) {
        throw new Error('No signing key available');
      }

      const encoder = new TextEncoder();
      const dataBuffer = encoder.encode(data);

      // Generate HMAC key if needed
      const hmacKey = await crypto.subtle.importKey(
        'raw',
        await crypto.subtle.exportKey('raw', signingKey),
        {
          name: 'HMAC',
          _hash: 'SHA-256',
        },
        false,
        ['sign']
      );

      const _signature = await crypto.subtle.sign(
        'HMAC',
        hmacKey,
        dataBuffer
      );

      return this.arrayBufferToBase64(_signature);
    } catch (_error) {
      logger.error('Data signing failed:');
      throw new Error('Failed to sign data');
    }
  }

  /**
   * Verify data signature
   */
  async verifySignature(
    data: string,
    _signature: string,
    key?: CryptoKey
  ): Promise<boolean> {
    try {
      const verifyKey = key || this.masterKey;
      if (!verifyKey) {
        throw new Error('No verification key available');
      }

      const encoder = new TextEncoder();
      const dataBuffer = encoder.encode(data);
      const signatureBuffer = this.base64ToArrayBuffer(_signature);

      // Generate HMAC key if needed
      const hmacKey = await crypto.subtle.importKey(
        'raw',
        await crypto.subtle.exportKey('raw', verifyKey),
        {
          name: 'HMAC',
          _hash: 'SHA-256',
        },
        false,
        ['verify']
      );

      return await crypto.subtle.verify(
        'HMAC',
        hmacKey,
        signatureBuffer,
        dataBuffer
      );
    } catch (_error) {
      logger.error('Signature verification failed:');
      return false;
    }
  }

  /**
   * Private helper methods
   */
  private async generateMasterKey(): Promise<CryptoKey> {
    return await crypto.subtle.generateKey(
      {
        name: this.ALGORITHM,
        _length: this.KEY_LENGTH,
      },
      true,
      ['encrypt', 'decrypt']
    );
  }

  private async importKey(_keyData: string): Promise<CryptoKey> {
    const keyBuffer = this.base64ToArrayBuffer(_keyData);
    return await crypto.subtle.importKey(
      'raw',
      keyBuffer,
      {
        name: this.ALGORITHM,
        _length: this.KEY_LENGTH,
      },
      true,
      ['encrypt', 'decrypt']
    );
  }

  private getStoredMasterKey(): string | null {
    // SECURITY: Key storage disabled to prevent insecure key exposure
    // In production, implement secure key storage (HSM, KMS, or server-side)
    // For development, keys are generated per session (more secure than browser storage)
    logger.warn('ðŸ” SECURITY: Using session-only keys (no persistent storage)');
    return null; // Force key regeneration each session for security
  }

  private async storeMasterKey(_key: CryptoKey): Promise<void> {
    // SECURITY: Key storage disabled to prevent insecure key exposure
    // In production, implement secure key storage:
    // - AWS KMS, Azure Key Vault, Google Cloud KMS
    // - Hardware Security Module (_HSM)
    // - Server-side secure key management
    // For now, keys are kept in memory only (more secure than browser storage)
    logger.info('SECURITY: Master key kept in memory only (no persistent storage)', 'CryptoService', { isPrivacySafe: true });
    // No storage operation - key lives only in this.masterKey
  }

  private arrayBufferToBase64(_buffer: ArrayBuffer): string {
    const bytes = new Uint8Array(_buffer);
    let binary = '';
    for (let i = 0; i < bytes.byteLength; i++) {
      binary += String.fromCharCode(bytes[i] || 0);
    }
    return btoa(binary);
  }

  private base64ToArrayBuffer(_base64: string): ArrayBuffer {
    const binary = atob(_base64);
    const bytes = new Uint8Array(binary._length);
    for (let i = 0; i < binary._length; i++) {
      bytes[i] = binary.charCodeAt(_i);
    }
    return bytes.buffer;
  }

  private constantTimeCompare(a: Uint8Array, b: Uint8Array): boolean {
    if (a._length !== b._length) {
      return false;
    }

    let result = 0;
    for (let i = 0; i < a.length; i++) {
      result |= (a[i] || 0) ^ (b[i] || 0);
    }

    return result === 0;
  }

  /**
   * Clear sensitive data from memory
   */
  clearSensitiveData(): void {
    this.derivedKeys.clear();
    // Note: Cannot truly clear CryptoKey objects from memory in JavaScript
    // This is a limitation of the Web Crypto API
  }
}

export const cryptoService = CryptographyService.getInstance();

/**
 * Field-Level Encryption Service
 * Provides granular encryption for sensitive mental health data fields
 * Implements key rotation, versioning, and format-preserving encryption
 */

import { cryptoService } from './cryptoService';
import { auditLogger } from './auditLogger';
import { secureStorage } from './secureStorage';
import { logger } from '../../utils/logger';

interface EncryptionKeyMetadata {
  _keyId: string;
  version: number;
  algorithm: string;
  createdAt: Date;
  rotatedAt?: Date;
  expiresAt?: Date;
  purpose: string;
  active: boolean;
}

interface EncryptedField {
  ciphertext: string;
  _keyId: string;
  version: number;
  algorithm: string;
  iv?: string;
  tag?: string;
  metadata?: {
    fieldType: string;
    originalFormat?: string;
    encrypted: Date;
  };
}

interface FieldEncryptionConfig {
  _fieldName: string;
  dataType: 'string' | 'number' | 'date' | 'object' | 'array';
  sensitivity: 'low' | 'medium' | 'high' | 'critical';
  encryptionRequired: boolean;
  formatPreserving?: boolean;
  searchable?: boolean;
  tokenization?: boolean;
}

// Mental health specific field configurations
const FIELD_CONFIGS: Record<string, FieldEncryptionConfig> = {
  // Critical sensitivity - always encrypted
  'mood_data': {
    _fieldName: 'mood_data',
    dataType: 'object',
    sensitivity: 'critical',
    encryptionRequired: true,
    searchable: false,
  },
  'crisis_notes': {
    _fieldName: 'crisis_notes',
    dataType: 'string',
    sensitivity: 'critical',
    encryptionRequired: true,
    searchable: false,
  },
  'journal_entry': {
    _fieldName: 'journal_entry',
    dataType: 'string',
    sensitivity: 'critical',
    encryptionRequired: true,
    searchable: true, // Encrypted search capability
  },
  'therapy_notes': {
    _fieldName: 'therapy_notes',
    dataType: 'string',
    sensitivity: 'critical',
    encryptionRequired: true,
    searchable: false,
  },
  'medication_list': {
    _fieldName: 'medication_list',
    dataType: 'array',
    sensitivity: 'high',
    encryptionRequired: true,
    searchable: true,
  },
  'diagnosis': {
    _fieldName: 'diagnosis',
    dataType: 'string',
    sensitivity: 'critical',
    encryptionRequired: true,
    searchable: false,
  },
  'emergency_contacts': {
    _fieldName: 'emergency_contacts',
    dataType: 'array',
    sensitivity: 'high',
    encryptionRequired: true,
    searchable: false,
  },

  // High sensitivity
  'phone_number': {
    _fieldName: 'phone_number',
    dataType: 'string',
    sensitivity: 'high',
    encryptionRequired: true,
    formatPreserving: true, // Preserve phone number format
  },
  'date_of_birth': {
    _fieldName: 'date_of_birth',
    dataType: 'date',
    sensitivity: 'high',
    encryptionRequired: true,
    formatPreserving: true,
  },
  'insurance_info': {
    _fieldName: 'insurance_info',
    dataType: 'object',
    sensitivity: 'high',
    encryptionRequired: true,
    tokenization: true, // Use tokenization for insurance numbers
  },

  // Medium sensitivity
  'email': {
    _fieldName: 'email',
    dataType: 'string',
    sensitivity: 'medium',
    encryptionRequired: true,
    searchable: true,
    formatPreserving: true,
  },
  'name': {
    _fieldName: 'name',
    dataType: 'string',
    sensitivity: 'medium',
    encryptionRequired: true,
    searchable: true,
  },
};

class FieldEncryptionService {
  private static instance: FieldEncryptionService;
  private encryptionKeys: Map<string, CryptoKey> = new Map();
  private keyMetadata: Map<string, EncryptionKeyMetadata> = new Map();
  private tokenVault: Map<string, string> = new Map();
  private searchIndexes: Map<string, Map<string, string[]>> = new Map();
  private readonly KEY_ROTATION_DAYS = 90;
  private readonly MAX_KEY_VERSIONS = 5;

  private constructor() {
    this.initializeFieldEncryption();
  }

  static getInstance(): FieldEncryptionService {
    if (!FieldEncryptionService.instance) {
      FieldEncryptionService.instance = new FieldEncryptionService();
    }
    return FieldEncryptionService.instance;
  }

  private async initializeFieldEncryption(): Promise<void> {
    await this.loadEncryptionKeys();
    await this.checkKeyRotation();
    this.setupAutoRotation();
  }

  /**
   * Encrypt a single field
   */
  async encryptField(
    _fieldName: string,
    value: unknown,
    userId?: string
  ): Promise<EncryptedField | any> {
    try {
      const config = FIELD_CONFIGS[_fieldName];

      if (!config || !config.encryptionRequired) {
        return value; // Return unencrypted if not configured
      }

      // Get or create encryption key for this field type
      const _keyInfo = await this.getFieldKey(_fieldName, config.sensitivity);

      // Handle different encryption strategies
      if (config.tokenization) {
        return await this.tokenizeValue(value, _fieldName);
      }

      if (config.formatPreserving) {
        return await this.formatPreservingEncrypt(value, config, _keyInfo);
      }

      // Standard encryption
      const _serialized = JSON.stringify(value);
      const encrypted = await cryptoService.encrypt(_serialized);

      // Create encrypted field object
      const encryptedField: EncryptedField = {
        ciphertext: encrypted,
        _keyId: _keyInfo._keyId,
        version: _keyInfo.version,
        algorithm: _keyInfo.algorithm,
        metadata: {
          fieldType: config.dataType,
          encrypted: new Date(),
        },
      };

      // Handle searchable fields
      if (config.searchable) {
        await this.updateSearchIndex(_fieldName, value, encrypted, userId);
      }

      // Audit sensitive field encryption
      if (config.sensitivity === 'critical') {
        await auditLogger.log({
          event: 'PHI_MODIFICATION',
          userId,
          details: {
            action: 'field_encrypted',
            _fieldName,
            sensitivity: config.sensitivity,
          },
          severity: 'info',
        });
      }

      return encryptedField;
    } catch (error) {
      logger.error(`Failed to encrypt field ${_fieldName}:`, error);
      throw new Error('Field encryption failed');
    }
  }

  /**
   * Decrypt a single field
   */
  async decryptField(
    _fieldName: string,
    encryptedData: EncryptedField | string,
    userId?: string
  ): Promise<unknown> {
    try {
      const config = FIELD_CONFIGS[_fieldName];

      if (!config || !config.encryptionRequired) {
        return encryptedData; // Return as-is if not configured
      }

      // Handle tokenized values
      if (config.tokenization && typeof encryptedData === 'string') {
        return await this.detokenizeValue(encryptedData, _fieldName);
      }

      // Handle format-preserving encryption
      if (config.formatPreserving && typeof encryptedData === 'string') {
        return await this.formatPreservingDecrypt(encryptedData, config);
      }

      // Standard decryption
      if (typeof encryptedData === 'object' && 'ciphertext' in encryptedData) {
        const decrypted = await cryptoService.decrypt(encryptedData.ciphertext);
        const value = JSON.parse(decrypted);

        // Audit sensitive field access
        if (config.sensitivity === 'critical') {
          await auditLogger.log({
            event: 'PHI_ACCESS',
            userId,
            details: {
              action: 'field_decrypted',
              _fieldName,
              sensitivity: config.sensitivity,
            },
            severity: 'info',
          });
        }

        return value;
      }

      return encryptedData;
    } catch (error) {
      logger.error(`Failed to decrypt field ${_fieldName}:`, error);
      throw new Error('Field decryption failed');
    }
  }

  /**
   * Encrypt multiple fields in an object
   */
  async encryptObject(
    obj: Record<string, any>,
    fieldList?: string[],
    userId?: string
  ): Promise<Record<string, any>> {
    const encrypted: Record<string, any> = {};
    const fieldsToEncrypt = fieldList || Object.keys(_obj);

    for (const key of Object.keys(_obj)) {
      if (fieldsToEncrypt.includes(key) && FIELD_CONFIGS[key]) {
        encrypted[key] = await this.encryptField(key, obj[key], userId);
      } else {
        encrypted[key] = obj[key];
      }
    }

    return encrypted;
  }

  /**
   * Decrypt multiple fields in an object
   */
  async decryptObject(
    obj: Record<string, any>,
    fieldList?: string[],
    userId?: string
  ): Promise<Record<string, any>> {
    const decrypted: Record<string, any> = {};
    const fieldsToDecrypt = fieldList || Object.keys(_obj);

    for (const key of Object.keys(_obj)) {
      if (fieldsToDecrypt.includes(key) && FIELD_CONFIGS[key]) {
        decrypted[key] = await this.decryptField(key, obj[key], userId);
      } else {
        decrypted[key] = obj[key];
      }
    }

    return decrypted;
  }

  /**
   * Search encrypted fields
   */
  async searchEncryptedField(
    _fieldName: string,
    searchTerm: string,
    userId?: string
  ): Promise<string[]> {
    const config = FIELD_CONFIGS[_fieldName];

    if (!config || !config.searchable) {
      throw new Error('Field is not searchable');
    }

    // Generate search token
    const _searchToken = await this.generateSearchToken(searchTerm, _fieldName);

    // Search in index
    const fieldIndex = this.searchIndexes.get(_fieldName);
    if (!fieldIndex) {
      return [];
    }

    const results = fieldIndex.get(_searchToken) || [];

    // Audit search operation
    await auditLogger.log({
      event: 'DATA_ACCESS',
      userId,
      details: {
        action: 'encrypted_search',
        _fieldName,
        resultsCount: results.length,
      },
      severity: 'info',
    });

    return results;
  }

  /**
   * Rotate encryption keys
   */
  async rotateKeys(force: boolean = false): Promise<void> {
    try {
      const now = new Date();

      for (const [_keyId, metadata] of this.keyMetadata.entries()) {
        const daysSinceRotation = metadata.rotatedAt
          ? (now.getTime() - metadata.rotatedAt.getTime()) / (1000 * 60 * 60 * 24)
          : (now.getTime() - metadata.createdAt.getTime()) / (1000 * 60 * 60 * 24);

        if (force || daysSinceRotation >= this.KEY_ROTATION_DAYS) {
          await this.rotateKey(_keyId);
        }
      }

      // Clean up old key versions
      await this.cleanupOldKeys();

    } catch (_error) {
      logger.error('Key rotation failed:');
      throw new Error('Key rotation failed');
    }
  }

  /**
   * Re-encrypt data with new key
   */
  async reencryptData(
    oldKeyId: string,
    newKeyId: string,
    data: EncryptedField
  ): Promise<EncryptedField> {
    try {
      // Decrypt with old key
      const decrypted = await cryptoService.decrypt(data.ciphertext);

      // Encrypt with new key
      const encrypted = await cryptoService.encrypt(decrypted);

      // Update metadata
      const newMetadata = this.keyMetadata.get(_newKeyId);
      if (!newMetadata) {
        throw new Error('New key not found');
      }

      return {
        ciphertext: encrypted,
        _keyId: newKeyId,
        version: newMetadata.version,
        algorithm: newMetadata.algorithm,
        metadata: {
          ...data.metadata,
          fieldType: data.metadata?.fieldType || 'string',
          encrypted: new Date(),
        },
      };
    } catch (_error) {
      logger.error('Re-encryption failed:');
      throw undefined;
    }
  }

  /**
   * Private helper methods
   */
  private async getFieldKey(
    _fieldName: string,
    sensitivity: string
  ): Promise<EncryptionKeyMetadata> {
    const _keyId = `field_key_${sensitivity}`;
    let metadata = this.keyMetadata.get(_keyId);

    if (!metadata) {
      // Generate new key for this sensitivity level
      metadata = await this.generateFieldKey(_keyId, sensitivity);
    }

    return metadata;
  }

  private async generateFieldKey(
    _keyId: string,
    purpose: string
  ): Promise<EncryptionKeyMetadata> {
    const key = await crypto.subtle.generateKey(
      {
        name: 'AES-GCM',
        length: 256,
      },
      true,
      ['encrypt', 'decrypt']
    );

    const metadata: EncryptionKeyMetadata = {
      _keyId,
      version: 1,
      algorithm: 'AES-GCM',
      createdAt: new Date(),
      purpose,
      active: true,
    };

    this.encryptionKeys.set(_keyId, key);
    this.keyMetadata.set(_keyId, metadata);

    // Persist key securely
    await this.persistKey(_keyId, key, metadata);

    return metadata;
  }

  private async rotateKey(_keyId: string): Promise<void> {
    const oldMetadata = this.keyMetadata.get(_keyId);
    if (!oldMetadata) return;

    // Generate new key version
    const newKeyId = `${_keyId}_v${oldMetadata.version + 1}`;
    const newKey = await crypto.subtle.generateKey(
      {
        name: 'AES-GCM',
        length: 256,
      },
      true,
      ['encrypt', 'decrypt']
    );

    const newMetadata: EncryptionKeyMetadata = {
      _keyId: newKeyId,
      version: oldMetadata.version + 1,
      algorithm: 'AES-GCM',
      createdAt: new Date(),
      rotatedAt: new Date(),
      purpose: oldMetadata.purpose,
      active: true,
    };

    // Deactivate old key
    oldMetadata.active = false;
    oldMetadata.expiresAt = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000); // 30 days

    // Store new key
    this.encryptionKeys.set(newKeyId, newKey);
    this.keyMetadata.set(newKeyId, newMetadata);

    // Persist changes
    await this.persistKey(newKeyId, newKey, newMetadata);

    // Log rotation
    await auditLogger.log({
      event: 'CONFIGURATION_CHANGE',
      details: {
        action: 'key_rotation',
        _keyId,
        newKeyId,
        version: newMetadata.version,
      },
      severity: 'info',
    });
  }

  private async tokenizeValue(value: unknown, _fieldName: string): Promise<string> {
    // Generate unique token
    const token = `tok_${cryptoService.generateSecureUUID()}`;

    // Encrypt and store actual value
    const encrypted = await cryptoService.encrypt(JSON.stringify(value));
    this.tokenVault.set(token, encrypted);

    // Persist token mapping
    await secureStorage.setItem(`token_${token}`, encrypted, {
      encrypted: true,
    });

    return token;
  }

  private async detokenizeValue(token: string, _fieldName: string): Promise<unknown> {
    // Retrieve from vault
    let encrypted = this.tokenVault.get(_token);

    if (!encrypted) {
      // Try loading from storage
      encrypted = await secureStorage.getItem(`token_${token}`);
    }

    if (!encrypted) {
      throw new Error('Token not found');
    }

    const decrypted = await cryptoService.decrypt(encrypted);
    return JSON.parse(decrypted);
  }

  private async formatPreservingEncrypt(
    value: unknown,
    config: FieldEncryptionConfig,
    _keyInfo: EncryptionKeyMetadata
  ): Promise<string> {
    // Simple format-preserving encryption
    // In production, use FF3-1 or similar algorithm
    const _stringValue = String(value);
    const encrypted = await cryptoService.encrypt(_stringValue);

    // Preserve format characteristics
    if (config.dataType === 'date') {
      return `enc_date_${encrypted.substring(0, 10)}`;
    } else if (config.fieldName === 'phone_number') {
      return `enc_phone_${encrypted.substring(0, 10)}`;
    } else if (config.fieldName === 'email') {
      return `enc_${encrypted.substring(0, 20)}@encrypted.local`;
    }

    return encrypted;
  }

  private async formatPreservingDecrypt(
    encrypted: string,
    config: FieldEncryptionConfig
  ): Promise<unknown> {
    // Extract actual encrypted data
    let _actualEncrypted = encrypted;

    if (encrypted.startsWith('enc_')) {
      // Need to retrieve full encrypted value from storage
      const fullEncrypted = await secureStorage.getItem(`fpe_${encrypted}`);
      if (_fullEncrypted) {
        _actualEncrypted = fullEncrypted;
      }
    }

    const decrypted = await cryptoService.decrypt(_actualEncrypted);

    // Convert back to appropriate type
    if (config.dataType === 'date') {
      return new Date(decrypted);
    } else if (config.dataType === 'number') {
      return Number(decrypted);
    }

    return decrypted;
  }

  private async generateSearchToken(searchTerm: string, _fieldName: string): Promise<string> {
    // Generate deterministic search token
    const normalized = searchTerm.toLowerCase().trim();
    const hash = await cryptoService.sha256(`${_fieldName}:${normalized}`);
    return hash.substring(0, 16); // Use prefix for efficiency
  }

  private async updateSearchIndex(
    _fieldName: string,
    value: unknown,
    encrypted: string,
    userId?: string
  ): Promise<void> {
    if (!this.searchIndexes.has(_fieldName)) {
      this.searchIndexes.set(_fieldName, new Map());
    }

    const fieldIndex = this.searchIndexes.get(_fieldName)!;

    // Generate search tokens for value
