/**
 * Rate Limiting and DDoS Protection Service
 * Implements comprehensive rate limiting, request throttling, and attack detection
 * OWASP compliant with adaptive protection mechanisms
 */

import { auditLogger } from './auditLogger';
import { _cryptoService } from './cryptoService';
import { secureStorage } from './SecureLocalStorage';
import { logger } from '../../utils/logger';

interface RateLimitConfig {
  windowMs: number; // Time window in milliseconds
  maxRequests: number; // Maximum requests per window
  skipSuccessfulRequests?: boolean; // Don't count successful requests
  skipFailedRequests?: boolean; // Don't count failed requests
  keyGenerator?: (context: unknown) => string; // Custom key generation
  handler?: (context: unknown) => void; // Custom rate limit handler
  onLimitReached?: (key: string, info: RateLimitInfo) => void;
}

interface RateLimitInfo {
  limit: number;
  current: number;
  remaining: number;
  resetTime: Date;
  retryAfter: number;
}

interface EndpointLimits {
  [endpoint: string]: RateLimitConfig;
}

interface AttackPattern {
  pattern: RegExp;
  severity: 'low' | 'medium' | 'high' | 'critical';
  action: 'log' | 'throttle' | 'block' | 'honeypot';
  description: string;
}

interface IPReputation {
  _ip: string;
  score: number; // 0-100, higher is worse
  lastSeen: Date;
  violations: number;
  blocked: boolean;
  reason?: string;
}

class RateLimiterService {
  private static instance: RateLimiterService;
  private requestCounts: Map<string, number[]> = new Map();
  private blockedIPs: Map<string, Date> = new Map();
  private ipReputations: Map<string, IPReputation> = new Map();
  private honeypotEndpoints: Set<string> = new Set();
  private attackPatterns: AttackPattern[] = [];
  private captchaRequired: Map<string, Date> = new Map();

  // Default rate limits for different endpoint types
  private readonly defaultLimits: EndpointLimits = {
    // Authentication endpoints - strict limits
    '/api/auth/login': {
      windowMs: 15 * 60 * 1000, // 15 minutes
      maxRequests: 5,
      skipSuccessfulRequests: true,
    },
    '/api/auth/register': {
      windowMs: 60 * 60 * 1000, // 1 hour
      maxRequests: 3,
    },
    '/api/auth/reset-password': {
      windowMs: 60 * 60 * 1000, // 1 hour
      maxRequests: 3,
    },
    '/api/auth/mfa': {
      windowMs: 5 * 60 * 1000, // 5 minutes
      maxRequests: 5,
      skipSuccessfulRequests: true,
    },

    // Crisis services - balanced for emergency access
    '/api/crisis/emergency': {
      windowMs: 60 * 1000, // 1 minute
      maxRequests: 10, // Higher limit for crisis situations
    },
    '/api/crisis/chat': {
      windowMs: 60 * 1000, // 1 minute
      maxRequests: 30, // Allow rapid messages in crisis
    },
    '/api/crisis/resources': {
      windowMs: 60 * 1000, // 1 minute
      maxRequests: 20,
    },

    // Health data endpoints - moderate limits
    '/api/health/mood': {
      windowMs: 60 * 1000, // 1 minute
      maxRequests: 10,
    },
    '/api/health/journal': {
      windowMs: 5 * 60 * 1000, // 5 minutes
      maxRequests: 20,
    },
    '/api/health/medication': {
      windowMs: 60 * 1000, // 1 minute
      maxRequests: 10,
    },

    // Community endpoints - stricter to prevent spam
    '/api/community/post': {
      windowMs: 5 * 60 * 1000, // 5 minutes
      maxRequests: 5,
    },
    '/api/community/comment': {
      windowMs: 60 * 1000, // 1 minute
      maxRequests: 10,
    },
    '/api/community/report': {
      windowMs: 5 * 60 * 1000, // 5 minutes
      maxRequests: 3,
    },

    // General API endpoints
    '/api/*': {
      windowMs: 60 * 1000, // 1 minute
      maxRequests: 60, // 1 request per second average
    },
  };

  private constructor() {
    this.initializeRateLimiter();
  }

  static getInstance(): RateLimiterService {
    if (!RateLimiterService.instance) {
      RateLimiterService.instance = new RateLimiterService();
    }
    return RateLimiterService.instance;
  }

  private initializeRateLimiter(): void {
    this.setupAttackPatterns();
    this.setupHoneypots();
    this.startCleanupInterval();
    this.loadBlockedIPs();
  }

  /**
   * Check if request should be rate limited
   */
  async checkRateLimit(params: {
    endpoint: string;
    _ip: string;
    userId?: string;
    _method?: string;
    headers?: Record<string, string>;
  }): Promise<{
    allowed: boolean;
    info: RateLimitInfo;
    reason?: string;
  }> {
    const { endpoint, _ip, userId, _method = 'GET' } = params;

    // Check if IP is blocked
    if (this.isIPBlocked(_ip)) {
      return {
        allowed: false,
        info: this.getRateLimitInfo(endpoint, 0, 0),
        reason: 'IP address is blocked',
      };
    }

    // Check for attack patterns
    const attackDetected = this.detectAttackPattern(endpoint, params.headers);
    if (attackDetected) {
      await this.handleAttackDetection(_ip, attackDetected);
      if (attackDetected.action === 'block') {
        return {
          allowed: false,
          info: this.getRateLimitInfo(endpoint, 0, 0),
          reason: 'Suspicious activity detected',
        };
      }
    }

    // Check if honeypot endpoint
    if (this.isHoneypot(endpoint)) {
      await this.handleHoneypotAccess(_ip, endpoint);
      return {
        allowed: false,
        info: this.getRateLimitInfo(endpoint, 0, 0),
        reason: 'Invalid endpoint',
      };
    }

    // Get rate limit configuration for endpoint
    const config = this.getRateLimitConfig(endpoint);

    // Generate rate limit key
    const key = this.generateKey(_ip, userId, endpoint);

    // Get current request count
    const count = this.getRequestCount(key, config.windowMs);

    // Check if limit exceeded
    if (count >= config.maxRequests) {
      await this.handleRateLimitExceeded(_ip, endpoint, count);

      // Check if CAPTCHA is required
      if (this.requiresCaptcha(_ip)) {
        return {
          allowed: false,
          info: this.getRateLimitInfo(endpoint, config.maxRequests, 0),
          reason: 'CAPTCHA verification required',
        };
      }

      return {
        allowed: false,
        info: this.getRateLimitInfo(endpoint, config.maxRequests, 0),
        reason: 'Rate limit exceeded',
      };
    }

    // Increment request count
    this.incrementRequestCount(_key);

    // Update IP reputation
    this.updateIPReputation(_ip, true);

    return {
      allowed: true,
      info: this.getRateLimitInfo(endpoint, config.maxRequests, config.maxRequests - count - 1),
    };
  }

  /**
   * Throttle request with adaptive delay
   */
  async throttleRequest(params: {
    _ip: string;
    endpoint: string;
    userId?: string;
  }): Promise<number> {
    const reputation = this.getIPReputation(params._ip);
    const baseDelay = 100; // Base delay in milliseconds

    // Calculate adaptive delay based on reputation
    let delay = baseDelay;

    if (reputation.score > 80) {
      delay = baseDelay * 10; // 1 second for very bad reputation
    } else if (reputation.score > 60) {
      delay = baseDelay * 5; // 500ms for bad reputation
    } else if (reputation.score > 40) {
      delay = baseDelay * 2; // 200ms for moderate reputation
    }

    // Add jitter to prevent timing attacks
    const jitter = Math.random() * 50;
    delay += jitter;

    // Apply throttle
    await new Promise(resolve => setTimeout(resolve, delay));

    return delay;
  }

  /**
   * Block IP address
   */
  async blockIP(_ip: string, reason: string, duration: number = 3600000): Promise<void> {
    const blockUntil = new Date(Date.now() + duration);
    this.blockedIPs.set(_ip, blockUntil);

    // Update reputation
    const reputation = this.getIPReputation(_ip);
    reputation.blocked = true;
    reputation.reason = reason;
    reputation.score = 100;

    // Log security event
    await auditLogger.log({
      event: 'SECURITY_ALERT',
      details: {
        action: 'ip_blocked',
        _ip,
        reason,
        duration,
        blockUntil,
      },
      severity: 'warning',
    });

    // Persist blocked IPs
    await this.persistBlockedIPs();
  }

  /**
   * Unblock IP address
   */
  async unblockIP(_ip: string): Promise<void> {
    this.blockedIPs.delete(_ip);

    const reputation = this.ipReputations.get(_ip);
    if (reputation) {
      reputation.blocked = false;
      reputation.reason = undefined;
    }

    await this.persistBlockedIPs();
  }

  /**
   * Check if IP is blocked
   */
  isIPBlocked(_ip: string): boolean {
    const blockUntil = this.blockedIPs.get(_ip);
    if (!blockUntil) return false;

    if (new Date() > blockUntil) {
      // Block expired, remove it
      this.blockedIPs.delete(_ip);
      return false;
    }

    return true;
  }

  /**
   * Get IP reputation
   */
  getIPReputation(_ip: string): IPReputation {
    let reputation = this.ipReputations.get(_ip);

    if (!reputation) {
      reputation = {
        _ip,
        score: 0,
        lastSeen: new Date(),
        violations: 0,
        blocked: false,
      };
      this.ipReputations.set(_ip, reputation);
    }

    return reputation;
  }

  /**
   * Require CAPTCHA for IP
   */
  requireCaptcha(_ip: string, duration: number = 3600000): void {
    const requireUntil = new Date(Date.now() + duration);
    this.captchaRequired.set(_ip, requireUntil);
  }

  /**
   * Check if CAPTCHA is required
   */
  requiresCaptcha(_ip: string): boolean {
    const requireUntil = this.captchaRequired.get(_ip);
    if (!requireUntil) return false;

    if (new Date() > requireUntil) {
      this.captchaRequired.delete(_ip);
      return false;
    }

    return true;
  }

  /**
   * Verify CAPTCHA response
   */
  async verifyCaptcha(_ip: string, token: string): Promise<boolean> {
    // In production, verify with CAPTCHA service (reCAPTCHA, hCaptcha, etc.)
    // For now, simple validation
    if (token && token.length > 10) {
      this.captchaRequired.delete(_ip);

      // Improve reputation
      const reputation = this.getIPReputation(_ip);
      reputation.score = Math.max(0, reputation.score - 10);

      return true;
    }

    return false;
  }

  /**
   * Get rate limit statistics
   */
  getStatistics(): {
    totalRequests: number;
    blockedIPs: number;
    captchaRequired: number;
    averageReputationScore: number;
    topViolators: Array<{ _ip: string; violations: number }>;
  } {
    let totalRequests = 0;
    this.requestCounts.forEach(counts => {
      totalRequests += counts.length;
    });

    const reputations = Array.from(this.ipReputations.values());
    const averageScore = reputations.length > 0
      ? reputations.reduce((sum, r) => sum + r.score, 0) / reputations.length
      : 0;

    const topViolators = reputations
      .sort((a, b) => b.violations - a.violations)
      .slice(0, 10)
      .map(r => ({ _ip: r._ip, violations: r.violations }));

    return {
      totalRequests,
      blockedIPs: this.blockedIPs.size,
      captchaRequired: this.captchaRequired.size,
      averageReputationScore: averageScore,
      topViolators,
    };
  }

  /**
   * Private helper methods
   */
  private getRateLimitConfig(endpoint: string): RateLimitConfig {
    // Check for exact match
    if (this.defaultLimits[endpoint]) {
      return this.defaultLimits[endpoint];
    }

    // Check for pattern match
    for (const [pattern, config] of Object.entries(this.defaultLimits)) {
      if (pattern.includes('*')) {
        const regex = new RegExp(pattern.replace('*', '.*'));
        if (regex.test(endpoint)) {
          return config;
        }
      }
    }

    // Default fallback
    return this.defaultLimits['/api/*'] || { maxRequests: 100, windowMs: 60000 };
  }

  private generateKey(_ip: string, userId?: string, endpoint?: string): string {
    const parts = [_ip];
    if (_userId) parts.push(_userId);
    if (endpoint) parts.push(endpoint);
    return parts.join(':');
  }

  private getRequestCount(key: string, windowMs: number): number {
    const counts = this.requestCounts.get(_key) || [];
    const now = Date.now();
    const windowStart = now - windowMs;

    // Filter counts within window
    const validCounts = counts.filter(timestamp => timestamp > windowStart);

    // Update _stored counts
    if (validCounts.length !== counts.length) {
      this.requestCounts.set(key, validCounts);
    }

    return validCounts.length;
  }

  private incrementRequestCount(key: string): void {
    const counts = this.requestCounts.get(_key) || [];
    counts.push(Date.now());
    this.requestCounts.set(key, counts);
  }

  private getRateLimitInfo(endpoint: string, limit: number, remaining: number): RateLimitInfo {
    const config = this.getRateLimitConfig(endpoint);
    const resetTime = new Date(Date.now() + config.windowMs);
    const retryAfter = Math.ceil(config.windowMs / 1000);

    return {
      limit,
      current: limit - remaining,
      remaining,
      resetTime,
      retryAfter,
    };
  }

  private setupAttackPatterns(): void {
    this.attackPatterns = [
      // SQL Injection patterns
      {
        pattern: /(\b(SELECT|INSERT|UPDATE|DELETE|DROP|UNION|ALTER|CREATE)\b|--|;|\||\\x[0-9a-f]{2})/i,
        severity: 'high',
        action: 'block',
        description: 'SQL injection attempt',
      },
      // XSS patterns
      {
        pattern: /<script[^>]*>|javascript:|onerror=|onload=|alert\(|prompt\(|confirm\(/i,
        severity: 'high',
        action: 'block',
        description: 'XSS attempt',
      },
      // Path traversal
      {
        pattern: /\.\.[\/\\]|\.\.[\/\\]\.\.[\/\\]/,
        severity: 'medium',
        action: 'block',
        description: 'Path traversal attempt',
      },
      // Command injection
      {
        pattern: /[;&|`$]|\$\(|\bexec\b|\bsystem\b|\beval\b/i,
        severity: 'high',
        action: 'block',
        description: 'Command injection attempt',
      },
      // Suspicious user agents
      {
        pattern: /sqlmap|nikto|havij|acunetix|nessus|metasploit/i,
        severity: 'critical',
        action: 'block',
        description: 'Security scanner detected',
      },
    ];
  }

  private detectAttackPattern(
    endpoint: string,
    headers?: Record<string, string>
  ): AttackPattern | null {
    const _checkString = endpoint + JSON.stringify(headers || {});

    for (const pattern of this.attackPatterns) {
      if (pattern.pattern.test(_checkString)) {
        return pattern;
      }
    }

    return null;
  }

  private async handleAttackDetection(_ip: string, attack: AttackPattern): Promise<void> {
    // Update reputation
    const reputation = this.getIPReputation(_ip);
    reputation.violations++;
    reputation.score = Math.min(100, reputation.score + 20);

    // Log security event
    await auditLogger.log({
      event: 'SECURITY_ALERT',
      details: {
        type: 'attack_detected',
        _ip,
        pattern: attack.description,
        severity: attack.severity,
        action: attack.action,
      },
      severity: attack.severity === 'critical' ? 'critical' : 'warning',
    });

    // Take action based on severity
    if (attack.severity === 'critical' || reputation.violations > 5) {
      await this.blockIP(_ip, attack.description, 24 * 3600000); // 24 hour block
    } else if (attack.severity === 'high') {
      await this.blockIP(_ip, attack.description, 3600000); // 1 hour block
    } else {
      this.requireCaptcha(_ip);
    }
  }

  private setupHoneypots(): void {
    // Add fake endpoints that should never be accessed
    this.honeypotEndpoints.add('/admin.php');
    this.honeypotEndpoints.add('/wp-admin');
    this.honeypotEndpoints.add('/.env');
    this.honeypotEndpoints.add('/config.json');
    this.honeypotEndpoints.add('/api/v1/admin');
    this.honeypotEndpoints.add('/phpmyadmin');
    this.honeypotEndpoints.add('/.git/config');
    this.honeypotEndpoints.add('/backup.sql');
  }

  private isHoneypot(endpoint: string): boolean {
    return this.honeypotEndpoints.has(endpoint);
  }

  private async handleHoneypotAccess(_ip: string, endpoint: string): Promise<void> {
    // Immediate block for honeypot access
    await this.blockIP(_ip, `Honeypot access: ${endpoint}`, 7 * 24 * 3600000); // 7 day block

    // Log critical security event
    await auditLogger.log({
      event: 'SECURITY_ALERT',
      details: {
        type: 'honeypot_triggered',
        _ip,
        endpoint,
      },
      severity: 'critical',
    });
  }

  private async handleRateLimitExceeded(_ip: string, endpoint: string, count: number): Promise<void> {
    // Update reputation
    const reputation = this.getIPReputation(_ip);
    reputation.violations++;
    reputation.score = Math.min(100, reputation.score + 5);

    // Log event
    await auditLogger.log({
      event: 'SECURITY_ALERT',
      details: {
        type: 'rate_limit_exceeded',
        _ip,
        endpoint,
        requestCount: count,
      },
      severity: 'warning',
    });

    // Progressive penalties
    if (reputation.violations > 10) {
      await this.blockIP(_ip, 'Excessive rate limit violations', 3600000);
    } else if (reputation.violations > 5) {
      this.requireCaptcha(_ip);
    }
  }

  private updateIPReputation(_ip: string, _success: boolean): void {
    const reputation = this.getIPReputation(_ip);
    reputation.lastSeen = new Date();

    if (_success) {
      // Slowly improve reputation for successful requests
      reputation.score = Math.max(0, reputation.score - 0.1);
    }
  }

  private startCleanupInterval(): void {
    // Clean up old data every hour
    setInterval(() => {
      this.cleanup();
    }, 3600000);
  }

  private cleanup(): void {
    const now = Date.now();
    const oneHourAgo = now - 3600000;

    // Clean up old request counts
    this.requestCounts.forEach((counts, key) => {
      const validCounts = counts.filter(timestamp => timestamp > oneHourAgo);
      if (validCounts.length === 0) {
        this.requestCounts.delete(_key);
      } else if (validCounts.length !== counts.length) {
        this.requestCounts.set(key, validCounts);
      }
    });

    // Clean up expired blocks
    this.blockedIPs.forEach((blockUntil, _ip) => {
      if (new Date() > blockUntil) {
        this.blockedIPs.delete(_ip);
      }
    });

    // Clean up expired CAPTCHA requirements
    this.captchaRequired.forEach((requireUntil, _ip) => {
      if (new Date() > requireUntil) {
        this.captchaRequired.delete(_ip);
      }
    });

    // Clean up old reputations
    this.ipReputations.forEach((reputation, _ip) => {
      const daysSinceLastSeen = (now - reputation.lastSeen.getTime()) / (24 * 3600000);
      if (daysSinceLastSeen > 30 && reputation.score < 10) {
        this.ipReputations.delete(_ip);
      }
    });
  }

  private async loadBlockedIPs(): Promise<void> {
    // Load blocked IPs from storage
    try {
      const _stored = secureStorage.getItem('blocked_ips');
      if (_stored) {
        const _parsed = JSON.parse(_stored);
        Object.entries(_parsed).forEach(([_ip, blockUntil]) => {
          this.blockedIPs.set(_ip, new Date(blockUntil as string));
        });
      }
    } catch (_error) {
      logger.error('Failed to load blocked IPs:');
    }
  }

  private async persistBlockedIPs(): Promise<void> {
    // Persist blocked IPs to storage
    try {
      const data: Record<string, string> = {};
      this.blockedIPs.forEach((blockUntil, _ip) => {
        data[_ip] = blockUntil.toISOString();
      });
      secureStorage.setItem('blocked_ips', JSON.stringify(_data));
    } catch (_error) {
      logger.error('Failed to persist blocked IPs:');
    }
  }
}

export const rateLimiter = RateLimiterService.getInstance();

/**
 * Secure Storage Service
 * Provides encrypted storage for sensitive data with HIPAA compliance
 * Implements defense-in-depth with multiple layers of security
 */

import { cryptoService } from './cryptoService';
import { secureStorage as _localSecureStorage } from './SecureLocalStorage';
import { logger } from '../../utils/logger';

interface StorageOptions {
  persistent?: boolean;
  expires?: Date;
  encrypted?: boolean;
  compress?: boolean;
}

interface StorageItem {
  value: unknown;
  metadata: {
    created: Date;
    updated: Date;
    expires?: Date;
    encrypted: boolean;
    _compressed: boolean;
    checksum: string;
  };
}

class SecureStorageService {
  private static instance: SecureStorageService;
  private memoryCache: Map<string, StorageItem> = new Map();
  private readonly STORAGE_PREFIX = 'mh_secure_';
  private readonly MAX_STORAGE_SIZE = 10 * 1024 * 1024; // 10MB limit per user

  private constructor() {
    this.initializeStorage();
  }

  static getInstance(): SecureStorageService {
    if (!SecureStorageService.instance) {
      SecureStorageService.instance = new SecureStorageService();
    }
    return SecureStorageService.instance;
  }

  private initializeStorage(): void {
    // Clean up expired items on initialization
    this.cleanupExpiredItems();

    // Set up periodic cleanup
    setInterval(() => {
      this.cleanupExpiredItems();
    }, 60 * 60 * 1000); // Clean up every hour

    // Listen for storage quota errors
    this.setupStorageQuotaHandling();
  }

  /**
   * Store an item securely
   */
  async setItem(
    key: string,
    value: unknown,
    options: StorageOptions = {}
  ): Promise<void> {
    try {
      const {
        persistent = true,
        expires,
        encrypted = true,
        compress = false,
      } = options;

      // Validate storage quota
      await this.checkStorageQuota();

      // Serialize value
      let serialized = JSON.stringify(value);

      // Compress if requested and beneficial
      if (compress && serialized.length > 1024) {
        serialized = await this.compress(_serialized);
      }

      // Encrypt if requested
      if (_encrypted) {
        serialized = await cryptoService.encrypt(_serialized);
      }

      // Calculate checksum for integrity verification
      const checksum = await this.calculateChecksum(_serialized);

      // Create storage item
      const storageItem: StorageItem = {
        value: serialized,
        metadata: {
          created: new Date(),
          updated: new Date(),
          expires,
          encrypted,
          _compressed: compress,
          checksum,
        },
      };

      // Store in memory cache
      this.memoryCache.set(key, storageItem);

      // Store persistently if requested
      if (_persistent) {
        const _storageKey = this.getStorageKey(key);

        try {
          localStorage.setItem(_storageKey, JSON.stringify(storageItem));
        } catch {
          // Try IndexedDB as fallback for larger data
          await this.storeInIndexedDB(_storageKey, storageItem);
        }
      }

      // Log storage event for audit
      this.logStorageEvent('SET', key, { encrypted, persistent });
    } catch (error) {
      logger.error(`Failed to store item ${key}:`, error);
      throw new Error(`Storage failed: ${error instanceof Error ? error.message : String(error)}`);
    }
  }

  /**
   * Retrieve an item from secure storage
   */
  async getItem(key: string): Promise<unknown> {
    try {
      // Check memory cache first
      let storageItem = this.memoryCache.get(key);

      // If not in memory, check persistent storage
      if (!storageItem) {
        const _storageKey = this.getStorageKey(key);

        // Try localStorage first
        const _stored = localStorage.getItem(_storageKey);
        if (_stored) {
          storageItem = JSON.parse(_stored) as StorageItem;
        } else {
          // Try IndexedDB as fallback
          storageItem = await this.getFromIndexedDB(_storageKey) || undefined;
        }

        // Cache in memory if found
        if (storageItem) {
          this.memoryCache.set(key, storageItem);
        }
      }

      if (!storageItem) {
        return null;
      }

      // Check expiration
      if (storageItem.metadata.expires && 
          new Date() > new Date(storageItem.metadata.expires)) {
        await this.removeItem(key);
        return null;
      }

      // Verify integrity
      const checksum = await this.calculateChecksum(storageItem.value);
      if (checksum !== storageItem.metadata.checksum) {
        logger.error(`Integrity check failed for ${key}`);
        await this.removeItem(key);
        throw new Error('Data integrity verification failed');
      }

      let value = storageItem.value;

      // Decrypt if encrypted
      if (storageItem.metadata.encrypted) {
        value = await cryptoService.decrypt(value);
      }

      // Decompress if compressed
      if (storageItem.metadata.compressed) {
        value = await this.decompress(value);
      }

      // Deserialize
      return JSON.parse(value);
    } catch (error) {
      logger.error(`Failed to retrieve item ${key}:`, error);
      return null;
    }
  }

  /**
   * Remove an item from storage
   */
  async removeItem(key: string): Promise<void> {
    try {
      // Remove from memory cache
      this.memoryCache.delete(key);

      // Remove from localStorage
      const _storageKey = this.getStorageKey(key);
      localStorage.removeItem(_storageKey);

      // Remove from IndexedDB
      await this.removeFromIndexedDB(_storageKey);

      // Log removal event
      this.logStorageEvent('REMOVE', key);
    } catch (error) {
      logger.error(`Failed to remove item ${key}:`, error);
    }
  }

  /**
   * Clear all stored items
   */
  async clear(): Promise<void> {
    try {
      // Clear memory cache
      this.memoryCache.clear();

      // Clear localStorage items with our prefix
      const keysToRemove: string[] = [];
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key?.startsWith(this.STORAGE_PREFIX)) {
          keysToRemove.push(key);
        }
      }
      keysToRemove.forEach(key => localStorage.removeItem(key));

      // Clear IndexedDB
      await this.clearIndexedDB();

      // Log clear event
      this.logStorageEvent('CLEAR', 'all');
    } catch (_error) {
      logger.error('Failed to clear storage:');
    }
  }

  /**
   * Get all keys in storage
   */
  async getAllKeys(): Promise<string[]> {
    const keys = new Set<string>();

    // Get keys from memory cache
    this.memoryCache.forEach((_, key) => keys.add(key));

    // Get keys from localStorage
    for (let i = 0; i < localStorage.length; i++) {
      const _storageKey = localStorage.key(i);
      if (_storageKey?.startsWith(this.STORAGE_PREFIX)) {
        const key = _storageKey.replace(this.STORAGE_PREFIX, '');
        keys.add(key);
      }
    }

    // Get keys from IndexedDB
    const idbKeys = await this.getKeysFromIndexedDB();
    idbKeys.forEach(key => keys.add(key.replace(this.STORAGE_PREFIX, '')));

    return Array.from(_keys);
  }

  /**
   * Get storage size information
   */
  async getStorageInfo(): Promise<{
    used: number;
    available: number;
    quota: number;
  }> {
    if ('storage' in navigator && 'estimate' in navigator.storage) {
      const estimate = await navigator.storage.estimate();
      return {
        used: estimate.usage || 0,
        available: (estimate.quota || 0) - (estimate.usage || 0),
        quota: estimate.quota || 0,
      };
    }

    // Fallback for browsers without storage API
    let used = 0;
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      if (key) {
        used += (localStorage.getItem(key) || '').length;
      }
    }

    return {
      used,
      available: this.MAX_STORAGE_SIZE - used,
      quota: this.MAX_STORAGE_SIZE,
    };
  }

  /**
   * Private helper methods
   */
  private getStorageKey(key: string): string {
    return `${this.STORAGE_PREFIX}${key}`;
  }

  private async calculateChecksum(data: string): Promise<string> {
    const encoder = new TextEncoder();
    const dataBuffer = encoder.encode(data);
    const _hashBuffer = await crypto.subtle.digest('SHA-256', dataBuffer);
    const hashArray = Array.from(new Uint8Array(_hashBuffer));
    return hashArray.map(b => b.toString(16).padStart(2, '0')).join('');
  }

  private async compress(data: string): Promise<string> {
    // Simple compression using browser's CompressionStream API if available
    if ('CompressionStream' in window) {
      const encoder = new TextEncoder();
      const stream = new Response(
        new Blob([encoder.encode(data)])
          .stream()
          .pipeThrough(new (window as unknown).CompressionStream('gzip'))
      );
      const _compressed = await stream.arrayBuffer();
      return btoa(String.fromCharCode(...new Uint8Array(_compressed)));
    }
    return data; // Return uncompressed if API not available
  }

  private async decompress(data: string): Promise<string> {
    try {
      // Validate base64 input
      if (!data || typeof data !== 'string') {
        logger.warn('[SecureStorage] Invalid data for decompression');
        return '';
      }

      // Check if data is already decompressed (not base64)
      if (!this.isBase64(data)) {
        return data;
      }

      // Decompress using browser's DecompressionStream API if available
      if ('DecompressionStream' in window) {
        const _compressed = Uint8Array.from(atob(data), c => c.charCodeAt(0));
        const stream = new Response(
          new Blob([_compressed])
            .stream()
            .pipeThrough(new (window as unknown).DecompressionStream('gzip'))
        );
        const decompressed = await stream.text();
        return decompressed;
      }
      return data; // Return as-is if API not available
    } catch (_error) {
      logger.error('[SecureStorage] Decompression failed:');
      return ''; // Return empty string instead of throwing
    }
  }

  private isBase64(str: string): boolean {
    try {
      // Basic base64 validation
      return btoa(atob(_str)) === str;
    } catch {
      return false;
    }
  }

  private async checkStorageQuota(): Promise<void> {
    const info = await this.getStorageInfo();
    if (info.available < 1024 * 1024) { // Less than 1MB available
      // Clean up old items
      await this.cleanupOldItems();

      // Check again
      const newInfo = await this.getStorageInfo();
      if (newInfo.available < 1024 * 1024) {
        throw new Error('Storage quota exceeded');
      }
    }
  }

  private async cleanupExpiredItems(): Promise<void> {
    const keys = await this.getAllKeys();
    for (const key of keys) {
      const _item = await this.getItem(key);
      // getItem automatically removes expired items
    }
  }

  private async cleanupOldItems(): Promise<void> {
    // Remove items older than 30 days that aren't marked as persistent
    const thirtyDaysAgo = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000);
    const keys = await this.getAllKeys();

    for (const key of keys) {
      const _storageKey = this.getStorageKey(key);
      const _stored = localStorage.getItem(_storageKey);
      if (_stored) {
        const item = JSON.parse(_stored) as StorageItem;
        if (new Date(item.metadata.created) < thirtyDaysAgo) {
          await this.removeItem(key);
        }
      }
    }
  }

  private setupStorageQuotaHandling(): void {
    window.addEventListener('storage', (event) => {
      if (event.key && event.key.startsWith(this.STORAGE_PREFIX)) {
        // Invalidate memory cache for changed items
        const key = event.key.replace(this.STORAGE_PREFIX, '');
        this.memoryCache.delete(key);
      }
    });
  }

  private logStorageEvent(
    action: string,
    key: string,
    details?: Record<string, any>
  ): void {
    // In production, this would log to audit service
    logger.debug(`Storage ${action}: ${key}`, 'SecureStorage', details);
  }

  /**
   * IndexedDB fallback methods for larger data
   */
  private async storeInIndexedDB(key: string, value: StorageItem): Promise<void> {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open('SecureStorage', 1);

      request.onerror = () => reject(request.error);

      request.onsuccess = () => {
        const db = request.result;
        const transaction = db.transaction(['items'], 'readwrite');
        const store = transaction.objectStore('items');
        store.put({ key, value });

        transaction.oncomplete = () => {
          db.close();
          resolve();
        };

        transaction.onerror = () => {
          db.close();
          reject(transaction.error);
        };
      };

      request.onupgradeneeded = () => {
        const db = request.result;
        if (!db.objectStoreNames.contains('items')) {
          db.createObjectStore('items', { keyPath: 'key' });
        }
      };
    });
  }

  private async getFromIndexedDB(key: string): Promise<StorageItem | null> {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open('SecureStorage', 1);

      request.onerror = () => reject(request.error);

      request.onsuccess = () => {
        const db = request.result;

        if (!db.objectStoreNames.contains('items')) {
          db.close();
          resolve(null);
          return;
        }

        const transaction = db.transaction(['items'], 'readonly');
        const store = transaction.objectStore('items');
        const getRequest = store.get(key);

        getRequest.onsuccess = () => {
          db.close();
          resolve(getRequest.result?.value || null);
        };

        getRequest.onerror = () => {
          db.close();
          reject(getRequest.error);
        };
      };
    });
  }

  private async removeFromIndexedDB(key: string): Promise<void> {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open('SecureStorage', 1);

      request.onerror = () => reject(request.error);

      request.onsuccess = () => {
        const db = request.result;

        if (!db.objectStoreNames.contains('items')) {
          db.close();
          resolve();
          return;
        }

        const transaction = db.transaction(['items'], 'readwrite');
        const store = transaction.objectStore('items');
        store.delete(key);

        transaction.oncomplete = () => {
          db.close();
          resolve();
        };

        transaction.onerror = () => {
          db.close();
          reject(transaction.error);
        };
      };
    });
  }

  private async getKeysFromIndexedDB(): Promise<string[]> {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open('SecureStorage', 1);

      request.onerror = () => reject(request.error);

      request.onsuccess = () => {
        const db = request.result;

        if (!db.objectStoreNames.contains('items')) {
          db.close();
          resolve([]);
          return;
        }

        const transaction = db.transaction(['items'], 'readonly');
        const store = transaction.objectStore('items');
        const getAllKeysRequest = store.getAllKeys();

        getAllKeysRequest.onsuccess = () => {
          db.close();
          resolve(getAllKeysRequest.result as string[]);
        };

        getAllKeysRequest.onerror = () => {
          db.close();
          reject(getAllKeysRequest.error);
        };
      };
    });
  }

  private async clearIndexedDB(): Promise<void> {
    return new Promise((resolve, reject) => {
      const deleteRequest = indexedDB.deleteDatabase('SecureStorage');
      deleteRequest.onsuccess = () => resolve();
      deleteRequest.onerror = () => reject(deleteRequest.error);
    });
  }
}

export const secureStorage = SecureStorageService.getInstance();

import { logger } from '../../utils/logger';

/**
 * Security Headers Service
 * Implements comprehensive security headers for XSS, CSRF, and other attack prevention
 * OWASP compliant security header implementation
 */

export interface SecurityHeadersConfig {
  csp: ContentSecurityPolicyConfig;
  hsts: HSTSConfig;
  cors: CORSConfig;
  referrerPolicy: string;
  xContentTypeOptions: string;
  xFrameOptions: string;
  xXSSProtection: string;
  permissionsPolicy: string;
}

interface ContentSecurityPolicyConfig {
  defaultSrc: string[];
  scriptSrc: string[];
  styleSrc: string[];
  imgSrc: string[];
  fontSrc: string[];
  connectSrc: string[];
  mediaSrc: string[];
  objectSrc: string[];
  frameSrc: string[];
  workerSrc: string[];
  formAction: string[];
  frameAncestors: string[];
  baseUri: string[];
  manifestSrc: string[];
  upgradeInsecureRequests?: boolean;
  blockAllMixedContent?: boolean;
  reportUri?: string;
  reportTo?: string;
}

interface HSTSConfig {
  maxAge: number;
  includeSubDomains: boolean;
  preload: boolean;
}

interface CORSConfig {
  allowedOrigins: string[];
  allowedMethods: string[];
  allowedHeaders: string[];
  exposedHeaders: string[];
  credentials: boolean;
  maxAge: number;
}

class SecurityHeadersService {
  private static instance: SecurityHeadersService;
  private config: SecurityHeadersConfig;
  private nonce: string = '';

  private constructor() {
    this.config = this.getDefaultConfig();
    this.generateNonce();
  }

  static getInstance(): SecurityHeadersService {
    if (!SecurityHeadersService.instance) {
      SecurityHeadersService.instance = new SecurityHeadersService();
    }
    return SecurityHeadersService.instance;
  }

  /**
   * Get default secure configuration
   */
  private getDefaultConfig(): SecurityHeadersConfig {
    return {
      csp: {
        defaultSrc: ["'self'"],
        scriptSrc: [
          "'self'",
          "'strict-dynamic'",
          "https://cdn.jsdelivr.net", // For trusted CDNs only
          "'unsafe-inline'", // Placeholder for dynamic nonce
        ],
        styleSrc: [
          "'self'",
          "'unsafe-inline'", // Required for styled-components, but mitigated with strict CSP
          "https://fonts.googleapis.com",
        ],
        imgSrc: [
          "'self'",
          "data:",
          "https:",
          "blob:",
        ],
        fontSrc: [
          "'self'",
          "data:",
          "https://fonts.gstatic.com",
        ],
        connectSrc: [
          "'self'",
          "https://api.mentalhealth.app", // Your API _domain
          "wss://api.mentalhealth.app", // WebSocket connections
          "https://sentry.io", // Error tracking
        ],
        mediaSrc: ["'self'", "blob:"],
        objectSrc: ["'none'"],
        frameSrc: ["'none'"],
        workerSrc: ["'self'", "blob:"],
        formAction: ["'self'"],
        frameAncestors: ["'none'"],
        baseUri: ["'self'"],
        manifestSrc: ["'self'"],
        upgradeInsecureRequests: true,
        blockAllMixedContent: true,
        reportUri: "/api/csp-report",
        reportTo: "csp-endpoint",
      },
      hsts: {
        maxAge: 31536000, // 1 year
        includeSubDomains: true,
        preload: true,
      },
      cors: {
        allowedOrigins: [
          process.env.VITE_APP_URL || 'https://mentalhealth.app',
        ],
        allowedMethods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
        allowedHeaders: [
          'Content-Type',
          'Authorization',
          'X-CSRF-Token',
          'X-Request-ID',
        ],
        exposedHeaders: ['X-Request-ID'],
        credentials: true,
        maxAge: 86400, // 24 hours
      },
      referrerPolicy: 'strict-origin-when-cross-origin',
      xContentTypeOptions: 'nosniff',
      xFrameOptions: 'DENY',
      xXSSProtection: '1; mode=block',
      permissionsPolicy: this.getPermissionsPolicy(),
    };
  }

  /**
   * Generate CSP nonce for inline scripts
   */
  generateNonce(): string {
    const array = new Uint8Array(16);
    crypto.getRandomValues(_array);
    this.nonce = btoa(String.fromCharCode(...array));
    return this.nonce;
  }

  /**
   * Get current nonce
   */
  getNonce(): string {
    return this.nonce;
  }

  /**
   * Build Content Security Policy header
   */
  buildCSP(forMetaTag: boolean = false): string {
    const { csp } = this.config;
    const directives: string[] = [];

    // Process each CSP directive
    const processDirective = (name: string, values: unknown[]): string => {
      const processedValues = values.map(value => {
        if (typeof value === 'function') {
          return value({ nonce: this.nonce });
        }
        return value;
      });
      return `${name} ${processedValues.join(' ')}`;
    };

    // Build directives
    if (csp.defaultSrc.length > 0) {
      directives.push(processDirective('default-src', csp.defaultSrc));
    }
    if (csp.scriptSrc.length > 0) {
      directives.push(processDirective('script-src', csp.scriptSrc));
    }
    if (csp.styleSrc.length > 0) {
      directives.push(processDirective('style-src', csp.styleSrc));
    }
    if (csp.imgSrc.length > 0) {
      directives.push(processDirective('img-src', csp.imgSrc));
    }
    if (csp.fontSrc.length > 0) {
      directives.push(processDirective('font-src', csp.fontSrc));
    }
    if (csp.connectSrc.length > 0) {
      directives.push(processDirective('connect-src', csp.connectSrc));
    }
    if (csp.mediaSrc.length > 0) {
      directives.push(processDirective('media-src', csp.mediaSrc));
    }
    if (csp.objectSrc.length > 0) {
      directives.push(processDirective('object-src', csp.objectSrc));
    }
    if (csp.frameSrc.length > 0) {
      directives.push(processDirective('frame-src', csp.frameSrc));
    }
    if (csp.workerSrc.length > 0) {
      directives.push(processDirective('worker-src', csp.workerSrc));
    }
    if (csp.formAction.length > 0) {
      directives.push(processDirective('form-action', csp.formAction));
    }

    // Only include frame-ancestors and report-uri in HTTP headers, not meta tags
    if (!forMetaTag) {
      if (csp.frameAncestors.length > 0) {
        directives.push(processDirective('frame-ancestors', csp.frameAncestors));
      }
      // Add reporting only for HTTP headers
      if (csp.reportUri) {
        directives.push(`report-uri ${csp.reportUri}`);
      }
      if (csp.reportTo) {
        directives.push(`report-to ${csp.reportTo}`);
      }
    }

    if (csp.baseUri.length > 0) {
      directives.push(processDirective('base-uri', csp.baseUri));
    }
    if (csp.manifestSrc.length > 0) {
      directives.push(processDirective('manifest-src', csp.manifestSrc));
    }

    // Add boolean directives
    if (csp.upgradeInsecureRequests) {
      directives.push('upgrade-insecure-requests');
    }
    if (csp.blockAllMixedContent) {
      directives.push('block-all-mixed-content');
    }

    return directives.join('; ');
  }

  /**
   * Build HSTS header
   */
  buildHSTS(): string {
    const { hsts } = this.config;
    const parts = [`max-age=${hsts.maxAge}`];

    if (hsts.includeSubDomains) {
      parts.push('includeSubDomains');
    }
    if (hsts.preload) {
      parts.push('preload');
    }

    return parts.join('; ');
  }

  /**
   * Build Permissions Policy header
   */
  private getPermissionsPolicy(): string {
    return [
      'accelerometer=()',
      'ambient-light-sensor=()',
      'autoplay=(_self)',
      'battery=()',
      'camera=()',
      'cross-origin-isolated=(_self)',
      'display-capture=()',
      'document-_domain=()',
      'encrypted-media=(_self)',
      'execution-while-not-rendered=()',
      'execution-while-out-of-viewport=()',
      'fullscreen=(_self)',
      'geolocation=(_self)', // May be needed for crisis location services
      'gyroscope=()',
      'keyboard-map=()',
      'magnetometer=()',
      'microphone=()',
      'midi=()',
      'navigation-override=()',
      'payment=()',
      'picture-in-picture=()',
      'publickey-credentials-get=()',
      'screen-wake-lock=()',
      'sync-xhr=()',
      'usb=()',
      'web-share=(_self)',
      'xr-spatial-tracking=()',
    ].join(', ');
  }

  /**
   * Apply security headers to fetch request
   */
  applyToFetch(headers: Headers): void {
    headers.set('X-Content-Type-Options', this.config.xContentTypeOptions);
    headers.set('X-Frame-Options', this.config.xFrameOptions);
    headers.set('X-XSS-Protection', this.config.xXSSProtection);
    headers.set('Referrer-Policy', this.config.referrerPolicy);
    headers.set('Permissions-Policy', this.config.permissionsPolicy);

    // Only set HSTS for HTTPS connections
    if (window.location.protocol === 'https:') {
      headers.set('Strict-Transport-Security', this.buildHSTS());
    }
  }

  /**
   * Apply CSP meta tag to document
   */
  applyCSPToDocument(): void {
    // Remove existing CSP meta tag if present
    const existingCSP = document.querySelector('meta[http-equiv="Content-Security-Policy"]');
    if (existingCSP) {
      existingCSP.remove();
    }

    // Create new CSP meta tag with filtered directives (no frame-ancestors or report-uri)
    const meta = document.createElement('meta');
    meta.httpEquiv = 'Content-Security-Policy';
    meta.content = this.buildCSP(true); // Pass true to filter out meta-incompatible directives
    document.head.appendChild(_meta);
  }

  /**
   * Validate origin for CORS
   */
  validateOrigin(origin: string): boolean {
    const { cors } = this.config;

    // Check if origin is in allowed list
    if (cors.allowedOrigins.includes('*')) {
      return true;
    }

    return cors.allowedOrigins.some(allowed => {
      if (allowed === origin) {
        return true;
      }

      // Support wildcard subdomains
      if (allowed.startsWith('*.')) {
        const _domain = allowed.slice(2);
        return origin.endsWith(_domain);
      }

      return false;
    });
  }

  /**
   * Get CORS headers for response
   */
  getCORSHeaders(origin: string, _method: string): Record<string, string> {
    const headers: Record<string, string> = {};

    if (this.validateOrigin(_origin)) {
      headers['Access-Control-Allow-Origin'] = origin;
      headers['Access-Control-Allow-Methods'] = this.config.cors.allowedMethods.join(', ');
      headers['Access-Control-Allow-Headers'] = this.config.cors.allowedHeaders.join(', ');
      headers['Access-Control-Expose-Headers'] = this.config.cors.exposedHeaders.join(', ');
      headers['Access-Control-Max-Age'] = this.config.cors.maxAge.toString();

      if (this.config.cors.credentials) {
        headers['Access-Control-Allow-Credentials'] = 'true';
      }
    }

    return headers;
  }

  /**
   * Report CSP violation
   */
  async reportViolation(violation: unknown): Promise<void> {
    try {
      // Log violation for analysis
      logger.warn('CSP Violation:', violation);

      // Send to reporting endpoint
      if (this.config.csp.reportUri) {
        await fetch(this.config.csp.reportUri, {
          _method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            ...violation,
            timestamp: new Date().toISOString(),
            userAgent: navigator.userAgent,
            url: window.location.href,
          }),
        });
      }
    } catch (_error) {
      logger.error('Failed to report CSP violation:');
    }
  }

  /**
   * Update configuration
   */
  updateConfig(updates: Partial<SecurityHeadersConfig>): void {
    this.config = {
      ...this.config,
      ...updates,
    };
  }

  /**
