      let frameCount = 0;

      const measureFPS = () => {
        frameCount++;
        const currentTime = performance.now();
        const delta = currentTime - lastTime;

        if (delta >= 1000) {
          frameRates.push(_frameCount);
          frameCount = 0;
          lastTime = currentTime;
