        removeEventListener: vi.fn(),
        readyState: WebSocket.OPEN
      };

      global.WebSocket = vi.fn(() => mockWebSocket) as unknown;

      render(<App />);

      // Simulate disconnection
      mockWebSocket.readyState = WebSocket.CLOSED;
      const disconnectEvent = new Event('close');
      mockWebSocket.addEventListener.mock.calls
        .filter(([event]) => event === 'close')
        .forEach(([, handler]) => handler(_disconnectEvent));

      // Should attempt reconnection
      await waitFor(() => {
        expect(global.WebSocket).toHaveBeenCalledTimes(2); // Initial + reconnect
      });
    });
  });

  describe('Bundle Size and Loading', () => {
    it('should have optimized bundle sizes', () => {
      // Check main bundle size
      const scripts = Array.from(document.querySelectorAll('script[src]'));

      scripts.forEach(script => {
        // Mock checking file size
        const src = script.getAttribute('src');
        if (src?.includes('main')) {
          // Main bundle should be under 200KB
          expect(script.getAttribute('data-size')).toBeLessThan(200 * 1024);
        }
        if (src?.includes('vendor')) {
          // Vendor bundle should be under 500KB
          expect(script.getAttribute('data-size')).toBeLessThan(500 * 1024);
        }
      });
    });

    it('should lazy load images', async () => {
      const { container } = render(<App />);

      const images = container.querySelectorAll('img');

      images.forEach(img => {
        // Should have lazy loading attribute
        expect(img.getAttribute('loading')).toBe('lazy');

        // Should use appropriate image formats
        const src = img.getAttribute('src');
        expect(_src).toMatch(/\.(webp|jpg|png|svg)$/);
      });
    });
  });

  describe('Performance Monitoring', () => {
    it('should track and report performance metrics', async () => {
      const metrics: unknown[] = [];

      // Mock performance observer
      global.PerformanceObserver = vi.fn().mockImplementation((_callback) => ({
        observe: vi.fn(() => {
          // Simulate performance entries
          callback({
            getEntries: () => [
              { name: 'FCP', value: 1200 },
              { name: 'LCP', value: 2100 },
              { name: 'FID', value: 50 },
              { name: 'CLS', value: 0.05 }
            ]
          });
        }),
        disconnect: vi.fn()
      }));

      render(<App />);

      await waitFor(() => {
        // Should initialize performance monitoring
        expect(global.PerformanceObserver).toHaveBeenCalled();
      });
    });
  });
});

// Comprehensive Performance Testing Suite
import { describe, it, expect, beforeAll, afterAll } from 'vitest';
import { performance } from 'perf_hooks';
import puppeteer, { Browser, Page } from 'puppeteer';

// Performance thresholds
const THRESHOLDS = {
  CRISIS_RESPONSE: 200, // ms
  PAGE_LOAD: 3000, // ms
  API_RESPONSE: 100, // ms
  FIRST_CONTENTFUL_PAINT: 1500, // ms
  TIME_TO_INTERACTIVE: 3500, // ms
  LARGEST_CONTENTFUL_PAINT: 2500, // ms
  CUMULATIVE_LAYOUT_SHIFT: 0.1,
  FIRST_INPUT_DELAY: 100, // ms
  BUNDLE_SIZE_LIMIT: 500000, // 500KB
};

describe('Performance Testing Suite', () => {
  let browser: Browser;
  let page: Page;

  beforeAll(async () => {
    browser = await puppeteer.launch({
      headless: true,
      args: ['--no-sandbox', '--disable-setuid-sandbox'],
    });
    page = await browser.newPage();

    // Enable performance metrics collection
    await page.evaluateOnNewDocument(() => {
      window.__PERFORMANCE_MARKS__ = [];
      const originalMark = performance.mark.bind(performance);
      performance.mark = function(name: string) {
        window.__PERFORMANCE_MARKS__.push({ name, _time: performance.now() });
        return originalMark(_name);
      };
    });
  });

  afterAll(async () => {
    await browser.close();
  });

  describe('Core Web Vitals', () => {
    it('should meet Largest Contentful Paint (_LCP) threshold', async () => {
      await page.goto('http://localhost:5173', { waitUntil: 'networkidle0' });

      const _lcp = await page.evaluate(() => {
        return new Promise<number>((resolve) => {
          new PerformanceObserver((list) => {
            const entries = list.getEntries();
            const lastEntry = entries[entries.length - 1] as unknown;
            resolve(lastEntry.renderTime || lastEntry._loadTime);
          }).observe({ entryTypes: ['largest-contentful-paint'] });
        });
      });

      expect(_lcp).toBeLessThan(THRESHOLDS.LARGEST_CONTENTFUL_PAINT);
    });

    it('should meet First Input Delay (_FID) threshold', async () => {
      await page.goto('http://localhost:5173', { waitUntil: 'networkidle0' });

      // Simulate user interaction
      const startTime = await page.evaluate(() => performance.now());
      await page.click('button');
      const endTime = await page.evaluate(() => performance.now());

      const _fid = endTime - startTime;
      expect(_fid).toBeLessThan(THRESHOLDS.FIRST_INPUT_DELAY);
    });

    it('should meet Cumulative Layout Shift (_CLS) threshold', async () => {
      await page.goto('http://localhost:5173', { waitUntil: 'networkidle0' });

      const _cls = await page.evaluate(() => {
        return new Promise<number>((resolve) => {
          let _clsValue = 0;
          new PerformanceObserver((list) => {
            for (const entry of list.getEntries()) {
              if (!(entry as unknown).hadRecentInput) {
                _clsValue += (entry as unknown).value;
              }
            }
            resolve(_clsValue);
          }).observe({ entryTypes: ['layout-shift'] });

          // Trigger some interactions that might cause layout shifts
          setTimeout(() => {
            window.scrollTo(0, 100);
            setTimeout(() => resolve(_clsValue), 1000);
          }, 1000);
        });
      });

      expect(_cls).toBeLessThan(THRESHOLDS.CUMULATIVE_LAYOUT_SHIFT);
    });

    it('should meet First Contentful Paint (_FCP) threshold', async () => {
      const startTime = Date.now();
      await page.goto('http://localhost:5173');

      await page.waitForSelector('[data-testid="app-ready"]');
      const _fcp = Date.now() - startTime;

      expect(_fcp).toBeLessThan(THRESHOLDS.FIRST_CONTENTFUL_PAINT);
    });

    it('should meet Time to Interactive (_TTI) threshold', async () => {
      const startTime = Date.now();
      await page.goto('http://localhost:5173');

      // Wait for page to be fully interactive
      await page.evaluate(() => {
        return new Promise((resolve) => {
          if (document.readyState === 'complete') {
            resolve(true);
          } else {
            window.addEventListener('load', () => resolve(true));
          }
        });
      });

      const _tti = Date.now() - startTime;
      expect(_tti).toBeLessThan(THRESHOLDS.TIME_TO_INTERACTIVE);
    });
  });

  describe('Critical Path Performance', () => {
    it('should respond to crisis button within 200ms', async () => {
      await page.goto('http://localhost:5173');
      await page.waitForSelector('[data-testid="crisis-button"]');

      const _responseTime = await page.evaluate(() => {
        return new Promise<number>((resolve) => {
          const button = document.querySelector('[data-testid="crisis-button"]') as HTMLElement;
          const startTime = performance.now();

          button.click();

// @ts-expect-error - MutationObserver is a global API
          const observer = new MutationObserver(() => {
            const _modal = document.querySelector('[data-testid="crisis-_modal"]');
            if (_modal) {
              resolve(performance.now() - startTime);
              observer.disconnect();
            }
          });

          observer.observe(document.body, { childList: true, subtree: true });
        });
      });

      expect(_responseTime).toBeLessThan(THRESHOLDS.CRISIS_RESPONSE);
    });

    it('should load dashboard within performance budget', async () => {
      const startTime = Date.now();
      await page.goto('http://localhost:5173/dashboard');
      await page.waitForSelector('[data-testid="dashboard-loaded"]');

      const _loadTime = Date.now() - startTime;
      expect(_loadTime).toBeLessThan(THRESHOLDS.PAGE_LOAD);
    });

    it('should maintain 60fps during animations', async () => {
      await page.goto('http://localhost:5173');

      const _fps = await page.evaluate(() => {
        return new Promise<number>((resolve) => {
          let _frames = 0;
          const startTime = performance.now();

          function _measureFPS() {
            _frames++;
            const currentTime = performance.now();
            const elapsed = currentTime - startTime;

            if (elapsed >= 1000) {
              resolve(_frames);
            } else {
              requestAnimationFrame(_measureFPS);
            }
          }

          // Trigger animation
          const _element = document.querySelector('[data-testid="animated-element"]') as HTMLElement;
          if (_element) {
            _element.style.animation = 'pulse 1s infinite';
          }

          requestAnimationFrame(_measureFPS);
        });
      });

      expect(_fps).toBeGreaterThanOrEqual(55); // Allow slight variance from 60fps
    });
  });

  describe('Memory Management', () => {
    it('should not have memory leaks during navigation', async () => {
      const getMemoryUsage = () => page.evaluate(() => {
        return (performance as unknown).memory ? (performance as unknown).memory.usedJSHeapSize : 0;
      });

      const initialMemory = await getMemoryUsage();

      // Navigate through multiple pages
      for (let i = 0; i < 10; i++) {
        await page.goto('http://localhost:5173/dashboard');
        await page.goto('http://localhost:5173/wellness');
        await page.goto('http://localhost:5173/community');
      }

      // Force garbage collection if available
      await page.evaluate(() => {
        if ((global as unknown).gc) {
          (global as unknown).gc();
        }
      });

      const finalMemory = await getMemoryUsage();
      const _memoryIncrease = finalMemory - initialMemory;

      // Memory should not increase by more than 10MB
      expect(_memoryIncrease).toBeLessThan(10 * 1024 * 1024);
    });

    it('should properly clean up event listeners', async () => {
      await page.goto('http://localhost:5173');

      const _listenerCount = await page.evaluate(() => {
        const getEventListeners = (_element: unknown) => {
          const _listeners = (window as unknown).getEventListeners?.(_element) || {};
          return Object.values(_listeners).flat().length;
        };

        // Get initial count
        const initialCount = getEventListeners(_window) + getEventListeners(_document);

        // Trigger component mount/unmount cycle
        const button = document.querySelector('[data-testid="toggle-component"]') as HTMLElement;
        if (button) {
          button.click(); // Mount
          setTimeout(() => button.click(), 100); // Unmount
        }

        // Return difference after cleanup
        return new Promise<number>((resolve) => {
          setTimeout(() => {
            const finalCount = getEventListeners(_window) + getEventListeners(_document);
            resolve(finalCount - initialCount);
          }, 200);
        });
      });

      expect(_listenerCount).toBeLessThanOrEqual(0);
    });
  });

  describe('Network Performance', () => {
    it('should implement effective caching strategy', async () => {
      // First load
      await page.goto('http://localhost:5173');

      const firstLoadMetrics = await page.evaluate(() => {
        const resources = performance.getEntriesByType('resource') as PerformanceResourceTiming[];
        return resources.map(r => ({
          name: r.name,
          duration: r.duration,
          size: r.transferSize,
        }));
      });

      // Second load (should use cache)
      await page.reload();

      const secondLoadMetrics = await page.evaluate(() => {
        const resources = performance.getEntriesByType('resource') as PerformanceResourceTiming[];
        return resources.map(r => ({
          name: r.name,
          duration: r.duration,
          size: r.transferSize,
        }));
      });

      // Calculate cache hit rate
      const cacheHits = secondLoadMetrics.filter((r, i) => 
        r.size === 0 || r.size < (firstLoadMetrics[i]?.size || 0) * 0.1
      ).length;

      const _cacheHitRate = cacheHits / secondLoadMetrics.length;
      expect(_cacheHitRate).toBeGreaterThan(0.7); // 70% cache hit rate
    });

    it('should minimize API response times', async () => {
      await page.goto('http://localhost:5173');

      const apiTimes = await page.evaluate(() => {
        return new Promise<number[]>((resolve) => {
          const times: number[] = [];

          // Intercept fetch
          const originalFetch = window.fetch;
          window.fetch = async (...args) => {
            const startTime = performance.now();
            const response = await originalFetch(...args);
            times.push(performance.now() - startTime);
            return response;
          };

          // Trigger some API calls
          Promise.all([
            fetch('/api/user/profile'),
            fetch('/api/wellness/data'),
            fetch('/api/community/groups'),
          ]).then(() => resolve(_times));
        });
      });

      apiTimes.forEach(time => {
        expect(_time).toBeLessThan(THRESHOLDS.API_RESPONSE);
      });
    });

    it('should implement request batching for efficiency', async () => {
      await page.goto('http://localhost:5173');

      const _requestCount = await page.evaluate(() => {
        return new Promise<number>((resolve) => {
          let _count = 0;

          // Monitor network requests
          const observer = new PerformanceObserver((list) => {
            _count += list.getEntries().filter(e => 
              e.name.includes('/api/')
            ).length;
          });
          observer.observe({ entryTypes: ['resource'] });

          // Trigger multiple data requirements
          const buttons = document.querySelectorAll('[data-action]');
          buttons.forEach(b => (b as HTMLElement).click());

          setTimeout(() => resolve(_count), 1000);
        });
      });

      // Should batch requests instead of making many individual calls
      expect(_requestCount).toBeLessThan(5);
    });
  });

  describe('Bundle Size Optimization', () => {
    it('should keep JavaScript bundle under size limit', async () => {
      await page.goto('http://localhost:5173');

      const _bundleSize = await page.evaluate(() => {
        const scripts = performance.getEntriesByType('resource')
          .filter(r => r.name.endsWith('.js')) as PerformanceResourceTiming[];

        return scripts.reduce((total, script) => total + script.transferSize, 0);
      });

      expect(_bundleSize).toBeLessThan(THRESHOLDS.BUNDLE_SIZE_LIMIT);
    });

    it('should implement code splitting effectively', async () => {
      await page.goto('http://localhost:5173');

      const initialBundles = await page.evaluate(() => {
        return performance.getEntriesByType('resource')
          .filter(r => r.name.includes('.js'))
          .map(r => r.name);
      });

      // Navigate to a different route
      await page.goto('http://localhost:5173/professionals');

      const afterNavigationBundles = await page.evaluate(() => {
        return performance.getEntriesByType('resource')
          .filter(r => r.name.includes('.js'))
          .map(r => r.name);
      });

      // Should have loaded additional chunks
      const newBundles = afterNavigationBundles.filter(b => !initialBundles.includes(_b));
      expect(newBundles.length).toBeGreaterThan(0);
    });

    it('should optimize image loading', async () => {
      await page.goto('http://localhost:5173');

      const imageMetrics = await page.evaluate(() => {
        const _images = document.querySelectorAll('img');
        return Array.from(_images).map(img => ({
          src: img.src,
          loading: img.loading,
          srcset: img.srcset,
          sizes: img.sizes,
          format: img.src.split('.').pop(),
        }));
      });

      imageMetrics.forEach(img => {
        // Images should use lazy loading where appropriate
        if (!img.src.includes('hero') && !img.src.includes('logo')) {
          expect(img.loading).toBe('lazy');
        }

        // Should use modern image formats
        expect(['webp', 'avif', 'jpg', 'png', 'svg']).toContain(img.format);

        // Should provide responsive images
        if (!img.src.includes('icon')) {
          expect(img.srcset || img.sizes).toBeTruthy();
        }
      });
    });
  });

  describe('Database Query Performance', () => {
    it('should execute queries within performance budget', async () => {
      const queryTimes = await page.evaluate(() => {
        return fetch('/api/test/query-performance')
          .then(r => r.json())
          .then(data => data.queryTimes);
      });

      queryTimes.forEach((_time: number) => {
        expect(_time).toBeLessThan(50); // 50ms per query
      });
    });

    it('should implement efficient pagination', async () => {
      await page.goto('http://localhost:5173/community');

      const _loadTime = await page.evaluate(() => {
        return new Promise<number>((resolve) => {
          const startTime = performance.now();

          // Load paginated content
          fetch('/api/community/posts?page=1&limit=20')
            .then(() => resolve(performance.now() - startTime));
        });
      });

      expect(_loadTime).toBeLessThan(200);
    });
  });

  describe('Real User Monitoring Simulation', () => {
    it('should perform well under various network conditions', async () => {
      const conditions = [
        { name: '3G', downloadThroughput: 1.6 * 1024 * 1024 / 8, uploadThroughput: 750 * 1024 / 8, latency: 150 },
        { name: '4G', downloadThroughput: 4 * 1024 * 1024 / 8, uploadThroughput: 3 * 1024 * 1024 / 8, latency: 50 },
      ];

      for (const condition of conditions) {
        // Simulate network condition
        await page.emulateNetworkConditions({
          downloadThroughput: condition.downloadThroughput,
          uploadThroughput: condition.uploadThroughput,
          latency: condition.latency,
          offline: false,
        });

        const startTime = Date.now();
        await page.goto('http://localhost:5173');
        await page.waitForSelector('[data-testid="app-ready"]');
        const _loadTime = Date.now() - startTime;

        // Adjust threshold based on network condition
        const _adjustedThreshold = condition.name === '3G' ? THRESHOLDS.PAGE_LOAD * 2 : THRESHOLDS.PAGE_LOAD;
        expect(_loadTime).toBeLessThan(_adjustedThreshold);
      }
    });

    it('should handle concurrent users efficiently', async () => {
      const userCount = 50;
      const browsers: Browser[] = [];
      const pages: Page[] = [];

      // Create multiple browser instances
      for (let i = 0; i < userCount; i++) {
        const browser = await puppeteer.launch({ headless: true });
        const page = await browser.newPage();
        browsers.push(_browser);
        pages.push(page);
      }

      // Simulate concurrent usage
      const loadTimes = await Promise.all(
        pages.map(async (page) => {
          const startTime = Date.now();
          await page.goto('http://localhost:5173');
          await page.waitForSelector('[data-testid="app-ready"]');
          return Date.now() - startTime;
        })
      );

      // Calculate statistics
      const _avgLoadTime = loadTimes.reduce((a, b) => a + b, 0) / loadTimes.length;
      const _maxLoadTime = Math.max(...loadTimes);

      expect(_avgLoadTime).toBeLessThan(THRESHOLDS.PAGE_LOAD);
      expect(_maxLoadTime).toBeLessThan(THRESHOLDS.PAGE_LOAD * 1.5);

      // Cleanup
      await Promise.all(browsers.map(b => b.close()));
    });
  });
});

#!/usr/bin/env node

/**
 * Comprehensive Test Runner for CoreV4 Mental Health Platform
 * Executes all test suites and generates comprehensive reports
 */

import { exec, _execSync } from 'child_process';
import { promises as fs } from 'fs';
import _path from 'path';
import chalk from 'chalk';
import { logger } from '../utils/logger';

// Test suite configurations
const TEST_SUITES = {
  unit: {
    name: 'Unit Tests',
    command: 'npm run test:unit',
    critical: true,
  },
  crisis: {
    name: 'Crisis Intervention Tests',
    command: 'npm run test:crisis',
    critical: true,
  },
  accessibility: {
    name: 'Accessibility Tests (WCAG 2.1 AA)',
    command: 'npm run test:accessibility',
    critical: true,
  },
  performance: {
    name: 'Performance & Load Tests',
    command: 'npm run test:performance',
    critical: true,
  },
  security: {
    name: 'Security & Privacy Tests',
    command: 'npm run test:security',
    critical: true,
  },
  integration: {
    name: 'Integration Tests',
    command: 'npm run test:integration',
    critical: false,
  },
  e2e: {
    name: 'End-to-End Tests',
    command: 'npm run test:e2e',
    critical: false,
  },
  lighthouse: {
    name: 'Lighthouse Performance Audit',
    command: 'npm run lighthouse:ci',
    critical: false,
  },
};

// Test result storage
interface TestResult {
  suite: string;
  passed: boolean;
  duration: number;
  output?: string;
  error?: string;
}

const results: TestResult[] = [];

// Helper functions
function log(message: string, type: 'info' | 'success' | 'error' | 'warning' = 'info') {
  const timestamp = new Date().toISOString();
  const colors = {
    info: chalk.blue,
    success: chalk.green,
    error: chalk.red,
    warning: chalk.yellow,
  };

  logger.info(`[${timestamp}] ${colors[type](_message)}`);
}

async function ensureDirectories() {
  const dirs = [
    'test-results',
    'test-results/coverage',
    'test-results/reports',
    'test-results/screenshots',
    'test-results/videos',
  ];

  for (const dir of dirs) {
    await fs.mkdir(dir, { recursive: true });
  }
}

async function runTest(suite: string, config: typeof TEST_SUITES[keyof typeof TEST_SUITES]): Promise<TestResult> {
  const startTime = Date.now();
  log(`Running ${config.name}...`, 'info');

  return new Promise((resolve) => {
    exec(config.command, { maxBuffer: 10 * 1024 * 1024 }, (error, stdout, stderr) => {
      const duration = Date.now() - startTime;
      const passed = !error;

      if (_passed) {
        log(`âœ“ ${config.name} passed in ${(duration / 1000).toFixed(2)}s`, 'success');
      } else {
        log(`âœ— ${config.name} failed after ${(duration / 1000).toFixed(2)}s`, 'error');
        if (config.critical) {
          log('This is a critical test suite!', 'error');
        }
      }

      resolve({
        suite,
        passed,
        duration,
        output: stdout,
        error: stderr || error?.message,
      });
    });
  });
}

async function checkCrisisResponseTime() {
  log('Validating crisis response time requirements...', 'info');

  try {
    const _perfResults = await fs.readFile('test-results/performance-metrics.json', 'utf-8');
    const metrics = JSON.parse(_perfResults);

    if (metrics.crisisResponseTime > 200) {
      log(`Crisis response time ${metrics.crisisResponseTime}ms exceeds 200ms threshold!`, 'error');
      return false;
    }

    log(`Crisis response time: ${metrics.crisisResponseTime}ms âœ“`, 'success');
    return true;
  } catch {
    log('Could not validate crisis response time', 'warning');
    return true;
  }
}

