/**
 * Open IndexedDB for crisis data
 */
function openCrisisDB(): Promise<IDBDatabase> {
  return new Promise((resolve, reject) => {
    const request = indexedDB.open('CrisisDB', 1);
    
    request.onerror = () => reject(request.error);
    request.onsuccess = () => resolve(request.result);
    
    request.onupgradeneeded = (event) => {
      const db = (event.target as IDBOpenDBRequest).result;
      
      // Create assessments store
      if (!db.objectStoreNames.contains('assessments')) {
        const assessmentStore = db.createObjectStore('assessments', { 
          keyPath: 'id' 
        });
        assessmentStore.createIndex('timestamp', 'timestamp');
        assessmentStore.createIndex('level', 'level');
      }
      
      // Create interactions store
      if (!db.objectStoreNames.contains('interactions')) {
        const interactionStore = db.createObjectStore('interactions', { 
          keyPath: 'id' 
        });
        interactionStore.createIndex('timestamp', 'timestamp');
        interactionStore.createIndex('action', 'action');
        interactionStore.createIndex('level', 'level');
      }
      
      // Create safety plans store
      if (!db.objectStoreNames.contains('safetyPlans')) {
        db.createObjectStore('safetyPlans', { 
          keyPath: 'id' 
        });
      }
    };
  });
}

/**
 * Generate unique ID for crisis records
 */
function generateId(): string {
  return `crisis_${Date.now()}_${Math.random().toString(36).substring(2, 9)}`;
}

/**
 * Get recent crisis assessments
 */
export async function getRecentAssessments(limit: number = 10): Promise<CrisisAssessment[]> {
  try {
    const db = await openCrisisDB();
    const tx = db.transaction('assessments', 'readonly');
    const store = tx.objectStore('assessments');
    const index = store.index('timestamp');
    
    const assessments: CrisisAssessment[] = [];
    const request = index.openCursor(null, 'prev'); // Most recent first
    
    return new Promise((resolve, reject) => {
      request.onsuccess = (event) => {
        const cursor = (event.target as IDBRequest).result;
        if (cursor && assessments.length < limit) {
          assessments.push(cursor.value);
          cursor.continue();
        } else {
          resolve(_assessments);
        }
      };
      request.onerror = () => reject(request.error);
    });
  } catch (_error) {
    logger.error('Failed to get recent assessments:');
    // Fallback to localStorage
    try {
      const _stored = localStorage.getItem(CRISIS_STORAGE_KEYS.lastAssessment) || '[]';
      const assessments = JSON.parse(_stored);
      return assessments.slice(-limit).reverse();
    } catch (_error) {
    logger.error('Failed to get assessments from localStorage:', fallbackError);
      return [];
    }
  }
}

/**
 * Get crisis trend analysis
 */
export async function getCrisisTrends(days: number = 7): Promise<{
  averageLevel: number;
  trendDirection: 'improving' | 'stable' | 'worsening';
  totalAssessments: number;
  criticalIncidents: number;
}> {
  const assessments = await getRecentAssessments(100);
  const cutoffDate = new Date();
  cutoffDate.setDate(cutoffDate.getDate() - days);
  
  const recentAssessments = assessments.filter(a => 
    new Date(a.timestamp) > cutoffDate
  );
  
  if (recentAssessments.length === 0) {
    return {
      averageLevel: 1,
      trendDirection: 'stable',
      totalAssessments: 0,
      criticalIncidents: 0
    };
  }

  // Convert levels to numbers for analysis
  const levelToNumber = {
    safe: 1,
    low: 2,
    moderate: 3,
    high: 4,
    critical: 5
  };

  const levelNumbers = recentAssessments.map(a => levelToNumber[a.level]);
  const averageLevel = levelNumbers.reduce((sum, level) => sum + level, 0) / levelNumbers.length;
  
  // Calculate trend (compare first half to second half)
  const midpoint = Math.floor(levelNumbers.length / 2);
  const firstHalf = levelNumbers.slice(0, midpoint);
  const secondHalf = levelNumbers.slice(_midpoint);
  
  const firstAvg = firstHalf.reduce((sum, level) => sum + level, 0) / firstHalf.length;
  const secondAvg = secondHalf.reduce((sum, level) => sum + level, 0) / secondHalf.length;
  
  let trendDirection: 'improving' | 'stable' | 'worsening';
  const difference = secondAvg - firstAvg;
  
  if (difference > 0.3) {
    trendDirection = 'worsening';
  } else if (difference < -0.3) {
    trendDirection = 'improving';
  } else {
    trendDirection = 'stable';
  }

  const criticalIncidents = recentAssessments.filter(a => 
    a.level === 'critical' || a.level === 'high'
  ).length;

  return {
    averageLevel,
    trendDirection,
    totalAssessments: recentAssessments.length,
    criticalIncidents
  };
}

import { logger } from '../logger';

/**
 * Mobile Touch Optimization Utilities
 * Provides enhanced touch interactions, gestures, and mobile-specific optimizations
 */

// Touch event interfaces for better type safety
interface TouchPoint {
  id: number;
  x: number;
  y: number;
  timestamp: number;
}

interface TouchGesture {
  type: 'tap' | 'doubleTap' | 'longPress' | 'swipe' | 'pinch' | 'rotate';
  startTime: number;
  duration: number;
  distance?: number;
  direction?: 'up' | 'down' | 'left' | 'right';
  scale?: number;
  rotation?: number;
  velocity?: number;
  points: TouchPoint[];
}

interface TouchOptimizationOptions {
  enableFastClick?: boolean;
  enableSwipeGestures?: boolean;
  enablePinchZoom?: boolean;
  enableRotation?: boolean;
  enableVibration?: boolean;
  doubleTapDelay?: number;
  longPressDelay?: number;
  swipeThreshold?: number;
  pinchThreshold?: number;
  rotationThreshold?: number;
}

class TouchOptimizationManager {
  private options: Required<TouchOptimizationOptions>;
  private activeGestures: Map<string, TouchGesture> = new Map();
  private touchHistory: TouchPoint[][] = [];
  private lastTap: { x: number; y: number; timestamp: number } | null = null;
  private longPressTimer: number | null = null;
  private gestureListeners: Map<string, Set<(gesture: TouchGesture) => void>> = new Map();
  private element: HTMLElement;
  private rafId: number | null = null;

  private readonly defaultOptions: Required<TouchOptimizationOptions> = {
    enableFastClick: true,
    enableSwipeGestures: true,
    enablePinchZoom: true,
    enableRotation: true,
    enableVibration: true,
    doubleTapDelay: 300,
    longPressDelay: 500,
    swipeThreshold: 50,
    pinchThreshold: 1.2,
    rotationThreshold: 15,
  };

  constructor(element: HTMLElement, options: TouchOptimizationOptions = {}) {
    this.element = element;
    this.options = { ...this.defaultOptions, ...options };
    this.initializeEventListeners();
    this.initializeMobileOptimizations();
  }

  private initializeEventListeners(): void {
    // Use passive listeners for better performance
    this.element.addEventListener('touchstart', this.handleTouchStart.bind(_this), { passive: true });
    this.element.addEventListener('touchmove', this.handleTouchMove.bind(_this), { passive: false });
    this.element.addEventListener('touchend', this.handleTouchEnd.bind(_this), { passive: true });
    this.element.addEventListener('touchcancel', this.handleTouchCancel.bind(_this), { passive: true });

    // Prevent default touch behaviors that interfere with custom gestures
    this.element.addEventListener('gesturestart', this.preventDefault, { passive: false });
    this.element.addEventListener('gesturechange', this.preventDefault, { passive: false });
    this.element.addEventListener('gestureend', this.preventDefault, { passive: false });
  }

  private initializeMobileOptimizations(): void {
    // Optimize touch targets for accessibility
    this.optimizeTouchTargets();
    
    // Enable hardware acceleration for smooth animations
    this.enableHardwareAcceleration();
    
    // Optimize scrolling performance
    this.optimizeScrolling();
    
    // Implement iOS momentum scrolling fix
    this.implementMomentumScrolling();
    
    // Add visual touch feedback
    this.addTouchFeedback();
  }

  private handleTouchStart(event: TouchEvent): void {
    const touches = Array.from(event.touches);
    const timestamp = performance.now();

    // Store touch history for gesture recognition
    const _touchPoints: TouchPoint[] = touches.map((touch, _index) => ({
      id: touch.identifier,
      x: touch.clientX,
      y: touch.clientY,
      timestamp,
    }));

    this.touchHistory.push(_touchPoints);

    // Handle single touch
    if (touches.length === 1 && touches[0]) {
      const touch = touches[0];
      this.handleSingleTouchStart(touch, timestamp);
    }

    // Handle multi-touch gestures
    if (touches.length === 2) {
      this.handleMultiTouchStart(touches, timestamp);
    }

    // Limit touch history to prevent memory leaks
    if (this.touchHistory.length > 10) {
      this.touchHistory.shift();
    }
  }

  private handleSingleTouchStart(touch: Touch, timestamp: number): void {
    // Setup long press detection
    if (this.longPressTimer) {
      clearTimeout(this.longPressTimer);
    }

    this.longPressTimer = window.setTimeout(() => {
      this.triggerGesture({
        type: 'longPress',
        startTime: timestamp,
        duration: this.options.longPressDelay,
        points: [{
          id: touch.identifier,
          x: touch.clientX,
          y: touch.clientY,
          timestamp,
        }],
      });

      // Provide haptic feedback for long press
      if (this.options.enableVibration) {
        this.vibrate([50]);
      }
    }, this.options.longPressDelay);
  }

  private handleMultiTouchStart(touches: Touch[], timestamp: number): void {
    if (touches.length === 2 && touches[0] && touches[1]) {
      // Initialize pinch/zoom gesture
      const _distance = this.calculateDistance(touches[0], touches[1]);
      const _angle = this.calculateAngle(touches[0], touches[1]);
      
      const gesture: TouchGesture = {
        type: 'pinch',
        startTime: timestamp,
        duration: 0,
        scale: 1,
        rotation: 0,
        points: touches.map((touch) => ({
          id: touch.identifier,
          x: touch.clientX,
          y: touch.clientY,
          timestamp,
        })),
      };

      this.activeGestures.set('pinch', gesture);
    }
  }

  private handleTouchMove(event: TouchEvent): void {
    const touches = Array.from(event.touches);
    const timestamp = performance.now();

    // Cancel long press if touch moves too much
    if (this.longPressTimer && touches.length === 1 && touches[0]) {
      const initialTouch = this.touchHistory[0]?.[0];
      if (initialTouch) {
        const distance = this.calculateDistance(
          { clientX: initialTouch.x, clientY: initialTouch.y } as Touch,
          touches[0]
        );
        
        if (distance > 10) {
          clearTimeout(this.longPressTimer);
          this.longPressTimer = null;
        }
      }
    }

    // Handle single touch move (potential swipe)
    if (touches.length === 1 && touches[0]) {
      this.handleSwipeGesture(touches[0], timestamp);
    }

    // Handle multi-touch move (pinch/zoom/rotate)
    if (touches.length === 2) {
      this.handleMultiTouchMove(touches, timestamp);
    }

    // Prevent default behavior for custom gestures
    if (touches.length > 1) {
      event.preventDefault();
    }
  }

  private handleSwipeGesture(touch: Touch, timestamp: number): void {
    if (!this.options.enableSwipeGestures) return;

    const initialTouch = this.touchHistory[0]?.[0];
    if (!initialTouch) return;

    const deltaX = touch.clientX - initialTouch.x;
    const deltaY = touch.clientY - initialTouch.y;
    const distance = Math.sqrt(deltaX * deltaX + deltaY * deltaY);
    const duration = timestamp - initialTouch.timestamp;

    // Only trigger swipe if distance exceeds threshold
    if (distance > this.options.swipeThreshold && duration < 1000) {
      const velocity = distance / duration;
      let direction: 'up' | 'down' | 'left' | 'right';

      if (Math.abs(_deltaX) > Math.abs(_deltaY)) {
        direction = deltaX > 0 ? 'right' : 'left';
      } else {
        direction = deltaY > 0 ? 'down' : 'up';
      }

      // Use RAF for smooth gesture handling
      if (this.rafId) {
        cancelAnimationFrame(this.rafId);
      }

      this.rafId = requestAnimationFrame(() => {
        this.triggerGesture({
          type: 'swipe',
          startTime: initialTouch.timestamp,
          duration,
          distance,
          direction,
          velocity,
          points: [initialTouch, {
            id: touch.identifier,
            x: touch.clientX,
            y: touch.clientY,
            timestamp,
          }],
        });
      });
    }
  }

  private handleMultiTouchMove(touches: Touch[], timestamp: number): void {
    if (!this.options.enablePinchZoom && !this.options.enableRotation) return;

    const pinchGesture = this.activeGestures.get('pinch');
    if (!pinchGesture || touches.length !== 2 || !touches[0] || !touches[1]) return;

    const currentDistance = this.calculateDistance(touches[0], touches[1]);
    const currentAngle = this.calculateAngle(touches[0], touches[1]);
    
    const point0 = pinchGesture.points[0];
    const point1 = pinchGesture.points[1];
    if (!point0 || !point1) return;
    
    const initialDistance = this.calculateDistance(
      { clientX: point0.x, clientY: point0.y } as Touch,
      { clientX: point1.x, clientY: point1.y } as Touch
    );
    
    const initialAngle = this.calculateAngle(
      { clientX: point0.x, clientY: point0.y } as Touch,
      { clientX: point1.x, clientY: point1.y } as Touch
    );

    const scale = currentDistance / initialDistance;
    const rotation = currentAngle - initialAngle;

    // Update gesture data
    pinchGesture.scale = scale;
    pinchGesture.rotation = rotation;
    pinchGesture.duration = timestamp - pinchGesture.startTime;

    // Trigger pinch gesture if scale threshold is exceeded
    if (this.options.enablePinchZoom && Math.abs(scale - 1) > (this.options.pinchThreshold - 1)) {
      this.triggerGesture({ ...pinchGesture });
    }

    // Trigger rotation gesture if rotation threshold is exceeded
    if (this.options.enableRotation && Math.abs(_rotation) > this.options.rotationThreshold) {
      this.triggerGesture({ ...pinchGesture, type: 'rotate' });
    }
  }

  private handleTouchEnd(event: TouchEvent): void {
    const touches = Array.from(event.touches);
    const timestamp = performance.now();

    // Clear long press timer
    if (this.longPressTimer) {
      clearTimeout(this.longPressTimer);
      this.longPressTimer = null;
    }

    // Handle tap gestures
    if (touches.length === 0 && event.changedTouches.length === 1 && event.changedTouches[0]) {
      this.handleTapGesture(event.changedTouches[0], timestamp);
    }

    // Clean up multi-touch gestures
    if (touches.length < 2) {
      this.activeGestures.delete('pinch');
    }
  }

  private handleTapGesture(touch: Touch, timestamp: number): void {
    const initialTouch = this.touchHistory[0]?.[0];
    if (!initialTouch) return;

    const distance = this.calculateDistance(
      { clientX: initialTouch.x, clientY: initialTouch.y } as Touch,
      touch
    );
    
    const duration = timestamp - initialTouch.timestamp;

    // Only register as tap if movement is minimal and duration is short
    if (distance < 10 && duration < 1000) {
      // Check for double tap
      if (this.lastTap && 
          timestamp - this.lastTap.timestamp < this.options.doubleTapDelay &&
          this.calculateDistance(
            { clientX: this.lastTap.x, clientY: this.lastTap.y } as Touch,
            touch
          ) < 50) {
        
        this.triggerGesture({
          type: 'doubleTap',
          startTime: this.lastTap.timestamp,
          duration: timestamp - this.lastTap.timestamp,
          points: [
            { id: 0, x: this.lastTap.x, y: this.lastTap.y, timestamp: this.lastTap.timestamp },
            { id: touch.identifier, x: touch.clientX, y: touch.clientY, timestamp },
          ],
        });

        this.lastTap = null;
      } else {
        // Single tap
        this.triggerGesture({
          type: 'tap',
          startTime: initialTouch.timestamp,
          duration,
          points: [initialTouch, {
            id: touch.identifier,
            x: touch.clientX,
            y: touch.clientY,
            timestamp,
          }],
        });

        this.lastTap = {
          x: touch.clientX,
          y: touch.clientY,
          timestamp,
        };
      }

      // Provide haptic feedback for taps
      if (this.options.enableVibration) {
        this.vibrate([25]);
      }
    }
  }

  private handleTouchCancel(): void {
    // Clean up all active gestures
    this.activeGestures.clear();
    
    if (this.longPressTimer) {
      clearTimeout(this.longPressTimer);
      this.longPressTimer = null;
    }

    if (this.rafId) {
      cancelAnimationFrame(this.rafId);
      this.rafId = null;
    }
  }

  private calculateDistance(touch1: Touch, touch2: Touch): number {
    const dx = touch1.clientX - touch2.clientX;
    const dy = touch1.clientY - touch2.clientY;
    return Math.sqrt(dx * dx + dy * dy);
  }

  private calculateAngle(touch1: Touch, touch2: Touch): number {
    return Math.atan2(touch2.clientY - touch1.clientY, touch2.clientX - touch1.clientX) * 180 / Math.PI;
  }

  private triggerGesture(gesture: TouchGesture): void {
    const listeners = this.gestureListeners.get(gesture.type);
    if (listeners) {
      listeners.forEach(_callback => {
        try {
          _callback(_gesture);
        } catch (error) {
          logger.error(`Error in gesture listener for ${gesture.type}:`, error);
        }
      });
    }

    // Also trigger generic gesture listener
    const genericListeners = this.gestureListeners.get('*');
    if (genericListeners) {
      genericListeners.forEach(_callback => {
        try {
          _callback(_gesture);
        } catch (_error) {
          logger.error('Error in generic gesture listener:');
        }
      });
    }
  }

  private preventDefault(event: Event): void {
    event.preventDefault();
  }

  private vibrate(_pattern: number | number[]): void {
    if ('vibrate' in navigator) {
      try {
        navigator.vibrate(_pattern);
      } catch (_error) {
    logger.warn('Vibration not supported or failed:');
      }
    }
  }

  private optimizeTouchTargets(): void {
    // Ensure minimum touch target size for accessibility
    const minTouchTarget = 44; // 44px minimum as per WCAG guidelines
    
    const clickableElements = this.element.querySelectorAll(
      'button, a, input, select, textarea, [role="button"], [tabindex]'
    );

    clickableElements.forEach((element) => {
      const rect = element.getBoundingClientRect();
      if (rect.width < minTouchTarget || rect.height < minTouchTarget) {
        (element as HTMLElement).style.minWidth = `${minTouchTarget}px`;
        (element as HTMLElement).style.minHeight = `${minTouchTarget}px`;
      }
    });
  }

  private enableHardwareAcceleration(): void {
    // Enable hardware acceleration for smooth animations
    this.element.style.transform = 'translateZ(0)';
    this.element.style.backfaceVisibility = 'hidden';
    this.element.style.perspective = '1000px';
  }

  private optimizeScrolling(): void {
    // Enable smooth scrolling and optimize scroll performance
    this.element.style.scrollBehavior = 'smooth';
    (this.element.style as unknown).webkitOverflowScrolling = 'touch';
    
    // Optimize scroll handling
    this.element.addEventListener('scroll', this.throttleScroll(this.handleScroll.bind(_this), 16), { passive: true });
  }

  private implementMomentumScrolling(): void {
    // Fix iOS momentum scrolling issues
    if (/iPad|iPhone|iPod/.test(navigator.userAgent)) {
      (this.element.style as unknown).webkitOverflowScrolling = 'touch';
      (this.element.style as unknown).overflowScrolling = 'touch';
    }
  }

  private addTouchFeedback(): void {
    // Add visual feedback for touch interactions
    const style = document.createElement('style');
    style.textContent = `
      .touch-feedback {
        position: relative;
        overflow: hidden;
      }
      
      .touch-feedback::before {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background: radial-gradient(circle, rgba(255,255,255,0.3) 0%, transparent 70%);
        transform: scale(0);
        transition: transform 0.3s ease-out;
        pointer-events: none;
      }
      
      .touch-feedback.active::before {
        transform: scale(1);
      }
      
      .touch-feedback:active {
        transform: scale(0.98);
        transition: transform 0.1s ease-out;
      }
    `;
    document.head.appendChild(style);

    // Apply touch feedback to interactive elements
    const interactiveElements = this.element.querySelectorAll(
      'button, a, [role="button"], .touch-target'
    );

    interactiveElements.forEach((element) => {
      element.classList.add('touch-feedback');
    });
  }

  private throttleScroll<T extends (...args: unknown[]) => any>(func: T, delay: number): T {
    let timeoutId: number | null = null;
    let lastExecTime = 0;
    
    return ((...args: Parameters<T>) => {
      const currentTime = Date.now();
      
      if (currentTime - lastExecTime > delay) {
        func(...args);
        lastExecTime = currentTime;
      } else {
        if (timeoutId) {
          clearTimeout(timeoutId);
        }
        
        timeoutId = window.setTimeout(() => {
          func(...args);
          lastExecTime = Date.now();
        }, delay);
      }
    }) as T;
  }

  private handleScroll(): void {
    // Optimize scroll performance by reducing expensive operations during scroll
    document.documentElement.classList.add('is-scrolling');
    
    // Remove class after scrolling stops
    clearTimeout(this.scrollTimeout);
    this.scrollTimeout = window.setTimeout(() => {
      document.documentElement.classList.remove('is-scrolling');
    }, 150);
  }

  private scrollTimeout: number = 0;

  // Public API methods
  public on(_gestureType: string, _callback: (gesture: TouchGesture) => void): void {
    if (!this.gestureListeners.has(_gestureType)) {
      this.gestureListeners.set(_gestureType, new Set());
    }
    this.gestureListeners.get(_gestureType)!.add(_callback);
  }

  public off(_gestureType: string, _callback: (gesture: TouchGesture) => void): void {
    const listeners = this.gestureListeners.get(_gestureType);
    if (listeners) {
      listeners.delete(_callback);
    }
  }

  public destroy(): void {
    // Clean up event listeners
    this.element.removeEventListener('touchstart', this.handleTouchStart);
    this.element.removeEventListener('touchmove', this.handleTouchMove);
    this.element.removeEventListener('touchend', this.handleTouchEnd);
    this.element.removeEventListener('touchcancel', this.handleTouchCancel);
    
    // Clear timers
    if (this.longPressTimer) {
      clearTimeout(this.longPressTimer);
    }
    
    if (this.rafId) {
      cancelAnimationFrame(this.rafId);
    }
    
    if (this.scrollTimeout) {
      clearTimeout(this.scrollTimeout);
    }
    
    // Clear data structures
    this.activeGestures.clear();
    this.gestureListeners.clear();
    this.touchHistory = [];
    this.lastTap = null;
  }
}

// Export factory function for easy initialization
export function initializeTouchOptimization(
  element: HTMLElement, 
  options?: TouchOptimizationOptions
): TouchOptimizationManager {
  return new TouchOptimizationManager(element, options);
}

// Export types
export type { TouchGesture, TouchOptimizationOptions, TouchPoint };
export { TouchOptimizationManager };

// Additional mobile optimization utilities
export const _MobileOptimizationUtils = {
  // Prevent zoom on double-tap
  preventDoubleTapZoom(element: HTMLElement): void {
    element.addEventListener('touchend', (event) => {
      const touch = event.changedTouches[0];
      if (!touch?.target) return;
      const element = touch.target as HTMLElement;
      
      // Prevent zoom on form inputs
      if (['INPUT', 'TEXTAREA', 'SELECT'].includes(element.tagName)) {
        event.preventDefault();
      }
    });
  },

  // Optimize input focus for iOS
  optimizeIOSInputFocus(): void {
    if (/iPad|iPhone|iPod/.test(navigator.userAgent)) {
      const inputs = document.querySelectorAll('input, textarea');
      
      inputs.forEach((input) => {
        input.addEventListener('focus', () => {
          // Prevent viewport zoom on input focus
          const viewport = document.querySelector('meta[name="viewport"]');
          if (viewport) {
            const currentContent = viewport.getAttribute('content') || '';
            viewport.setAttribute('content', `${currentContent  }, user-scalable=no`);
            
            // Restore after blur
            input.addEventListener('blur', () => {
              viewport.setAttribute('content', currentContent);
            }, { once: true });
          }
        });
      });
    }
  },

  // Add safe area padding for notched devices
  addSafeAreaSupport(): void {
    const style = document.createElement('style');
    style.textContent = `
      .safe-area-top {
        padding-top: env(safe-area-inset-top);
      }
      
      .safe-area-bottom {
        padding-bottom: env(safe-area-inset-bottom);
      }
      
      .safe-area-left {
        padding-left: env(safe-area-inset-left);
      }
      
      .safe-area-right {
        padding-right: env(safe-area-inset-right);
      }
      
      .safe-area-all {
        padding: 
          env(safe-area-inset-top) 
          env(safe-area-inset-right) 
          env(safe-area-inset-bottom) 
          env(safe-area-inset-left);
      }
    `;
    document.head.appendChild(style);
  },

  // Enable performance optimizations for mobile
  enableMobilePerformanceMode(): void {
    // Reduce motion for better performance
    if (window.matchMedia('(prefers-reduced-motion: reduce)').matches) {
      document.documentElement.classList.add('reduce-motion');
    }

    // Add CSS for performance optimizations
    const style = document.createElement('style');
    style.textContent = `
      .reduce-motion * {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
      }
      
      .performance-mode .not-critical {
        display: none;
      }
      
      .performance-mode img:not(.critical) {
        visibility: hidden;
      }
    `;
    document.head.appendChild(style);
  }
};

/**
 * Memory Leak Prevention Utilities for CoreV4
 * Provides automatic cleanup, weak references, and memory monitoring
 */

import { useRef, useEffect } from 'react';
import { performanceMonitor } from './performanceMonitor';
import { logger } from '../logger';

/**
 * WeakMap-based cache for preventing memory leaks
 */
export class WeakCache<K extends object, V> {
  private cache = new WeakMap<K, V>();
  private refCount = new WeakMap<K, number>();

  set(key: K, value: V): void {
    this.cache.set(key, value);
    this.refCount.set(key, (this.refCount.get(key) || 0) + 1);
  }

  get(key: K): V | undefined {
    return this.cache.get(key);
  }

  has(key: K): boolean {
    return this.cache.has(key);
  }

  delete(key: K): boolean {
    this.refCount.delete(key);
    return this.cache.delete(key);
  }

  getRefCount(key: K): number {
    return this.refCount.get(key) || 0;
  }
}

/**
 * Automatic cleanup manager for subscriptions and timers
 */
export class CleanupManager {
  private cleanupFunctions: Set<() => void> = new Set();
  private intervals: Set<number> = new Set();
  private timeouts: Set<number> = new Set();
  private animationFrames: Set<number> = new Set();
// @ts-expect-error - MutationObserver is a global API
  private observers: Set<MutationObserver | IntersectionObserver | ResizeObserver> = new Set();
  private eventListeners: Map<EventTarget, Map<string, EventListener>> = new Map();
  private abortControllers: Set<AbortController> = new Set();

  /**
   * Register a cleanup function
   */
  register(cleanup: () => void): void {
    this.cleanupFunctions.add(cleanup);
  }

  /**
   * Create a managed interval
   */
  setInterval(callback: () => void, delay: number): number {
    const id = window.setInterval(callback, delay);
    this.intervals.add(id);
    return id;
  }

  /**
   * Create a managed timeout
   */
  setTimeout(callback: () => void, delay: number): number {
    const id = window.setTimeout(() => {
      callback();
      this.timeouts.delete(id);
    }, delay);
    this.timeouts.add(id);
    return id;
  }

  /**
   * Create a managed animation frame
   */
  requestAnimationFrame(callback: FrameRequestCallback): number {
    const id = window.requestAnimationFrame((_time) => {
      callback(_time);
      this.animationFrames.delete(id);
    });
    this.animationFrames.add(id);
    return id;
  }

  /**
   * Add a managed event listener
   */
  addEventListener(
    target: EventTarget,
    type: string,
    listener: EventListener,
    options?: AddEventListenerOptions
  ): void {
    target.addEventListener(type, listener, options);

    if (!this.eventListeners.has(_target)) {
      this.eventListeners.set(target, new Map());
    }
    this.eventListeners.get(_target)!.set(type, listener);
  }

  /**
   * Create a managed observer
// @ts-expect-error - MutationObserver is a global API
// @ts-expect-error - MutationObserver is a global API
   */
  createMutationObserver(callback: MutationCallback): MutationObserver {
    const observer = new MutationObserver(callback);
    this.observers.add(_observer);
    return observer;
  }

  /**
   * Create a managed intersection observer
   */
// @ts-expect-error - IntersectionObserver is a global API
// @ts-expect-error - IntersectionObserver is a global API
  createIntersectionObserver(
    callback: IntersectionObserverCallback,
    options?: IntersectionObserverInit
  ): IntersectionObserver {
    const observer = new IntersectionObserver(callback, options);
    this.observers.add(_observer);
    return observer;
// @ts-expect-error - ResizeObserver is a global API
// @ts-expect-error - ResizeObserver is a global API
  }

  /**
   * Create a managed resize observer
   */
  createResizeObserver(callback: ResizeObserverCallback): ResizeObserver {
    const observer = new ResizeObserver(callback);
    this.observers.add(_observer);
    return observer;
  }

  /**
   * Create a managed abort controller
   */
  createAbortController(): AbortController {
    const controller = new AbortController();
    this.abortControllers.add(_controller);
    return controller;
  }

  /**
   * Clean up all managed resources
   */
  cleanup(): void {
    // Clear intervals
    this.intervals.forEach(id => clearInterval(id));
    this.intervals.clear();

    // Clear timeouts
    this.timeouts.forEach(id => clearTimeout(id));
    this.timeouts.clear();

    // Cancel animation frames
    this.animationFrames.forEach(id => cancelAnimationFrame(id));
    this.animationFrames.clear();

    // Disconnect observers
    this.observers.forEach(observer => observer.disconnect());
    this.observers.clear();

    // Remove event listeners
    this.eventListeners.forEach((listeners, target) => {
      listeners.forEach((listener, type) => {
        target.removeEventListener(type, listener);
      });
    });
    this.eventListeners.clear();

    // Abort fetch requests
    this.abortControllers.forEach(controller => controller.abort());
    this.abortControllers.clear();

    // Execute cleanup functions
    this.cleanupFunctions.forEach(cleanup => {
      try {
        cleanup();
      } catch (_error) {
        logger.error('Cleanup function error: ');
      }
    });
    this.cleanupFunctions.clear();

    // Record cleanup
    performanceMonitor.recordMetric('memory_cleanup', this.cleanupFunctions.size);
  }
}

/**
 * Memory-efficient event emitter
 */
export class MemoryEfficientEventEmitter<T extends Record<string, any>> {
  private listeners = new Map<keyof T, Set<(data: unknown) => void>>();
  private maxListeners = 10;

  on<K extends keyof T>(event: K, listener: (data: T[K]) => void): () => void {
    if (!this.listeners.has(event)) {
      this.listeners.set(event, new Set());
    }

    const eventListeners = this.listeners.get(event)!;

    // Warn if too many listeners (potential leak)
    if (eventListeners.size >= this.maxListeners) {
      logger.warn(`[Memory] Possible memory leak: ${String(event)} has ${eventListeners.size} listeners`);
      performanceMonitor.recordMetric('potential_memory_leak', eventListeners.size, {
        event: String(event)
      });
    }

    eventListeners.add(listener);

    // Return cleanup function
    return () => {
      eventListeners.delete(listener);
      if (eventListeners.size === 0) {
        this.listeners.delete(event);
      }
    };
  }

  emit<K extends keyof T>(event: K, data: T[K]): void {
    const eventListeners = this.listeners.get(event);
    if (eventListeners) {
      eventListeners.forEach(listener => {
        try {
          listener(data);
        } catch (error) {
          logger.error(`Error in event listener for ${String(event)}:`, error);
        }
      });
    }
  }

  removeAllListeners(event?: keyof T): void {
    if (event) {
      this.listeners.delete(event);
    } else {
      this.listeners.clear();
    }
  }

  listenerCount(event: keyof T): number {
    return this.listeners.get(event)?.size || 0;
  }
}

/**
 * Object pool for reusing objects and reducing garbage collection
 */
export class ObjectPool<T> {
  private pool: T[] = [];
  private maxSize: number;
  private createFn: () => T;
  private resetFn: (_obj: T) => void;

  constructor(
    createFn: () => T,
    resetFn: (_obj: T) => void,
    maxSize: number = 100
  ) {
    this.createFn = createFn;
    this.resetFn = resetFn;
    this.maxSize = maxSize;
  }

  acquire(): T {
    if (this.pool.length > 0) {
      return this.pool.pop()!;
    }
    return this.createFn();
  }

  release(_obj: T): void {
    if (this.pool.length < this.maxSize) {
      this.resetFn(_obj);
      this.pool.push(_obj);
    }
  }

  clear(): void {
    this.pool = [];
  }

  get size(): number {
    return this.pool.length;
  }
}

/**
 * Debounced function with automatic cleanup
 */
export function createDebouncedFunction<T extends (...args: unknown[]) => any>(
  fn: T,
  delay: number,
  options: { leading?: boolean; trailing?: boolean; maxWait?: number } = {}
): T & { cancel: () => void; flush: () => void } {
  let timeoutId: number | null = null;
  let lastCallTime: number | null = null;
  let lastInvokeTime = 0;
  let lastArgs: unknown[] | null = null;
  let lastThis: unknown = null;
  let result: unknown;

  const { leading = false, trailing = true, maxWait } = options;

  function invokeFunc(_time: number) {
    const args = lastArgs;
    const thisArg = lastThis;

    lastArgs = null;
    lastThis = null;
    lastInvokeTime = _time;
    result = fn.apply(thisArg, args!);
    return result;
  }

  function leadingEdge(_time: number) {
    lastInvokeTime = _time;
    timeoutId = window.setTimeout(timerExpired, delay);
    return leading ? invokeFunc(_time) : result;
  }

  function timerExpired() {
    const _time = Date.now();
    if (shouldInvoke(_time)) {
      return trailingEdge(_time);
    }
    timeoutId = window.setTimeout(timerExpired, remainingWait(_time));
  }

  function trailingEdge(_time: number) {
    timeoutId = null;
    if (trailing && lastArgs) {
      return invokeFunc(_time);
    }
    lastArgs = null;
    lastThis = null;
    return result;
  }

  function shouldInvoke(_time: number) {
    const timeSinceLastCall = lastCallTime ? _time - lastCallTime : 0;
    const timeSinceLastInvoke = _time - lastInvokeTime;

    return (
      lastCallTime === null ||
      timeSinceLastCall >= delay ||
      timeSinceLastCall < 0 ||
      (maxWait !== undefined && timeSinceLastInvoke >= maxWait)
    );
  }

  function remainingWait(_time: number) {
    const timeSinceLastCall = lastCallTime ? _time - lastCallTime : 0;
    const timeSinceLastInvoke = _time - lastInvokeTime;
    const timeWaiting = delay - timeSinceLastCall;

    return maxWait !== undefined
      ? Math.min(timeWaiting, maxWait - timeSinceLastInvoke)
      : timeWaiting;
  }

  function debounced(this: unknown, ...args: unknown[]) {
    const _time = Date.now();
    const _isInvoking = shouldInvoke(_time);

    lastArgs = args;
    lastThis = this;
    lastCallTime = _time;

    if (_isInvoking) {
      if (timeoutId === null) {
        return leadingEdge(_time);
      }
      if (maxWait !== undefined) {
        timeoutId = window.setTimeout(timerExpired, delay);
        return invokeFunc(_time);
      }
    }

    if (timeoutId === null) {
      timeoutId = window.setTimeout(timerExpired, delay);
    }

    return result;
  }

  debounced.cancel = function() {
    if (timeoutId !== null) {
      clearTimeout(_timeoutId);
    }
    lastInvokeTime = 0;
    lastArgs = null;
    lastCallTime = null;
    lastThis = null;
    timeoutId = null;
  };

  debounced.flush = function() {
    return timeoutId === null ? result : trailingEdge(Date.now());
  };

  return debounced as T & { cancel: () => void; flush: () => void };
}
// @ts-expect-error - IntersectionObserver is a global API

/**
 * Memory-efficient image loader with automatic cleanup
 */
export class ImageLoader {
  private cache = new Map<string, HTMLImageElement>();
  private loading = new Map<string, Promise<HTMLImageElement>>();
  private observers = new WeakMap<HTMLImageElement, IntersectionObserver>();

  async load(src: string): Promise<HTMLImageElement> {
    // Check cache
    if (this.cache.has(src)) {
      return this.cache.get(src)!;
    }

    // Check if already loading
    if (this.loading.has(src)) {
      return this.loading.get(src)!;
    }

    // Start loading
    const loadPromise = new Promise<HTMLImageElement>((resolve, reject) => {
      const img = new Image();

      img.onload = () => {
        this.cache.set(src, img);
        this.loading.delete(src);
        resolve(img);
      };

      img.onerror = () => {
        this.loading.delete(src);
        reject(new Error(`Failed to load image: ${src}`));
      };

// @ts-expect-error - IntersectionObserver is a global API
      img.src = src;
    });

    this.loading.set(src, loadPromise);
    return loadPromise;
  }

  lazyLoad(element: HTMLImageElement, src: string): void {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            this.load(src).then(img => {
              element.src = img.src;
              observer.disconnect();
              this.observers.delete(_element);
            });
          }
        });
      },
      { rootMargin: '50px' }
    );

    observer.observe(_element);
    this.observers.set(element, observer);
  }

  clear(): void {
    this.cache.clear();
    this.loading.clear();
  }

  remove(src: string): void {
    this.cache.delete(src);
    this.loading.delete(src);
  }
}

/**
 * React hook for automatic cleanup
 */
export function useCleanup() {
  const cleanupManager = useRef(new CleanupManager());

  useEffect(() => {
    return () => {
      cleanupManager.current.cleanup();
    };
  }, []);

  return cleanupManager.current;
}

// Export singleton instances
export const globalCleanupManager = new CleanupManager();
export const globalImageLoader = new ImageLoader();

// Automatic cleanup on page unload
if (typeof window !== 'undefined') {
  window.addEventListener('beforeunload', () => {
    globalCleanupManager.cleanup();
    globalImageLoader.clear();
  });
}

