// Breakpoint definitions
export const BREAKPOINTS = {
  xs: 0,     // Extra small devices (portrait phones)
  sm: 640,   // Small devices (landscape phones)
  md: 768,   // Medium devices (_tablets)
  lg: 1024,  // Large devices (_desktops)
  xl: 1280,  // Extra large devices (large desktops)
  '2xl': 1536 // 2X large devices (larger desktops)
} as const;

// Get current breakpoint
export function getCurrentBreakpoint(): keyof typeof BREAKPOINTS {
  const width = window.innerWidth;

  if (width >= BREAKPOINTS['2xl']) return '2xl';
  if (width >= BREAKPOINTS.xl) return 'xl';
  if (width >= BREAKPOINTS.lg) return 'lg';
  if (width >= BREAKPOINTS.md) return 'md';
  if (width >= BREAKPOINTS.sm) return 'sm';
  return 'xs';
}

// Check if current device is mobile
export function isMobileDevice(): boolean {
  return /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
}

// Check if current device supports touch
export function isTouchDevice(): boolean {
  return 'ontouchstart' in window || navigator.maxTouchPoints > 0;
}

// Get device orientation
export function getDeviceOrientation(): 'portrait' | 'landscape' {
  return window.innerHeight > window.innerWidth ? 'portrait' : 'landscape';
}

// Test responsive layout
export class ResponsiveLayoutTester {
  private originalWidth: number;
  private originalHeight: number;
  private testResults: Map<string, TestResult> = new Map();

  constructor() {
    this.originalWidth = window.innerWidth;
    this.originalHeight = window.innerHeight;
  }

  // Test all device profiles
  async testAllDevices(testFn: (device: DeviceProfile) => Promise<boolean>): Promise<TestReport> {
    const results: TestResult[] = [];

    for (const device of DEVICE_PROFILES) {
      const result = await this.testDevice(device, testFn);
      results.push(result);
      this.testResults.set(device.name, result);
    }

    return this.generateReport(results);
  }

  // Test specific device
  async testDevice(
    device: DeviceProfile, 
    testFn: (device: DeviceProfile) => Promise<boolean>
  ): Promise<TestResult> {
    const startTime = performance.now();

    // Simulate device viewport
    this.setViewport(device.width, device.height);

    // Run test
    let passed = false;
    let error: Error | undefined;

    try {
      passed = await testFn(_device);
    } catch (_error) {
      error = e as Error;
      passed = false;
    }

    const duration = performance.now() - startTime;

    // Reset viewport
    this.resetViewport();

    return {
      device: device.name,
      passed,
      duration,
      error: error?.message,
      timestamp: new Date()
    };
  }

  // Set viewport size (for testing only)
  private setViewport(width: number, height: number) {
    // This would typically be done with a testing framework like Playwright or Cypress
    // For development, we can use CSS to simulate different viewports
    document.documentElement.style.setProperty('--test-viewport-width', `${width}px`);
    document.documentElement.style.setProperty('--test-viewport-height', `${height}px`);
  }

  // Reset viewport
  private resetViewport() {
    document.documentElement.style.removeProperty('--test-viewport-width');
    document.documentElement.style.removeProperty('--test-viewport-height');
  }

  // Generate test report
  private generateReport(results: TestResult[]): TestReport {
    const passed = results.filter(r => r.passed).length;
    const failed = results.filter(r => !r.passed).length;
    const totalDuration = results.reduce((sum, r) => sum + r.duration, 0);

    return {
      summary: {
        total: results.length,
        passed,
        failed,
        passRate: (passed / results.length) * 100,
        averageDuration: totalDuration / results.length
      },
      results,
      generatedAt: new Date(),
      recommendations: this.generateRecommendations(results)
    };
  }

  // Generate recommendations based on test results
  private generateRecommendations(results: TestResult[]): string[] {
    const recommendations: string[] = [];
    const failedDevices = results.filter(r => !r.passed);

    if (failedDevices.length > 0) {
      const mobileFailures = failedDevices.filter(r => 
        DEVICE_PROFILES.find(d => d.name === r.device)?.mobile
      );

      if (mobileFailures.length > 0) {
        recommendations.push('Mobile responsiveness needs improvement');
      }

      const tabletFailures = failedDevices.filter(r => {
        const device = DEVICE_PROFILES.find(d => d.name === r.device);
        return device && !device.mobile && device.touch;
      });

      if (tabletFailures.length > 0) {
        recommendations.push('Tablet layout optimization required');
      }
    }

    const slowTests = results.filter(r => r.duration > 1000);
    if (slowTests.length > 0) {
      recommendations.push('Performance optimization needed for some viewports');
    }

    return recommendations;
  }
}

// Test result interfaces
interface TestResult {
  device: string;
  passed: boolean;
  duration: number;
  error?: string;
  timestamp: Date;
}

interface TestReport {
  summary: {
    total: number;
    passed: number;
    failed: number;
    passRate: number;
    averageDuration: number;
  };
  results: TestResult[];
  generatedAt: Date;
  recommendations: string[];
}

// CSS Media Query validator
export function validateMediaQueries(): MediaQueryValidation[] {
  const validations: MediaQueryValidation[] = [];

  // Check common breakpoints
  for (const [name, minWidth] of Object.entries(_BREAKPOINTS)) {
    const query = `(min-width: ${minWidth}px)`;
    const matches = window.matchMedia(_query).matches;

    validations.push({
      breakpoint: name,
      query,
      matches,
      currentWidth: window.innerWidth
    });
  }

  // Check orientation
  validations.push({
    breakpoint: 'orientation',
    query: '(orientation: portrait)',
    matches: window.matchMedia('(orientation: portrait)').matches,
    currentWidth: window.innerWidth
  });

  // Check touch capability
  validations.push({
    breakpoint: 'touch',
    query: '(hover: none)',
    matches: window.matchMedia('(hover: none)').matches,
    currentWidth: window.innerWidth
  });

  return validations;
}

interface MediaQueryValidation {
  breakpoint: string;
  query: string;
  matches: boolean;
  currentWidth: number;
}

// Accessibility viewport testing
export function testAccessibilityAtBreakpoint(breakpoint: keyof typeof BREAKPOINTS): AccessibilityTest {
  const tests: AccessibilityCheck[] = [];

  // Check font sizes
  const minFontSize = 14; // Minimum recommended font size
  const bodyFontSize = parseFloat(
    window.getComputedStyle(document.body).fontSize
  );

  tests.push({
    name: 'Font Size',
    passed: bodyFontSize >= minFontSize,
    message: `Body font size is ${bodyFontSize}px (min: ${minFontSize}px)`
  });

  // Check touch target sizes
  const minTouchTarget = 44; // Minimum touch target size in pixels
  const buttons = document.querySelectorAll('button');
  let touchTargetsPassed = true;

  buttons.forEach(button => {
    const rect = button.getBoundingClientRect();
    if (rect.width < minTouchTarget || rect.height < minTouchTarget) {
      touchTargetsPassed = false;
    }
  });

  tests.push({
    name: 'Touch Targets',
    passed: touchTargetsPassed,
    message: touchTargetsPassed 
      ? 'All touch targets meet minimum size' 
      : 'Some touch targets are too small'
  });

  // Check contrast ratios
  tests.push({
    name: 'Color Contrast',
    passed: true, // Would need more complex checking
    message: 'Manual verification required'
  });

  // Check viewport zoom
  const viewportMeta = document.querySelector('meta[name="viewport"]');
  const allowsZoom = !viewportMeta?.getAttribute('content')?.includes('user-scalable=no');

  tests.push({
    name: 'Viewport Zoom',
    passed: allowsZoom,
    message: allowsZoom ? 'Zoom is enabled' : 'Zoom is disabled'
  });

  return {
    breakpoint,
    timestamp: new Date(),
    tests,
    passed: tests.every(t => t.passed)
  };
}

interface AccessibilityCheck {
  name: string;
  passed: boolean;
  message: string;
}

interface AccessibilityTest {
  breakpoint: string;
  timestamp: Date;
  tests: AccessibilityCheck[];
  passed: boolean;
}

// Export test runner for use in components
export const _responsiveTester = new ResponsiveLayoutTester();

import { logger } from '../utils/logger';
/**
 * Runtime Guards - Defensive Programming Patterns
 * Prevents common runtime errors and lexical declaration issues
 */

// Safe variable accessor to prevent undefined access
export function safeAccess<T>(obj: unknown, path: string, defaultValue?: T): T | undefined {
  try {
    const keys = path.split('.');
    let result = obj;

    for (const key of keys) {
      if (result == null || typeof result !== 'object') {
        return defaultValue;
      }
      result = result[key];
    }

    return result !== undefined ? result : defaultValue;
  } catch (_error) {
    logger.warn('Safe access failed:', path, e);
    return defaultValue;
  }
}

// Safe function caller to prevent call stack issues
export function safeCall<T extends (...args: unknown[]) => any>(
  fn: T | undefined | null,
  defaultValue?: ReturnType<T>,
  ...args: Parameters<T>
): ReturnType<T> | undefined {
  try {
    if (typeof fn === 'function') {
      return fn(...args);
    }
    return defaultValue;
  } catch (_error) {
    logger.error('Safe call failed:', e);
    return defaultValue;
  }
}

// Guard against temporal dead zone violations
export function initializeVariable<T>(
  getter: () => T,
  defaultValue: T,
  varName?: string
): T {
  try {
    const result = getter();
    return result !== undefined ? result : defaultValue;
  } catch {
    if (e instanceof ReferenceError && e.message.includes('before initialization')) {
      logger.warn(`Temporal dead zone detected for ${varName || 'variable'}, using default:`, defaultValue);
      return defaultValue;
    }
    throw e; // Re-throw other errors
  }
}

// Safe module loader to prevent circular dependency issues
export function safeImport<T>(
  importPromise: Promise<T>,
  fallback: T
): Promise<T> {
  return importPromise.catch((error) => {
    logger.error('Module import failed, using fallback:', error);
    return fallback;
  });
}

// Global error handler setup
export function setupRuntimeGuards() {
  // Prevent unhandled errors from crashing the app
  window.addEventListener('error', (event) => {
    logger.error('ðŸš¨ Runtime Error Caught:', {
      message: event.message,
      filename: event.filename,
      lineno: event.lineno,
      colno: event.colno,
      error: event.error,
      timestamp: new Date().toISOString()
    });

    // Log to storage for analysis
    try {
      const errorLog = JSON.parse(localStorage.getItem('runtime_errors') || '[]');
      errorLog.push({
        type: 'script_error',
        message: event.message,
        filename: event.filename,
        lineno: event.lineno,
        colno: event.colno,
        stack: event.error?.stack,
        timestamp: new Date().toISOString()
      });

      // Keep only last 50 errors
      const _recentErrors = errorLog.slice(-50);
      localStorage.setItem('runtime_errors', JSON.stringify(_recentErrors));
    } catch (_error) {
      logger.warn('Failed to log runtime error: ', e);
    }

    // Don't prevent default error handling
    return false;
  });

  // Prevent unhandled promise rejections
  window.addEventListener('unhandledrejection', (event) => {
    logger.error('ðŸš¨ Unhandled Promise Rejection:', {
      reason: event.reason,
      promise: event.promise,
      timestamp: new Date().toISOString()
    });

    // Log promise rejections
    try {
      const rejectionLog = JSON.parse(localStorage.getItem('promise_rejections') || '[]');
      rejectionLog.push({
        reason: event.reason?.toString(),
        stack: event.reason?.stack,
        timestamp: new Date().toISOString()
      });

      // Keep only last 30 rejections
      const _recentRejections = rejectionLog.slice(-30);
      localStorage.setItem('promise_rejections', JSON.stringify(_recentRejections));
    } catch (_error) {
    logger.warn('Failed to log promise rejection:', e);
    }

    // Prevent the rejection from being logged to console
    event.preventDefault();
  });

  logger.info('âœ… Runtime guards initialized');
}

// Memory leak prevention
export class MemoryLeakGuard {
  private static instance: MemoryLeakGuard;
  private observers: Set<unknown> = new Set();
  private timers: Set<unknown> = new Set();
  private listeners: Map<EventTarget, Map<string, EventListener>> = new Map();

  static getInstance(): MemoryLeakGuard {
    if (!MemoryLeakGuard.instance) {
      MemoryLeakGuard.instance = new MemoryLeakGuard();
    }
    return MemoryLeakGuard.instance;
  }

  // Track observers for cleanup
  trackObserver(observer: unknown) {
    this.observers.add(_observer);
    return observer;
  }

  // Track timers for cleanup
  trackTimer(_timerId: unknown) {
    this.timers.add(_timerId);
    return _timerId;
  }

  // Track event listeners for cleanup
  trackListener(element: EventTarget, event: string, listener: EventListener) {
    if (!this.listeners.has(_element)) {
      this.listeners.set(element, new Map());
    }
    this.listeners.get(_element)!.set(event, listener);
  }

  // Clean up all tracked resources
  cleanup() {
    // Disconnect observers
    this.observers.forEach(observer => {
      try {
        if (observer.disconnect) observer.disconnect();
        if (observer.unobserve) observer.unobserve();
      } catch (_error) {
    logger.warn('Failed to disconnect observer:', e);
      }
    });
    this.observers.clear();

    // Clear timers
    this.timers.forEach(_timerId => {
      try {
        clearTimeout(_timerId);
        clearInterval(_timerId);
      } catch (_error) {
    logger.warn('Failed to clear timer:', e);
      }
    });
    this.timers.clear();

    // Remove event listeners
    this.listeners.forEach((events, element) => {
      events.forEach((listener, event) => {
        try {
          element.removeEventListener(event, listener);
        } catch (_error) {
    logger.warn('Failed to remove event listener:', e);
        }
      });
    });
    this.listeners.clear();
  }
}

// Performance monitoring guard
export class PerformanceGuard {
  private static metrics: Map<string, number> = new Map();

  static startMeasurement(name: string) {
    this.metrics.set(name, performance.now());
  }

  static endMeasurement(name: string, warnThreshold = 1000) {
    const start = this.metrics.get(name);
    if (_start) {
      const duration = performance.now() - start;
      this.metrics.delete(name);

      if (duration > warnThreshold) {
        logger.warn(`âš ï¸ Performance warning: ${name} took ${duration.toFixed(2)}ms`);
      }

      return duration;
    }
    return 0;
  }
}

// Initialize guards when module loads
if (typeof window !== 'undefined') {
  setupRuntimeGuards();
}

export default {
  safeAccess,
  safeCall,
  initializeVariable,
  safeImport,
  setupRuntimeGuards,
  MemoryLeakGuard,
  PerformanceGuard
};

/// <reference lib="webworker" />
/**
 * Web Worker for Heavy Chart Calculations
 * Offloads intensive data processing from main thread
 */

// @ts-expect-error - DedicatedWorkerGlobalScope is a global API
declare const self: DedicatedWorkerGlobalScope;

interface ChartDataPoint {
  date: string;
  value: number;
  label?: string;
  metadata?: unknown;
}

interface ProcessingRequest {
  type: 'PROCESS_MOOD_DATA' | 'AGGREGATE_WELLNESS' | 'CALCULATE_TRENDS' | 'SAMPLE_DATA';
  data: ChartDataPoint[];
  options?: {
    interval?: 'day' | 'week' | 'month';
    smoothing?: boolean;
    sampling?: number;
    trendLine?: boolean;
  };
}

interface ProcessingResponse {
  type: string;
  result: unknown;
  processingTime: number;
}

// Utility functions for data processing
const dataProcessors = {
  /**
   * Process mood data for visualization
   */
  processMoodData(data: ChartDataPoint[], options: any = {}) {
    const startTime = performance.now();

    // Group by interval
    const grouped = this.groupByInterval(data, options.interval || 'day');

    // Calculate averages
    const averaged = Object.entries(grouped).map(([date, points]) => ({
      date,
      value: points.reduce((sum, p) => sum + p.value, 0) / points.length,
      count: points.length,
      min: Math.min(...points.map(p => p.value)),
      max: Math.max(...points.map(p => p.value)),
    }));

    // Apply smoothing if requested
    const result = options.smoothing 
      ? this.applySmoothing(averaged, 3)
      : averaged;

    return {
      processed: result,
      stats: this.calculateStats(data),
      processingTime: performance.now() - startTime,
    };
  },

  /**
   * Aggregate wellness metrics
   */
  aggregateWellness(data: ChartDataPoint[], _options: any = {}) {
    const startTime = performance.now();

    // Calculate rolling averages
    const rollingAvg = this.calculateRollingAverage(data, 7);

    // Identify patterns
    const patterns = this.identifyPatterns(data);

    // Calculate wellness score
    const wellnessScore = this.calculateWellnessScore(data);

    return {
      aggregated: rollingAvg,
      patterns,
      wellnessScore,
      insights: this.generateInsights(patterns, wellnessScore),
      processingTime: performance.now() - startTime,
    };
  },

  /**
   * Calculate trend lines and predictions
   */
  calculateTrends(data: ChartDataPoint[], _options: any = {}) {
    const startTime = performance.now();

    // Linear regression for trend line
    const trendLine = this.linearRegression(data);

    // Calculate moving averages
    const movingAvg = {
      short: this.calculateMovingAverage(data, 7),
      medium: this.calculateMovingAverage(data, 30),
      long: this.calculateMovingAverage(data, 90),
    };

    // Detect trend direction
    const trendDirection = this.detectTrendDirection(trendLine);

    // Simple prediction (next 7 days)
    const predictions = this.generatePredictions(trendLine, 7);

    return {
      trendLine,
      movingAverages: movingAvg,
      direction: trendDirection,
      predictions,
      confidence: this.calculateConfidence(data, trendLine),
      processingTime: performance.now() - startTime,
    };
  },

  /**
   * Sample large datasets for performance
   */
  sampleData(data: ChartDataPoint[], options: any = {}) {
    const targetSize = options.sampling || 100;

    if (data.length <= targetSize) {
      return data;
    }

    const step = Math.ceil(data.length / targetSize);
    const sampled: ChartDataPoint[] = [];

    for (let i = 0; i < data.length; i += step) {
      // Take average of points in step range
      const slice = data.slice(i, Math.min(i + step, data.length));
      const avgValue = slice.reduce((sum, p) => sum + p.value, 0) / slice.length;

      const middlePoint = slice[Math.floor(slice.length / 2)];
      if (!middlePoint) continue;

      sampled.push({
        date: middlePoint.date,
        value: avgValue,
        metadata: { sampled: true, originalCount: slice.length },
      });
    }

    return sampled;
  },

  // Helper functions
  groupByInterval(data: ChartDataPoint[], interval: string) {
    const grouped: { [key: string]: ChartDataPoint[] } = {};

    data.forEach(point => {
      const date = new Date(point.date);
      let key: string;

      switch (interval) {
        case 'week':
          const week = this.getWeekNumber(date);
          key = `${date.getFullYear()}-W${week}`;
          break;
        case 'month':
          key = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
          break;
        default: // day
          key = date.toISOString().split('T')[0] || date.toISOString();
      }

      if (!grouped[key]) {
        grouped[key] = [];
      }
      grouped[key]!.push(point);
    });

    return grouped;
  },

  getWeekNumber(date: Date): number {
    const d = new Date(Date.UTC(date.getFullYear(), date.getMonth(), date.getDate()));
    const dayNum = d.getUTCDay() || 7;
    d.setUTCDate(d.getUTCDate() + 4 - dayNum);
    const yearStart = new Date(Date.UTC(d.getUTCFullYear(), 0, 1));
    return Math.ceil((((d.getTime() - yearStart.getTime()) / 86400000) + 1) / 7);
  },

  applySmoothing(data: any[], windowSize: number) {
    const smoothed = [];

    for (let i = 0; i < data.length; i++) {
      const start = Math.max(0, i - Math.floor(windowSize / 2));
      const end = Math.min(data.length, i + Math.ceil(windowSize / 2));
      const window = data.slice(start, end);

      const avgValue = window.reduce((sum, p) => sum + p.value, 0) / window.length;

      smoothed.push({
        ...data[i],
        value: avgValue,
        smoothed: true,
      });
    }

    return smoothed;
  },

  calculateStats(data: ChartDataPoint[]) {
    const values = data.map(p => p.value);
    const sum = values.reduce((a, b) => a + b, 0);
    const mean = sum / values.length;

    const sortedValues = [...values].sort((a, b) => a - b);
    const median = sortedValues[Math.floor(sortedValues.length / 2)];

    const variance = values.reduce((sum, val) => sum + Math.pow(val - mean, 2), 0) / values.length;
    const stdDev = Math.sqrt(variance);

    return {
      mean,
      median,
      min: Math.min(...values),
      max: Math.max(...values),
      stdDev,
      count: values.length,
    };
  },

  calculateRollingAverage(data: ChartDataPoint[], windowSize: number) {
    const result = [];

    for (let i = 0; i < data.length; i++) {
      const start = Math.max(0, i - windowSize + 1);
      const window = data.slice(start, i + 1);
      const avg = window.reduce((sum, p) => sum + p.value, 0) / window.length;

      result.push({
        ...data[i],
        rollingAvg: avg,
      });
    }

    return result;
  },

  identifyPatterns(data: ChartDataPoint[]) {
    const patterns = {
      weeklyPattern: this.findWeeklyPattern(data),
      monthlyPattern: this.findMonthlyPattern(data),
      peaks: this.findPeaks(data),
      troughs: this.findTroughs(data),
    };

    return patterns;
  },

  findWeeklyPattern(data: ChartDataPoint[]) {
    const dayAverages: { [key: number]: number[] } = {};

    data.forEach(point => {
      const day = new Date(point.date).getDay();
      if (!dayAverages[day]) {
        dayAverages[day] = [];
      }
      dayAverages[day].push(point.value);
    });

    const pattern = Object.entries(dayAverages).map(([day, values]) => ({
      day: parseInt(day),
      average: values.reduce((sum, v) => sum + v, 0) / values.length,
    }));

    return pattern;
  },

  findMonthlyPattern(data: ChartDataPoint[]) {
    const monthAverages: { [key: number]: number[] } = {};

    data.forEach(point => {
      const month = new Date(point.date).getMonth();
      if (!monthAverages[month]) {
        monthAverages[month] = [];
      }
      monthAverages[month].push(point.value);
    });

    const pattern = Object.entries(monthAverages).map(([month, values]) => ({
      month: parseInt(month),
      average: values.reduce((sum, v) => sum + v, 0) / values.length,
    }));

    return pattern;
  },

  findPeaks(data: ChartDataPoint[], threshold = 0.8) {
    const values = data.map(p => p.value);
    const max = Math.max(...values);
    const peakThreshold = max * threshold;

    return data.filter(p => p.value >= peakThreshold);
  },

  findTroughs(data: ChartDataPoint[], threshold = 0.2) {
    const values = data.map(p => p.value);
    const max = Math.max(...values);
    const troughThreshold = max * threshold;

    return data.filter(p => p.value <= troughThreshold);
  },

  calculateWellnessScore(data: ChartDataPoint[]) {
    const recentData = data.slice(-30); // Last 30 days
    const avgMood = recentData.reduce((sum, p) => sum + p.value, 0) / recentData.length;
    const consistency = 1 - (this.calculateStats(recentData).stdDev / 10);
    const trend = this.detectTrendDirection(this.linearRegression(recentData));

    const trendScore = trend === 'improving' ? 1.2 : trend === 'declining' ? 0.8 : 1;

    return Math.min(100, Math.max(0, (avgMood * 10 * consistency * trendScore)));
  },

  generateInsights(patterns: any, wellnessScore: number) {
    const insights = [];

    if (wellnessScore > 70) {
      insights.push('Your overall wellness is excellent. Keep up the great work!');
    } else if (wellnessScore > 50) {
      insights.push('Your wellness is good, with room for improvement.');
    } else {
      insights.push('Your wellness score suggests you might benefit from additional support.');
    }

    // Weekly pattern insights
    const bestDay = patterns.weeklyPattern.reduce((best: any, current: any) => 
      current.average > best.average ? current : best
    );
    insights.push(`You tend to feel best on ${this.getDayName(bestDay.day)}s.`);

    return insights;
  },

  getDayName(day: number): string {
    const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    return days[day] || 'Unknown';
  },

  linearRegression(data: ChartDataPoint[]) {
    const n = data.length;
    let sumX = 0, sumY = 0, sumXY = 0, sumX2 = 0;

    data.forEach((point, i) => {
      sumX += i;
      sumY += point.value;
      sumXY += i * point.value;
      sumX2 += i * i;
    });

    const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
    const intercept = (sumY - slope * sumX) / n;

    return { slope, intercept };
  },

  calculateMovingAverage(data: ChartDataPoint[], period: number) {
    const result = [];

    for (let i = period - 1; i < data.length; i++) {
      const slice = data.slice(i - period + 1, i + 1);
      const avg = slice.reduce((sum, p) => sum + p.value, 0) / period;

      const dataPoint = data[i];
      if (!dataPoint) continue;

      result.push({
        date: dataPoint.date,
        value: avg,
      });
    }

    return result;
  },

  detectTrendDirection(regression: { slope: number; intercept: number }) {
    if (regression.slope > 0.1) return 'improving';
    if (regression.slope < -0.1) return 'declining';
    return 'stable';
  },

  generatePredictions(regression: { slope: number; intercept: number }, days: number) {
    const predictions = [];
    const today = new Date();

    for (let i = 1; i <= days; i++) {
      const futureDate = new Date(today);
      futureDate.setDate(today.getDate() + i);

      const predictedValue = regression.intercept + regression.slope * i;

      predictions.push({
        date: futureDate.toISOString(),
        value: Math.max(0, Math.min(10, predictedValue)), // Clamp between 0-10
        isPrediction: true,
      });
    }

    return predictions;
  },

  calculateConfidence(data: ChartDataPoint[], regression: { slope: number; intercept: number }) {
    // Calculate R-squared
    const yMean = data.reduce((sum, p) => sum + p.value, 0) / data.length;
    let ssRes = 0, ssTot = 0;

    data.forEach((point, i) => {
      const predicted = regression.intercept + regression.slope * i;
      ssRes += Math.pow(point.value - predicted, 2);
      ssTot += Math.pow(point.value - yMean, 2);
    });

    const rSquared = 1 - (ssRes / ssTot);
    return Math.max(0, Math.min(1, rSquared));
  },
};

// Message handler
self.addEventListener('message', (event: MessageEvent<ProcessingRequest>) => {
  const { type, data, options } = event.data;
  const startTime = performance.now();

  let result: unknown;

  try {
    switch (type) {
      case 'PROCESS_MOOD_DATA':
        result = dataProcessors.processMoodData(data, options);
        break;

      case 'AGGREGATE_WELLNESS':
        result = dataProcessors.aggregateWellness(data, options);
        break;

      case 'CALCULATE_TRENDS':
        result = dataProcessors.calculateTrends(data, options);
        break;

      case 'SAMPLE_DATA':
        result = dataProcessors.sampleData(data, options);
        break;

      default:
        throw new Error(`Unknown processing type: ${type}`);
    }

    const response: ProcessingResponse = {
      type,
      result,
      processingTime: performance.now() - startTime,
    };

    self.postMessage(response);
  } catch (error) {
    self.postMessage({
      type: 'ERROR',
      error: error instanceof Error ? error.message : 'Processing error',
      processingTime: performance.now() - startTime,
    });
  }
});

