/**
 * Unified Error Boundary Component
 * Combines crisis-aware features, multiple fallback options, and comprehensive error handling
 * Ensures users never lose access to critical crisis intervention resources during errors
 */

import React, { Component, ErrorInfo, ReactNode } from 'react';
import { AlertTriangle, Phone, MessageCircle, Home, RefreshCw } from 'lucide-react';
import { logError } from '../utils/logger';
import { logger } from '../services/logging/logger';

interface ErrorBoundaryState {
  hasError: boolean;
  error: Error | null;
  errorInfo: ErrorInfo | null;
  errorId: string;
}

interface ErrorBoundaryProps {
  children: ReactNode;
  fallback?: ReactNode;
  FallbackComponent?: React.ComponentType<unknown>;
  fallbackRender?: (props: { error: Error; resetErrorBoundary: () => void }) => ReactNode;
  onError?: (error: Error, errorInfo: ErrorInfo) => void;
  onReset?: () => void;
  showCrisisResources?: boolean;
}

export class ErrorBoundary extends Component<ErrorBoundaryProps, ErrorBoundaryState> {
  private retryCount = 0;
  private maxRetries = 3;

  constructor(props: ErrorBoundaryProps) {
    super(props);

    this.state = {
      hasError: false,
      error: null,
      errorInfo: null,
      errorId: this.generateErrorId()
    };
  }

  private generateErrorId(): string {
    return `err_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  static getDerivedStateFromError(error: Error): Partial<ErrorBoundaryState> {
    return {
      hasError: true,
      error,
      errorId: `err_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
    };
  }

  override componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    // Update state with error info
    this.setState({ error, errorInfo });

    // Log error with multiple logging systems
    logError('Component error caught by ErrorBoundary', 'ErrorBoundary', { error, errorInfo });

    // Log to crisis-aware logging system if available
    if (logger?.logCrisisIntervention) {
      logger.logCrisisIntervention('component_error', undefined, {
        error: error.message,
        component: errorInfo.componentStack,
        severity: 'high',
        emergency_resources_available: true,
        errorId: this.state.errorId
      });
    }

    // Log error details for debugging
    logger.error('ðŸš¨ ERROR BOUNDARY CAUGHT:', {
      error: error.message,
      stack: error.stack,
      componentStack: errorInfo.componentStack,
      errorId: this.state.errorId,
      timestamp: new Date().toISOString(),
      userAgent: navigator.userAgent,
      url: window.location.href
    });

    // Send error to monitoring service
    this.reportError(error, errorInfo);

    // Call custom error handler
    if (this.props.onError) {
      this.props.onError(error, errorInfo);
    }
  }

  private reportError = async (error: Error, errorInfo: ErrorInfo) => {
    try {
      // Store error in localStorage for analysis
      const errorReport = {
        errorId: this.state.errorId,
        message: error.message,
        stack: error.stack,
        componentStack: errorInfo.componentStack,
        timestamp: new Date().toISOString(),
        url: window.location.href,
        userAgent: navigator.userAgent
      };

      localStorage.setItem(`error_${this.state.errorId}`, JSON.stringify(errorReport));

      // Send to monitoring service in production
      if (import.meta.env.PROD) {
        try {
          fetch('/api/monitoring/crisis-error', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(errorReport),
          }).catch(() => {
            // Fail silently - don&apos;t let monitoring failures affect crisis support
          });
        } catch {
          // Fail silently - don&apos;t let monitoring failures affect crisis support
          console.error('Monitoring error:', error);
        }
      }

      // Send to Sentry if available
      if ((window as unknown).Sentry) {
        (window as unknown).Sentry.captureException(error, {
          tags: {
            component: 'ErrorBoundary',
            errorId: this.state.errorId
          },
          extra: {
            componentStack: errorInfo.componentStack
          }
        });
      }
    } catch {
      logger.error('Failed to report error', 'ErrorBoundary', error);
    }
  };

  private handleReset = () => {
    if (this.retryCount < this.maxRetries) {
      this.retryCount++;
      logger.info(`Retrying... (${this.retryCount}/${this.maxRetries})`, 'ErrorBoundary');

      this.setState({
        hasError: false,
        error: null,
        errorInfo: null,
        errorId: this.generateErrorId()
      });

      // Call onReset callback if provided
      if (this.props.onReset) {
        this.props.onReset();
      }
    } else {
      logger.error('âŒ Maximum retry attempts reached');
    }
  };

  private handleReload = () => {
    window.location.reload();
  };

  private handleEmergencyCall = (number: string) => {
    if (logger?.logCrisisIntervention) {
      logger.logCrisisIntervention('emergency_call_from_error_boundary', undefined, {
        number,
        error_context: this.state.error?.message
      });
    }

    // Try to initiate call
    window.open(`tel:${number}`, '_self');
  };

  private renderCrisisResources = () => (
    <div className="bg-red-50 border-l-4 border-red-500 p-4 mb-6">
      <div className="flex items-center mb-3">
        <AlertTriangle className="h-6 w-6 text-red-600 mr-2" />
        <h3 className="text-lg font-semibold text-red-800">
          Emergency Resources Available
        </h3>
      </div>

      <p className="text-red-700 mb-4">
        Even though there{"'"}s a technical issue, your safety is our priority. 
        These resources are always available:
      </p>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
        <button
          onClick={() => this.handleEmergencyCall('911')}
          className="flex items-center justify-center px-4 py-3 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
        >
          <Phone className="h-5 w-5 mr-2" />
          Emergency: 911
        </button>

        <button
          onClick={() => this.handleEmergencyCall('988')}
          className="flex items-center justify-center px-4 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
        >
          <Phone className="h-5 w-5 mr-2" />
          Crisis Hotline: 988
        </button>

        <button
          onClick={() => window.open('sms:741741', '_self')}
          className="flex items-center justify-center px-4 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
        >
          <MessageCircle className="h-5 w-5 mr-2" />
          Crisis Text: 741741
        </button>

        <button
          onClick={() => window.location.href = '/crisis'}
          className="flex items-center justify-center px-4 py-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors"
        >
          <AlertTriangle className="h-5 w-5 mr-2" />
          Crisis Resources
        </button>
      </div>
    </div>
  );

  private renderErrorUI() {
    const { error, errorInfo, errorId } = this.state;
    const canRetry = this.retryCount < this.maxRetries;

    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-red-50 to-orange-50 p-4">
        <div className="max-w-2xl w-full bg-white rounded-2xl shadow-xl overflow-hidden">
          <div className="bg-gradient-to-r from-red-600 to-orange-600 px-6 py-4">
            <div className="flex items-center">
              <AlertTriangle className="h-8 w-8 text-white mr-3" />
              <div>
                <h1 className="text-xl font-bold text-white">
                  Technical Issue Detected
                </h1>
                <p className="text-red-100">
                  Your safety resources remain available
                </p>
              </div>
            </div>
          </div>

          <div className="p-6">
            {(this.props.showCrisisResources !== false) && this.renderCrisisResources()}

            <div className="bg-gray-50 rounded-lg p-4 mb-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-2">
                What happened?
              </h3>
              <p className="text-gray-700 mb-3">
                A component on this page encountered an unexpected error. 
                This doesn{"'"}t affect the safety and crisis support features of the application.
              </p>

              {import.meta.env.DEV && error && (
                <details className="mt-4">
                  <summary className="cursor-pointer text-sm text-gray-600 hover:text-gray-800 mb-2">
                    Technical Details (Development Mode)
                  </summary>
                  <div className="bg-gray-100 rounded p-3 text-xs font-mono overflow-auto max-h-48">
                    <div className="text-red-600 font-semibold mb-2">Error:</div>
                    <div className="mb-3">{error.message}</div>
                    {error.stack && (
                      <>
                        <div className="text-red-600 font-semibold mb-2">Stack Trace:</div>
                        <pre className="whitespace-pre-wrap">{error.stack}</pre>
                      </>
                    )}
                    {errorInfo?.componentStack && (
                      <>
                        <div className="text-red-600 font-semibold mb-2 mt-3">Component Stack:</div>
                        <pre className="whitespace-pre-wrap">{errorInfo.componentStack}</pre>
                      </>
                    )}
                  </div>
                </details>
              )}

              <div className="text-sm text-gray-500 mt-2">
                Error ID: {errorId}
              </div>
            </div>

            <div className="flex flex-col sm:flex-row gap-3 justify-center">
              {canRetry && (
                <button
                  onClick={this.handleReset}
                  className="flex items-center justify-center px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                >
                  <RefreshCw className="h-5 w-5 mr-2" />
                  Try Again ({this.maxRetries - this.retryCount} left)
                </button>
              )}

              <button
                onClick={() => window.location.href = '/'}
                className="flex items-center justify-center px-6 py-3 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
              >
                <Home className="h-5 w-5 mr-2" />
                Go to Home
              </button>

              <button
                onClick={this.handleReload}
                className="flex items-center justify-center px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
              >
                <RefreshCw className="h-5 w-5 mr-2" />
                Reload Page
              </button>

              <button
                onClick={() => window.location.href = '/crisis'}
                className="flex items-center justify-center px-6 py-3 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
              >
                <AlertTriangle className="h-5 w-5 mr-2" />
                Crisis Support
              </button>
            </div>

            <div className="mt-6 pt-6 border-t border-gray-200">
              <p className="text-sm text-gray-600 text-center">
                If you{"'"}re experiencing a mental health emergency, please contact emergency services immediately. 
                Technical issues never prevent access to crisis support resources.
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  override render() {
    if (this.state.hasError) {
      // Support react-error-boundary fallbackRender prop
      if (this.props.fallbackRender && this.state.error) {
        return this.props.fallbackRender({
          error: this.state.error,
          resetErrorBoundary: this.handleReset
        });
      }

      // Support react-error-boundary FallbackComponent prop
      if (this.props.FallbackComponent && this.state.error) {
        const FallbackComponent = this.props.FallbackComponent;
        return <FallbackComponent error={this.state.error} resetErrorBoundary={this.handleReset} />;
      }

      // Support our own fallback prop
      if (this.props.fallback) {
        return this.props.fallback;
      }

      // Render default error UI with crisis resources
      return this.renderErrorUI();
    }

    return this.props.children;
  }
}

// Export alias for backward compatibility
export class EmergencyErrorBoundary extends ErrorBoundary {}
export class CrisisErrorBoundary extends ErrorBoundary {}

/**
 * Higher-Order Component for Crisis Error Boundaries
 * Automatically wraps crisis-related components with error boundaries
 */
export interface WithErrorBoundaryOptions {
  showCrisisResources?: boolean;
  fallback?: React.ReactNode;
  FallbackComponent?: React.ComponentType<unknown>;
  fallbackRender?: (props: { error: Error; resetErrorBoundary: () => void }) => ReactNode;
  onError?: (error: Error, errorInfo: ErrorInfo) => void;
  onReset?: () => void;
}

/**
 * HOC that wraps components with error boundary
 */
export function withErrorBoundary<P extends object>(
  Component: React.ComponentType<P>,
  options: WithErrorBoundaryOptions = {}
) {
  const WrappedComponent = React.forwardRef<any, P & React.RefAttributes<unknown>>((props, ref) => {
    const { ...componentProps } = props;
    return (
      <ErrorBoundary {...options}>
        <Component {...componentProps as P} ref={ref} />
      </ErrorBoundary>
    );
  });

  // Set display name for debugging
  WrappedComponent.displayName = `withErrorBoundary(${Component.displayName || Component.name})`;

  return WrappedComponent;
}

/**
 * Alias for crisis-aware error boundary HOC
 */
export const _withCrisisErrorBoundary = withErrorBoundary;

/**
 * Decorator version for class components
 */
export function CrisisProtected(options: WithErrorBoundaryOptions = {}) {
  return function <P extends object>(Component: React.ComponentType<P>) {
    return withErrorBoundary(Component, options);
  };
}

/**
 * Hook version for functional components
 */
export function useErrorBoundary() {
  return React.useCallback((component: React.ComponentType<unknown>, options?: WithErrorBoundaryOptions) => {
    return withErrorBoundary(component, options);
  }, []);
}

// Global error handler for uncaught errors
export const _setupGlobalErrorHandling = () => {
  // Handle uncaught JavaScript errors
  window.addEventListener('error', (event) => {
    logger.error('ðŸš¨ UNCAUGHT ERROR:', {
      message: event.message,
      source: event.filename,
      line: event.lineno,
      column: event.colno,
      error: event.error?.stack,
      timestamp: new Date().toISOString()
    });

    // Store error for analysis
    const errorReport = {
      type: 'uncaught_error',
      message: event.message,
      source: event.filename,
      line: event.lineno,
      column: event.colno,
      stack: event.error?.stack,
      timestamp: new Date().toISOString()
    };

    try {
      localStorage.setItem(`uncaught_error_${Date.now()}`, JSON.stringify(errorReport));
    } catch (err) {
      logger.error('Failed to store error report', 'ErrorBoundary', err);
    }
  });

  // Handle unhandled promise rejections
  window.addEventListener('unhandledrejection', (event) => {
    logger.error('ðŸš¨ UNHANDLED PROMISE REJECTION:', {
      reason: event.reason,
      promise: event.promise,
      timestamp: new Date().toISOString()
    });

    // Store rejection for analysis
    const rejectionReport = {
      type: 'unhandled_rejection',
      reason: event.reason?.toString(),
      stack: event.reason?.stack,
      timestamp: new Date().toISOString()
    };

    try {
      localStorage.setItem(`rejection_${Date.now()}`, JSON.stringify(rejectionReport));
    } catch (err) {
      logger.error('Failed to store rejection report', 'ErrorBoundary', err);
    }
  });

  logger.info('Global error handling initialized', 'ErrorBoundary');
};

export default ErrorBoundary;

/**
 * Real-Time Crisis Chat System
 * 
 * Secure, encrypted chat for crisis intervention
 * Connects to peer supporters and professional counselors
 * Privacy-first with end-to-end encryption
 */

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Send, Phone, Shield, Lock,
  AlertCircle, UserCheck,
  MessageSquare, Volume2, VolumeX
} from 'lucide-react';
import { logger } from '../../utils/logger';

interface ChatMessage {
  id: string;
  senderId: string;
  senderName: string;
  senderRole: 'user' | 'peer' | 'professional' | 'moderator' | 'system';
  content: string;
  timestamp: Date;
  type: 'text' | 'voice' | 'image' | 'system' | 'crisis-alert';
  encrypted: boolean;
  readStatus: 'sent' | 'delivered' | 'read';
  supportLevel?: 'info' | 'support' | 'intervention';
  metadata?: Record<string, unknown>;
}

interface ChatParticipant {
  id: string;
  name: string;
  role: 'user' | 'peer' | 'professional' | 'moderator';
  status: 'online' | 'away' | 'offline';
  avatar?: string;
  credentials?: string;
  specializations?: string[];
  responseTime: string;
  rating?: number;
  isTyping: boolean;
}

interface ChatRoom {
  id: string;
  type: 'crisis' | 'peer-support' | 'group' | 'professional';
  participants: ChatParticipant[];
  messages: ChatMessage[];
  isActive: boolean;
  isEncrypted: boolean;
  moderationLevel: 'light' | 'standard' | 'strict';
  emergencyProtocols: boolean;
  created: Date;
  lastActivity: Date;
}

interface CrisisProtocol {
  id: string;
  trigger: string;
  action: 'alert' | 'escalate' | 'emergency' | 'resources';
  automated: boolean;
  priority: 'low' | 'medium' | 'high' | 'critical';
}

export function RealTimeCrisisChat() {
  const [___currentRoom, _setCurrentRoom] = useState<ChatRoom | null>(null);
  const [messages, _setMessages] = useState<ChatMessage[]>([]);
  const [newMessage, _setNewMessage] = useState('');
  const [isConnected, _setIsConnected] = useState(false);
  const [availableSupport, _setAvailableSupport] = useState<ChatParticipant[]>([]);
  const [___chatType, _setChatType] = useState<'crisis' | 'peer' | 'professional'>('crisis');
  const [isTyping, _setIsTyping] = useState(false);
  const [soundEnabled, _setSoundEnabled] = useState(true);
  const [___chatSettings, _setChatSettings] = useState({
    notifications: true,
    readReceipts: true,
    autoConnect: true,
    emergencySharing: true
  });

  const messagesEndRef = useRef<HTMLDivElement>(null);
  const __inputRef   = useRef<HTMLInputElement>(null);
  const __chatContainerRef   = useRef<HTMLDivElement>(null);

  // Initialize crisis chat connection
  useEffect(() => {
    initializeCrisisChat();
    return () => {
      disconnectFromChat();
    };
     
  }, [initializeCrisisChat]);

  // Auto-scroll to latest message
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Handle typing indicators
  const __handleTyping   = useCallback(() => {
    if (!isTyping) {
      setIsTyping(true);
      // Send typing indicator to other participants
      // sendTypingIndicator(true);
      
      // Clear typing indicator after 3 seconds of inactivity
      setTimeout(() => {
        setIsTyping(false);
        // sendTypingIndicator(false);
      }, 3000);
    }
  }, [isTyping]);

  const initializeCrisisChat = async () => {
    try {
      // Connect to crisis chat service
      await connectToCrisisService();
      
      // Load available support options
      const supporters = await loadAvailableSupport();
      setAvailableSupport(supporters);
      
      // Set up emergency protocols
      setupEmergencyProtocols();
      
      setIsConnected(true);
      
      // Send system welcome message
      addSystemMessage(
        'Crisis support is now available. You are connected to trained volunteers and professionals.',
        'info'
      );

    } catch (error) {
      logger.error('Failed to initialize crisis chat', 'RealTimeCrisisChat', error);
      addSystemMessage(
        'Having trouble connecting to chat. Emergency hotlines are still available: 988',
        'crisis-alert'
      );
    }
  };

  const connectToCrisisService = async () => {
    // In production, this would establish WebSocket connection
    // with end-to-end encryption for crisis support
    return new Promise(resolve => setTimeout(resolve, 1000));
  };

  const loadAvailableSupport = async (): Promise<ChatParticipant[]> => {
    // Mock data - in production, would load from crisis support API
    return [
      {
        id: 'peer-1',
        name: 'Sarah',
        role: 'peer',
        status: 'online',
        responseTime: '< 2 min',
        rating: 4.9,
        isTyping: false
      },
      {
        id: 'pro-1',
        name: 'Dr. Martinez',
        role: 'professional',
        status: 'online',
        credentials: 'Licensed Clinical Social Worker',
        specializations: ['Crisis Intervention', 'Trauma'],
        responseTime: '< 5 min',
        rating: 4.95,
        isTyping: false
      },
      {
        id: 'peer-2',
        name: 'Alex',
        role: 'peer',
        status: 'online',
        responseTime: '< 3 min',
        rating: 4.8,
        isTyping: false
      }
    ];
  };

  const setupEmergencyProtocols = () => {
    // Configure automated crisis detection and response
    const protocols: CrisisProtocol[] = [
      {
        id: 'suicide-keywords',
        trigger: 'suicide|kill myself|end it all',
        action: 'emergency',
        automated: true,
        priority: 'critical'
      },
      {
        id: 'immediate-danger',
        trigger: 'going to hurt|about to|right now',
        action: 'escalate',
        automated: true,
        priority: 'critical'
      }
    ];
    
    // Set up keyword monitoring for crisis escalation
    protocols.forEach(_protocol => {
      // In production, this would configure server-side monitoring
    });
  };

  const sendMessage = async () => {
    if (!newMessage.trim() || !_currentRoom) return;

    const message: ChatMessage = {
      id: generateMessageId(),
      senderId: 'current-user',
      senderName: 'You',
      senderRole: 'user',
      content: newMessage,
      timestamp: new Date(),
      type: 'text',
      encrypted: true,
      readStatus: 'sent',
      supportLevel: 'support'
    };

    // Add message to local state immediately
    setMessages(prev => [...prev, message]);
    setNewMessage('');

    // Check for crisis indicators
    await checkCrisisIndicators(message);

    // Send to chat service
    await sendToParticipants(message);

    // Play send sound if enabled
    if (_soundEnabled) {
      playNotificationSound('send');
    }

    // Request response from available supporters
    if (availableSupport.length > 0) {
      requestSupportResponse(message);
    }
  };

  const checkCrisisIndicators = async (message: ChatMessage) => {
    const crisisKeywords = [
      'suicide', 'kill myself', 'end it all', 'no hope', 'better off dead',
      'can\'t go on', 'hurt myself', 'give up', 'no point living'
    ];

    const content = message.content.toLowerCase();
    const _hasCrisisIndicators = crisisKeywords.some(_keyword => 
      content.includes(_keyword)
    );

    if (_hasCrisisIndicators) {
      // Immediate crisis response
      await triggerCrisisProtocol('critical', message);
      
      // Add crisis resources message
      addSystemMessage(
        'ðŸš¨ Crisis support activated. Professional help is connecting now. For immediate help: Call 988 or text HOME to 741741',
        'crisis-alert'
      );

      // Auto-connect to professional counselor
      await connectToProfessional();
    }
  };

  const triggerCrisisProtocol = async (priority: string, _message: ChatMessage) => {
    // Log crisis event (_anonymized)
    logger.crisis('Crisis protocol triggered', priority as 'low' | 'medium' | 'high' | 'critical', 'RealTimeCrisisChat', { priority, timestamp: new Date() });
    
    // In production, this would:
    // 1. Alert crisis intervention team
    // 2. Initiate emergency protocols if needed
    // 3. Connect to appropriate level of support
    
    if (priority === 'critical') {
      // Show emergency resources immediately
      showEmergencyResources();
    }
  };

  const connectToProfessional = async () => {
    const professional = availableSupport.find(p => p.role === 'professional');
    if (_professional) {
      // Simulate professional joining chat
      setTimeout(() => {
        const welcomeMessage: ChatMessage = {
          id: generateMessageId(),
          senderId: professional.id,
          senderName: professional.name,
          senderRole: 'professional',
          content: `Hi, I&apos;m ${professional.name}, a ${professional.credentials}. I&apos;m here to help and support you through this difficult time. You&apos;re not alone.`,
          timestamp: new Date(),
          type: 'text',
          encrypted: true,
          readStatus: 'delivered',
          supportLevel: 'intervention'
        };
        
        setMessages(prev => [...prev, welcomeMessage]);
        
        if (_soundEnabled) {
          playNotificationSound('professional');
        }
      }, 2000);
    }
  };

  const addSystemMessage = (content: string, level: 'info' | 'crisis-alert' = 'info') => {
    const systemMessage: ChatMessage = {
      id: generateMessageId(),
      senderId: 'system',
      senderName: 'Crisis Support System',
      senderRole: 'system',
      content,
      timestamp: new Date(),
      type: 'system',
      encrypted: false,
      readStatus: 'delivered',
      supportLevel: level === 'crisis-alert' ? 'intervention' : 'info'
    };

    setMessages(prev => [...prev, systemMessage]);
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const playNotificationSound = (type: 'send' | 'receive' | 'professional' | 'alert') => {
    if (!soundEnabled) return;
    
    // In production, would play appropriate notification sounds
    logger.info(`Playing ${type} notification sound`, 'RealTimeCrisisChat');
  };

  const showEmergencyResources = () => {
    const emergencyMessage: ChatMessage = {
