  const defaultActions: QuickAction[] = [
    {
      id: '1',
      label: 'Log Mood',
      icon: 'mood',
      description: 'Track how you\'re feeling',
      action: '/wellness/mood',
      color: 'bg-purple-500',
      category: 'tracking',
      keyboard: 'alt+m',
