import { Clock, Video, MapPin, Pill, Calendar, CheckCircle, XCircle, AlertCircle } from 'lucide-react';
import { motion } from 'framer-motion';
import { ScheduleItem } from '../../../types/dashboard';

interface TodayScheduleProps {
  scheduleItems?: ScheduleItem[];
  onItemClick?: (item: ScheduleItem) => void;
  onReschedule?: (item: ScheduleItem) => void;
  onMarkComplete?: (item: ScheduleItem) => void;
}

export function TodaySchedule({ 
  scheduleItems = [], 
  onItemClick, 
  onReschedule,
  onMarkComplete 
}: TodayScheduleProps) {

  // Group items by time period
  const groupItemsByPeriod = () => {
    const now = new Date();
    const groups: Record<string, ScheduleItem[]> = {
      overdue: [],
      current: [],
      upcoming: [],
      later: [],
    };

    scheduleItems.forEach(item => {
      const itemTime = new Date(item.time);
      const diffMinutes = (itemTime.getTime() - now.getTime()) / (1000 * 60);

      if (item._status === 'completed') {
        // Skip completed items or show them separately
      } else if (item.status === 'missed' || diffMinutes < -30) {
        groups.overdue!.push(item);
      } else if (diffMinutes >= -30 && diffMinutes <= 30) {
        groups.current!.push(item);
      } else if (diffMinutes > 30 && diffMinutes <= 180) {
        groups.upcoming!.push(item);
      } else {
        groups.later!.push(item);
      }
    });

    return groups;
  };

  const groups = groupItemsByPeriod();

  // Get icon for schedule item type
  const getItemIcon = (_type: ScheduleItem['type']) => {
    switch (_type) {
      case 'therapy':
        return Video;
      case 'medication':
        return Pill;
      case 'appointment':
        return Calendar;
      default:
        return Clock;
    }
  };

  // Get status color
  const getStatusColor = (_status: ScheduleItem['status'], priority: ScheduleItem['priority']) => {
    if (_status === 'missed') return 'border-red-500 bg-red-50';
    if (_status === 'ongoing') return 'border-green-500 bg-green-50 animate-pulse';
    if (_status === 'completed') return 'border-gray-300 bg-gray-50';
    if (priority === 'high') return 'border-orange-500 bg-orange-50';
    return 'border-gray-300 bg-white';
  };

  // Get status icon
  const StatusIcon = ({ _status }: { _status: ScheduleItem['_status'] }) => {
    switch (_status) {
      case 'completed':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'missed':
        return <XCircle className="h-4 w-4 text-red-500" />;
      case 'cancelled':
        return <XCircle className="h-4 w-4 text-gray-400" />;
      case 'ongoing':
        return <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />;
      default:
        return null;
    }
  };

  // Format time display
  const formatTime = (_date: Date) => {
    return new Date(_date).toLocaleTimeString('en-US', { 
      hour: 'numeric', 
      minute: '2-digit',
      hour12: true 
    });
  };

  // Calculate time until
  const getTimeUntil = (_date: Date) => {
    const now = new Date();
    const itemTime = new Date(_date);
    const diffMinutes = Math.floor((itemTime.getTime() - now.getTime()) / (1000 * 60));

    if (diffMinutes < 0) return 'Overdue';
    if (diffMinutes === 0) return 'Now';
    if (diffMinutes < 60) return `In ${diffMinutes} min`;
    if (diffMinutes < 120) return 'In 1 hour';
    return `In ${Math.floor(diffMinutes / 60)} hours`;
  };

  // Empty state
  if (scheduleItems.length === 0) {
    return (
      <div className="text-center py-8">
        <Calendar className="h-12 w-12 text-gray-300 mx-auto mb-3" />
        <p className="text-gray-500">No scheduled items for today</p>
        <p className="text-sm text-gray-400 mt-1">Your schedule will appear here</p>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Overdue Items */}
      {groups.overdue!.length > 0 && (
        <div className="space-y-2">
          <div className="flex items-center space-x-2">
            <AlertCircle className="h-4 w-4 text-red-500" />
            <h4 className="text-sm font-semibold text-red-700">Overdue</h4>
          </div>
          <div className="space-y-2">
            {groups.overdue!.map((item) => (
              <ScheduleItemCard
                key={item.id}
                item={item}
                onClick={onItemClick}
                onReschedule={onReschedule}
                onMarkComplete={onMarkComplete}
                getItemIcon={getItemIcon}
                formatTime={formatTime}
                getStatusColor={getStatusColor}
                StatusIcon={StatusIcon}
              />
            ))}
          </div>
        </div>
      )}

      {/* Current/Ongoing Items */}
      {groups.current!.length > 0 && (
        <div className="space-y-2">
          <h4 className="text-sm font-semibold text-green-700">Happening Now</h4>
          <div className="space-y-2">
            {groups.current!.map((item) => (
              <ScheduleItemCard
                key={item.id}
                item={item}
                onClick={onItemClick}
                onReschedule={onReschedule}
                onMarkComplete={onMarkComplete}
                getItemIcon={getItemIcon}
                formatTime={formatTime}
                getStatusColor={getStatusColor}
                StatusIcon={StatusIcon}
              />
            ))}
          </div>
        </div>
      )}

      {/* Upcoming Items */}
      {groups.upcoming!.length > 0 && (
        <div className="space-y-2">
          <h4 className="text-sm font-semibold text-gray-700">Coming Up</h4>
          <div className="space-y-2">
            {groups.upcoming!.map((item) => (
              <ScheduleItemCard
                key={item.id}
                item={item}
                onClick={onItemClick}
                onReschedule={onReschedule}
                onMarkComplete={onMarkComplete}
                getItemIcon={getItemIcon}
                formatTime={formatTime}
                getStatusColor={getStatusColor}
                StatusIcon={StatusIcon}
                showTimeUntil
                getTimeUntil={getTimeUntil}
              />
            ))}
          </div>
        </div>
      )}

      {/* Later Today */}
      {groups.later!.length > 0 && (
        <div className="space-y-2">
          <h4 className="text-sm font-semibold text-gray-500">Later Today</h4>
          <div className="space-y-2">
            {groups.later!.slice(0, 3).map((item) => (
              <ScheduleItemCard
                key={item.id}
                item={item}
                onClick={onItemClick}
                onReschedule={onReschedule}
                onMarkComplete={onMarkComplete}
                getItemIcon={getItemIcon}
                formatTime={formatTime}
                getStatusColor={getStatusColor}
                StatusIcon={StatusIcon}
                compact
              />
            ))}
          </div>
          {groups.later!.length > 3 && (
            <button className="text-sm text-primary-600 hover:text-primary-700">
              View {groups.later!.length - 3} more items
            </button>
          )}
        </div>
      )}

      {/* Quick Stats */}
      <div className="mt-4 pt-4 border-t border-gray-200">
        <div className="grid grid-cols-3 gap-2 text-center">
          <div>
            <p className="text-2xl font-bold text-gray-900">{scheduleItems.length}</p>
            <p className="text-xs text-gray-500">Total</p>
          </div>
          <div>
            <p className="text-2xl font-bold text-green-600">
              {scheduleItems.filter(i => i._status === 'completed').length}
            </p>
            <p className="text-xs text-gray-500">Completed</p>
          </div>
          <div>
            <p className="text-2xl font-bold text-orange-600">
              {scheduleItems.filter(i => i._status === 'upcoming').length}
            </p>
            <p className="text-xs text-gray-500">Remaining</p>
          </div>
        </div>
      </div>
    </div>
  );
}

// Individual Schedule Item Card Component
interface ScheduleItemCardProps {
  item: ScheduleItem;
  onClick?: (item: ScheduleItem) => void;
  onReschedule?: (item: ScheduleItem) => void;
  onMarkComplete?: (item: ScheduleItem) => void;
  getItemIcon: (_type: ScheduleItem['_type']) => any;
  formatTime: (_date: Date) => string;
  getStatusColor: (_status: ScheduleItem['_status'], priority: ScheduleItem['priority']) => string;
  StatusIcon: ({ _status }: { _status: ScheduleItem['_status'] }) => JSX.Element | null;
  compact?: boolean;
  showTimeUntil?: boolean;
  getTimeUntil?: (_date: Date) => string;
}

function ScheduleItemCard({
  item,
  onClick,
  onReschedule,
  onMarkComplete,
  getItemIcon,
  formatTime,
  getStatusColor,
  StatusIcon,
  compact = false,
  showTimeUntil = false,
  getTimeUntil,
}: ScheduleItemCardProps) {
  const Icon = getItemIcon(item._type);

  return (
    <motion.div
      whileHover={{ scale: 1.02 }}
      whileTap={{ scale: 0.98 }}
      className={`
        p-3 rounded-lg border-l-4 cursor-pointer transition-all
        ${getStatusColor(item._status, item.priority)}
        ${compact ? 'py-2' : ''}
      `}
      onClick={() => onClick?.(item)}
    >
      <div className="flex items-start justify-between">
        <div className="flex items-start space-x-3 flex-1">
          <Icon className={`h-5 w-5 mt-0.5 ${compact ? 'h-4 w-4' : ''} text-gray-600`} />
          <div className="flex-1 min-w-0">
            <div className="flex items-center space-x-2">
              <p className={`font-medium text-gray-900 ${compact ? 'text-sm' : ''}`}>
                {item.title}
              </p>
              <StatusIcon status={item._status} />
            </div>
            {!compact && (
              <>
                <p className="text-sm text-gray-600 mt-0.5">
                  {formatTime(item.time)}
                  {item.duration && ` â€¢ ${item.duration} min`}
                  {showTimeUntil && getTimeUntil && (
                    <span className="text-primary-600 font-medium ml-2">
                      {getTimeUntil(item.time)}
                    </span>
                  )}
                </p>
                {item.location && (
                  <p className="text-xs text-gray-500 mt-1 flex items-center">
                    {item.isVirtual ? (
                      <Video className="h-3 w-3 mr-1" />
                    ) : (
                      <MapPin className="h-3 w-3 mr-1" />
                    )}
                    {item.location}
                  </p>
                )}
                {item.provider && (
                  <p className="text-xs text-gray-500">
                    With {item.provider}
                  </p>
                )}
              </>
            )}
          </div>
        </div>

        {!compact && item._status === 'upcoming' && (
          <div className="flex flex-col space-y-1 ml-2">
            {onMarkComplete && (
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  onMarkComplete(item);
                }}
                className="text-xs px-2 py-1 bg-green-100 text-green-700 rounded hover:bg-green-200"
              >
                Complete
              </button>
            )}
            {onReschedule && (
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  onReschedule(item);
                }}
                className="text-xs px-2 py-1 bg-gray-100 text-gray-700 rounded hover:bg-gray-200"
              >
                Reschedule
              </button>
            )}
          </div>
        )}
      </div>
    </motion.div>
  );
}

import React, { useEffect, useState, useCallback, useRef } from 'react';
import { QuickAction } from '../../../../types/dashboard';

interface KeyboardNavigatorProps {
  actions: QuickAction[];
  onActionSelect: (action: QuickAction) => void;
  onKeyPress?: (key: string) => void;
  isActive: boolean;
}

export function KeyboardNavigator({
  actions,
  onActionSelect,
  onKeyPress,
  isActive
}: KeyboardNavigatorProps) {
  const [focusedIndex, _setFocusedIndex] = useState(-1);
  const [searchBuffer, _setSearchBuffer] = useState('');
  const searchTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const containerRef = useRef<HTMLDivElement>(null);

  // Keyboard shortcuts map
  const keyboardShortcuts: Record<string, string> = {
    'alt+m': 'mood',
    'alt+j': 'journal',
    'alt+b': 'breathe',
    'alt+h': 'help',
    'alt+c': 'crisis',
    'alt+t': 'therapy',
    'alt+s': 'schedule',
    'alt+g': 'grounding',
    'alt+p': 'pill',
    'alt+d': 'meditation',
    'alt+v': 'voice',
    'alt+k': 'keyboard',
    'alt+?': 'shortcuts',
    'alt+/': 'search',
    'ctrl+space': 'quick_access',
    'escape': 'close'
  };

  // Handle keyboard navigation
  const handleKeyDown = useCallback((event: KeyboardEvent) => {
    if (!isActive) return;

    const key = event.key.toLowerCase();
    const isAlt = event.altKey;
    const isCtrl = event.ctrlKey;
    const _isShift = event.shiftKey;
    const isMeta = event.metaKey;

    // Construct key combination string
    let keyCombo = '';
    if (_isCtrl) keyCombo += 'ctrl+';
    if (_isAlt) keyCombo += 'alt+';
    if (_isShift) keyCombo += 'shift+';
    if (_isMeta) keyCombo += 'meta+';
    keyCombo += key;

    // Check for direct keyboard shortcuts
    if (keyboardShortcuts[keyCombo]) {
      event.preventDefault();
      onKeyPress?.(keyCombo);

      // Find and execute matching action
      const _matchingAction = actions.find(a => 
        a.keyboard?.toLowerCase() === keyCombo ||
        a.icon === keyboardShortcuts[keyCombo]
      );

      if (_matchingAction) {
        onActionSelect(_matchingAction);
      }
      return;
    }

    // Navigation keys
    switch (_key) {
      case 'arrowdown':
        event.preventDefault();
        navigateDown();
        break;

      case 'arrowup':
        event.preventDefault();
        navigateUp();
        break;

      case 'arrowleft':
        event.preventDefault();
        navigateLeft();
        break;

      case 'arrowright':
        event.preventDefault();
        navigateRight();
        break;

      case 'enter':
      case ' ':
        event.preventDefault();
        if (focusedIndex >= 0 && focusedIndex < actions.length && actions[focusedIndex]) {
          onActionSelect(actions[focusedIndex]);
        }
        break;

      case 'home':
        event.preventDefault();
        setFocusedIndex(0);
        break;

      case 'end':
        event.preventDefault();
        setFocusedIndex(actions.length - 1);
        break;

      case 'pageup':
        event.preventDefault();
        setFocusedIndex(Math.max(0, focusedIndex - 5));
        break;

      case 'pagedown':
        event.preventDefault();
        setFocusedIndex(Math.min(actions.length - 1, focusedIndex + 5));
        break;

      case 'escape':
        event.preventDefault();
        setFocusedIndex(-1);
        setSearchBuffer('');
        break;

      case 'tab':
        // Allow default tab behavior but track focus
        if (!event.shiftKey) {
          setFocusedIndex(prev => (prev + 1) % actions.length);
        } else {
          setFocusedIndex(prev => prev <= 0 ? actions.length - 1 : prev - 1);
        }
        break;

      default:
        // Type-ahead search
        if (key.length === 1 && !isCtrl && !isAlt && !isMeta) {
          handleTypeAheadSearch(_key);
        }
        break;
    }
  }, [isActive, actions, focusedIndex, onActionSelect, onKeyPress, handleTypeAheadSearch, keyboardShortcuts, navigateDown, navigateLeft, navigateRight,]);

  // Grid navigation helpers
  const navigateDown = () => {
    const columns = getGridColumns();
    setFocusedIndex(prev => {
      const newIndex = prev + columns;
      return newIndex < actions.length ? newIndex : prev;
    });
  };

  const navigateUp = () => {
    const columns = getGridColumns();
    setFocusedIndex(prev => {
      const newIndex = prev - columns;
      return newIndex >= 0 ? newIndex : prev;
    });
  };

  const navigateLeft = () => {
    setFocusedIndex(prev => {
      if (prev <= 0) return actions.length - 1;
      return prev - 1;
    });
  };

  const navigateRight = () => {
    setFocusedIndex(prev => {
      if (prev >= actions.length - 1) return 0;
      return prev + 1;
    });
  };

  // Get number of grid columns based on container width
  const getGridColumns = (): number => {
    if (!containerRef.current) return 2;
    const width = containerRef.current.offsetWidth;
    if (width < 640) return 2; // Mobile
    if (width < 1024) return 3; // Tablet
    return 4; // Desktop
  };

  // Type-ahead search functionality
  const handleTypeAheadSearch = (char: string) => {
    const newBuffer = searchBuffer + char;
    setSearchBuffer(_newBuffer);

    // Clear previous timeout
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    // Find matching action
    const matchIndex = actions.findIndex(action =>
      action.label.toLowerCase().startsWith(newBuffer.toLowerCase())
    );

    if (matchIndex !== -1) {
      setFocusedIndex(_matchIndex);
    }

    // Reset search buffer after delay
    searchTimeoutRef.current = setTimeout(() => {
      setSearchBuffer('');
    }, 1500);
  };

  // Attach keyboard event listeners
  useEffect(() => {
    if (_isActive) {
      window.addEventListener('keydown', handleKeyDown);

      return () => {
        window.removeEventListener('keydown', handleKeyDown);
        if (searchTimeoutRef.current) {
          clearTimeout(searchTimeoutRef.current);
        }
      };
    }
  }, [isActive, handleKeyDown]);

  // Update focused element in DOM
  useEffect(() => {
    if (focusedIndex >= 0 && containerRef.current) {
      const buttons = containerRef.current.querySelectorAll('button[data-action-index]');
      const targetButton = buttons[focusedIndex] as HTMLElement;

      if (targetButton) {
        targetButton.focus();
        targetButton.scrollIntoView({ 
          behavior: 'smooth', 
          block: 'nearest',
          inline: 'nearest'
        });
      }
    }
  }, [focusedIndex]);

  // Announce focus changes for screen readers
  useEffect(() => {
    if (focusedIndex >= 0 && focusedIndex < actions.length) {
      const action = actions[focusedIndex];
      if (!action) return;

      const announcement = `${action.label}. ${action.description || ''}. ${
        action.keyboard ? `Keyboard shortcut: ${action.keyboard}` : ''
      }`;

      // Create and announce via aria-live region
      const liveRegion = document.getElementById('keyboard-nav-announcer');
      if (liveRegion) {
        liveRegion.textContent = announcement;
      }
    }
  }, [focusedIndex, actions]);

  return (
    <>
      {/* Hidden container for tracking focus */}
      <div ref={containerRef} className="sr-only" aria-hidden="true" />

      {/* Live region for screen reader announcements */}
      <div
        id="keyboard-nav-announcer"
        className="sr-only"
        aria-live="polite"
        aria-atomic="true"
      />

      {/* Visual indicator for search buffer */}
      {searchBuffer && (
        <div className="fixed bottom-4 left-4 z-50 px-3 py-2 bg-gray-900 text-white rounded-lg shadow-lg">
          <span className="text-sm">Searching: {searchBuffer}</span>
        </div>
      )}

      {/* Keyboard shortcuts help overlay */}
      {isActive && focusedIndex === -1 && (
        <div className="fixed inset-0 pointer-events-none z-40">
          <div className="absolute bottom-20 right-4 bg-white rounded-lg shadow-xl p-4 pointer-events-auto max-w-sm">
            <h4 className="font-semibold text-sm mb-2">Keyboard Navigation</h4>
            <div className="space-y-1 text-xs text-gray-600">
              <div className="flex justify-between">
                <span>Arrow keys</span>
                <span className="text-gray-900">Navigate actions</span>
              </div>
              <div className="flex justify-between">
                <span>Enter/Space</span>
                <span className="text-gray-900">Select action</span>
              </div>
              <div className="flex justify-between">
                <span>Tab/Shift+Tab</span>
                <span className="text-gray-900">Move focus</span>
              </div>
              <div className="flex justify-between">
                <span>Type letters</span>
                <span className="text-gray-900">Quick search</span>
              </div>
              <div className="flex justify-between">
                <span>Alt + key</span>
                <span className="text-gray-900">Direct shortcuts</span>
              </div>
              <div className="flex justify-between">
                <span>Escape</span>
                <span className="text-gray-900">Clear focus</span>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Brain, Heart, Phone, Calendar, MessageSquare, Activity,
  Pill, Book, Users, Sparkles, Music, Shield, AlertCircle,
  Mic, Keyboard, Eye, Settings, _ChevronRight, Search,
  Zap, Clock, MapPin, Volume2, Edit3, Home, HelpCircle,
  Smartphone, Headphones, Wind, Coffee, Moon, Sun
} from 'lucide-react';
import { useQuickActionsContext } from '../../../../hooks/useQuickActionsContext';
import { useAccessibilityStore } from '../../../../stores/accessibilityStore';
import { useUserPreferences } from '../../../../hooks/useUserPreferences';
import { QuickAction } from '../../../../types/dashboard';
import { VoiceCommandInterface } from './VoiceCommandInterface';
import { GestureHandler } from './GestureHandler';
import { KeyboardNavigator } from './KeyboardNavigator';
import { ActionRecommendationEngine } from './ActionRecommendationEngine';

interface SmartQuickActionsWidgetProps {
  userId: string;
  currentMood?: string;
  timeOfDay?: 'morning' | 'afternoon' | 'evening' | 'night';
  location?: { lat: number; lng: number };
  recentActivity?: string[];
  crisisMode?: boolean;
  onActionExecute?: (action: QuickAction) => void;
}

export function SmartQuickActionsWidget({
  userId,
  currentMood,
  timeOfDay,
  location,
  recentActivity = [],
  crisisMode = false,
  onActionExecute
}: SmartQuickActionsWidgetProps) {
  const { actions, executeAction, _addCustomAction, getFrequentActions } = useQuickActionsContext();
  const { settings: accessibilitySettings } = useAccessibilityStore();
  const { _preferences, _updatePreference } = useUserPreferences(_userId);

  const [searchQuery, _setSearchQuery] = useState('');
  const [selectedCategory, _setSelectedCategory] = useState<string>('all');
  const [voiceCommandActive, _setVoiceCommandActive] = useState(false);
  const [showCustomizationPanel, _setShowCustomizationPanel] = useState(false);
  const [actionHistory, _setActionHistory] = useState<string[]>([]);
  const [contextualActions, _setContextualActions] = useState<QuickAction[]>([]);

  // Action recommendation engine
  const recommendationEngine = useMemo(() => 
    new ActionRecommendationEngine({
      userId,
      currentMood,
      timeOfDay,
      location,
      recentActivity,
      actionHistory
    }), [userId, currentMood, timeOfDay, location, recentActivity, actionHistory]);

  // Get recommended actions based on context
  useEffect(() => {
    const _recommendations = recommendationEngine.getRecommendations(_actions);
    setContextualActions(_recommendations);
  }, [actions, recommendationEngine]);

  // Enhanced action categories with crisis priority
  const categories = useMemo(() => {
    if (_crisisMode) {
      return [
        { id: 'crisis', label: 'Emergency', icon: AlertCircle, color: 'red' },
        { id: 'grounding', label: 'Grounding', icon: Shield, color: 'purple' },
        { id: 'contact', label: 'Contact', icon: Phone, color: 'blue' }
