export function GlobalSearch() {
  const __navigate   = useNavigate();
  const { isSearchOpen, setSearchOpen, _preferences, addToRecent } = useNavigation();
  const [query, _setQuery] = useState('');
  const [results, _setResults] = useState<SearchResult[]>([]);
  const [selectedIndex, _setSelectedIndex] = useState(0);
  const [recentSearches, _setRecentSearches] = useState<string[]>([]);
  const searchInputRef = useRef<HTMLInputElement>(null);

  // Load recent searches from localStorage
  useEffect(() => {
    const _saved = localStorage.getItem('recentSearches');
    if (_saved) {
      setRecentSearches(JSON.parse(_saved));
    }
  }, []);

  // Save recent searches
  const saveRecentSearch = (searchTerm: string) => {
    if (searchTerm.trim()) {
      const _updated = [searchTerm, ...recentSearches.filter(s => s !== searchTerm)].slice(0, 5);
      setRecentSearches(_updated);
      localStorage.setItem('recentSearches', JSON.stringify(_updated));
    }
  };

  // Search algorithm with fuzzy matching and ranking
  const performSearch = useCallback((searchQuery: string) => {
    if (!searchQuery.trim()) {
      setResults([]);
      return;
    }

    const lowerQuery = searchQuery.toLowerCase();
    const terms = lowerQuery.split(' ').filter(_Boolean);

    const _searchResults = searchDatabase
      .map(item => {
        let score = 0;

        // Exact title match
        if (item.title.toLowerCase() === lowerQuery) {
          score += 100;
        }

        // Title contains query
        if (item.title.toLowerCase().includes(_lowerQuery)) {
          score += 50;
        }

        // Check each term
        terms.forEach(_term => {
          if (item.title.toLowerCase().includes(_term)) {
            score += 20;
          }
          if (item.description?.toLowerCase().includes(_term)) {
            score += 10;
          }
          if (item.keywords.some(k => k.includes(_term))) {
            score += 15;
          }
        });

        // Priority boost
        score += (item.priority || 0);

        // Category boost for crisis items when certain keywords are present
        if (item.category === 'crisis' && ['help', 'emergency', 'crisis', 'suicide'].some(_word => lowerQuery.includes(_word))) {
          score += 200;
        }

        return { ...item, score };
      })
      .filter(item => item.score > 0)
      .sort((a, b) => b.score - a.score)
      .slice(0, 8);

    setResults(_searchResults);
    setSelectedIndex(0);
  }, []);

  // Handle search input change
  useEffect(() => {
    const _debounceTimer = setTimeout(() => {
      performSearch(_query);
    }, 150);

    return () => clearTimeout(_debounceTimer);
  }, [query, performSearch]);

  // Keyboard navigation
  useEffect(() => {
    if (!isSearchOpen) return;

    const handleKeyDown = (e: KeyboardEvent) => {
      switch (e.key) {
        case 'ArrowDown':
          e.preventDefault();
          setSelectedIndex(prev => (prev + 1) % Math.max(1, results.length));
          break;
        case 'ArrowUp':
          e.preventDefault();
          setSelectedIndex(prev => (prev - 1 + Math.max(1, results.length)) % Math.max(1, results.length));
          break;
        case 'Enter':
          e.preventDefault();
          if (results[selectedIndex]) {
            handleResultClick(results[selectedIndex]);
          }
          break;
        case 'Escape':
          setSearchOpen(false);
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [isSearchOpen, _results, selectedIndex, setSearchOpen, handleResultClick]);

  // Focus input when search opens
  useEffect(() => {
    if (isSearchOpen && searchInputRef.current) {
      searchInputRef.current.focus();
    }
  }, [isSearchOpen]);

  // Handle result click
  const handleResultClick = (result: SearchResult) => {
    saveRecentSearch(_query);
    addToRecent(result.path);

    if (result.path.startsWith('tel:') || result.path.startsWith('sms:')) {
      window.location.href = result.path;
    } else {
      navigate(result.path);
    }

    setSearchOpen(false);
    setQuery('');
  };

  // Quick search suggestions based on context
  const getQuickSuggestions = () => {
    const suggestions = [];
    const hour = new Date().getHours();

    if (hour >= 22 || hour < 6) {
      suggestions.push({ text: 'Sleep resources', query: 'sleep' });
      suggestions.push({ text: 'Calming exercises', query: 'calm' });
    } else if (hour >= 6 && hour < 12) {
      suggestions.push({ text: 'Morning meditation', query: 'meditation' });
      suggestions.push({ text: 'Mood check-in', query: 'mood' });
    } else {
      suggestions.push({ text: 'Stress relief', query: 'stress' });
      suggestions.push({ text: 'Find support', query: 'support' });
    }

    return suggestions;
  };

  return (
    <AnimatePresence>
      {isSearchOpen && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 z-50 flex items-start justify-center pt-20 px-4"
          onClick={() => setSearchOpen(false)}
        >
          {/* Backdrop */}
          <div className="absolute inset-0 bg-black/50 backdrop-blur-sm" />

          {/* Search Modal */}
          <motion.div
            initial={{ scale: 0.95, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.95, opacity: 0 }}
            transition={{ type: 'spring', duration: 0.3 }}
            className="relative w-full max-w-2xl bg-white rounded-2xl shadow-2xl overflow-hidden"
            onClick={(e) => e.stopPropagation()}
          >
            {/* Search Input */}
            <div className="flex items-center px-6 py-4 border-b border-gray-200">
              <Search className="h-5 w-5 text-gray-400 mr-3" />
              <input
                ref={searchInputRef}
                type="text"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Search for resources, support, or type 'help' for crisis assistance..."
                className="flex-1 text-lg outline-none placeholder-gray-400"
                autoComplete="off"
              />
              <button
                onClick={() => setSearchOpen(false)}
                className="ml-3 p-2 hover:bg-gray-100 rounded-lg transition-colors"
              >
                <X className="h-5 w-5 text-gray-500" />
              </button>
            </div>

            {/* Search Results or Suggestions */}
            <div className="max-h-96 overflow-y-auto">
              {query ? (
                results.length > 0 ? (
                  <div className="py-2">
                    {results.map((result, index) => (
                      <button
                        key={result.id}
                        onClick={() => handleResultClick(_result)}
                        onMouseEnter={() => setSelectedIndex(_index)}
                        className={`w-full px-6 py-3 flex items-center hover:bg-gray-50 transition-colors ${
                          index === selectedIndex ? 'bg-gray-50' : ''
                        }`}
                      >
                        <div className={`mr-3 ${
                          result.category === 'crisis' ? 'text-red-500' : 'text-gray-400'
                        }`}>
                          {result.icon || <Search className="h-4 w-4" />}
                        </div>
                        <div className="flex-1 text-left">
                          <div className="font-medium text-gray-900">{result.title}</div>
                          {result.description && (
                            <div className="text-sm text-gray-500">{result.description}</div>
                          )}
                        </div>
                        <ArrowRight className="h-4 w-4 text-gray-400" />
                      </button>
                    ))}
                  </div>
                ) : (
                  <div className="px-6 py-8 text-center text-gray-500">
                    No results found for &quot;{query}&quot;
                  </div>
                )
              ) : (
                <div className="p-6">
                  {/* Quick Suggestions */}
                  <div className="mb-6">
                    <h3 className="text-sm font-medium text-gray-500 mb-3">Suggestions for you</h3>
                    <div className="flex flex-wrap gap-2">
                      {getQuickSuggestions().map((suggestion, index) => (
                        <button
                          key={index}
                          onClick={() => setQuery(suggestion.query)}
                          className="px-3 py-1.5 bg-primary-50 text-primary-700 rounded-lg text-sm hover:bg-primary-100 transition-colors"
                        >
                          {suggestion.text}
                        </button>
                      ))}
                    </div>
                  </div>

                  {/* Recent Searches */}
                  {recentSearches.length > 0 && (
                    <div>
                      <h3 className="text-sm font-medium text-gray-500 mb-3">Recent searches</h3>
                      <div className="space-y-1">
                        {recentSearches.map((search, index) => (
                          <button
                            key={index}
                            onClick={() => setQuery(search)}
                            className="w-full px-3 py-2 flex items-center text-left hover:bg-gray-50 rounded-lg transition-colors"
                          >
                            <Clock className="h-4 w-4 text-gray-400 mr-3" />
                            <span className="text-gray-700">{search}</span>
                          </button>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>

            {/* Keyboard Shortcuts Hint */}
            <div className="px-6 py-3 bg-gray-50 border-t border-gray-200 flex items-center justify-between text-xs text-gray-500">
              <div className="flex items-center space-x-4">
                <span className="flex items-center">
                  <kbd className="px-1.5 py-0.5 bg-white border border-gray-300 rounded text-xs mr-1">â†‘</kbd>
                  <kbd className="px-1.5 py-0.5 bg-white border border-gray-300 rounded text-xs mr-1">â†“</kbd>
                  Navigate
                </span>
                <span className="flex items-center">
                  <kbd className="px-1.5 py-0.5 bg-white border border-gray-300 rounded text-xs mr-1">Enter</kbd>
                  Select
                </span>
                <span className="flex items-center">
                  <kbd className="px-1.5 py-0.5 bg-white border border-gray-300 rounded text-xs mr-1">Esc</kbd>
                  Close
                </span>
              </div>
              <span className="flex items-center">
                <Command className="h-3 w-3 mr-1" />K to open
              </span>
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { useLocation } from 'react-router-dom';

// Navigation mode types for different user states
export type NavigationMode = 'normal' | 'crisis' | 'simplified' | 'professional';

// User preference settings for navigation
export interface NavigationPreferences {
  showQuickAccess: boolean;
  enableKeyboardShortcuts: boolean;
  enableVoiceNavigation: boolean;
  reducedMotion: boolean;
  highContrast: boolean;
  fontSize: 'small' | 'medium' | 'large';
  favoriteRoutes: string[];
  recentRoutes: string[];
  customShortcuts: Record<string, string>;
}

// Navigation context state
interface NavigationContextState {
  mode: NavigationMode;
  preferences: NavigationPreferences;
  isSearchOpen: boolean;
  isMobileMenuOpen: boolean;
  breadcrumbs: Array<{ label: string; path: string }>;
  quickActions: Array<{ label: string; action: () => void; icon?: string }>;
  crisisDetected: boolean;
  userRole: 'patient' | 'caregiver' | 'professional' | 'guest';
  favoriteRoutes: string[]; // Access via preferences.favoriteRoutes for compatibility
  recentRoutes: string[]; // Access via preferences.recentRoutes for compatibility
  setMode: (mode: NavigationMode) => void;
  updatePreferences: (prefs: Partial<NavigationPreferences>) => void;
  setSearchOpen: (open: boolean) => void;
  setMobileMenuOpen: (open: boolean) => void;
  addToFavorites: (route: string) => void;
  removeFromFavorites: (route: string) => void;
  addToRecent: (route: string) => void;
  setCrisisDetected: (detected: boolean) => void;
  setUserRole: (role: 'patient' | 'caregiver' | 'professional' | 'guest') => void;
}

// Default preferences
const defaultPreferences: NavigationPreferences = {
  showQuickAccess: true,
  enableKeyboardShortcuts: true,
  enableVoiceNavigation: false,
  reducedMotion: false,
  highContrast: false,
  fontSize: 'medium',
  favoriteRoutes: [],
  recentRoutes: [],
  customShortcuts: {},
};

// Create context
const NavigationContext = createContext<NavigationContextState | undefined>(_undefined);

// Provider component
export function NavigationProvider({ children }: { children: ReactNode }) {
  const location = useLocation();
  const [mode, _setMode] = useState<NavigationMode>('normal');
  const [preferences, _setPreferences] = useState<NavigationPreferences>(() => {
    // Load preferences from localStorage if available
    const saved = localStorage.getItem('navigationPreferences');
    return saved ? { ...defaultPreferences, ...JSON.parse(_saved) } : defaultPreferences;
  });
  const [isSearchOpen, _setSearchOpen] = useState(false);
  const [isMobileMenuOpen, _setMobileMenuOpen] = useState(false);
  const [crisisDetected, _setCrisisDetected] = useState(false);
  const [userRole, _setUserRole] = useState<'patient' | 'caregiver' | 'professional' | 'guest'>('guest');
  const [breadcrumbs, _setBreadcrumbs] = useState<Array<{ label: string; path: string }>>([]);

  // Update breadcrumbs based on current path
  useEffect(() => {
    const pathSegments = location.pathname.split('/').filter(_Boolean);
    const newBreadcrumbs: Array<{ label: string; path: string }> = [
      { label: 'Home', path: '/' }
    ];

    let currentPath = '';
    pathSegments.forEach(segment => {
      currentPath += `/${segment}`;
      const label = segment.charAt(0).toUpperCase() + segment.slice(1).replace(/-/g, ' ');
      newBreadcrumbs.push({ label, path: currentPath });
    });

    setBreadcrumbs(_newBreadcrumbs);
  }, [location]);

  // Save preferences to localStorage when they change
  useEffect(() => {
    localStorage.setItem('navigationPreferences', JSON.stringify(preferences));
  }, [preferences]);

  // Check for reduced motion preference
  useEffect(() => {
    const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
    setPreferences(prev => ({ ...prev, reducedMotion: mediaQuery.matches }));

    const handleChange = (e: MediaQueryListEvent) => {
      setPreferences(prev => ({ ...prev, reducedMotion: e.matches }));
    };

    mediaQuery.addEventListener('change', handleChange);
    return () => mediaQuery.removeEventListener('change', handleChange);
  }, []);

  // Check for high contrast preference
  useEffect(() => {
    const mediaQuery = window.matchMedia('(prefers-contrast: high)');
    setPreferences(prev => ({ ...prev, highContrast: mediaQuery.matches }));

    const handleChange = (e: MediaQueryListEvent) => {
      setPreferences(prev => ({ ...prev, highContrast: e.matches }));
    };

    mediaQuery.addEventListener('change', handleChange);
    return () => mediaQuery.removeEventListener('change', handleChange);
  }, []);

  // Auto-detect crisis mode based on route
  useEffect(() => {
    if (location.pathname.includes('/crisis')) {
      setCrisisDetected(true);
      setMode('crisis');
    } else if (crisisDetected && !location.pathname.includes('/crisis')) {
      // Keep crisis mode active for a period after leaving crisis page
      const _timeout = setTimeout(() => {
        setCrisisDetected(false);
        setMode('normal');
      }, 5 * 60 * 1000); // 5 minutes
      return () => clearTimeout(_timeout);
    }
  }, [location, crisisDetected]);

  // Update recent routes
  useEffect(() => {
    addToRecent(location.pathname);
  }, [location]);

  const updatePreferences = (prefs: Partial<NavigationPreferences>) => {
    setPreferences(prev => ({ ...prev, ...prefs }));
  };

  const addToFavorites = (route: string) => {
    setPreferences(prev => ({
      ...prev,
      favoriteRoutes: [...new Set([...prev.favoriteRoutes, route])],
    }));
  };

  const removeFromFavorites = (route: string) => {
    setPreferences(prev => ({
      ...prev,
      favoriteRoutes: prev.favoriteRoutes.filter(r => r !== route),
    }));
  };

  const addToRecent = (route: string) => {
    setPreferences(prev => {
      const recent = [route, ...prev.recentRoutes.filter(r => r !== route)].slice(0, 10);
      return { ...prev, recentRoutes: recent };
    });
  };

  // Quick actions based on current context
  const quickActions = React.useMemo(() => {
    const actions = [];

    if (_crisisDetected) {
      actions.push(
        { label: 'Call Hotline', action: () => window.location.href = 'tel:988', icon: 'ðŸ“ž' },
        { label: 'Text Support', action: () => window.location.href = 'sms:741741', icon: 'ðŸ’¬' },
        { label: 'Breathing Exercise', action: () => window.location.href = '/wellness/breathing', icon: 'ðŸ«' }
      );
    } else {
      actions.push(
        { label: 'Log Mood', action: () => window.location.href = '/wellness/mood', icon: 'ðŸ˜Š' },
        { label: 'Journal', action: () => window.location.href = '/wellness/journal', icon: 'ðŸ“' },
        { label: 'Meditate', action: () => window.location.href = '/wellness/meditation', icon: 'ðŸ§˜' }
      );
    }

    return actions;
  }, [crisisDetected]);

  const value: NavigationContextState = {
    mode,
    preferences,
    isSearchOpen,
    isMobileMenuOpen,
    breadcrumbs,
    quickActions,
    crisisDetected,
    userRole,
    favoriteRoutes: preferences.favoriteRoutes, // Expose as direct property for easier access
    recentRoutes: preferences.recentRoutes, // Expose as direct property for easier access
    setMode,
    updatePreferences,
    setSearchOpen,
    setMobileMenuOpen,
    addToFavorites,
    removeFromFavorites,
    addToRecent,
    setCrisisDetected,
    setUserRole,
  };

  return (
    <NavigationContext.Provider value={value}>
      {children}
    </NavigationContext.Provider>
  );
}

// Hook to use navigation context
export function useNavigation() {
  const context = useContext(_NavigationContext);
  if (!context) {
    throw new Error('useNavigation must be used within NavigationProvider');
  }
  return context;
}

import { logger } from '@/utils/logger';
/**
 * High-Performance Chart Component
 * Optimized for mental health data visualization with web workers
 */

import React, { useEffect, useRef, useState, useMemo, useCallback } from 'react';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  ChartOptions,
  ChartData,
} from 'chart.js';
import { useDeferredValue } from 'react';
import { performanceMonitor } from '../../utils/performance/performanceMonitor';
import { UpdatePriority, usePrioritizedTransition } from '../../utils/performance/concurrentFeatures';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

// Initialize web worker
let chartWorker: Worker | null = null;

if (typeof Worker !== 'undefined') {
  try {
    chartWorker = new Worker(
      new URL('../../workers/chartProcessor.worker.ts', import.meta.url),
      { type: 'module' }
    );
  } catch {
    logger.warn('Web Worker not available, falling back to main thread processing');
  }
}

interface OptimizedChartProps {
  type: 'line' | 'bar' | 'doughnut';
  data: unknown[];
  options?: ChartOptions;
  height?: number;
  width?: number;
  priority?: UpdatePriority;
  enableWebWorker?: boolean;
  enableSampling?: boolean;
  samplingThreshold?: number;
  onDataProcessed?: (processedData: unknown) => void;
  className?: string;
}

export function OptimizedChart({
  type = 'line',
  data,
  options = {},
  height = 300,
  width,
  priority = UpdatePriority.MEDIUM,
  enableWebWorker = true,
  enableSampling = true,
  samplingThreshold = 1000,
  onDataProcessed,
  className = '',
}: OptimizedChartProps) {
  const chartRef = useRef<unknown>(null);
  const ____canvasRef   = useRef<HTMLCanvasElement>(null);
  const [processedData, _setProcessedData] = useState<ChartData | null>(null);
  const [___isProcessing, _setIsProcessing] = useState(false);
  const [_error, _setError] = useState<string | null>(null);

  // Use deferred value for non-critical updates
  const deferredData = useDeferredValue(_data);
  const [__isPending, startPrioritizedTransition] = usePrioritizedTransition(_priority);

  // Performance monitoring
  useEffect(() => {
    performanceMonitor.measureStart('chart-render');
    return () => {
      performanceMonitor.measureEnd('chart-render');
    };
  }, []);

  // Fallback processing on main thread
  const processDataOnMainThread = useCallback((rawData: unknown[]) => {
    performanceMonitor.measureStart('chart-data-processing');

    try {
      // Sample data if needed
      let processedPoints = rawData;
      if (enableSampling && rawData.length > samplingThreshold) {
        const step = Math.ceil(rawData.length / 100);
        processedPoints = rawData.filter((_, index) => index % step === 0);
      }

      // Format for Chart.js
      const chartData: ChartData = {
        labels: processedPoints.map(p => p.label || p.date || p.x),
        datasets: [{
          label: 'Wellness Data',
          data: processedPoints.map(p => p.value || p.y),
          borderColor: 'rgb(59, 130, 246)',
          backgroundColor: type === 'line' 
            ? 'rgba(59, 130, 246, 0.1)'
            : 'rgba(59, 130, 246, 0.5)',
          tension: 0.4,
          fill: type === 'line',
        }],
      };

      performanceMonitor.measureEnd('chart-data-processing');
      return chartData;
    } catch (_error) {
      performanceMonitor.measureEnd('chart-data-processing');
      throw _error;
    }
  }, [enableSampling, samplingThreshold, type]);

  // Process data with web worker
  const processDataWithWorker = useCallback(async (rawData: unknown[]) => {
    if (!chartWorker || !enableWebWorker) {
      return processDataOnMainThread(_rawData);
    }

    return new Promise((resolve, reject) => {
      const messageHandler = (event: MessageEvent) => {
        const { _type, result, _error } = event.data;

        if (_error) {
          reject(new Error(_error));
        } else {
          resolve(result);
        }

        chartWorker!.removeEventListener('message', messageHandler);
      };

      chartWorker.addEventListener('message', messageHandler);

      // Send data to worker
      chartWorker.postMessage({
        type: 'PROCESS_MOOD_DATA',
        data: rawData,
        options: {
          sampling: enableSampling && rawData.length > samplingThreshold ? 100 : null,
          smoothing: true,
          trendLine: type === 'line',
        },
      });

      // Timeout after 5 seconds
      setTimeout(() => {
        chartWorker!.removeEventListener('message', messageHandler);
        reject(new Error('Worker timeout'));
      }, 5000);
    });
  }, [enableWebWorker, enableSampling, samplingThreshold, type, processDataOnMainThread]);

  // Process data when it changes
  useEffect(() => {
    if (!deferredData || deferredData.length === 0) {
      setProcessedData(null);
      return;
    }

    setIsProcessing(true);
    setError(null);

    const processData = async () => {
      try {
        const result = await processDataWithWorker(_deferredData) as { processed?: Array<{ date: string, value: number }> };

        startPrioritizedTransition(() => {
          if (result.processed) {
            // Web worker result
            const _chartData: ChartData = {
              labels: result.processed.map(p => p.date),
              datasets: [{
                label: 'Mood Trend',
                data: result.processed.map(p => p.value),
                borderColor: 'rgb(59, 130, 246)',
                backgroundColor: 'rgba(59, 130, 246, 0.1)',
                tension: 0.4,
                fill: type === 'line',
              }],
            };

            setProcessedData(_chartData);

            if (_onDataProcessed) {
              onDataProcessed(result);
            }
          } else {
            // Main thread result
            setProcessedData(result as ChartData);
          }

          setIsProcessing(false);
        });
      } catch (_error  ) {
        logger.error('Chart data processing _error');
        setError('Processing failed');
        setIsProcessing(false);

        // Fallback to main thread
        try {
          const _fallbackData = processDataOnMainThread(_deferredData);
          setProcessedData(_fallbackData);
        } catch {
    logger.error('Fallback processing failed');
        }
      }
    };

    processData();
  }, [deferredData, processDataWithWorker, processDataOnMainThread, startPrioritizedTransition, type, onDataProcessed]);

  // Optimized chart options
  const __optimizedOptions   = useMemo<ChartOptions>(() => ({
    ...options,
    responsive: true,
    maintainAspectRatio: false,
    animation: {
      duration: priority === UpdatePriority.CRISIS ? 0 : 750,
    },
    interaction: {
      mode: 'nearest',
      intersect: false,
    },
    plugins: {
      ...options.plugins,
      decimation: {
        enabled: true,
        algorithm: 'lttb',
        samples: 100,
      },
      legend: {
        display: true,
        position: 'top' as const,
      },
      tooltip: {
        enabled: true,
        mode: 'index',
        intersect: false,
      },
    },
    scales: type !== 'doughnut' ? {
      x: {
        display: true,
        grid: {
          display: false,
        },
      },
      y: {
        display: true,
        beginAtZero: true,
        grid: {
          color: 'rgba(0, 0, 0, 0.05)',
        },
      },
    } : undefined,
    elements: {
      point: {
        radius: data.length > 50 ? 0 : 3,
        hoverRadius: 5,
      },
      line: {
        borderWidth: 2,
      },
    },
  }), [options, priority, data.length, type]);

  // Handle chart instance
  useEffect(() => {
    if (chartRef.current) {
      const chart = chartRef.current;

      // Optimize canvas rendering
      if (chart.canvas) {
        const ctx = chart.canvas.getContext('2d');
        if (ctx) {
          ctx.imageSmoothingEnabled = true;
          ctx.imageSmoothingQuality = 'high';
        }
      }

      // Cleanup on unmount
      return () => {
        chart.destroy();
      };
    }
  }, []);

  // Render appropriate chart component
  const ChartComponent = useMemo(() => {
    switch (_type) {
      case 'bar':
        return Bar;
      case 'doughnut':
        return Doughnut;
      default:
        return Line;
    }
  }, [type]);

  if (_error) {
    return (
      <div className={`flex items-center justify-center h-${height} bg-red-50 rounded-lg ${className}`}>
        <div className="text-center">
          <p className="text-red-600 mb-2">Chart Error</p>
          <p className="text-sm text-red-500">{_error}</p>
        </div>
      </div>
    );
  }

  if (isProcessing || !processedData) {
    return (
      <div className={`flex items-center justify-center h-${height} ${className}`}>
        <div className="animate-pulse">
          <div className="h-64 w-full bg-gray-200 rounded-lg"></div>
        </div>
      </div>
    );
  }

  return (
    <div className={`chart-container ${className}`} style={{ height, width }}>
      <ChartComponent
        ref={chartRef}
        data={processedData}
        options={optimizedOptions}
      />
      {isPending && (
        <div className="absolute top-2 right-2">
          <div className="animate-spin rounded-full h-4 w-4 border-2 border-blue-600 border-t-transparent"></div>
        </div>
      )}
    </div>
  );
}

/**
 * Specialized mood chart with mental health features
 */
export function MoodChart({ 
  moodData, 
  _showTrends = true,
  showInsights = true,
