                  <div className="grid grid-cols-7 gap-2">
                    {availableDays.map((date, index) => {
                      const isSelected = selectedDate?.toDateString() === date.toDateString();
                      const dayName = date.toLocaleDateString('en-US', { weekday: 'short' });
