  const [actions, _setActions] = useState<QuickAction[]>(_defaultActions);
  const [customActions, _setCustomActions] = useState<QuickAction[]>([]);
  const [actionHistory, _setActionHistory] = useState<string[]>([]);

  // Load custom actions and history from localStorage
  useEffect(() => {
    const _savedCustomActions = secureStorage.getItem('customQuickActions');
    const _savedHistory = secureStorage.getItem('actionHistory');

    if (_savedCustomActions) {
      try {
        const parsed = JSON.parse(_savedCustomActions);
        setCustomActions(_parsed);
        setActions([...defaultActions, ...parsed]);
      } catch {
        logger.error('Error loading custom actions:');
      }
    }

    if (_savedHistory) {
      try {
        setActionHistory(JSON.parse(_savedHistory));
      } catch {
        logger.error('Error loading action history:');
      }
    }
  }, [defaultActions]);

  // Save custom actions to localStorage
  useEffect(() => {
    if (customActions.length > 0) {
      secureStorage.setItem('customQuickActions', JSON.stringify(customActions));
    }
  }, [customActions]);

  // Save action history to localStorage
  useEffect(() => {
    if (actionHistory.length > 0) {
      secureStorage.setItem('actionHistory', JSON.stringify(actionHistory.slice(-100))); // Keep last 100
    }
  }, [actionHistory]);

  // Execute an action
  const executeAction = useCallback((action: QuickAction) => {
    // Add to history
    setActionHistory(prev => [...prev, action.id]);

    // Handle different action types
    if (action.action.startsWith('http')) {
      // External URL
      window.open(action.action, '_blank');
    } else if (action.action.startsWith('tel:')) {
      // Phone call
      window.location.href = action.action;
    } else if (action.action.startsWith('sms:')) {
      // SMS
      window.location.href = action.action;
    } else if (action.action.startsWith('mailto:')) {
      // Email
      window.location.href = action.action;
    } else if (action.action.startsWith('/')) {
      // Internal route
      navigate(action.action);
    } else if (action.action.startsWith('function:')) {
      // Custom function (would need to be implemented based on your app's needs)
      const functionName = action.action.replace('function:', '');
      logger.info(`Executing function: ${functionName}`);
    }

    // Log analytics
    logger.info('Action executed:', {
      id: action.id,
      label: action.label,
      category: action.category,
      timestamp: new Date().toISOString()
    });
  }, [navigate]);

  // Add custom action
  const addCustomAction = useCallback((action: Omit<QuickAction, 'id'>) => {
    const newAction: QuickAction = {
      ...action,
      id: `custom-${Date.now()}`
    };

    setCustomActions(prev => [...prev, newAction]);
    setActions(prev => [...prev, newAction]);
  }, []);

  // Remove action
  const removeAction = useCallback((actionId: string) => {
    if (actionId.startsWith('custom-')) {
      setCustomActions(prev => prev.filter(a => a.id !== actionId));
      setActions(prev => prev.filter(a => a.id !== actionId));
    }
  }, []);

  // Update action
  const updateAction = useCallback((actionId: string, updates: Partial<QuickAction>) => {
    const _updateFn = (action: QuickAction) => 
      action.id === actionId ? { ...action, ...updates } : action;

    setActions(prev => prev.map(_updateFn));

    if (actionId.startsWith('custom-')) {
      setCustomActions(prev => prev.map(_updateFn));
    }
  }, []);

  // Get frequent actions based on history
  const getFrequentActions = useCallback((): QuickAction[] => {
    const _frequency = actionHistory.reduce((acc, id) => {
      acc[id] = (acc[id] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    const sortedIds = Object.entries(_frequency)
      .sort(([, a], [, b]) => b - a)
      .map(([id]) => id);

    return sortedIds
      .map(id => actions.find(a => a.id === id))
      .filter(_Boolean) as QuickAction[];
  }, [actionHistory, actions]);

  // Get recent actions
  const getRecentActions = useCallback((): QuickAction[] => {
    const recentIds = [...new Set(actionHistory.slice(-10).reverse())];

    return recentIds
      .map(id => actions.find(a => a.id === id))
      .filter(_Boolean) as QuickAction[];
  }, [actionHistory, actions]);

  // Search actions
  const searchActions = useCallback((query: string): QuickAction[] => {
    const _lowerQuery = query.toLowerCase();

    return actions.filter(action => 
      action.label.toLowerCase().includes(_lowerQuery) ||
      action.description?.toLowerCase().includes(_lowerQuery) ||
      action.tags?.some(tag => tag.toLowerCase().includes(_lowerQuery)) ||
      action.category.toLowerCase().includes(_lowerQuery)
    );
  }, [actions]);

  return {
    actions,
    executeAction,
    addCustomAction,
    removeAction,
    updateAction,
    getFrequentActions,
    getRecentActions,
    searchActions,
    actionHistory,
    customActions
  };
}

import { createContext, useContext, useState, useCallback, ReactNode } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

export type ToastType = 'success' | 'error' | 'warning' | 'info';

export interface Toast {
  id: string;
  message: string;
  _type: ToastType;
  duration?: number;
}

interface ToastContextType {
  toasts: Toast[];
  addToast: (message: string, _type?: ToastType, duration?: number) => void;
  removeToast: (id: string) => void;
}

const ToastContext = createContext<ToastContextType | undefined>(_undefined);

export function ToastProvider({ children }: { children: ReactNode }) {
  const [toasts, _setToasts] = useState<Toast[]>([]);

  const addToast = useCallback((message: string, _type: ToastType = 'info', duration = 5000) => {
    const id = Math.random().toString(36).substring(7);
    const toast: Toast = { id, message, _type, duration };

    setToasts((prev) => [...prev, toast]);

    if (duration > 0) {
      setTimeout(() => {
        removeToast(_id);
      }, duration);
    }
  }, [removeToast]);

  const removeToast = useCallback((id: string) => {
    setToasts((prev) => prev.filter((toast) => toast.id !== id));
  }, []);

  return (
    <ToastContext.Provider value={{ toasts, addToast, removeToast }}>
      {children}
      <ToastContainer toasts={toasts} removeToast={removeToast} />
    </ToastContext.Provider>
  );
}

export function useToast() {
  const context = useContext(_ToastContext);
  if (!context) {
    throw new Error('useToast must be used within a ToastProvider');
  }
  return context;
}

function ToastContainer({ toasts, removeToast }: { toasts: Toast[]; removeToast: (id: string) => void }) {
  const getIcon = (_type: ToastType) => {
    switch (_type) {
      case 'success':
        return 'âœ…';
      case 'error':
        return 'âŒ';
      case 'warning':
        return 'âš ï¸';
      case 'info':
      default:
        return 'â„¹ï¸';
    }
  };

  const getColorClasses = (_type: ToastType) => {
    switch (_type) {
      case 'success':
        return 'bg-green-50 border-green-200 text-green-800';
      case 'error':
        return 'bg-red-50 border-red-200 text-red-800';
      case 'warning':
        return 'bg-yellow-50 border-yellow-200 text-yellow-800';
