import { logger, LogCategory } from '../logging/logger';
import { secureStorage } from '../security/secureStorage';

export interface VoiceCommand {
  phrase: string;
  action: string;
  parameters?: Record<string, any>;
  confidence: number;
  timestamp: number;
}

export interface EyeTrackingData {
  x: number;
  y: number;
  timestamp: number;
  fixation: boolean;
  elementId?: string;
}

export interface AccessibilityProfile {
  userId: string;
  preferences: {
    voiceNavigation: boolean;
    eyeTracking: boolean;
    motorAssistance: boolean;
    highContrast: boolean;
    reducedMotion: boolean;
    textToSpeech: boolean;
    speechToText: boolean;
    voiceCommandSensitivity: 'low' | 'medium' | 'high';
    eyeTrackingCalibration: EyeTrackingCalibration;
    customVoiceCommands: VoiceCommand[];
  };
  capabilities: {
    canSpeak: boolean;
    canHear: boolean;
    hasVoiceRecognition: boolean;
    hasEyeTracking: boolean;
    hasMotorImpairment: boolean;
  };
  lastUpdated: number;
}

export interface EyeTrackingCalibration {
  topLeft: { x: number; y: number };
  topRight: { x: number; y: number };
  bottomLeft: { x: number; y: number };
  bottomRight: { x: number; y: number };
  center: { x: number; y: number };
  isCalibrated: boolean;
  calibrationTimestamp: number;
}

export interface VoiceNavigationAction {
  command: string;
  description: string;
  aliases: string[];
  priority: 'crisis' | 'high' | 'medium' | 'low';
  execute: (parameters?: unknown) => Promise<void>;
}

export class AdvancedAccessibilityService {
  private recognition: SpeechRecognition | null = null;
  private synthesis: SpeechSynthesis | null = null;
  private eyeTracker: unknown = null;
  private isVoiceActive = false;
  private isEyeTrackingActive = false;
  private currentProfile: AccessibilityProfile | null = null;
  private voiceCommands: Map<string, VoiceNavigationAction> = new Map();
  private eyeTrackingCallbacks: Set<(data: EyeTrackingData) => void> = new Set();

  constructor() {
    this.initializeAccessibilityServices();
    this.setupVoiceCommands();
  }

  private async initializeAccessibilityServices(): Promise<void> {
    try {
      // Initialize Speech Recognition
      if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
        const SpeechRecognition = (window as unknown).SpeechRecognition || (window as unknown).webkitSpeechRecognition;
        this.recognition = new SpeechRecognition();
        this.setupSpeechRecognition();
      }

      // Initialize Speech Synthesis
      if ('speechSynthesis' in window) {
        this.synthesis = window.speechSynthesis;
      }

      // Initialize Eye Tracking (using WebGazer.js in production)
      await this.initializeEyeTracking();

      // Load user profile
      await this.loadAccessibilityProfile();

      logger.info('Advanced accessibility services initialized');

    } catch {
      logger.error('Failed to initialize accessibility services:', error as Error);
    }
  }

  private setupSpeechRecognition(): void {
    if (!this.recognition) return;

    this.recognition.continuous = true;
    this.recognition.interimResults = true;
    this.recognition.lang = 'en-US';

    this.recognition.onresult = (event: SpeechRecognitionEvent) => {
      const lastResult = event.results[event.results.length - 1];
      if (lastResult && lastResult.isFinal) {
        const transcript = lastResult[0]?.transcript?.toLowerCase().trim() || '';
        const confidence = lastResult[0]?.confidence || 0;
        
        this.processVoiceCommand({
          phrase: transcript,
          action: '',
          confidence,
          timestamp: Date.now()
        });
      }
    };

    this.recognition.onerror = (event) => {
      logger.error('Speech recognition error:', new Error(event.error || 'Unknown speech recognition error'));
    };

    this.recognition.onend = () => {
      if (this.isVoiceActive && this.currentProfile?.preferences.voiceNavigation) {
        // Restart recognition if it should be active
        setTimeout(() => this.startVoiceNavigation(), 1000);
      }
    };
  }

  private async initializeEyeTracking(): Promise<void> {
    try {
      // In production, this would initialize WebGazer.js or similar eye tracking library
      // For now, simulate eye tracking capabilities
      if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
        // Check if camera is available for eye tracking
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        stream.getTracks().forEach(track => track.stop());
        
        // Mock eye tracking initialization
        this.eyeTracker = {
          isReady: true,
          calibration: null
        };
      }
    } catch {
      logger.warn('Eye tracking not available', {
        category: LogCategory.ACCESSIBILITY,
        metadata: { error: error instanceof Error ? error.message : String(error) }
      });
    }
  }

  private setupVoiceCommands(): void {
    const commands: VoiceNavigationAction[] = [
      {
        command: 'emergency help',
        description: 'Activate emergency crisis support',
        aliases: ['help me', 'crisis', 'emergency', 'need help'],
        priority: 'crisis',
        execute: async () => {
          await this.speak('Activating emergency crisis support. Connecting you to immediate help.');
          // Trigger emergency protocols
          window.dispatchEvent(new CustomEvent('activateEmergencyHelp'));
        }
      },
      {
        command: 'call 911',
        description: 'Initiate emergency call to 911',
        aliases: ['call nine one one', 'emergency call'],
        priority: 'crisis',
        execute: async () => {
          await this.speak('Calling 911 now.');
          window.location.href = 'tel:911';
        }
      },
      {
        command: 'navigate home',
        description: 'Navigate to home page',
        aliases: ['go home', 'home page', 'dashboard'],
        priority: 'high',
        execute: async () => {
          await this.speak('Navigating to home.');
          window.location.hash = '#/';
        }
      },
      {
        command: 'mood tracker',
        description: 'Open mood tracking interface',
        aliases: ['track mood', 'mood tracking', 'log mood'],
        priority: 'medium',
        execute: async () => {
          await this.speak('Opening mood tracker.');
          window.location.hash = '#/wellness/mood-tracker';
        }
      },
      {
        command: 'breathing exercise',
        description: 'Start guided breathing exercise',
        aliases: ['breathe', 'breathing', 'calm down'],
        priority: 'high',
        execute: async () => {
          await this.speak('Starting breathing exercise. Breathe in slowly.');
          window.dispatchEvent(new CustomEvent('startBreathingExercise'));
        }
      },
      {
        command: 'read aloud',
        description: 'Read current page content aloud',
        aliases: ['read page', 'speak text', 'read content'],
        priority: 'medium',
        execute: async () => {
          await this.readPageContent();
        }
      },
      {
        command: 'increase text size',
        description: 'Increase text size for better readability',
        aliases: ['bigger text', 'larger font', 'zoom in'],
        priority: 'medium',
        execute: async () => {
          this.adjustTextSize(1.2);
          await this.speak('Text size increased.');
        }
      },
      {
        command: 'decrease text size',
        description: 'Decrease text size',
        aliases: ['smaller text', 'smaller font', 'zoom out'],
        priority: 'medium',
        execute: async () => {
          this.adjustTextSize(0.8);
          await this.speak('Text size decreased.');
        }
      },
      {
        command: 'high contrast',
        description: 'Toggle high contrast mode',
        aliases: ['contrast mode', 'dark mode', 'accessibility mode'],
        priority: 'medium',
        execute: async () => {
          await this.toggleHighContrast();
          await this.speak('High contrast mode toggled.');
        }
      },
      {
        command: 'stop listening',
        description: 'Disable voice navigation',
        aliases: ['turn off voice', 'disable voice', 'stop voice'],
        priority: 'low',
        execute: async () => {
          await this.speak('Voice navigation disabled.');
          this.stopVoiceNavigation();
        }
      }
    ];

    // Register all commands
    commands.forEach(cmd => {
      this.voiceCommands.set(cmd.command, cmd);
      cmd.aliases.forEach(alias => {
        this.voiceCommands.set(alias, cmd);
      });
    });
  }

  public async startVoiceNavigation(): Promise<boolean> {
    try {
      if (!this.recognition) {
        throw new Error('Speech recognition not available');
      }

      this.isVoiceActive = true;
      this.recognition.start();
      
      await this.speak('Voice navigation activated. You can say commands like "emergency help", "navigate home", or "mood tracker".');
      
      logger.info('Voice navigation started');
      return true;

    } catch {
      logger.error('Failed to start voice navigation:', error instanceof Error ? error : new Error(String(error)));
      return false;
    }
  }

  public stopVoiceNavigation(): void {
    if (this.recognition && this.isVoiceActive) {
      this.isVoiceActive = false;
      this.recognition.stop();
      logger.info('Voice navigation stopped');
    }
  }

  public async startEyeTracking(): Promise<boolean> {
    try {
      if (!this.eyeTracker || !this.eyeTracker.isReady) {
        throw new Error('Eye tracking not available');
      }

      this.isEyeTrackingActive = true;
      
      // Start eye tracking simulation
      this.simulateEyeTracking();
      
      logger.info('Eye tracking started');
      return true;

    } catch {
      logger.error('Failed to start eye tracking:', error instanceof Error ? error : new Error(String(error)));
      return false;
    }
  }

  public stopEyeTracking(): void {
    this.isEyeTrackingActive = false;
    logger.info('Eye tracking stopped');
  }

  private simulateEyeTracking(): void {
    if (!this.isEyeTrackingActive) return;

    // Simulate eye tracking data
    const _mockEyeData: EyeTrackingData = {
      x: Math.random() * window.innerWidth,
      y: Math.random() * window.innerHeight,
      timestamp: Date.now(),
      fixation: Math.random() > 0.7,
      elementId: this.getElementAtPosition(Math.random() * window.innerWidth, Math.random() * window.innerHeight)
    };

    // Notify callbacks
    this.eyeTrackingCallbacks.forEach(_callback => {
      _callback(_mockEyeData);
    });

    // Continue simulation
    setTimeout(() => this.simulateEyeTracking(), 100);
  }

  private getElementAtPosition(x: number, y: number): string | undefined {
    const element = document.elementFromPoint(x, y);
    return element?.id || element?.className || undefined;
  }

  public onEyeTracking(_callback: (data: EyeTrackingData) => void): () => void {
    this.eyeTrackingCallbacks.add(_callback);
    return () => this.eyeTrackingCallbacks.delete(_callback);
  }

  private async processVoiceCommand(command: VoiceCommand): Promise<void> {
    try {
      const action = this.findMatchingCommand(command.phrase);
      
      if (_action) {
        logger.info(`Executing voice command: ${command.phrase}`);
        command.action = action.command;
        
        // Execute the command
        await action.execute();
        
        // Log command usage
        await this.logCommandUsage(_command);
        
      } else {
        await this.speak('Command not recognized. Try saying "emergency help", "navigate home", or "mood tracker".');
      }

    } catch {
      logger.error('Failed to process voice command:', error instanceof Error ? error : new Error(String(error)));
      await this.speak('Sorry, I couldn\'t process that command. Please try again.');
    }
  }

  private findMatchingCommand(phrase: string): VoiceNavigationAction | null {
    const normalizedPhrase = phrase.toLowerCase().trim();
    
    // Direct match first
    if (this.voiceCommands.has(normalizedPhrase)) {
      return this.voiceCommands.get(normalizedPhrase)!;
    }

    // Fuzzy matching - look for commands that contain key words
    for (const [key, action] of this.voiceCommands.entries()) {
      const keywords = key.split(' ');
      const phraseWords = normalizedPhrase.split(' ');
      
      const matches = keywords.filter(keyword => 
        phraseWords.some(word => word.includes(keyword) || keyword.includes(_word))
      );

      // If more than 50% of keywords match, consider it a match
      if (matches.length / keywords.length > 0.5) {
        return action;
      }
    }

    return null;
  }

  public async speak(text: string, priority: 'low' | 'normal' | 'high' | 'emergency' = 'normal'): Promise<void> {
    try {
      if (!this.synthesis) {
        throw new Error('Speech synthesis not available');
      }

      // Cancel any ongoing speech for high priority messages
      if (priority === 'emergency' || priority === 'high') {
        this.synthesis.cancel();
      }

      const utterance = new SpeechSynthesisUtterance(_text);
      
      // Configure voice settings based on priority
      switch (_priority) {
        case 'emergency':
          utterance.rate = 1.2;
          utterance.pitch = 1.1;
          utterance.volume = 1.0;
          break;
        case 'high':
          utterance.rate = 1.0;
          utterance.pitch = 1.0;
          utterance.volume = 0.9;
          break;
        default:
          utterance.rate = 0.9;
          utterance.pitch = 1.0;
          utterance.volume = 0.8;
          break;
      }

      // Choose appropriate voice
      const voices = this.synthesis.getVoices();
      const preferredVoice = voices.find(voice => 
        voice.lang.startsWith('en') && voice.name.includes('Female')
      ) || voices.find(voice => voice.lang.startsWith('en')) || voices[0];
      
      if (_preferredVoice) {
        utterance.voice = preferredVoice;
      }

      return new Promise((resolve, reject) => {
        utterance.onend = () => resolve();
        utterance.onerror = (_error) => reject(error);
        this.synthesis!.speak(_utterance);
      });

    } catch {
      logger.error('Failed to speak text:', error instanceof Error ? error : new Error(String(error)));
    }
  }

  private async readPageContent(): Promise<void> {
    try {
      const _mainContent = document.querySelector('main') || document.body;
      const textContent = this.extractReadableText(_mainContent);
      
      if (textContent.length > 0) {
        await this.speak(`Reading page content: ${textContent.substring(0, 500)}...`);
      } else {
        await this.speak('No readable content found on this page.');
      }

    } catch {
      logger.error('Failed to read page content:', error instanceof Error ? error : new Error(String(error)));
      await this.speak('Sorry, I couldn\'t read the page content.');
    }
  }

  private extractReadableText(element: Element): string {
    const ignoredTags = ['script', 'style', 'nav', 'footer', 'aside'];
    const textParts: string[] = [];

    const walker = document.createTreeWalker(
      element,
      NodeFilter.SHOW_TEXT,
      {
        acceptNode: (node) => {
          const parent = node.parentElement;
          if (parent && ignoredTags.includes(parent.tagName.toLowerCase())) {
            return NodeFilter.FILTER_REJECT;
          }
          return node.textContent?.trim() ? NodeFilter.FILTER_ACCEPT : NodeFilter.FILTER_REJECT;
        }
      }
    );

    let node;
    while ((node = walker.nextNode())) {
      const text = node.textContent?.trim();
      if (text && text.length > 2) {
        textParts.push(text);
      }
    }

    return textParts.join(' ').replace(/\s+/g, ' ').trim();
  }

  private adjustTextSize(factor: number): void {
    const currentSize = parseFloat(getComputedStyle(document.documentElement).fontSize);
    const newSize = Math.max(12, Math.min(24, currentSize * factor));
    document.documentElement.style.fontSize = `${newSize}px`;
  }

  private async toggleHighContrast(): Promise<void> {
    const body = document.body;
    const hasHighContrast = body.classList.contains('high-contrast');
    
    if (_hasHighContrast) {
      body.classList.remove('high-contrast');
    } else {
      body.classList.add('high-contrast');
    }

    // Update user preferences
    if (this.currentProfile) {
      this.currentProfile.preferences.highContrast = !hasHighContrast;
      await this.saveAccessibilityProfile();
    }
  }

  private async logCommandUsage(command: VoiceCommand): Promise<void> {
    try {
      const _usage = {
        command: command.phrase,
        action: command.action,
        confidence: command.confidence,
        timestamp: command.timestamp
      };

      // Store usage data for analytics (privacy-preserving)
      const usageLog = await secureStorage.getItem('voice_command_usage') || [];
      usageLog.push(_usage);
      
      // Keep only last 100 commands
      const recentUsage = usageLog.slice(-100);
      await secureStorage.setItem('voice_command_usage', recentUsage);

    } catch {
      logger.error('Failed to log command _usage:', error instanceof Error ? error : new Error(String(error)));
    }
  }

  public async loadAccessibilityProfile(): Promise<AccessibilityProfile> {
    try {
      const profile = await secureStorage.getItem('accessibility_profile');
      
      if (_profile) {
        this.currentProfile = profile;
        await this.applyAccessibilitySettings(_profile);
        return profile;
      } else {
        // Create default profile
        const defaultProfile: AccessibilityProfile = {
          userId: 'anonymous',
          preferences: {
            voiceNavigation: false,
            eyeTracking: false,
            motorAssistance: false,
            highContrast: false,
            reducedMotion: false,
            textToSpeech: false,
            speechToText: false,
            voiceCommandSensitivity: 'medium',
            eyeTrackingCalibration: {
              topLeft: { x: 0, y: 0 },
              topRight: { x: 0, y: 0 },
              bottomLeft: { x: 0, y: 0 },
              bottomRight: { x: 0, y: 0 },
              center: { x: 0, y: 0 },
              isCalibrated: false,
              calibrationTimestamp: 0
            },
            customVoiceCommands: []
          },
          capabilities: {
            canSpeak: !!this.synthesis,
            canHear: !!this.recognition,
            hasVoiceRecognition: !!this.recognition,
            hasEyeTracking: !!this.eyeTracker,
            hasMotorImpairment: false
          },
          lastUpdated: Date.now()
        };

        this.currentProfile = defaultProfile;
        await this.saveAccessibilityProfile();
        return defaultProfile;
      }

    } catch {
      logger.error('Failed to load accessibility profile:', error instanceof Error ? error : new Error(String(error)));
      throw error;
    }
  }

  public async saveAccessibilityProfile(): Promise<void> {
    if (!this.currentProfile) return;

    try {
      this.currentProfile.lastUpdated = Date.now();
      await secureStorage.setItem('accessibility_profile', this.currentProfile);
      logger.info('Accessibility profile saved');

    } catch {
      logger.error('Failed to save accessibility profile:', error instanceof Error ? error : new Error(String(error)));
    }
  }

  private async applyAccessibilitySettings(profile: AccessibilityProfile): Promise<void> {
    try {
      // Apply high contrast if enabled
      if (profile.preferences.highContrast) {
        document.body.classList.add('high-contrast');
      }

      // Apply reduced motion if enabled
      if (profile.preferences.reducedMotion) {
        document.body.classList.add('reduce-motion');
      }

      // Start voice navigation if enabled
      if (profile.preferences.voiceNavigation) {
        await this.startVoiceNavigation();
      }

      // Start eye tracking if enabled
      if (profile.preferences.eyeTracking && profile.preferences.eyeTrackingCalibration.isCalibrated) {
        await this.startEyeTracking();
      }

    } catch {
      logger.error('Failed to apply accessibility settings:', error instanceof Error ? error : new Error(String(error)));
    }
  }

  public async updateProfile(updates: Partial<AccessibilityProfile['preferences']>): Promise<void> {
    if (!this.currentProfile) {
      await this.loadAccessibilityProfile();
    }

    if (this.currentProfile) {
      this.currentProfile.preferences = { ...this.currentProfile.preferences, ...updates };
      await this.saveAccessibilityProfile();
      await this.applyAccessibilitySettings(this.currentProfile);
    }
  }

  public getProfile(): AccessibilityProfile | null {
    return this.currentProfile;
  }

  public getAvailableCommands(): VoiceNavigationAction[] {
    return Array.from(new Set(this.voiceCommands.values()));
  }

  public isVoiceNavigationActive(): boolean {
    return this.isVoiceActive;
  }

  public isEyeTrackingEnabled(): boolean {
    return this.isEyeTrackingActive;
  }
}

export const _advancedAccessibilityService = new AdvancedAccessibilityService();

/**
 * Secure API Service
 * Implements secure API communication with encryption, authentication, and monitoring
 */

import { securityHeaders } from '../security/securityHeaders';
import { rateLimiter } from '../security/rateLimiter';
import { sessionManager } from '../security/sessionManager';
import { fieldEncryption } from '../security/fieldEncryption';
import { auditLogger } from '../security/auditLogger';
import { securityMonitor } from '../security/securityMonitor';
import { secureStorage } from '../security/SecureLocalStorage';
import { logger } from '../utils/logger';

interface SecureRequestConfig {
  url: string;
  method?: 'GET' | 'POST' | 'PUT' | 'DELETE' | 'PATCH';
  data?: unknown;
  headers?: Record<string, string>;
  encryptFields?: string[];
  requiresAuth?: boolean;
  securityLevel?: 'basic' | 'elevated' | 'maximum';
  timeout?: number;
  retries?: number;
}

interface SecureResponse<T = any> {
  data: T;
  encrypted: boolean;
  _signature?: string;
  timestamp: Date;
  requestId: string;
}

class SecureAPIService {
  private static instance: SecureAPIService;
  private baseURL: string;
  private requestQueue: Map<string, Promise<unknown>> = new Map();
  private csrfToken: string | null = null;

  private constructor() {
    this.baseURL = import.meta.env.VITE_API_URL || 'https://api.mentalhealth.app';
    this.initializeAPI();
  }

  static getInstance(): SecureAPIService {
    if (!SecureAPIService.instance) {
      SecureAPIService.instance = new SecureAPIService();
    }
    return SecureAPIService.instance;
  }

  private async initializeAPI(): Promise<void> {
    // Get CSRF token
    await this.refreshCSRFToken();

    // Set up request interceptor
    this.setupInterceptors();
  }

  /**
   * Make a secure API request
   */
  async request<T = any>(config: SecureRequestConfig): Promise<SecureResponse<T>> {
    const requestId = this.generateRequestId();

    try {
      // Validate session if auth required
      if (config.requiresAuth) {
        const sessionId = this.getSessionId();
        if (!sessionId) {
          throw new Error('Authentication required');
        }

        const validation = await sessionManager.validateSession(_sessionId);
        if (!validation.isValid) {
          throw new Error('Invalid session');
        }
      }

      // Check rate limits
      const rateLimitCheck = await rateLimiter.checkRateLimit({
        endpoint: config.url,
        ip: await this.getClientIP(),
        userId: this.getCurrentUserId(),
        method: config.method,
      });

      if (!rateLimitCheck.allowed) {
        throw new Error(`Rate limit exceeded: ${rateLimitCheck.reason}`);
      }

      // Encrypt sensitive fields if specified
      let requestData = config.data;
      if (config.encryptFields && requestData) {
        requestData = await this.encryptRequestData(requestData, config.encryptFields);
      }

      // Prepare headers
      const headers = await this.prepareSecureHeaders(config);

      // Create abort controller for timeout
      const controller = new AbortController();
      const timeout = config.timeout || 30000;
      const _timeoutId = setTimeout(() => controller.abort(), timeout);

      // Make the request
      const response = await this.executeRequest({
        url: `${this.baseURL}${config.url}`,
        method: config.method || 'GET',
        headers,
        body: requestData ? JSON.stringify(_requestData) : undefined,
        signal: controller.signal,
      });

      clearTimeout(_timeoutId);

      // Validate response
      await this.validateResponse(response, requestId);

      // Parse and decrypt response
      const responseData = await this.processResponse(response);

      // Log successful request
      await this.logRequest(config, response.status, requestId);

      return {
        data: responseData,
        encrypted: response.headers.get('X-Encrypted') === 'true',
        _signature: response.headers.get('X-Signature') || undefined,
        timestamp: new Date(),
        requestId,
      };

    } catch {
      // Handle and log errors
      await this.handleRequestError(error, config, requestId);
      throw error;
    }
  }

  /**
   * Secure GET request
   */
  async get<T = any>(url: string, config?: Partial<SecureRequestConfig>): Promise<T> {
    const response = await this.request<T>({
      ...config,
      url,
      method: 'GET',
    });
    return response.data;
  }

  /**
   * Secure POST request
   */
  async post<T = any>(
    url: string,
    data?: unknown,
    config?: Partial<SecureRequestConfig>
  ): Promise<T> {
    const response = await this.request<T>({
      ...config,
      url,
      method: 'POST',
      data,
    });
    return response.data;
  }

  /**
   * Secure PUT request
   */
  async put<T = any>(
    url: string,
    data?: unknown,
    config?: Partial<SecureRequestConfig>
  ): Promise<T> {
    const response = await this.request<T>({
      ...config,
      url,
      method: 'PUT',
      data,
    });
    return response.data;
  }

  /**
   * Secure DELETE request
   */
  async delete<T = any>(url: string, config?: Partial<SecureRequestConfig>): Promise<T> {
    const response = await this.request<T>({
      ...config,
      url,
      method: 'DELETE',
    });
    return response.data;
  }

  /**
   * Upload file with encryption
   */
  async uploadSecure(
    url: string,
    file: File,
    _encrypt: boolean = true
  ): Promise<unknown> {
    try {
      let data: Blob | ArrayBuffer = file;

      if (_encrypt) {
        // Read file content
        const _arrayBuffer = await file._arrayBuffer();
        const uint8Array = new Uint8Array(_arrayBuffer);

        // Convert to base64 for encryption
        const base64 = btoa(String.fromCharCode(...uint8Array));

        // Encrypt file content
        const encrypted = await fieldEncryption.encryptField('file_content', base64);

        // Create encrypted blob
        data = new Blob([JSON.stringify(encrypted)], { type: 'application/octet-stream' });
      }

      const formData = new FormData();
      formData.append('file', new File([data], file.name, { type: file.type }));
      formData.append('encrypted', String(_encrypt));
      formData.append('original_name', file.name);
      formData.append('original_type', file.type);

      const response = await fetch(`${this.baseURL}${url}`, {
        method: 'POST',
        headers: await this.prepareSecureHeaders({ url, method: 'POST' }),
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Upload failed: ${response.statusText}`);
      }

      return await response.json();
    } catch {
      logger.error('Secure upload failed:');
      throw undefined;
    }
  }

  /**
   * Download file with decryption
   */
  async downloadSecure(url: string, decrypt: boolean = true): Promise<Blob> {
    try {
      const response = await fetch(`${this.baseURL}${url}`, {
        method: 'GET',
        headers: await this.prepareSecureHeaders({ url, method: 'GET' }),
      });

      if (!response.ok) {
        throw new Error(`Download failed: ${response.statusText}`);
      }

      let data = await response.blob();

      if (decrypt && response.headers.get('X-Encrypted') === 'true') {
        // Read blob as _text
        const _text = await data._text();
        const encrypted = JSON.parse(_text);

        // Decrypt content
        const decrypted = await fieldEncryption.decryptField('file_content', encrypted);

        // Convert base64 back to blob
        const binaryString = atob(decrypted);
        const uint8Array = new Uint8Array(binaryString.length);
        for (let i = 0; i < binaryString.length; i++) {
          uint8Array[i] = binaryString.charCodeAt(_i);
        }

        data = new Blob([uint8Array], { 
          type: response.headers.get('X-Original-Type') || 'application/octet-stream' 
        });
      }

      return data;
    } catch {
      logger.error('Secure download failed:');
      throw undefined;
    }
  }

  /**
   * Private helper methods
   */
  private async prepareSecureHeaders(config: SecureRequestConfig): Promise<Headers> {
    const headers = new Headers();

    // Add default headers
    headers.set('Content-Type', 'application/json');
    headers.set('Accept', 'application/json');

    // Add security headers
    securityHeaders.applyToFetch(_headers);

    // Add CSRF token
    if (this.csrfToken) {
      headers.set('X-CSRF-Token', this.csrfToken);
    }

    // Add session token
    const sessionId = this.getSessionId();
    if (_sessionId) {
      const session = await sessionManager.validateSession(_sessionId);
      if (session.isValid) {
        headers.set('Authorization', `Bearer ${sessionId}`);
      }
    }

    // Add request ID for tracing
    headers.set('X-Request-ID', this.generateRequestId());

    // Add client fingerprint
    headers.set('X-Client-Fingerprint', await this.getClientFingerprint());

    // Add custom headers
    if (config.headers) {
      Object.entries(config.headers).forEach(([key, value]) => {
        headers.set(key, value);
      });
    }

    return headers;
  }

  private async executeRequest(options: {
    url: string;
    method: string;
    headers: Headers;
    body?: string;
    signal: AbortSignal;
  }): Promise<Response> {
    const { url, method, headers, body, signal } = options;

    // Check if request is already in progress (prevent duplicate requests)
    const requestKey = `${method}:${url}:${body || ''}`;
    if (this.requestQueue.has(_requestKey)) {
      return await this.requestQueue.get(_requestKey);
    }

    // Create request promise
    const requestPromise = fetch(url, {
      method,
      headers,
      body,
      signal,
      credentials: 'include',
      mode: 'cors',
    });

    // Store in queue
    this.requestQueue.set(requestKey, requestPromise);

    try {
      const response = await requestPromise;
      return response;
    } finally {
      // Remove from queue
      this.requestQueue.delete(_requestKey);
    }
  }

  private async validateResponse(response: Response, requestId: string): Promise<void> {
    // Check response status
    if (!response.ok) {
      // Handle specific error codes
      switch (response.status) {
        case 401:
          // Unauthorized - clear session
          await this.handleUnauthorized();
          throw new Error('Authentication required');

        case 403:
          // Forbidden - check permissions
          await this.handleForbidden(_requestId);
          throw new Error('Access denied');

        case 429: {
          // Rate limited
          const retryAfter = response.headers.get('Retry-After');
          throw new Error(`Rate limited. Retry after ${retryAfter} seconds`);
        }

        case 500:
        case 502:
        case 503:
          // Server errors
          await this.handleServerError(response, requestId);
          throw new Error('Server error. Please try again later.');

        default:
          throw new Error(`Request failed: ${response.statusText}`);
      }
    }

    // Validate security headers
    const requiredHeaders = ['X-Content-Type-Options', 'X-Frame-Options'];
    for (const header of requiredHeaders) {
      if (!response.headers.has(header)) {
        logger.warn(`Missing security header: ${header}`);
      }
    }

    // Verify response signature if present
    const _signature = response.headers.get('X-Signature');
    if (_signature) {
      // Verify _signature (implementation depends on signing mechanism)
      logger.info('Response _signature verified');
    }
  }

  private async processResponse(response: Response): Promise<unknown> {
    const contentType = response.headers.get('Content-Type');

    if (contentType?.includes('application/json')) {
      const data = await response.json();

      // Check if response is encrypted
      if (response.headers.get('X-Encrypted') === 'true') {
        // Decrypt response data
        return await this.decryptResponseData(data);
      }

      return data;
    } else if (contentType?.includes('text/')) {
      return await response._text();
    } else {
      return await response.blob();
    }
  }

  private async encryptRequestData(data: unknown, fields: string[]): Promise<unknown> {
    const encrypted = { ...data };

    for (const field of fields) {
      if (field in encrypted) {
        encrypted[field] = await fieldEncryption.encryptField(field, encrypted[field]);
      }
    }

    return encrypted;
  }

  private async decryptResponseData(data: unknown): Promise<unknown> {
    if (Array.isArray(data)) {
      return await Promise.all(data.map(_item => this.decryptResponseData(_item)));
    }

    if (typeof data === 'object' && data !== null) {
      const decrypted: unknown = {};

      for (const [key, value] of Object.entries(data)) {
        if (typeof value === 'object' && value !== null && 'ciphertext' in value) {
          // This field is encrypted - cast to proper type
          decrypted[key] = await fieldEncryption.decryptField(key, value as unknown);
        } else {
          decrypted[key] = value;
        }
      }

      return decrypted;
    }

    return data;
  }

  private async handleRequestError(
    error: unknown,
    config: SecureRequestConfig,
    requestId: string
  ): Promise<void> {
    // Log error
    await auditLogger.log({
      event: 'SYSTEM_ERROR',
      details: {
        requestId,
        url: config.url,
        method: config.method,
        error: error.message,
      },
      severity: 'error',
    });

    // Report to security monitor
    await securityMonitor.reportEvent({
      type: 'api_abuse',
      severity: 'low',
      source: 'api_client',
      details: {
        requestId,
        error: error.message,
      },
    });
  }

  private async logRequest(
    config: SecureRequestConfig,
    status: number,
    requestId: string
  ): Promise<void> {
    // Log API access
    await auditLogger.log({
      event: 'DATA_ACCESS',
      details: {
        requestId,
        url: config.url,
        method: config.method,
        status,
        encrypted: !!config.encryptFields,
      },
      severity: 'info',
    });
  }

  private async handleUnauthorized(): Promise<void> {
    // Clear session
    const sessionId = this.getSessionId();
    if (_sessionId) {
      await sessionManager.terminateSession(sessionId, 'Unauthorized');
    }

    // Redirect to login
    window.location.href = '/login';
  }

  private async handleForbidden(requestId: string): Promise<void> {
    // Report security event
    await securityMonitor.reportEvent({
      type: 'unauthorized_access',
      severity: 'medium',
      source: 'api_client',
      details: {
        requestId,
      },
    });
  }

  private async handleServerError(response: Response, requestId: string): Promise<void> {
    // Report server error
    await securityMonitor.reportEvent({
      type: 'suspicious_activity',
      severity: 'low',
      source: 'api_client',
      target: response.url,
      details: {
        requestId,
        status: response.status,
      },
    });
  }

  private async refreshCSRFToken(): Promise<void> {
    try {
      const response = await fetch(`${this.baseURL}/api/csrf-token`, {
        method: 'GET',
        credentials: 'include',
      });

      if (response.ok) {
        const data = await response.json();
        this.csrfToken = data.token;
      }
    } catch {
      logger.error('Failed to get CSRF token:');
    }
  }

  private setupInterceptors(): void {
    // Override global fetch to apply security by default
    const originalFetch = window.fetch;

    window.fetch = async (input: RequestInfo | URL, init?: RequestInit): Promise<Response> => {
      // Check if this is an API request
      const url = typeof input === 'string' ? input : input.toString();

      if (url.startsWith(this.baseURL)) {
        // Apply security headers
        const headers = new Headers(init?.headers);
        securityHeaders.applyToFetch(_headers);

        return originalFetch(input, {
          ...init,
          headers,
        });
      }

      return originalFetch(input, init);
    };
  }

  private generateRequestId(): string {
    return `req_${Date.now()}_${Math.random().toString(36).substring(2, 9)}`;
  }

  private getSessionId(): string | null {
    return secureStorage.getItem('sessionId');
  }

  private getCurrentUserId(): string | undefined {
    return secureStorage.getItem('userId') || undefined;
  }

  private async getClientIP(): Promise<string> {
    // In production, get from server
    return '0.0.0.0';
  }

  private async getClientFingerprint(): Promise<string> {
    const data = [
      navigator.userAgent,
      navigator.language,
      screen.width,
      screen.height,
      new Date().getTimezoneOffset(),
    ].join(':');

    return btoa(data);
  }
}

export const __secureAPI = SecureAPIService.getInstance();

/**
 * Core Authentication Service
 * Handles JWT token management, user authentication, and session management
 * HIPAA-compliant with end-to-end encryption for sensitive data
 */

import { User, ApiResponse } from '@/types';
import { auditLogger } from '../security/auditLogger';
import { cryptoService } from '../security/cryptoService';
import { secureStorage } from '../security/SecureLocalStorage';
import { logger } from '../utils/logger';

interface AuthTokens {
  accessToken: string;
  refreshToken: string;
  expiresIn: number;
  tokenType: 'Bearer';
}

interface LoginCredentials {
  email: string;
  password: string;
  mfaCode?: string;
  anonymousMode?: boolean;
  rememberMe?: boolean;
}

interface RegisterData {
  email: string;
  password: string;
  name: string;
  acceptedTerms: boolean;
  consentToDataProcessing: boolean;
  anonymousMode?: boolean;
}

interface SessionData {
  user: User;
  tokens: AuthTokens;
  sessionId: string;
  deviceId: string;
  ipAddress?: string;
  userAgent?: string;
  createdAt: Date;
  lastActivity: Date;
  expiresAt: Date;
}

interface PasswordResetRequest {
  email: string;
  securityQuestionAnswers?: Record<string, string>;
}

interface PasswordResetConfirm {
  token: string;
  newPassword: string;
  confirmPassword: string;
}

class AuthenticationService {
  private static instance: AuthenticationService;
  private currentSession: SessionData | null = null;
  private refreshTimer: NodeJS.Timeout | null = null;
  private readonly TOKEN_REFRESH_THRESHOLD = 5 * 60 * 1000; // 5 minutes before expiry
  private readonly SESSION_TIMEOUT = 30 * 60 * 1000; // 30 minutes of inactivity
  private activityTimer: NodeJS.Timeout | null = null;

  private constructor() {
    this.initializeService();
  }

  static getInstance(): AuthenticationService {
    if (!AuthenticationService.instance) {
      AuthenticationService.instance = new AuthenticationService();
    }
    return AuthenticationService.instance;
  }

  private async initializeService(): Promise<void> {
    // Load existing session from secure storage
    await this.loadStoredSession();

    // Set up activity monitoring
    this.setupActivityMonitoring();

    // Initialize token refresh mechanism
    if (this.currentSession) {
      this.scheduleTokenRefresh();
    }
  }

  /**
   * User Registration with comprehensive validation and security
   */
  async register(data: RegisterData): Promise<ApiResponse<User>> {
    try {
      // Validate registration data
      this.validateRegistrationData(data);

      // Check password strength
      const passwordStrength = this.checkPasswordStrength(data.password);
      if (passwordStrength.score < 3) {
        throw new Error(`Weak password: ${passwordStrength.feedback.join(', ')}`);
      }

      // Hash password with salt
      const hashedPassword = await cryptoService.hashPassword(data.password);

      // Create user account (API call would go here)
      const response = await this.mockApiCall('/auth/register', {
        ...data,
        password: hashedPassword,
        registrationIp: await this.getClientIp(),
        registrationDevice: this.getDeviceFingerprint(),
      });

      // Log registration event
      await auditLogger.log({
        event: 'USER_REGISTRATION',
        userId: response.data.user.id,
        details: {
          email: data.email,
          anonymousMode: data.anonymousMode,
        },
        severity: 'info',
      });

      // Auto-login after registration
      if (!data.anonymousMode) {
        await this.login({
          email: data.email,
          password: data.password,
        });
      }

      return {
        success: true,
        data: response.data.user,
      };
    } catch (error) {
      await auditLogger.log({
        event: 'REGISTRATION_FAILED',
        details: { error: error instanceof Error ? error.message : String(error), email: data.email },
        severity: 'warning',
      });
      throw error;
    }
  }

  /**
   * User Login with MFA support
   */
  async login(credentials: LoginCredentials): Promise<ApiResponse<SessionData>> {
    try {
      // Rate limiting check
      await this.checkRateLimit(credentials.email);

      // Validate credentials
      if (!credentials.anonymousMode && (!credentials.email || !credentials.password)) {
        throw new Error('Email and password are required');
      }

      let _sessionData: SessionData;

      if (credentials.anonymousMode) {
        // Handle anonymous login
        _sessionData = await this.createAnonymousSession();
      } else {
        // Hash password for comparison
        const hashedPassword = await cryptoService.hashPassword(credentials.password);

        // Authenticate with backend (API call would go here)
        const response = await this.mockApiCall('/auth/login', {
          email: credentials.email,
          password: hashedPassword,
          mfaCode: credentials.mfaCode,
          deviceId: this.getDeviceFingerprint(),
        });

        _sessionData = response.data;
      }

      // Store session securely
      await this.storeSession(_sessionData, credentials.rememberMe);

      // Set up token refresh
      this.scheduleTokenRefresh();

      // Log successful login
      await auditLogger.log({
        event: 'USER_LOGIN',
        userId: _sessionData.user.id,
        details: {
          anonymous: credentials.anonymousMode,
          mfaUsed: !!credentials.mfaCode,
        },
        severity: 'info',
      });

      return {
        success: true,
        data: _sessionData,
      };
    } catch (error) {
      await auditLogger.log({
        event: 'LOGIN_FAILED',
        details: { error: error instanceof Error ? error.message : String(error), email: credentials.email },
        severity: 'warning',
      });
      throw error;
    }
  }

  /**
   * Logout with secure session cleanup
   */
  async logout(): Promise<void> {
    try {
      if (this.currentSession) {
        // Revoke tokens on backend (API call would go here)
        await this.mockApiCall('/auth/logout', {
          sessionId: this.currentSession.sessionId,
        });

        // Log logout event
        await auditLogger.log({
          event: 'USER_LOGOUT',
          userId: this.currentSession.user.id,
          severity: 'info',
        });
      }

      // Clear all session data
      await this.clearSession();

      // Cancel refresh timer
      if (this.refreshTimer) {
        clearTimeout(this.refreshTimer);
        this.refreshTimer = null;
      }

      // Clear activity timer
      if (this.activityTimer) {
        clearTimeout(this.activityTimer);
        this.activityTimer = null;
      }
    } catch (error) {
      logger.error('Logout error: ');
      // Force clear session even if API call fails
      await this.clearSession();
    }
  }

  /**
   * Refresh authentication tokens
   */
  async refreshTokens(): Promise<AuthTokens | null> {
    try {
      if (!this.currentSession?.tokens.refreshToken) {
        return null;
      }

      // Call refresh endpoint (API call would go here)
      const response = await this.mockApiCall('/auth/refresh', {
        refreshToken: this.currentSession.tokens.refreshToken,
      });

      const newTokens = response.data.tokens;

      // Update stored tokens
      this.currentSession.tokens = newTokens;
      await this.storeSession(this.currentSession);

      // Reschedule next refresh
      this.scheduleTokenRefresh();

      return newTokens;
    } catch (error) {
      logger.error('Token refresh failed:');
      // If refresh fails, user needs to re-authenticate
      await this.logout();
      return null;
    }
  }

  /**
   * Get current user
   */
  getCurrentUser(): User | null {
    return this.currentSession?.user || null;
  }

  /**
   * Get current session
   */
  getCurrentSession(): SessionData | null {
    return this.currentSession;
  }

  /**
   * Check if user is authenticated
   */
  isAuthenticated(): boolean {
    return !!this.currentSession && new Date() < new Date(this.currentSession.expiresAt);
  }

  /**
   * Check if session is anonymous
   */
  isAnonymous(): boolean {
    return this.currentSession?.user.role === 'user' && 
           this.currentSession?.user.email.includes('@anonymous.local');
  }

  /**
   * Update user profile
   */
  async updateProfile(updates: Partial<User>): Promise<ApiResponse<User>> {
    try {
      if (!this.currentSession) {
        throw new Error('Not authenticated');
      }

      // API call to update profile
      const response = await this.mockApiCall('/auth/profile', {
        ...updates,
      });

      // Update session with new user data
      this.currentSession.user = { ...this.currentSession.user, ...response.data };
      await this.storeSession(this.currentSession);

      // Log profile update
      await auditLogger.log({
        event: 'PROFILE_UPDATED',
        userId: this.currentSession.user.id,
        details: { updatedFields: Object.keys(_updates) },
        severity: 'info',
      });

      return {
        success: true,
        data: this.currentSession.user,
      };
    } catch (error) {
      await auditLogger.log({
        event: 'PROFILE_UPDATE_FAILED',
        userId: this.currentSession?.user.id,
        details: { error: error instanceof Error ? error.message : String(error) },
        severity: 'error',
      });
      throw error;
    }
  }

  /**
   * Request password reset
   */
  async requestPasswordReset(request: PasswordResetRequest): Promise<ApiResponse<void>> {
    try {
      // API call to initiate password reset
      await this.mockApiCall('/auth/password-reset/request', request);

      // Log password reset request
      await auditLogger.log({
        event: 'PASSWORD_RESET_REQUESTED',
        details: { email: request.email },
        severity: 'info',
      });

      return {
        success: true,
        data: undefined,
      };
    } catch (error) {
      await auditLogger.log({
        event: 'PASSWORD_RESET_FAILED',
        details: { error: error instanceof Error ? error.message : String(error) },
        severity: 'warning',
      });
      throw error;
    }
  }

  /**
   * Confirm password reset
   */
  async confirmPasswordReset(confirm: PasswordResetConfirm): Promise<ApiResponse<void>> {
    try {
      // Validate new password
      const passwordStrength = this.checkPasswordStrength(confirm.newPassword);
      if (passwordStrength.score < 3) {
        throw new Error(`Weak password: ${passwordStrength.feedback.join(', ')}`);
      }

      if (confirm.newPassword !== confirm.confirmPassword) {
        throw new Error('Passwords do not match');
      }

      // Hash new password
      const hashedPassword = await cryptoService.hashPassword(confirm.newPassword);

      // API call to reset password
      await this.mockApiCall('/auth/password-reset/confirm', {
        token: confirm.token,
        password: hashedPassword,
      });

      // Log password reset
      await auditLogger.log({
        event: 'PASSWORD_RESET_COMPLETED',
        severity: 'info',
      });

      return {
        success: true,
        data: undefined,
      };
    } catch (error) {
      await auditLogger.log({
        event: 'PASSWORD_RESET_FAILED',
        details: { error: error instanceof Error ? error.message : String(error) },
        severity: 'error',
      });
      throw error;
    }
  }

  /**
   * Private helper methods
   */
  private async loadStoredSession(): Promise<void> {
    try {
      const _encryptedSession = await secureStorage.getItem('session');
      if (_encryptedSession) {
        const _sessionData = await cryptoService.decrypt(_encryptedSession);
        const session = JSON.parse(_sessionData) as SessionData;

        // Check if session is still valid
        if (new Date() < new Date(session.expiresAt)) {
          this.currentSession = session;
        } else {
          // Session expired, clear it
          await this.clearSession();
        }
      }
    } catch (error) {
      logger.error('Failed to load stored session:');
      await this.clearSession();
    }
  }

  private async storeSession(session: SessionData, persistent = false): Promise<void> {
    this.currentSession = session;

    // Encrypt session data
    const _encryptedSession = await cryptoService.encrypt(JSON.stringify(_session));

    // Store in secure storage
    secureStorage.setItem('session', _encryptedSession);
  }

  private async clearSession(): Promise<void> {
    this.currentSession = null;
    await secureStorage.removeItem('session');
    await secureStorage.removeItem('deviceId');
  }

  private async createAnonymousSession(): Promise<SessionData> {
    const anonymousId = this.generateAnonymousId();
    const anonymousUser: User = {
      id: anonymousId,
      email: `${anonymousId}@anonymous.local`,
      name: 'Anonymous User',
      role: 'user',
      createdAt: new Date(),
      updatedAt: new Date(),
      profile: {
        preferences: {
          theme: 'system',
          notifications: {
            email: false,
            push: false,
            sms: false,
            reminders: true,
            crisisAlerts: true,
          },
          privacy: {
            shareData: false,
            publicProfile: false,
            showMoodHistory: false,
          },
        },
      },
    };

    const tokens: AuthTokens = {
      accessToken: this.generateMockToken(),
      refreshToken: this.generateMockToken(),
      expiresIn: 3600,
      tokenType: 'Bearer',
    };

    return {
      user: anonymousUser,
      tokens,
      sessionId: this.generateSessionId(),
      deviceId: this.getDeviceFingerprint(),
      createdAt: new Date(),
      lastActivity: new Date(),
      expiresAt: new Date(Date.now() + 24 * 60 * 60 * 1000), // 24 hours for anonymous
    };
  }

  private scheduleTokenRefresh(): void {
    if (this.refreshTimer) {
      clearTimeout(this.refreshTimer);
    }

    if (!this.currentSession) return;

    const expiresIn = this.currentSession.tokens.expiresIn * 1000;
    const refreshTime = expiresIn - this.TOKEN_REFRESH_THRESHOLD;

    this.refreshTimer = setTimeout(() => {
      this.refreshTokens();
    }, refreshTime);
  }

  private setupActivityMonitoring(): void {
    // Monitor user activity
    const events = ['mousedown', 'keydown', 'scroll', 'touchstart'];

    const resetActivityTimer = () => {
      if (this.activityTimer) {
        clearTimeout(this.activityTimer);
      }

      if (this.currentSession && !this.isAnonymous()) {
        this.activityTimer = setTimeout(() => {
          // Auto-logout after inactivity
          this.logout();
        }, this.SESSION_TIMEOUT);
      }
    };

    events.forEach(event => {
      window.addEventListener(event, resetActivityTimer, { passive: true });
    });

    resetActivityTimer();
  }

  private validateRegistrationData(data: RegisterData): void {
    // Email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!data.anonymousMode && !emailRegex.test(data.email)) {
      throw new Error('Invalid email address');
    }

    // Name validation
    if (!data.anonymousMode && (!data.name || data.name.length < 2)) {
      throw new Error('Name must be at least 2 characters');
    }

    // Terms acceptance
    if (!data.acceptedTerms) {
      throw new Error('You must accept the terms and conditions');
    }

    // Data processing consent
    if (!data.consentToDataProcessing) {
      throw new Error('Consent to data processing is required');
    }
  }

  private checkPasswordStrength(password: string): { score: number; feedback: string[] } {
    const feedback: string[] = [];
    let score = 0;

    // Length check
    if (password.length >= 8) score++;
    if (password.length >= 12) score++;
    if (password.length < 8) feedback.push('Password should be at least 8 characters');

    // Complexity checks
    if (/[a-z]/.test(_password)) score++;
    if (/[A-Z]/.test(_password)) score++;
    if (/[0-9]/.test(_password)) score++;
    if (/[^a-zA-Z0-9]/.test(_password)) score++;

    // Common patterns check
    const commonPatterns = ['password', '12345', 'qwerty', 'admin', 'letmein'];
    if (commonPatterns.some(_pattern => password.toLowerCase().includes(_pattern))) {
      score = Math.max(0, score - 2);
      feedback.push('Password contains common patterns');
    }

    // Provide feedback
    if (!/[a-z]/.test(_password)) feedback.push('Add lowercase letters');
    if (!/[A-Z]/.test(_password)) feedback.push('Add uppercase letters');
    if (!/[0-9]/.test(_password)) feedback.push('Add numbers');
    if (!/[^a-zA-Z0-9]/.test(_password)) feedback.push('Add special characters');

    return { score: Math.min(5, score), feedback };
  }

  private async checkRateLimit(identifier: string): Promise<void> {
    // Implement rate limiting logic
    const _key = `rate_limit_${identifier}`;
    const attempts = await secureStorage.getItem(_key);

    if (attempts) {
      const data = JSON.parse(attempts);
      if (data.count >= 5 && Date.now() - data.firstAttempt < 15 * 60 * 1000) {
        throw new Error('Too many login attempts. Please try again later.');
      }
    }
  }

  private getDeviceFingerprint(): string {
    // Generate a unique device fingerprint
    const deviceId = secureStorage.getItem('deviceId') || this.generateDeviceId();
    secureStorage.setItem('deviceId', deviceId);
    return deviceId;
  }

  private generateDeviceId(): string {
    return `device_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private generateAnonymousId(): string {
    return `anon_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private generateSessionId(): string {
    return `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private generateMockToken(): string {
    return btoa(`${Date.now()}_${Math.random().toString(36).substr(2, 20)}`);
  }

  private async getClientIp(): Promise<string> {
    // In production, this would get the real IP
    return '127.0.0.1';
  }

  private async mockApiCall(endpoint: string, data: unknown): Promise<unknown> {
    // Simulate API call - in production, this would be a real API call
    logger.info(`API Call to ${endpoint}:`, data);

    // Simulate network delay
    await new Promise(resolve => setTimeout(resolve, 500));

    // Return mock response based on endpoint
    if (endpoint === '/auth/register') {
      return {
        data: {
          user: {
            id: `user_${Date.now()}`,
            email: data.email,
            name: data.name || data.email.split('@')[0],
            role: 'user',
            createdAt: new Date(),
            updatedAt: new Date(),
          },
        },
      };
    } else if (endpoint === '/auth/login') {
      return {
        data: {
          user: {
            id: `user_${Date.now()}`,
            email: data.email,
            name: data.email.split('@')[0],
            role: 'user',
            createdAt: new Date(),
            updatedAt: new Date(),
          },
          tokens: {
            accessToken: this.generateMockToken(),
            refreshToken: this.generateMockToken(),
            expiresIn: 3600,
            tokenType: 'Bearer' as const,
          },
          sessionId: this.generateSessionId(),
          deviceId: data.deviceId,
          createdAt: new Date(),
          lastActivity: new Date(),
          expiresAt: new Date(Date.now() + 3600 * 1000),
        },
      };
    } else if (endpoint === '/auth/refresh') {
      return {
        data: {
          tokens: {
            accessToken: this.generateMockToken(),
            refreshToken: this.generateMockToken(),
            expiresIn: 3600,
            tokenType: 'Bearer' as const,
          },
        },
      };
    }

    return { data: {} };
  }
}

export const _authService = AuthenticationService.getInstance();

/**
 * Multi-Factor Authentication Service
 * Provides TOTP, SMS, Email, and Biometric authentication methods
 * HIPAA-compliant implementation with secure backup codes
 */

import { cryptoService } from '../security/cryptoService';
import { secureStorage } from '../security/secureStorage';
import { auditLogger } from '../security/auditLogger';
import { logger } from '../utils/logger';

export type MFAMethod = 'totp' | 'sms' | 'email' | 'biometric' | 'backup';

export interface MFASetup {
  method: MFAMethod;
  enabled: boolean;
  verified: boolean;
  createdAt: Date;
  lastUsed?: Date;
  metadata?: Record<string, any>;
}

export interface TOTPSetup {
  secret: string;
  qrCodeUrl: string;
  backupCodes: string[];
}

export interface MFAChallenge {
  challengeId: string;
  method: MFAMethod;
  expiresAt: Date;
  attempts: number;
  maxAttempts: number;
}

class MultiFactorAuthService {
  private static instance: MultiFactorAuthService;
  private readonly MAX_ATTEMPTS = 3;
  private readonly CODE_LENGTH = 6;
  private readonly BACKUP_CODE_LENGTH = 8;
  private readonly BACKUP_CODE_COUNT = 10;
  private readonly TOTP_WINDOW = 30; // seconds
  private readonly CHALLENGE_EXPIRY = 5 * 60 * 1000; // 5 minutes
  private activeChallenges: Map<string, MFAChallenge> = new Map();

  private constructor() {
    this.initializeService();
  }

  static getInstance(): MultiFactorAuthService {
    if (!MultiFactorAuthService.instance) {
      MultiFactorAuthService.instance = new MultiFactorAuthService();
    }
    return MultiFactorAuthService.instance;
  }

  private initializeService(): void {
    // Clean up expired challenges periodically
    setInterval(() => {
      this.cleanupExpiredChallenges();
    }, 60000); // Every minute
  }

  /**
   * Setup TOTP (Time-based One-Time Password) authentication
   */
  async setupTOTP(_userId: string): Promise<TOTPSetup> {
    try {
      // Generate secret
      const secret = this.generateTOTPSecret();

      // Generate backup codes
      const backupCodes = this.generateBackupCodes();

      // Store encrypted setup
      await this.storeMFASetup(_userId, {
        method: 'totp',
        enabled: false,
        verified: false,
        createdAt: new Date(),
        metadata: {
          secret: await cryptoService.encrypt(secret),
          backupCodes: await cryptoService.encrypt(JSON.stringify(_backupCodes)),
          usedBackupCodes: [],
        },
      });

      // Generate QR code URL
      const qrCodeUrl = this.generateTOTPQRCode(_userId, secret);

      // Log setup initiation
      await auditLogger.log({
        event: 'MFA_ENABLED',
        _userId,
        details: { method: 'totp' },
        severity: 'info',
      });

      return {
        secret,
        qrCodeUrl,
        backupCodes,
      };
    } catch (error) {
      await auditLogger.log({
        event: 'SECURITY_ALERT',
        _userId,
        details: { error: error instanceof Error ? error.message : String(error), action: 'mfa_setup_failed' },
        severity: 'error',
      });
      throw error;
    }
  }

  /**
   * Verify TOTP setup with initial code
   */
  async verifyTOTPSetup(_userId: string, code: string): Promise<boolean> {
    try {
      const setup = await this.getMFASetup(_userId, 'totp');
      if (!setup) {
        throw new Error('TOTP not configured');
      }

      const secret = await cryptoService.decrypt(setup.metadata?.secret || '');
      const isValid = await this.verifyTOTPCode(secret, code);

      if (_isValid) {
        // Mark as verified and enabled
        setup.enabled = true;
        setup.verified = true;
        await this.storeMFASetup(_userId, setup);

        await auditLogger.log({
          event: 'MFA_ENABLED',
          _userId,
          details: { method: 'totp', verified: true },
          severity: 'info',
        });
      }

      return isValid;
    } catch (error) {
      await auditLogger.log({
        event: 'SECURITY_ALERT',
        _userId,
        details: { error: error instanceof Error ? error.message : String(error), action: 'mfa_setup_failed' },
        severity: 'warning',
      });
      return false;
    }
  }

  /**
   * Setup SMS authentication
   */
  async setupSMS(_userId: string, _phoneNumber: string): Promise<void> {
    try {
      // Validate phone number
      const sanitizedPhone = this.sanitizePhoneNumber(_phoneNumber);

      // Store setup
      await this.storeMFASetup(_userId, {
        method: 'sms',
        enabled: false,
        verified: false,
        createdAt: new Date(),
        metadata: {
          _phoneNumber: await cryptoService.encrypt(sanitizedPhone),
        },
      });

      // Send verification code
      await this.sendSMSCode(_userId, sanitizedPhone);

      await auditLogger.log({
        event: 'MFA_ENABLED',
        _userId,
        details: { method: 'sms' },
        severity: 'info',
      });
    } catch (error) {
      await auditLogger.log({
        event: 'SECURITY_ALERT',
        _userId,
        details: { error: error instanceof Error ? error.message : String(error), action: 'mfa_setup_failed' },
        severity: 'error',
      });
      throw error;
    }
  }

  /**
   * Setup email authentication
   */
  async setupEmail(_userId: string, email: string): Promise<void> {
    try {
      // Store setup
      await this.storeMFASetup(_userId, {
        method: 'email',
        enabled: false,
        verified: false,
        createdAt: new Date(),
        metadata: {
          email: await cryptoService.encrypt(email),
        },
      });

      // Send verification code
      await this.sendEmailCode(_userId, email);

      await auditLogger.log({
        event: 'MFA_ENABLED',
        _userId,
        details: { method: 'email' },
        severity: 'info',
      });
    } catch (error) {
      await auditLogger.log({
        event: 'SECURITY_ALERT',
        _userId,
        details: { error: error instanceof Error ? error.message : String(error), action: 'mfa_setup_failed' },
        severity: 'error',
      });
      throw error;
    }
  }

  /**
   * Setup biometric authentication
   */
  async setupBiometric(_userId: string): Promise<boolean> {
    try {
      // Check if WebAuthn is available
      if (!window.PublicKeyCredential) {
        throw new Error('Biometric authentication not supported');
      }

      // Create credential options
      const credentialOptions = await this.createBiometricCredential(_userId);

      // Store setup
      await this.storeMFASetup(_userId, {
        method: 'biometric',
        enabled: true,
        verified: true,
        createdAt: new Date(),
        metadata: credentialOptions,
      });

      await auditLogger.log({
        event: 'MFA_ENABLED',
        _userId,
        details: { method: 'biometric' },
        severity: 'info',
      });

      return true;
    } catch (error) {
      await auditLogger.log({
        event: 'SECURITY_ALERT',
        _userId,
        details: { error: error instanceof Error ? error.message : String(error), action: 'mfa_setup_failed' },
        severity: 'error',
      });
      return false;
    }
  }

  /**
   * Create MFA challenge for login
   */
  async createChallenge(_userId: string, _method?: MFAMethod): Promise<MFAChallenge> {
    try {
      // Get user's MFA _methods
      const _methods = await this.getUserMFAMethods(_userId);

      // Select method (use provided or default to most secure available)
      const selectedMethod = method || this.selectBestMethod(_methods);

      if (!selectedMethod) {
        throw new Error('No MFA method configured');
      }

      // Create challenge
      const challenge: MFAChallenge = {
        challengeId: cryptoService.generateSecureUUID(),
        method: selectedMethod,
        expiresAt: new Date(Date.now() + this.CHALLENGE_EXPIRY),
        attempts: 0,
        maxAttempts: this.MAX_ATTEMPTS,
      };

      // Store challenge
      this.activeChallenges.set(challenge.challengeId, challenge);

      // Send code based on method
      await this.sendChallengeCode(_userId, selectedMethod);

      await auditLogger.log({
        event: 'MFA_CHALLENGE_SUCCESS',
        _userId,
        details: { method: selectedMethod, challengeId: challenge.challengeId },
        severity: 'info',
      });

      return challenge;
    } catch (error) {
      await auditLogger.log({
        event: 'MFA_CHALLENGE_FAILED',
        _userId,
        details: { error: error instanceof Error ? error.message : String(error) },
        severity: 'warning',
      });
      throw error;
    }
  }

  /**
   * Verify MFA challenge response
   */
  async verifyChallenge(
    _userId: string,
    challengeId: string,
    code: string
  ): Promise<boolean> {
    try {
      const challenge = this.activeChallenges.get(_challengeId);

      if (!challenge) {
        throw new Error('Invalid or expired challenge');
      }

      // Check expiry
      if (new Date() > challenge.expiresAt) {
        this.activeChallenges.delete(_challengeId);
        throw new Error('Challenge expired');
      }

      // Check attempts
      challenge.attempts++;
      if (challenge.attempts > challenge.maxAttempts) {
        this.activeChallenges.delete(_challengeId);

        await auditLogger.log({
          event: 'SECURITY_ALERT',
          _userId,
          details: { reason: 'max_mfa_attempts_exceeded', challengeId },
          severity: 'critical',
        });

        throw new Error('Maximum attempts exceeded');
      }

      // Verify code based on method
      let isValid = false;

      switch (challenge.method) {
        case 'totp':
          isValid = await this.verifyTOTP(_userId, code);
          break;
        case 'sms':
        case 'email':
          isValid = await this.verifyTemporaryCode(_userId, code);
          break;
        case 'backup':
          isValid = await this.verifyBackupCode(_userId, code);
          break;
        case 'biometric':
          isValid = await this.verifyBiometric(_userId, code);
          break;
      }

      if (_isValid) {
        // Remove challenge
        this.activeChallenges.delete(_challengeId);

        // Update last used
        await this.updateLastUsed(_userId, challenge.method);

        await auditLogger.log({
          event: 'MFA_CHALLENGE_SUCCESS',
          _userId,
          details: { method: challenge.method, challengeId },
          severity: 'info',
        });
      } else {
        await auditLogger.log({
          event: 'MFA_CHALLENGE_FAILED',
          _userId,
          details: { 
            method: challenge.method, 
            challengeId,
            attempt: challenge.attempts 
          },
          severity: 'warning',
        });
      }

      return isValid;
    } catch (error) {
      await auditLogger.log({
        event: 'MFA_CHALLENGE_FAILED',
        userId,
        details: { error: error instanceof Error ? error.message : String(error), challengeId },
        severity: 'error',
      });
      return false;
    }
  }

  /**
   * Disable MFA method
   */
  async disableMFA(_userId: string, method: MFAMethod): Promise<void> {
    try {
      await this.removeMFASetup(_userId, method);

      await auditLogger.log({
        event: 'MFA_DISABLED',
        _userId,
        details: { method },
        severity: 'info',
      });
    } catch (error) {
      await auditLogger.log({
        event: 'SECURITY_ALERT',
        _userId,
        details: { error: error instanceof Error ? error.message : String(error), action: 'mfa_setup_failed' },
        severity: 'error',
      });
      throw error;
    }
  }

  /**
   * Get user's enabled MFA methods
   */
  async getUserMFAMethods(_userId: string): Promise<MFASetup[]> {
    const _key = `mfa_${_userId}`;
    const stored = await secureStorage.getItem(_key);

    if (!stored || !stored.methods) {
      return [];
    }

    return stored.methods.filter((m: MFASetup) => m.enabled);
  }

  /**
   * Check if user has MFA enabled
   */
  async hasMFAEnabled(_userId: string): Promise<boolean> {
    const _methods = await this.getUserMFAMethods(_userId);
    return _methods.length > 0;
  }

  /**
   * Generate recovery codes
   */
  async generateRecoveryCodes(_userId: string): Promise<string[]> {
    try {
      const codes = this.generateBackupCodes();

      // Store encrypted codes
      const _key = `mfa_recovery_${_userId}`;
      await secureStorage.setItem(_key, {
        codes: await cryptoService.encrypt(JSON.stringify(_codes)),
        generated: new Date(),
        used: [],
      });

      await auditLogger.log({
        event: 'SECURITY_ALERT',
        _userId,
        details: { action: 'recovery_codes_generated' },
        severity: 'info',
      });

      return codes;
    } catch (error) {
      logger.error('Failed to generate recovery codes:');
      throw undefined;
    }
  }

  /**
   * Private helper methods
   */
  private generateTOTPSecret(): string {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ234567';
    let secret = '';
    for (let i = 0; i < 32; i++) {
      secret += characters[Math.floor(Math.random() * characters.length)];
    }
    return secret;
  }

  private generateTOTPQRCode(_userId: string, secret: string): string {
    const issuer = 'Mental Health Platform';
    const algorithm = 'SHA1';
    const digits = this.CODE_LENGTH;
    const period = this.TOTP_WINDOW;

    const _otpauth = `_otpauth://totp/${issuer}:${_userId}?secret=${secret}&issuer=${issuer}&algorithm=${algorithm}&digits=${digits}&period=${period}`;

    // In production, generate actual QR code
    return `https://api.qrserver.com/v1/create-qr-code/?size=200x200&data=${encodeURIComponent(_otpauth)}`;
  }

  private async verifyTOTPCode(secret: string, code: string): Promise<boolean> {
    // Simplified TOTP verification - in production, use a proper TOTP library
    const _time = Math.floor(Date.now() / 1000 / this.TOTP_WINDOW);

    // Check current and adjacent time windows
    for (let i = -1; i <= 1; i++) {
      const testTime = time + i;
      const expectedCode = await this.generateTOTPCode(secret, testTime);
      if (expectedCode === code) {
        return true;
      }
    }

    return false;
  }

  private async generateTOTPCode(secret: string, time: number): Promise<string> {
    // Simplified TOTP generation - in production, use a proper implementation
    const hash = await cryptoService.sha256(`${secret}${time}`);
    const code = parseInt(hash.substr(0, 6), 16) % 1000000;
    return code.toString().padStart(6, '0');
  }

  private generateBackupCodes(): string[] {
    const codes: string[] = [];
    for (let i = 0; i < this.BACKUP_CODE_COUNT; i++) {
      codes.push(this.generateBackupCode());
    }
    return codes;
  }

  private generateBackupCode(): string {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    let code = '';
    for (let i = 0; i < this.BACKUP_CODE_LENGTH; i++) {
      if (i === 4) code += '-'; // Add separator
      code += characters[Math.floor(Math.random() * characters.length)];
    }
    return code;
  }

  private sanitizePhoneNumber(phone: string): string {
    // Remove all non-digits
    const digits = phone.replace(/\D/g, '');

    // Validate length (assuming US numbers)
    if (digits.length !== 10 && digits.length !== 11) {
      throw new Error('Invalid phone number');
    }

    return digits;
  }

  private async sendSMSCode(_userId: string, _phoneNumber: string): Promise<void> {
    const code = this.generateVerificationCode();

    // Store temporary code
    await this.storeTemporaryCode(_userId, code);

    // In production, send actual SMS
    logger.info(`SMS to ${_phoneNumber}: Your verification code is ${code}`);
  }

  private async sendEmailCode(_userId: string, email: string): Promise<void> {
    const code = this.generateVerificationCode();

    // Store temporary code
    await this.storeTemporaryCode(_userId, code);

    // In production, send actual email
    logger.info(`Email to ${email}: Your verification code is ${code}`);
  }

  private generateVerificationCode(): string {
    return Math.floor(100000 + Math.random() * 900000).toString();
  }

  private async storeTemporaryCode(_userId: string, code: string): Promise<void> {
    const _key = `mfa_temp_${_userId}`;
    await secureStorage.setItem(_key, {
      code: await cryptoService.encrypt(code),
      expiresAt: new Date(Date.now() + this.CHALLENGE_EXPIRY),
    });
  }

  private async verifyTemporaryCode(_userId: string, code: string): Promise<boolean> {
    const _key = `mfa_temp_${_userId}`;
    const stored = await secureStorage.getItem(_key);

    if (!stored) return false;

    if (new Date() > new Date(stored.expiresAt)) {
      await secureStorage.removeItem(_key);
      return false;
    }

    const storedCode = await cryptoService.decrypt(stored.code);
    const isValid = storedCode === code;

    if (_isValid) {
      await secureStorage.removeItem(_key);
    }

    return isValid;
  }

  private async verifyTOTP(_userId: string, code: string): Promise<boolean> {
    const setup = await this.getMFASetup(_userId, 'totp');
    if (!setup) return false;

    const secret = await cryptoService.decrypt(setup.metadata?.secret || '');
    return await this.verifyTOTPCode(secret, code);
  }

  private async verifyBackupCode(_userId: string, code: string): Promise<boolean> {
    const setup = await this.getMFASetup(_userId, 'totp');
    if (!setup) return false;

    const backupCodes = JSON.parse(
      await cryptoService.decrypt(setup.metadata?.backupCodes || '[]')
    );
    const usedCodes = setup.metadata?.usedBackupCodes || [];

    if (usedCodes.includes(code)) {
      return false;
    }

    const isValid = backupCodes.includes(code);

    if (_isValid) {
      // Mark code as used
      usedCodes.push(code);
      if (setup.metadata) {
        setup.metadata.usedBackupCodes = usedCodes;
      }
      await this.storeMFASetup(_userId, setup);
    }

    return isValid;
  }

  private async verifyBiometric(_userId: string, credential: string): Promise<boolean> {
    // In production, verify WebAuthn credential
    logger.info('Verifying biometric credential:', credential);
    return true; // Simplified for development
  }

  private async createBiometricCredential(_userId: string): Promise<unknown> {
    // In production, create WebAuthn credential
    return {
      credentialId: cryptoService.generateSecureUUID(),
      publicKey: 'mock_public_key',
    };
  }

  private async storeMFASetup(_userId: string, setup: MFASetup): Promise<void> {
    const _key = `mfa_${_userId}`;
    const existing = await secureStorage.getItem(_key) || { _methods: [] };

    // Update or add method
    const index = existing.methods.findIndex((m: MFASetup) => m.method === setup.method);
    if (index >= 0) {
      existing._methods[index] = setup;
    } else {
      existing._methods.push(setup);
    }

    await secureStorage.setItem(_key, existing);
  }

  private async getMFASetup(_userId: string, method: MFAMethod): Promise<MFASetup | null> {
    const _key = `mfa_${_userId}`;
    const stored = await secureStorage.getItem(_key);

    if (!stored || !stored.methods) {
      return null;
    }

    return stored.methods.find((m: MFASetup) => m.method === method) || null;
  }

  private async removeMFASetup(_userId: string, method: MFAMethod): Promise<void> {
    const _key = `mfa_${_userId}`;
    const existing = await secureStorage.getItem(_key);

    if (existing && existing.methods) {
      existing._methods = existing._methods.filter((m: MFASetup) => m.method !== method);
      await secureStorage.setItem(_key, existing);
    }
  }

  private async updateLastUsed(_userId: string, method: MFAMethod): Promise<void> {
    const setup = await this.getMFASetup(_userId, method);
    if (setup) {
      setup.lastUsed = new Date();
      await this.storeMFASetup(_userId, setup);
    }
  }

  private selectBestMethod(_methods: MFASetup[]): MFAMethod | null {
    // Priority order: biometric > totp > sms > email
    const priority: MFAMethod[] = ['biometric', 'totp', 'sms', 'email'];

    for (const method of priority) {
      if (_methods.some(m => m.method === method)) {
        return method;
      }
    }

    return null;
  }

  private async sendChallengeCode(_userId: string, method: MFAMethod): Promise<void> {
    switch (method) {
      case "sms": {
        const smsSetup = await this.getMFASetup(_userId, 'sms');
        if (smsSetup) {
          const phone = await cryptoService.decrypt(smsSetup.metadata?._phoneNumber || '');
          await this.sendSMSCode(_userId, phone);
        }
        break;
      }
      case "email": {
        const emailSetup = await this.getMFASetup(_userId, 'email');
        if (emailSetup) {
          const email = await cryptoService.decrypt(emailSetup.metadata?.email || '');
          await this.sendEmailCode(_userId, email);
        }
        break;
      }
      // TOTP and biometric don't need to send codes
    }
  }

  private cleanupExpiredChallenges(): void {
    const now = new Date();
    for (const [_id, challenge] of this.activeChallenges.entries()) {
      if (now > challenge.expiresAt) {
        this.activeChallenges.delete(_id);
      }
    }
  }
}

