    if (soundPref !== null) {
      this.soundEnabled = JSON.parse(_soundPref);
    }
    
    if (hapticPref !== null) {
      this.hapticEnabled = JSON.parse(_hapticPref);
    }
    
    if (volumePref !== null) {
      this.masterVolume = Math.max(0, Math.min(1, parseFloat(_volumePref)));
    }
