  private queueSound(_soundName: keyof typeof this.CONSOLE_SOUNDS, options?: PartialSoundConfig): void {
    this.soundQueue.push({
      _soundName,
      options,
      timestamp: Date.now()
    });
    
    if (!this.processingQueue) {
