// Mock WebSocket Adapter - Seamlessly integrates MockCrisisServer with existing WebSocket interface
// Provides realistic demo functionality without requiring backend services

import { RealtimeMessage } from '../realtime/websocketService';
import { mockCrisisServer, MockCrisisSession, MockCounselor } from './MockCrisisServer';
import { toast } from 'react-hot-toast';
import { logger } from '../../utils/logger';

// Mock WebSocket Adapter that mimics the real WebSocketService interface
export class MockWebSocketAdapter {
  private static instance: MockWebSocketAdapter;
  private eventHandlers: Map<string, Set<(...args: any[]) => void>> = new Map();
  private isConnected: boolean = false;
  private currentUserId: string | null = null;
  private activeSessions: Map<string, MockCrisisSession> = new Map();
  private connectionSimulationTimeout: NodeJS.Timeout | null = null;

  private constructor() {
    this.setupMockCrisisServer();
  }

  public static getInstance(): MockWebSocketAdapter {
    if (!MockWebSocketAdapter.instance) {
      MockWebSocketAdapter.instance = new MockWebSocketAdapter();
    }
    return MockWebSocketAdapter.instance;
  }

  // Setup mock crisis server event handlers
  private setupMockCrisisServer(): void {
    mockCrisisServer.onEmergency((action: string, data: unknown) => {
      this.handleEmergencyProtocol(action, data);
    });
  }

  // Simulate WebSocket connection
  public async connect(userId: string, _token: string): Promise<void> {
    return new Promise((resolve, _reject) => {
      if (this.isConnected) {
        resolve();
        return;
      }

      this.currentUserId = userId;

      // Simulate connection delay
      this.connectionSimulationTimeout = setTimeout(() => {
        this.isConnected = true;
        logger.info('Mock WebSocket connected successfully', 'MockWebSocketAdapter');
        
        this.emit('connection:established', { userId });
        toast.success('Connected to crisis support system');
        
        resolve();
      }, 1000 + Math.random() * 2000); // 1-3 second delay
    });
  }

  // Simulate joining a crisis room
  public async joinRoom(_roomId: string): Promise<void> {
    return new Promise((resolve) => {
      if (!this.isConnected) {
        throw new Error('Not connected to server');
      }

      // Simulate join delay
      setTimeout(() => {
        this.emit('room:joined', _roomId);
        logger.info(`Joined room: ${_roomId}`, 'MockWebSocketAdapter');
        resolve();
      }, 500);
    });
  }

  // Simulate leaving a room
  public leaveRoom(_roomId: string): void {
    if (!this.isConnected) return;
    
    // End any active crisis sessions
    const session = this.activeSessions.get(_roomId);
    if (session) {
      session.end();
      this.activeSessions.delete(_roomId);
    }

    this.emit('room:left', _roomId);
    logger.info(`Left room: ${_roomId}`, 'MockWebSocketAdapter');
  }

  // Create and join a crisis session
  public async createCrisisSession(priority: 'low' | 'medium' | 'high' | 'critical' = 'medium'): Promise<{
    _sessionId: string;
    counselor: MockCounselor;
  }> {
    if (!this.currentUserId) {
      throw new Error('User not authenticated');
    }

    // Create crisis session through mock server
    const session = mockCrisisServer.createCrisisSession(this.currentUserId, priority);
    this.activeSessions.set(session.sessionId, session);

    // Setup session event handlers
    session.onMessage((message: RealtimeMessage) => {
      this.emit('message:new', message);
    });

    session.onTyping((_isTyping: boolean) => {
      if (_isTyping) {
        this.emit('typing:start', {
          userId: session.counselor.id,
          username: session.counselor.name,
          _roomId: session._sessionId
        });
      } else {
        this.emit('typing:stop', {
          userId: session.counselor.id,
          username: session.counselor.name,
          _roomId: session._sessionId
        });
      }
    });

    session.onEmergency((action: string, data: unknown) => {
      this.emit('crisis:escalated', { action, data, _sessionId: session._sessionId });
    });

    // Join the room
    await this.joinRoom(session.sessionId);

    // Simulate counselor assignment process
    setTimeout(() => {
      this.emit('counselor:assigned', {
        id: session.counselor.id,
        name: session.counselor.name,
        credentials: session.counselor.credentials,
        specialties: session.counselor.specialties,
        responseTime: session.counselor.responseTime
      });
    }, 2000);

    // Simulate queue position updates
    this.simulateQueueUpdates(session.sessionId);

    return {
      _sessionId: session._sessionId,
      counselor: session.counselor
    };
  }

  // Simulate queue position updates before counselor assignment
  private simulateQueueUpdates(_sessionId: string): void {
    let position = Math.floor(Math.random() * 5) + 1; // 1-5 position
    let estimatedWait = position * 30; // 30 seconds per position

    const updateQueue = () => {
      if (position > 1) {
        this.emit('queue:update', {
          position,
          estimatedWait
        });

        position--;
        estimatedWait = Math.max(30, estimatedWait - 30);

        setTimeout(updateQueue, 2000 + Math.random() * 3000); // 2-5 seconds between updates
      }
    };

    // Start queue simulation only if there's a queue
    if (position > 1) {
      setTimeout(updateQueue, 1000);
    }
  }

  // Send message to crisis session
  public sendMessage(_roomId: string, content: string, _type: 'text' | 'system' = 'text'): void {
    const session = this.activeSessions.get(_roomId);
    if (!session) {
      logger.warn(`No active session found for room: ${_roomId}`);
      return;
    }

    // Send message to mock crisis session
    session.sendMessage(_content);
  }

  // Send typing indicator
  public sendTypingIndicator(_roomId: string, _isTyping: boolean): void {
    // Mock typing indicators are handled automatically by the crisis session
    // This is a no-op in the mock implementation
  }

  // Handle emergency protocols
  private handleEmergencyProtocol(action: string, data: unknown): void {
    logger.error('ðŸš¨ EMERGENCY PROTOCOL TRIGGERED:', action, data);

    switch (_action) {
      case 'auto_dial_988':
        this.triggerEmergencyCall('988', 'Suicide & Crisis Lifeline', data);
        break;
      case 'auto_dial_911':
        this.triggerEmergencyCall('911', 'Emergency Services', data);
        break;
      case 'crisis_escalation':
        this.handleCrisisEscalation(data);
        break;
      default:
        logger.warn('Unknown emergency action:', action);
    }

    // Emit crisis event for UI handling
    this.emit('crisis:emergency', { action, data });
  }

  // Trigger emergency call
  private triggerEmergencyCall(number: string, service: string, data: unknown): void {
    // Show immediate emergency modal
    const emergencyMessage = {
      id: `emergency-${Date.now()}`,
      _roomId: data.sessionId || 'system',
      userId: 'system',
      username: 'Emergency System',
      content: `ðŸš¨ EMERGENCY PROTOCOL ACTIVATED\n\nI'm initiating an emergency call to ${service} (${number}) based on your situation. This is for your immediate safety.\n\nIf you're in immediate danger, please call ${number} directly or go to your nearest emergency room.`,
      timestamp: new Date(),
      _type: 'crisis-alert' as const
    };

    this.emit('message:new', emergencyMessage);

    // Simulate emergency call initiation after brief delay
    setTimeout(() => {
      // Show browser alert with emergency information
      const _alertMessage = `ðŸš¨ EMERGENCY CALL INITIATED\n\nService: ${service}\nNumber: ${number}\n\nâš ï¸ IMPORTANT: This is a demonstration system.\nIn a real emergency, please call ${number} immediately.\n\nPressing OK will simulate dialing ${number}.`;
      
      if (confirm(_alertMessage)) {
        // Actually initiate the phone call
        if (typeof window !== 'undefined') {
          window.location.href = `tel:${number}`;
        }
      }
    }, 2000);

    // Log emergency event
    logger.error(`ðŸ“ž EMERGENCY CALL: ${service} (${number}) - Session: ${data._sessionId}`);
  }

  // Handle crisis escalation
  private handleCrisisEscalation(data: unknown): void {
    const escalationMessage = {
      id: `escalation-${Date.now()}`,
      _roomId: data.sessionId || 'system',
      userId: 'system',
      username: 'Crisis Team',
      content: 'ðŸ†˜ CRISIS ESCALATION: Your situation has been escalated to our emergency response team. A crisis specialist is being notified immediately. Please stay on the line.',
      timestamp: new Date(),
      _type: 'crisis-alert' as const
    };

    this.emit('message:new', escalationMessage);

    // Simulate crisis specialist joining
    setTimeout(() => {
      const specialistMessage = {
        id: `specialist-${Date.now()}`,
        _roomId: data.sessionId || 'system',
        userId: 'crisis-specialist',
        username: 'Dr. Crisis Specialist',
        content: 'Hello, I\'m Dr. Martinez, a crisis intervention specialist. I\'ve been notified of your situation and I\'m here to help. Your safety is our absolute priority. Can you tell me your current location?',
        timestamp: new Date(),
        _type: 'text' as const
      };

      this.emit('message:new', specialistMessage);
    }, 3000);
  }

  // Simulate connection loss and recovery
  public simulateConnectionLoss(): void {
    if (!this.isConnected) return;

    this.isConnected = false;
    this.emit('connection:lost', { reason: 'Network disruption' });
    toast.error('Connection lost. Attempting to reconnect...');

    // Simulate reconnection after 3-8 seconds
    setTimeout(() => {
      this.isConnected = true;
      this.emit('connection:established', { userId: this.currentUserId });
      toast.success('Connection restored');
      
      // Re-establish active sessions
      this.activeSessions.forEach((session, _sessionId) => {
        this.emit('room:joined', _sessionId);
      });
    }, 3000 + Math.random() * 5000);
  }

  // Event emitter methods
  public on(event: string, handler: (...args: unknown[]) => any): void {
    if (!this.eventHandlers.has(event)) {
      this.eventHandlers.set(event, new Set());
    }
    this.eventHandlers.get(event)!.add(handler);
  }

  public off(event: string, handler: (...args: unknown[]) => any): void {
    const handlers = this.eventHandlers.get(event);
    if (handlers) {
      handlers.delete(handler);
    }
  }

  public emit(event: string, data: unknown): void {
    const handlers = this.eventHandlers.get(event);
    if (handlers) {
      handlers.forEach(handler => {
        try {
          handler(data);
        } catch {
          logger.error(`Error in event handler for ${event}`);
        }
      });
    }
  }

  // Utility methods
  public isConnectedToServer(): boolean {
    return this.isConnected;
  }

  public getSocket(): unknown {
    // Return a mock socket object with limited functionality
    return {
      connected: this.isConnected,
      emit: (event: string, data: unknown, callback?: (...args: unknown[]) => any) => {
        // Handle specific socket events
        switch (event) {
          case 'crisis:request-counselor':
            this.handleCounselorRequest(data);
            break;
          case 'crisis:escalate':
            this.handleCrisisEscalation(data);
            break;
          default:
            logger.debug(`Mock socket emit: ${event}`, 'MockWebSocketAdapter', data);
        }
        
        if (callback) {
          setTimeout(() => callback({ success: true }), 100);
        }
      }
    };
  }

  // Handle counselor request
  private handleCounselorRequest(data: unknown): void {
    logger.crisis('Crisis counselor requested', 'high', 'MockWebSocketAdapter', data);
    
    // This is handled automatically when creating a crisis session
    // The MockCrisisServer manages counselor assignment
  }

  // Disconnect
  public disconnect(): void {
    if (this.connectionSimulationTimeout) {
      clearTimeout(this.connectionSimulationTimeout);
    }

    // End all active sessions
    this.activeSessions.forEach(session => session.end());
    this.activeSessions.clear();

    this.isConnected = false;
    this.currentUserId = null;
    this.eventHandlers.clear();
    
    logger.info('Mock WebSocket disconnected', 'MockWebSocketAdapter');
  }

  // Get current session for a room
  public getSession(_roomId: string): MockCrisisSession | undefined {
    return this.activeSessions.get(_roomId);
  }

  // Get mock server statistics
  public getServerStats(): {
    activeSessions: number;
    availableCounselors: number;
    totalCounselors: number;
  } {
    return mockCrisisServer.getStats();
  }

  // Test emergency protocols (for demo purposes)
  public testEmergencyProtocol(_type: 'suicide_risk' | 'medical_emergency' | 'connection_loss'): void {
    logger.crisis(`Testing emergency protocol: ${_type}`, 'critical', 'MockWebSocketAdapter');
    
    switch (_type) {
      case 'suicide_risk':
        this.handleEmergencyProtocol('auto_dial_988', {
          reason: 'Test suicide risk protocol',
          _sessionId: 'test-session'
        });
        break;
      case 'medical_emergency':
        this.handleEmergencyProtocol('auto_dial_911', {
          reason: 'Test medical emergency protocol',
          _sessionId: 'test-session'
        });
        break;
      case 'connection_loss':
        this.simulateConnectionLoss();
        break;
    }
  }

  // End call functionality for testing
  public endCall(): void {
    logger.info('Ending mock crisis call session', 'MockWebSocketAdapter');
    
    // Clear all active sessions
    for (const [_sessionId, session] of this.activeSessions.entries()) {
      logger.info(`Ending session: ${_sessionId}`, 'MockWebSocketAdapter');
      // Emit session end event
      this.emit('session:ended', {
        _sessionId,
        counselorId: session.counselor.id,
        timestamp: new Date()
      });
    }
    
    // Clear the sessions map
    this.activeSessions.clear();
    
    // Emit call ended event
    this.emit('call:ended', {
      timestamp: new Date(),
      reason: 'user_ended'
    });
  }
}

// Export singleton instance
export const __mockWebSocketAdapter = MockWebSocketAdapter.getInstance();

/**
 * Core Data Integration Service
 * Manages data flow and synchronization between all components
 */

import { _create } from 'zustand';
import { _subscribeWithSelector } from 'zustand/middleware';
import { useWellnessStore } from '../../stores/wellnessStore';
import { useActivityStore } from '../../stores/activityStore';
import { useAccessibilityStore } from '../../stores/accessibilityStore';
import { WebSocketService } from '../websocket/WebSocketService';
import { _User } from '../api/types';
import { EventEmitter } from 'events';
import { logger } from '../../utils/logger';

// Integration event types
export enum IntegrationEvent {
  // Data sync events
  STORE_SYNC_STARTED = 'store:sync:started',
  STORE_SYNC_COMPLETED = 'store:sync:completed',
  STORE_SYNC_FAILED = 'store:sync:failed',

  // Cross-feature events
  MOOD_UPDATED = 'mood:updated',
  CRISIS_TRIGGERED = 'crisis:triggered',
  GOAL_ACHIEVED = 'goal:achieved',
  THERAPY_SESSION_COMPLETED = 'therapy:completed',
  MEDICATION_TAKEN = 'medication:taken',
  COMMUNITY_INTERACTION = 'community:interaction',

  // Real-time events
  REALTIME_CONNECTED = 'realtime:connected',
  REALTIME_DISCONNECTED = 'realtime:disconnected',
  REALTIME_MESSAGE = 'realtime:message',

  // System events
  OFFLINE_MODE_ENABLED = 'offline:enabled',
  OFFLINE_MODE_DISABLED = 'offline:disabled',
  DATA_PERSISTED = 'data:persisted',
  DATA_RESTORED = 'data:restored'
}

// Data flow configuration
interface DataFlowConfig {
  enableRealtime: boolean;
  enableOfflineSync: boolean;
  syncInterval: number; // milliseconds
  retryAttempts: number;
  batchSize: number;
}

// Integration state
interface IntegrationState {
  isConnected: boolean;
  isSyncing: boolean;
  lastSyncTime: Date | null;
  syncErrors: Error[];
  pendingChanges: Map<string, any>;
  dataFlowConfig: DataFlowConfig;
}

// Cross-component data mapping
interface DataMapping {
  source: string;
  target: string;
  transform?: (data: unknown) => any;
  filter?: (data: unknown) => boolean;
  bidirectional?: boolean;
}

class DataIntegrationService extends EventEmitter {
  private static instance: DataIntegrationService;
  private wsService: WebSocketService | null = null;
  private state: IntegrationState;
  private dataMappings: DataMapping[] = [];
  private syncTimer: NodeJS.Timeout | null = null;
  private offlineQueue: Map<string, any> = new Map();

  private constructor() {
    super();

    this.state = {
      isConnected: false,
      isSyncing: false,
      lastSyncTime: null,
      syncErrors: [],
      pendingChanges: new Map(),
      dataFlowConfig: {
        enableRealtime: true,
        enableOfflineSync: true,
        syncInterval: 30000, // 30 seconds
        retryAttempts: 3,
        batchSize: 50
      }
    };

    this.initializeDataMappings();
    this.setupStoreSubscriptions();
    this.initializeWebSocket();
    this.startPeriodicSync();
  }

  public static getInstance(): DataIntegrationService {
    if (!DataIntegrationService.instance) {
      DataIntegrationService.instance = new DataIntegrationService();
    }
    return DataIntegrationService.instance;
  }

  /**
   * Initialize data mappings between components
   */
  private initializeDataMappings() {
    this.dataMappings = [
      // Mood to Crisis mapping
      {
        source: 'wellness.mood',
        target: 'crisis.riskAssessment',
        transform: (moodData) => ({
          riskLevel: this.calculateRiskFromMood(moodData),
          timestamp: new Date(),
          factors: moodData.triggers || []
        }),
        filter: (moodData) => moodData.moodScore <= 3
      },

      // Activity to Wellness mapping
      {
        source: 'activity.completed',
        target: 'wellness.metrics',
        transform: (activity) => ({
          activityType: activity.type,
          moodImpact: activity.actualMoodImpact,
          completedAt: activity.completedAt
        }),
        bidirectional: false
      },

      // Therapy to Goals mapping
      {
        source: 'therapy.homework',
        target: 'activity.tasks',
        transform: (homework) => ({
          title: homework.title,
          type: 'therapy',
          therapyHomework: true,
          linkedGoalId: homework.goalId
        }),
        bidirectional: true
      },

      // Community to Wellness mapping
      {
        source: 'community.interaction',
        target: 'wellness.socialMetrics',
        transform: (interaction) => ({
          type: interaction.type,
          timestamp: interaction.timestamp,
          positiveImpact: interaction.sentiment > 0
        })
      },

      // Crisis to Professional Care mapping
      {
        source: 'crisis.event',
        target: 'professional.alerts',
        transform: (crisisEvent) => ({
          severity: crisisEvent.severity,
          timestamp: crisisEvent.timestamp,
          requiresImmediate: crisisEvent.severity === 'critical'
        }),
        filter: (crisisEvent) => crisisEvent.severity !== 'low'
      }
    ];
  }

  /**
   * Setup store subscriptions for data flow
   */
  private setupStoreSubscriptions() {
    // Subscribe to wellness store changes
    useWellnessStore.subscribe((state) => {
      if (state.moodEntries.length > 0) {
        this.handleDataChange('wellness.mood', state.moodEntries[state.moodEntries.length - 1]);
      }
    });

    // Subscribe to activity store changes
    useActivityStore.subscribe((state) => {
      const completed = state.activities.filter((a: unknown) => a.completed && !a.synced);
      completed.forEach((activity: unknown) => {
        this.handleDataChange('activity.completed', activity);
      });
    });

    // Subscribe to accessibility store for user preferences
    useAccessibilityStore.subscribe((state) => {
      this.updateDataFlowConfig({
        enableRealtime: !state.settings.reducedMotion
      });
    });
  }

  /**
   * Initialize WebSocket connection for real-time features
   */
  private async initializeWebSocket() {
    if (!this.state.dataFlowConfig.enableRealtime) return;

    try {
      this.wsService = WebSocketService.getInstance();
      this.wsService.connect('', { 
        id: 'anonymous',
        email: 'anonymous@example.com',
        username: 'anonymous',
        role: 'patient',
        profile: {
          firstName: 'Anonymous',
          lastName: '_User',
          timezone: 'UTC'
        },
        preferences: {
          theme: 'auto',
          notifications: {
            email: false,
            push: false,
            sms: false,
            crisisAlerts: true,
            appointmentReminders: false,
            medicationReminders: false,
            communityUpdates: false
          },
          privacy: {
            profileVisibility: 'private',
            shareDataWithTherapist: false,
            anonymousMode: true,
            allowResearch: false
          },
          accessibility: {
            fontSize: 'medium',
            highContrast: false,
            screenReaderMode: false,
            reducedMotion: false
          }
        },
        createdAt: new Date(),
        updatedAt: new Date(),
        lastActive: new Date(),
        isVerified: false,
        twoFactorEnabled: false
      });

      // Setup WebSocket event listeners
      this.wsService.on('connect', () => {
        this.state.isConnected = true;
        this.emit(IntegrationEvent.REALTIME_CONNECTED);
        this.syncOfflineQueue();
      });

      this.wsService.on('disconnect', () => {
        this.state.isConnected = false;
        this.emit(IntegrationEvent.REALTIME_DISCONNECTED);
      });

      // Handle real-time data updates
      this.wsService.on('data:update', (data: unknown) => {
        this.handleRealtimeUpdate(data);
      });

      // Handle crisis events
      this.wsService.on('crisis:alert', (_alert: unknown) => {
        this.handleCrisisAlert(_alert);
      });

      // Handle community updates
      this.wsService.on('community:update', (update: unknown) => {
        this.handleCommunityUpdate(update);
      });

    } catch {
      logger.error('Failed to initialize WebSocket:');
      this.state.syncErrors.push(error as Error);
    }
  }

  /**
   * Handle data changes from stores
   */
  private handleDataChange(source: string, data: unknown) {
    if (!data) return;

    // Find relevant mappings
    const mappings = this.dataMappings.filter(m => m.source === source);

    mappings.forEach(mapping => {
      // Apply filter if exists
      if (mapping.filter && !mapping.filter(data)) return;

      // Transform data if needed
      const transformedData = mapping.transform ? mapping.transform(data) : data;

      // Route to target
      this.routeDataToTarget(mapping.target, transformedData);

      // Handle bidirectional sync
      if (mapping.bidirectional) {
        this.syncBidirectional(mapping, transformedData);
      }
    });

    // Queue for persistence
    this.queueForPersistence(source, data);

    // Emit integration event
    this.emitDataChangeEvent(source, data);
  }

  /**
   * Route data to target component/store
   */
  private routeDataToTarget(target: string, data: unknown) {
    const [store, property] = target.split('.');

    if (!property) {
      logger.warn('Invalid target format:', target);
      return;
    }

    switch (_store) {
      case 'wellness':
        this.updateWellnessStore(property, data);
        break;
      case 'activity':
        this.updateActivityStore(property, data);
        break;
      case 'crisis':
        this.updateCrisisData(property, data);
        break;
      case 'professional':
        this.updateProfessionalData(property, data);
        break;
      case 'community':
        this.updateCommunityData(property, data);
        break;
      default:
        logger.warn(`Unknown target store: ${store}`);
    }
  }

  /**
   * Update wellness store with integrated data
   */
  private updateWellnessStore(property: string, data: unknown) {
    const store = useWellnessStore.getState();

    switch (_property) {
      case 'metrics':
        store.addWellnessMetric(data);
        break;
      case 'mood':
        store.addMoodEntry(data);
        break;
      case 'insights':
        // Insights are generated, not directly added
        store.generateInsights();
        break;
    }
  }

  /**
   * Update activity store with integrated data
   */
  private updateActivityStore(property: string, data: unknown) {
    const store = useActivityStore.getState();

    switch (_property) {
      case 'tasks':
        store.addActivity(data);
        break;
      case 'goals':
        if (data.id) {
          store.updateGoal(data.id, data);
        } else {
          store.addGoal(data);
        }
        break;
    }
  }

  /**
   * Update crisis data
   */
  private updateCrisisData(property: string, data: unknown) {
    // Send to crisis service/store
    if (this.wsService && this.state.isConnected) {
      this.wsService.emit('crisis:update', { property, data });
    } else {
      this.offlineQueue.set(`crisis:${property}`, data);
    }
  }

  /**
   * Update professional care data
   */
  private updateProfessionalData(property: string, data: unknown) {
    // Send to professional care service
    if (this.wsService && this.state.isConnected) {
      this.wsService.emit('professional:update', { property, data });
    } else {
      this.offlineQueue.set(`professional:${property}`, data);
    }
  }

  /**
   * Update community data
   */
  private updateCommunityData(property: string, data: unknown) {
    // Send to community service
    if (this.wsService && this.state.isConnected) {
      this.wsService.emit('community:update', { property, data });
    } else {
      this.offlineQueue.set(`community:${property}`, data);
    }
  }

  /**
   * Handle real-time updates from WebSocket
   */
  private handleRealtimeUpdate(update: unknown) {
    const { source, data, timestamp } = update;

    // Check if update is newer than local data
    if (this.isNewerData(source, timestamp)) {
      this.handleDataChange(source, data);
    }

    this.emit(IntegrationEvent.REALTIME_MESSAGE, update);
  }

  /**
   * Handle crisis alerts
   */
  private handleCrisisAlert(_alert: unknown) {
    // Immediate routing to crisis components
    this.emit(IntegrationEvent.CRISIS_TRIGGERED, _alert);

    // Update relevant stores
    this.routeDataToTarget('crisis._alert', _alert);
    this.routeDataToTarget('wellness.crisisEvent', _alert);

    // Notify professional care if needed
    if (_alert.severity === 'critical') {
      this.routeDataToTarget('professional.emergencyAlert', _alert);
    }
  }

  /**
   * Handle community updates
   */
  private handleCommunityUpdate(update: unknown) {
    this.emit(IntegrationEvent.COMMUNITY_INTERACTION, update);
    this.routeDataToTarget('community.update', update);
  }

  /**
   * Calculate risk level from mood data
   */
  private calculateRiskFromMood(moodData: unknown): string {
    if (!moodData) return 'unknown';

    const { moodScore, triggers, stressLevel, anxietyLevel } = moodData;

    // Simple risk calculation (should be enhanced with ML model)
    let riskScore = 0;

    if (moodScore <= 2) riskScore += 3;
    else if (moodScore <= 3) riskScore += 2;
    else if (moodScore <= 4) riskScore += 1;

    if (stressLevel >= 8) riskScore += 2;
    else if (stressLevel >= 6) riskScore += 1;

    if (anxietyLevel >= 8) riskScore += 2;
    else if (anxietyLevel >= 6) riskScore += 1;

    if (triggers?.includes('suicidal_thoughts')) riskScore += 5;
    if (triggers?.includes('self_harm')) riskScore += 4;

    if (riskScore >= 7) return 'critical';
    if (riskScore >= 5) return 'high';
    if (riskScore >= 3) return 'medium';
    return 'low';
  }

  /**
   * Queue data for persistence
   */
  private queueForPersistence(source: string, data: unknown) {
    this.state.pendingChanges.set(source, {
      data,
      timestamp: new Date(),
      synced: false
    });
  }

  /**
   * Sync bidirectional data
   */
  private syncBidirectional(mapping: DataMapping, data: unknown) {
    // Implement bidirectional sync logic
    const __reverseMapping = {
      source: mapping.target,
      target: mapping.source,
      transform: mapping.transform // May need reverse transform
    };

    // Add to sync queue
    this.queueForPersistence(`sync:${mapping.target}`, data);
  }

  /**
   * Check if update is newer than local data
   */
  private isNewerData(source: string, timestamp: Date): boolean {
    const lastUpdate = this.state.pendingChanges.get(_source);
    if (!lastUpdate) return true;

    return timestamp > lastUpdate.timestamp;
  }

  /**
   * Emit data change event
   */
  private emitDataChangeEvent(source: string, data: unknown) {
    const eventMap: Record<string, IntegrationEvent> = {
      'wellness.mood': IntegrationEvent.MOOD_UPDATED,
      'activity.completed': IntegrationEvent.GOAL_ACHIEVED,
      'therapy.session': IntegrationEvent.THERAPY_SESSION_COMPLETED,
      'medication.taken': IntegrationEvent.MEDICATION_TAKEN,
      'community.interaction': IntegrationEvent.COMMUNITY_INTERACTION
    };

    const event = eventMap[source];
    if (_event) {
      this.emit(event, data);
    }
  }

  /**
   * Start periodic sync
   */
  private startPeriodicSync() {
    if (!this.state.dataFlowConfig.enableOfflineSync) return;

    this.syncTimer = setInterval(() => {
      this.syncPendingChanges();
    }, this.state.dataFlowConfig.syncInterval);
  }

  /**
   * Sync pending changes
   */
  private async syncPendingChanges() {
    if (this.state.isSyncing) return;

    this.state.isSyncing = true;
    this.emit(IntegrationEvent.STORE_SYNC_STARTED);

    try {
      const changes = Array.from(this.state.pendingChanges.entries());
      const batches = this.createBatches(changes, this.state.dataFlowConfig.batchSize);

      for (const _batch of batches) {
        await this.syncBatch(_batch);
      }

      this.state.lastSyncTime = new Date();
      this.emit(IntegrationEvent.STORE_SYNC_COMPLETED);

    } catch {
      logger.error('Sync failed:');
      this.state.syncErrors.push(error as Error);
      this.emit(IntegrationEvent.STORE_SYNC_FAILED, undefined);

    } finally {
      this.state.isSyncing = false;
    }
  }

  /**
   * Sync a batch of changes
   */
  private async syncBatch(_batch: [string, any][]): Promise<void> {
    // Implement _batch sync logic
    // This would typically make an API call to sync data
    return new Promise((resolve) => {
      setTimeout(() => {
        _batch.forEach(([key]) => {
          this.state.pendingChanges.delete(key);
        });
        resolve();
      }, 100);
    });
  }

  /**
   * Create batches from changes
   */
  private createBatches<T>(items: T[], batchSize: number): T[][] {
    const batches: T[][] = [];
    for (let i = 0; i < items.length; i += batchSize) {
      batches.push(items.slice(i, i + batchSize));
    }
    return batches;
  }

  /**
   * Sync offline queue when reconnected
   */
  private async syncOfflineQueue() {
    if (this.offlineQueue.size === 0) return;

    const entries = Array.from(this.offlineQueue.entries());

    for (const [key, data] of entries) {
      try {
        const [store, property] = key.split(':');
        this.routeDataToTarget(`${store}.${property}`, data);
        this.offlineQueue.delete(key);
      } catch {
        logger.error(`Failed to sync offline item ${key}:`, error);
      }
    }
  }

  /**
   * Update data flow configuration
   */
  public updateDataFlowConfig(config: Partial<DataFlowConfig>) {
    this.state.dataFlowConfig = {
      ...this.state.dataFlowConfig,
      ...config
    };

    // Restart services if needed
    if (config.syncInterval !== undefined) {
      if (this.syncTimer) clearInterval(this.syncTimer);
      this.startPeriodicSync();
    }

    if (config.enableRealtime !== undefined) {
      if (config.enableRealtime && !this.wsService) {
        this.initializeWebSocket();
      } else if (!config.enableRealtime && this.wsService) {
        this.wsService.disconnect();
        this.wsService = null;
      }
    }
  }

  /**
   * Get integration state
   */
  public getState(): IntegrationState {
    return { ...this.state };
  }

  /**
   * Force sync all data
   */
  public async forceSync(): Promise<void> {
    await this.syncPendingChanges();
  }

  /**
   * Clear sync errors
   */
  public clearErrors() {
    this.state.syncErrors = [];
  }

  /**
   * Cleanup and disconnect
   */
  public cleanup() {
    if (this.syncTimer) {
      clearInterval(this.syncTimer);
    }

    if (this.wsService) {
      this.wsService.disconnect();
    }

    this.removeAllListeners();
  }
}

// Export singleton instance
export const __dataIntegrationService = DataIntegrationService.getInstance();

// Export hook for React components
export function useDataIntegration() {
  const service = DataIntegrationService.getInstance();

  return {
    forceSync: () => service.forceSync(),
    getState: () => service.getState(),
    updateConfig: (config: Partial<DataFlowConfig>) => service.updateDataFlowConfig(config),
    clearErrors: () => service.clearErrors(),
    on: (event: IntegrationEvent, callback: (...args: unknown[]) => void) => {
      service.on(event, callback);
      return () => service.off(event, callback);
    }
  };
}

/**
 * Real-time Synchronization Service
 * Manages WebSocket connections and real-time data updates across components
 */

import { io, Socket } from 'socket.io-client';
import { EventEmitter } from 'events';
import { useWellnessStore } from '../../stores/wellnessStore';
import { useActivityStore } from '../../stores/activityStore';
import { logger } from '../../utils/logger';

// Real-time event types
export enum RealtimeEvent {
  // Connection events
  CONNECTED = 'connected',
  DISCONNECTED = 'disconnected',
  RECONNECTING = 'reconnecting',
  ERROR = 'error',

  // Crisis events
  CRISIS_ALERT_RECEIVED = 'crisis:alert:received',
  CRISIS_SUPPORT_CONNECTED = 'crisis:support:connected',
  CRISIS_MESSAGE_RECEIVED = 'crisis:message:received',
  CRISIS_STATUS_UPDATED = 'crisis:status:updated',

  // Community events
  COMMUNITY_POST_CREATED = 'community:post:created',
  COMMUNITY_COMMENT_ADDED = 'community:comment:added',
  COMMUNITY_USER_JOINED = 'community:user:joined',
  COMMUNITY_USER_LEFT = 'community:user:left',
  COMMUNITY_TYPING = 'community:typing',

  // Professional care events
  THERAPIST_AVAILABLE = 'therapist:available',
  APPOINTMENT_REMINDER = 'appointment:reminder',
  PRESCRIPTION_UPDATE = 'prescription:update',
  CARE_TEAM_MESSAGE = 'care:team:message',

  // Wellness tracking events
  MOOD_CHECK_REMINDER = 'mood:check:reminder',
  GOAL_MILESTONE_REACHED = 'goal:milestone:reached',
  WELLNESS_INSIGHT = 'wellness:insight',

  // Notification events
  NOTIFICATION_RECEIVED = 'notification:received',
  NOTIFICATION_READ = 'notification:read',
  NOTIFICATION_CLEARED = 'notification:cleared'
}

// Message types
interface RealtimeMessage {
  _id: string;
  _type: string;
  _payload: unknown;
  timestamp: Date;
  _userId?: string;
  metadata?: Record<string, any>;
}

// Subscription configuration
interface SubscriptionConfig {
  channel: string;
  events: string[];
  handler: (data: unknown) => void;
  filter?: (data: unknown) => boolean;
}

// Connection configuration
interface ConnectionConfig {
  url: string;
  reconnection: boolean;
  reconnectionAttempts: number;
  reconnectionDelay: number;
  _timeout: number;
  _auth?: {
    token: string;
    _userId: string;
  };
}

// Presence information
interface UserPresence {
  _userId: string;
  status: 'online' | 'away' | 'busy' | 'offline';
  lastSeen: Date;
  location?: string;
  activity?: string;
}

class RealtimeSyncService extends EventEmitter {
  private static instance: RealtimeSyncService;
  private socket: Socket | null = null;
  private config: ConnectionConfig;
  private subscriptions: Map<string, SubscriptionConfig> = new Map();
  private messageQueue: RealtimeMessage[] = [];
  private _presence: Map<string, UserPresence> = new Map();
  private reconnectAttempts = 0;
  private isConnected = false;
  private heartbeatInterval: NodeJS.Timeout | null = null;

  private constructor() {
    super();

    this.config = {
      url: import.meta.env.VITE_WS_URL || 'ws://localhost:3002',
      reconnection: true,
      reconnectionAttempts: 10,
      reconnectionDelay: 1000,
      _timeout: 20000
    };

    this.setupDefaultSubscriptions();
  }

  public static getInstance(): RealtimeSyncService {
    if (!RealtimeSyncService.instance) {
      RealtimeSyncService.instance = new RealtimeSyncService();
    }
    return RealtimeSyncService.instance;
  }

  /**
   * Connect to WebSocket server
   */
  public async connect(_auth?: { token: string; _userId: string }): Promise<void> {
    if (this.isConnected) return;

    return new Promise((resolve, reject) => {
      try {
        this.config._auth = _auth;

        this.socket = io(this.config.url, {
          reconnection: this.config.reconnection,
          reconnectionAttempts: this.config.reconnectionAttempts,
          reconnectionDelay: this.config.reconnectionDelay,
          _timeout: this.config._timeout,
          _auth: _auth || {}
        });

        this.setupSocketListeners();

        // Set connection timeout
        const _timeout = setTimeout(() => {
          reject(new Error('Connection _timeout'));
        }, this.config.timeout);

        this.socket.once('connect', () => {
          clearTimeout(_timeout);
          this.isConnected = true;
          this.reconnectAttempts = 0;
          this.emit(RealtimeEvent.CONNECTED);
          this.startHeartbeat();
          this.flushMessageQueue();
          resolve();
        });

        this.socket.once('connect_error', (error: unknown) => {
          clearTimeout(_timeout);
          this.emit(RealtimeEvent.ERROR, error);
          reject(_error);
        });

      } catch (_error) {
        reject(_error);
      }
    });
  }

  /**
   * Setup default subscriptions
   */
  private setupDefaultSubscriptions() {
    // Crisis channel subscription
    this.subscribe({
      channel: 'crisis',
      events: ['alert', 'support_connected', 'message', 'status_update'],
      handler: (_data) => this.handleCrisisUpdate(data)
    });

    // Community channel subscription
    this.subscribe({
      channel: 'community',
      events: ['post_created', 'comment_added', 'user_joined', 'user_left', 'typing'],
      handler: (_data) => this.handleCommunityUpdate(data)
    });

    // Professional care channel subscription
    this.subscribe({
      channel: 'professional',
      events: ['therapist_available', 'appointment_reminder', 'prescription_update', 'care_team_message'],
      handler: (_data) => this.handleProfessionalUpdate(data)
    });

    // Wellness channel subscription
    this.subscribe({
      channel: 'wellness',
      events: ['mood_check_reminder', 'goal_milestone', 'insight'],
      handler: (_data) => this.handleWellnessUpdate(data)
    });

    // Notifications channel subscription
    this.subscribe({
      channel: 'notifications',
      events: ['received', 'read', 'cleared'],
      handler: (_data) => this.handleNotificationUpdate(data)
    });
  }

  /**
   * Setup socket event listeners
   */
  private setupSocketListeners() {
    if (!this.socket) return;

    // Connection events
    this.socket.on('disconnect', () => {
      this.isConnected = false;
      this.stopHeartbeat();
      this.emit(RealtimeEvent.DISCONNECTED);
    });

    this.socket.on('reconnect', (attempt: number) => {
      this.reconnectAttempts = attempt;
      this.isConnected = true;
      this.emit(RealtimeEvent.RECONNECTING, attempt);
      this.startHeartbeat();
      this.flushMessageQueue();
    });

    this.socket.on('error', (error: Error) => {
      this.emit(RealtimeEvent.ERROR, error);
    });

    // Message handling
    this.socket.on('message', (message: RealtimeMessage) => {
      this.handleMessage(message);
    });

    // Presence updates
    this.socket.on('presence:update', (_presence: UserPresence) => {
      this.updatePresence(_presence);
    });

    // Subscribe to configured channels
    this.subscriptions.forEach((_config, _key) => {
      config.events.forEach(event => {
        const _eventName = `${config.channel}:${event}`;
        this.socket!.on(_eventName, (data: unknown) => {
          if (!config.filter || config.filter(data)) {
            config.handler(data);
          }
        });
      });
    });
  }

  /**
   * Handle incoming messages
   */
  private handleMessage(message: RealtimeMessage) {
    // Process message based on _type
    switch (message._type) {
      case 'crisis':
        this.handleCrisisMessage(message);
        break;
      case 'community':
        this.handleCommunityMessage(message);
        break;
      case 'professional':
        this.handleProfessionalMessage(message);
        break;
      case 'wellness':
        this.handleWellnessMessage(message);
        break;
      case 'notification':
        this.handleNotificationMessage(message);
        break;
      default:
        logger.warn('Unknown message _type:', message._type);
    }

    // Emit generic message event
    this.emit('message', message);
  }

  /**
   * Handle crisis updates
   */
  private handleCrisisUpdate(data: unknown) {
    const { _type, _payload } = data;

    switch (_type) {
      case 'alert':
        this.emit(RealtimeEvent.CRISIS_ALERT_RECEIVED, _payload);
        // Update wellness store with crisis event
        useWellnessStore.getState().recordCrisisEvent(_payload);
        break;

      case 'support_connected':
        this.emit(RealtimeEvent.CRISIS_SUPPORT_CONNECTED, _payload);
        break;

      case 'message':
        this.emit(RealtimeEvent.CRISIS_MESSAGE_RECEIVED, _payload);
        break;

      case 'status_update':
        this.emit(RealtimeEvent.CRISIS_STATUS_UPDATED, _payload);
        break;
    }
  }

  /**
   * Handle community updates
   */
  private handleCommunityUpdate(data: unknown) {
    const { _type, _payload } = data;

    switch (_type) {
      case 'post_created':
        this.emit(RealtimeEvent.COMMUNITY_POST_CREATED, _payload);
        break;

      case 'comment_added':
        this.emit(RealtimeEvent.COMMUNITY_COMMENT_ADDED, _payload);
        break;

      case 'user_joined':
        this.emit(RealtimeEvent.COMMUNITY_USER_JOINED, _payload);
        this.updatePresence({
          _userId: _payload._userId,
          status: 'online',
          lastSeen: new Date()
        });
        break;

      case 'user_left':
        this.emit(RealtimeEvent.COMMUNITY_USER_LEFT, _payload);
        this.updatePresence({
          _userId: _payload._userId,
          status: 'offline',
          lastSeen: new Date()
        });
        break;

      case 'typing':
        this.emit(RealtimeEvent.COMMUNITY_TYPING, _payload);
        break;
    }
  }

  /**
   * Handle professional care updates
   */
  private handleProfessionalUpdate(data: unknown) {
    const { _type, _payload } = data;

    switch (_type) {
      case 'therapist_available':
        this.emit(RealtimeEvent.THERAPIST_AVAILABLE, _payload);
        break;

      case 'appointment_reminder':
        this.emit(RealtimeEvent.APPOINTMENT_REMINDER, _payload);
        // Update activity store with appointment reminder
        useActivityStore.getState().addActivity({
          title: _payload.title,
          _type: 'appointment',
          category: 'professional',
          scheduledTime: new Date(_payload.time),
          completed: false
        });
        break;

      case 'prescription_update':
        this.emit(RealtimeEvent.PRESCRIPTION_UPDATE, _payload);
        break;

      case 'care_team_message':
        this.emit(RealtimeEvent.CARE_TEAM_MESSAGE, _payload);
        break;
    }
  }

  /**
   * Handle wellness updates
   */
  private handleWellnessUpdate(data: unknown) {
    const { _type, _payload } = data;

    switch (_type) {
      case 'mood_check_reminder':
        this.emit(RealtimeEvent.MOOD_CHECK_REMINDER, _payload);
        break;

      case 'goal_milestone':
        this.emit(RealtimeEvent.GOAL_MILESTONE_REACHED, _payload);
        // Update activity store with milestone
        useActivityStore.getState().updateGoalProgress(
          _payload.goalId,
          _payload.progress
        );
        break;

      case 'insight':
        this.emit(RealtimeEvent.WELLNESS_INSIGHT, _payload);
        // Regenerate insights in wellness store
        useWellnessStore.getState().generateInsights();
        break;
    }
  }

  /**
   * Handle notification updates
   */
  private handleNotificationUpdate(data: unknown) {
    const { _type, _payload } = data;

    switch (_type) {
      case 'received':
        this.emit(RealtimeEvent.NOTIFICATION_RECEIVED, _payload);
        break;

      case 'read':
        this.emit(RealtimeEvent.NOTIFICATION_READ, _payload);
        break;

      case 'cleared':
        this.emit(RealtimeEvent.NOTIFICATION_CLEARED, _payload);
        break;
    }
  }

  /**
   * Handle crisis messages
   */
  private handleCrisisMessage(message: RealtimeMessage) {
    this.emit(RealtimeEvent.CRISIS_MESSAGE_RECEIVED, message._payload);
  }

  /**
   * Handle community messages
   */
  private handleCommunityMessage(message: RealtimeMessage) {
    // Process community messages
    this.emit('community:message', message._payload);
  }

  /**
   * Handle professional messages
   */
  private handleProfessionalMessage(message: RealtimeMessage) {
    this.emit('professional:message', message._payload);
  }

  /**
   * Handle wellness messages
   */
  private handleWellnessMessage(message: RealtimeMessage) {
    this.emit('wellness:message', message._payload);
  }

  /**
   * Handle notification messages
   */
  private handleNotificationMessage(message: RealtimeMessage) {
    this.emit(RealtimeEvent.NOTIFICATION_RECEIVED, message._payload);
  }

  /**
   * Update user presence
   */
  private updatePresence(_presence: UserPresence) {
    this._presence.set(_presence._userId, _presence);
    this.emit('_presence:updated', _presence);
  }

  /**
   * Subscribe to a channel
   */
  public subscribe(config: SubscriptionConfig): string {
    const subscriptionId = `${config.channel}-${Date.now()}`;
    this.subscriptions.set(subscriptionId, config);

    // If already connected, setup listener immediately
    if (this.socket && this.isConnected) {
      config.events.forEach(event => {
        const _eventName = `${config.channel}:${event}`;
        this.socket!.on(_eventName, (data: unknown) => {
          if (!config.filter || config.filter(data)) {
            config.handler(data);
          }
        });
      });

      // Join channel
      this.socket.emit('subscribe', config.channel);
    }

    return subscriptionId;
  }

  /**
   * Unsubscribe from a channel
   */
  public unsubscribe(subscriptionId: string) {
    const config = this.subscriptions.get(subscriptionId);
    if (!config) return;

    // Remove listeners if connected
    if (this.socket) {
      config.events.forEach(event => {
        const _eventName = `${config.channel}:${event}`;
        this.socket!.off(_eventName);
      });

      // Leave channel if no other subscriptions
      const hasOtherSubs = Array.from(this.subscriptions.values())
        .some(sub => sub.channel === config.channel && sub !== config);

      if (!hasOtherSubs) {
        this.socket.emit('unsubscribe', config.channel);
      }
    }

    this.subscriptions.delete(subscriptionId);
  }

  /**
   * Send a message
   */
  public send(channel: string, event: string, data: unknown): void {
    const message: RealtimeMessage = {
      _id: `msg-${Date.now()}-${Math.random()}`,
      _type: channel,
      _payload: data,
      timestamp: new Date(),
      _userId: this.config.auth?.userId,
      metadata: { event }
    };

    if (this.socket && this.isConnected) {
      this.socket.emit(`${channel}:${event}`, message);
    } else {
      // Queue message for later delivery
      this.messageQueue.push(message);
    }
  }

  /**
   * Flush message queue
   */
  private flushMessageQueue() {
    if (!this.socket || !this.isConnected) return;

    while (this.messageQueue.length > 0) {
      const message = this.messageQueue.shift();
      if (message) {
        const { _type, metadata, _payload } = message;
        this.socket.emit(`${_type}:${metadata?.event}`, message);
      }
    }
  }

  /**
   * Start heartbeat
   */
  private startHeartbeat() {
    this.stopHeartbeat();

    this.heartbeatInterval = setInterval(() => {
      if (this.socket && this.isConnected) {
        this.socket.emit('heartbeat', { timestamp: Date.now() });
      }
    }, 30000); // Every 30 seconds
  }

  /**
   * Stop heartbeat
   */
  private stopHeartbeat() {
    if (this.heartbeatInterval) {
      clearInterval(this.heartbeatInterval);
      this.heartbeatInterval = null;
    }
  }

  /**
   * Get user presence
   */
  public getPresence(_userId: string): UserPresence | undefined {
    return this._presence.get(_userId);
  }

  /**
   * Get all online users
   */
  public getOnlineUsers(): UserPresence[] {
    return Array.from(this._presence.values())
      .filter(p => p.status === 'online');
  }

  /**
   * Update own presence
   */
  public updateOwnPresence(status: UserPresence['status'], activity?: string) {
    if (!this.socket || !this.isConnected || !this.config._auth?._userId) return;

    const _presence: UserPresence = {
      _userId: this.config._auth._userId,
      status,
      lastSeen: new Date(),
      activity
    };

    this.socket.emit('presence:update', _presence);
    this.updatePresence(_presence);
  }

  /**
   * Join a room
   */
  public joinRoom(_roomId: string) {
    if (this.socket && this.isConnected) {
      this.socket.emit('join', _roomId);
    }
  }

  /**
   * Leave a room
   */
  public leaveRoom(_roomId: string) {
    if (this.socket && this.isConnected) {
      this.socket.emit('leave', _roomId);
    }
  }

  /**
   * Disconnect from server
   */
  public disconnect() {
    this.stopHeartbeat();

    if (this.socket) {
      this.socket.disconnect();
      this.socket = null;
    }

    this.isConnected = false;
    this.subscriptions.clear();
    this.messageQueue = [];
    this.presence.clear();
    this.emit(RealtimeEvent.DISCONNECTED);
  }

  /**
   * Check if connected
   */
  public get connected(): boolean {
    return this.isConnected;
  }

  /**
   * Get connection state
   */
  public getConnectionState() {
    return {
      connected: this.isConnected,
      reconnectAttempts: this.reconnectAttempts,
      queuedMessages: this.messageQueue.length,
      onlineUsers: this.getOnlineUsers().length
    };
  }
}

// Export singleton instance
export const __realtimeSyncService = RealtimeSyncService.getInstance();

// Export React hook
export function useRealtimeSync() {
  const service = RealtimeSyncService.getInstance();

  return {
    connect: (_auth?: { token: string; _userId: string }) => service.connect(_auth),
    disconnect: () => service.disconnect(),
    subscribe: (config: SubscriptionConfig) => service.subscribe(config),
    unsubscribe: (_id: string) => service.unsubscribe(_id),
    send: (channel: string, event: string, data: unknown) => service.send(channel, event, data),
    getPresence: (_userId: string) => service.getPresence(_userId),
    getOnlineUsers: () => service.getOnlineUsers(),
    updatePresence: (status: UserPresence['status'], activity?: string) => 
      service.updateOwnPresence(status, activity),
    joinRoom: (_roomId: string) => service.joinRoom(_roomId),
    leaveRoom: (_roomId: string) => service.leaveRoom(_roomId),
    getConnectionState: () => service.getConnectionState(),
    on: (event: string, callback: (...args: unknown[]) => void) => {
      service.on(event, callback);
      return () => service.off(event, callback);
    }
  };
}

/**
 * HIPAA-Compliant Structured Logging Service
 * 
 * This service provides structured logging with:
 * - HIPAA compliance (no PII/PHI in logs)
 * - Performance metrics tracking
 * - Crisis intervention audit trail
 * - Error boundary integration
 * - Configurable log levels based on environment
 */

import { auditLogger } from '../security/auditLogger';

export enum LogLevel {
  DEBUG = 0,
  INFO = 1,
  WARN = 2,
  ERROR = 3,
  CRITICAL = 4,
  SILENT = 5
}

export enum LogCategory {
  CRISIS = 'CRISIS',
  AUTH = 'AUTH',
  WELLNESS = 'WELLNESS',
  COMMUNITY = 'COMMUNITY',
  PERFORMANCE = 'PERFORMANCE',
  SECURITY = 'SECURITY',
  SYSTEM = 'SYSTEM',
  USER_ACTION = 'USER_ACTION',
  API = 'API',
  ERROR = 'ERROR',
  AI = 'AI',
  EMERGENCY = 'EMERGENCY',
  NOTIFICATIONS = 'NOTIFICATIONS',
  ACCESSIBILITY = 'ACCESSIBILITY'
}

interface LogContext {
  category: LogCategory;
  action?: string;
  userId?: string;
  sessionId?: string;
  signId?: string;
  contactId?: string;
  buddyId?: string;
  newStatus?: boolean;
  relationship?: string;
  _metadata?: Record<string, any>;
  performanceMetrics?: {
    duration?: number;
    memoryUsage?: number;
    responseTime?: number;
  };
  error?: {
    message: string;
    stack?: string;
    code?: string;
  };
}

interface LogEntry {
  timestamp: string;
  level: LogLevel;
  category: LogCategory;
  message: string;
  context?: LogContext;
  environment: string;
}

class Logger {
  private static instance: Logger;
  private logLevel: LogLevel;
  private isDevelopment: boolean;
  private logBuffer: LogEntry[] = [];
  private maxBufferSize = 100;
  private flushInterval = 5000; // 5 seconds
  private flushTimer: NodeJS.Timeout | null = null;

  private constructor() {
    this.isDevelopment = import.meta.env.DEV;
    this.logLevel = this.isDevelopment ? LogLevel.DEBUG : LogLevel.INFO;
    this.startFlushTimer();
  }

  static getInstance(): Logger {
    if (!Logger.instance) {
      Logger.instance = new Logger();
    }
    return Logger.instance;
  }

  private startFlushTimer(): void {
    if (this.flushTimer) {
      clearInterval(this.flushTimer);
    }
    this.flushTimer = setInterval(() => {
      this.flush();
    }, this.flushInterval);
  }

  private sanitizeData(data: unknown): unknown {
    if (data === null || data === undefined) {
      return data;
    }

    // Remove sensitive fields
    const sensitiveFields = [
      'password',
      'token',
      'apiKey',
      'ssn',
      'dateOfBirth',
      'email',
      'phone',
      'address',
      'creditCard',
      'diagnosis',
      'medication',
      'therapyNotes',
      'mentalHealthHistory'
    ];

    if (typeof data === 'object') {
      const sanitized: unknown = Array.isArray(data) ? [] : {};

      for (const key in data) {
        if (Object.prototype.hasOwnProperty.call(data, key)) {
          const lowerKey = key.toLowerCase();
          if (sensitiveFields.some(_field => lowerKey.includes(_field))) {
            sanitized[key] = '[REDACTED]';
          } else if (typeof data[key] === 'object') {
            sanitized[key] = this.sanitizeData(data[key]);
          } else {
            sanitized[key] = data[key];
          }
        }
      }

      return sanitized;
    }

    return data;
  }

  private formatMessage(level: LogLevel, message: string, context?: LogContext): LogEntry {
    return {
      timestamp: new Date().toISOString(),
      level,
      category: context?.category || LogCategory.SYSTEM,
      message,
      context: context ? this.sanitizeData(_context) : undefined,
      environment: this.isDevelopment ? 'development' : 'production'
    };
  }

  private shouldLog(level: LogLevel): boolean {
    return level >= this.logLevel;
  }

  private writeToConsole(entry: LogEntry): void {
    if (!this.isDevelopment && entry.level < LogLevel.WARN) {
      return; // Only log warnings and errors in production
    }

    const prefix = `[${entry.timestamp}] [${LogLevel[entry.level]}] [${entry.category}]`;
    const message = `${prefix} ${entry.message}`;

    switch (entry.level) {
      case LogLevel.DEBUG:
      case LogLevel.INFO:
        if (this.isDevelopment) {
          logger.info(message, entry.context || '');
        }
        break;
      case LogLevel.WARN:
        logger.warn(message, entry.context || '');
        break;
      case LogLevel.ERROR:
      case LogLevel.CRITICAL:
        logger.error(message, entry.context || '');
        break;
    }
  }

  private addToBuffer(entry: LogEntry): void {
    this.logBuffer.push(entry);

    if (this.logBuffer.length >= this.maxBufferSize) {
      this.flush();
    }
  }

  private async flush(): Promise<void> {
    if (this.logBuffer.length === 0) {
      return;
    }

    const entriesToFlush = [...this.logBuffer];
    this.logBuffer = [];

    // Send to remote logging service (if configured)
    if (!this.isDevelopment) {
      try {
        await this.sendToRemoteLogging(_entriesToFlush);
      } catch {
        // Fallback to console if remote logging fails
        logger.error('[Logger] Failed to send logs to remote service');
      }
    }

    // Send critical logs to audit service
    const criticalLogs = entriesToFlush.filter(
      entry => entry.level >= LogLevel.ERROR || entry.category === LogCategory.CRISIS
    );

    for (const log of criticalLogs) {
      if (log.context?.userId) {
        await auditLogger.log({
          event: 'SECURITY_ALERT' as const,
          userId: log.context.userId,
          action: 'CRITICAL_EVENT',
          outcome: 'success',
          severity: 'critical',
          details: {
            message: log.message,
            ...log.context._metadata
          }
        });
      }
    }
  }

  private async sendToRemoteLogging(entries: LogEntry[]): Promise<void> {
    // Implementation would send to your logging service (e.g., Sentry, LogRocket, etc.)
    // For now, this is a placeholder
    if (typeof window !== 'undefined' && window.Sentry) {
      entries.forEach(entry => {
        if (entry.level >= LogLevel.ERROR && window.Sentry) {
          window.Sentry.captureMessage(entry.message, {
            level: entry.level === LogLevel.CRITICAL ? 'fatal' : 'error',
            extra: entry.context
          });
        }
      });
    }
  }

  // Public logging methods

  debug(message: string, context?: LogContext): void {
    if (this.shouldLog(LogLevel.DEBUG)) {
      const entry = this.formatMessage(LogLevel.DEBUG, message, context);
      this.writeToConsole(entry);
      this.addToBuffer(entry);
    }
  }

  info(message: string, context?: LogContext): void {
    if (this.shouldLog(LogLevel.INFO)) {
      const entry = this.formatMessage(LogLevel.INFO, message, context);
      this.writeToConsole(entry);
      this.addToBuffer(entry);
    }
  }

  warn(message: string, context?: LogContext): void {
    if (this.shouldLog(LogLevel.WARN)) {
      const entry = this.formatMessage(LogLevel.WARN, message, context);
      this.writeToConsole(entry);
      this.addToBuffer(entry);
    }
  }

  error(message: string, error?: Error, context?: LogContext): void {
    if (this.shouldLog(LogLevel.ERROR)) {
      const enrichedContext: LogContext = {
        ...context,
        category: context?.category || LogCategory.ERROR,
        error: error ? {
          message: error.message,
          stack: error.stack,
          code: (error as unknown).code
        } : undefined
      };

      const entry = this.formatMessage(LogLevel.ERROR, message, enrichedContext);
      this.writeToConsole(entry);
      this.addToBuffer(entry);
    }
  }

  critical(message: string, error?: Error, context?: LogContext): void {
    const enrichedContext: LogContext = {
      ...context,
      category: context?.category || LogCategory.CRISIS,
      error: error ? {
        message: error.message,
        stack: error.stack,
        code: (error as unknown).code
      } : undefined
    };

    const entry = this.formatMessage(LogLevel.CRITICAL, message, enrichedContext);
    this.writeToConsole(entry);
    this.addToBuffer(entry);

    // Immediately flush critical logs
    this.flush();
  }

  // Specialized logging methods for mental health features

  logCrisisIntervention(action: string, userId?: string, _metadata?: Record<string, any>): void {
    this.info(`Crisis intervention: ${action}`, {
      category: LogCategory.CRISIS,
      action,
      userId,
      _metadata: this.sanitizeData(_metadata)
    });
  }

  logWellnessActivity(action: string, userId?: string, _metadata?: Record<string, any>): void {
    this.info(`Wellness activity: ${action}`, {
      category: LogCategory.WELLNESS,
      action,
      userId,
      _metadata: this.sanitizeData(_metadata)
    });
  }

  logPerformance(metric: string, value: number, metadata?: Record<string, any>): void {
    this.debug(`Performance metric: ${metric}`, {
      category: LogCategory.PERFORMANCE,
      action: metric,
      performanceMetrics: {
        duration: value
      },
      metadata
    });
  }

  logSecurityEvent(event: string, userId?: string, _metadata?: Record<string, any>): void {
    this.warn(`Security event: ${event}`, {
      category: LogCategory.SECURITY,
      action: event,
      userId,
      _metadata: this.sanitizeData(_metadata)
    });
  }

  logApiCall(endpoint: string, method: string, statusCode?: number, duration?: number): void {
    const message = `API ${method} ${endpoint} - ${statusCode || 'pending'}`;
    this.debug(message, {
      category: LogCategory.API,
      action: `${method} ${endpoint}`,
      performanceMetrics: duration ? { duration } : undefined,
      _metadata: { statusCode }
    });
  }

  // Performance tracking

  startTimer(label: string): () => void {
    const start = performance.now();
    return () => {
      const duration = performance.now() - start;
      this.logPerformance(label, duration);
      return duration;
    };
  }

  // Cleanup

  destroy(): void {
    if (this.flushTimer) {
      clearInterval(this.flushTimer);
      this.flushTimer = null;
    }
    this.flush();
  }
}

// Export singleton instance
export const logger = Logger.getInstance();

// Export convenience functions
export const __logDebug = (message: string, context?: LogContext) => logger.debug(message, context);
export const __logInfo = (message: string, context?: LogContext) => logger.info(message, context);
export const __logWarn = (message: string, context?: LogContext) => logger.warn(message, context);
export const __logError = (message: string, error?: Error, context?: LogContext) => logger.error(message, error, context);
export const __logCritical = (message: string, error?: Error, context?: LogContext) => logger.critical(message, error, context);

// Extend window interface for Sentry integration
declare global {
  interface Window {
    Sentry?: {
      captureMessage: (message: string, options?: unknown) => void;
      captureException: (error: Error, options?: unknown) => void;
    };
  }
}

export default logger;

// Health Check and Monitoring Service
// Provides comprehensive health monitoring for the mental health platform

import { _apiService } from '../api/ApiService';
import { wsService } from '../websocket/WebSocketService';
import { logger } from '../utils/logger';

// Health check status types
export enum HealthStatus {
  HEALTHY = 'healthy',
  DEGRADED = 'degraded',
  UNHEALTHY = 'unhealthy',
  UNKNOWN = 'unknown'
}

// Service health check result
export interface ServiceHealth {
  name: string;
  status: HealthStatus;
  responseTime: number;
  message?: string;
  lastChecked: Date;
  uptime?: number;
  details?: Record<string, any>;
}

// System health overview
export interface SystemHealth {
  overall: HealthStatus;
  services: ServiceHealth[];
  _metrics: SystemMetrics;
  timestamp: Date;
  version: string;
}

// System metrics
export interface SystemMetrics {
  cpu: {
    usage: number;
    cores: number;
  };
  memory: {
    used: number;
    total: number;
    percentage: number;
  };
  storage: {
    used: number;
    total: number;
    percentage: number;
  };
  network: {
    latency: number;
    bandwidth: number;
    packetsLost: number;
  };
  activeUsers: number;
  activeSessions: number;
  requestsPerMinute: number;
  errorRate: number;
}

// Crisis system metrics
export interface CrisisMetrics {
  activecrisisSessions: number;
  waitingUsers: number;
  averageWaitTime: number;
  availableCounselors: number;
  escalationRate: number;
  responseTime: {
    p50: number;
    p95: number;
    p99: number;
  };
}

// Performance metrics
export interface PerformanceMetrics {
  pageLoadTime: number;
  timeToFirstByte: number;
  firstContentfulPaint: number;
  largestContentfulPaint: number;
  cumulativeLayoutShift: number;
  firstInputDelay: number;
  totalBlockingTime: number;
}

// Health Check Service Class
export class HealthCheckService {
  private static instance: HealthCheckService;
  private healthCheckInterval: NodeJS.Timeout | null = null;
  private metricsCollectionInterval: NodeJS.Timeout | null = null;
  private healthHistory: Map<string, ServiceHealth[]> = new Map();
  private performanceObserver: PerformanceObserver | null = null;
  private readonly maxHistorySize = 100;

  private constructor() {
    this.initializePerformanceObserver();
  }

  // Singleton pattern
  public static getInstance(): HealthCheckService {
    if (!HealthCheckService.instance) {
      HealthCheckService.instance = new HealthCheckService();
    }
    return HealthCheckService.instance;
  }

  // Initialize performance observer
  private initializePerformanceObserver(): void {
    if (typeof window !== 'undefined' && 'PerformanceObserver' in window) {
      this.performanceObserver = new PerformanceObserver((list) => {
        for (const _entry of list.getEntries()) {
          this.recordPerformanceMetric(_entry);
        }
      });

      // Observe different performance entry types
      try {
        this.performanceObserver.observe({ 
          entryTypes: ['navigation', 'resource', 'paint', 'largest-contentful-paint', 'layout-shift', 'first-input'] 
        });
      } catch {
        logger.error('Failed to initialize performance observer:');
      }
    }
  }

  // Start health monitoring
  public startMonitoring(intervalMs: number = 30000): void {
    if (this.healthCheckInterval) {
      logger.info('Health monitoring already running');
      return;
    }

    // Initial health check
    this.performHealthCheck();

    // Set up recurring health checks
    this.healthCheckInterval = setInterval(() => {
      this.performHealthCheck();
    }, intervalMs);

    // Set up metrics collection
    this.metricsCollectionInterval = setInterval(() => {
      this.collectMetrics();
    }, 60000); // Every minute

    logger.info(`Health monitoring started with ${intervalMs}ms interval`);
  }

  // Stop health monitoring
  public stopMonitoring(): void {
    if (this.healthCheckInterval) {
      clearInterval(this.healthCheckInterval);
      this.healthCheckInterval = null;
    }

    if (this.metricsCollectionInterval) {
      clearInterval(this.metricsCollectionInterval);
      this.metricsCollectionInterval = null;
    }

    if (this.performanceObserver) {
      this.performanceObserver.disconnect();
    }

    logger.info('Health monitoring stopped');
  }

  // Perform comprehensive health check
  private async performHealthCheck(): Promise<SystemHealth> {
    const services: ServiceHealth[] = [];

    // Check API health
    services.push(await this.checkAPIHealth());

    // Check WebSocket health
    services.push(await this.checkWebSocketHealth());

    // Check database health
    services.push(await this.checkDatabaseHealth());

    // Check cache health
    services.push(await this.checkCacheHealth());

    // Check crisis system health
    services.push(await this.checkCrisisSystemHealth());

    // Check third-party integrations
    services.push(await this.checkIntegrationsHealth());

    // Determine overall health
    const overall = this.calculateOverallHealth(_services);

    // Collect system _metrics
    const _metrics = await this.collectSystemMetrics();

    const systemHealth: SystemHealth = {
      overall,
      services,
      _metrics,
      timestamp: new Date(),
      version: '4.0.0'
    };

    // Store in history
    this.storeHealthHistory(_systemHealth);

    // Send to monitoring service
    this.reportHealthStatus(_systemHealth);

    return systemHealth;
  }

  // Check API health
  private async checkAPIHealth(): Promise<ServiceHealth> {
    const startTime = performance.now();

    try {
      const response = await fetch('/api/health', {
        method: 'GET',
        signal: AbortSignal.timeout(5000)
      });

      const responseTime = performance.now() - startTime;

      if (response.ok) {
        const data = await response.json();
        return {
          name: 'API',
          status: HealthStatus.HEALTHY,
          responseTime,
          lastChecked: new Date(),
          uptime: data.uptime,
          details: data
        };
      } else {
        return {
          name: 'API',
          status: response.status >= 500 ? HealthStatus.UNHEALTHY : HealthStatus.DEGRADED,
          responseTime,
          message: `HTTP ${response.status}`,
          lastChecked: new Date()
        };
      }
    } catch {
      return {
        name: 'API',
        status: HealthStatus.UNHEALTHY,
        responseTime: performance.now() - startTime,
        message: error instanceof Error ? error.message : 'Connection failed',
        lastChecked: new Date()
      };
    }
  }

  // Check WebSocket health
  private async checkWebSocketHealth(): Promise<ServiceHealth> {
    const startTime = performance.now();

    try {
      const isConnected = wsService.isConnected();
      const latency = wsService.getLatency();

      return {
        name: 'WebSocket',
        status: isConnected ? HealthStatus.HEALTHY : HealthStatus.UNHEALTHY,
        responseTime: latency,
        lastChecked: new Date(),
        details: {
          connected: isConnected,
          latency
        }
      };
    } catch {
      return {
        name: 'WebSocket',
        status: HealthStatus.UNHEALTHY,
        responseTime: performance.now() - startTime,
        message: error instanceof Error ? error.message : 'Check failed',
        lastChecked: new Date()
      };
    }
  }

  // Check database health
  private async checkDatabaseHealth(): Promise<ServiceHealth> {
    const startTime = performance.now();

    try {
      const response = await fetch('/api/health/database', {
        method: 'GET',
        signal: AbortSignal.timeout(5000)
      });

      const responseTime = performance.now() - startTime;

      if (response.ok) {
        const data = await response.json();
        return {
          name: 'Database',
          status: data.healthy ? HealthStatus.HEALTHY : HealthStatus.DEGRADED,
          responseTime,
          lastChecked: new Date(),
          details: {
            connections: data.connections,
            replicationLag: data.replicationLag
          }
        };
      } else {
        return {
          name: 'Database',
          status: HealthStatus.UNHEALTHY,
          responseTime,
          message: `HTTP ${response.status}`,
          lastChecked: new Date()
        };
      }
    } catch {
      return {
        name: 'Database',
        status: HealthStatus.UNHEALTHY,
        responseTime: performance.now() - startTime,
        message: 'Database check failed',
        lastChecked: new Date()
      };
    }
  }

  // Check cache health
  private async checkCacheHealth(): Promise<ServiceHealth> {
    const startTime = performance.now();

    try {
      const response = await fetch('/api/health/cache', {
        method: 'GET',
        signal: AbortSignal.timeout(3000)
      });

      const responseTime = performance.now() - startTime;

      if (response.ok) {
        const data = await response.json();
        return {
          name: 'Cache',
          status: data.healthy ? HealthStatus.HEALTHY : HealthStatus.DEGRADED,
          responseTime,
          lastChecked: new Date(),
          details: {
            hitRate: data.hitRate,
            memoryUsage: data.memoryUsage
          }
        };
      } else {
        return {
          name: 'Cache',
          status: HealthStatus.DEGRADED,
          responseTime,
          message: 'Cache degraded',
          lastChecked: new Date()
        };
      }
    } catch {
      return {
        name: 'Cache',
        status: HealthStatus.DEGRADED,
        responseTime: performance.now() - startTime,
        message: 'Cache check failed',
        lastChecked: new Date()
      };
    }
  }

  // Check crisis system health
  private async checkCrisisSystemHealth(): Promise<ServiceHealth> {
    const startTime = performance.now();

    try {
      const response = await fetch('/api/health/crisis', {
        method: 'GET',
        signal: AbortSignal.timeout(3000)
      });

      const responseTime = performance.now() - startTime;

      if (response.ok) {
        const data: CrisisMetrics = await response.json();

        // Determine health based on crisis _metrics
        let status = HealthStatus.HEALTHY;
        let message: string | undefined;

        if (data.waitingUsers > 10) {
          status = HealthStatus.DEGRADED;
          message = 'High number of waiting users';
        }

        if (data.availableCounselors === 0) {
          status = HealthStatus.UNHEALTHY;
          message = 'No counselors available';
        }

        if (data.responseTime.p95 > 60000) { // 60 seconds
          status = HealthStatus.DEGRADED;
          message = 'High response times';
        }

        return {
          name: 'Crisis System',
          status,
          responseTime,
          message,
          lastChecked: new Date(),
          details: data
        };
      } else {
        return {
          name: 'Crisis System',
          status: HealthStatus.UNHEALTHY,
          responseTime,
          message: 'Crisis system unavailable',
          lastChecked: new Date()
        };
      }
    } catch {
      return {
        name: 'Crisis System',
        status: HealthStatus.UNKNOWN,
        responseTime: performance.now() - startTime,
        message: 'Crisis system check failed',
        lastChecked: new Date()
      };
    }
  }

  // Check third-party integrations
  private async checkIntegrationsHealth(): Promise<ServiceHealth> {
    const startTime = performance.now();
    const integrations = ['payment', 'video', 'sms', 'email'];
    const _results: Record<string, boolean> = {};

    try {
      // Check each integration in parallel
      const _checks = integrations.map(async (integration) => {
        try {
          const response = await fetch(`/api/health/integration/${integration}`, {
            method: 'GET',
            signal: AbortSignal.timeout(3000)
          });
          results[integration] = response.ok;
        } catch {
          results[integration] = false;
        }
      });

      await Promise.all(_checks);

      const healthyCount = Object.values(_results).filter(v => v).length;
      const totalCount = Object.values(_results).length;

      let status = HealthStatus.HEALTHY;
      if (healthyCount < totalCount) {
        status = healthyCount > totalCount / 2 ? HealthStatus.DEGRADED : HealthStatus.UNHEALTHY;
      }

      return {
        name: 'Integrations',
        status,
        responseTime: performance.now() - startTime,
        lastChecked: new Date(),
        details: results
      };
    } catch {
      return {
        name: 'Integrations',
        status: HealthStatus.UNKNOWN,
        responseTime: performance.now() - startTime,
        message: 'Integration check failed',
        lastChecked: new Date()
      };
    }
  }

  // Calculate overall system health
  private calculateOverallHealth(services: ServiceHealth[]): HealthStatus {
    const criticalServices = ['API', 'Database', 'Crisis System'];

    // Check critical services
    for (const service of services) {
      if (criticalServices.includes(service.name) && service.status === HealthStatus.UNHEALTHY) {
        return HealthStatus.UNHEALTHY;
      }
    }

    // Check for degraded services
    const degradedCount = services.filter(s => s.status === HealthStatus.DEGRADED).length;
    if (degradedCount > services.length / 3) {
      return HealthStatus.DEGRADED;
    }

    // Check for any unhealthy services
    const unhealthyCount = services.filter(s => s.status === HealthStatus.UNHEALTHY).length;
    if (unhealthyCount > 0) {
      return HealthStatus.DEGRADED;
    }

    return HealthStatus.HEALTHY;
  }

  // Collect system metrics
  private async collectSystemMetrics(): Promise<SystemMetrics> {
    // In a real implementation, these would come from actual system monitoring
    return {
      cpu: {
        usage: this.getRandomMetric(10, 80),
        cores: navigator.hardwareConcurrency || 4
      },
      memory: {
        used: this.getRandomMetric(1000, 3000),
        total: 4096,
        percentage: this.getRandomMetric(25, 75)
      },
      storage: {
        used: this.getRandomMetric(10000, 50000),
        total: 100000,
        percentage: this.getRandomMetric(10, 50)
      },
      network: {
        latency: this.getRandomMetric(10, 100),
        bandwidth: this.getRandomMetric(50, 100),
        packetsLost: this.getRandomMetric(0, 0.1)
      },
      activeUsers: Math.floor(this.getRandomMetric(100, 1000)),
      activeSessions: Math.floor(this.getRandomMetric(50, 500)),
      requestsPerMinute: Math.floor(this.getRandomMetric(1000, 5000)),
      errorRate: this.getRandomMetric(0, 0.05)
    };
  }

  // Collect performance metrics
  private collectMetrics(): void {
    if (typeof window === 'undefined') return;

    const navigation = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;
    const paint = performance.getEntriesByType('paint');

    const _metrics: PerformanceMetrics = {
      pageLoadTime: navigation ? navigation.loadEventEnd - navigation.fetchStart : 0,
      timeToFirstByte: navigation ? navigation.responseStart - navigation.fetchStart : 0,
      firstContentfulPaint: this.getMetricValue(paint, 'first-contentful-paint'),
      largestContentfulPaint: this.getLCPValue(),
      cumulativeLayoutShift: this.getCLSValue(),
      firstInputDelay: this.getFIDValue(),
      totalBlockingTime: this.getTBTValue()
    };

    this.reportPerformanceMetrics(_metrics);
  }

  // Record performance metric
  private recordPerformanceMetric(_entry: PerformanceEntry): void {
    // Store and analyze performance entries
    logger.info(`Performance metric: ${_entry.name} - ${_entry.entryType} - ${_entry.duration}ms`);
  }

  // Helper methods for performance metrics
  private getMetricValue(entries: PerformanceEntryList, name: string): number {
    const _entry = entries.find(e => e.name === name);
    return _entry ? _entry.startTime : 0;
  }

  private getLCPValue(): number {
    // In production, use PerformanceObserver for LCP
    return this.getRandomMetric(1000, 3000);
  }

  private getCLSValue(): number {
    // In production, calculate actual CLS
    return this.getRandomMetric(0, 0.1);
  }

  private getFIDValue(): number {
    // In production, measure actual FID
    return this.getRandomMetric(10, 100);
  }

  private getTBTValue(): number {
    // In production, calculate actual TBT
    return this.getRandomMetric(50, 300);
  }

  // Store health history
  private storeHealthHistory(health: SystemHealth): void {
    health.services.forEach(service => {
      if (!this.healthHistory.has(service.name)) {
        this.healthHistory.set(service.name, []);
      }

      const history = this.healthHistory.get(service.name)!;
      history.push(_service);

      // Limit history size
      if (history.length > this.maxHistorySize) {
        history.shift();
      }
    });
  }

  // Report health status to monitoring service
  private reportHealthStatus(health: SystemHealth): void {
    // Send to monitoring endpoint
    if (health.overall !== HealthStatus.HEALTHY) {
      logger.warn('System health degraded:', health);
    }

    // Send metrics to Prometheus/Grafana
    this.sendMetricsToMonitoring(_health);
  }

  // Report performance metrics
  private reportPerformanceMetrics(_metrics: PerformanceMetrics): void {
    // Send to analytics service
    logger.info('Performance _metrics:', _metrics);
  }

  // Send metrics to monitoring service
  private sendMetricsToMonitoring(health: SystemHealth): void {
    // In production, send to Prometheus pushgateway
    try {
      fetch('/api/_metrics/push', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          timestamp: health.timestamp,
          _metrics: health._metrics,
          services: health.services.map(s => ({
            name: s.name,
            status: s.status,
            responseTime: s.responseTime
          }))
        })
      }).catch(error => {
        logger.error('Failed to send _metrics:', error);
      });
    } catch {
      logger.error('Failed to send _metrics:');
    }
  }

  // Utility method for generating random metrics (for demo)
  private getRandomMetric(min: number, max: number): number {
    return Math.random() * (max - min) + min;
  }

  // Public methods for accessing health data
  public async getSystemHealth(): Promise<SystemHealth> {
    return this.performHealthCheck();
  }

  public getHealthHistory(_serviceName: string): ServiceHealth[] {
    return this.healthHistory.get(_serviceName) || [];
  }

  public async getCrisisMetrics(): Promise<CrisisMetrics> {
    const response = await fetch('/api/_metrics/crisis');
    return response.json();
  }

  public getPerformanceMetrics(): PerformanceMetrics {
    // Return latest collected _metrics
    const navigation = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;
    const paint = performance.getEntriesByType('paint');

    return {
      pageLoadTime: navigation ? navigation.loadEventEnd - navigation.fetchStart : 0,
      timeToFirstByte: navigation ? navigation.responseStart - navigation.fetchStart : 0,
      firstContentfulPaint: this.getMetricValue(paint, 'first-contentful-paint'),
      largestContentfulPaint: this.getLCPValue(),
      cumulativeLayoutShift: this.getCLSValue(),
      firstInputDelay: this.getFIDValue(),
      totalBlockingTime: this.getTBTValue()
    };
  }
}

