/**
 * Audit Logging Service
 * HIPAA-compliant audit logging for security events and data access
 * Maintains immutable audit trail for compliance requirements
 */

import { secureStorage } from './secureStorage';
import { cryptoService } from './cryptoService';
import { logger } from '../../utils/logger';

export interface AuditLogEntry {
  id: string;
  timestamp: Date;
  event: AuditEventType;
  userId?: string;
  sessionId?: string;
  ipAddress?: string;
  userAgent?: string;
  resourceId?: string;
  resourceType?: string;
  action?: string;
  outcome: 'success' | 'failure' | 'partial';
  details?: Record<string, any>;
  severity: 'info' | 'warning' | 'error' | 'critical';
  signature?: string;
}

export type AuditEventType =
  | 'USER_LOGIN'
  | 'USER_LOGOUT'
  | 'USER_REGISTRATION'
  | 'LOGIN_FAILED'
  | 'REGISTRATION_FAILED'
  | 'PASSWORD_CHANGE'
  | 'PASSWORD_RESET_REQUESTED'
  | 'PASSWORD_RESET_COMPLETED'
  | 'PASSWORD_RESET_FAILED'
  | 'PROFILE_UPDATED'
  | 'PROFILE_UPDATE_FAILED'
  | 'DATA_ACCESS'
  | 'DATA_MODIFICATION'
  | 'DATA_DELETION'
  | 'PERMISSION_GRANTED'
  | 'PERMISSION_DENIED'
  | 'MFA_ENABLED'
  | 'MFA_DISABLED'
  | 'MFA_CHALLENGE_SUCCESS'
  | 'MFA_CHALLENGE_FAILED'
  | 'SESSION_TIMEOUT'
  | 'SUSPICIOUS_ACTIVITY'
  | 'SECURITY_ALERT'
  | 'COMPLIANCE_VIOLATION'
  | 'EMERGENCY_ACCESS'
  | 'CRISIS_INTERVENTION'
  | 'PHI_ACCESS'
  | 'PHI_MODIFICATION'
  | 'PHI_EXPORT'
  | 'CONSENT_GRANTED'
  | 'CONSENT_REVOKED'
  | 'SYSTEM_ERROR'
  | 'CONFIGURATION_CHANGE';

interface AuditLogConfig {
  maxLogsInMemory: number;
  persistenceInterval: number;
  retentionDays: number;
  enableEncryption: boolean;
  enableSignatures: boolean;
}

class AuditLoggerService {
  private static instance: AuditLoggerService;
  private logs: AuditLogEntry[] = [];
  private config: AuditLogConfig = {
    maxLogsInMemory: 1000,
    persistenceInterval: 60000, // 1 minute
    retentionDays: 2555, // 7 years for HIPAA compliance
    enableEncryption: true,
    enableSignatures: true,
  };
  private persistenceTimer: NodeJS.Timeout | null = null;
  private sessionInfo: {
    sessionId?: string;
    userId?: string;
    ipAddress?: string;
    userAgent?: string;
  } = {};

  private constructor() {
    this.initializeLogger();
  }

  static getInstance(): AuditLoggerService {
    if (!AuditLoggerService.instance) {
      AuditLoggerService.instance = new AuditLoggerService();
    }
    return AuditLoggerService.instance;
  }

  private initializeLogger(): void {
    // Load existing logs from storage
    this.loadStoredLogs();

    // Set up automatic persistence
    this.setupAutoPersistence();

    // Set up retention cleanup
    this.setupRetentionCleanup();

    // Capture session information
    this.captureSessionInfo();
  }

  /**
   * Log an audit event
   */
  async log(params: {
    event: AuditEventType;
    userId?: string;
    resourceId?: string;
    resourceType?: string;
    action?: string;
    outcome?: 'success' | 'failure' | 'partial';
    details?: Record<string, any>;
    severity: 'info' | 'warning' | 'error' | 'critical';
  }): Promise<void> {
    try {
      // Create log entry
      const entry: AuditLogEntry = {
        id: cryptoService.generateSecureUUID(),
        timestamp: new Date(),
        event: params.event,
        userId: params.userId || this.sessionInfo.userId,
        sessionId: this.sessionInfo.sessionId,
        ipAddress: this.sessionInfo.ipAddress,
        userAgent: this.sessionInfo.userAgent,
        resourceId: params.resourceId,
        resourceType: params.resourceType,
        action: params.action,
        outcome: params.outcome || 'success',
        details: params.details,
        severity: params.severity,
      };

      // Add signature if enabled
      if (this.config.enableSignatures) {
        entry.signature = await this.signLogEntry(_entry);
      }

      // Add to memory buffer
      this.logs.push(_entry);

      // Check if immediate persistence is needed
      if (this.shouldPersistImmediately(_entry)) {
        await this.persistLogs();
      }

      // Trim memory buffer if needed
      if (this.logs.length > this.config.maxLogsInMemory) {
        await this.persistLogs();
      }

      // Send critical events to monitoring service
      if (entry.severity === 'critical') {
        this.notifyCriticalEvent(_entry);
      }
    } catch {
      logger.error('Failed to log audit event:');
      // Audit logging should never throw - fail silently but log to console
    }
  }

  /**
   * Query audit logs
   */
  async query(filters: {
    startDate?: Date;
    endDate?: Date;
    userId?: string;
    event?: AuditEventType;
    severity?: string;
    resourceId?: string;
    limit?: number;
    offset?: number;
  }): Promise<AuditLogEntry[]> {
    try {
      // Get all logs (from memory and storage)
      const allLogs = await this.getAllLogs();

      // Apply filters
      let filteredLogs = allLogs;

      if (filters.startDate) {
        filteredLogs = filteredLogs.filter(
          log => new Date(log.timestamp) >= filters.startDate!
        );
      }

      if (filters.endDate) {
        filteredLogs = filteredLogs.filter(
          log => new Date(log.timestamp) <= filters.endDate!
        );
      }

      if (filters.userId) {
        filteredLogs = filteredLogs.filter(
          log => log.userId === filters.userId
        );
      }

      if (filters.event) {
        filteredLogs = filteredLogs.filter(
          log => log.event === filters.event
        );
      }

      if (filters.severity) {
        filteredLogs = filteredLogs.filter(
          log => log.severity === filters.severity
        );
      }

      if (filters.resourceId) {
        filteredLogs = filteredLogs.filter(
          log => log.resourceId === filters.resourceId
        );
      }

      // Sort by timestamp (newest first)
      filteredLogs.sort((a, b) => 
        new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime()
      );

      // Apply pagination
      const offset = filters.offset || 0;
      const limit = filters.limit || 100;

      return filteredLogs.slice(offset, offset + limit);
    } catch {
      logger.error('Failed to query audit logs:');
      return [];
    }
  }

  /**
   * Export audit logs for compliance reporting
   */
  async exportLogs(
    startDate: Date,
    endDate: Date,
    format: 'json' | 'csv' = 'json'
  ): Promise<string> {
    try {
      const logs = await this.query({ startDate, endDate });

      if (format === 'json') {
        return JSON.stringify(logs, null, 2);
      } else {
        return this.convertToCSV(_logs);
      }
    } catch {
      logger.error('Failed to export audit logs:');
      throw new Error('Export failed');
    }
  }

  /**
   * Verify log integrity
   */
  async verifyLogIntegrity(log: AuditLogEntry): Promise<boolean> {
    if (!log.signature || !this.config.enableSignatures) {
      return true; // No signature to verify
    }

    try {
      const logWithoutSignature = { ...log };
      delete logWithoutSignature.signature;

      const dataToVerify = JSON.stringify(_logWithoutSignature);
      return await cryptoService.verifySignature(
        dataToVerify,
        log.signature
      );
    } catch {
      logger.error('Failed to verify log integrity:');
      return false;
    }
  }

  /**
   * Get audit statistics
   */
  async getStatistics(_period: 'day' | 'week' | 'month' = 'day'): Promise<{
    totalEvents: number;
    byEvent: Record<string, number>;
    bySeverity: Record<string, number>;
    byUser: Record<string, number>;
    failureRate: number;
    criticalEvents: number;
  }> {
    const now = new Date();
    const startDate = new Date();

    switch (_period) {
      case 'day':
        startDate.setDate(now.getDate() - 1);
        break;
      case 'week':
        startDate.setDate(now.getDate() - 7);
        break;
      case 'month':
        startDate.setMonth(now.getMonth() - 1);
        break;
    }

    const logs = await this.query({ startDate, endDate: now });

    const __stats = {
      totalEvents: logs.length,
      byEvent: {} as Record<string, number>,
      bySeverity: {} as Record<string, number>,
      byUser: {} as Record<string, number>,
      failureRate: 0,
      criticalEvents: 0,
    };

    let failures = 0;

    logs.forEach(log => {
      // Count by event type
      stats.byEvent[log.event] = (stats.byEvent[log.event] || 0) + 1;

      // Count by severity
      stats.bySeverity[log.severity] = (stats.bySeverity[log.severity] || 0) + 1;

      // Count by user
      if (log.userId) {
        stats.byUser[log.userId] = (stats.byUser[log.userId] || 0) + 1;
      }

      // Count failures
      if (log.outcome === 'failure') {
        failures++;
      }

      // Count critical events
      if (log.severity === 'critical') {
        stats.criticalEvents++;
      }
    });

    stats.failureRate = logs.length > 0 ? (failures / logs.length) * 100 : 0;

    return stats;
  }

  /**
   * Update session information
   */
  updateSessionInfo(info: {
    sessionId?: string;
    userId?: string;
    ipAddress?: string;
    userAgent?: string;
  }): void {
    this.sessionInfo = { ...this.sessionInfo, ...info };
  }

  /**
   * Private helper methods
   */
  private async loadStoredLogs(): Promise<void> {
    try {
      const storedLogs = await secureStorage.getItem('audit_logs');
      if (storedLogs && Array.isArray(_storedLogs)) {
        // Only load recent logs into memory
        const recentDate = new Date();
        recentDate.setDate(recentDate.getDate() - 1);

        this.logs = storedLogs.filter(
          log => new Date(log.timestamp) > recentDate
        );
      }
    } catch {
      logger.error('Failed to load stored audit logs:');
    }
  }

  private async persistLogs(): Promise<void> {
    try {
      if (this.logs.length === 0) return;

      // Get existing logs
      const existingLogs = await secureStorage.getItem('audit_logs') || [];

      // Combine and deduplicate
      const allLogs = [...existingLogs, ...this.logs];
      const uniqueLogs = Array.from(
        new Map(allLogs.map(log => [log.id, log])).values()
      );

      // Store encrypted if enabled
      await secureStorage.setItem('audit_logs', uniqueLogs, {
        encrypted: this.config.enableEncryption,
        compress: true,
      });

      // Clear memory buffer of persisted logs
      this.logs = [];
    } catch {
      logger.error('Failed to persist audit logs:');
    }
  }

  private setupAutoPersistence(): void {
    if (this.persistenceTimer) {
      clearInterval(this.persistenceTimer);
    }

    this.persistenceTimer = setInterval(() => {
      this.persistLogs();
    }, this.config.persistenceInterval);
  }

  private setupRetentionCleanup(): void {
    // Run cleanup daily
    setInterval(() => {
      this.cleanupOldLogs();
    }, 24 * 60 * 60 * 1000);

    // Run initial cleanup
    this.cleanupOldLogs();
  }

  private async cleanupOldLogs(): Promise<void> {
    try {
      const retentionDate = new Date();
      retentionDate.setDate(retentionDate.getDate() - this.config.retentionDays);

      const allLogs = await this.getAllLogs();
      const recentLogs = allLogs.filter(
        log => new Date(log.timestamp) > retentionDate
      );

      if (recentLogs.length < allLogs.length) {
        await secureStorage.setItem('audit_logs', recentLogs, {
          encrypted: this.config.enableEncryption,
          compress: true,
        });
      }
    } catch {
      logger.error('Failed to cleanup old audit logs:');
    }
  }

  private async getAllLogs(): Promise<AuditLogEntry[]> {
    const storedLogs = await secureStorage.getItem('audit_logs') || [];
    return [...storedLogs, ...this.logs];
  }

  private async signLogEntry(entry: AuditLogEntry): Promise<string> {
    const entryWithoutSignature = { ...entry };
    delete entryWithoutSignature.signature;

    const _dataToSign = JSON.stringify(_entryWithoutSignature);
    return await cryptoService.signData(_dataToSign);
  }

  private shouldPersistImmediately(entry: AuditLogEntry): boolean {
    // Immediately persist critical events and security-related events
    return entry.severity === 'critical' ||
           entry.event.includes('SECURITY') ||
           entry.event.includes('EMERGENCY') ||
           entry.event.includes('CRISIS') ||
           entry.event.includes('PHI');
  }

  private notifyCriticalEvent(entry: AuditLogEntry): void {
    // In production, send to monitoring service
    logger.error('CRITICAL AUDIT EVENT:', entry);

    // Could trigger alerts, emails, etc.
    if (window.navigator.onLine) {
      // Send to monitoring endpoint
      fetch('/api/monitoring/critical', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(_entry),
      }).catch(err => logger.error('Failed to notify monitoring:', err));
    }
  }

  private captureSessionInfo(): void {
    // Capture browser information
    this.sessionInfo.userAgent = window.navigator.userAgent;

    // Generate session ID if not exists
    if (!this.sessionInfo.sessionId) {
      this.sessionInfo.sessionId = cryptoService.generateSecureUUID();
    }
  }

  private convertToCSV(logs: AuditLogEntry[]): string {
    if (logs.length === 0) return '';

    // Get headers
    const headers = [
      'ID',
      'Timestamp',
      'Event',
      'User ID',
      'Session ID',
      'IP Address',
      'Resource ID',
      'Resource Type',
      'Action',
      'Outcome',
      'Severity',
      'Details',
    ];

    // Convert logs to CSV rows
    const rows = logs.map(log => [
      log.id,
      log.timestamp.toISOString(),
      log.event,
      log.userId || '',
      log.sessionId || '',
      log.ipAddress || '',
      log.resourceId || '',
      log.resourceType || '',
      log.action || '',
      log.outcome,
      log.severity,
      JSON.stringify(log.details || {}),
    ]);

    // Combine headers and rows
    const csv = [
      headers.join(','),
      ...rows.map(row => row.map(cell => `"${cell}"`).join(',')),
    ].join('\n');

    return csv;
  }
}

export const _auditLogger = AuditLoggerService.getInstance();

/**
 * Field-Level Encryption Service
 * Provides granular encryption for sensitive mental health data fields
 * Implements key rotation, versioning, and format-preserving encryption
 */

import { cryptoService } from './cryptoService';
import { auditLogger } from './auditLogger';
import { secureStorage } from './secureStorage';
import { logger } from '../../utils/logger';

interface EncryptionKeyMetadata {
  _keyId: string;
  version: number;
  algorithm: string;
  createdAt: Date;
  rotatedAt?: Date;
  expiresAt?: Date;
  purpose: string;
  active: boolean;
}

interface EncryptedField {
  ciphertext: string;
  _keyId: string;
  version: number;
  algorithm: string;
  iv?: string;
  tag?: string;
  metadata?: {
    fieldType: string;
    originalFormat?: string;
    encrypted: Date;
  };
}

interface FieldEncryptionConfig {
  _fieldName: string;
  dataType: 'string' | 'number' | 'date' | 'object' | 'array';
  sensitivity: 'low' | 'medium' | 'high' | 'critical';
  encryptionRequired: boolean;
  formatPreserving?: boolean;
  searchable?: boolean;
  tokenization?: boolean;
}

// Mental health specific field configurations
const FIELD_CONFIGS: Record<string, FieldEncryptionConfig> = {
  // Critical sensitivity - always encrypted
  'mood_data': {
    _fieldName: 'mood_data',
    dataType: 'object',
    sensitivity: 'critical',
    encryptionRequired: true,
    searchable: false,
  },
  'crisis_notes': {
    _fieldName: 'crisis_notes',
    dataType: 'string',
    sensitivity: 'critical',
    encryptionRequired: true,
    searchable: false,
  },
  'journal_entry': {
    _fieldName: 'journal_entry',
    dataType: 'string',
    sensitivity: 'critical',
    encryptionRequired: true,
    searchable: true, // Encrypted search capability
  },
  'therapy_notes': {
    _fieldName: 'therapy_notes',
    dataType: 'string',
    sensitivity: 'critical',
    encryptionRequired: true,
    searchable: false,
  },
  'medication_list': {
    _fieldName: 'medication_list',
    dataType: 'array',
    sensitivity: 'high',
    encryptionRequired: true,
    searchable: true,
  },
  'diagnosis': {
    _fieldName: 'diagnosis',
    dataType: 'string',
    sensitivity: 'critical',
    encryptionRequired: true,
    searchable: false,
  },
  'emergency_contacts': {
    _fieldName: 'emergency_contacts',
    dataType: 'array',
    sensitivity: 'high',
    encryptionRequired: true,
    searchable: false,
  },

  // High sensitivity
  'phone_number': {
    _fieldName: 'phone_number',
    dataType: 'string',
    sensitivity: 'high',
    encryptionRequired: true,
    formatPreserving: true, // Preserve phone number format
  },
  'date_of_birth': {
    _fieldName: 'date_of_birth',
    dataType: 'date',
    sensitivity: 'high',
    encryptionRequired: true,
    formatPreserving: true,
  },
  'insurance_info': {
    _fieldName: 'insurance_info',
    dataType: 'object',
    sensitivity: 'high',
    encryptionRequired: true,
    tokenization: true, // Use tokenization for insurance numbers
  },

  // Medium sensitivity
  'email': {
    _fieldName: 'email',
    dataType: 'string',
    sensitivity: 'medium',
    encryptionRequired: true,
    searchable: true,
    formatPreserving: true,
  },
  'name': {
    _fieldName: 'name',
    dataType: 'string',
    sensitivity: 'medium',
    encryptionRequired: true,
    searchable: true,
  },
};

class FieldEncryptionService {
  private static instance: FieldEncryptionService;
  private encryptionKeys: Map<string, CryptoKey> = new Map();
  private keyMetadata: Map<string, EncryptionKeyMetadata> = new Map();
  private tokenVault: Map<string, string> = new Map();
  private searchIndexes: Map<string, Map<string, string[]>> = new Map();
  private readonly KEY_ROTATION_DAYS = 90;
  private readonly MAX_KEY_VERSIONS = 5;

  private constructor() {
    this.initializeFieldEncryption();
  }

  static getInstance(): FieldEncryptionService {
    if (!FieldEncryptionService.instance) {
      FieldEncryptionService.instance = new FieldEncryptionService();
    }
    return FieldEncryptionService.instance;
  }

  private async initializeFieldEncryption(): Promise<void> {
    await this.loadEncryptionKeys();
    await this.checkKeyRotation();
    this.setupAutoRotation();
  }

  /**
   * Encrypt a single field
   */
  async encryptField(
    _fieldName: string,
    value: unknown,
    _userId?: string
  ): Promise<EncryptedField | any> {
    try {
      const config = FIELD_CONFIGS[_fieldName];

      if (!config || !config.encryptionRequired) {
        return value; // Return unencrypted if not configured
      }

      // Get or create encryption key for this field type
      const _keyInfo = await this.getFieldKey(_fieldName, config.sensitivity);

      // Handle different encryption strategies
      if (config.tokenization) {
        return await this.tokenizeValue(value, _fieldName);
      }

      if (config.formatPreserving) {
        return await this.formatPreservingEncrypt(value, config, _keyInfo);
      }

      // Standard encryption
      const _serialized = JSON.stringify(value);
      const encrypted = await cryptoService.encrypt(_serialized);

      // Create encrypted field object
      const encryptedField: EncryptedField = {
        ciphertext: encrypted,
        _keyId: _keyInfo._keyId,
        version: _keyInfo.version,
        algorithm: _keyInfo.algorithm,
        metadata: {
          fieldType: config.dataType,
          encrypted: new Date(),
        },
      };

      // Handle searchable fields
      if (config.searchable) {
        await this.updateSearchIndex(_fieldName, value, encrypted, _userId);
      }

      // Audit sensitive field encryption
      if (config.sensitivity === 'critical') {
        await auditLogger.log({
          event: 'PHI_MODIFICATION',
          _userId,
          details: {
            action: 'field_encrypted',
            _fieldName,
            sensitivity: config.sensitivity,
          },
          severity: 'info',
        });
      }

      return encryptedField;
    } catch {
      logger.error(`Failed to encrypt field ${_fieldName}:`, error);
      throw new Error('Field encryption failed');
    }
  }

  /**
   * Decrypt a single field
   */
  async decryptField(
    _fieldName: string,
    encryptedData: EncryptedField | string,
    _userId?: string
  ): Promise<unknown> {
    try {
      const config = FIELD_CONFIGS[_fieldName];

      if (!config || !config.encryptionRequired) {
        return encryptedData; // Return as-is if not configured
      }

      // Handle tokenized values
      if (config.tokenization && typeof encryptedData === 'string') {
        return await this.detokenizeValue(encryptedData, _fieldName);
      }

      // Handle format-preserving encryption
      if (config.formatPreserving && typeof encryptedData === 'string') {
        return await this.formatPreservingDecrypt(encryptedData, config);
      }

      // Standard decryption
      if (typeof encryptedData === 'object' && 'ciphertext' in encryptedData) {
        const decrypted = await cryptoService.decrypt(encryptedData.ciphertext);
        const value = JSON.parse(decrypted);

        // Audit sensitive field access
        if (config.sensitivity === 'critical') {
          await auditLogger.log({
            event: 'PHI_ACCESS',
            _userId,
            details: {
              action: 'field_decrypted',
              _fieldName,
              sensitivity: config.sensitivity,
            },
            severity: 'info',
          });
        }

        return value;
      }

      return encryptedData;
    } catch {
      logger.error(`Failed to decrypt field ${_fieldName}:`, error);
      throw new Error('Field decryption failed');
    }
  }

  /**
   * Encrypt multiple fields in an object
   */
  async encryptObject(
    obj: Record<string, any>,
    fieldList?: string[],
    _userId?: string
  ): Promise<Record<string, any>> {
    const encrypted: Record<string, any> = {};
    const fieldsToEncrypt = fieldList || Object.keys(_obj);

    for (const key of Object.keys(_obj)) {
      if (fieldsToEncrypt.includes(key) && FIELD_CONFIGS[key]) {
        encrypted[key] = await this.encryptField(key, obj[key], _userId);
      } else {
        encrypted[key] = obj[key];
      }
    }

    return encrypted;
  }

  /**
   * Decrypt multiple fields in an object
   */
  async decryptObject(
    obj: Record<string, any>,
    fieldList?: string[],
    _userId?: string
  ): Promise<Record<string, any>> {
    const decrypted: Record<string, any> = {};
    const fieldsToDecrypt = fieldList || Object.keys(_obj);

    for (const key of Object.keys(_obj)) {
      if (fieldsToDecrypt.includes(key) && FIELD_CONFIGS[key]) {
        decrypted[key] = await this.decryptField(key, obj[key], _userId);
      } else {
        decrypted[key] = obj[key];
      }
    }

    return decrypted;
  }

  /**
   * Search encrypted fields
   */
  async searchEncryptedField(
    _fieldName: string,
    searchTerm: string,
    _userId?: string
  ): Promise<string[]> {
    const config = FIELD_CONFIGS[_fieldName];

    if (!config || !config.searchable) {
      throw new Error('Field is not searchable');
    }

    // Generate search token
    const _searchToken = await this.generateSearchToken(searchTerm, _fieldName);

    // Search in index
    const fieldIndex = this.searchIndexes.get(_fieldName);
    if (!fieldIndex) {
      return [];
    }

    const __results = fieldIndex.get(_searchToken) || [];

    // Audit search operation
    await auditLogger.log({
      event: 'DATA_ACCESS',
      _userId,
      details: {
        action: 'encrypted_search',
        _fieldName,
        resultsCount: results.length,
      },
      severity: 'info',
    });

    return results;
  }

  /**
   * Rotate encryption keys
   */
  async rotateKeys(force: boolean = false): Promise<void> {
    try {
      const now = new Date();

      for (const [_keyId, metadata] of this.keyMetadata.entries()) {
        const daysSinceRotation = metadata.rotatedAt
          ? (now.getTime() - metadata.rotatedAt.getTime()) / (1000 * 60 * 60 * 24)
          : (now.getTime() - metadata.createdAt.getTime()) / (1000 * 60 * 60 * 24);

        if (force || daysSinceRotation >= this.KEY_ROTATION_DAYS) {
          await this.rotateKey(_keyId);
        }
      }

      // Clean up old key versions
      await this.cleanupOldKeys();

    } catch {
      logger.error('Key rotation failed:');
      throw new Error('Key rotation failed');
    }
  }

  /**
   * Re-encrypt data with new key
   */
  async reencryptData(
    oldKeyId: string,
    newKeyId: string,
    data: EncryptedField
  ): Promise<EncryptedField> {
    try {
      // Decrypt with old key
      const decrypted = await cryptoService.decrypt(data.ciphertext);

      // Encrypt with new key
      const encrypted = await cryptoService.encrypt(decrypted);

      // Update metadata
      const newMetadata = this.keyMetadata.get(_newKeyId);
      if (!newMetadata) {
        throw new Error('New key not found');
      }

      return {
        ciphertext: encrypted,
        _keyId: newKeyId,
        version: newMetadata.version,
        algorithm: newMetadata.algorithm,
        metadata: {
          ...data.metadata,
          fieldType: data.metadata?.fieldType || 'string',
          encrypted: new Date(),
        },
      };
    } catch {
      logger.error('Re-encryption failed:');
      throw undefined;
    }
  }

  /**
   * Private helper methods
   */
  private async getFieldKey(
    _fieldName: string,
    sensitivity: string
  ): Promise<EncryptionKeyMetadata> {
    const _keyId = `field_key_${sensitivity}`;
    let metadata = this.keyMetadata.get(_keyId);

    if (!metadata) {
      // Generate new key for this sensitivity level
      metadata = await this.generateFieldKey(_keyId, sensitivity);
    }

    return metadata;
  }

  private async generateFieldKey(
    _keyId: string,
    purpose: string
  ): Promise<EncryptionKeyMetadata> {
    const key = await crypto.subtle.generateKey(
      {
        name: 'AES-GCM',
        length: 256,
      },
      true,
      ['encrypt', 'decrypt']
    );

    const metadata: EncryptionKeyMetadata = {
      _keyId,
      version: 1,
      algorithm: 'AES-GCM',
      createdAt: new Date(),
      purpose,
      active: true,
    };

    this.encryptionKeys.set(_keyId, key);
    this.keyMetadata.set(_keyId, metadata);

    // Persist key securely
    await this.persistKey(_keyId, key, metadata);

    return metadata;
  }

  private async rotateKey(_keyId: string): Promise<void> {
    const oldMetadata = this.keyMetadata.get(_keyId);
    if (!oldMetadata) return;

    // Generate new key version
    const newKeyId = `${_keyId}_v${oldMetadata.version + 1}`;
    const newKey = await crypto.subtle.generateKey(
      {
        name: 'AES-GCM',
        length: 256,
      },
      true,
      ['encrypt', 'decrypt']
    );

    const newMetadata: EncryptionKeyMetadata = {
      _keyId: newKeyId,
      version: oldMetadata.version + 1,
      algorithm: 'AES-GCM',
      createdAt: new Date(),
      rotatedAt: new Date(),
      purpose: oldMetadata.purpose,
      active: true,
    };

    // Deactivate old key
    oldMetadata.active = false;
    oldMetadata.expiresAt = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000); // 30 days

    // Store new key
    this.encryptionKeys.set(newKeyId, newKey);
    this.keyMetadata.set(newKeyId, newMetadata);

    // Persist changes
    await this.persistKey(newKeyId, newKey, newMetadata);

    // Log rotation
    await auditLogger.log({
      event: 'CONFIGURATION_CHANGE',
      details: {
        action: 'key_rotation',
        _keyId,
        newKeyId,
        version: newMetadata.version,
      },
      severity: 'info',
    });
  }

  private async tokenizeValue(value: unknown, _fieldName: string): Promise<string> {
    // Generate unique token
    const token = `tok_${cryptoService.generateSecureUUID()}`;

    // Encrypt and store actual value
    const encrypted = await cryptoService.encrypt(JSON.stringify(value));
    this.tokenVault.set(token, encrypted);

    // Persist token mapping
    await secureStorage.setItem(`token_${token}`, encrypted, {
      encrypted: true,
    });

    return token;
  }

  private async detokenizeValue(token: string, _fieldName: string): Promise<unknown> {
    // Retrieve from vault
    let encrypted = this.tokenVault.get(_token);

    if (!encrypted) {
      // Try loading from storage
      encrypted = await secureStorage.getItem(`token_${token}`);
    }

    if (!encrypted) {
      throw new Error('Token not found');
    }

    const decrypted = await cryptoService.decrypt(encrypted);
    return JSON.parse(decrypted);
  }

  private async formatPreservingEncrypt(
    value: unknown,
    config: FieldEncryptionConfig,
    _keyInfo: EncryptionKeyMetadata
  ): Promise<string> {
    // Simple format-preserving encryption
    // In production, use FF3-1 or similar algorithm
    const _stringValue = String(value);
    const encrypted = await cryptoService.encrypt(_stringValue);

    // Preserve format characteristics
    if (config.dataType === 'date') {
      return `enc_date_${encrypted.substring(0, 10)}`;
    } else if (config.fieldName === 'phone_number') {
      return `enc_phone_${encrypted.substring(0, 10)}`;
    } else if (config.fieldName === 'email') {
      return `enc_${encrypted.substring(0, 20)}@encrypted.local`;
    }

    return encrypted;
  }

  private async formatPreservingDecrypt(
    encrypted: string,
    config: FieldEncryptionConfig
  ): Promise<unknown> {
    // Extract actual encrypted data
    let _actualEncrypted = encrypted;

    if (encrypted.startsWith('enc_')) {
      // Need to retrieve full encrypted value from storage
      const fullEncrypted = await secureStorage.getItem(`fpe_${encrypted}`);
      if (_fullEncrypted) {
        _actualEncrypted = fullEncrypted;
      }
    }

    const decrypted = await cryptoService.decrypt(_actualEncrypted);

    // Convert back to appropriate type
    if (config.dataType === 'date') {
      return new Date(decrypted);
    } else if (config.dataType === 'number') {
      return Number(decrypted);
    }

    return decrypted;
  }

  private async generateSearchToken(searchTerm: string, _fieldName: string): Promise<string> {
    // Generate deterministic search token
    const normalized = searchTerm.toLowerCase().trim();
    const hash = await cryptoService.sha256(`${_fieldName}:${normalized}`);
    return hash.substring(0, 16); // Use prefix for efficiency
  }

  private async updateSearchIndex(
    _fieldName: string,
    value: unknown,
    encrypted: string,
    _userId?: string
  ): Promise<void> {
    if (!this.searchIndexes.has(_fieldName)) {
      this.searchIndexes.set(_fieldName, new Map());
    }

    const fieldIndex = this.searchIndexes.get(_fieldName)!;

    // Generate search tokens for value
    const searchableText = String(value).toLowerCase();
    const words = searchableText.split(/\s+/);

    for (const word of words) {
      if (word.length > 2) { // Skip very short words
        const token = await this.generateSearchToken(word, _fieldName);

        const entries = fieldIndex.get(_token) || [];
        if (!entries.includes(encrypted)) {
          entries.push(encrypted);
          fieldIndex.set(token, entries);
        }
      }
    }
  }

  private async loadEncryptionKeys(): Promise<void> {
