    globalImageLoader.clear();
  });
}

/**
 * React 18/19 Concurrent Features Utilities
 * Leverages advanced React features for optimal performance in mental health app
 */

import React, {
  Suspense,
  startTransition,
  useDeferredValue,
  useTransition,
  lazy,
  memo,
